    const {
      page = '1',
      limit = '50',
      levels,
      categories,
      search,
      startDate,
      endDate,
      userId,
      botId,
      tradeId
    } = req.query;

    const pageNum = parseInt(page as string, 10);
    const limitNum = parseInt(limit as string, 10);
    const offset = (pageNum - 1) * limitNum;

    // Build dynamic query
    let whereConditions: string[] = [];
    let queryParams: any[] = [];
    let paramIndex = 1;

    // Level filter
    if (levels) {
      const levelArray = (levels as string).split(',');
      whereConditions.push(`level = ANY($${paramIndex})`);
      queryParams.push(levelArray);
      paramIndex++;
    }

    // Category filter
    if (categories) {
      const categoryArray = (categories as string).split(',');
      whereConditions.push(`category = ANY($${paramIndex})`);
      queryParams.push(categoryArray);
      paramIndex++;
    }

    // Search filter
    if (search) {
      whereConditions.push(`(message ILIKE $${paramIndex} OR category ILIKE $${paramIndex + 1})`);
      queryParams.push(`%${search}%`, `%${search}%`);
      paramIndex += 2;
    }

    // Date range filter
    if (startDate) {
      whereConditions.push(`timestamp >= $${paramIndex}`);
      queryParams.push(new Date(startDate as string));
      paramIndex++;
    }

    if (endDate) {
      whereConditions.push(`timestamp <= $${paramIndex}`);
      queryParams.push(new Date(endDate as string));
      paramIndex++;
    }

    // User filter
    if (userId) {
      whereConditions.push(`user_id = $${paramIndex}`);
      queryParams.push(userId);
      paramIndex++;
    }

    // Bot filter
    if (botId) {
      whereConditions.push(`bot_id = $${paramIndex}`);
      queryParams.push(botId);
      paramIndex++;
    }

    // Trade filter
    if (tradeId) {
      whereConditions.push(`trade_id = $${paramIndex}`);
      queryParams.push(tradeId);
      paramIndex++;
    }

    const whereClause = whereConditions.length > 0 ? `WHERE ${whereConditions.join(' AND ')}` : '';

    // Get total count
    const countQuery = `
      SELECT COUNT(*) as total 
      FROM logs 
      ${whereClause}
    `;

    const countResult = await database.query(countQuery, queryParams);
    const total = parseInt((countResult as any).rows[0].total, 10);

    // Get logs with pagination
    const logsQuery = `
      SELECT 
        id,
        level,
        message,
        category,
        user_id as "userId",
        bot_id as "botId", 
        trade_id as "tradeId",
        metadata,
        timestamp
      FROM logs 
      ${whereClause}
      ORDER BY timestamp DESC
      LIMIT $${paramIndex} OFFSET $${paramIndex + 1}
    `;

    queryParams.push(limitNum, offset);

    const logsResult = await database.query(logsQuery, queryParams);
    
    const logs: LogEntry[] = (logsResult as any).rows.map((row: any) => ({
      id: row.id,
      level: row.level,
      message: row.message,
      category: row.category,
      userId: row.userId,
      botId: row.botId,
      tradeId: row.tradeId,
      metadata: row.metadata,
      timestamp: new Date(row.timestamp)
    }));

    const responseTime = Date.now() - startTime;

    res.json({
      success: true,
      data: {
        logs,
        total,
        page: pageNum,
        limit: limitNum,
        totalPages: Math.ceil(total / limitNum)
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/api/logs'
      }
    });

    logger.info('Logs retrieved successfully', {
      total,
      page: pageNum,
      limit: limitNum,
      filters: {
        levels,
        categories,
        search,
        startDate,
        endDate,
        userId,
        botId,
        tradeId
      },
      responseTime
    });

  } catch (error) {
    const responseTime = Date.now() - startTime;
    const errorMessage = error instanceof Error ? error.message : String(error);

    logger.error('Failed to retrieve logs', error);

    res.status(500).json({
      success: false,
      error: {
        message: 'Failed to retrieve logs',
        details: errorMessage
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/api/logs'
      }
    });
  }
}

