  // Create the fixed signal processor
  const processor = new FixedSMASignalProcessor();
  
  // Trading state
  let position: 'long' | 'short' | null = null;
  let entryPrice = 0;
  let totalProfit = 0;
  let trades = 0;
  
  // Process candles with sliding window
