      price !== undefined && u !== undefined && pricePrev !== undefined && uPrev !== undefined &&
      price > u && pricePrev <= uPrev
    ) {
      // Price crosses above upper band - potential sell signal
      signals.push(-1);
    } else {
      // No signal
      signals.push(0);
    }
  }

  return signals
