/**
 * SMA Strategy Backtest
 * 
 * This script runs a backtest of the SMA signal processor against synthetic price data
 * to validate its performance and signal generation.
 */

import * as fs from 'fs';
import * as path from 'path';

import { generateSyntheticCandles, type Candle } from '../../../shared/src/test-utils/data-generators';
import type { SMASignalConfig } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.4,
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Main function to run the backtest
async function runBacktest() {
  console.log('Starting SMA Backtest');
  console.log(`Configuration: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}, Threshold: ${config.confidenceThreshold}`);
  
  // Generate test data using shared utility
  const candles = generateSyntheticCandles({
    count: 200,
    startPrice: 100,
    trend: 'mixed',
    timeframe: '15m'
  });
  
  const processor = new SMASignalProcessor(config);
  
  interface SignalRecord {
    timestamp: number;
    price: number;
    signal: 'buy' | 'sell';
    confidence: number;
    reason: string;
  }

  interface TradeRecord {
    type: string;
    timestamp: number;
    price: number;
    profit?: number;
    reason?: string;
  }
  
  const signals: SignalRecord[] = [];
  const trades: TradeRecord[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      if (!candle) {continue;} // Skip if candle is undefined
      
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell' && position.entryPrice !== null) {
          const profit = position.entryPrice - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy' && position.entryPrice !== null) {
          const profit = candle.close - position.entryPrice;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null && position.entryPrice !== null) {
    const lastCandle = candles[candles.length - 1];
    if (lastCandle) {
      let profit = 0;
      
      if (position.side === 'buy') {
        profit = lastCandle.close - position.entryPrice;
        trades.push({
          type: 'Close Long (End)',
          timestamp: lastCandle.timestamp,
          price: lastCandle.close,
          profit
        });
      } else if (position.side === 'sell') {
        profit = position.entryPrice - lastCandle.close;
        trades.push({
          type: 'Close Short (End)',
          timestamp: lastCandle.timestamp,
          price: lastCandle.close,
          profit
        });
      }
      
      pnl += profit;
    }
  }
  
  // Log results
  console.log(`Generated ${signals.length} signals and ${trades.length} trades`);
  console.log(`Final P&L: ${pnl.toFixed(2)}`);
  
  // Generate report
  const report = {
    config,
    summary: {
      signalCount: signals.length,
      tradeCount: trades.length,
      pnl
    },
    signals,
    trades
  };
  
  // Write report to file
  fs.writeFileSync(
    path.join(__dirname, 'sma-backtest-results.json'),
    JSON.stringify(report, null, 2)
  );
  
  console.log('Backtest complete. Results saved to sma-backtest-results.json');
}

// Run the backtest
runBacktest().catch(console.error);

/**
 * Compare original and improved SMA signal processors
 * 
 * This script runs both the original and improved SMA signal processors
 * on the same data and compares their signal generation.
 */

import * as fs from 'fs';
import * as path from 'path';

import { ImprovedSMASignalProcessor } from '../../src/JabbrLabs/signals/sma/improved-sma-signal-processor';
import type { SMASignalConfig} from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalOutput } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';
import type { Candle } from '../../src/JabbrLabs/target-reacher/interfaces';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.4, // Lowered threshold for comparison
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Generate synthetic price data for testing
function generateSyntheticCandles(options: {
  startPrice: number,
  dataPoints: number,
  volatility: number,
  trendStrength: number,
  trendChangeProbability: number
}): Candle[] {
  const candles: Candle[] = [];
  let currentPrice = options.startPrice;
  let trend = 1; // 1 = up, -1 = down

  for (let i = 0; i < options.dataPoints; i++) {
    // Potentially change trend
    if (Math.random() < options.trendChangeProbability) {
      trend *= -1;
    }
    
    // Calculate price movement
    const trendMovement = trend * options.trendStrength * currentPrice;
    const randomMovement = (Math.random() * 2 - 1) * options.volatility * currentPrice;
    const movement = trendMovement + randomMovement;
    
    // Calculate candle prices
    const open = currentPrice;
    const close = currentPrice + movement;
    const high = Math.max(open, close) + Math.random() * options.volatility * currentPrice;
    const low = Math.min(open, close) - Math.random() * options.volatility * currentPrice;
    const volume = Math.random() * 100000 + 10000;
    
    // Create candle
    candles.push({
      timestamp: Date.now() + i * 900000, // 15-minute intervals
      open,
      high,
      low,
      close,
      volume
    });
    
    // Update current price for next iteration
    currentPrice = close;
  }
  
  return candles;
}

// Process signals and execute trades
function processAndTrade(processor: SMASignalProcessor | ImprovedSMASignalProcessor, candles: Candle[]) {
  const signals: any[] = [];
  const trades: any[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell') {
          const profit = position.entryPrice! - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy') {
          const profit = candle.close - position.entryPrice!;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null) {
    const lastCandle = candles[candles.length - 1];
    let profit = 0;
    
    if (position.side === 'buy') {
      profit = lastCandle.close - position.entryPrice!;
      trades.push({
        type: 'Close Long (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    } else {
      profit = position.entryPrice! - lastCandle.close;
      trades.push({
        type: 'Close Short (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    }
    
    pnl += profit;
  }
  
  return {
    signals,
    trades,
    pnl
  };
}

// Main test function
async function runComparisonTest() {
  console.log('Starting SMA Signal Processor Comparison Test');
  console.log(`Configuration: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}`);
  
  // Generate test data
  const candles = generateSyntheticCandles({
    startPrice: 100,
    dataPoints: 200,
    volatility: 0.01,
    trendStrength: 0.005,
    trendChangeProbability: 0.05
  });
  
  // Create processors
  const originalProcessor = new SMASignalProcessor(config);
  const improvedProcessor = new ImprovedSMASignalProcessor(config);
  
  // Process with both implementations
  console.log('\nRunning Original SMA Signal Processor...');
  const originalResults = processAndTrade(originalProcessor, candles);
  
  console.log('\nRunning Improved SMA Signal Processor...');
  const improvedResults = processAndTrade(improvedProcessor, candles);
  
  // Log comparison results
  console.log('\n===== COMPARISON RESULTS =====');
  console.log(`Original: ${originalResults.signals.length} signals, ${originalResults.trades.length} trades, P&L: ${originalResults.pnl.toFixed(2)}`);
  console.log(`Improved: ${improvedResults.signals.length} signals, ${improvedResults.trades.length} trades, P&L: ${improvedResults.pnl.toFixed(2)}`);
  
  // Generate comparison report
  const report = {
    config,
    summary: {
      original: {
        signalCount: originalResults.signals.length,
        tradeCount: originalResults.trades.length,
        pnl: originalResults.pnl
      },
      improved: {
        signalCount: improvedResults.signals.length,
        tradeCount: improvedResults.trades.length,
        pnl: improvedResults.pnl
      },
      comparison: {
        signalDifference: improvedResults.signals.length - originalResults.signals.length,
        pnlDifference: improvedResults.pnl - originalResults.pnl
      }
    },
    details: {
      original: {
        signals: originalResults.signals,
        trades: originalResults.trades
      },
      improved: {
        signals: improvedResults.signals,
        trades: improvedResults.trades
      }
    }
  };
  
  // Write report to file
  fs.writeFileSync(
    path.join(__dirname, 'sma-comparison-report.json'),
    JSON.stringify(report, null, 2)
  );
  
  console.log('Test complete. Comparison report saved to sma-comparison-report.json');
}

runComparisonTest().catch(console.error);

#!/usr/bin/env node

/**
 * Performance Analysis Script
 * 
 * Runs comprehensive performance tests and analysis on the trading bot platform
 */

import * as path from 'path';
import * as fs from 'fs/promises';
import { performanceProfiler } from '../../src/utils/performance-profiler';
import SystemMonitorService from '../../src/services/system-monitor.service';

// Import key components for testing
import { IndicatorService } from '../../src/services/indicator.service';

interface PerformanceTestSuite {
  name: string;
  description: string;
  tests: PerformanceTest[];
}

interface PerformanceTest {
  name: string;
  category: 'api' | 'database' | 'strategy' | 'websocket' | 'calculation' | 'other';
  testFunction: () => Promise<any>;
  iterations?: number;
  warmupIterations?: number;
}

class PerformanceAnalyzer {
  private systemMonitor: SystemMonitorService;
  private testResults: any[] = [];

  constructor() {
    this.systemMonitor = new SystemMonitorService({
      collectInterval: 1000, // 1 second for detailed monitoring
      enableAlerts: true
    });
  }

  /**
   * Run all performance tests
   */
  async runAnalysis(): Promise<void> {
    console.log('üöÄ Starting Performance Analysis...\n');
    
    // Start system monitoring
    this.systemMonitor.start();
    
    try {
      // Define test suites
      const testSuites: PerformanceTestSuite[] = [
        this.createCalculationTests(),
        this.createIndicatorTests(),
        this.createSignalProcessingTests(),
        this.createMemoryTests()
      ];

      // Run each test suite
      for (const suite of testSuites) {
        await this.runTestSuite(suite);
      }

      // Generate comprehensive report
      await this.generateReport();

    } finally {
      this.systemMonitor.stop();
    }
  }

  /**
   * Create calculation performance tests
   */
  private createCalculationTests(): PerformanceTestSuite {
    return {
      name: 'Mathematical Calculations',
      description: 'Tests performance of mathematical operations used in trading algorithms',
      tests: [
        {
          name: 'Simple Moving Average Calculation',
          category: 'calculation',
          testFunction: async () => {
            const data = Array.from({ length: 1000 }, (_, i) => i + Math.random());
            const period = 20;
            
            // Calculate SMA
            const sma: number[] = [];
            for (let i = period - 1; i < data.length; i++) {
              const sum = data.slice(i - period + 1, i + 1).reduce((a, b) => a + b, 0);
              sma.push(sum / period);
            }
            
            return sma.length;
          },
          iterations: 1000
        },
        {
          name: 'Exponential Moving Average Calculation',
          category: 'calculation',
          testFunction: async () => {
            const data = Array.from({ length: 1000 }, (_, i) => i + Math.random());
            const period = 20;
            const multiplier = 2 / (period + 1);
            
            // Calculate EMA
            let ema = data[0];
            for (let i = 1; i < data.length; i++) {
              ema = (data[i] * multiplier) + (ema * (1 - multiplier));
            }
            
            return ema;
          },
          iterations: 1000
        },
        {
          name: 'RSI Calculation',
          category: 'calculation',
          testFunction: async () => {
            const prices = Array.from({ length: 100 }, () => 100 + Math.random() * 50);
            const period = 14;
            
            // Calculate price changes
            const changes = prices.slice(1).map((price, i) => price - prices[i]);
            
            // Separate gains and losses
            const gains = changes.map(change => change > 0 ? change : 0);
            const losses = changes.map(change => change < 0 ? -change : 0);
            
            // Calculate average gain and loss
            const avgGain = gains.slice(0, period).reduce((a, b) => a + b) / period;
            const avgLoss = losses.slice(0, period).reduce((a, b) => a + b) / period;
            
            // Calculate RS and RSI
            const rs = avgGain / avgLoss;
            const rsi = 100 - (100 / (1 + rs));
            
            return rsi;
          },
          iterations: 500
        },
        {
          name: 'Large Array Processing',
          category: 'calculation',
          testFunction: async () => {
            const size = 100000;
            const data = Array.from({ length: size }, (_, i) => Math.random() * 100);
            
            // Perform multiple operations
            const filtered = data.filter(x => x > 50);
            const mapped = filtered.map(x => x * 2);
            const reduced = mapped.reduce((sum, x) => sum + x, 0);
            
            return reduced;
          },
          iterations: 100
        }
      ]
    };
  }

  /**
   * Create indicator service tests
   */
  private createIndicatorTests(): PerformanceTestSuite {
    return {
      name: 'Indicator Service Performance',
      description: 'Tests performance of the indicator calculation service',
      tests: [
        {
          name: 'Multiple Indicator Calculations',
          category: 'calculation',
          testFunction: async () => {
            const indicatorService = new IndicatorService();
            const prices = Array.from({ length: 200 }, () => ({
              timestamp: Date.now(),
              open: 100 + Math.random() * 10,
              high: 105 + Math.random() * 10,
              low: 95 + Math.random() * 10,
              close: 100 + Math.random() * 10,
              volume: 1000 + Math.random() * 500
            }));

            // Calculate multiple indicators
            const sma20 = indicatorService.calculateSMA(prices.map(p => p.close), 20);
            const sma50 = indicatorService.calculateSMA(prices.map(p => p.close), 50);
            const ema12 = indicatorService.calculateEMA(prices.map(p => p.close), 12);
            const rsi = indicatorService.calculateRSI(prices.map(p => p.close), 14);

            return { sma20: sma20.length, sma50: sma50.length, ema12: ema12.length, rsi: rsi.length };
          },
          iterations: 200
        }
      ]
    };
  }

  /**
   * Create signal processing tests
   */
  private createSignalProcessingTests(): PerformanceTestSuite {
    return {
      name: 'Signal Processing Performance',
      description: 'Tests performance of signal processing algorithms',
      tests: [
        {
          name: 'SMA Signal Generation',
          category: 'calculation',
          testFunction: async () => {
            const prices = Array.from({ length: 500 }, () => Math.random() * 100);
            const signals = [];
            
            for (let i = 20; i < prices.length; i++) {
              const sma20 = prices.slice(i - 20, i).reduce((a, b) => a + b) / 20;
              const sma50 = i >= 50 ? prices.slice(i - 50, i).reduce((a, b) => a + b) / 50 : 0;
              
              if (sma20 > sma50) {
                signals.push('BUY');
              } else if (sma20 < sma50) {
                signals.push('SELL');
              }
            }
            
            return signals.length;
          },
          iterations: 100
        }
      ]
    };
  }

  /**
   * Create memory usage tests
   */
  private createMemoryTests(): PerformanceTestSuite {
    return {
      name: 'Memory Usage Tests',
      description: 'Tests memory usage and garbage collection performance',
      tests: [
        {
          name: 'Large Array Processing',
          category: 'other',
          testFunction: async () => {
            const initialMemory = process.memoryUsage();
            const data = Array.from({ length: 100000 }, () => Math.random());
            const processed = data.map(x => x * 2).filter(x => x > 1);
            const finalMemory = process.memoryUsage();
            
            return {
              processed: processed.length,
              memoryDelta: finalMemory.heapUsed - initialMemory.heapUsed
            };
          },
          iterations: 10
        }
      ]
    };
  }

  /**
   * Run a test suite
   */
  private async runTestSuite(suite: PerformanceTestSuite): Promise<void> {
    console.log(`\nüìä Running ${suite.name}`);
    console.log(`   ${suite.description}\n`);

    for (const test of suite.tests) {
      await this.runTest(test);
    }
  }

  /**
   * Run an individual test
   */
  private async runTest(test: PerformanceTest): Promise<void> {
    const iterations = test.iterations || 100;
    const warmupIterations = test.warmupIterations || 10;
    
    console.log(`   üß™ ${test.name}`);
    
    // Warmup
    for (let i = 0; i < warmupIterations; i++) {
      await test.testFunction();
    }
    
    // Actual test
    const startTime = performanceProfiler.now();
    const results = [];
    
    for (let i = 0; i < iterations; i++) {
      const testStart = performanceProfiler.now();
      const result = await test.testFunction();
      const testEnd = performanceProfiler.now();
      
      results.push({
        duration: testEnd - testStart,
        result
      });
    }
    
    const endTime = performanceProfiler.now();
    const totalDuration = endTime - startTime;
    const avgDuration = totalDuration / iterations;
    
    this.testResults.push({
      suiteName: test.category,
      testName: test.name,
      iterations,
      totalDuration,
      avgDuration,
      minDuration: Math.min(...results.map(r => r.duration)),
      maxDuration: Math.max(...results.map(r => r.duration)),
      results
    });
    
    console.log(`      ‚è±Ô∏è  Average: ${avgDuration.toFixed(2)}ms`);
    console.log(`      üìà Total: ${totalDuration.toFixed(2)}ms`);
  }

  /**
   * Generate comprehensive report
   */
  private async generateReport(): Promise<void> {
    console.log('\nüìä Generating Performance Report...\n');
    
    const report = {
      timestamp: new Date().toISOString(),
      systemInfo: {
        nodeVersion: process.version,
        platform: process.platform,
        arch: process.arch,
        cpus: require('os').cpus().length,
        memory: process.memoryUsage()
      },
      testResults: this.testResults,
      summary: this.generateSummary()
    };
    
    // Save report
    const reportPath = path.join(__dirname, '../../reports/performance-report.json');
    await fs.writeFile(reportPath, JSON.stringify(report, null, 2));
    
    console.log(`üìã Performance report saved to: ${reportPath}`);
    console.log('\nüìä Performance Summary:');
    console.log(this.generateSummary());
  }

  /**
   * Generate performance summary
   */
  private generateSummary(): any {
    const categories = {};
    
    for (const result of this.testResults) {
      if (!categories[result.suiteName]) {
        categories[result.suiteName] = {
          testCount: 0,
          totalAvgDuration: 0,
          tests: []
        };
      }
      
      categories[result.suiteName].testCount++;
      categories[result.suiteName].totalAvgDuration += result.avgDuration;
      categories[result.suiteName].tests.push({
        name: result.testName,
        avgDuration: result.avgDuration,
        iterations: result.iterations
      });
    }
    
    return categories;
  }
}

// Run analysis if called directly
if (require.main === module) {
  const analyzer = new PerformanceAnalyzer();
  analyzer.runAnalysis().catch(console.error);
}

export default PerformanceAnalyzer;

/**
 * Unified Trading Engine (JabbrLabs Implementation)
 * 
 * This module provides the JabbrLabs-specific trading engine implementation.
 * It extends the core trading engine with advanced features and strategies.
 */

// Re-export the core trading engine for compatibility
// export { UnifiedTradingEngine } from '../../core/unified-trading-engine'

// JabbrLabs enhancements interface
export interface JabbrLabsTradingEngine {
  // Advanced signal processing
  processAdvancedSignals: (signals: any[]) => Promise<any[]>
  
  // Enhanced risk management
  applyAdvancedRiskRules: (position: any) => Promise<{ approved: boolean; reason?: string }>
  
  // Custom order routing
  routeCustomOrders: (order: any) => Promise<{ route: string; modifications?: any }>
}

/**
 * JabbrLabs Enhanced Trading Engine
 * Provides advanced trading implementations for specialized strategies
 */
export class EnhancedTradingEngine implements JabbrLabsTradingEngine {
  
  /**
   * Process advanced signals with JabbrLabs proprietary algorithms
   */
  async processAdvancedSignals(signals: any[] = []): Promise<any[]> {
    // logger.info('üî¨ [JabbrLabs] Processing advanced signals', LogCategory.SIGNAL, { 
    //   signalCount: signals.length 
    // });
    
    // Apply advanced signal filtering and enhancement
    // logger.info('‚úÖ [JabbrLabs] Advanced signal processing complete', LogCategory.SIGNAL, { 
    //   enhanced: enhancedSignals.length 
    // });
    
    return signals.map(signal => ({
      ...signal,
      // Add JabbrLabs signal enhancements
      jabbrLabsScore: this.calculateJabbrLabsScore(signal),
      enhancedConfidence: this.enhanceConfidence(signal),
      processed: true,
      processedAt: Date.now()
    }));
  }
  
  /**
   * Apply enhanced risk management rules
   */
  async applyAdvancedRiskRules(position: any): Promise<{ approved: boolean; reason?: string }> {
    // logger.info('üõ°Ô∏è [JabbrLabs] Applying advanced risk rules', LogCategory.RISK, {
    //   positionId: position?.id 
    // });
    
    // JabbrLabs advanced risk checks
    const riskChecks = [
      this.checkPositionSize(position),
      this.checkMarketConditions(position),
      this.checkVolatilityLimits(position)
    ];
    
    const failedChecks = riskChecks.filter(check => !check.passed);
    
    if (failedChecks.length > 0) {
      const reason = failedChecks.map(check => check.reason).join(', ');
      // logger.warn('‚ö†Ô∏è [JabbrLabs] Risk rules failed', LogCategory.RISK, { reason });
      return { approved: false, reason };
    }
    
    // logger.info('‚úÖ [JabbrLabs] Risk rules passed', LogCategory.RISK);
    return { approved: true };
  }
  
  /**
   * Route orders through custom JabbrLabs logic
   */
  async routeCustomOrders(order: any): Promise<{ route: string; modifications?: any }> {
    // logger.info('üöÄ [JabbrLabs] Routing custom order', LogCategory.ORDER, { 
    //   orderId: order?.id,
    //   type: order?.type 
    // });
    
    // Determine optimal routing based on order characteristics
    const route = this.determineOptimalRoute(order);
    const modifications = this.applyOrderOptimizations(order);
    
    // logger.info('‚úÖ [JabbrLabs] Order routing complete', LogCategory.ORDER, { 
    //   route,
    //   hasModifications: !!modifications 
    // });
    
    return { route, modifications };
  }
  
  // Helper methods
  private calculateJabbrLabsScore(signal: any): number {
    // Proprietary JabbrLabs scoring algorithm
    const baseScore = signal.confidence ?? 0.5;
    const volatilityBonus = (signal.marketData?.volatility ?? 0) * 0.1;
    const volumeBonus = Math.min((signal.marketData?.volume ?? 0) / 1000000, 0.2);
    
    return Math.min(1, baseScore + volatilityBonus + volumeBonus);
  }
  
  private enhanceConfidence(signal: any): number {
    // Enhanced confidence calculation
    const baseConfidence = signal.confidence ?? 0.5;
    const marketStrength = signal.marketData?.orderBookImbalance ?? 0.5;
    
    return (baseConfidence + marketStrength) / 2;
  }
  
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private checkPositionSize(_position: any): { passed: boolean; reason?: string } {
    const maxPositionSize = 10000; // Example limit
    const size = Math.abs(_position?.size ?? 0);
    if (size > maxPositionSize) {
      return { passed: false, reason: `Position size ${size} exceeds limit ${maxPositionSize}` };
    }
    return { passed: true };
  }
  
  private checkMarketConditions(_position: any): { passed: boolean; reason?: string } {
    // Check if market conditions are suitable for trading
    const volatility = _position?.marketData?.volatility ?? 0;
    
    if (volatility > 0.1) { // 10% volatility limit
      return { passed: false, reason: `Market volatility ${volatility} too high` };
    }
    
    return { passed: true };
  }
  
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private checkVolatilityLimits(_position: any): { passed: boolean; reason?: string } {
    // intentionally unused
    return { passed: true };
  }
  
  private determineOptimalRoute(_order: any): string {
    // Determine best execution route
    if (_order?.type === 'market') {return 'fast-execution';}
    if (_order?.type === 'limit') {return 'optimal-fill';}
    return 'standard';
  }
  
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private applyOrderOptimizations(_order: any): any {
    return null;
  }
}

/**
 * Average Price Indicator
 *
 * Provides a function to calculate the average (mean) price from an array of candle data.
 *
 * Usage:
 *   import { calculateAveragePrice } from './average-price'
 *
 *   const avg = calculateAveragePrice(candles)
 */

// NOTE: This file is self-contained and does NOT import itself. There is no real circular dependency here. If flagged, this is a tool limitation, not a code issue.

export interface Candle {
  open: number
  high: number
  low: number
  close: number
  volume?: number
  timestamp?: number
}

/**
 * Calculate the average (mean) price from an array of candles.
 * Uses the average of the low prices by default, but can be extended.
 *
 * @param candles Array of candle objects
 * @param priceType 'low' | 'close' | 'high' | 'open' (default: 'low')
 * @returns Average price
 */
export function calculateAveragePrice(
  candles: Candle[],
  priceType: 'low' | 'close' | 'high' | 'open' = 'low'
): number {
  if (!candles.length) {throw new Error('No candle data provided')}
  const sum = candles.reduce((acc, candle) => {
    const validPriceTypes = ['low', 'close', 'high', 'open'] as const;
    if (!validPriceTypes.includes(priceType)) {
      throw new Error(`Invalid price type: ${priceType}`);
    }
    return acc + (candle[priceType] ?? 0);
  }, 0);
  return sum / candles.length
}

/**
 * Unified Indicator Interface
 * 
 * Provides a standard interface for technical indicators to ensure
 * consistent usage across the platform.
 */

import type { Candle } from '../target-reacher/interfaces';

/**
 * Indicator metadata for documentation and validation
 */
export interface IndicatorMetadata {
  name: string;
  description: string;
  category: 'trend' | 'momentum' | 'volatility' | 'volume' | 'overlay' | 'oscillator';
  parameters: Array<{
    name: string;
    type: 'number' | 'boolean' | 'string';
    description: string;
    default: any;
    min?: number;
    max?: number;
    required?: boolean;
  }>;
  outputs: Array<{
    name: string;
    type: 'number' | 'array';
    description: string;
  }>;
  minimumDataPoints: number;
  version: string;
}

/**
 * Indicator calculation result with metadata
 */
export interface IndicatorResult<T = number | number[]> {
  value: T;
  metadata?: {
    timestamp?: number;
    confidence?: number;
    parameters?: Record<string, unknown>;
    dataPoints?: number;
  };
}

/**
 * Base interface for all indicators
 */
export interface IIndicator<T = number | number[]> {
  /**
   * Get indicator metadata
   */
  getMetadata(): IndicatorMetadata;
  
  /**
   * Calculate indicator value(s) from price data
   * 
   * @param data Input price data
   * @returns Indicator value(s) or result with metadata
   */
  calculate(data: number[]): T;
  
  /**
   * Calculate indicator value(s) from candle data
   * 
   * @param candles Input candle data
   * @param priceSource Which price to use from candles
   * @returns Indicator value(s) or result with metadata
   */
  calculateFromCandles(candles: Candle[], priceSource?: 'open' | 'high' | 'low' | 'close'): T;
  
  /**
   * Calculate with detailed result including metadata
   * 
   * @param data Input price data
   * @returns Detailed result with metadata
   */
  calculateWithMetadata(data: number[]): IndicatorResult<T>;
  
  /**
   * Validate input parameters
   * 
   * @param parameters Parameters to validate
   * @returns Validation result
   */
  validateParameters(parameters: Record<string, unknown>): { valid: boolean; errors: string[] };
  
  /**
   * Get indicator name
   */
  getName(): string;
  
  /**
   * Get indicator parameters
   */
  getParameters(): Record<string, unknown>;
  
  /**
   * Update indicator parameters
   * 
   * @param parameters New parameters to update
   */
  updateParameters(parameters: Record<string, unknown>): void;
  
  /**
   * Reset indicator state (for stateful indicators)
   */
  reset(): void;
  
  /**
   * Clone the indicator with same parameters
   */
  clone(): IIndicator<T>;
}

/**
 * Base abstract class for indicators
 */
export abstract class BaseIndicator<T = number | number[]> implements IIndicator<T> {
  protected name: string;
  protected parameters: Record<string, unknown>;
  protected metadata: IndicatorMetadata;
  
  constructor(metadata: IndicatorMetadata, parameters: Record<string, unknown> = {}) {
    this.metadata = metadata;
    this.name = metadata.name;
    this.parameters = this.setDefaultParameters(parameters);
  }
  
  abstract calculate(data: number[]): T;
  
  getMetadata(): IndicatorMetadata {
    return { ...this.metadata };
  }
  
  calculateFromCandles(candles: Candle[], priceSource: 'open' | 'high' | 'low' | 'close' = 'close'): T {
    const validPriceSources = ['open', 'high', 'low', 'close'] as const;
    if (!validPriceSources.includes(priceSource)) {
      throw new Error(`Invalid price source: ${priceSource}`);
    }
    const prices = candles.map(candle => candle[priceSource]);
    return this.calculate(prices);
  }
  
  calculateWithMetadata(data: number[]): IndicatorResult<T> {
    const value = this.calculate(data);
    // Check for NaN/Infinity in result (supports number or array)
    const checkFinite = (v: any) => {
      if (typeof v === 'number') {return isFinite(v) && !isNaN(v);}
      if (Array.isArray(v)) {return v.every(x => typeof x === 'number' && isFinite(x) && !isNaN(x));}
      return false;
    };
    if (!checkFinite(value)) {
      throw new Error(`Indicator ${this.name} calculation returned non-finite value(s). Check input data and parameters.`);
    }
    return {
      value,
      metadata: {
        timestamp: Date.now(),
        parameters: this.getParameters(),
        dataPoints: data.length
      }
    };
  }
  
  validateParameters(parameters: Record<string, unknown>): { valid: boolean; errors: string[] } {
    const errors: string[] = [];
    
    for (const paramDef of this.metadata.parameters) {
      const value = parameters[paramDef.name];
      
      // Check required parameters
      if (paramDef.required && (value === undefined || value === null)) {
        errors.push(`Parameter '${paramDef.name}' is required`);
        continue;
      }
      
      if (value !== undefined && value !== null) {
        // Type validation
        if (paramDef.type === 'number' && typeof value !== 'number') {
          errors.push(`Parameter '${paramDef.name}' must be a number`);
          continue;
        }
        
        if (paramDef.type === 'boolean' && typeof value !== 'boolean') {
          errors.push(`Parameter '${paramDef.name}' must be a boolean`);
          continue;
        }
        
        if (paramDef.type === 'string' && typeof value !== 'string') {
          errors.push(`Parameter '${paramDef.name}' must be a string`);
          continue;
        }
        
        // Range validation for numbers
        if (paramDef.type === 'number' && typeof value === 'number') {
          if (paramDef.min !== undefined && value < paramDef.min) {
            errors.push(`Parameter '${paramDef.name}' must be at least ${paramDef.min}`);
          }
          
          if (paramDef.max !== undefined && value > paramDef.max) {
            errors.push(`Parameter '${paramDef.name}' must be at most ${paramDef.max}`);
          }
        }
      }
    }
    
    return { valid: errors.length === 0, errors };
  }
  
  getName(): string {
    return this.name;
  }
  
  getParameters(): Record<string, unknown> {
    return { ...this.parameters };
  }
  
  updateParameters(parameters: Record<string, unknown>): void {
    const validation = this.validateParameters(parameters);
    if (!validation.valid) {
      throw new Error(`Invalid parameters: ${validation.errors.join(', ')}`);
    }
    
    this.parameters = { ...this.parameters, ...parameters };
  }
  
  reset(): void {
    // Default implementation - override in stateful indicators
  }
  
  abstract clone(): IIndicator<T>;
  
  /**
   * Set default parameters from metadata
   */
  private setDefaultParameters(parameters: Record<string, unknown>): Record<string, unknown> {
    const result = { ...parameters };
    
    for (const paramDef of this.metadata.parameters) {
      if (result[paramDef.name] === undefined) {
        result[paramDef.name] = paramDef.default;
      }
    }
    
    return result;
  }
  
  /**
   * Validate that enough data points are available
   * 
   * @param data Input data array
   * @param minLength Minimum required length (optional, uses metadata if not provided)
   */
  protected validateDataLength(data: number[], minLength?: number): void {
    const requiredLength = minLength || this.metadata.minimumDataPoints;
    if (data.length < requiredLength) {
      throw new Error(`Insufficient data for ${this.name} calculation. Need at least ${requiredLength} data points, got ${data.length}.`);
    }
  }
  
  /**
   * Get numeric parameter value with validation
   * 
   * @param key Parameter key
   * @param defaultValue Default value if parameter is not set
   * @returns Parameter value
   */
  protected getNumericParameter(key: string, defaultValue: number): number {
    if (typeof key !== 'string' || !key) {
      throw new Error('Parameter key must be a non-empty string');
    }
    const value = Object.prototype.hasOwnProperty.call(this.parameters, key) ? this.parameters[key as keyof typeof this.parameters] : undefined;
    if (value === undefined) {
      return defaultValue;
    }
    
    const numValue = Number(value);
    if (isNaN(numValue)) {
      throw new Error(`Invalid parameter value for ${key}: ${value} is not a number`);
    }
    
    return numValue;
  }
  
  /**
   * Get boolean parameter value with validation
   * 
   * @param key Parameter key
   * @param defaultValue Default value if parameter is not set
   * @returns Parameter value
   */
  protected getBooleanParameter(key: string, defaultValue: boolean): boolean {
    if (typeof key !== 'string' || !key) {
      throw new Error('Parameter key must be a non-empty string');
    }
    const value = Object.prototype.hasOwnProperty.call(this.parameters, key) ? this.parameters[key as keyof typeof this.parameters] : undefined;
    if (value === undefined) {
      return defaultValue;
    }
    
    return Boolean(value);
  }
  
  /**
   * Validate numeric array input
   * 
   * @param data Input data array
   * @returns Validated data array
   */
  protected validateNumericArray(data: number[]): number[] {
    if (!Array.isArray(data)) {
      throw new Error(`Input data must be an array for ${this.name}`);
    }
    // Reject NaN, Infinity, -Infinity, and non-numbers
    const validData = data.filter(value => typeof value === 'number' && !isNaN(value) && isFinite(value));
    if (validData.length !== data.length) {
      throw new Error(`Input data contains invalid values for ${this.name}. All values must be finite numbers.`);
    }
    return validData;
  }
}

// Canonical config validator for Target Reacher. No duplications.
import type {
  ConfigValidationResult,
  ValidationError,
  ValidationWarning
} from './interfaces'

export class ConfigValidator {
  private errors: ValidationError[] = [];
  private warnings: ValidationWarning[] = [];

  reset(): void {
    this.errors = [];
    this.warnings = [];
  }

  addError(field: string, message: string, code = 'VALIDATION_ERROR'): void {
    this.errors.push({ field, message, code });
  }

  addWarning(field: string, message: string, suggestion?: string): void {
    this.warnings.push({ field, message, suggestion });
  }

  required(obj: Record<string, unknown>, field: string, fieldName?: string): boolean {
    const name = fieldName ?? field;
    if (obj[field] === undefined || obj[field] === null) {
      this.addError(field, `${name} is required`, 'REQUIRED');
      return false;
    }
    return true;
  }

  number(obj: Record<string, unknown>, field: string, options: {
    min?: number;
    max?: number;
    integer?: boolean;
    fieldName?: string;
  } = {}): boolean {
    const name = options.fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (typeof value !== 'number' || isNaN(value)) {
      this.addError(field, `${name} must be a number`, 'INVALID_TYPE');
      return false;
    }
    if (options.integer && !Number.isInteger(value)) {
      this.addError(field, `${name} must be an integer`, 'INVALID_TYPE');
      return false;
    }
    if (options.min !== undefined && value < options.min) {
      this.addError(field, `${name} must be at least ${options.min}`, 'OUT_OF_RANGE');
      return false;
    }
    if (options.max !== undefined && value > options.max) {
      this.addError(field, `${name} must be at most ${options.max}`, 'OUT_OF_RANGE');
      return false;
    }
    return true;
  }

  string(obj: Record<string, unknown>, field: string, options: {
    minLength?: number;
    maxLength?: number;
    pattern?: RegExp;
    enum?: string[];
    fieldName?: string;
  } = {}): boolean {
    const name = options.fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (typeof value !== 'string') {
      this.addError(field, `${name} must be a string`, 'INVALID_TYPE');
      return false;
    }
    if (options.minLength !== undefined && value.length < options.minLength) {
      this.addError(field, `${name} must be at least ${options.minLength} characters`, 'TOO_SHORT');
      return false;
    }
    if (options.maxLength !== undefined && value.length > options.maxLength) {
      this.addError(field, `${name} must be at most ${options.maxLength} characters`, 'TOO_LONG');
      return false;
    }
    if (options.pattern && !options.pattern.test(value)) {
      this.addError(field, `${name} format is invalid`, 'INVALID_FORMAT');
      return false;
    }
    if (options.enum && !options.enum.includes(value)) {
      this.addError(field, `${name} must be one of: ${options.enum.join(', ')}`, 'INVALID_VALUE');
      return false;
    }
    return true;
  }

  object(obj: Record<string, unknown>, field: string, validator: (value: unknown) => boolean, fieldName?: string): boolean {
    const name = fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (typeof value !== 'object' || value === null || Array.isArray(value)) {
      this.addError(field, `${name} must be an object`, 'INVALID_TYPE');
      return false;
    }
    return validator(value);
  }

  array(obj: Record<string, unknown>, field: string, options: {
    minLength?: number;
    maxLength?: number;
    itemValidator?: (item: unknown, index: number) => boolean;
    fieldName?: string;
  } = {}): boolean {
    const name = options.fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (!Array.isArray(value)) {
      this.addError(field, `${name} must be an array`, 'INVALID_TYPE');
      return false;
    }
    if (options.minLength !== undefined && value.length < options.minLength) {
      this.addError(field, `${name} must have at least ${options.minLength} items`, 'TOO_SHORT');
      return false;
    }
    if (options.maxLength !== undefined && value.length > options.maxLength) {
      this.addError(field, `${name} must have at most ${options.maxLength} items`, 'TOO_LONG');
      return false;
    }
    if (options.itemValidator) {
      for (let i = 0; i < value.length; i++) {
        if (!options.itemValidator(value.at(i), i)) {
          return false;
        }
      }
    }
    return true;
  }

  warn(condition: boolean, field: string, message: string, suggestion?: string): void {
    if (condition) {
      this.addWarning(field, message, suggestion);
    }
  }

  getResult(): ConfigValidationResult {
    return {
      valid: this.errors.length === 0,
      errors: [...this.errors],
      warnings: [...this.warnings],
    };
  }
}

export function validateConfig(config: Record<string, unknown>, validatorFn: (validator: ConfigValidator) => void): ConfigValidationResult {
  const validator = new ConfigValidator();
  validator.reset();
  validatorFn(validator);
  return validator.getResult();
}

// [Canonical parameter mapping for Target Reacher. No duplications.]

// Utility to map between camelCase and snake_case for strategy parameters

export function camelToSnake(obj: Record<string, unknown>): Record<string, unknown> {
  const out: Record<string, unknown> = {};
  for (const key in obj) {
    if (!Object.prototype.hasOwnProperty.call(obj, key)) {continue;}
    const snake = key.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
    out[snake] = obj[key];
  }
  return out;
}

export function snakeToCamel(obj: Record<string, unknown>): Record<string, unknown> {
  if (!obj || typeof obj !== 'object') {return obj;}
  const result: Record<string, unknown> = {};
  for (const key in obj) {
    if (!Object.prototype.hasOwnProperty.call(obj, key)) {continue;}
    const camelKey = key.replace(/_([a-z])/g, (_, letter) => letter.toUpperCase());
    const value = obj[key];
    result[camelKey] = (value && typeof value === 'object' && !Array.isArray(value))
      ? snakeToCamel(value as Record<string, unknown>)
      : value;
  }
  return result;
}

/**
 * Modular Target Reacher Strategy
 * 
 * Redesigned Target Reacher strategy using the new modular system
 */

import type { ConfigValidator } from './config-validator';
import { validateConfig } from './config-validator'
import type { 
  IStrategy, 
  StrategyConfig, 
  StrategyContext, 
  StrategyResult, 
  StrategyState,
  ConfigValidationResult,
  Candle
} from './interfaces'

export class ModularTargetReacherStrategy implements IStrategy {
  readonly name = 'target-reacher'
  readonly version = '2.0.0'
  readonly description = 'Modular Target Reacher strategy with fixed and average price sources (pure signal generator)'
  readonly supportedMarkets = ['spot', 'futures', 'cross-margin'] as const

  constructor(private readonly config: StrategyConfig, private readonly context: StrategyContext) {}

  async initialize(): Promise<void> {
    // No-op for pure signal strategy
  }

  async execute(): Promise<StrategyResult> {
    const { priceSource, fixedPrice, averagePeriod, averageType } = this.config.parameters
    const currentPriceRaw = await this.context.marketData.getCurrentPrice(this.context.symbol)
    const currentPrice = typeof currentPriceRaw === 'number' ? currentPriceRaw : Number(currentPriceRaw) || 0
    let targetPrice: number | undefined

    if (priceSource === 'fixed') {
      targetPrice = typeof fixedPrice === 'number' ? fixedPrice : Number(fixedPrice) || undefined
    } else if (priceSource === 'average') {
      const avgPeriod = typeof averagePeriod === 'number' && !isNaN(averagePeriod) ? averagePeriod : 20;
      const candles: Candle[] = await this.context.marketData.getCandles(this.context.symbol, '1h', avgPeriod)
      const priceKey: keyof Candle = (averageType ?? 'low') as keyof Candle;
      targetPrice = candles.length > 0
        ? candles.reduce((sum, c) => {
            const val = c[priceKey];
            return sum + (typeof val === 'number' ? val : Number(val) || 0);
          }, 0) / candles.length
        : undefined;
    } else {
      throw new Error('Unsupported price source')
    }

    if (typeof targetPrice === 'number') {
      if (currentPrice <= targetPrice) {
        return { success: true, action: 'buy', reason: `Current price <= target (${targetPrice})` }
      } else if (currentPrice >= targetPrice) {
        return { success: true, action: 'sell', reason: `Current price >= target (${targetPrice})` }
      }
    }
    return { success: true, action: 'hold', reason: 'No signal' }
  }

  async cleanup(): Promise<void> {
    // No-op for pure signal strategy
  }

  validateConfig(config: Record<string, unknown>): ConfigValidationResult {
    return validateConfig(config, (validator: ConfigValidator) => {
      validator.required(config, 'parameters')
      const params = config.parameters as Record<string, unknown>
      validator.string(params, 'priceSource', { enum: ['fixed', 'average'], fieldName: 'Price Source' })
      if (params.priceSource === 'fixed') {
        validator.number(params, 'fixedPrice', { min: 0, fieldName: 'Fixed Price' })
      } else if (params.priceSource === 'average') {
        validator.number(params, 'averagePeriod', { min: 1, max: 500, integer: true, fieldName: 'Average Period' })
        validator.string(params, 'averageType', { enum: ['low', 'close', 'high', 'open'], fieldName: 'Average Type' })
      }
    })
  }

  getDefaultConfig(): StrategyConfig {
    // Provide a unique default config for ModularTargetReacherStrategy
    return {
      type: 'modular-target-reacher',
      parameters: {
        priceSource: 'fixed',
        fixedPrice: 50,
        averagePeriod: 10,
        averageType: 'close',
      },
    };
  }

  getState(): StrategyState {
    // Provide a unique state for ModularTargetReacherStrategy
    return {
      isRunning: false,
      totalProfit: 0,
      tradesExecuted: 0,
      lastUpdate: new Date(),
      customState: { modular: true },
    };
  }
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  setState(_state: Partial<StrategyState>): void {
    // No-op for pure signal strategy
  }
}

