/**
 * Configuration Manager
 * Handles loading, validating, and optimizing configuration based on environment
 */
export class ConfigManager {
  private static instance: ConfigManager;
  private config: Config;
  private environment: Environment;

  private constructor() {
    this.environment = this.detectEnvironment();
    this.loadEnvironmentFile();
    this.config = this.loadAndValidateConfig();
    this.applyEnvironmentOptimizations();
  }

  /**
   * Get singleton instance
   */
  public static getInstance(): ConfigManager {
    if (!ConfigManager.instance) {
      ConfigManager.instance = new ConfigManager();
    }
    return ConfigManager.instance;
  }

  /**
   * Get configuration
   */
  public getConfig(): Config {
    return { ...this.config };
  }

  /**
   * Get environment
   */
  public getEnvironment(): Environment {
    return this.environment;
  }

  /**
   * Get database configuration
   */
  public getDatabaseConfig() {
    return {
      host: this.config.DB_HOST,
      port: this.config.DB_PORT,
      database: this.config.DB_NAME,
      user: this.config.DB_USER,
      password: this.config.DB_PASSWORD,
      ssl: this.config.DB_SSL,
      pool: {
        min: this.config.DB_POOL_MIN,
        max: this.config.DB_POOL_MAX,
        idleTimeoutMillis: this.config.DB_POOL_IDLE_TIMEOUT,
        connectionTimeoutMillis: this.config.DB_POOL_CONNECTION_TIMEOUT,
      },
    };
  }

  /**
   * Get Redis configuration
   */
  public getRedisConfig() {
    return {
      host: this.config.REDIS_HOST,
      port: this.config.REDIS_PORT,
      password: this.config.REDIS_PASSWORD,
      db: this.config.REDIS_DB,
      clusterMode: this.config.REDIS_CLUSTER_MODE,
    };
  }

  /**
   * Get performance monitoring configuration
   */
  public getPerformanceConfig() {
    return {
      enabled: this.config.ENABLE_PERFORMANCE_MONITORING,
      sampleRate: this.config.PERFORMANCE_SAMPLE_RATE,
      metricsInterval: this.config.METRICS_COLLECTION_INTERVAL,
      serviceName: this.config.APM_SERVICE_NAME,
      environment: this.config.APM_ENVIRONMENT || this.environment,
    };
  }

  /**
   * Get security configuration
   */
  public getSecurityConfig() {
    return {
      corsOrigin: this.config.CORS_ORIGIN.split(','),
      rateLimit: {
        windowMs: this.config.RATE_LIMIT_WINDOW,
        max: this.config.RATE_LIMIT_MAX_REQUESTS,
      },
      bcryptRounds: this.config.BCRYPT_ROUNDS,
      helmet: this.config.ENABLE_HELMET,
      compression: this.config.ENABLE_COMPRESSION,
      jwt: {
        secret: this.config.JWT_SECRET,
        expiresIn: this.config.JWT_EXPIRES_IN,
      },
    };
  }

  /**
   * Get feature flags
   */
  public getFeatureFlags() {
    return {
      mockData: this.config.ENABLE_MOCK_DATA,
      testRoutes: this.config.ENABLE_TEST_ROUTES,
      skipAuth: this.config.SKIP_AUTH_FOR_TESTING,
      debugRoutes: this.config.ENABLE_DEBUG_ROUTES,
      swaggerUI: this.config.ENABLE_SWAGGER_UI,
      hotReload: this.config.ENABLE_HOT_RELOAD,
      advancedAnalytics: this.config.ENABLE_ADVANCED_ANALYTICS,
      machineLearning: this.config.ENABLE_MACHINE_LEARNING,
      riskManagement: this.config.ENABLE_RISK_MANAGEMENT,
      portfolioOptimization: this.config.ENABLE_PORTFOLIO_OPTIMIZATION,
    };
  }

  /**
   * Check if configuration is valid for environment
   */
  public validateConfiguration(): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    // Production-specific validations
    if (this.environment === 'production') {
      if (this.config.JWT_SECRET.length < 64) {
        errors.push('JWT_SECRET must be at least 64 characters in production');
      }
      if (this.config.ENABLE_DEBUG_ROUTES) {
        errors.push('Debug routes must be disabled in production');
      }
      if (this.config.ENABLE_TEST_ROUTES) {
        errors.push('Test routes must be disabled in production');
      }
      if (!this.config.DB_SSL) {
        errors.push('Database SSL must be enabled in production');
      }
    }

    // Development-specific validations
    if (this.environment === 'development') {
      if (!this.config.ENABLE_DEBUG_ROUTES) {
        console.warn('Debug routes are disabled in development environment');
      }
    }

    // Test-specific validations
    if (this.environment === 'test') {
      if (!this.config.USE_MOCK_EXCHANGE) {
        errors.push('Mock exchange should be enabled in test environment');
      }
    }

    return {
      valid: errors.length === 0,
      errors,
    };
  }

  /**
   * Detect current environment
   */
  private detectEnvironment(): Environment {
    const env = process.env.NODE_ENV?.toLowerCase();
    if (env === 'production' || env === 'test') {
      return env;
    }
    return 'development';
  }

  /**
   * Load environment-specific .env file
   */
  private loadEnvironmentFile(): void {
    const envFile = path.join(process.cwd(), 'config', `.env.${this.environment}`);
    
    if (fs.existsSync(envFile)) {
      dotenv.config({ path: envFile });
      console.log(`‚úÖ Loaded environment configuration: ${envFile}`);
    } else {
      console.warn(`‚ö†Ô∏è  Environment file not found: ${envFile}`);
      // Fallback to root .env file
      dotenv.config({ path: path.join(process.cwd(), '../../.env') });
    }
  }

  /**
   * Load and validate configuration
   */
  private loadAndValidateConfig(): Config {
    try {
      const config = ConfigSchema.parse(process.env);
      console.log(`‚úÖ Configuration validated for ${this.environment} environment`);
      return config;
    } catch (error) {
      console.error('‚ùå Configuration validation failed:', error);
      throw new Error(`Invalid configuration: ${error}`);
    }
  }

  /**
   * Apply environment-specific optimizations
   */
  private applyEnvironmentOptimizations(): void {
    const optimizations = EnvironmentOptimizations[this.environment];
    
    // Apply optimizations
    Object.assign(this.config, optimizations);
    
    console.log(`‚úÖ Applied ${this.environment} environment optimizations`);
    
    // Log key optimizations
    console.log(`üìä Database pool: ${this.config.DB_POOL_MIN}-${this.config.DB_POOL_MAX} connections`);
    console.log(`üìù Log level: ${this.config.LOG_LEVEL}`);
    console.log(`üîç Performance sampling: ${this.config.PERFORMANCE_SAMPLE_RATE * 100}%`);
  }
}

// Export singleton instance
export const configManager = ConfigManager.getInstance();
export const config = configManager.getConfig();

// Export utility functions
export function getEnvironment(): Environment {
  return configManager.getEnvironment();
}

export function isProduction(): boolean {
  return configManager.getEnvironment() === 'production';
}

export function isDevelopment(): boolean {
  return configManager.getEnvironment() === 'development';
}

export function isTest(): boolean {
  return configManager.getEnvironment() === 'test';
}

export function validateConfig(): { valid: boolean; errors: string[] } {
  return configManager.validateConfiguration();
}

import fs from 'fs/promises';
import path from 'path';
import crypto from 'crypto';

import { database } from './database.config';

/**
 * Migration record interface
 */
interface Migration {
  id: number;
  name: string;
  filename: string;
  executed_at: Date;
  checksum: string;
}

/**
 * Database Migration Runner
 * Handles executing SQL migration files and tracking migration history
 */
export class MigrationRunner {
  private migrationsPath: string;

  constructor(migrationsPath?: string) {
    this.migrationsPath = migrationsPath || path.join(__dirname, 'migrations');
  }

  /**
   * Initialize migration tracking table
   */
  async initializeMigrationTable(): Promise<void> {
    const createTableSQL = `
      CREATE TABLE IF NOT EXISTS migrations (
        id SERIAL PRIMARY KEY,
        name VARCHAR(255) NOT NULL UNIQUE,
        filename VARCHAR(255) NOT NULL,
        executed_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
        checksum VARCHAR(64) NOT NULL
      );
      
      CREATE INDEX IF NOT EXISTS idx_migrations_name ON migrations(name);
      CREATE INDEX IF NOT EXISTS idx_migrations_executed_at ON migrations(executed_at);
    `;

    try {
      await database.query(createTableSQL);
      console.log('‚úÖ Migration tracking table initialized');
    } catch (error) {
      console.error('‚ùå Failed to initialize migration table:', error);
      throw error;
    }
  }

  /**
   * Handle pre-existing database with tables but no migration tracking
   */
  async handlePreExistingDatabase(): Promise<void> {
    try {
      // Check if key tables exist (indicating this is a pre-existing database)
      const tableExistsResult = await database.query(`
        SELECT table_name 
        FROM information_schema.tables 
        WHERE table_schema = 'public' 
        AND table_name IN ('users', 'bots', 'trades', 'positions')
      `);

      // If tables exist but no migrations are recorded, mark initial migrations as complete
      if (tableExistsResult.length > 0) {
        const executedMigrations = await this.getExecutedMigrations();
        
        if (executedMigrations.length === 0) {
          console.log('üîç Detected pre-existing database with tables but no migration tracking');
          
          // Mark initial schema as already applied
          const initialMigrationFiles = await this.getMigrationFiles();
          const initialSchema = initialMigrationFiles.find(m => m.name.includes('initial_schema'));
          
          if (initialSchema) {
            await database.query(`
              INSERT INTO migrations (name, filename, checksum)
              VALUES ($1, $2, $3)
              ON CONFLICT (name) DO NOTHING
            `, [initialSchema.name, initialSchema.filename, initialSchema.checksum]);
            
            console.log(`‚úÖ Marked initial schema migration as complete: ${initialSchema.name}`);
          }
        }
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è Warning: Could not check for pre-existing database:', error);
      // Continue with normal migration process
    }
  }

  /**
   * Get all executed migrations from database
   */
  async getExecutedMigrations(): Promise<Migration[]> {
    try {
      return await database.query<Migration>(`
        SELECT id, name, filename, executed_at, checksum 
        FROM migrations 
        ORDER BY id ASC
      `);
    } catch (error) {
      // If table doesn't exist, return empty array
      if (error instanceof Error && error.message.includes('relation "migrations" does not exist')) {
        return [];
      }
      throw error;
    }
  }

  /**
   * Get all migration files from filesystem
   */
  async getMigrationFiles(): Promise<{ name: string; filename: string; content: string; checksum: string }[]> {
    try {
      const files = await fs.readdir(this.migrationsPath);
      const migrationFiles = files
        .filter(file => file.endsWith('.sql'))
        .sort(); // Ensure consistent ordering

      const migrations = [];
      for (const filename of migrationFiles) {
        const filePath = path.join(this.migrationsPath, filename);
        const content = await fs.readFile(filePath, 'utf-8');
        const name = this.extractMigrationName(filename);
        const checksum = this.calculateChecksum(content);

        migrations.push({
          name,
          filename,
          content,
          checksum
        });
      }

      return migrations;
    } catch (error) {
      console.error('‚ùå Failed to read migration files:', error);
      throw error;
    }
  }

  /**
   * Execute pending migrations
   */
  async runMigrations(): Promise<void> {
    console.log('üîÑ Starting database migrations...');

    // Ensure database is connected
    if (!database.isConnectionActive()) {
      await database.connect();
    }

    // Initialize migration table
    await this.initializeMigrationTable();

    // Check if this is a pre-existing database with tables
    await this.handlePreExistingDatabase();

    // Get executed migrations and available migration files
    const [executedMigrations, migrationFiles] = await Promise.all([
      this.getExecutedMigrations(),
      this.getMigrationFiles()
    ]);

    // Find pending migrations
    const executedNames = new Set(executedMigrations.map(m => m.name));
    const pendingMigrations = migrationFiles.filter(m => !executedNames.has(m.name));

    if (pendingMigrations.length === 0) {
      console.log('‚úÖ No pending migrations');
      return;
    }

    console.log(`üìã Found ${pendingMigrations.length} pending migrations:`);
    pendingMigrations.forEach(m => console.log(`   - ${m.name}`));

    // Execute pending migrations
    for (const migration of pendingMigrations) {
      await this.executeMigration(migration);
    }

    console.log('‚úÖ All migrations completed successfully');
  }

  /**
   * Execute a single migration
   */
  private async executeMigration(migration: { name: string; filename: string; content: string; checksum: string }): Promise<void> {
    console.log(`üîÑ Executing migration: ${migration.name}`);

    try {
      await database.transaction(async (client) => {
        // Execute the migration SQL
        await client.query(migration.content);

        // Record the migration in the tracking table
        await client.query(`
          INSERT INTO migrations (name, filename, checksum)
          VALUES ($1, $2, $3)
        `, [migration.name, migration.filename, migration.checksum]);
      });

      console.log(`‚úÖ Migration completed: ${migration.name}`);
    } catch (error) {
      console.error(`‚ùå Migration failed: ${migration.name}`, error);
      throw error;
    }
  }

  /**
   * Verify migration integrity
   */
  async verifyMigrations(): Promise<{ valid: boolean; issues: string[] }> {
    const issues: string[] = [];

    try {
      const [executedMigrations, migrationFiles] = await Promise.all([
        this.getExecutedMigrations(),
        this.getMigrationFiles()
      ]);

      // Check for missing migration files
      for (const executed of executedMigrations) {
        const file = migrationFiles.find(f => f.name === executed.name);
        if (!file) {
          issues.push(`Migration file missing: ${executed.filename}`);
          continue;
        }

        // Check checksum integrity
        if (file.checksum !== executed.checksum) {
          issues.push(`Migration checksum mismatch: ${executed.name} (file may have been modified)`);
        }
      }

      // Check for out-of-order migrations
      const executedNames = executedMigrations.map(m => m.name);
      const fileNames = migrationFiles.map(f => f.name);
      
      for (let i = 0; i < executedNames.length; i++) {
        const executedName = executedNames.at(i);
        const expectedName = fileNames.at(i);
        
        if (executedName !== expectedName) {
          issues.push(`Migration order mismatch at position ${i}: expected ${expectedName}, found ${executedName}`);
        }
      }

      return {
        valid: issues.length === 0,
        issues
      };
    } catch (error) {
      issues.push(`Verification failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      return { valid: false, issues };
    }
  }

  /**
   * Get migration status
   */
  async getStatus(): Promise<{
    executed: number;
    pending: number;
    total: number;
    migrations: Array<{
      name: string;
      status: 'executed' | 'pending';
      executed_at?: Date;
    }>;
  }> {
    const [executedMigrations, migrationFiles] = await Promise.all([
      this.getExecutedMigrations(),
      this.getMigrationFiles()
    ]);

    const executedMap = new Map(executedMigrations.map(m => [m.name, m]));
    
    const migrations = migrationFiles.map(file => {
      const executed = executedMap.get(file.name);
      return {
        name: file.name,
        status: executed ? 'executed' as const : 'pending' as const,
        executed_at: executed?.executed_at
      };
    });

    return {
      executed: executedMigrations.length,
      pending: migrationFiles.length - executedMigrations.length,
      total: migrationFiles.length,
      migrations
    };
  }

  /**
   * Extract migration name from filename
   */
  private extractMigrationName(filename: string): string {
    // Remove .sql extension and use the filename as the name
    return filename.replace(/\.sql$/, '');
  }

  /**
   * Calculate checksum for migration content
   */
  private calculateChecksum(content: string): string {
    return crypto.createHash('sha256').update(content).digest('hex');
  }

  /**
   * Create a new migration file
   */
  async createMigration(name: string): Promise<string> {
    const timestamp = new Date().toISOString().replace(/[:\-T]/g, '').split('.')[0];
    const filename = `${timestamp}_${name.replace(/\s+/g, '_').toLowerCase()}.sql`;
    const filepath = path.join(this.migrationsPath, filename);

    const template = `-- ============================================================================
-- Migration: ${filename}
-- Description: ${name}
-- Created: ${new Date().toISOString()}
-- ============================================================================

-- Add your SQL statements here

-- Example:
-- CREATE TABLE example (
--     id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
--     name VARCHAR(255) NOT NULL,
--     created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW()
-- );

-- Log migration completion
INSERT INTO logs (level, message, category, metadata)
VALUES ('info', 'Migration ${filename} completed', 'migration', '{"migration": "${filename}"}');
`;

    await fs.writeFile(filepath, template, 'utf-8');
    console.log(`‚úÖ Created migration file: ${filename}`);
    return filepath;
  }
}

