/**
 * Application Monitor Service
 * 
 * Monitors application performance including response times, throughput, error rates,
 * and API endpoint metrics. Provides detailed performance analytics for the trading platform.
 */

import { EventEmitter } from 'events';

import logger from './logging.service';

export interface EndpointMetrics {
  path: string;
  method: string;
  count: number;
  totalTime: number;
  averageTime: number;
  minTime: number;
  maxTime: number;
  errorCount: number;
  errorRate: number;
  lastCall: Date;
  statusCodes: Record<number, number>;
}

export interface ApplicationMetrics {
  performance: {
    responseTime: {
      average: number;
      p50: number;
      p95: number;
      p99: number;
    };
    throughput: {
      requestsPerSecond: number;
      requestsPerMinute: number;
    };
    errorRate: number;
    uptime: number;
  };
  endpoints: Record<string, EndpointMetrics>;
  errors: {
    total: number;
    byType: Record<string, number>;
    recent: ErrorLog[];
  };
  database: {
    queryCount: number;
    averageQueryTime: number;
    slowQueries: number;
    connectionPoolSize: number;
    activeConnections: number;
  };
  websocket: {
    activeConnections: number;
    messagesPerSecond: number;
    totalMessages: number;
    disconnections: number;
  };
  memory: {
    heapUsed: number;
    heapTotal: number;
    external: number;
    rss: number;
    arrayBuffers: number;
  };
  system: {
    uptime: number;
    cpuUsage: {
      user: number;
      system: number;
    };
    platform: string;
    arch: string;
    nodeVersion: string;
  };
  timestamp: Date;
}

export interface RequestLog {
  id: string;
  method: string;
  path: string;
  statusCode: number;
  responseTime: number;
  userAgent?: string;
  ip?: string;
  timestamp: Date;
  error?: string;
}

export interface ErrorLog {
  id: string;
  type: string;
  message: string;
  stack?: string;
  context?: any;
  timestamp: Date;
  level: 'error' | 'warning';
}

export interface PerformanceAlert {
  type: 'response_time' | 'error_rate' | 'throughput' | 'database' | 'memory';
  level: 'warning' | 'critical';
  message: string;
  value: number;
  threshold: number;
  timestamp: Date;
}

export interface ApplicationMonitorConfig {
  collectInterval: number; // milliseconds
  requestRetentionPeriod: number; // milliseconds
  errorRetentionPeriod: number; // milliseconds
  maxRequestLogs: number;
  maxErrorLogs: number;
  alertThresholds: {
    responseTime: { warning: number; critical: number }; // milliseconds
    errorRate: { warning: number; critical: number }; // percentage
    throughput: { warning: number; critical: number }; // requests per second
    databaseQueryTime: { warning: number; critical: number }; // milliseconds
  };
  enableDetailedLogging: boolean;
}

export class ApplicationMonitorService extends EventEmitter {
  private static instance: ApplicationMonitorService;
  private config: ApplicationMonitorConfig;
  private isRunning = false;
  private monitorInterval?: NodeJS.Timeout;
  private startTime: Date;
  
  // Metrics storage
  private requestLogs: RequestLog[] = [];
  private errorLogs: ErrorLog[] = [];
  private endpointMetrics: Map<string, EndpointMetrics> = new Map();
  private responseTimes: number[] = [];
  private requestsPerInterval = 0;
  private errorsPerInterval = 0;
  
  // Database metrics
  private databaseMetrics = {
    queryCount: 0,
    totalQueryTime: 0,
    slowQueries: 0,
    connectionPoolSize: 0,
    activeConnections: 0
  };
  
  // WebSocket metrics
  private websocketMetrics = {
    activeConnections: 0,
    messagesPerSecond: 0,
    totalMessages: 0,
    disconnections: 0
  };

  private constructor(config: Partial<ApplicationMonitorConfig> = {}) {
    super();
    
    this.config = {
      collectInterval: 10000, // 10 seconds
      requestRetentionPeriod: 60 * 60 * 1000, // 1 hour
      errorRetentionPeriod: 24 * 60 * 60 * 1000, // 24 hours
      maxRequestLogs: 10000,
      maxErrorLogs: 1000,
      alertThresholds: {
        responseTime: { warning: 500, critical: 2000 },
        errorRate: { warning: 5, critical: 10 },
        throughput: { warning: 10, critical: 5 },
        databaseQueryTime: { warning: 100, critical: 500 }
      },
      enableDetailedLogging: true,
      ...config
    };

    this.startTime = new Date();
    logger.info('Application Monitor Service initialized', { config: this.config });
  }

  /**
   * Get singleton instance
   */
  public static getInstance(config?: Partial<ApplicationMonitorConfig>): ApplicationMonitorService {
    if (!ApplicationMonitorService.instance) {
      ApplicationMonitorService.instance = new ApplicationMonitorService(config);
    }
    return ApplicationMonitorService.instance;
  }

  /**
   * Reset singleton instance (for testing)
   */
  public static resetInstance(): void {
    if (ApplicationMonitorService.instance) {
      ApplicationMonitorService.instance.shutdown();
      ApplicationMonitorService.instance = undefined as any;
    }
  }

  /**
   * Start application monitoring
   */
  public start(): void {
    if (this.isRunning) {
      logger.warn('Application Monitor is already running');
      return;
    }

    this.isRunning = true;
    
    // Start periodic collection and analysis
    this.monitorInterval = setInterval(() => {
      this.collectMetrics();
      this.checkAlerts();
      this.cleanupOldLogs();
    }, this.config.collectInterval);

    logger.info('Application Monitor started', {
      interval: this.config.collectInterval
    });

    this.emit('started');
  }

  /**
   * Stop application monitoring
   */
  public stop(): void {
    if (!this.isRunning) {
      return;
    }

    this.isRunning = false;
    
    if (this.monitorInterval) {
      clearInterval(this.monitorInterval);
      this.monitorInterval = undefined;
    }

    logger.info('Application Monitor stopped');
    this.emit('stopped');
  }

  /**
   * Log a request
   */
  public logRequest(log: Omit<RequestLog, 'id' | 'timestamp'>): void {
    const requestLog: RequestLog = {
      id: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      ...log
    };

    // Add to request logs
    this.requestLogs.push(requestLog);
    
    // Update endpoint metrics
    this.updateEndpointMetrics(requestLog);
    
    // Track response times
    this.responseTimes.push(requestLog.responseTime);
    this.requestsPerInterval++;
    
    // Track errors
    if (requestLog.statusCode >= 400) {
      this.errorsPerInterval++;
    }

    // Emit request event
    this.emit('request', requestLog);

    if (this.config.enableDetailedLogging) {
      logger.debug('Request logged', {
        method: requestLog.method,
        path: requestLog.path,
        statusCode: requestLog.statusCode,
        responseTime: requestLog.responseTime
      });
    }
  }

  /**
   * Log an error
   */
  public logError(error: Omit<ErrorLog, 'id' | 'timestamp'>): void {
    const errorLog: ErrorLog = {
      id: `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      ...error
    };

    this.errorLogs.push(errorLog);
    this.emit('error', errorLog);

    logger.error('Application error logged', {
      type: errorLog.type,
      message: errorLog.message,
      level: errorLog.level
    });
  }

  /**
   * Update database metrics
   */
  public updateDatabaseMetrics(metrics: {
    queryTime?: number;
    connectionPoolSize?: number;
    activeConnections?: number;
    isSlowQuery?: boolean;
  }): void {
    if (metrics.queryTime !== undefined) {
      this.databaseMetrics.queryCount++;
      this.databaseMetrics.totalQueryTime += metrics.queryTime;
      
      if (metrics.isSlowQuery) {
        this.databaseMetrics.slowQueries++;
      }
    }
    
    if (metrics.connectionPoolSize !== undefined) {
      this.databaseMetrics.connectionPoolSize = metrics.connectionPoolSize;
    }
    
    if (metrics.activeConnections !== undefined) {
      this.databaseMetrics.activeConnections = metrics.activeConnections;
    }
  }

  /**
   * Update WebSocket metrics
   */
  public updateWebSocketMetrics(metrics: {
    activeConnections?: number;
    messagesSent?: number;
    disconnection?: boolean;
  }): void {
    if (metrics.activeConnections !== undefined) {
      this.websocketMetrics.activeConnections = metrics.activeConnections;
    }
    
    if (metrics.messagesSent !== undefined) {
      this.websocketMetrics.totalMessages += metrics.messagesSent;
    }
    
    if (metrics.disconnection) {
      this.websocketMetrics.disconnections++;
    }
  }

  /**
   * Get current application metrics
   */
  public getCurrentMetrics(): ApplicationMetrics {
    const now = new Date();
    const uptime = (now.getTime() - this.startTime.getTime()) / 1000;
    const recentRequests = this.getRecentRequests(60000); // Last minute
    const recentErrors = this.getRecentErrors(60000); // Last minute

    // Calculate response time percentiles
    const sortedResponseTimes = [...this.responseTimes].sort((a, b) => a - b);
    const responseTimeStats = this.calculatePercentiles(sortedResponseTimes);

    // Calculate throughput
    const requestsPerMinute = recentRequests.length;
    const requestsPerSecond = requestsPerMinute / 60;

    // Calculate error rate
    const errorRate = recentRequests.length > 0 
      ? (recentErrors.length / recentRequests.length) * 100 
      : 0;

    // Calculate database averages
    const averageQueryTime = this.databaseMetrics.queryCount > 0
      ? this.databaseMetrics.totalQueryTime / this.databaseMetrics.queryCount
      : 0;

    // Calculate WebSocket messages per second
    const messagesPerSecond = this.websocketMetrics.totalMessages / uptime;

    // Get memory and system metrics
    const memoryUsage = process.memoryUsage();
    const systemUptime = process.uptime();
    const cpuUsage = process.cpuUsage();

    return {
      performance: {
        responseTime: {
          average: responseTimeStats.average,
          p50: responseTimeStats.p50,
          p95: responseTimeStats.p95,
          p99: responseTimeStats.p99
        },
        throughput: {
          requestsPerSecond,
          requestsPerMinute
        },
        errorRate,
        uptime
      },
      endpoints: this.getEndpointMetricsSummary(),
      errors: {
        total: this.errorLogs.length,
        byType: this.getErrorsByType(),
        recent: this.getRecentErrors(3600000).slice(0, 10) // Last hour, top 10
      },
      database: {
        queryCount: this.databaseMetrics.queryCount,
        averageQueryTime,
        slowQueries: this.databaseMetrics.slowQueries,
        connectionPoolSize: this.databaseMetrics.connectionPoolSize,
        activeConnections: this.databaseMetrics.activeConnections
      },
      websocket: {
        activeConnections: this.websocketMetrics.activeConnections,
        messagesPerSecond,
        totalMessages: this.websocketMetrics.totalMessages,
        disconnections: this.websocketMetrics.disconnections
      },
      memory: {
        heapUsed: memoryUsage.heapUsed,
        heapTotal: memoryUsage.heapTotal,
        external: memoryUsage.external,
        rss: memoryUsage.rss,
        arrayBuffers: memoryUsage.arrayBuffers
      },
      system: {
        uptime: systemUptime,
        cpuUsage: {
          user: cpuUsage.user,
          system: cpuUsage.system
        },
        platform: process.platform,
        arch: process.arch,
        nodeVersion: process.version
      },
      timestamp: now
    };
  }

  /**
   * Get endpoint metrics summary
   */
  private getEndpointMetricsSummary(): Record<string, EndpointMetrics> {
    const summary: Record<string, EndpointMetrics> = {};
    
    for (const [key, metrics] of this.endpointMetrics) {
      summary[key] = { ...metrics };
    }
    
    return summary;
  }

  /**
   * Update endpoint metrics
   */
  private updateEndpointMetrics(request: RequestLog): void {
    const key = `${request.method}:${request.path}`;
    const existing = this.endpointMetrics.get(key);
    
    if (existing) {
      existing.count++;
      existing.totalTime += request.responseTime;
      existing.averageTime = existing.totalTime / existing.count;
      existing.minTime = Math.min(existing.minTime, request.responseTime);
      existing.maxTime = Math.max(existing.maxTime, request.responseTime);
      existing.lastCall = request.timestamp;
      existing.statusCodes[request.statusCode] = (existing.statusCodes[request.statusCode] || 0) + 1;
      
      if (request.statusCode >= 400) {
        existing.errorCount++;
        existing.errorRate = (existing.errorCount / existing.count) * 100;
      }
    } else {
      const newMetrics: EndpointMetrics = {
        path: request.path,
        method: request.method,
        count: 1,
        totalTime: request.responseTime,
        averageTime: request.responseTime,
        minTime: request.responseTime,
        maxTime: request.responseTime,
        errorCount: request.statusCode >= 400 ? 1 : 0,
        errorRate: request.statusCode >= 400 ? 100 : 0,
        lastCall: request.timestamp,
        statusCodes: { [request.statusCode]: 1 }
      };
      
      this.endpointMetrics.set(key, newMetrics);
    }
  }

  /**
   * Calculate response time percentiles
   */
  private calculatePercentiles(responseTimes: number[]): {
    average: number;
    p50: number;
    p95: number;
    p99: number;
  } {
    if (responseTimes.length === 0) {
      return { average: 0, p50: 0, p95: 0, p99: 0 };
    }

    const average = responseTimes.reduce((sum, time) => sum + time, 0) / responseTimes.length;
    const p50 = responseTimes[Math.floor(responseTimes.length * 0.5)] || 0;
    const p95 = responseTimes[Math.floor(responseTimes.length * 0.95)] || 0;
    const p99 = responseTimes[Math.floor(responseTimes.length * 0.99)] || 0;

    return { average, p50, p95, p99 };
  }

  /**
   * Get recent requests
   */
  private getRecentRequests(duration: number): RequestLog[] {
    const cutoff = new Date(Date.now() - duration);
    return this.requestLogs.filter(log => log.timestamp >= cutoff);
  }

  /**
   * Get recent errors
   */
  private getRecentErrors(duration: number): ErrorLog[] {
    const cutoff = new Date(Date.now() - duration);
    return this.errorLogs.filter(log => log.timestamp >= cutoff);
  }

  /**
   * Get errors by type
   */
  private getErrorsByType(): Record<string, number> {
    const byType: Record<string, number> = {};
    
    for (const error of this.errorLogs) {
      byType[error.type] = (byType[error.type] || 0) + 1;
    }
    
    return byType;
  }

  /**
   * Collect metrics and reset interval counters
   */
  private collectMetrics(): void {
    const metrics = this.getCurrentMetrics();
    
    // Reset interval counters
    this.requestsPerInterval = 0;
    this.errorsPerInterval = 0;
    
    // Update WebSocket messages per second
    this.websocketMetrics.messagesPerSecond = 
      this.websocketMetrics.totalMessages / metrics.performance.uptime;
    
    this.emit('metrics', metrics);
    
    logger.debug('Application metrics collected', {
      requestsPerSecond: metrics.performance.throughput.requestsPerSecond,
      errorRate: metrics.performance.errorRate,
      averageResponseTime: metrics.performance.responseTime.average
    });
  }

  /**
   * Check for performance alerts
   */
  private checkAlerts(): void {
    const metrics = this.getCurrentMetrics();
    const alerts: PerformanceAlert[] = [];

    // Response time alerts
    const avgResponseTime = metrics.performance.responseTime.average;
    if (avgResponseTime >= this.config.alertThresholds.responseTime.critical) {
      alerts.push({
        type: 'response_time',
        level: 'critical',
        message: `Critical response time: ${avgResponseTime.toFixed(1)}ms`,
        value: avgResponseTime,
        threshold: this.config.alertThresholds.responseTime.critical,
        timestamp: new Date()
      });
    } else if (avgResponseTime >= this.config.alertThresholds.responseTime.warning) {
      alerts.push({
        type: 'response_time',
        level: 'warning',
        message: `High response time: ${avgResponseTime.toFixed(1)}ms`,
        value: avgResponseTime,
        threshold: this.config.alertThresholds.responseTime.warning,
        timestamp: new Date()
      });
    }

    // Error rate alerts
    const errorRate = metrics.performance.errorRate;
    if (errorRate >= this.config.alertThresholds.errorRate.critical) {
      alerts.push({
        type: 'error_rate',
        level: 'critical',
        message: `Critical error rate: ${errorRate.toFixed(1)}%`,
        value: errorRate,
        threshold: this.config.alertThresholds.errorRate.critical,
        timestamp: new Date()
      });
    } else if (errorRate >= this.config.alertThresholds.errorRate.warning) {
      alerts.push({
        type: 'error_rate',
        level: 'warning',
        message: `High error rate: ${errorRate.toFixed(1)}%`,
        value: errorRate,
        threshold: this.config.alertThresholds.errorRate.warning,
        timestamp: new Date()
      });
    }

    // Throughput alerts (low throughput)
    const throughput = metrics.performance.throughput.requestsPerSecond;
    if (throughput <= this.config.alertThresholds.throughput.critical && throughput > 0) {
      alerts.push({
        type: 'throughput',
        level: 'critical',
        message: `Low throughput: ${throughput.toFixed(1)} req/s`,
        value: throughput,
        threshold: this.config.alertThresholds.throughput.critical,
        timestamp: new Date()
      });
    } else if (throughput <= this.config.alertThresholds.throughput.warning && throughput > 0) {
      alerts.push({
        type: 'throughput',
        level: 'warning',
        message: `Low throughput: ${throughput.toFixed(1)} req/s`,
        value: throughput,
        threshold: this.config.alertThresholds.throughput.warning,
        timestamp: new Date()
      });
    }

    // Database alerts
    const dbQueryTime = metrics.database.averageQueryTime;
    if (dbQueryTime >= this.config.alertThresholds.databaseQueryTime.critical) {
      alerts.push({
        type: 'database',
        level: 'critical',
        message: `Critical database query time: ${dbQueryTime.toFixed(1)}ms`,
        value: dbQueryTime,
        threshold: this.config.alertThresholds.databaseQueryTime.critical,
        timestamp: new Date()
      });
    } else if (dbQueryTime >= this.config.alertThresholds.databaseQueryTime.warning) {
      alerts.push({
        type: 'database',
        level: 'warning',
        message: `High database query time: ${dbQueryTime.toFixed(1)}ms`,
        value: dbQueryTime,
        threshold: this.config.alertThresholds.databaseQueryTime.warning,
        timestamp: new Date()
      });
    }

    // Emit alerts
    for (const alert of alerts) {
      this.emit('alert', alert);
      logger.warn('Performance alert triggered', alert);
    }
  }

  /**
   * Clean up old logs
   */
  private cleanupOldLogs(): void {
    const now = Date.now();
    
    // Clean up request logs
    const requestCutoff = new Date(now - this.config.requestRetentionPeriod);
    const initialRequestCount = this.requestLogs.length;
    this.requestLogs = this.requestLogs.filter(log => log.timestamp >= requestCutoff);
    
    // Limit request logs by count
    if (this.requestLogs.length > this.config.maxRequestLogs) {
      this.requestLogs = this.requestLogs.slice(-this.config.maxRequestLogs);
    }
    
    // Clean up error logs
    const errorCutoff = new Date(now - this.config.errorRetentionPeriod);
    const initialErrorCount = this.errorLogs.length;
    this.errorLogs = this.errorLogs.filter(log => log.timestamp >= errorCutoff);
    
    // Limit error logs by count
    if (this.errorLogs.length > this.config.maxErrorLogs) {
      this.errorLogs = this.errorLogs.slice(-this.config.maxErrorLogs);
    }
    
    // Clean up response times array
    if (this.responseTimes.length > 10000) {
      this.responseTimes = this.responseTimes.slice(-5000);
    }
    
    const requestsRemoved = initialRequestCount - this.requestLogs.length;
    const errorsRemoved = initialErrorCount - this.errorLogs.length;
    
    if (requestsRemoved > 0 || errorsRemoved > 0) {
      logger.debug('Cleaned up old logs', {
        requestsRemoved,
        errorsRemoved
      });
    }
  }

  /**
   * Get monitoring statistics
   */
  public getStats(): {
    isRunning: boolean;
    uptime: number;
    requestsLogged: number;
    errorsLogged: number;
    endpointsTracked: number;
  } {
    return {
      isRunning: this.isRunning,
      uptime: Date.now() - this.startTime.getTime(),
      requestsLogged: this.requestLogs.length,
      errorsLogged: this.errorLogs.length,
      endpointsTracked: this.endpointMetrics.size
    };
  }

  /**
   * Reset all metrics
   */
  public resetMetrics(): void {
    this.requestLogs = [];
    this.errorLogs = [];
    this.endpointMetrics.clear();
    this.responseTimes = [];
    this.requestsPerInterval = 0;
    this.errorsPerInterval = 0;
    
    this.databaseMetrics = {
      queryCount: 0,
      totalQueryTime: 0,
      slowQueries: 0,
      connectionPoolSize: 0,
      activeConnections: 0
    };
    
    this.websocketMetrics = {
      activeConnections: 0,
      messagesPerSecond: 0,
      totalMessages: 0,
      disconnections: 0
    };

    logger.info('Application Monitor metrics reset');
    this.emit('reset');
  }

  /**
   * Reset all metrics (legacy method - kept for compatibility)
   */
  public reset(): void {
    this.resetMetrics();
  }

  /**
   * Shutdown the monitor
   */
  public shutdown(): void {
    this.stop();
    this.reset();
    this.removeAllListeners();
    logger.info('Application Monitor shut down');
  }
}

export default ApplicationMonitorService;

/**
 * Data service for downloading and managing historical market data
 */

// Import axios if needed (commented out as we're using mock data)
// import axios from 'axios';
import fs from 'fs/promises';
import path from 'path';

import type { Candle } from '../JabbrLabs/target-reacher/interfaces';

import logger from './logging.service';

/**
 * Default data directory for storing cached historical data
 */
const DEFAULT_DATA_DIR = path.join(process.cwd(), 'data', 'historical');

/**
 * Download historical price data for a symbol and timeframe
 * Uses a local cache to avoid repeated downloads
 */
export async function downloadHistoricalData(
  symbol: string,
  timeframe: string,
  startTime: number,
  endTime: number,
  cacheDir: string = DEFAULT_DATA_DIR
): Promise<Candle[]> {
  // Validate and sanitize inputs
  if (!symbol || typeof symbol !== 'string') {
    throw new Error('Invalid symbol provided');
  }
  if (!timeframe || typeof timeframe !== 'string') {
    throw new Error('Invalid timeframe provided');
  }
  
  // Normalize symbol for file naming (remove dangerous characters)
  const normalizedSymbol = symbol.replace(/[^a-zA-Z0-9_-]/g, '_');
  const normalizedTimeframe = timeframe.replace(/[^a-zA-Z0-9_-]/g, '_');
  const cacheFileName = `${normalizedSymbol}_${normalizedTimeframe}_${startTime}_${endTime}.json`;
  
  // Validate that cache directory is within allowed bounds
  const resolvedCacheDir = path.resolve(cacheDir);
  const resolvedBasePath = path.resolve(DEFAULT_DATA_DIR);
  if (!resolvedCacheDir.startsWith(resolvedBasePath)) {
    throw new Error('Invalid cache directory path');
  }
  
  const cachePath = path.join(resolvedCacheDir, path.basename(cacheFileName));
  
  // Try to load from cache first
  try {
    await fs.mkdir(cacheDir, { recursive: true });
    
    try {
      const cacheFile = await fs.readFile(cachePath, 'utf8');
      const cachedData = JSON.parse(cacheFile);
      logger.info(`Loaded ${cachedData.length} candles from cache for ${symbol} ${timeframe}`);
      return cachedData;
    } catch (err) {
      // Cache file doesn't exist, continue to download
      logger.debug(`No cache found for ${symbol} ${timeframe}, downloading...`);
    }
  } catch (err) {
    logger.warn(`Could not create cache directory: ${err instanceof Error ? err.message : String(err)}`);
  }
  
  try {
    // For this example, we'll generate mock data
    // In production, you would fetch from an exchange API
    const candles = generateMockCandles(symbol, timeframe, startTime, endTime);
    
    // Save to cache
    try {
      await fs.writeFile(cachePath, JSON.stringify(candles));
      logger.debug(`Saved ${candles.length} candles to cache for ${symbol} ${timeframe}`);
    } catch (err) {
      logger.warn(`Could not save to cache: ${err instanceof Error ? err.message : String(err)}`);
    }
    
    return candles;
  } catch (error) {
    logger.error(`Failed to download historical data: ${error instanceof Error ? error.message : String(error)}`);
    throw new Error(`Failed to download historical data for ${symbol} ${timeframe}`);
  }
}

/**
 * Generate mock price data for testing and examples
 */
function generateMockCandles(
  symbol: string,
  timeframe: string,
  startTime: number,
  endTime: number
): Candle[] {
  const candles: Candle[] = [];
  let currentTime = startTime;
  
  // Calculate time increment based on timeframe
  const timeIncrement = getTimeframeMilliseconds(timeframe);
  
  // Initial price
  let price = 30000; // Starting price (e.g., for BTC)
  
  while (currentTime <= endTime) {
    // Generate some realistic price movement with trends and volatility
    const trend = Math.sin(currentTime / (86400000 * 30)) * 5000; // 30-day cycle
    const volatility = (Math.random() - 0.5) * 500; // Random noise
    
    price = Math.max(100, price + trend / 100 + volatility);
    
    // Calculate OHLC
    const open = price;
    const high = open * (1 + Math.random() * 0.02); // Up to 2% higher
    const low = open * (1 - Math.random() * 0.02); // Up to 2% lower
    const close = (open + high + low) / 3 + (Math.random() - 0.5) * (high - low);
    
    // Random volume
    const volume = 10 + Math.random() * 100;
    
    candles.push({
      timestamp: currentTime,
      open,
      high,
      low,
      close,
      volume
    });
    
    currentTime += timeIncrement;
  }
  
  return candles;
}

/**
 * Convert timeframe string to milliseconds
 */
function getTimeframeMilliseconds(timeframe: string): number {
  const unit = timeframe.charAt(timeframe.length - 1);
  const value = parseInt(timeframe.substring(0, timeframe.length - 1));
  
  switch (unit) {
    case 'm': return value * 60 * 1000;
    case 'h': return value * 60 * 60 * 1000;
    case 'd': return value * 24 * 60 * 60 * 1000;
    case 'w': return value * 7 * 24 * 60 * 60 * 1000;
    default: throw new Error(`Invalid timeframe unit: ${unit}`);
  }
}

/**
 * Save candle data to a CSV file for external analysis
 */
export async function exportCandlesToCsv(
  candles: Candle[],
  filePath: string
): Promise<void> {
  try {
    const headers = 'timestamp,datetime,open,high,low,close,volume\n';
    const rows = candles.map(c => 
      `${c.timestamp},${new Date(c.timestamp).toISOString()},${c.open},${c.high},${c.low},${c.close},${c.volume}`
    ).join('\n');
    
    const csvContent = headers + rows;
    await fs.writeFile(filePath, csvContent);
    
    logger.info(`Exported ${candles.length} candles to ${filePath}`);
  } catch (error) {
    logger.error(`Failed to export candles to CSV: ${error instanceof Error ? error.message : String(error)}`);
    throw error;
  }
}

/**
 * Database Monitor Service
 * 
 * Monitors database performance, connections, query patterns, and health.
 * Provides insights into database bottlenecks and optimization opportunities.
 */

import { EventEmitter } from 'events';
import { performance } from 'perf_hooks';

import type { JabbrWebSocketServer } from '../websocket/websocket.service';

import { database } from './database.service';
import logger from './logging.service';

export interface DatabaseMetrics {
  connections: {
    active: number;
    idle: number;
    total: number;
    maxAllowed: number;
    utilizationPercentage: number;
  };
  queries: {
    totalExecuted: number;
    averageTime: number;
    slowQueries: number;
    failedQueries: number;
    queriesPerSecond: number;
    longestQuery: QueryMetric | null;
  };
  tables: {
    mostAccessed: TableMetric[];
    tableStats: Map<string, TableStatistic>;
  };
  performance: {
    transactionsPerSecond: number;
    lockWaitTime: number;
    cacheHitRatio: number;
    indexEfficiency: number;
  };
  health: {
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
    lastCheck: Date;
    uptime: number;
  };
  timestamp: Date;
}

export interface QueryMetric {
  id: string;
  query: string;
  duration: number;
  timestamp: Date;
  tableName?: string;
  operation: 'SELECT' | 'INSERT' | 'UPDATE' | 'DELETE' | 'OTHER';
  rowsAffected?: number;
  error?: string;
}

export interface TableMetric {
  tableName: string;
  accessCount: number;
  totalTime: number;
  averageTime: number;
  operations: {
    select: number;
    insert: number;
    update: number;
    delete: number;
  };
}

export interface TableStatistic {
  tableName: string;
  rowCount: number;
  sizeInMB: number;
  indexCount: number;
  lastAnalyzed: Date;
  fragmentationPercentage: number;
}

export interface DatabaseMonitorConfig {
  metricsRetentionHours: number;
  slowQueryThresholdMs: number;
  enableQueryLogging: boolean;
  healthCheckInterval: number;
  connectionThresholds: {
    warning: number;
    critical: number;
  };
  performanceThresholds: {
    slowQueryMs: number;
    highLockWaitMs: number;
    lowCacheHitRatio: number;
  };
}

export class DatabaseMonitorService extends EventEmitter {
  private config: DatabaseMonitorConfig;
  private websocketServer?: JabbrWebSocketServer;
  private queryHistory: QueryMetric[] = [];
  private tableMetrics: Map<string, TableMetric> = new Map();
  private metricsHistory: DatabaseMetrics[] = [];
  private isMonitoring = false;
  private healthCheckInterval?: NodeJS.Timeout;
  private cleanupInterval?: NodeJS.Timeout;
  private queryCounter = 0;
  private startTime: Date;
  private lastMetricsCalculation = Date.now();

  constructor(
    config: Partial<DatabaseMonitorConfig> = {},
    websocketServer?: JabbrWebSocketServer
  ) {
    super();
    
    this.config = {
      metricsRetentionHours: 24,
      slowQueryThresholdMs: 1000,
      enableQueryLogging: true,
      healthCheckInterval: 30000, // 30 seconds
      connectionThresholds: {
        warning: 80, // 80% of max connections
        critical: 95 // 95% of max connections
      },
      performanceThresholds: {
        slowQueryMs: 1000,
        highLockWaitMs: 100,
        lowCacheHitRatio: 0.8
      },
      ...config
    };

    this.websocketServer = websocketServer;
    this.startTime = new Date();

    logger.info('Database Monitor Service initialized', { config: this.config });
  }

  /**
   * Start database monitoring
   */
  public start(): void {
    if (this.isMonitoring) {
      logger.warn('Database Monitor Service is already running');
      return;
    }

    this.isMonitoring = true;

    // Setup query monitoring
    if (this.config.enableQueryLogging) {
      this.setupQueryMonitoring();
    }

    // Start health checks
    this.startHealthChecks();

    // Cleanup old metrics
    this.scheduleMetricsCleanup();

    logger.info('Database Monitor Service started');
    this.emit('started');
  }

  /**
   * Stop database monitoring
   */
  public stop(): void {
    if (!this.isMonitoring) {
      return;
    }

    this.isMonitoring = false;

    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = undefined;
    }

    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = undefined;
    }

    logger.info('Database Monitor Service stopped');
    this.emit('stopped');
  }

  /**
   * Get current database metrics
   */
  public async getCurrentMetrics(): Promise<DatabaseMetrics> {
    try {
      const connections = await this.getConnectionMetrics();
      const queries = this.getQueryMetrics();
      const tables = await this.getTableMetrics();
      const performance = await this.getPerformanceMetrics();
      const health = await this.getHealthMetrics();

      const metrics: DatabaseMetrics = {
        connections,
        queries,
        tables,
        performance,
        health,
        timestamp: new Date()
      };

      // Store metrics history
      this.metricsHistory.push(metrics);

      // Emit metrics
      this.emit('metricsUpdate', metrics);        // Broadcast via WebSocket
        if (this.websocketServer) {
          this.websocketServer.broadcast('system', {
            type: 'database_metrics',
            data: metrics
          });
        }

      return metrics;

    } catch (error) {
      logger.error('Failed to collect database metrics', { error });
      throw error;
    }
  }

  /**
   * Record a query execution
   */
  public recordQuery(
    query: string,
    duration: number,
    error?: string,
    rowsAffected?: number
  ): void {
    if (!this.isMonitoring || !this.config.enableQueryLogging) {
      return;
    }

    this.queryCounter++;

    const queryMetric: QueryMetric = {
      id: `query_${this.queryCounter}_${Date.now()}`,
      query: this.sanitizeQuery(query),
      duration,
      timestamp: new Date(),
      operation: this.determineOperation(query),
      rowsAffected,
      error,
      tableName: this.extractTableName(query)
    };

    this.queryHistory.push(queryMetric);

    // Update table metrics
    if (queryMetric.tableName) {
      this.updateTableMetrics(queryMetric);
    }

    // Check for slow queries
    if (duration > this.config.slowQueryThresholdMs) {
      this.handleSlowQuery(queryMetric);
    }

    // Check for query errors
    if (error) {
      this.handleQueryError(queryMetric);
    }

    this.emit('queryExecuted', queryMetric);
  }

  /**
   * Get query history
   */
  public getQueryHistory(limit = 100): QueryMetric[] {
    return this.queryHistory
      .slice(-limit)
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Get slow queries
   */
  public getSlowQueries(limit = 50): QueryMetric[] {
    return this.queryHistory
      .filter(query => query.duration > this.config.slowQueryThresholdMs)
      .sort((a, b) => b.duration - a.duration)
      .slice(0, limit);
  }

  /**
   * Get table access patterns
   */
  public getTableAccessPatterns(): TableMetric[] {
    return Array.from(this.tableMetrics.values())
      .sort((a, b) => b.accessCount - a.accessCount);
  }

  /**
   * Get database health summary
   */
  public async getHealthSummary(): Promise<{
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
    uptime: number;
    lastCheck: Date;
  }> {
    try {
      return await this.getHealthMetrics();
    } catch (error) {
      logger.error('Failed to get database health summary', { error });
      return {
        status: 'critical',
        issues: ['Failed to check database health'],
        uptime: 0,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Get connection metrics
   */
  private async getConnectionMetrics(): Promise<DatabaseMetrics['connections']> {
    try {
      // Get current connection stats
      const connectionQuery = `
        SELECT 
          count(*) as total_connections,
          count(*) FILTER (WHERE state = 'active') as active_connections,
          count(*) FILTER (WHERE state = 'idle') as idle_connections
        FROM pg_stat_activity 
        WHERE datname = current_database()
      `;

      const result = await database.query(connectionQuery) as any;
      const connectionStats = result.rows?.[0] || {};

      // Get max connections setting
      const maxConnResult = await database.query('SHOW max_connections') as any;
      const maxConnections = parseInt(maxConnResult.rows?.[0]?.max_connections) || 100;

      const active = parseInt(connectionStats.active_connections) || 0;
      const idle = parseInt(connectionStats.idle_connections) || 0;
      const total = parseInt(connectionStats.total_connections) || 0;

      return {
        active,
        idle,
        total,
        maxAllowed: maxConnections,
        utilizationPercentage: (total / maxConnections) * 100
      };

    } catch (error) {
      logger.error('Failed to get connection metrics', { error });
      return {
        active: 0,
        idle: 0,
        total: 0,
        maxAllowed: 100,
        utilizationPercentage: 0
      };
    }
  }

  /**
   * Get query metrics
   */
  private getQueryMetrics(): DatabaseMetrics['queries'] {
    const now = Date.now();
    const timeWindow = now - this.lastMetricsCalculation;
    const recentQueries = this.queryHistory.filter(
      q => now - q.timestamp.getTime() < timeWindow
    );

    const totalExecuted = this.queryHistory.length;
    const averageTime = recentQueries.length > 0 
      ? recentQueries.reduce((sum, q) => sum + q.duration, 0) / recentQueries.length
      : 0;

    const slowQueries = this.queryHistory.filter(
      q => q.duration > this.config.slowQueryThresholdMs
    ).length;

    const failedQueries = this.queryHistory.filter(q => q.error).length;

    const queriesPerSecond = timeWindow > 0 
      ? (recentQueries.length / (timeWindow / 1000))
      : 0;

    const longestQuery = this.queryHistory.length > 0
      ? this.queryHistory.reduce((longest, current) => 
          current.duration > longest.duration ? current : longest
        )
      : null;

    this.lastMetricsCalculation = now;

    return {
      totalExecuted,
      averageTime,
      slowQueries,
      failedQueries,
      queriesPerSecond,
      longestQuery
    };
  }

  /**
   * Get table metrics
   */
  private async getTableMetrics(): Promise<DatabaseMetrics['tables']> {
    try {
      // Get table statistics from PostgreSQL
      const tableStatsQuery = `
        SELECT 
          schemaname,
          tablename,
          n_tup_ins as inserts,
          n_tup_upd as updates,
          n_tup_del as deletes,
          seq_scan as sequential_scans,
          seq_tup_read as sequential_reads,
          idx_scan as index_scans,
          idx_tup_fetch as index_reads
        FROM pg_stat_user_tables
        ORDER BY (seq_scan + idx_scan) DESC
        LIMIT 10
      `;

      const tableStatsResult = await database.query(tableStatsQuery) as any;
      const tableStats = new Map<string, TableStatistic>();

      // Get table sizes
      const tableSizeQuery = `
        SELECT 
          tablename,
          pg_total_relation_size(schemaname||'.'||tablename) as size_bytes
        FROM pg_tables 
        WHERE schemaname = 'public'
      `;

      const tableSizeResult = await database.query(tableSizeQuery) as any;
      const tableSizes = new Map<string, number>();

      (tableSizeResult.rows || []).forEach((row: any) => {
        tableSizes.set(row.tablename, parseInt(row.size_bytes) || 0);
      });

      // Process table statistics
      for (const row of (tableStatsResult.rows || [])) {
        const tableName = row.tablename;
        const sizeBytes = tableSizes.get(tableName) || 0;

        tableStats.set(tableName, {
          tableName,
          rowCount: parseInt(row.sequential_reads) + parseInt(row.index_reads),
          sizeInMB: sizeBytes / (1024 * 1024),
          indexCount: parseInt(row.index_scans) > 0 ? 1 : 0, // Simplified
          lastAnalyzed: new Date(), // Would need pg_stat_user_tables.last_analyze
          fragmentationPercentage: 0 // Would need more complex query
        });
      }

      // Get most accessed tables from our metrics
      const mostAccessed = Array.from(this.tableMetrics.values())
        .sort((a, b) => b.accessCount - a.accessCount)
        .slice(0, 10);

      return {
        mostAccessed,
        tableStats
      };

    } catch (error) {
      logger.error('Failed to get table metrics', { error });
      return {
        mostAccessed: [],
        tableStats: new Map()
      };
    }
  }

  /**
   * Get performance metrics
   */
  private async getPerformanceMetrics(): Promise<DatabaseMetrics['performance']> {
    try {
      // Get database statistics
      const statsQuery = `
        SELECT 
          xact_commit,
          xact_rollback,
          blks_read,
          blks_hit,
          tup_returned,
          tup_fetched,
          tup_inserted,
          tup_updated,
          tup_deleted
        FROM pg_stat_database 
        WHERE datname = current_database()
      `;

      const result = await database.query(statsQuery) as any;
      const stats = result.rows?.[0] || {};

      // Calculate cache hit ratio
      const blksRead = parseInt(stats.blks_read) || 0;
      const blksHit = parseInt(stats.blks_hit) || 0;
      const cacheHitRatio = (blksRead + blksHit) > 0 
        ? blksHit / (blksRead + blksHit) 
        : 1;

      // Calculate transactions per second (approximation)
      const commits = parseInt(stats.xact_commit) || 0;
      const rollbacks = parseInt(stats.xact_rollback) || 0;
      const uptime = (Date.now() - this.startTime.getTime()) / 1000;
      const transactionsPerSecond = uptime > 0 
        ? (commits + rollbacks) / uptime 
        : 0;

      // Get lock information
      const lockQuery = `
        SELECT count(*) as lock_count
        FROM pg_locks 
        WHERE NOT granted
      `;

      const lockResult = await database.query(lockQuery) as any;
      const lockCount = parseInt(lockResult.rows?.[0]?.lock_count) || 0;

      return {
        transactionsPerSecond,
        lockWaitTime: lockCount * 10, // Simplified estimate
        cacheHitRatio,
        indexEfficiency: cacheHitRatio // Simplified - would need more complex calculation
      };

    } catch (error) {
      logger.error('Failed to get performance metrics', { error });
      return {
        transactionsPerSecond: 0,
        lockWaitTime: 0,
        cacheHitRatio: 1,
        indexEfficiency: 1
      };
    }
  }

  /**
   * Get health metrics
   */
  private async getHealthMetrics(): Promise<DatabaseMetrics['health']> {
    const issues: string[] = [];
    let status: 'healthy' | 'warning' | 'critical' = 'healthy';

    try {
      // Check connections
      const connections = await this.getConnectionMetrics();
      if (connections.utilizationPercentage > this.config.connectionThresholds.critical) {
        issues.push(`Critical connection usage: ${connections.utilizationPercentage.toFixed(1)}%`);
        status = 'critical';
      } else if (connections.utilizationPercentage > this.config.connectionThresholds.warning) {
        issues.push(`High connection usage: ${connections.utilizationPercentage.toFixed(1)}%`);
        if (status === 'healthy') {status = 'warning';}
      }

      // Check performance
      const performance = await this.getPerformanceMetrics();
      if (performance.cacheHitRatio < this.config.performanceThresholds.lowCacheHitRatio) {
        issues.push(`Low cache hit ratio: ${(performance.cacheHitRatio * 100).toFixed(1)}%`);
        if (status === 'healthy') {status = 'warning';}
      }

      if (performance.lockWaitTime > this.config.performanceThresholds.highLockWaitMs) {
        issues.push(`High lock wait time: ${performance.lockWaitTime.toFixed(0)}ms`);
        if (status === 'healthy') {status = 'warning';}
      }

      // Check for too many slow queries
      const slowQueries = this.getSlowQueries(10);
      if (slowQueries.length > 5) {
        issues.push(`Many slow queries: ${slowQueries.length} recent slow queries`);
        if (status === 'healthy') {status = 'warning';}
      }

      // Check connectivity
      try {
        await database.query('SELECT 1');
      } catch (connectError) {
        issues.push(`Database connectivity error: ${connectError instanceof Error ? connectError.message : String(connectError)}`);
        status = 'critical';
      }
      
      return {
        status,
        issues,
        lastCheck: new Date(),
        uptime: (Date.now() - this.startTime.getTime()) / 1000
      };

    } catch (error) {
      return {
        status: 'critical',
        issues: [`Health check error: ${error instanceof Error ? error.message : String(error)}`],
        lastCheck: new Date(),
        uptime: (Date.now() - this.startTime.getTime()) / 1000
      };
    }
  }

  /**
   * Setup query monitoring hooks
   */
  private setupQueryMonitoring(): void {
    // Note: This would typically hook into the database client
    // For now, we'll rely on manual recordQuery calls
    logger.info('Query monitoring setup completed');
  }

  /**
   * Start periodic health checks
   */
  private startHealthChecks(): void {
    this.healthCheckInterval = setInterval(async () => {
      try {
        await this.getCurrentMetrics();
      } catch (error) {
        logger.error('Database health check failed', { error });
      }
    }, this.config.healthCheckInterval);
  }

  /**
   * Schedule metrics cleanup
   */
  private scheduleMetricsCleanup(): void {
    const cleanupIntervalMs = 60 * 60 * 1000; // 1 hour

    // Clear existing interval if any
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }

    this.cleanupInterval = setInterval(() => {
      this.cleanupOldMetrics();
    }, cleanupIntervalMs);
  }

  /**
   * Clean up old metrics data
   */
  private cleanupOldMetrics(): void {
    const cutoffTime = Date.now() - (this.config.metricsRetentionHours * 60 * 60 * 1000);

    // Clean query history
    this.queryHistory = this.queryHistory.filter(
      query => query.timestamp.getTime() > cutoffTime
    );

    // Clean metrics history
    this.metricsHistory = this.metricsHistory.filter(
      metrics => metrics.timestamp.getTime() > cutoffTime
    );

    logger.debug('Cleaned up old database metrics', {
      queryHistorySize: this.queryHistory.length,
      metricsHistorySize: this.metricsHistory.length
    });
  }

  /**
   * Handle slow query detection
   */
  private handleSlowQuery(queryMetric: QueryMetric): void {
    logger.warn('Slow query detected', {
      queryId: queryMetric.id,
      duration: queryMetric.duration,
      query: queryMetric.query,
      tableName: queryMetric.tableName
    });

    this.emit('slowQuery', queryMetric);

    // Broadcast slow query alert
    if (this.websocketServer) {
      this.websocketServer.broadcast('alerts', {
        type: 'slow_query',
        data: {
          queryId: queryMetric.id,
          duration: queryMetric.duration,
          query: queryMetric.query,
          timestamp: queryMetric.timestamp
        }
      });
    }
  }

  /**
   * Handle query error
   */
  private handleQueryError(queryMetric: QueryMetric): void {
    logger.error('Database query error', {
      queryId: queryMetric.id,
      error: queryMetric.error,
      query: queryMetric.query
    });

    this.emit('queryError', queryMetric);
  }

  /**
   * Update table metrics
   */
  private updateTableMetrics(queryMetric: QueryMetric): void {
    if (!queryMetric.tableName) {return;}

    const existing = this.tableMetrics.get(queryMetric.tableName) || {
      tableName: queryMetric.tableName,
      accessCount: 0,
      totalTime: 0,
      averageTime: 0,
      operations: {
        select: 0,
        insert: 0,
        update: 0,
        delete: 0
      }
    };

    existing.accessCount++;
    existing.totalTime += queryMetric.duration;
    existing.averageTime = existing.totalTime / existing.accessCount;

    // Update operation counts
    const operation = queryMetric.operation.toLowerCase();
    if (operation in existing.operations) {
      (existing.operations as any)[operation]++;
    }

    this.tableMetrics.set(queryMetric.tableName, existing);
  }

  /**
   * Utility functions
   */
  private sanitizeQuery(query: string): string {
    // Remove sensitive data and normalize query
    return query
      .replace(/VALUES\s*\([^)]*\)/gi, 'VALUES (...)')
      .replace(/=\s*'[^']*'/gi, "= '***'")
      .replace(/=\s*\d+/gi, '= ***')
      .substring(0, 200);
  }

  private determineOperation(query: string): QueryMetric['operation'] {
    const normalizedQuery = query.trim().toUpperCase();
    
    if (normalizedQuery.startsWith('SELECT')) {return 'SELECT';}
    if (normalizedQuery.startsWith('INSERT')) {return 'INSERT';}
    if (normalizedQuery.startsWith('UPDATE')) {return 'UPDATE';}
    if (normalizedQuery.startsWith('DELETE')) {return 'DELETE';}
    
    return 'OTHER';
  }

  private extractTableName(query: string): string | undefined {
    // Simple table name extraction
    const patterns = [
      /FROM\s+(\w+)/i,
      /UPDATE\s+(\w+)/i,
      /INSERT\s+INTO\s+(\w+)/i,
      /DELETE\s+FROM\s+(\w+)/i
    ];

    for (const pattern of patterns) {
      const match = query.match(pattern);
      if (match && match[1]) {
        return match[1].toLowerCase();
      }
    }

    return undefined;
  }

  /**
   * Get metrics for specific time range
   */
  public getMetricsHistory(
    startTime: Date,
    endTime: Date = new Date()
  ): DatabaseMetrics[] {
    return this.metricsHistory.filter(
      metrics => metrics.timestamp >= startTime && metrics.timestamp <= endTime
    );
  }

  /**
   * Export metrics data
   */
  public exportMetrics(): {
    queries: QueryMetric[];
    tables: TableMetric[];
    metricsHistory: DatabaseMetrics[];
    summary: {
      totalQueries: number;
      slowQueries: number;
      errorQueries: number;
      monitoringDuration: number;
    };
  } {
    return {
      queries: this.queryHistory,
      tables: Array.from(this.tableMetrics.values()),
      metricsHistory: this.metricsHistory,
      summary: {
        totalQueries: this.queryHistory.length,
        slowQueries: this.queryHistory.filter(q => q.duration > this.config.slowQueryThresholdMs).length,
        errorQueries: this.queryHistory.filter(q => q.error).length,
        monitoringDuration: (Date.now() - this.startTime.getTime()) / 1000
      }
    };
  }

  /**
   * Shutdown the database monitor service
   */
  public shutdown(): void {
    this.stop();
    this.queryHistory = [];
    this.tableMetrics.clear();
    this.metricsHistory = [];
    this.removeAllListeners();
    
    logger.info('Database Monitor Service shut down');
  }
}

export default DatabaseMonitorService;

/**
 * Metrics Collector Service
 * 
 * Centralized metrics collection and aggregation service that gathers metrics
 * from all monitoring services and provides unified metrics storage and querying.
 */

import { EventEmitter } from 'events';

import type { ApplicationMetrics } from './application-monitor.service';
import logger from './logging.service';
import type { SystemMetrics } from './system-monitor.service';

export interface MetricValue {
  timestamp: Date;
  value: number;
  labels?: Record<string, string>;
}

export interface MetricSeries {
  name: string;
  type: 'counter' | 'gauge' | 'histogram' | 'summary';
  unit?: string;
  description?: string;
  values: MetricValue[];
}

export interface AggregatedMetrics {
  system: {
    cpu: {
      usage: MetricSeries;
      loadAverage: MetricSeries;
    };
    memory: {
      usage: MetricSeries;
      percentage: MetricSeries;
    };
    disk: {
      usage: MetricSeries;
      percentage: MetricSeries;
    };
    process: {
      uptime: MetricSeries;
      memoryUsage: MetricSeries;
    };
  };
  application: {
    performance: {
      responseTime: MetricSeries;
      throughput: MetricSeries;
      errorRate: MetricSeries;
    };
    database: {
      queryTime: MetricSeries;
      queryCount: MetricSeries;
      connectionPool: MetricSeries;
    };
    websocket: {
      activeConnections: MetricSeries;
      messagesPerSecond: MetricSeries;
    };
  };
  trading: {
    bots: {
      activeCount: MetricSeries;
      totalTrades: MetricSeries;
      totalPnL: MetricSeries;
      winRate: MetricSeries;
    };
    orders: {
      placed: MetricSeries;
      filled: MetricSeries;
      cancelled: MetricSeries;
      failed: MetricSeries;
    };
    positions: {
      open: MetricSeries;
      unrealizedPnL: MetricSeries;
    };
  };
  custom: Record<string, MetricSeries>;
}

export interface MetricsQuery {
  metric: string;
  startTime?: Date;
  endTime?: Date;
  labels?: Record<string, string>;
  aggregation?: 'avg' | 'sum' | 'min' | 'max' | 'count' | 'last';
  groupBy?: string[];
  interval?: number; // milliseconds for time-based grouping
}

export interface QueryResult {
  metric: string;
  values: Array<{
    timestamp: Date;
    value: number;
    labels?: Record<string, string>;
  }>;
  aggregation?: string;
  interval?: number;
}

export interface MetricsCollectorConfig {
  retentionPeriod: number; // milliseconds
  maxMetricsPerSeries: number;
  aggregationInterval: number; // milliseconds
  enablePersistence: boolean;
  persistenceInterval: number; // milliseconds
}

export class MetricsCollectorService extends EventEmitter {
  private config: MetricsCollectorConfig;
  private metrics: Map<string, MetricSeries> = new Map();
  private aggregatedMetrics: AggregatedMetrics;
  private aggregationInterval?: NodeJS.Timeout;
  private persistenceInterval?: NodeJS.Timeout;
  private isRunning = false;

  constructor(config: Partial<MetricsCollectorConfig> = {}) {
    super();
    
    this.config = {
      retentionPeriod: 24 * 60 * 60 * 1000, // 24 hours
      maxMetricsPerSeries: 10000,
      aggregationInterval: 60000, // 1 minute
      enablePersistence: true,
      persistenceInterval: 5 * 60 * 1000, // 5 minutes
      ...config
    };

    this.aggregatedMetrics = this.initializeAggregatedMetrics();
    logger.info('Metrics Collector Service initialized', { config: this.config });
  }

  /**
   * Start metrics collection
   */
  public start(): void {
    if (this.isRunning) {
      logger.warn('Metrics Collector is already running');
      return;
    }

    this.isRunning = true;

    // Start aggregation interval
    this.aggregationInterval = setInterval(() => {
      this.performAggregation();
      this.cleanupOldMetrics();
    }, this.config.aggregationInterval);

    // Start persistence interval if enabled
    if (this.config.enablePersistence) {
      this.persistenceInterval = setInterval(() => {
        void this.persistMetrics();
      }, this.config.persistenceInterval);
    }

    logger.info('Metrics Collector started', {
      aggregationInterval: this.config.aggregationInterval,
      persistenceEnabled: this.config.enablePersistence
    });

    this.emit('started');
  }

  /**
   * Stop metrics collection
   */
  public stop(): void {
    if (!this.isRunning) {
      return;
    }

    this.isRunning = false;

    if (this.aggregationInterval) {
      clearInterval(this.aggregationInterval);
      this.aggregationInterval = undefined;
    }

    if (this.persistenceInterval) {
      clearInterval(this.persistenceInterval);
      this.persistenceInterval = undefined;
    }

    logger.info('Metrics Collector stopped');
    this.emit('stopped');
  }

  /**
   * Record a metric value
   */
  public recordMetric(
    name: string,
    value: number,
    type: MetricSeries['type'] = 'gauge',
    options: {
      unit?: string;
      description?: string;
      labels?: Record<string, string>;
      timestamp?: Date;
    } = {}
  ): void {
    if (!this.isRunning) {
      throw new Error('Metrics collector is not running. Call start() first.');
    }

    const metricValue: MetricValue = {
      timestamp: options.timestamp || new Date(),
      value,
      labels: options.labels
    };

    let series = this.metrics.get(name);
    
    if (!series) {
      series = {
        name,
        type,
        unit: options.unit,
        description: options.description,
        values: []
      };
      this.metrics.set(name, series);
    }

    // Add the value
    series.values.push(metricValue);

    // Trim if exceeding max size
    if (series.values.length > this.config.maxMetricsPerSeries) {
      series.values = series.values.slice(-this.config.maxMetricsPerSeries / 2);
    }

    // Emit metric event
    this.emit('metric', { name, value: metricValue, series });

    logger.debug('Metric recorded', {
      name,
      value,
      type,
      labels: options.labels
    });
  }

  /**
   * Record counter increment
   */
  public incrementCounter(name: string, amount = 1, labels?: Record<string, string>): void {
    const existing = this.getLatestValue(name);
    const newValue = (existing?.value || 0) + amount;
    
    this.recordMetric(name, newValue, 'counter', { labels });
  }

  /**
   * Record gauge value
   */
  public recordGauge(name: string, value: number, labels?: Record<string, string>): void {
    this.recordMetric(name, value, 'gauge', { labels });
  }

  /**
   * Record histogram value (simplified implementation)
   */
  public recordHistogram(name: string, value: number, labels?: Record<string, string>): void {
    // For simplicity, we'll store histogram values as individual points
    // In a production system, you'd want proper histogram buckets
    this.recordMetric(`${name}_value`, value, 'histogram', { labels });
    this.incrementCounter(`${name}_count`, 1, labels);
  }

  /**
   * Process system metrics
   */
  public processSystemMetrics(metrics: SystemMetrics): void {
    const timestamp = metrics.timestamp;

    // CPU metrics
    this.recordMetric('system.cpu.usage', metrics.cpu.usage, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'CPU usage percentage'
    });

    this.recordMetric('system.cpu.load_average_1m', metrics.cpu.loadAverage.at(0) || 0, 'gauge', {
      unit: 'load',
      timestamp,
      description: '1-minute load average'
    });

    // Memory metrics
    this.recordMetric('system.memory.usage', metrics.memory.used, 'gauge', {
      unit: 'bytes',
      timestamp,
      description: 'Memory usage in bytes'
    });

    this.recordMetric('system.memory.percentage', metrics.memory.percentage, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'Memory usage percentage'
    });

    // Disk metrics
    this.recordMetric('system.disk.usage', metrics.disk.used, 'gauge', {
      unit: 'bytes',
      timestamp,
      description: 'Disk usage in bytes'
    });

    this.recordMetric('system.disk.percentage', metrics.disk.percentage, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'Disk usage percentage'
    });

    // Process metrics
    this.recordMetric('system.process.uptime', metrics.process.uptime, 'gauge', {
      unit: 'seconds',
      timestamp,
      description: 'Process uptime in seconds'
    });

    this.recordMetric('system.process.memory', metrics.process.memoryUsage.heapUsed, 'gauge', {
      unit: 'bytes',
      timestamp,
      description: 'Process heap memory usage'
    });
  }

  /**
   * Process application metrics
   */
  public processApplicationMetrics(metrics: ApplicationMetrics): void {
    const timestamp = metrics.timestamp;

    // Performance metrics
    this.recordMetric('app.performance.response_time_avg', metrics.performance.responseTime.average, 'gauge', {
      unit: 'milliseconds',
      timestamp,
      description: 'Average response time'
    });

    this.recordMetric('app.performance.throughput', metrics.performance.throughput.requestsPerSecond, 'gauge', {
      unit: 'requests/second',
      timestamp,
      description: 'Requests per second'
    });

    this.recordMetric('app.performance.error_rate', metrics.performance.errorRate, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'Error rate percentage'
    });

    // Database metrics
    this.recordMetric('app.database.query_time_avg', metrics.database.averageQueryTime, 'gauge', {
      unit: 'milliseconds',
      timestamp,
      description: 'Average database query time'
    });

    this.recordMetric('app.database.query_count', metrics.database.queryCount, 'counter', {
      unit: 'queries',
      timestamp,
      description: 'Total database queries'
    });

    this.recordMetric('app.database.active_connections', metrics.database.activeConnections, 'gauge', {
      unit: 'connections',
      timestamp,
      description: 'Active database connections'
    });

    // WebSocket metrics
    this.recordMetric('app.websocket.active_connections', metrics.websocket.activeConnections, 'gauge', {
      unit: 'connections',
      timestamp,
      description: 'Active WebSocket connections'
    });

    this.recordMetric('app.websocket.messages_per_second', metrics.websocket.messagesPerSecond, 'gauge', {
      unit: 'messages/second',
      timestamp,
      description: 'WebSocket messages per second'
    });
  }

  /**
   * Process trading metrics
   */
  public processTradingMetrics(metrics: {
    activeBots: number;
    totalTrades: number;
    totalPnL: number;
    winRate: number;
    ordersPlaced: number;
    ordersFilled: number;
    ordersCancelled: number;
    ordersFailed: number;
    openPositions: number;
    unrealizedPnL: number;
    timestamp?: Date;
  }): void {
    const timestamp = metrics.timestamp || new Date();

    // Bot metrics
    this.recordMetric('trading.bots.active', metrics.activeBots, 'gauge', {
      unit: 'bots',
      timestamp,
      description: 'Number of active trading bots'
    });

    this.recordMetric('trading.bots.total_trades', metrics.totalTrades, 'counter', {
      unit: 'trades',
      timestamp,
      description: 'Total trades executed'
    });

    this.recordMetric('trading.bots.total_pnl', metrics.totalPnL, 'gauge', {
      unit: 'currency',
      timestamp,
      description: 'Total profit and loss'
    });

    this.recordMetric('trading.bots.win_rate', metrics.winRate, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'Win rate percentage'
    });

    // Order metrics
    this.recordMetric('trading.orders.placed', metrics.ordersPlaced, 'counter', {
      unit: 'orders',
      timestamp,
      description: 'Orders placed'
    });

    this.recordMetric('trading.orders.filled', metrics.ordersFilled, 'counter', {
      unit: 'orders',
      timestamp,
      description: 'Orders filled'
    });

    this.recordMetric('trading.orders.cancelled', metrics.ordersCancelled, 'counter', {
      unit: 'orders',
      timestamp,
      description: 'Orders cancelled'
    });

    this.recordMetric('trading.orders.failed', metrics.ordersFailed, 'counter', {
      unit: 'orders',
      timestamp,
      description: 'Orders failed'
    });

    // Position metrics
    this.recordMetric('trading.positions.open', metrics.openPositions, 'gauge', {
      unit: 'positions',
      timestamp,
      description: 'Open positions'
    });

    this.recordMetric('trading.positions.unrealized_pnl', metrics.unrealizedPnL, 'gauge', {
      unit: 'currency',
      timestamp,
      description: 'Unrealized profit and loss'
    });
  }

  /**
   * Query metrics
   */
  public queryMetrics(query: MetricsQuery): QueryResult {
    const series = this.metrics.get(query.metric);
    
    if (!series) {
      return {
        metric: query.metric,
        values: []
      };
    }

    let values = [...series.values];

    // Filter by time range
    if (query.startTime) {
      values = values.filter(v => v.timestamp >= query.startTime!);
    }
    
    if (query.endTime) {
      values = values.filter(v => v.timestamp <= query.endTime!);
    }

    // Filter by labels
    if (query.labels) {
      values = values.filter(v => {
        if (!v.labels) {return false;}
        
        for (const [key, value] of Object.entries(query.labels!)) {
          if (v.labels[key] !== value) {return false;}
        }
        
        return true;
      });
    }

    // Apply aggregation
    if (query.aggregation && values.length > 0) {
      const aggregatedValue = this.aggregateValues(values, query.aggregation);
      const lastValue = values.at(-1);
      if (lastValue) {
        return {
          metric: query.metric,
          values: [{
            timestamp: lastValue.timestamp,
            value: aggregatedValue,
            labels: query.labels
          }],
          aggregation: query.aggregation
        };
      }
    }

    // Group by interval if specified
    if (query.interval && query.interval > 0) {
      values = this.groupByInterval(values, query.interval);
    }

    return {
      metric: query.metric,
      values: values.map(v => ({
        timestamp: v.timestamp,
        value: v.value,
        labels: v.labels
      })),
      interval: query.interval
    };
  }

  /**
   * Get all metric names
   */
  public getMetricNames(): string[] {
    return Array.from(this.metrics.keys());
  }

  /**
   * Get metric series
   */
  public getMetricSeries(name: string): MetricSeries | undefined {
    return this.metrics.get(name);
  }

  /**
   * Get latest value for a metric
   */
  public getLatestValue(name: string): MetricValue | undefined {
    const series = this.metrics.get(name);
    if (!series || series.values.length === 0) {
      return undefined;
    }
    
    return series.values[series.values.length - 1];
  }

  /**
   * Get aggregated metrics
   */
  public getAggregatedMetrics(): AggregatedMetrics {
    return this.aggregatedMetrics;
  }

  /**
   * Get metrics summary for dashboard
   */
  public getMetricsSummary(): {
    totalMetrics: number;
    totalDataPoints: number;
    oldestDataPoint: Date | null;
    newestDataPoint: Date | null;
    memoryUsage: number;
  } {
    let totalDataPoints = 0;
    let oldestDataPoint: Date | null = null;
    let newestDataPoint: Date | null = null;

    for (const series of this.metrics.values()) {
      totalDataPoints += series.values.length;
      
      if (series.values.length > 0) {
        const firstValue = series.values.at(0);
        const lastValue = series.values[series.values.length - 1];
        
        if (firstValue && lastValue) {
          const oldest = firstValue.timestamp;
          const newest = lastValue.timestamp;
          
          if (!oldestDataPoint || oldest < oldestDataPoint) {
            oldestDataPoint = oldest;
          }
          
          if (!newestDataPoint || newest > newestDataPoint) {
            newestDataPoint = newest;
          }
        }
      }
    }

    return {
      totalMetrics: this.metrics.size,
      totalDataPoints,
      oldestDataPoint,
      newestDataPoint,
      memoryUsage: process.memoryUsage().heapUsed
    };
  }

  /**
   * Initialize aggregated metrics structure
   */
  private initializeAggregatedMetrics(): AggregatedMetrics {
    const createSeries = (name: string, type: MetricSeries['type'] = 'gauge'): MetricSeries => ({
      name,
      type,
      values: []
    });

    return {
      system: {
        cpu: {
          usage: createSeries('system.cpu.usage'),
          loadAverage: createSeries('system.cpu.load_average_1m')
        },
        memory: {
          usage: createSeries('system.memory.usage'),
          percentage: createSeries('system.memory.percentage')
        },
        disk: {
          usage: createSeries('system.disk.usage'),
          percentage: createSeries('system.disk.percentage')
        },
        process: {
          uptime: createSeries('system.process.uptime'),
          memoryUsage: createSeries('system.process.memory')
        }
      },
      application: {
        performance: {
          responseTime: createSeries('app.performance.response_time_avg'),
          throughput: createSeries('app.performance.throughput'),
          errorRate: createSeries('app.performance.error_rate')
        },
        database: {
          queryTime: createSeries('app.database.query_time_avg'),
          queryCount: createSeries('app.database.query_count', 'counter'),
          connectionPool: createSeries('app.database.active_connections')
        },
        websocket: {
          activeConnections: createSeries('app.websocket.active_connections'),
          messagesPerSecond: createSeries('app.websocket.messages_per_second')
        }
      },
      trading: {
        bots: {
          activeCount: createSeries('trading.bots.active'),
          totalTrades: createSeries('trading.bots.total_trades', 'counter'),
          totalPnL: createSeries('trading.bots.total_pnl'),
          winRate: createSeries('trading.bots.win_rate')
        },
        orders: {
          placed: createSeries('trading.orders.placed', 'counter'),
          filled: createSeries('trading.orders.filled', 'counter'),
          cancelled: createSeries('trading.orders.cancelled', 'counter'),
          failed: createSeries('trading.orders.failed', 'counter')
        },
        positions: {
          open: createSeries('trading.positions.open'),
          unrealizedPnL: createSeries('trading.positions.unrealized_pnl')
        }
      },
      custom: {}
    };
  }

  /**
   * Perform aggregation
   */
  private performAggregation(): void {
    // Update aggregated metrics with latest values
    for (const [key, series] of this.metrics) {
      if (series.values.length === 0) {continue;}
      
      const latestValue = series.values[series.values.length - 1];
      
      // Update aggregated structure
      if (key.startsWith('system.cpu.usage') && latestValue) {
        this.aggregatedMetrics.system.cpu.usage.values.push(latestValue);
      } else if (key.startsWith('system.memory.percentage') && latestValue) {
        this.aggregatedMetrics.system.memory.percentage.values.push(latestValue);
      }
      // Add more mappings as needed...
    }

    this.emit('aggregation', this.aggregatedMetrics);
  }

  /**
   * Aggregate values using specified method
   */
  private aggregateValues(values: MetricValue[], method: string): number {
    if (values.length === 0) {return 0;}

    switch (method) {
      case 'avg':
        return values.reduce((sum, v) => sum + v.value, 0) / values.length;
      case 'sum':
        return values.reduce((sum, v) => sum + v.value, 0);
      case 'min':
        return Math.min(...values.map(v => v.value));
      case 'max':
        return Math.max(...values.map(v => v.value));
      case 'count':
        return values.length;
      case 'last':
        return values.length > 0 ? values.at(-1)?.value ?? 0 : 0;
      default:
        return values.length > 0 ? values.at(-1)?.value ?? 0 : 0;
    }
  }

  /**
   * Group values by time interval
   */
  private groupByInterval(values: MetricValue[], interval: number): MetricValue[] {
    if (values.length === 0) {return [];}

    const firstValue = values.at(0);
    if (!firstValue) {return [];}

    const grouped: MetricValue[] = [];
    const startTime = firstValue.timestamp.getTime();
    
    let currentBucket = Math.floor((firstValue.timestamp.getTime() - startTime) / interval);
    let bucketValues: MetricValue[] = [];

    for (const value of values) {
      const bucket = Math.floor((value.timestamp.getTime() - startTime) / interval);
      
      if (bucket === currentBucket) {
        bucketValues.push(value);
      } else {
        // Process current bucket
        if (bucketValues.length > 0) {
          const avgValue = bucketValues.reduce((sum, v) => sum + v.value, 0) / bucketValues.length;
          const firstBucketValue = bucketValues.at(0);
          grouped.push({
            timestamp: new Date(startTime + currentBucket * interval),
            value: avgValue,
            labels: firstBucketValue?.labels
          });
        }
        
        // Start new bucket
        currentBucket = bucket;
        bucketValues = [value];
      }
    }

    // Process last bucket
    if (bucketValues.length > 0) {
      const avgValue = bucketValues.reduce((sum, v) => sum + v.value, 0) / bucketValues.length;
      const firstBucketValue = bucketValues.at(0);
      grouped.push({
        timestamp: new Date(startTime + currentBucket * interval),
        value: avgValue,
        labels: firstBucketValue?.labels
      });
    }

    return grouped;
  }

  /**
   * Clean up old metrics
   */
  private cleanupOldMetrics(): void {
    const cutoff = new Date(Date.now() - this.config.retentionPeriod);
    let totalRemoved = 0;

    for (const [name, series] of this.metrics) {
      const initialLength = series.values.length;
      series.values = series.values.filter(v => v.timestamp >= cutoff);
      totalRemoved += initialLength - series.values.length;
    }

    if (totalRemoved > 0) {
      logger.debug(`Cleaned up ${totalRemoved} old metric values`);
    }
  }

  /**
   * Persist metrics to storage (placeholder implementation)
   */
  private async persistMetrics(): Promise<void> {
    try {
      // In a real implementation, this would save metrics to a database
      // For now, we'll just log the operation
      const summary = this.getMetricsSummary();
      logger.debug('Metrics persistence checkpoint', {
        totalMetrics: summary.totalMetrics,
        totalDataPoints: summary.totalDataPoints
      });
      
      this.emit('persistence', summary);
    } catch (error) {
      logger.error('Failed to persist metrics', { error });
    }
  }

  /**
   * Reset all metrics
   */
  public reset(): void {
    this.metrics.clear();
    this.aggregatedMetrics = this.initializeAggregatedMetrics();
    
    logger.info('Metrics Collector reset');
    this.emit('reset');
  }

  /**
   * Shutdown the metrics collector
   */
  public shutdown(): void {
    this.stop();
    
    // Perform final persistence if enabled
    if (this.config.enablePersistence) {
      this.persistMetrics();
    }
    
    this.reset();
    this.removeAllListeners();
    
    logger.info('Metrics Collector shut down');
  }
}

