/**
 * Strategy Plugin Manager
 * 
 * Comprehensive plugin system for custom trading strategies with security,
 * validation, and dynamic loading capabilities.
 */

import fs from 'fs/promises';
import path from 'path';

import { z } from 'zod';

import type { 
  IStrategy, 
  StrategyConfig, 
  StrategyContext, 
  ConfigValidationResult,
  StrategyResult 
} from '../JabbrLabs/target-reacher/interfaces';
import logger from '../services/logging.service';

// Plugin metadata interface
export interface PluginMetadata {
  name: string;
  version: string;
  description: string;
  author: string;
  supportedMarkets: string[];
  riskLevel: 'low' | 'medium' | 'high';
  category: string;
  tags: string[];
  createdAt: string;
  updatedAt: string;
}

// Plugin information for registry
export interface PluginInfo {
  id: string;
  metadata: PluginMetadata;
  filePath: string;
  isLoaded: boolean;
  loadedAt?: string;
  error?: string;
  instance?: IStrategy;
}

// Plugin configuration schema for validation
const PluginMetadataSchema = z.object({
  name: z.string().min(1).max(100),
  version: z.string().regex(/^\d+\.\d+\.\d+$/),
  description: z.string().min(1).max(500),
  author: z.string().min(1).max(100),
  supportedMarkets: z.array(z.string()).min(1),
  riskLevel: z.enum(['low', 'medium', 'high']),
  category: z.string().min(1).max(50),
  tags: z.array(z.string()).optional().default([]),
  createdAt: z.string().datetime(),
  updatedAt: z.string().datetime()
});

// Plugin wrapper for sandboxing
export interface PluginWrapper {
  execute(context: StrategyContext): Promise<StrategyResult>;
  validateConfig(config: Record<string, unknown>): ConfigValidationResult;
  getDefaultConfig(): StrategyConfig;
  cleanup(): Promise<void>;
}

export class StrategyPluginManager {
  private pluginRegistry: Map<string, PluginInfo> = new Map();
  private pluginDirectory: string;
  private allowedDependencies: Set<string>;
  private securityEnabled: boolean;

  constructor(
    pluginDirectory: string = path.join(process.cwd(), 'plugins'),
    securityEnabled = true
  ) {
    this.pluginDirectory = pluginDirectory;
    this.securityEnabled = securityEnabled;
    
    // Whitelist of allowed dependencies for security
    this.allowedDependencies = new Set([
      'lodash',
      'moment',
      'decimal.js',
      'ta-lib',
      'technicalindicators'
    ]);
  }

  /**
   * Initialize the plugin manager
   */
  async initialize(): Promise<void> {
    try {
      // Ensure plugin directory exists
      await this.ensurePluginDirectory();
      
      // Load existing plugins
      await this.scanAndLoadPlugins();
      
      logger.info(`Strategy Plugin Manager initialized with ${this.pluginRegistry.size} plugins`);
    } catch (error) {
      logger.error('Failed to initialize Strategy Plugin Manager', { error });
      throw error;
    }
  }

  /**
   * Register a new plugin from file path
   */
  async registerPlugin(filePath: string): Promise<string> {
    try {
      const absolutePath = path.resolve(filePath);
      
      // Security check: ensure plugin is in allowed directory
      if (this.securityEnabled && !absolutePath.startsWith(path.resolve(this.pluginDirectory))) {
        throw new Error('Plugin must be located in the designated plugin directory');
      }

      // Load and validate plugin metadata
      const pluginModule = await this.loadPluginModule(absolutePath);
      const metadata = await this.validatePluginMetadata(pluginModule.metadata);
      
      // Generate unique plugin ID
      const pluginId = this.generatePluginId(metadata.name, metadata.version);
      
      // Create plugin info
      const pluginInfo: PluginInfo = {
        id: pluginId,
        metadata,
        filePath: absolutePath,
        isLoaded: false
      };

      // Validate plugin implementation
      await this.validatePluginImplementation(pluginModule);
      
      // Register plugin
      this.pluginRegistry.set(pluginId, pluginInfo);
      
      logger.info(`Plugin registered: ${metadata.name} v${metadata.version}`, {
        pluginId,
        author: metadata.author,
        markets: metadata.supportedMarkets
      });

      return pluginId;
    } catch (error) {
      logger.error(`Failed to register plugin: ${filePath}`, { error });
      throw error;
    }
  }

  /**
   * Load a plugin and create an instance
   */
  async loadPlugin(pluginId: string, config: StrategyConfig, context: StrategyContext): Promise<IStrategy> {
    try {
      const pluginInfo = this.pluginRegistry.get(pluginId);
      if (!pluginInfo) {
        throw new Error(`Plugin not found: ${pluginId}`);
      }

      // Load plugin module
      const pluginModule = await this.loadPluginModule(pluginInfo.filePath);
      
      // Validate configuration
      const tempInstance = new pluginModule.default();
      const configValidation = tempInstance.validateConfig(config.parameters || {});
      
      if (!configValidation.valid) {
        throw new Error(`Invalid plugin configuration: ${configValidation.errors.map((e: any) => e.message).join(', ')}`);
      }

      // Create plugin instance with dependency injection
      const pluginInstance = new pluginModule.default();
      await pluginInstance.initialize(context);
      
      // Update plugin info
      pluginInfo.isLoaded = true;
      pluginInfo.loadedAt = new Date().toISOString();
      pluginInfo.instance = pluginInstance;
      pluginInfo.error = undefined;

      logger.info(`Plugin loaded: ${pluginInfo.metadata.name}`, {
        pluginId,
        botId: context.botConfig?.id
      });

      return pluginInstance;
    } catch (error) {
      // Update plugin info with error
      const pluginInfo = this.pluginRegistry.get(pluginId);
      if (pluginInfo) {
        pluginInfo.error = error instanceof Error ? error.message : String(error);
        pluginInfo.isLoaded = false;
      }

      logger.error(`Failed to load plugin: ${pluginId}`, { error });
      throw error;
    }
  }

  /**
   * Unload a plugin
   */
  async unloadPlugin(pluginId: string): Promise<void> {
    try {
      const pluginInfo = this.pluginRegistry.get(pluginId);
      if (!pluginInfo || !pluginInfo.isLoaded) {
        return;
      }

      // Cleanup plugin instance
      if (pluginInfo.instance && typeof pluginInfo.instance.cleanup === 'function') {
        await pluginInfo.instance.cleanup({} as StrategyContext);
      }

      // Clear module cache to allow hot reloading
      delete require.cache[require.resolve(pluginInfo.filePath)];

      // Update plugin info
      pluginInfo.isLoaded = false;
      pluginInfo.instance = undefined;
      pluginInfo.loadedAt = undefined;

      logger.info(`Plugin unloaded: ${pluginInfo.metadata.name}`, { pluginId });
    } catch (error) {
      logger.error(`Failed to unload plugin: ${pluginId}`, { error });
      throw error;
    }
  }

  /**
   * Get all registered plugins
   */
  getPlugins(): PluginInfo[] {
    return Array.from(this.pluginRegistry.values());
  }

  /**
   * Get plugin by ID
   */
  getPlugin(pluginId: string): PluginInfo | undefined {
    return this.pluginRegistry.get(pluginId);
  }

  /**
   * Search plugins by criteria
   */
  searchPlugins(criteria: {
    name?: string;
    category?: string;
    riskLevel?: string;
    supportedMarket?: string;
    tags?: string[];
  }): PluginInfo[] {
    return this.getPlugins().filter(plugin => {
      if (criteria.name && !plugin.metadata.name.toLowerCase().includes(criteria.name.toLowerCase())) {
        return false;
      }
      if (criteria.category && plugin.metadata.category !== criteria.category) {
        return false;
      }
      if (criteria.riskLevel && plugin.metadata.riskLevel !== criteria.riskLevel) {
        return false;
      }
      if (criteria.supportedMarket && !plugin.metadata.supportedMarkets.includes(criteria.supportedMarket)) {
        return false;
      }
      if (criteria.tags && !criteria.tags.some(tag => plugin.metadata.tags.includes(tag))) {
        return false;
      }
      return true;
    });
  }

  /**
   * Validate plugin configuration
   */
  async validatePluginConfig(pluginId: string, config: Record<string, unknown>): Promise<ConfigValidationResult> {
    try {
      const pluginInfo = this.pluginRegistry.get(pluginId);
      if (!pluginInfo) {
        return {
          valid: false,
          errors: [{ field: 'plugin', message: 'Plugin not found', code: 'PLUGIN_NOT_FOUND' }],
          warnings: []
        };
      }

      // Load plugin module if not already loaded
      const pluginModule = await this.loadPluginModule(pluginInfo.filePath);
      const tempInstance = new pluginModule.default();
      
      return tempInstance.validateConfig(config);
    } catch (error) {
      return {
        valid: false,
        errors: [{ field: 'validation', message: error instanceof Error ? error.message : String(error), code: 'VALIDATION_ERROR' }],
        warnings: []
      };
    }
  }

  /**
   * Private: Ensure plugin directory exists
   */
  private async ensurePluginDirectory(): Promise<void> {
    try {
      await fs.access(this.pluginDirectory);
    } catch {
      await fs.mkdir(this.pluginDirectory, { recursive: true });
      logger.info(`Created plugin directory: ${this.pluginDirectory}`);
    }
  }

  /**
   * Private: Scan and load all plugins in directory
   */
  private async scanAndLoadPlugins(): Promise<void> {
    try {
      const files = await fs.readdir(this.pluginDirectory);
      const pluginFiles = files.filter(file => file.endsWith('.js') || file.endsWith('.ts'));

      for (const file of pluginFiles) {
        try {
          const filePath = path.join(this.pluginDirectory, file);
          await this.registerPlugin(filePath);
        } catch (error) {
          logger.warn(`Failed to load plugin file: ${file}`, { error });
        }
      }
    } catch (error) {
      logger.error('Failed to scan plugin directory', { error });
    }
  }

  /**
   * Private: Load plugin module with security checks
   */
  private async loadPluginModule(filePath: string): Promise<any> {
    try {
      // Ensure we have an absolute path
      const absolutePath = path.isAbsolute(filePath) ? filePath : path.resolve(process.cwd(), filePath);
      
      // Security: Check file exists and is readable
      await fs.access(absolutePath, fs.constants.R_OK);
      
      // Security: Validate file extension
      const ext = path.extname(absolutePath);
      if (!['.js', '.ts'].includes(ext)) {
        throw new Error('Invalid plugin file extension. Only .js and .ts files are allowed.');
      }

      // Clear module cache for hot reloading
      if (require.cache[require.resolve(absolutePath)]) {
        delete require.cache[require.resolve(absolutePath)];
      }
      
      // Dynamic import for ES modules or require for CommonJS
      let pluginModule;
      try {
        pluginModule = await import(absolutePath);
      } catch (importError: unknown) {
        try {
          pluginModule = require(absolutePath);
        } catch (requireError: unknown) {
          const importMsg = importError instanceof Error ? importError.message : 'Import error';
          const requireMsg = requireError instanceof Error ? requireError.message : 'Require error';
          throw new Error(`Failed to import plugin: ${importMsg || requireMsg}`);
        }
      }

      if (!pluginModule.default) {
        throw new Error('Plugin must have a default export');
      }

      return pluginModule;
    } catch (error) {
      throw new Error(`Failed to load plugin module: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Private: Validate plugin metadata
   */
  private async validatePluginMetadata(metadata: any): Promise<PluginMetadata> {
    try {
      return PluginMetadataSchema.parse(metadata);
    } catch (error) {
      if (error instanceof z.ZodError) {
        const errorMessages = error.errors.map(e => `${e.path.join('.')}: ${e.message}`).join(', ');
        throw new Error(`Invalid plugin metadata: ${errorMessages}`);
      }
      throw error;
    }
  }

  /**
   * Private: Validate plugin implementation
   */
  private async validatePluginImplementation(pluginModule: any): Promise<void> {
    const PluginClass = pluginModule.default;
    
    if (typeof PluginClass !== 'function') {
      throw new Error('Plugin default export must be a class constructor');
    }

    // Create temporary instance to check interface compliance
    const tempInstance = new PluginClass();
    
    // Check required methods
    const requiredMethods = ['initialize', 'execute', 'cleanup', 'validateConfig', 'getDefaultConfig'];
    for (const method of requiredMethods) {
      if (typeof (tempInstance as any)[method] !== 'function') {
        throw new Error(`Plugin must implement ${method} method`);
      }
    }

    // Check required properties
    const requiredProperties = ['name', 'version', 'description', 'supportedMarkets'];
    for (const prop of requiredProperties) {
      if ((tempInstance as any)[prop] === undefined) {
        throw new Error(`Plugin must have ${prop} property`);
      }
    }
  }

  /**
   * Private: Generate unique plugin ID
   */
  private generatePluginId(name: string, version: string): string {
    const sanitizedName = name.toLowerCase().replace(/[^a-z0-9-]/g, '-');
    const sanitizedVersion = version.replace(/[^0-9.]/g, '');
    return `${sanitizedName}-${sanitizedVersion}`;
  }
}

// Export singleton instance
export const strategyPluginManager = new StrategyPluginManager();

import type { Server as HTTPServer } from 'http';
import type { IncomingMessage } from 'http';
import { parse as parseUrl } from 'url';

import type { 
  WebSocketMessage, 
  WebSocketResponse} from '@jabbr/shared';
import { 
  CONSTANTS 
} from '@jabbr/shared';
import { WebSocketServer, WebSocket } from 'ws';

import { AuthService } from '../auth/auth.service';
import { database } from '../database/database.config';
import BotStatusService from '../services/bot-status.service';

/**
 * WebSocket Connection with user context
 */
interface AuthenticatedConnection {
  ws: WebSocket;
  userId: string;
  email: string;
  connectedAt: Date;
  lastHeartbeat: Date;
  subscribedChannels: Set<string>;
  sessionId: string;
}

/**
 * WebSocket Server for real-time trading bot communication
 * Handles authentication, channel subscriptions, and message routing
 */
export class JabbrWebSocketServer {
  private wss: WebSocketServer;
  private authService: AuthService;
  private botStatusService: BotStatusService;
  private connections: Map<string, AuthenticatedConnection> = new Map();
  private channels: Map<string, Set<string>> = new Map(); // channel -> sessionIds
  private heartbeatInterval: NodeJS.Timeout | null = null;
  private cleanupInterval: NodeJS.Timeout | null = null;

  // Configuration
  private readonly HEARTBEAT_INTERVAL = 30000; // 30 seconds
  private readonly CONNECTION_TIMEOUT = 60000; // 60 seconds
  private readonly MAX_CONNECTIONS_PER_USER = 5;

  constructor(httpServer: HTTPServer) {
    this.authService = new AuthService();
    
    // Create WebSocket server
    this.wss = new WebSocketServer({
      server: httpServer,
      path: '/ws',
      verifyClient: this.verifyClient.bind(this)
    });

    this.botStatusService = new BotStatusService(this);

    this.setupEventHandlers();
    this.startHeartbeat();
    this.startCleanup();

    console.log('üîå WebSocket server initialized on /ws');
  }

  public getBotStatusService(): BotStatusService {
    return this.botStatusService;
  }

  /**
   * Verify client connection during WebSocket handshake
   */
  private verifyClient(info: { origin: string; secure: boolean; req: IncomingMessage }): boolean {
    try {
      const url = parseUrl(info.req.url || '', true);
      const token = url.query.token as string;

      if (!token) {
        console.log('‚ùå WebSocket connection rejected: No token provided');
        return false;
      }

      // Verify JWT token
      const decoded = this.authService.verifyAccessToken(token);
      if (!decoded) {
        console.log('‚ùå WebSocket connection rejected: Invalid token');
        return false;
      }

      // Store user info for connection setup
      (info.req as any).user = decoded;
      return true;
    } catch (error) {
      console.error('‚ùå WebSocket verification error:', error);
      return false;
    }
  }

  /**
   * Setup WebSocket event handlers
   */
  private setupEventHandlers(): void {
    this.wss.on('connection', this.handleConnection.bind(this));
    this.wss.on('error', this.handleServerError.bind(this));

    console.log('üì° WebSocket event handlers configured');
  }

  /**
   * Handle new WebSocket connection
   */
  private handleConnection(ws: WebSocket, req: IncomingMessage): void {
    try {
      const user = (req as any).user;
      if (!user) {
        ws.close(1008, 'Authentication required');
        return;
      }

      // Check connection limits
      const existingConnections = Array.from(this.connections.values())
        .filter(conn => conn.userId === user.userId);
      
      if (existingConnections.length >= this.MAX_CONNECTIONS_PER_USER) {
        ws.close(1008, 'Too many connections');
        return;
      }

      // Create authenticated connection
      const sessionId = this.generateSessionId();
      const connection: AuthenticatedConnection = {
        ws,
        userId: user.userId,
        email: user.email,
        connectedAt: new Date(),
        lastHeartbeat: new Date(),
        subscribedChannels: new Set(),
        sessionId
      };

      // Store connection
      this.connections.set(sessionId, connection);

      // Setup connection event handlers
      ws.on('message', (data) => this.handleMessage(sessionId, data));
      ws.on('close', () => this.handleDisconnection(sessionId));
      ws.on('error', (error) => this.handleConnectionError(sessionId, error));
      ws.on('pong', () => this.handlePong(sessionId));

      // Send welcome message
      this.sendToConnection(sessionId, {
        type: 'connection',
        channel: CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH,
        data: {
          status: 'connected',
          sessionId,
          timestamp: new Date().toISOString(),
          availableChannels: Object.values(CONSTANTS.WS_CHANNELS)
        }
      });

      console.log(`‚úÖ WebSocket connected: ${user.email} (${sessionId})`);
      
      // Log connection to database
      this.logConnectionEvent(user.userId, 'connected', sessionId);

    } catch (error) {
      console.error('‚ùå Connection setup error:', error);
      ws.close(1011, 'Internal server error');
    }
  }

  /**
   * Handle incoming WebSocket message
   */
  private handleMessage(sessionId: string, data: any): void {
    try {
      const connection = this.connections.get(sessionId);
      if (!connection) {return;}

      // Convert data to string if it's a Buffer
      const messageString = data instanceof Buffer ? data.toString() : data.toString();
      const message: WebSocketMessage = JSON.parse(messageString);
      
      // Update heartbeat
      connection.lastHeartbeat = new Date();

      // Route message based on type
      switch (message.type) {
        case 'subscribe':
          this.handleSubscription(sessionId, message);
          break;
        case 'unsubscribe':
          this.handleUnsubscription(sessionId, message);
          break;
        case 'ping':
          this.handlePing(sessionId);
          break;
        case 'bot_command':
          this.handleBotCommand(sessionId, message);
          break;
        default:
          this.sendError(sessionId, `Unknown message type: ${message.type}`);
      }

    } catch (error) {
      console.error(`‚ùå Message handling error for ${sessionId}:`, error);
      this.sendError(sessionId, 'Invalid message format');
    }
  }

  /**
   * Handle channel subscription
   */
  private handleSubscription(sessionId: string, message: WebSocketMessage): void {
    const connection = this.connections.get(sessionId);
    if (!connection) {return;}

    const channel = message.channel;
    if (!channel || !this.isValidChannel(channel)) {
      this.sendError(sessionId, `Invalid channel: ${channel}`);
      return;
    }

    // Add to connection's subscribed channels
    connection.subscribedChannels.add(channel);

    // Add to global channel mapping
    if (!this.channels.has(channel)) {
      this.channels.set(channel, new Set());
    }
    this.channels.get(channel)!.add(sessionId);

    // Send confirmation
    this.sendToConnection(sessionId, {
      type: 'subscribed',
      channel,
      data: {
        status: 'subscribed',
        channel,
        timestamp: new Date().toISOString()
      }
    });

    console.log(`üì∫ ${connection.email} subscribed to ${channel}`);
  }

  /**
   * Handle channel unsubscription
   */
  private handleUnsubscription(sessionId: string, message: WebSocketMessage): void {
    const connection = this.connections.get(sessionId);
    if (!connection) {return;}

    const channel = message.channel;
    if (!channel) {return;}

    // Remove from connection's subscribed channels
    connection.subscribedChannels.delete(channel);

    // Remove from global channel mapping
    const channelSessions = this.channels.get(channel);
    if (channelSessions) {
      channelSessions.delete(sessionId);
      if (channelSessions.size === 0) {
        this.channels.delete(channel);
      }
    }

    // Send confirmation
    this.sendToConnection(sessionId, {
      type: 'unsubscribed',
      channel,
      data: {
        status: 'unsubscribed',
        channel,
        timestamp: new Date().toISOString()
      }
    });

    console.log(`üì∫ ${connection.email} unsubscribed from ${channel}`);
  }

  /**
   * Handle ping message
   */
  private handlePing(sessionId: string): void {
    this.sendToConnection(sessionId, {
      type: 'pong',
      channel: CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH,
      data: {
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Handle pong response
   */
  private handlePong(sessionId: string): void {
    const connection = this.connections.get(sessionId);
    if (connection) {
      connection.lastHeartbeat = new Date();
    }
  }

  /**
   * Handle bot command messages
   */
  private handleBotCommand(sessionId: string, message: WebSocketMessage): void {
    // This will be expanded when we implement bot management
    console.log(`ü§ñ Bot command from ${sessionId}:`, message.data);
    
    // For now, just acknowledge
    this.sendToConnection(sessionId, {
      type: 'bot_command_ack',
      channel: CONSTANTS.WS_CHANNELS.BOT_STATUS,
      data: {
        status: 'received',
        command: message.data,
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Handle connection disconnection
   */
  private handleDisconnection(sessionId: string): void {
    const connection = this.connections.get(sessionId);
    if (!connection) {return;}

    // Remove from all channels
    for (const channel of connection.subscribedChannels) {
      const channelSessions = this.channels.get(channel);
      if (channelSessions) {
        channelSessions.delete(sessionId);
        if (channelSessions.size === 0) {
          this.channels.delete(channel);
        }
      }
    }

    // Remove connection
    this.connections.delete(sessionId);

    console.log(`‚ùå WebSocket disconnected: ${connection.email} (${sessionId})`);
    
    // Log disconnection to database
    this.logConnectionEvent(connection.userId, 'disconnected', sessionId);
  }

  /**
   * Handle connection error
   */
  private handleConnectionError(sessionId: string, error: Error): void {
    console.error(`‚ùå WebSocket connection error for ${sessionId}:`, error);
    
    const connection = this.connections.get(sessionId);
    if (connection) {
      this.logConnectionEvent(connection.userId, 'error', sessionId, error.message);
    }
  }

  /**
   * Handle server error
   */
  private handleServerError(error: Error): void {
    console.error('‚ùå WebSocket server error:', error);
  }

  /**
   * Send message to specific connection
   */
  private sendToConnection(sessionId: string, message: WebSocketResponse): void {
    const connection = this.connections.get(sessionId);
    if (!connection || connection.ws.readyState !== WebSocket.OPEN) {
      return;
    }

    try {
      connection.ws.send(JSON.stringify(message));
    } catch (error) {
      console.error(`‚ùå Failed to send message to ${sessionId}:`, error);
    }
  }

  /**
   * Send error message to connection
   */
  private sendError(sessionId: string, error: string): void {
    this.sendToConnection(sessionId, {
      type: 'error',
      channel: CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH,
      data: {
        error,
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Broadcast message to all subscribers of a channel
   */
  public broadcast(channel: string, message: Omit<WebSocketResponse, 'channel'>): void {
    const sessionIds = this.channels.get(channel);
    if (!sessionIds || sessionIds.size === 0) {return;}

    const fullMessage: WebSocketResponse = {
      ...message,
      channel
    };

    for (const sessionId of sessionIds) {
      this.sendToConnection(sessionId, fullMessage);
    }

    console.log(`üì° Broadcasted to ${sessionIds.size} subscribers on ${channel}`);
  }

  /**
   * Send message to specific user (all their connections)
   */
  public sendToUser(userId: string, message: WebSocketResponse): void {
    const userConnections = Array.from(this.connections.values())
      .filter(conn => conn.userId === userId);

    for (const connection of userConnections) {
      this.sendToConnection(connection.sessionId, message);
    }
  }

  /**
   * Start heartbeat monitoring
   */
  private startHeartbeat(): void {
    this.heartbeatInterval = setInterval(() => {
      for (const connection of this.connections.values()) {
        if (connection.ws.readyState === WebSocket.OPEN) {
          connection.ws.ping();
        }
      }
    }, this.HEARTBEAT_INTERVAL);

    console.log('üíì WebSocket heartbeat started');
  }

  /**
   * Start connection cleanup
   */
  private startCleanup(): void {
    this.cleanupInterval = setInterval(() => {
      const now = new Date();
      const expiredConnections: string[] = [];

      for (const connection of this.connections.values()) {
        const timeSinceHeartbeat = now.getTime() - connection.lastHeartbeat.getTime();
        
        if (timeSinceHeartbeat > this.CONNECTION_TIMEOUT) {
          expiredConnections.push(connection.sessionId);
        }
      }

      // Clean up expired connections
      for (const sessionId of expiredConnections) {
        const connection = this.connections.get(sessionId);
        if (connection) {
          console.log(`üßπ Cleaning up expired connection: ${connection.email} (${sessionId})`);
          connection.ws.terminate();
          this.handleDisconnection(sessionId);
        }
      }

    }, this.HEARTBEAT_INTERVAL);

    console.log('üßπ WebSocket cleanup started');
  }

  /**
   * Generate unique session ID
   */
  private generateSessionId(): string {
    return `ws_${Date.now()}_${Math.random().toString(36).substring(2)}`;
  }

  /**
   * Log connection event to database
   */
  private async logConnectionEvent(
    userId: string, 
    event: string, 
    sessionId: string, 
    details?: string
  ): Promise<void> {
    try {
      await database.query(`
        INSERT INTO logs (level, message, category, user_id, metadata)
        VALUES ($1, $2, $3, $4, $5)
      `, [
        'info',
        `WebSocket ${event}`,
        'websocket',
        userId,
        JSON.stringify({
          event,
          sessionId,
          details,
          timestamp: new Date().toISOString()
        })
      ]);
    } catch (error) {
      console.error('‚ùå Failed to log connection event:', error);
    }
  }

  /**
   * Get server statistics
   */
  public getStats(): {
    connections: number;
    channels: number;
    connectionsByChannel: Record<string, number>;
    connectionsByUser: Record<string, number>;
  } {
    const connectionsByChannel: Record<string, number> = {};
    const connectionsByUser: Record<string, number> = {};

    // Count connections by channel
    for (const [channel, sessions] of this.channels) {
      if (typeof channel === 'string') {
        connectionsByChannel[channel] = sessions.size;
      }
    }

    // Count connections by user
    for (const connection of this.connections.values()) {
      connectionsByUser[connection.userId] = (connectionsByUser[connection.userId] || 0) + 1;
    }

    return {
      connections: this.connections.size,
      channels: this.channels.size,
      connectionsByChannel,
      connectionsByUser
    };
  }

  /**
   * Graceful shutdown
   */
  public async shutdown(): Promise<void> {
    console.log('üîå Shutting down WebSocket server...');

    // Clear intervals
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
    }
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }

    // Close all connections
    for (const connection of this.connections.values()) {
      connection.ws.close(1001, 'Server shutting down');
    }

    // Close server
    this.wss.close();

    console.log('‚úÖ WebSocket server shutdown complete');
  }

  /**
   * Check if channel is valid
   */
  private isValidChannel(channel: string): boolean {
    return Object.values(CONSTANTS.WS_CHANNELS).includes(channel as any);
  }
}

