"use client";
import { zodResolver } from "@hookform/resolvers/zod";
import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import * as z from "zod";

// Example strategies and their parameters
const STRATEGIES = {
  SMA: {
    label: "Simple Moving Average",
    params: [
      { name: "period", label: "Period", type: "number", min: 1, max: 200, default: 14 },
      { name: "threshold", label: "Threshold", type: "number", min: 0, max: 100, default: 50 },
    ],
  },
  EMA: {
    label: "Exponential Moving Average",
    params: [
      { name: "period", label: "Period", type: "number", min: 1, max: 200, default: 14 },
      { name: "multiplier", label: "Multiplier", type: "number", min: 1, max: 10, default: 2 },
    ],
  },
  Custom: {
    label: "Custom Script",
    params: [
      { name: "script", label: "Strategy Script", type: "textarea" },
    ],
  },
};

const MARKET_TYPES = [
  { value: "spot", label: "Spot" },
  { value: "futures", label: "Futures" },
];

const schema = z.object({
  botName: z.string().min(3, "Bot name must be at least 3 characters"),
  marketType: z.enum(["spot", "futures"]),
  strategy: z.enum(["SMA", "EMA", "Custom"]),
  parameters: z.record(z.any()),
});

type FormData = z.infer<typeof schema>;

export default function BotCreatePage() {
  const [strategy, setStrategy] = useState<keyof typeof STRATEGIES>("SMA");
  const [marketType, setMarketType] = useState("spot");
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isSubmitting },
    reset,
  } = useForm<FormData>({
    resolver: zodResolver(schema),
    defaultValues: {
      botName: "",
      marketType: "spot",
      strategy: "SMA",
      parameters: {},
    },
  });

  // Watch for changes to update dynamic fields
  const selectedStrategy = watch("strategy");
  const selectedMarketType = watch("marketType");

  useEffect(() => {
    if (selectedStrategy) {setStrategy(selectedStrategy as keyof typeof STRATEGIES);}
  }, [selectedStrategy]);
  useEffect(() => {
    if (selectedMarketType) {setMarketType(selectedMarketType);}
  }, [selectedMarketType]);

  // Dynamic parameter fields
  const parameterFields = STRATEGIES[strategy].params;

  // Example: market-specific parameters
  const marketSpecificFields =
    marketType === "futures"
      ? [
          { name: "leverage", label: "Leverage", type: "number", min: 1, max: 100, default: 10 },
          { name: "marginType", label: "Margin Type", type: "select", options: ["isolated", "cross"], default: "isolated" },
        ]
      : [];

  // Combine all parameter fields
  const allFields = [...parameterFields, ...marketSpecificFields];

  const onSubmit = async (data: FormData) => {
    // Prepare payload
    const payload = {
      botName: data.botName,
      marketType: data.marketType,
      strategy: data.strategy,
      parameters: data.parameters,
    };
    try {
      const res = await fetch("/api/bots", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (!res.ok) {throw new Error("Failed to create bot");}
      reset();
      alert("Bot created successfully!");
    } catch (err: unknown) {
      if (err instanceof Error) {
        alert(err.message);
      } else {
        alert("Error creating bot");
      }
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded shadow">
      <h1 className="text-2xl font-bold mb-4">Create New Trading Bot</h1>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div>
          <label className="block font-medium">Bot Name</label>
          <input
            {...register("botName")}
            className="w-full border rounded px-3 py-2 mt-1"
            placeholder="Enter bot name"
            disabled={isSubmitting}
          />
          {errors.botName && <span className="text-red-500 text-sm">{errors.botName.message}</span>}
        </div>
        <div>
          <label className="block font-medium">Market Type</label>
          <select
            {...register("marketType")}
            className="w-full border rounded px-3 py-2 mt-1"
            disabled={isSubmitting}
          >
            {MARKET_TYPES.map((m) => (
              <option key={m.value} value={m.value}>
                {m.label}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label className="block font-medium">Strategy</label>
          <select
            {...register("strategy")}
            className="w-full border rounded px-3 py-2 mt-1"
            disabled={isSubmitting}
          >
            {Object.entries(STRATEGIES).map(([key, s]) => (
              <option key={key} value={key}>
                {s.label}
              </option>
            ))}
          </select>
        </div>
        {/* Dynamic parameter fields */}
        {allFields.map((field) => (
          <div key={field.name}>
            <label className="block font-medium">{field.label}</label>
            {field.type === "select" && "options" in field ? (
              <select
                {...register(`parameters.${field.name}`)}
                className="w-full border rounded px-3 py-2 mt-1"
                defaultValue={"default" in field ? String(field.default) : undefined}
                disabled={isSubmitting}
              >
                {Array.isArray(field.options) && field.options.map((opt: string) => (
                  <option key={opt} value={opt}>
                    {opt}
                  </option>
                ))}
              </select>
            ) : field.type === "textarea" ? (
              <textarea
                {...register(`parameters.${field.name}`)}
                className="w-full border rounded px-3 py-2 mt-1"
                rows={4}
                placeholder={field.label}
                disabled={isSubmitting}
              />
            ) : (
              <input
                type={field.type}
                {...register(`parameters.${field.name}`)}
                className="w-full border rounded px-3 py-2 mt-1"
                min={"min" in field ? String(field.min) : undefined}
                max={"max" in field ? String(field.max) : undefined}
                defaultValue={"default" in field ? String(field.default) : undefined}
                step={field.type === "number" ? 1 : undefined}
                disabled={isSubmitting}
              />
            )}
          </div>
        ))}
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50"
          disabled={isSubmitting}
        >
          {isSubmitting ? "Creating..." : "Create Bot"}
        </button>
      </form>
    </div>
  );
}

"use client";

import { CONSTANTS } from '@jabbr/shared/src';
import type { Bot, BotStatus } from '@jabbr/shared/src/types';
import Link from 'next/link';
import React, { useState, useEffect } from 'react';

import StrategyMonitor from '../../components/StrategyMonitor';
import useWebSocket from '../../hooks/useWebSocket';


// Enhanced filter options
interface BotFilters {
  status?: BotStatus | 'all';
  strategy?: string | 'all';
  exchange?: string | 'all';
  search?: string;
}

// Sort options
type SortField = 'name' | 'status' | 'strategy' | 'exchange' | 'createdAt' | 'performance';
type SortDirection = 'asc' | 'desc';

interface SortConfig {
  field: SortField;
  direction: SortDirection;
}

export default function BotsPage() {
  const [bots, setBots] = useState<Bot[]>([]);
  const [filteredBots, setFilteredBots] = useState<Bot[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedBot, setSelectedBot] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'bots' | 'strategies'>('bots');
  const [filters, setFilters] = useState<BotFilters>({ status: 'all', strategy: 'all', exchange: 'all', search: '' });
  const [sortConfig, setSortConfig] = useState<SortConfig>({ field: 'createdAt', direction: 'desc' });
  const [showFilters, setShowFilters] = useState(false);

  const {
    isConnected,
    subscribe
  } = useWebSocket({
    url: 'ws://localhost:3002/ws',
    onOpen: () => {
      // Subscribe to bot status updates
      subscribe(CONSTANTS.WS_CHANNELS.BOT_STATUS);
    },
    onMessage: (message) => {
      // Handle real-time bot status updates
      if (message.channel === CONSTANTS.WS_CHANNELS.BOT_STATUS && message.data) {
        setBots(prevBots => 
          prevBots.map(bot => 
            bot.id === message.data.botId 
              ? { ...bot, status: message.data.status }
              : bot
          )
        );
      }
    }
  });

  // Fetch bots on component mount
  useEffect(() => {
    fetchBots();
  }, []);

  // Filter and sort bots when filters or sort config changes
  useEffect(() => {
    let filtered = [...bots];

    // Apply filters
    if (filters.status && filters.status !== 'all') {
      filtered = filtered.filter(bot => bot.status === filters.status);
    }
    if (filters.strategy && filters.strategy !== 'all') {
      filtered = filtered.filter(bot => bot.strategy === filters.strategy);
    }
    if (filters.exchange && filters.exchange !== 'all') {
      filtered = filtered.filter(bot => bot.exchange === filters.exchange);
    }
    if (filters.search) {
      filtered = filtered.filter(bot => 
        bot.name.toLowerCase().includes(filters.search!.toLowerCase()) ||
        bot.description?.toLowerCase().includes(filters.search!.toLowerCase())
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      let aValue: any = a[sortConfig.field]; // eslint-disable-line @typescript-eslint/no-explicit-any
      let bValue: any = b[sortConfig.field]; // eslint-disable-line @typescript-eslint/no-explicit-any

      // Handle special cases
      if (sortConfig.field === 'performance') {
        aValue = a.performance?.totalPnL || 0;
        bValue = b.performance?.totalPnL || 0;
      }

      if (typeof aValue === 'string') {
        aValue = aValue.toLowerCase();
        bValue = bValue.toLowerCase();
      }

      if (sortConfig.direction === 'asc') {
        return aValue > bValue ? 1 : -1;
      } 
        return aValue < bValue ? 1 : -1;
      
    });

    setFilteredBots(filtered);
  }, [bots, filters, sortConfig]);

  const fetchBots = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await fetch('/api/bots', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` // JWT token
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch bots: ${response.statusText}`);
      }

      const data = await response.json();
      setBots(data.data || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch bots');
      console.error('Error fetching bots:', err);
    } finally {
      setLoading(false);
    }
  };

  // Bot lifecycle control functions
  const handleBotAction = async (botId: string, action: 'start' | 'stop' | 'pause' | 'resume') => {
    try {
      const response = await fetch(`/api/bots/${botId}/${action}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to ${action} bot`);
      }

      // Refresh bots list to get updated status
      await fetchBots();
    } catch (err) {
      alert(err instanceof Error ? err.message : `Failed to ${action} bot`);
    }
  };

  const getStatusColor = (status: BotStatus): string => {
    switch (status) {
      case 'running': return 'text-green-600 bg-green-100';
      case 'stopped': return 'text-gray-600 bg-gray-100';
      case 'paused': return 'text-yellow-600 bg-yellow-100';
      case 'starting': case 'stopping': case 'pausing': return 'text-blue-600 bg-blue-100';
      case 'error': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusIcon = (status: BotStatus): string => {
    switch (status) {
      case 'running': return 'üü¢';
      case 'stopped': return '‚ö´';
      case 'paused': return 'üü°';
      case 'starting': case 'stopping': case 'pausing': return 'üîÑ';
      case 'error': return 'üî¥';
      default: return '‚ö´';
    }
  };

  const canPerformAction = (status: BotStatus, action: string): boolean => {
    switch (action) {
      case 'start': return ['stopped', 'error'].includes(status);
      case 'stop': return ['running', 'paused'].includes(status);
      case 'pause': return status === 'running';
      case 'resume': return status === 'paused';
      default: return false;
    }
  };

  // Enhanced bot management functions
  const handleSort = (field: SortField) => {
    setSortConfig(prev => ({
      field,
      direction: prev.field === field && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const handleFilterChange = (filterType: keyof BotFilters, value: string) => {
    setFilters(prev => ({ ...prev, [filterType]: value }));
  };

  // Note: Bulk actions will be implemented in future iterations

  const deleteBot = async (botId: string) => {
    if (!confirm('Are you sure you want to delete this bot? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`/api/bots/${botId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete bot');
      }

      await fetchBots();
    } catch (err) {
      alert(err instanceof Error ? err.message : 'Failed to delete bot');
    }
  };

  // Get unique values for filter dropdowns
  const getUniqueStrategies = () => [...new Set(bots.map(bot => bot.strategy))];
  const getUniqueExchanges = () => [...new Set(bots.map(bot => bot.exchange))];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 p-8">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center justify-center h-64">
            <div className="text-lg text-gray-600">Loading bots...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex flex-col mb-8">
          <div className="flex justify-between items-center mb-6">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 mb-2">
                ü§ñ Trading Bots
              </h1>
              <p className="text-lg text-gray-600">
                Manage your automated trading bots ({filteredBots.length} of {bots.length} bots)
              </p>
            </div>
            
            <div className="flex space-x-4">
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors font-medium"
              >
                üîç {showFilters ? 'Hide' : 'Show'} Filters
              </button>
              
              <Link
                href="/bots/create"
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors font-medium"
              >
                ‚ûï Create New Bot
              </Link>
              
              <button
                onClick={fetchBots}
                className="bg-gray-600 text-white px-6 py-3 rounded-lg hover:bg-gray-700 transition-colors font-medium"
              >
                üîÑ Refresh
              </button>
            </div>
          </div>

          {/* Enhanced Filters */}
          {showFilters && (
            <div className="bg-white rounded-lg shadow-md p-6 mb-6">
              <h3 className="text-lg font-semibold mb-4">Filter & Search Bots</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {/* Search */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Search</label>
                  <input
                    type="text"
                    placeholder="Bot name or description..."
                    value={filters.search || ''}
                    onChange={(e) => handleFilterChange('search', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                {/* Status Filter */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
                  <select
                    value={filters.status || 'all'}
                    onChange={(e) => handleFilterChange('status', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="all">All Status</option>
                    <option value="running">Running</option>
                    <option value="stopped">Stopped</option>
                    <option value="paused">Paused</option>
                    <option value="error">Error</option>
                    <option value="starting">Starting</option>
                    <option value="stopping">Stopping</option>
                  </select>
                </div>

                {/* Strategy Filter */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Strategy</label>
                  <select
                    value={filters.strategy || 'all'}
                    onChange={(e) => handleFilterChange('strategy', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="all">All Strategies</option>
                    {getUniqueStrategies().map(strategy => (
                      <option key={strategy} value={strategy}>{strategy}</option>
                    ))}
                  </select>
                </div>

                {/* Exchange Filter */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Exchange</label>
                  <select
                    value={filters.exchange || 'all'}
                    onChange={(e) => handleFilterChange('exchange', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="all">All Exchanges</option>
                    {getUniqueExchanges().map(exchange => (
                      <option key={exchange} value={exchange}>{exchange}</option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Sort Options */}
              <div className="mt-4 pt-4 border-t border-gray-200">
                <label className="block text-sm font-medium text-gray-700 mb-2">Sort By</label>
                <div className="flex space-x-2">
                  {[
                    { field: 'name' as SortField, label: 'Name' },
                    { field: 'status' as SortField, label: 'Status' },
                    { field: 'strategy' as SortField, label: 'Strategy' },
                    { field: 'createdAt' as SortField, label: 'Created' },
                    { field: 'performance' as SortField, label: 'Performance' },
                  ].map(({ field, label }) => (
                    <button
                      key={field}
                      onClick={() => handleSort(field)}
                      className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                        sortConfig.field === field
                          ? 'bg-blue-600 text-white'
                          : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                      }`}
                    >
                      {label} {sortConfig.field === field && (sortConfig.direction === 'asc' ? '‚Üë' : '‚Üì')}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* WebSocket Status */}
        <div className="mb-6 bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium">Real-time Status:</span>
            <span className={`text-sm font-semibold ${isConnected ? 'text-green-600' : 'text-red-600'}`}>
              {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
            </span>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="mb-6 border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            <button
              onClick={() => setActiveTab('bots')}
              className={`py-2 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'bots'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              ü§ñ Bot Management
            </button>
            <button
              onClick={() => setActiveTab('strategies')}
              className={`py-2 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'strategies'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              üìä Strategy Monitor
            </button>
          </nav>
        </div>

        {/* Error Display */}
        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
            <strong>Error:</strong> {error}
          </div>
        )}

        {/* Tab Content */}
        {activeTab === 'bots' && (
          <>
            {/* Bots Grid */}
            {bots.length === 0 ? (
              <div className="bg-white rounded-lg shadow-md p-12 text-center">
                <div className="text-6xl mb-4">ü§ñ</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No Trading Bots Yet</h3>
                <p className="text-gray-600 mb-6">
                  Get started by creating your first automated trading bot
                </p>
                <Link
                  href="/bots/create"
                  className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors font-medium inline-block"
                >
                  Create Your First Bot
                </Link>
              </div>
            ) : filteredBots.length === 0 ? (
              <div className="bg-white rounded-lg shadow-md p-12 text-center">
                <div className="text-4xl mb-4">üîç</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No Bots Match Your Filters</h3>
                <p className="text-gray-600 mb-6">
                  Try adjusting your search criteria or clear the filters
                </p>
                <button
                  onClick={() => setFilters({ status: 'all', strategy: 'all', exchange: 'all', search: '' })}
                  className="bg-gray-600 text-white px-6 py-3 rounded-lg hover:bg-gray-700 transition-colors font-medium"
                >
                  Clear Filters
                </button>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredBots.map((bot) => (
                  <div key={bot.id} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow">
                    {/* Bot Header */}
                    <div className="flex justify-between items-start mb-4">
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-1">{bot.name}</h3>
                        <p className="text-sm text-gray-600">{bot.description || 'No description'}</p>
                      </div>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(bot.status)}`}>
                        {getStatusIcon(bot.status)} {bot.status.toUpperCase()}
                      </span>
                    </div>

                    {/* Bot Details */}
                    <div className="space-y-2 mb-4">
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Strategy:</span>
                        <span className="font-medium">{bot.strategy}</span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Exchange:</span>
                        <span className="font-medium">{bot.exchange}</span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Created:</span>
                        <span className="font-medium">{new Date(bot.createdAt).toLocaleDateString()}</span>
                      </div>
                    </div>

                    {/* Performance Metrics */}
                    {bot.performance && (
                      <div className="bg-gray-50 rounded p-3 mb-4">
                        <h4 className="text-sm font-medium text-gray-900 mb-2">Performance</h4>
                        <div className="grid grid-cols-2 gap-2 text-xs">
                          <div>
                            <span className="text-gray-600">Trades:</span>
                            <span className="font-medium ml-1">{bot.performance.totalTrades}</span>
                          </div>
                          <div>
                            <span className="text-gray-600">Win Rate:</span>
                            <span className="font-medium ml-1">{bot.performance.winRate}%</span>
                          </div>
                          <div>
                            <span className="text-gray-600">Total P&L:</span>
                            <span className={`font-medium ml-1 ${bot.performance.totalPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                              ${bot.performance.totalPnL.toFixed(2)}
                            </span>
                          </div>
                          <div>
                            <span className="text-gray-600">Max Drawdown:</span>
                            <span className={`font-medium ml-1 ${bot.performance.maxDrawdown >= 0 ? 'text-red-600' : 'text-green-600'}`}>
                              {bot.performance.maxDrawdown.toFixed(2)}%
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Bot Actions */}
                    <div className="flex space-x-2">
                      {canPerformAction(bot.status, 'start') && (
                        <button
                          onClick={() => handleBotAction(bot.id, 'start')}
                          className="flex-1 bg-green-600 text-white px-3 py-2 rounded text-sm hover:bg-green-700 transition-colors"
                        >
                          ‚ñ∂Ô∏è Start
                        </button>
                      )}
                      
                      {canPerformAction(bot.status, 'pause') && (
                        <button
                          onClick={() => handleBotAction(bot.id, 'pause')}
                          className="flex-1 bg-yellow-600 text-white px-3 py-2 rounded text-sm hover:bg-yellow-700 transition-colors"
                        >
                          ‚è∏Ô∏è Pause
                        </button>
                      )}
                      
                      {canPerformAction(bot.status, 'resume') && (
                        <button
                          onClick={() => handleBotAction(bot.id, 'resume')}
                          className="flex-1 bg-blue-600 text-white px-3 py-2 rounded text-sm hover:bg-blue-700 transition-colors"
                        >
                          ‚ñ∂Ô∏è Resume
                        </button>
                      )}
                      
                      {canPerformAction(bot.status, 'stop') && (
                        <button
                          onClick={() => handleBotAction(bot.id, 'stop')}
                          className="flex-1 bg-red-600 text-white px-3 py-2 rounded text-sm hover:bg-red-700 transition-colors"
                        >
                          ‚èπÔ∏è Stop
                        </button>
                      )}
                    </div>

                    {/* Enhanced Quick Actions */}
                    <div className="flex space-x-2 mt-3">
                      <button
                        onClick={() => setSelectedBot(selectedBot === bot.id ? null : bot.id)}
                        className="flex-1 bg-gray-200 text-gray-700 px-3 py-2 rounded text-sm hover:bg-gray-300 transition-colors"
                      >
                        üìä Details
                      </button>
                      <Link
                        href={`/bots/${bot.id}/edit`}
                        className="flex-1 bg-blue-200 text-blue-700 px-3 py-2 rounded text-sm hover:bg-blue-300 transition-colors text-center"
                      >
                        ‚öôÔ∏è Edit
                      </Link>
                      <button
                        onClick={() => deleteBot(bot.id)}
                        disabled={!canPerformAction(bot.status, 'stop')}
                        className="flex-1 bg-red-200 text-red-700 px-3 py-2 rounded text-sm hover:bg-red-300 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        üóëÔ∏è Delete
                      </button>
                    </div>

                    {/* Expanded Details */}
                    {selectedBot === bot.id && (
                      <div className="mt-4 p-3 bg-gray-50 rounded text-sm">
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <h5 className="font-medium mb-2">Configuration</h5>
                            <div className="space-y-1 text-xs">
                              <div><span className="font-medium">Symbol:</span> {bot.configuration.symbol}</div>
                              <div><span className="font-medium">Timeframe:</span> {bot.configuration.timeframe}</div>
                              <div><span className="font-medium">Leverage:</span> {bot.configuration.leverage}x</div>
                              <div><span className="font-medium">Trade Amount:</span> ${bot.configuration.tradeAmount}</div>
                            </div>
                          </div>
                          <div>
                            <h5 className="font-medium mb-2">Risk Management</h5>
                            <div className="space-y-1 text-xs">
                              <div><span className="font-medium">Max Daily Loss:</span> {bot.riskManagement.maxDailyLoss}%</div>
                              <div><span className="font-medium">Max Drawdown:</span> {bot.riskManagement.maxDrawdown}%</div>
                              <div><span className="font-medium">Max Trades:</span> {bot.riskManagement.maxConcurrentTrades}</div>
                              <div><span className="font-medium">Risk Score:</span> {bot.riskManagement.riskScore}/10</div>
                            </div>
                          </div>
                        </div>
                        <div className="mt-3 pt-3 border-t border-gray-200">
                          <h5 className="font-medium mb-2">Advanced Details</h5>
                          <div className="grid grid-cols-1 gap-2 text-xs">
                            <div><span className="font-medium">Bot ID:</span> {bot.id}</div>
                            <div><span className="font-medium">API Key ID:</span> {bot.exchangeApiKeyId}</div>
                            <div><span className="font-medium">Created:</span> {new Date(bot.createdAt).toLocaleString()}</div>
                            <div><span className="font-medium">Last Updated:</span> {new Date(bot.updatedAt).toLocaleString()}</div>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}

            {/* Enhanced Summary Stats */}
            {bots.length > 0 && (
              <div className="mt-8 bg-white rounded-lg shadow-md p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold">Portfolio Summary</h3>
                  {filteredBots.length !== bots.length && (
                    <span className="text-sm text-gray-500">
                      Showing {filteredBots.length} of {bots.length} bots
                    </span>
                  )}
                </div>
                <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-gray-900">{filteredBots.length}</div>
                    <div className="text-sm text-gray-600">Filtered Bots</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600">
                      {filteredBots.filter(bot => bot.status === 'running').length}
                    </div>
                    <div className="text-sm text-gray-600">Running</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-gray-600">
                      {filteredBots.filter(bot => bot.status === 'stopped').length}
                    </div>
                    <div className="text-sm text-gray-600">Stopped</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-yellow-600">
                      {filteredBots.filter(bot => bot.status === 'paused').length}
                    </div>
                    <div className="text-sm text-gray-600">Paused</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-red-600">
                      {filteredBots.filter(bot => bot.status === 'error').length}
                    </div>
                    <div className="text-sm text-gray-600">Error</div>
                  </div>
                </div>
                
                {/* Performance Summary */}
                {filteredBots.some(bot => bot.performance) && (
                  <div className="mt-6 pt-6 border-t border-gray-200">
                    <h4 className="text-md font-semibold mb-3">Performance Overview</h4>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div className="text-center">
                        <div className={`text-xl font-bold ${
                          filteredBots.reduce((sum, bot) => sum + (bot.performance?.totalPnL || 0), 0) >= 0
                            ? 'text-green-600' : 'text-red-600'
                        }`}>
                          ${filteredBots.reduce((sum, bot) => sum + (bot.performance?.totalPnL || 0), 0).toFixed(2)}
                        </div>
                        <div className="text-sm text-gray-600">Total P&L</div>
                      </div>
                      <div className="text-center">
                        <div className="text-xl font-bold text-gray-900">
                          {filteredBots.reduce((sum, bot) => sum + (bot.performance?.totalTrades || 0), 0)}
                        </div>
                        <div className="text-sm text-gray-600">Total Trades</div>
                      </div>
                      <div className="text-center">
                        <div className="text-xl font-bold text-blue-600">
                          {filteredBots.length > 0 
                            ? (filteredBots.reduce((sum, bot) => sum + (bot.performance?.winRate || 0), 0) / filteredBots.filter(bot => bot.performance).length || 0).toFixed(1)
                            : '0.0'
                          }%
                        </div>
                        <div className="text-sm text-gray-600">Avg Win Rate</div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}
          </>
        )}

        {activeTab === 'strategies' && (
          <StrategyMonitor />
        )}
      </div>
    </div>
  );
}

'use client';

import React from 'react';

import { componentClasses } from '../utils/theme';

interface LoadingSpinnerProps {
  size?: 'sm' | 'md' | 'lg' | 'xl';
  color?: 'primary' | 'secondary' | 'success' | 'warning' | 'error';
  className?: string;
}

export const LoadingSpinner: React.FC<LoadingSpinnerProps> = ({
  size = 'md',
  color = 'primary',
  className = '',
}) => {
  const sizeClasses = {
    sm: 'w-4 h-4',
    md: 'w-6 h-6',
    lg: 'w-8 h-8',
    xl: 'w-12 h-12',
  };

  const colorClasses = {
    primary: 'text-brand-primary',
    secondary: 'text-brand-secondary',
    success: 'text-status-success',
    warning: 'text-status-warning',
    error: 'text-status-error',
  };

  return (
    <div
      className={`animate-spin rounded-full border-2 border-current border-t-transparent ${sizeClasses[size]} ${colorClasses[color]} ${className}`}
      role="status"
      aria-label="Loading"
    >
      <span className="sr-only">Loading...</span>
    </div>
  );
};

interface LoadingStateProps {
  message?: string;
  size?: 'sm' | 'md' | 'lg';
  className?: string;
}

export const LoadingState: React.FC<LoadingStateProps> = ({
  message = 'Loading...',
  size = 'md',
  className = '',
}) => {
  return (
    <div className={`flex flex-col items-center justify-center p-8 ${className}`}>
      <LoadingSpinner size={size === 'sm' ? 'md' : size === 'lg' ? 'xl' : 'lg'} />
      <p className="mt-4 text-secondary text-sm">{message}</p>
    </div>
  );
};

interface LoadingOverlayProps {
  isVisible: boolean;
  message?: string;
  className?: string;
}

export const LoadingOverlay: React.FC<LoadingOverlayProps> = ({
  isVisible,
  message = 'Loading...',
  className = '',
}) => {
  if (!isVisible) {
    return null;
  }

  return (
    <div className={`fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 ${className}`}>
      <div className={`${componentClasses.card} p-8 text-center`}>
        <LoadingSpinner size="lg" className="mx-auto mb-4" />
        <p className="text-primary font-medium">{message}</p>
      </div>
    </div>
  );
};

interface LoadingCardProps {
  title?: string;
  message?: string;
  className?: string;
}

export const LoadingCard: React.FC<LoadingCardProps> = ({
  title = 'Loading',
  message = 'Please wait while we fetch your data...',
  className = '',
}) => {
  return (
    <div className={`${componentClasses.card} ${className}`}>
      <div className="text-center py-12">
        <LoadingSpinner size="lg" className="mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-primary mb-2">{title}</h3>
        <p className="text-secondary text-sm">{message}</p>
      </div>
    </div>
  );
};

interface LoadingTableProps {
  rows?: number;
  columns?: number;
  className?: string;
}

export const LoadingTable: React.FC<LoadingTableProps> = ({
  rows = 5,
  columns = 4,
  className = '',
}) => {
  return (
    <div className={`${componentClasses.card} ${className}`}>
      <div className="animate-pulse">
        {/* Table header */}
        <div className="flex space-x-4 p-4 border-b border-border">
          {Array.from({ length: columns }).map((_, index) => (
            <div
              key={`header-${index}`}
              className="h-4 bg-surface-secondary rounded flex-1"
            />
          ))}
        </div>
        
        {/* Table rows */}
        {Array.from({ length: rows }).map((_, rowIndex) => (
          <div key={`row-${rowIndex}`} className="flex space-x-4 p-4 border-b border-border last:border-b-0">
            {Array.from({ length: columns }).map((_, colIndex) => (
              <div
                key={`cell-${rowIndex}-${colIndex}`}
                className="h-4 bg-surface-secondary rounded flex-1"
              />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

interface LoadingButtonProps {
  isLoading: boolean;
  children: React.ReactNode;
  onClick?: () => void;
  disabled?: boolean;
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  variant?: 'primary' | 'secondary' | 'success' | 'warning' | 'error';
}

export const LoadingButton: React.FC<LoadingButtonProps> = ({
  isLoading,
  children,
  onClick,
  disabled = false,
  className = '',
  size = 'md',
  variant = 'primary',
}) => {
  const isDisabled = disabled || isLoading;
  
  const variantClasses = {
    primary: componentClasses.button.primary,
    secondary: componentClasses.button.secondary,
    success: componentClasses.button.success,
    warning: componentClasses.button.secondary, // fallback to secondary
    error: componentClasses.button.danger,
  };

  const spinnerSizes = {
    sm: 'sm' as const,
    md: 'sm' as const,
    lg: 'md' as const,
  };

  return (
    <button
      onClick={onClick}
      disabled={isDisabled}
      className={`${variantClasses[variant]} ${className} ${isDisabled ? 'opacity-50 cursor-not-allowed' : ''}`}
    >
      <div className="flex items-center justify-center space-x-2">
        {isLoading && <LoadingSpinner size={spinnerSizes[size]} color="primary" />}
        <span>{children}</span>
      </div>
    </button>
  );
};

export default LoadingSpinner;

/**
 * Strategy Monitor Component
 * 
 * Real-time monitoring component for strategy performance, signals, and execution.
 * Integrates with WebSocket to display live strategy data.
 */

"use client";

import type { StrategyPerformanceMetrics, SignalSummary} from '@jabbr/shared/src';
import { CONSTANTS, PositionSummary, RiskMetrics } from '@jabbr/shared/src';
import React, { useState } from 'react';

import useWebSocket from '../hooks/useWebSocket';

// Temporary type definitions until shared types are fully available
interface StrategyUpdateMessage {
  type: 'performance' | 'signal' | 'trade' | 'position' | 'risk_alert' | 'state_change';
  botId: string;
  strategyName: string;
  data: any; // eslint-disable-line @typescript-eslint/no-explicit-any
  timestamp: Date;
}

interface StrategyMonitorProps {
  botId?: string; // If provided, monitor specific bot, otherwise monitor all
  className?: string;
}

interface StrategyData {
  [botId: string]: StrategyPerformanceMetrics;
}

export const StrategyMonitor: React.FC<StrategyMonitorProps> = ({ 
  botId, 
  className = "" 
}) => {
  const [strategies, setStrategies] = useState<StrategyData>({});
  const [recentSignals, setRecentSignals] = useState<SignalSummary[]>([]);
  const [riskAlerts, setRiskAlerts] = useState<Array<{ botId: string; alerts: string[]; timestamp: Date }>>([]);
  const [selectedBotId, setSelectedBotId] = useState<string | null>(botId || null);

  const {
    isConnected,
    subscribe
  } = useWebSocket({
    url: 'ws://localhost:3002/ws',
    onOpen: () => {
      // Subscribe to strategy monitoring channels
      subscribe(CONSTANTS.WS_CHANNELS.SIGNALS);
      subscribe(CONSTANTS.WS_CHANNELS.BOT_STATUS);
      subscribe(CONSTANTS.WS_CHANNELS.POSITIONS);
      subscribe(CONSTANTS.WS_CHANNELS.TRADES);
    },
    onMessage: (message: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
      handleWebSocketMessage(message);
    }
  });

  const handleWebSocketMessage = (message: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
    if (message.channel === CONSTANTS.WS_CHANNELS.SIGNALS && message.data) {
      const update: StrategyUpdateMessage = message.data;
      handleStrategyUpdate(update);
    } else if (message.channel === CONSTANTS.WS_CHANNELS.BOT_STATUS && message.data) {
      handleBotStatusUpdate(message.data);
    }
  };

  const handleStrategyUpdate = (update: StrategyUpdateMessage) => {
    const { type, botId: updateBotId, data, timestamp } = update;

    // Filter by specific bot if specified
    if (botId && updateBotId !== botId) {return;}

    switch (type) {
      case 'performance':
        if (data.metrics) {
          setStrategies(prev => ({
            ...prev,
            [updateBotId]: data.metrics
          }));
        }
        break;

      case 'signal':
        if (data.signal) {
          setRecentSignals(prev => {
            // Keep last 20 signals
            return [data.signal, ...prev].slice(0, 20);
          });
        }
        break;

      case 'trade':
        // Update strategy metrics if trade data includes metrics
        if (data.metrics) {
          setStrategies(prev => ({
            ...prev,
            [updateBotId]: data.metrics
          }));
        }
        break;

      case 'state_change':
        if (data.metrics) {
          setStrategies(prev => ({
            ...prev,
            [updateBotId]: data.metrics
          }));
        }
        break;

      case 'risk_alert':
        if (data.alerts) {
          setRiskAlerts(prev => {
            const newAlert = {
              botId: updateBotId,
              alerts: data.alerts,
              timestamp: new Date(timestamp)
            };
            return [newAlert, ...prev].slice(0, 10); // Keep last 10 alerts
          });
        }
        break;
    }
  };

  const handleBotStatusUpdate = (data: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
    if (data.botId && data.strategy) {
      // Filter by specific bot if specified
      if (botId && data.botId !== botId) {return;}

      setStrategies(prev => ({
        ...prev,
        [data.botId]: {
          ...prev[data.botId],
          ...data.strategy,
          timestamp: new Date()
        }
      }));
    }
  };

  const getStrategyMetrics = (botId: string): StrategyPerformanceMetrics | null => {
    return strategies[botId] || null;
  };

  const formatCurrency = (amount: number): string => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount);
  };

  const formatPercentage = (value: number): string => {
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const formatUptime = (uptime: number): string => {
    const seconds = Math.floor(uptime / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    
    if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } 
      return `${seconds}s`;
    
  };

  const getRiskColor = (riskScore: number): string => {
    if (riskScore <= 3) {return 'text-green-600 bg-green-100';}
    if (riskScore <= 6) {return 'text-yellow-600 bg-yellow-100';}
    return 'text-red-600 bg-red-100';
  };

  const getStatusColor = (isRunning: boolean): string => {
    return isRunning ? 'text-green-600 bg-green-100' : 'text-gray-600 bg-gray-100';
  };

  const selectedStrategy = selectedBotId ? getStrategyMetrics(selectedBotId) : null;
  const allStrategies = Object.entries(strategies);

  return (
    <div className={`strategy-monitor ${className}`}>
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">
            üìä Strategy Monitor
          </h2>
          <p className="text-gray-600">
            Real-time strategy performance and signal monitoring
          </p>
        </div>
        
        <div className="flex items-center space-x-4">
          <span className={`text-sm font-semibold ${isConnected ? 'text-green-600' : 'text-red-600'}`}>
            {isConnected ? 'üü¢ Live' : 'üî¥ Offline'}
          </span>
          
          {allStrategies.length > 1 && (
            <select
              value={selectedBotId || ''}
              onChange={(e) => setSelectedBotId(e.target.value || null)}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="">All Strategies</option>
              {allStrategies.map(([botId, strategy]) => (
                <option key={botId} value={botId}>
                  {strategy.strategyName} ({botId})
                </option>
              ))}
            </select>
          )}
        </div>
      </div>

      {/* Risk Alerts */}
      {riskAlerts.length > 0 && (
        <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
          <h3 className="text-lg font-semibold text-red-800 mb-2">‚ö†Ô∏è Risk Alerts</h3>
          <div className="space-y-2">
            {riskAlerts.slice(0, 3).map((alert, index) => (
              <div key={index} className="flex justify-between items-center">
                <div>
                  <span className="font-medium text-red-700">Bot {alert.botId}:</span>
                  <span className="ml-2 text-red-600">{alert.alerts.join(', ')}</span>
                </div>
                <span className="text-xs text-red-500">
                  {alert.timestamp.toLocaleTimeString()}
                </span>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Strategy Performance Overview */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Strategy Performance</h3>
            
            {allStrategies.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-6xl mb-4">üìä</div>
                <p className="text-gray-600">No active strategies found</p>
                <p className="text-sm text-gray-500 mt-2">
                  Start a bot to see real-time strategy monitoring
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {(selectedStrategy ? [[selectedBotId!, selectedStrategy]] : allStrategies)
                  .filter(([, strategy]) => typeof strategy === 'object' && strategy !== null)
                  .map(([strategyBotId, strategyData]) => {
                    const strategy = strategyData as StrategyPerformanceMetrics;
                    return (
                  <div key={String(strategyBotId)} className="border border-gray-200 rounded-lg p-4">
                    {/* Strategy Header */}
                    <div className="flex justify-between items-center mb-3">
                      <div>
                        <h4 className="font-semibold text-gray-900">
                          {strategy.strategyName} v{strategy.strategyVersion}
                        </h4>
                        <p className="text-sm text-gray-600">Bot ID: {String(strategyBotId)}</p>
                      </div>
                      
                      <div className="flex items-center space-x-3">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(strategy.isRunning)}`}>
                          {strategy.isRunning ? 'üü¢ Running' : '‚ö´ Stopped'}
                        </span>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getRiskColor(strategy.riskMetrics.riskScore)}`}>
                          Risk: {strategy.riskMetrics.riskScore}/10
                        </span>
                      </div>
                    </div>

                    {/* Key Metrics */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                      <div className="text-center">
                        <div className={`text-xl font-bold ${strategy.totalPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {formatCurrency(strategy.totalPnL)}
                        </div>
                        <div className="text-xs text-gray-600">Total P&L</div>
                      </div>
                      
                      <div className="text-center">
                        <div className={`text-xl font-bold ${strategy.dailyPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {formatCurrency(strategy.dailyPnL)}
                        </div>
                        <div className="text-xs text-gray-600">Daily P&L</div>
                      </div>
                      
                      <div className="text-center">
                        <div className="text-xl font-bold text-gray-900">
                          {strategy.winRate.toFixed(1)}%
                        </div>
                        <div className="text-xs text-gray-600">Win Rate</div>
                      </div>
                      
                      <div className="text-center">
                        <div className="text-xl font-bold text-gray-900">
                          {strategy.totalTrades}
                        </div>
                        <div className="text-xs text-gray-600">Total Trades</div>
                      </div>
                    </div>

                    {/* Detailed Stats */}
                    <div className="grid grid-cols-3 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Uptime:</span>
                        <span className="ml-2 font-medium">{formatUptime(strategy.uptime)}</span>
                      </div>
                      
                      <div>
                        <span className="text-gray-600">Max Drawdown:</span>
                        <span className="ml-2 font-medium text-red-600">
                          {formatPercentage(strategy.maxDrawdown)}
                        </span>
                      </div>
                      
                      <div>
                        <span className="text-gray-600">Current Drawdown:</span>
                        <span className="ml-2 font-medium text-orange-600">
                          {formatPercentage(strategy.currentDrawdown)}
                        </span>
                      </div>
                    </div>

                    {/* Current Positions */}
                    {strategy.currentPositions && strategy.currentPositions.length > 0 && (
                      <div className="mt-4 pt-4 border-t border-gray-200">
                        <h5 className="font-medium text-gray-900 mb-2">Current Positions</h5>
                        <div className="space-y-2">
                          {strategy.currentPositions.map((position: any, index: number) => ( // eslint-disable-line @typescript-eslint/no-explicit-any
                            <div key={index} className="flex justify-between items-center text-sm">
                              <span className="font-medium">
                                {position.symbol} {position.side.toUpperCase()}
                              </span>
                              <span className="text-gray-600">
                                Size: {position.size} @ {formatCurrency(position.entryPrice)}
                              </span>
                              <span className={`font-medium ${position.unrealizedPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                                {formatCurrency(position.unrealizedPnL)}
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                    );
                  })}
              </div>
            )}
          </div>
        </div>

        {/* Recent Signals */}
        <div>
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Recent Signals</h3>
            
            {recentSignals.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-4xl mb-2">üì°</div>
                <p className="text-gray-600 text-sm">No signals yet</p>
              </div>
            ) : (
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {recentSignals.map((signal) => (
                  <div key={signal.id} className="border border-gray-200 rounded p-3">
                    <div className="flex justify-between items-center mb-2">
                      <span className="font-medium text-gray-900">
                        {signal.symbol}
                      </span>
                      <span className={`px-2 py-1 rounded text-xs font-medium ${
                        signal.side === 'buy' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                      }`}>
                        {signal.side.toUpperCase()}
                      </span>
                    </div>
                    
                    <div className="text-sm text-gray-600 space-y-1">
                      <div className="flex justify-between">
                        <span>Price:</span>
                        <span className="font-medium">{formatCurrency(signal.price)}</span>
                      </div>
                      
                      <div className="flex justify-between">
                        <span>Confidence:</span>
                        <span className="font-medium">{(signal.confidence * 100).toFixed(1)}%</span>
                      </div>
                      
                      <div className="flex justify-between">
                        <span>Time:</span>
                        <span className="font-medium">
                          {signal.timestamp.toLocaleTimeString()}
                        </span>
                      </div>
                      
                      <div className="flex justify-between">
                        <span>Status:</span>
                        <span className={`font-medium ${
                          signal.executed 
                            ? signal.result === 'win' 
                              ? 'text-green-600' 
                              : signal.result === 'loss' 
                                ? 'text-red-600' 
                                : 'text-blue-600'
                            : 'text-yellow-600'
                        }`}>
                          {signal.executed 
                            ? signal.result?.toUpperCase() || 'EXECUTED' 
                            : 'PENDING'}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Summary Statistics */}
      {allStrategies.length > 1 && !selectedBotId && (
        <div className="mt-6 bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-4">Portfolio Summary</h3>
          <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">{allStrategies.length}</div>
              <div className="text-sm text-gray-600">Active Strategies</div>
            </div>
            
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">
                {allStrategies.filter(([, strategy]) => strategy.isRunning).length}
              </div>
              <div className="text-sm text-gray-600">Running</div>
            </div>
            
            <div className="text-center">
              <div className={`text-2xl font-bold ${
                allStrategies.reduce((sum, [, strategy]) => sum + strategy.totalPnL, 0) >= 0 ? 'text-green-600' : 'text-red-600'
              }`}>
                {formatCurrency(allStrategies.reduce((sum, [, strategy]) => sum + strategy.totalPnL, 0))}
              </div>
              <div className="text-sm text-gray-600">Total P&L</div>
            </div>
            
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {allStrategies.reduce((sum, [, strategy]) => sum + strategy.totalTrades, 0)}
              </div>
              <div className="text-sm text-gray-600">Total Trades</div>
            </div>
            
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {allStrategies.length > 0 
                  ? (allStrategies.reduce((sum, [, strategy]) => sum + strategy.winRate, 0) / allStrategies.length).toFixed(1)
                  : '0.0'
                }%
              </div>
              <div className="text-sm text-gray-600">Avg Win Rate</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StrategyMonitor;

'use client';

import React, { useState, useEffect, useMemo } from 'react';

import type { ApiError } from '../services/api';

import { useWebSocketContext } from '../contexts/WebSocketContext';
import { apiService } from '../services/api';
import { componentClasses, getPnLColor } from '../utils/theme';
import ErrorHandler from '../utils/errorHandler';

import { ErrorBoundaryWrapper } from './ErrorBoundary';
import { LoadingState, LoadingTable } from './Loading';

// Types for trading activity data
interface Trade {
  id: string;
  botId: string;
  botName: string;
  symbol: string;
  side: 'buy' | 'sell';
  type: 'market' | 'limit' | 'stop' | 'stop-limit';
  amount: number;
  price: number;
  executedPrice?: number;
  executedAmount?: number;
  status: 'pending' | 'filled' | 'partial' | 'cancelled' | 'rejected';
  fee: number;
  pnl?: number;
  timestamp: string;
  orderId: string;
  exchange: string;
  strategy: string;
  slippage?: number;
  executionTime?: number;
}

interface OrderUpdate {
  orderId: string;
  botId: string;
  status: 'pending' | 'filled' | 'partial' | 'cancelled' | 'rejected';
  filledAmount?: number;
  remainingAmount?: number;
  averagePrice?: number;
  fee?: number;
  timestamp: string;
}

interface TradingMetrics {
  totalTrades: number;
  totalVolume: number;
  totalPnL: number;
  winRate: number;
  averageExecutionTime: number;
  averageSlippage: number;
  successRate: number;
  activeBots: number;
}

interface FilterOptions {
  botId?: string;
  symbol?: string;
  side?: 'buy' | 'sell' | 'all';
  status?: 'all' | 'pending' | 'filled' | 'partial' | 'cancelled' | 'rejected';
  exchange?: string;
  timeframe?: '1h' | '24h' | '7d' | '30d' | 'all';
}

interface SortOptions {
  field: 'timestamp' | 'symbol' | 'amount' | 'price' | 'pnl' | 'status';
  direction: 'asc' | 'desc';
}

export const TradingActivityMonitor: React.FC = () => {
  // State management
  const [trades, setTrades] = useState<Trade[]>([]);
  const [orderUpdates, setOrderUpdates] = useState<OrderUpdate[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [filters, setFilters] = useState<FilterOptions>({
    side: 'all',
    status: 'all',
    timeframe: '24h'
  });
  const [sortOptions, setSortOptions] = useState<SortOptions>({
    field: 'timestamp',
    direction: 'desc'
  });
  const [selectedTrade, setSelectedTrade] = useState<Trade | null>(null);
  const [showOrderDetails, setShowOrderDetails] = useState(false);

  // WebSocket context
  const { 
    isConnected, 
    subscribe, 
    unsubscribe,
    marketData,
    botStatuses 
  } = useWebSocketContext();

  // Load initial trading data
  useEffect(() => {
    const loadTradingData = async () => {
      setIsLoading(true);
      setError(null);
      
      try {
        const response = await apiService.getTrades({
          timeframe: filters.timeframe,
          symbol: filters.symbol,
          status: filters.status !== 'all' ? filters.status : undefined,
        });

        if (response.success) {
          setTrades(response.data as unknown as Trade[] || []);
        } else {
          throw new Error(response.error || 'Failed to load trading data');
        }
      } catch (err) {
        console.error('Error loading trading data:', err);
        const errorState = ErrorHandler.handleUnknownError(err, 'Loading trading data');
        setError(errorState.message);
      } finally {
        setIsLoading(false);
      }
    };

    loadTradingData();
  }, [filters.timeframe, filters.symbol, filters.status]);

  // Subscribe to real-time updates
  useEffect(() => {
    if (isConnected) {
      // Subscribe to trade updates
      subscribe('trades');
      subscribe('orders');
      subscribe('positions');

      return () => {
        unsubscribe('trades');
        unsubscribe('orders');
        unsubscribe('positions');
      };
    }
  }, [isConnected, subscribe, unsubscribe]);

  // Filter and sort trades
  const filteredAndSortedTrades = useMemo(() => {
    const filtered = trades.filter(trade => {
      // Filter by bot
      if (filters.botId && trade.botId !== filters.botId) {
        return false;
      }
      
      // Filter by symbol
      if (filters.symbol && trade.symbol !== filters.symbol) {
        return false;
      }
      
      // Filter by side
      if (filters.side && filters.side !== 'all' && trade.side !== filters.side) {
        return false;
      }
      
      // Filter by status
      if (filters.status && filters.status !== 'all' && trade.status !== filters.status) {
        return false;
      }
      
      // Filter by exchange
      if (filters.exchange && trade.exchange !== filters.exchange) {
        return false;
      }
      
      // Filter by timeframe
      if (filters.timeframe && filters.timeframe !== 'all') {
        const tradeTime = new Date(trade.timestamp);
        const now = new Date();
        const timeDiff = now.getTime() - tradeTime.getTime();
        
        switch (filters.timeframe) {
          case '1h':
            if (timeDiff > 60 * 60 * 1000) {
              return false;
            }
            break;
          case '24h':
            if (timeDiff > 24 * 60 * 60 * 1000) {
              return false;
            }
            break;
          case '7d':
            if (timeDiff > 7 * 24 * 60 * 60 * 1000) {
              return false;
            }
            break;
          case '30d':
            if (timeDiff > 30 * 24 * 60 * 60 * 1000) {
              return false;
            }
            break;
        }
      }
      
      return true;
    });

    // Sort trades
    filtered.sort((a, b) => {
      const direction = sortOptions.direction === 'asc' ? 1 : -1;
      
      switch (sortOptions.field) {
        case 'timestamp':
          return direction * (new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime());
        case 'symbol':
          return direction * a.symbol.localeCompare(b.symbol);
        case 'amount':
          return direction * (a.amount - b.amount);
        case 'price':
          return direction * (a.price - b.price);
        case 'pnl':
          return direction * ((a.pnl || 0) - (b.pnl || 0));
        case 'status':
          return direction * a.status.localeCompare(b.status);
        default:
          return 0;
      }
    });

    return filtered;
  }, [trades, filters, sortOptions]);

  // Calculate trading metrics
  const tradingMetrics = useMemo((): TradingMetrics => {
    const filledTrades = filteredAndSortedTrades.filter(trade => trade.status === 'filled');
    const totalVolume = filledTrades.reduce((sum, trade) => sum + (trade.amount * trade.price), 0);
    const totalPnL = filledTrades.reduce((sum, trade) => sum + (trade.pnl || 0), 0);
    const winningTrades = filledTrades.filter(trade => (trade.pnl || 0) > 0);
    const tradesWithExecutionTime = filledTrades.filter(trade => trade.executionTime);
    const tradesWithSlippage = filledTrades.filter(trade => trade.slippage !== undefined);
    const activeBots = new Set(trades.map(trade => trade.botId)).size;

    return {
      totalTrades: filledTrades.length,
      totalVolume,
      totalPnL,
      winRate: filledTrades.length > 0 ? (winningTrades.length / filledTrades.length) * 100 : 0,
      averageExecutionTime: tradesWithExecutionTime.length > 0 
        ? tradesWithExecutionTime.reduce((sum, trade) => sum + (trade.executionTime || 0), 0) / tradesWithExecutionTime.length 
        : 0,
      averageSlippage: tradesWithSlippage.length > 0
        ? tradesWithSlippage.reduce((sum, trade) => sum + (trade.slippage || 0), 0) / tradesWithSlippage.length
        : 0,
      successRate: trades.length > 0 ? (filledTrades.length / trades.length) * 100 : 0,
      activeBots
    };
  }, [filteredAndSortedTrades, trades]);

  // Utility functions
  const formatCurrency = (amount: number, currency = 'USDT'): string => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency === 'USDT' ? 'USD' : currency,
      minimumFractionDigits: 2,
      maximumFractionDigits: 6
    }).format(amount);
  };

  const formatPercentage = (value: number): string => {
    return `${value.toFixed(2)}%`;
  };

  const formatTime = (timestamp: string): string => {
    return new Intl.DateTimeFormat('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    }).format(new Date(timestamp));
  };

  const getTradingStatusColor = (status: string): string => {
    const statusMap: Record<string, string> = {
      filled: 'text-status-success bg-status-success/10 border-status-success/20',
      pending: 'text-status-warning bg-status-warning/10 border-status-warning/20',
      partial: 'text-brand-primary bg-brand-primary/10 border-brand-primary/20',
      cancelled: 'text-muted bg-surface-secondary border-border',
      rejected: 'text-status-error bg-status-error/10 border-status-error/20',
    };
    return statusMap[status] || 'text-muted bg-surface-secondary border-border';
  };

  const getSideColor = (side: string): string => {
    return side === 'buy' ? 'text-status-success' : 'text-status-error';
  };

  // Event handlers
  const handleFilterChange = (newFilters: Partial<FilterOptions>) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
  };

  const handleSortChange = (field: SortOptions['field']) => {
    setSortOptions(prev => ({
      field,
      direction: prev.field === field && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  const handleTradeClick = (trade: Trade) => {
    setSelectedTrade(trade);
    setShowOrderDetails(true);
  };

  const handleRefresh = () => {
    window.location.reload();
  };

  return (
    <ErrorBoundaryWrapper>
      <div className="trading-activity-monitor space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-primary">Trading Activity Monitor</h2>
            <p className="text-secondary mt-1">
              Real-time monitoring of all trading activity across your bots
            </p>
          </div>
          
          <div className="flex items-center space-x-3">
            {/* Connection Status */}
            <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg text-sm ${
              isConnected 
                ? 'bg-status-success/10 text-status-success border border-status-success/20' 
                : 'bg-status-error/10 text-status-error border border-status-error/20'
            }`}>
              <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-status-success' : 'bg-status-error'}`} />
              <span className="font-medium">
                {isConnected ? 'Live' : 'Disconnected'}
              </span>
            </div>
            
            {/* Refresh Button */}
            <button
              onClick={handleRefresh}
              disabled={isLoading}
              className={`${componentClasses.button.secondary} flex items-center space-x-2`}
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
              </svg>
              <span>Refresh</span>
            </button>
          </div>
        </div>

      {/* Trading Metrics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <div className={componentClasses.card}>
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="text-sm font-medium text-secondary">Total Trades</h3>
              <svg className="w-6 h-6 text-brand-primary mt-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
            </div>
          </div>
          <div className="text-2xl font-bold text-primary">{tradingMetrics.totalTrades.toLocaleString()}</div>
          <div className="text-sm text-muted mt-1">
            {tradingMetrics.activeBots} active bots
          </div>
        </div>

        <div className={componentClasses.card}>
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="text-sm font-medium text-secondary">Total Volume</h3>
              <svg className="w-6 h-6 text-brand-secondary mt-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
              </svg>
            </div>
          </div>
          <div className="text-2xl font-bold text-primary">{formatCurrency(tradingMetrics.totalVolume)}</div>
          <div className="text-sm text-muted mt-1">
            Success Rate: {formatPercentage(tradingMetrics.successRate)}
          </div>
        </div>

        <div className={componentClasses.card}>
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="text-sm font-medium text-secondary">Total P&L</h3>
              <svg className="w-6 h-6 text-status-success mt-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
              </svg>
            </div>
          </div>
          <div className={`text-2xl font-bold ${getPnLColor(tradingMetrics.totalPnL)}`}>
            {formatCurrency(tradingMetrics.totalPnL)}
          </div>
          <div className="text-sm text-muted mt-1">
            Win Rate: {formatPercentage(tradingMetrics.winRate)}
          </div>
        </div>

        <div className={componentClasses.card}>
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="text-sm font-medium text-secondary">Avg Execution</h3>
              <svg className="w-6 h-6 text-status-warning mt-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
          </div>
          <div className="text-2xl font-bold text-primary">{tradingMetrics.averageExecutionTime.toFixed(0)}ms</div>
          <div className="text-sm text-muted mt-1">
            Avg Slippage: {formatPercentage(tradingMetrics.averageSlippage)}
          </div>
        </div>
      </div>

      {/* Filter Controls */}
      <div className={componentClasses.card}>
        <div className="flex items-center gap-2 mb-4">
          <svg className="w-5 h-5 text-secondary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.293A1 1 0 013 6.586V4z" />
          </svg>
          <h3 className="text-lg font-semibold text-primary">Filters & Controls</h3>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
          {/* Timeframe Filter */}
          <div>
            <label className="block text-sm font-medium text-secondary mb-2">
              Timeframe
            </label>
            <select
              value={filters.timeframe}
              onChange={(e) => handleFilterChange({ timeframe: e.target.value as FilterOptions['timeframe'] })}
              className={componentClasses.form.select}
            >
              <option value="1h">Last Hour</option>
              <option value="24h">Last 24h</option>
              <option value="7d">Last 7 days</option>
              <option value="30d">Last 30 days</option>
              <option value="all">All Time</option>
            </select>
          </div>

          {/* Side Filter */}
          <div>
            <label className="block text-sm font-medium text-secondary mb-2">
              Side
            </label>
            <select
              value={filters.side}
              onChange={(e) => handleFilterChange({ side: e.target.value as FilterOptions['side'] })}
              className={componentClasses.form.select}
            >
              <option value="all">All</option>
              <option value="buy">Buy Only</option>
              <option value="sell">Sell Only</option>
            </select>
          </div>

          {/* Status Filter */}
          <div>
            <label className="block text-sm font-medium text-secondary mb-2">
              Status
            </label>
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange({ status: e.target.value as FilterOptions['status'] })}
              className={componentClasses.form.select}
            >
              <option value="all">All</option>
              <option value="filled">Filled</option>
              <option value="pending">Pending</option>
              <option value="partial">Partial</option>
              <option value="cancelled">Cancelled</option>
              <option value="rejected">Rejected</option>
            </select>
          </div>

          {/* Symbol Filter */}
          <div>
            <label className="block text-sm font-medium text-secondary mb-2">
              Symbol
            </label>
            <input
              type="text"
              value={filters.symbol || ''}
              onChange={(e) => handleFilterChange({ symbol: e.target.value || undefined })}
              placeholder="e.g., BTCUSDT"
              className={componentClasses.form.input}
            />
          </div>

          {/* Exchange Filter */}
          <div>
            <label className="block text-sm font-medium text-secondary mb-2">
              Exchange
            </label>
            <select
              value={filters.exchange || ''}
              onChange={(e) => handleFilterChange({ exchange: e.target.value || undefined })}
              className={componentClasses.form.select}
            >
              <option value="">All Exchanges</option>
              <option value="bybit">Bybit</option>
              <option value="binance">Binance</option>
              <option value="okx">OKX</option>
              <option value="coinbase">Coinbase</option>
              <option value="kraken">Kraken</option>
            </select>
          </div>

          {/* Clear Filters */}
          <div className="flex items-end">
            <button
              onClick={() => setFilters({ side: 'all', status: 'all', timeframe: '24h' })}
              className={`${componentClasses.button.secondary} w-full`}
            >
              Clear Filters
            </button>
          </div>
        </div>
      </div>

        {/* Loading State */}
        {isLoading && (
          <LoadingTable rows={8} columns={10} className="mb-6" />
        )}

        {/* Error State */}
        {error && (
          <div className="bg-status-error/10 border border-status-error/20 rounded-lg p-4 mb-6">
            <div className="flex items-start">
              <svg className="w-5 h-5 text-status-error mr-3 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.998-.833-2.768 0L3.046 16.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
              <div>
                <h4 className="font-medium text-status-error">Error Loading Trading Data</h4>
                <p className="text-sm text-status-error/80 mt-1">{error}</p>
              </div>
            </div>
          </div>
        )}

        {/* Trading Activity Table */}
        {!isLoading && !error && (
          <div className={componentClasses.card}>
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-lg font-semibold text-primary">
                Recent Trading Activity ({filteredAndSortedTrades.length} trades)
              </h3>
              <div className="text-sm text-secondary">
                Last updated: {new Date().toLocaleTimeString()}
              </div>
            </div>
            
            {filteredAndSortedTrades.length === 0 ? (
              <div className="text-center py-12">
                <svg className="w-12 h-12 text-muted mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
                <h4 className="text-lg font-medium text-primary mb-2">No Trading Activity</h4>
                <p className="text-secondary">
                  No trades found matching your current filters. Try adjusting the filters or check if your bots are running.
                </p>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-surface-secondary">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-secondary uppercase tracking-wider">
                        <button
                          onClick={() => handleSortChange('timestamp')}
                          className="flex items-center space-x-1 hover:text-primary transition-colors"
                        >
                          <span>Time</span>
                          {sortOptions.field === 'timestamp' && (
                            <span className="text-brand-primary">
                              {sortOptions.direction === 'desc' ? '‚Üì' : '‚Üë'}
                            </span>
                          )}
                        </button>
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-secondary uppercase tracking-wider">Bot</th>
