class ArchitecturalAnalyzer {
  private config: AnalysisConfig;
  private projectRoot: string;
  private allElements: ArchitecturalElement[] = [];
  private fileContents: Map<string, string> = new Map();
  private dependencyGraph: Map<string, string[]> = new Map();

  constructor(projectRoot: string, config: Partial<AnalysisConfig> = {}) {
    this.projectRoot = projectRoot;
    this.config = { ...DEFAULT_CONFIG, ...config };
  }

  async analyzeProject(): Promise<ArchitecturalReport> {
    console.log('üèóÔ∏è Starting comprehensive architectural analysis...');
    console.log(`üìÅ Project root: ${this.projectRoot}`);
    
    const files = await this.discoverFiles();
    console.log(`üìÑ Found ${files.length} files to analyze`);
    
    await this.parseFiles(files);
    console.log(`üîç Extracted ${this.allElements.length} architectural elements`);
    
    const patterns = await this.analyzePatterns();
    const consolidationOpportunities = await this.identifyConsolidationOpportunities();
    const performanceInsights = await this.analyzePerformance();
    const recommendations = this.generateRecommendations(patterns, consolidationOpportunities, performanceInsights);
    const roadmap = this.generateImplementationRoadmap(patterns, consolidationOpportunities, performanceInsights);
    
    const report: ArchitecturalReport = {
      timestamp: new Date().toISOString(),
      projectMetrics: this.calculateProjectMetrics(),
      patterns,
      consolidationOpportunities,
      performanceInsights,
      recommendations,
      implementationRoadmap: roadmap
    };
    
    console.log('‚úÖ Architectural analysis complete!');
    return report;
  }

  private async discoverFiles(): Promise<string[]> {
    const files: string[] = [];
    
    const scanDirectory = (dirPath: string): void => {
      try {
        const entries = fs.readdirSync(dirPath, { withFileTypes: true });
        for (const entry of entries) {
          const fullPath = path.join(dirPath, entry.name);
          if (entry.isDirectory()) {
            if (!this.shouldExcludePath(fullPath)) {
              scanDirectory(fullPath);
            }
          } else if (entry.isFile()) {
            if (this.shouldIncludeFile(fullPath) && !this.shouldExcludePath(fullPath)) {
              files.push(fullPath);
            }
          }
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è Could not scan directory: ${dirPath}`);
      }
    };

    scanDirectory(this.projectRoot);
    return files;
  }

  private shouldIncludeFile(filePath: string): boolean {
    const fileName = path.basename(filePath);
    const extensions = ['.ts', '.tsx', '.js', '.jsx'];
    return extensions.some(ext => fileName.endsWith(ext));
  }

  private shouldExcludePath(filePath: string): boolean {
    const relativePath = path.relative(this.projectRoot, filePath);
    const excludePatterns = [
      'node_modules',
      'dist',
      'build',
      'coverage',
      '.d.ts',
      '.test.',
      '.spec.'
    ];
    
    return excludePatterns.some(pattern => 
      relativePath.includes(pattern) || path.basename(filePath).includes(pattern)
    );
  }

  private async parseFiles(files: string[]): Promise<void> {
    for (const filePath of files) {
      try {
        const stats = fs.statSync(filePath);
        if (stats.size > this.config.maxFileSize) {
          console.warn(`‚ö†Ô∏è Skipping large file: ${filePath} (${stats.size} bytes)`);
          continue;
        }

        const content = fs.readFileSync(filePath, 'utf-8');
        this.fileContents.set(filePath, content);
        
        const elements = await this.extractElementsFromFile(filePath, content);
        this.allElements.push(...elements);
        
        const dependencies = this.extractDependencies(content);
        this.dependencyGraph.set(filePath, dependencies);
        
      } catch (error) {
        console.warn(`‚ö†Ô∏è Error processing file ${filePath}: ${error}`);
      }
    }
  }

  private async extractElementsFromFile(filePath: string, content: string): Promise<ArchitecturalElement[]> {
    const elements: ArchitecturalElement[] = [];

    // Extract functions
    const functionPattern = /(?:^|\n)\s*(?:export\s+)?(?:async\s+)?(?:function\s+(\w+)|(?:const|let|var)\s+(\w+)\s*[:=]\s*(?:async\s+)?\(|(\w+)\s*[:=]\s*(?:async\s+)?\([^)]*\)\s*(?::\s*[^=]+)?\s*=>)/gm;
    let match;
    
    while ((match = functionPattern.exec(content)) !== null) {
      const functionName = match[1] || match[2] || match[3];
      if (functionName && functionName.length > 1) {
        const startLine = content.substring(0, match.index!).split('\n').length;
        const functionBody = this.extractBlock(content, match.index! + match[0].length);
        
        if (functionBody && functionBody.split('\n').length >= this.config.minFunctionSize) {
          elements.push({
            type: 'function',
            name: functionName,
            filePath,
            startLine,
            endLine: startLine + functionBody.split('\n').length - 1,
            complexity: this.calculateComplexity(functionBody),
            dependencies: this.extractDependencies(functionBody),
            usageCount: 0,
            size: functionBody.split('\n').length,
            content: functionBody,
            hash: createHash('md5').update(functionBody.replace(/\s+/g, ' ')).digest('hex')
          });
        }
      }
    }

    // Extract classes
    const classPattern = /(?:^|\n)\s*(?:export\s+)?(?:abstract\s+)?class\s+(\w+)/gm;
    while ((match = classPattern.exec(content)) !== null) {
      const className = match[1];
      if (className) {
        const startLine = content.substring(0, match.index!).split('\n').length;
        const classBody = this.extractBlock(content, match.index! + match[0].length);
        
        if (classBody) {
          elements.push({
            type: 'class',
            name: className,
            filePath,
            startLine,
            endLine: startLine + classBody.split('\n').length - 1,
            complexity: this.calculateComplexity(classBody),
            dependencies: this.extractDependencies(classBody),
            usageCount: 0,
            size: classBody.split('\n').length,
            content: classBody,
            hash: createHash('md5').update(classBody.replace(/\s+/g, ' ')).digest('hex')
          });
        }
      }
    }

    return elements;
  }

  private extractBlock(content: string, startIndex: number): string | null {
    let braceCount = 0;
    let inBlock = false;
    let blockStart = startIndex;
    
    for (let i = startIndex; i < content.length; i++) {
      const char = content[i];
      
      if (char === '{') {
        if (!inBlock) {
          blockStart = i;
          inBlock = true;
        }
        braceCount++;
      } else if (char === '}') {
        braceCount--;
        if (inBlock && braceCount === 0) {
          return content.substring(blockStart, i + 1);
        }
      }
    }
    
    return null;
  }

  private extractDependencies(content: string): string[] {
    const dependencies: string[] = [];
    
    const importPattern = /import\s+(?:\{[^}]+\}|\w+|\*\s+as\s+\w+)\s+from\s+['"]([^'"]+)['"]/g;
    let match;
    
    while ((match = importPattern.exec(content)) !== null) {
      dependencies.push(match[1]);
    }
    
    const requirePattern = /require\s*\(\s*['"]([^'"]+)['"]\s*\)/g;
    while ((match = requirePattern.exec(content)) !== null) {
      dependencies.push(match[1]);
    }
    
    return [...new Set(dependencies)];
  }

  private calculateComplexity(code: string): number {
    let complexity = 1;
    
    const complexityPatterns = [
      /\bif\b/g, /\belse\b/g, /\bwhile\b/g, /\bfor\b/g,
      /\bswitch\b/g, /\bcase\b/g, /\bcatch\b/g,
      /\b&&\b/g, /\b\|\|\b/g, /\?\s*[^:]+\s*:/g
    ];
    
    complexityPatterns.forEach(pattern => {
      const matches = code.match(pattern);
      if (matches) complexity += matches.length;
    });
    
    return complexity;
  }

  private async analyzePatterns(): Promise<CodePattern[]> {
    const patterns: CodePattern[] = [];
    
    // Find duplicate functions
    const functionsByHash = new Map<string, ArchitecturalElement[]>();
    this.allElements
      .filter(el => el.type === 'function')
      .forEach(func => {
        if (!functionsByHash.has(func.hash)) {
          functionsByHash.set(func.hash, []);
        }
        functionsByHash.get(func.hash)!.push(func);
      });
    
    functionsByHash.forEach((functions, hash) => {
      if (functions.length > 1) {
        const avgSize = functions.reduce((sum, f) => sum + f.size, 0) / functions.length;
        patterns.push({
          type: 'duplicate',
          elements: functions,
          description: `Found ${functions.length} duplicate functions with identical logic`,
          severity: functions.length > 3 ? 'high' : 'medium',
          recommendedAction: 'Extract common function to utility module and replace duplicates',
          estimatedImpact: {
            performance: 2,
            maintainability: 8,
            codeReduction: (functions.length - 1) * avgSize
          },
          implementationEffort: 'medium'
        });
      }
    });
    
    // Find complex functions
    const complexFunctions = this.allElements
      .filter(el => el.type === 'function' && el.complexity > 10)
      .sort((a, b) => b.complexity - a.complexity);
    
    if (complexFunctions.length > 0) {
      patterns.push({
        type: 'complex',
        elements: complexFunctions,
        description: `Found ${complexFunctions.length} highly complex functions that are difficult to maintain`,
        severity: 'high',
        recommendedAction: 'Break down complex functions into smaller, focused functions',
        estimatedImpact: {
          performance: 3,
          maintainability: 9,
          codeReduction: 0
        },
        implementationEffort: 'high'
      });
    }
    
    return patterns;
  }

  private async identifyConsolidationOpportunities(): Promise<ConsolidationOpportunity[]> {
    const opportunities: ConsolidationOpportunity[] = [];
    
    // Find similar functions that could be unified
    const similarFunctions = this.findSimilarFunctions();
    
    if (similarFunctions.length > 0) {
      opportunities.push({
        type: 'method-centralization',
        targetFiles: [...new Set(similarFunctions.map(f => f.filePath))],
        suggestedLocation: 'src/utils/common.ts',
        suggestedName: 'centralizedUtilities',
        affectedElements: similarFunctions,
        benefits: {
          codeReduction: similarFunctions.reduce((sum, f) => sum + f.size, 0) * 0.7,
          maintainabilityImprovement: 8,
          performanceGain: 2
        },
        implementationSteps: [
          'Create centralized utility module',
          'Extract common patterns',
          'Replace duplicated code',
          'Update imports across project'
        ],
        riskLevel: 'low'
      });
    }
    
    return opportunities;
  }

  private findSimilarFunctions(): ArchitecturalElement[] {
    const functions = this.allElements.filter(el => el.type === 'function');
    const similar: ArchitecturalElement[] = [];
    
    for (let i = 0; i < functions.length; i++) {
      for (let j = i + 1; j < functions.length; j++) {
        const similarity = this.calculateSimilarity(functions[i].content, functions[j].content);
        if (similarity > this.config.similarityThreshold) {
          if (!similar.includes(functions[i])) similar.push(functions[i]);
          if (!similar.includes(functions[j])) similar.push(functions[j]);
        }
      }
    }
    
    return similar;
  }

  private calculateSimilarity(content1: string, content2: string): number {
    const tokens1 = new Set(content1.replace(/\s+/g, ' ').split(' '));
    const tokens2 = new Set(content2.replace(/\s+/g, ' ').split(' '));
    
    const intersection = new Set([...tokens1].filter(token => tokens2.has(token)));
    const union = new Set([...tokens1, ...tokens2]);
    
    return intersection.size / union.size;
  }

  private async analyzePerformance(): Promise<PerformanceInsight[]> {
    const insights: PerformanceInsight[] = [];
    
    // Find large functions that could be performance bottlenecks
    const largeFunctions = this.allElements
      .filter(el => el.type === 'function' && el.size > 100)
      .sort((a, b) => b.size - a.size);
    
    largeFunctions.slice(0, 5).forEach(func => {
      insights.push({
        type: 'bottleneck',
        location: `${func.filePath}:${func.startLine}`,
        description: `Large function "${func.name}" with ${func.size} lines may impact performance`,
        currentMetrics: {
          executionTime: func.size * 0.1, // estimated
        },
        optimization: {
          strategy: 'Break down into smaller functions and optimize critical paths',
          expectedImprovement: '20-40% performance improvement',
          implementationComplexity: 'medium'
        },
        priority: Math.min(10, Math.floor(func.size / 20))
      });
    });
    
    return insights;
  }

  private generateRecommendations(
    patterns: CodePattern[], 
    opportunities: ConsolidationOpportunity[], 
    insights: PerformanceInsight[]
  ): { immediate: string[]; shortTerm: string[]; longTerm: string[] } {
    const immediate: string[] = [];
    const shortTerm: string[] = [];
    const longTerm: string[] = [];
    
    patterns.forEach(pattern => {
      if (pattern.severity === 'critical' || pattern.severity === 'high') {
        immediate.push(pattern.recommendedAction);
      } else {
        shortTerm.push(pattern.recommendedAction);
      }
    });
    
    opportunities.forEach(opp => {
      if (opp.riskLevel === 'low') {
        shortTerm.push(`Implement ${opp.type}: ${opp.suggestedName}`);
      } else {
        longTerm.push(`Consider ${opp.type}: ${opp.suggestedName}`);
      }
    });
    
    insights.forEach(insight => {
      if (insight.priority > 7) {
        immediate.push(`Address performance bottleneck: ${insight.description}`);
      } else if (insight.priority > 4) {
        shortTerm.push(`Optimize: ${insight.description}`);
      } else {
        longTerm.push(`Monitor: ${insight.description}`);
      }
    });
    
    return { immediate, shortTerm, longTerm };
  }

  private generateImplementationRoadmap(
    patterns: CodePattern[], 
    opportunities: ConsolidationOpportunity[], 
    insights: PerformanceInsight[]
  ): { phase: string; tasks: string[]; estimatedEffort: string; expectedBenefits: string[] }[] {
    return [
      {
        phase: 'Phase 1: Critical Issues',
        tasks: [
          'Fix duplicate code patterns',
          'Address high complexity functions',
          'Resolve critical performance bottlenecks'
        ],
        estimatedEffort: '2-3 weeks',
        expectedBenefits: [
          'Improved code maintainability',
          'Reduced technical debt',
          'Better performance'
        ]
      },
      {
        phase: 'Phase 2: Code Consolidation',
        tasks: [
          'Implement utility extraction opportunities',
          'Centralize common methods',
          'Standardize interfaces'
        ],
        estimatedEffort: '3-4 weeks',
        expectedBenefits: [
          'Reduced code duplication',
          'Improved developer productivity',
          'Better code organization'
        ]
      },
      {
        phase: 'Phase 3: Long-term Optimization',
        tasks: [
          'Monitor performance metrics',
          'Implement advanced optimizations',
          'Continuous architectural improvements'
        ],
        estimatedEffort: 'Ongoing',
        expectedBenefits: [
          'Sustained performance',
          'Scalable architecture',
          'Future-proof codebase'
        ]
      }
    ];
  }

  private calculateProjectMetrics(): {
    totalFiles: number;
    totalLines: number;
    totalFunctions: number;
    totalClasses: number;
    duplicateCount: number;
    complexityScore: number;
  } {
    const functions = this.allElements.filter(el => el.type === 'function');
    const classes = this.allElements.filter(el => el.type === 'class');
    
    const totalLines = Array.from(this.fileContents.values())
      .reduce((sum, content) => sum + content.split('\n').length, 0);
    
    const duplicateCount = this.findDuplicateCount();
    const complexityScore = this.calculateAverageComplexity();
    
    return {
      totalFiles: this.fileContents.size,
      totalLines,
      totalFunctions: functions.length,
      totalClasses: classes.length,
      duplicateCount,
      complexityScore
    };
  }

  private findDuplicateCount(): number {
    const hashes = new Set<string>();
    let duplicates = 0;
    
    this.allElements.forEach(el => {
      if (hashes.has(el.hash)) {
        duplicates++;
      } else {
        hashes.add(el.hash);
      }
    });
    
    return duplicates;
  }

  private calculateAverageComplexity(): number {
    const complexities = this.allElements.map(el => el.complexity);
    return complexities.length > 0 
      ? complexities.reduce((sum, c) => sum + c, 0) / complexities.length 
      : 0;
  }

  async saveReport(report: ArchitecturalReport, outputPath?: string): Promise<void> {
    const reportPath = outputPath || path.join(this.projectRoot, 'reports', 'architectural-analysis.json');
    const reportDir = path.dirname(reportPath);
    
    if (!fs.existsSync(reportDir)) {
      fs.mkdirSync(reportDir, { recursive: true });
    }
    
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
    console.log(`üìä Report saved to: ${reportPath}`);
  }
}

// Main execution
async function main(): Promise<void> {
  try {
    const projectRoot = process.cwd();
    console.log('üöÄ Starting Architectural Analysis...');
    
    const analyzer = new ArchitecturalAnalyzer(projectRoot);
    const report = await analyzer.analyzeProject();
    
    await analyzer.saveReport(report);
    
    console.log('\nüìã Analysis Summary:');
    console.log(`‚îú‚îÄ‚îÄ Files analyzed: ${report.projectMetrics.totalFiles}`);
    console.log(`‚îú‚îÄ‚îÄ Functions found: ${report.projectMetrics.totalFunctions}`);
    console.log(`‚îú‚îÄ‚îÄ Classes found: ${report.projectMetrics.totalClasses}`);
    console.log(`‚îú‚îÄ‚îÄ Patterns identified: ${report.patterns.length}`);
    console.log(`‚îú‚îÄ‚îÄ Consolidation opportunities: ${report.consolidationOpportunities.length}`);
    console.log(`‚îú‚îÄ‚îÄ Performance insights: ${report.performanceInsights.length}`);
    console.log(`‚îî‚îÄ‚îÄ Average complexity: ${report.projectMetrics.complexityScore.toFixed(2)}`);
    
    console.log('\nüí° Key Recommendations:');
    report.recommendations.immediate.slice(0, 3).forEach((rec, i) => {
      console.log(`${i + 1}. ${rec}`);
    });
    
    console.log('\n‚úÖ Architectural analysis complete! Check the full report for detailed recommendations.');
    
  } catch (error) {
    console.error('‚ùå Error during architectural analysis:', error);
    process.exit(1);
  }
}

// Execute if run directly
if (require.main === module) {
  main();
}

#!/usr/bin/env tsx

/**
 * Duplicate Method Detection Script
 * 
 * This script analyzes the codebase to identify methods with identical or very similar code.
 * It provides a detailed report of duplicate methods, including their location and code comparison.
 * 
 * Features:
 * - Detects exact duplicate methods
 * - Identifies similar methods with configurable similarity threshold
 * - Provides detailed reports with code comparisons
 * - Supports refactoring suggestions
 * - Works with TypeScript and JavaScript files
 * 
 * Usage:
 *   npx tsx scripts/analysis/duplicate-method-detector.ts
 *   npm run detect:duplicates
 */

import * as fs from 'fs';
import * as path from 'path';
import { createHash } from 'crypto';

// Types and interfaces
interface MethodInfo {
  name: string;
  signature: string;
  body: string;
  normalizedBody: string;
  hash: string;
  filePath: string;
  startLine: number;
  endLine: number;
  className?: string;
  isStatic: boolean;
  isAsync: boolean;
  parameters: string[];
  returnType?: string;
}

interface DuplicateGroup {
  hash: string;
  methods: MethodInfo[];
  type: 'exact' | 'similar';
  similarityScore?: number;
}

interface DetectionOptions {
  minMethodLength: number;
  similarityThreshold: number;
  includePrivateMethods: boolean;
  includeGettersSetters: boolean;
  excludePatterns: string[];
  maxFileSize: number;
}

interface DetectionResult {
  totalMethods: number;
  totalFiles: number;
  duplicateGroups: DuplicateGroup[];
  exactDuplicates: number;
  similarDuplicates: number;
  potentialSavings: {
    linesOfCode: number;
    duplicateFiles: number;
  };
}

// Default configuration
const DEFAULT_OPTIONS: DetectionOptions = {
  minMethodLength: 3, // Minimum lines of code in method body
  similarityThreshold: 0.85, // 85% similarity threshold
  includePrivateMethods: true,
  includeGettersSetters: false,
  excludePatterns: [
    'test',
    'spec',
    '.d.ts',
    'node_modules',
    'dist',
    'build'
  ],
  maxFileSize: 50000 // 50KB max file size
};

// Utility functions
class MethodExtractor {
  private static readonly METHOD_PATTERNS = [
    // Regular function declarations
    /(?:^|\n)\s*((?:export\s+)?(?:async\s+)?(?:static\s+)?(?:private\s+|public\s+|protected\s+)?(?:abstract\s+)?(?:readonly\s+)?(?:get\s+|set\s+)?(\w+)\s*\([^)]*\)\s*(?::\s*[^{]+)?\s*\{)/gm,
    // Arrow functions as class properties
    /(?:^|\n)\s*((?:private\s+|public\s+|protected\s+)?(?:static\s+)?(?:readonly\s+)?(\w+)\s*[:=]\s*(?:async\s+)?\([^)]*\)\s*(?::\s*[^=]+)?\s*=>\s*\{)/gm,
    // Method definitions in objects
    /(?:^|\n)\s*((\w+)\s*\([^)]*\)\s*(?::\s*[^{]+)?\s*\{)/gm
  ];

  static extractMethods(content: string, filePath: string): MethodInfo[] {
    const methods: MethodInfo[] = [];
    const lines = content.split('\n');

    for (const pattern of this.METHOD_PATTERNS) {
      let match;
      pattern.lastIndex = 0; // Reset regex state

      while ((match = pattern.exec(content)) !== null) {
        try {
          const methodInfo = this.parseMethodFromMatch(match, content, filePath, lines);
          if (methodInfo && this.isValidMethod(methodInfo)) {
            methods.push(methodInfo);
          }
        } catch (error) {
          // Skip malformed methods
          continue;
        }
      }
    }

    return this.deduplicateMethods(methods);
  }

  private static parseMethodFromMatch(
    match: RegExpExecArray,
    content: string,
    filePath: string,
    lines: string[]
  ): MethodInfo | null {
    const fullMatch = match[1];
    const methodName = match[2] || this.extractMethodName(fullMatch);
    
    if (!methodName || methodName.length < 2) return null;

    const startIndex = match.index!;
    const startLine = content.substring(0, startIndex).split('\n').length;
    
    // Find method body using brace matching
    const methodBody = this.extractMethodBody(content, startIndex + fullMatch.length - 1);
    if (!methodBody) return null;

    const endLine = startLine + methodBody.split('\n').length - 1;
    const normalizedBody = this.normalizeMethodBody(methodBody);
    
    return {
      name: methodName,
      signature: this.extractSignature(fullMatch),
      body: methodBody,
      normalizedBody,
      hash: this.generateMethodHash(normalizedBody),
      filePath,
      startLine,
      endLine,
      className: this.extractClassName(content, startIndex),
      isStatic: fullMatch.includes('static'),
      isAsync: fullMatch.includes('async'),
      parameters: this.extractParameters(fullMatch),
      returnType: this.extractReturnType(fullMatch)
    };
  }

  private static extractMethodName(signature: string): string {
    const nameMatch = signature.match(/(\w+)\s*\(/);
    return nameMatch ? nameMatch[1] : '';
  }

  private static extractMethodBody(content: string, startIndex: number): string | null {
    let braceCount = 0;
    let currentIndex = startIndex;
    let foundFirstBrace = false;
    
    while (currentIndex < content.length) {
      const char = content[currentIndex];
      
      if (char === '{') {
        braceCount++;
        foundFirstBrace = true;
      } else if (char === '}') {
        braceCount--;
        if (foundFirstBrace && braceCount === 0) {
          return content.substring(startIndex, currentIndex + 1);
        }
      }
      
      currentIndex++;
    }
    
    return null;
  }

  private static normalizeMethodBody(body: string): string {
    return body
      // Remove comments
      .replace(/\/\*[\s\S]*?\*\//g, '')
      .replace(/\/\/.*$/gm, '')
      // Normalize whitespace
      .replace(/\s+/g, ' ')
      // Remove unnecessary semicolons
      .replace(/;\s*}/g, '}')
      // Normalize string literals (basic)
      .replace(/"[^"]*"/g, '""')
      .replace(/'[^']*'/g, "''")
      .replace(/`[^`]*`/g, '``')
      // Remove trailing/leading spaces
      .trim();
  }

  private static generateMethodHash(normalizedBody: string): string {
    return createHash('md5').update(normalizedBody).digest('hex');
  }

  private static extractSignature(fullMatch: string): string {
    const signatureMatch = fullMatch.match(/(\w+\s*\([^)]*\)(?:\s*:\s*[^{]+)?)/);
    return signatureMatch ? signatureMatch[1].trim() : fullMatch.trim();
  }

  private static extractClassName(content: string, methodIndex: number): string | undefined {
    const beforeMethod = content.substring(0, methodIndex);
    const classMatch = beforeMethod.match(/class\s+(\w+)/g);
    if (classMatch && classMatch.length > 0) {
      const lastClass = classMatch[classMatch.length - 1];
      const nameMatch = lastClass.match(/class\s+(\w+)/);
      return nameMatch ? nameMatch[1] : undefined;
    }
    return undefined;
  }

  private static extractParameters(signature: string): string[] {
    const paramMatch = signature.match(/\(([^)]*)\)/);
    if (!paramMatch || !paramMatch[1].trim()) return [];
    
    return paramMatch[1]
      .split(',')
      .map(param => param.trim().split(':')[0].trim())
      .filter(param => param.length > 0);
  }

  private static extractReturnType(signature: string): string | undefined {
    const returnMatch = signature.match(/\):\s*([^{]+)/);
    return returnMatch ? returnMatch[1].trim() : undefined;
  }

  private static isValidMethod(method: MethodInfo): boolean {
    // Filter out very short methods
    const bodyLines = method.body.split('\n').length;
    if (bodyLines < DEFAULT_OPTIONS.minMethodLength) return false;
    
    // Filter out getters/setters if configured
    if (!DEFAULT_OPTIONS.includeGettersSetters && 
        (method.signature.includes('get ') || method.signature.includes('set '))) {
      return false;
    }
    
    // Filter out private methods if configured
    if (!DEFAULT_OPTIONS.includePrivateMethods && method.signature.includes('private')) {
      return false;
    }
    
    // Filter out common trivial methods
    const trivialPatterns = [
      /^{\s*return\s+\w+;\s*}$/,
      /^{\s*this\.\w+\s*=\s*\w+;\s*}$/,
      /^{\s*}$/
    ];
    
    return !trivialPatterns.some(pattern => pattern.test(method.normalizedBody));
  }

  private static deduplicateMethods(methods: MethodInfo[]): MethodInfo[] {
    const seen = new Set<string>();
    return methods.filter(method => {
      const key = `${method.filePath}:${method.startLine}:${method.name}`;
      if (seen.has(key)) return false;
      seen.add(key);
      return true;
    });
  }
}

// Main duplicate method detector class
class DuplicateMethodDetector {
  private options: DetectionOptions;

  constructor(options: Partial<DetectionOptions> = {}) {
    this.options = { ...DEFAULT_OPTIONS, ...options };
  }

  async detectDuplicates(rootPath: string = process.cwd()): Promise<DetectionResult> {
    console.log('üîç Starting duplicate method detection...');
    console.log(`üìÅ Scanning directory: ${rootPath}`);
    
    const files = this.findTypeScriptFiles(rootPath);
    console.log(`üìÑ Found ${files.length} TypeScript files to analyze`);
    
    const allMethods: MethodInfo[] = [];
    let processedFiles = 0;

    for (const file of files) {
      try {
        const methods = await this.extractMethodsFromFile(file);
        allMethods.push(...methods);
        processedFiles++;
        
        if (processedFiles % 50 === 0) {
          console.log(`‚è≥ Processed ${processedFiles}/${files.length} files...`);
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è Could not process file ${file}: ${error}`);
      }
    }

    console.log(`‚úÖ Extracted ${allMethods.length} methods from ${processedFiles} files`);
    
    const duplicateGroups = this.findDuplicateGroups(allMethods);
    
    return this.buildResult(allMethods, processedFiles, duplicateGroups);
  }

  private findTypeScriptFiles(rootPath: string): string[] {
    const files: string[] = [];
    
    const scanDirectory = (dirPath: string): void => {
      try {
        const entries = fs.readdirSync(dirPath, { withFileTypes: true });
        
        for (const entry of entries) {
          const fullPath = path.join(dirPath, entry.name);
          
          if (entry.isDirectory()) {
            // Skip excluded directories
            if (this.shouldExcludePath(entry.name)) continue;
            scanDirectory(fullPath);
          } else if (entry.isFile()) {
            // Include TypeScript files
            if (this.isTypeScriptFile(entry.name) && !this.shouldExcludePath(fullPath)) {
              files.push(fullPath);
            }
          }
        }
      } catch (error) {
        // Skip directories we can't read
      }
    };

    scanDirectory(rootPath);
    return files;
  }

  private shouldExcludePath(filePath: string): boolean {
    return this.options.excludePatterns.some(pattern => 
      filePath.includes(pattern)
    );
  }

  private isTypeScriptFile(fileName: string): boolean {
    return /\.(ts|tsx|js|jsx)$/.test(fileName) && !fileName.endsWith('.d.ts');
  }

  private async extractMethodsFromFile(filePath: string): Promise<MethodInfo[]> {
    try {
      const stats = fs.statSync(filePath);
      if (stats.size > this.options.maxFileSize) {
        console.warn(`‚ö†Ô∏è Skipping large file: ${filePath} (${stats.size} bytes)`);
        return [];
      }

      const content = fs.readFileSync(filePath, 'utf-8');
      return MethodExtractor.extractMethods(content, filePath);
    } catch (error) {
      console.warn(`‚ö†Ô∏è Error reading file ${filePath}: ${error}`);
      return [];
    }
  }

  private findDuplicateGroups(methods: MethodInfo[]): DuplicateGroup[] {
    const groups: DuplicateGroup[] = [];
    const methodsByHash = new Map<string, MethodInfo[]>();

    // Group methods by their normalized hash
    for (const method of methods) {
      if (!methodsByHash.has(method.hash)) {
        methodsByHash.set(method.hash, []);
      }
      methodsByHash.get(method.hash)!.push(method);
    }

    // Find exact duplicates
    for (const [hash, groupMethods] of methodsByHash) {
      if (groupMethods.length > 1) {
        groups.push({
          hash,
          methods: groupMethods,
          type: 'exact'
        });
      }
    }

    // Find similar methods (cross-compare remaining methods)
    const singleMethods = Array.from(methodsByHash.values())
      .filter(group => group.length === 1)
      .map(group => group[0]);

    const similarGroups = this.findSimilarMethods(singleMethods);
    groups.push(...similarGroups);

    return groups;
  }

  private findSimilarMethods(methods: MethodInfo[]): DuplicateGroup[] {
    const similarGroups: DuplicateGroup[] = [];
    const processed = new Set<number>();

    for (let i = 0; i < methods.length; i++) {
      if (processed.has(i)) continue;

      const similarMethods: MethodInfo[] = [methods[i]];
      
      for (let j = i + 1; j < methods.length; j++) {
        if (processed.has(j)) continue;

        const similarity = this.calculateSimilarity(methods[i], methods[j]);
        if (similarity >= this.options.similarityThreshold) {
          similarMethods.push(methods[j]);
          processed.add(j);
        }
      }

      if (similarMethods.length > 1) {
        similarGroups.push({
          hash: methods[i].hash,
          methods: similarMethods,
          type: 'similar',
          similarityScore: this.calculateGroupSimilarity(similarMethods)
        });
      }

      processed.add(i);
    }

    return similarGroups;
  }

  private calculateSimilarity(method1: MethodInfo, method2: MethodInfo): number {
    // Use Levenshtein distance for similarity calculation
    const normalized1 = method1.normalizedBody;
    const normalized2 = method2.normalizedBody;
    
    if (normalized1 === normalized2) return 1.0;
    
    const distance = this.levenshteinDistance(normalized1, normalized2);
    const maxLength = Math.max(normalized1.length, normalized2.length);
    
    return maxLength > 0 ? 1 - (distance / maxLength) : 0;
  }

  private levenshteinDistance(str1: string, str2: string): number {
    const matrix: number[][] = [];
    
    for (let i = 0; i <= str2.length; i++) {
      matrix[i] = [i];
    }
    
    for (let j = 0; j <= str1.length; j++) {
      matrix[0][j] = j;
    }
    
    for (let i = 1; i <= str2.length; i++) {
      for (let j = 1; j <= str1.length; j++) {
        if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
          matrix[i][j] = matrix[i - 1][j - 1];
        } else {
          matrix[i][j] = Math.min(
            matrix[i - 1][j - 1] + 1, // substitution
            matrix[i][j - 1] + 1,     // insertion
            matrix[i - 1][j] + 1      // deletion
          );
        }
      }
    }
    
    return matrix[str2.length][str1.length];
  }

  private calculateGroupSimilarity(methods: MethodInfo[]): number {
    if (methods.length < 2) return 1.0;
    
    let totalSimilarity = 0;
    let comparisons = 0;
    
    for (let i = 0; i < methods.length; i++) {
      for (let j = i + 1; j < methods.length; j++) {
        totalSimilarity += this.calculateSimilarity(methods[i], methods[j]);
        comparisons++;
      }
    }
    
    return comparisons > 0 ? totalSimilarity / comparisons : 0;
  }

  private buildResult(
    allMethods: MethodInfo[],
    processedFiles: number,
    duplicateGroups: DuplicateGroup[]
  ): DetectionResult {
    const exactDuplicates = duplicateGroups.filter(g => g.type === 'exact').length;
    const similarDuplicates = duplicateGroups.filter(g => g.type === 'similar').length;
    
    const potentialSavings = this.calculatePotentialSavings(duplicateGroups);
    
    return {
      totalMethods: allMethods.length,
      totalFiles: processedFiles,
      duplicateGroups,
      exactDuplicates,
      similarDuplicates,
      potentialSavings
    };
  }

  private calculatePotentialSavings(groups: DuplicateGroup[]): { linesOfCode: number; duplicateFiles: number } {
    let linesOfCode = 0;
    const duplicateFiles = new Set<string>();
    
    for (const group of groups) {
      if (group.methods.length > 1) {
        // Calculate potential savings (keep one, remove others)
        const duplicateMethodsCount = group.methods.length - 1;
        const avgLinesPerMethod = group.methods.reduce((sum, method) => 
          sum + (method.endLine - method.startLine + 1), 0) / group.methods.length;
        
        linesOfCode += Math.round(duplicateMethodsCount * avgLinesPerMethod);
        
        group.methods.forEach(method => duplicateFiles.add(method.filePath));
      }
    }
    
    return {
      linesOfCode,
      duplicateFiles: duplicateFiles.size
    };
  }
}

