#!/usr/bin/env node

import * as fs from 'fs';
import * as path from 'path';
import { execSync } from 'child_process';

/**
 * Post-Implementation Validation Script
 * Runs comprehensive checks after code changes to ensure quality and compliance
 * Part of Task 53.4 - Create New Post-Implementation Check Scripts
 */

interface ValidationRule {
  id: string;
  name: string;
  description: string;
  severity: 'error' | 'warning' | 'info';
  check: () => Promise<ValidationResult>;
}

interface ValidationResult {
  passed: boolean;
  message: string;
  details?: string[];
  suggestions?: string[];
}

interface ValidationReport {
  timestamp: string;
  project: string;
  task: string;
  summary: {
    totalChecks: number;
    passed: number;
    failed: number;
    warnings: number;
    errors: number;
  };
  results: Array<{
    rule: ValidationRule;
    result: ValidationResult;
  }>;
  overallStatus: 'PASS' | 'FAIL' | 'WARNING';
}

class PostImplementationValidator {
  private projectRoot: string;
  private reportDir: string;
  private timestamp: string;
  private rules: ValidationRule[];

  constructor() {
    this.projectRoot = process.cwd();
    this.reportDir = path.join(this.projectRoot, 'scripts', 'analysis', 'reports', 'quality');
    this.timestamp = new Date().toISOString().split('T')[0];
    
    // Ensure reports directory exists
    if (!fs.existsSync(this.reportDir)) {
      fs.mkdirSync(this.reportDir, { recursive: true });
    }

    this.rules = this.defineValidationRules();
  }

  /**
   * Define all validation rules for post-implementation checks
   */
  private defineValidationRules(): ValidationRule[] {
    return [
      {
        id: 'typescript-compilation',
        name: 'TypeScript Compilation',
        description: 'Verify that all TypeScript code compiles without errors',
        severity: 'error',
        check: async () => this.checkTypeScriptCompilation()
      },
      {
        id: 'test-execution',
        name: 'Test Execution',
        description: 'Ensure all tests pass after implementation changes',
        severity: 'error',
        check: async () => this.checkTestExecution()
      },
      {
        id: 'lint-compliance',
        name: 'Lint Compliance',
        description: 'Verify code follows linting rules and standards',
        severity: 'warning',
        check: async () => this.checkLintCompliance()
      },
      {
        id: 'import-resolution',
        name: 'Import Resolution',
        description: 'Check that all imports resolve correctly across workspaces',
        severity: 'error',
        check: async () => this.checkImportResolution()
      },
      {
        id: 'documentation-sync',
        name: 'Documentation Synchronization',
        description: 'Ensure documentation is updated and synchronized with code changes',
        severity: 'warning',
        check: async () => this.checkDocumentationSync()
      },
      {
        id: 'security-compliance',
        name: 'Security Compliance',
        description: 'Verify no new security vulnerabilities are introduced',
        severity: 'error',
        check: async () => this.checkSecurityCompliance()
      }
    ];
  }

  /**
   * Check TypeScript compilation across all workspaces
   */
  private async checkTypeScriptCompilation(): Promise<ValidationResult> {
    try {
      console.log('üîß Checking TypeScript compilation...');
      
      const workspaces = ['packages/backend', 'packages/frontend', 'packages/shared'];
      const errors: string[] = [];
      
      for (const workspace of workspaces) {
        try {
          const output = execSync(`npm run build --workspace=${workspace}`, {
            stdio: 'pipe',
            encoding: 'utf8',
            cwd: this.projectRoot
          });
        } catch (error: any) {
          errors.push(`${workspace}: ${error.message}`);
        }
      }
      
      if (errors.length === 0) {
        return {
          passed: true,
          message: 'All TypeScript code compiles successfully',
          details: [`Checked ${workspaces.length} workspaces`]
        };
      } else {
        return {
          passed: false,
          message: `TypeScript compilation failed in ${errors.length} workspace(s)`,
          details: errors,
          suggestions: [
            'Run npm run build in each failed workspace to see detailed errors',
            'Check for missing dependencies or type definitions',
            'Verify import paths are correct'
          ]
        };
      }
    } catch (error: any) {
      return {
        passed: false,
        message: 'Failed to check TypeScript compilation',
        details: [error.message]
      };
    }
  }

  /**
   * Check test execution across all workspaces
   */
  private async checkTestExecution(): Promise<ValidationResult> {
    try {
      console.log('üß™ Checking test execution...');
      
      const testResults: string[] = [];
      const errors: string[] = [];
      
      try {
        const output = execSync('npm run test:unit', {
          stdio: 'pipe',
          encoding: 'utf8',
          cwd: this.projectRoot
        });
        testResults.push('Unit tests: PASSED');
      } catch (error: any) {
        errors.push(`Unit tests: ${error.message}`);
      }
      
      if (errors.length === 0) {
        return {
          passed: true,
          message: 'All tests pass successfully',
          details: testResults
        };
      } else {
        return {
          passed: false,
          message: `Test execution failed`,
          details: errors,
          suggestions: [
            'Run npm run test:unit to see detailed test failures',
            'Check for breaking changes in implementation',
            'Update tests if API changes are intentional'
          ]
        };
      }
    } catch (error: any) {
      return {
        passed: false,
        message: 'Failed to execute tests',
        details: [error.message]
      };
    }
  }
  /**
   * Check lint compliance
   */
  private async checkLintCompliance(): Promise<ValidationResult> {
    try {
      console.log('üìã Checking lint compliance...');
      
      const output = execSync('npm run lint:root', {
        stdio: 'pipe',
        encoding: 'utf8',
        cwd: this.projectRoot
      });
      
      return {
        passed: true,
        message: 'Code passes all linting rules',
        details: ['No linting errors found']
      };
    } catch (error: any) {
      const errorOutput = error.stdout || error.message;
      const warningCount = (errorOutput.match(/warning/gi) || []).length;
      const errorCount = (errorOutput.match(/error/gi) || []).length;
      
      return {
        passed: errorCount === 0,
        message: `Linting found ${errorCount} errors and ${warningCount} warnings`,
        details: [errorOutput],
        suggestions: [
          'Run npm run lint:fix to automatically fix some issues',
          'Review and fix remaining linting errors manually'
        ]
      };
    }
  }

  /**
   * Check import resolution across workspaces
   */
  private async checkImportResolution(): Promise<ValidationResult> {
    try {
      console.log('üîó Checking import resolution...');
      
      const sourceFiles = this.getSourceFiles([
        path.join(this.projectRoot, 'packages'),
        path.join(this.projectRoot, 'scripts')
      ]);
      
      const unresolvedImports: string[] = [];
      
      for (const file of sourceFiles) {
        const content = fs.readFileSync(file, 'utf8');
        const imports = content.match(/^import.*from\s+['"][^'"]+['"];?$/gm) || [];
        
        for (const importStatement of imports) {
          const match = importStatement.match(/from\s+['"]([^'"]+)['"];?$/);
          if (match) {
            const importPath = match[1];
            if (importPath.startsWith('.') || importPath.startsWith('@jabbr/')) {
              // Check relative and workspace imports
              const resolvedPath = this.resolveImportPath(file, importPath);
              if (!resolvedPath) {
                unresolvedImports.push(`${path.relative(this.projectRoot, file)}: ${importPath}`);
              }
            }
          }
        }
      }
      
      if (unresolvedImports.length === 0) {
        return {
          passed: true,
          message: 'All imports resolve correctly',
          details: [`Checked ${sourceFiles.length} files`]
        };
      } else {
        return {
          passed: false,
          message: `Found ${unresolvedImports.length} unresolved imports`,
          details: unresolvedImports.slice(0, 10), // Limit output
          suggestions: [
            'Check file paths and ensure referenced files exist',
            'Verify workspace package configurations',
            'Update import paths if files were moved'
          ]
        };
      }
    } catch (error: any) {
      return {
        passed: false,
        message: 'Failed to check import resolution',
        details: [error.message]
      };
    }
  }

  /**
   * Check documentation synchronization
   */
  private async checkDocumentationSync(): Promise<ValidationResult> {
    try {
      console.log('üìñ Checking documentation synchronization...');
      
      const output = execSync('npx tsx scripts/validate-documentation-precise.ts', {
        stdio: 'pipe',
        encoding: 'utf8',
        cwd: this.projectRoot
      });
      
      return {
        passed: true,
        message: 'Documentation is synchronized and up-to-date',
        details: ['All documentation validation checks passed']
      };
    } catch (error: any) {
      const errorOutput = error.stdout || error.message;
      const criticalIssues = (errorOutput.match(/critical issue/gi) || []).length;
      
      return {
        passed: criticalIssues === 0,
        message: `Documentation validation found ${criticalIssues} critical issues`,
        details: [errorOutput],
        suggestions: [
          'Update documentation to reflect recent code changes',
          'Check date stamps and version numbers',
          'Ensure all referenced files exist'
        ]
      };
    }
  }

  /**
   * Check security compliance
   */
  private async checkSecurityCompliance(): Promise<ValidationResult> {
    try {
      console.log('üîí Checking security compliance...');
      
      const output = execSync('npm run security:check', {
        stdio: 'pipe',
        encoding: 'utf8',
        cwd: this.projectRoot
      });
      
      return {
        passed: true,
        message: 'No security violations detected',
        details: ['Security checks passed']
      };
    } catch (error: any) {
      const errorOutput = error.stdout || error.message;
      const errorCount = (errorOutput.match(/error/gi) || []).length;
      
      return {
        passed: errorCount === 0,
        message: `Security check found ${errorCount} issues`,
        details: [errorOutput],
        suggestions: [
          'Review and fix security violations',
          'Consider using safer alternatives for flagged patterns',
          'Add security exceptions if issues are false positives'
        ]
      };
    }
  }
  /**
   * Get all source files recursively
   */
  private getSourceFiles(dirs: string[], extensions: string[] = ['.ts', '.tsx', '.js', '.jsx']): string[] {
    const files: string[] = [];
    
    const scanDirectory = (currentDir: string) => {
      if (!fs.existsSync(currentDir)) return;
      
      const entries = fs.readdirSync(currentDir, { withFileTypes: true });
      
      for (const entry of entries) {
        const fullPath = path.join(currentDir, entry.name);
        
        if (entry.isDirectory()) {
          // Skip node_modules, dist, build, etc.
          if (['node_modules', 'dist', 'build', 'coverage', '.next', 'out'].includes(entry.name)) {
            continue;
          }
          scanDirectory(fullPath);
        } else if (entry.isFile()) {
          const ext = path.extname(entry.name);
          if (extensions.includes(ext)) {
            files.push(fullPath);
          }
        }
      }
    };
    
    for (const dir of dirs) {
      scanDirectory(dir);
    }
    return files;
  }

  /**
   * Resolve import path to actual file
   */
  private resolveImportPath(fromFile: string, importPath: string): string | null {
    try {
      if (importPath.startsWith('@jabbr/')) {
        // Workspace import
        const workspaceName = importPath.split('/')[1];
        const workspacePath = path.join(this.projectRoot, 'packages', workspaceName);
        const packageJson = path.join(workspacePath, 'package.json');
        
        if (fs.existsSync(packageJson)) {
          const pkg = JSON.parse(fs.readFileSync(packageJson, 'utf8'));
          const mainFile = pkg.main || 'dist/index.js';
          return path.join(workspacePath, mainFile);
        }
        return null;
      } else if (importPath.startsWith('.')) {
        // Relative import
        const basePath = path.dirname(fromFile);
        const fullPath = path.resolve(basePath, importPath);
        
        // Try different extensions
        const possibleFiles = [
          fullPath,
          fullPath + '.ts',
          fullPath + '.tsx',
          fullPath + '.js',
          fullPath + '.jsx',
          path.join(fullPath, 'index.ts'),
          path.join(fullPath, 'index.tsx'),
          path.join(fullPath, 'index.js'),
          path.join(fullPath, 'index.jsx')
        ];
        
        for (const file of possibleFiles) {
          if (fs.existsSync(file)) {
            return file;
          }
        }
        return null;
      }
      
      return null; // External packages are not checked
    } catch {
      return null;
    }
  }

  /**
   * Run all validation checks
   */
  async validate(): Promise<ValidationReport> {
    console.log('üöÄ Starting post-implementation validation...');
    console.log('üìÅ Project:', this.projectRoot);
    console.log('');
    
    const results: Array<{ rule: ValidationRule; result: ValidationResult }> = [];
    let passed = 0;
    let failed = 0;
    let warnings = 0;
    let errors = 0;
    
    for (const rule of this.rules) {
      console.log(`üîç Running: ${rule.name}...`);
      const result = await rule.check();
      results.push({ rule, result });
      
      if (result.passed) {
        passed++;
        console.log(`‚úÖ ${rule.name}: PASSED`);
      } else {
        failed++;
        if (rule.severity === 'error') {
          errors++;
          console.log(`‚ùå ${rule.name}: FAILED (ERROR)`);
        } else {
          warnings++;
          console.log(`‚ö†Ô∏è  ${rule.name}: FAILED (WARNING)`);
        }
      }
      console.log('');
    }
    
    const overallStatus = errors > 0 ? 'FAIL' : warnings > 0 ? 'WARNING' : 'PASS';
    
    const report: ValidationReport = {
      timestamp: new Date().toISOString(),
      project: 'JBR Trading Platform',
      task: 'Task 53.4 - Post-Implementation Validation',
      summary: {
        totalChecks: this.rules.length,
        passed,
        failed,
        warnings,
        errors
      },
      results,
      overallStatus
    };
    
    // Save report
    const reportPath = path.join(this.reportDir, `post-implementation-${this.timestamp}.json`);
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
    
    // Display summary
    console.log('üéØ POST-IMPLEMENTATION VALIDATION SUMMARY:');
    console.log(`üìä Overall Status: ${overallStatus}`);
    console.log(`‚úÖ Passed: ${passed}/${this.rules.length}`);
    console.log(`‚ùå Failed: ${failed}/${this.rules.length}`);
    console.log(`üö® Errors: ${errors}`);
    console.log(`‚ö†Ô∏è  Warnings: ${warnings}`);
    console.log('');
    
    if (failed > 0) {
      console.log('‚ùå FAILED CHECKS:');
      for (const { rule, result } of results) {
        if (!result.passed) {
          console.log(`   ${rule.name}: ${result.message}`);
          if (result.suggestions) {
            result.suggestions.forEach(suggestion => {
              console.log(`     üí° ${suggestion}`);
            });
          }
        }
      }
    }
    
    console.log(`üìã Full report saved: ${reportPath}`);
    
    return report;
  }
}

// Run validation if called directly
if (require.main === module) {
  const validator = new PostImplementationValidator();
  validator.validate()
    .then(report => {
      process.exit(report.overallStatus === 'FAIL' ? 1 : 0);
    })
    .catch(error => {
      console.error('‚ùå Validation failed:', error);
      process.exit(1);
    });
}

export { PostImplementationValidator, ValidationReport, ValidationResult, ValidationRule };

#!/usr/bin/env node

import { execSync } from 'child_process';
import * as fs from 'fs';
import * as path from 'path';

/**
* Production Violations Analyzer
* Implements Task 41.4 - Identify and Categorize Production Violations
* 
* This script identifies and categorizes code violations that would prevent
* the application from being production-ready, prioritizing critical issues.
*/

interface ViolationCategories {
 CRITICAL: string;
 HIGH: string;
 MEDIUM: string;
 LOW: string;
 INFO: string;
}

interface TypeScriptViolation {
 file: string;
 line: number;
 column: number;
 code: string;
 message: string;
 severity: string;
}

interface ESLintViolation {
 file: string;
 line: number;
 column: number;
 level: string;
 message: string;
 rule: string;
 severity: string;
}

interface CheckResult {
 category: string;
 status: string;
 violations: (TypeScriptViolation | ESLintViolation)[];
 message: string;
}

interface CategoryStats {
 CRITICAL: number;
 HIGH: number;
 MEDIUM: number;
 LOW: number;
 INFO: number;
}

interface DuplicationResults {
 summary?: {
   totalIssues?: number;
   severity?: string;
 };
}

interface ProductionReport {
 timestamp: string;
 project: string;
 task: string;
 productionReadiness: string;
 checks: CheckResult[];
 violations: {
   total: number;
   byCategory: CategoryStats;
   details: any[];
 };
 duplicationIssues: {
   total: number;
   severity: string;
 } | null;
 recommendations: string[];
 blockers: number;
}

class ProductionViolationsAnalyzer {
 private projectRoot: string;
 private reportDir: string;
 private timestamp: string;
 private violationCategories: ViolationCategories;

 constructor() {
   this.projectRoot = process.cwd();
   this.reportDir = path.join(this.projectRoot, 'scripts', 'analysis', 'reports', 'quality');
   this.timestamp = new Date().toISOString().split('T')[0];
   
   // Ensure reports directory exists
   if (!fs.existsSync(this.reportDir)) {
     fs.mkdirSync(this.reportDir, { recursive: true });
   }
   
   // Production readiness categories
   this.violationCategories = {
     CRITICAL: 'Blocks production deployment',
     HIGH: 'Significant production risk',
     MEDIUM: 'Should be fixed before production',
     LOW: 'Improve before production',
     INFO: 'Consider for code quality'
   };
 }

 /**
  * Run TypeScript compilation check
  */
 checkTypeScriptCompilation(): CheckResult {
   console.log('üîç Checking TypeScript compilation...');
   
   try {
     execSync('npx tsc --noEmit', { stdio: 'pipe' });
     return {
       category: 'typescript',
       status: 'pass',
       violations: [],
       message: 'TypeScript compilation successful'
     };
   } catch (error: any) {
     const output: string = error.stdout?.toString() || error.stderr?.toString() || '';
     const violations: TypeScriptViolation[] = this.parseTypeScriptErrors(output);
     
     return {
       category: 'typescript',
       status: 'fail',
       violations,
       message: `TypeScript compilation failed with ${violations.length} errors`
     };
   }
 }

 /**
  * Parse TypeScript compilation errors
  */
 parseTypeScriptErrors(output: string): TypeScriptViolation[] {
   const violations: TypeScriptViolation[] = [];
   const lines: string[] = output.split('\n');
   
   for (const line of lines) {
     if (line.includes('error TS')) {
       const match: RegExpMatchArray | null = line.match(/(.+)\((\d+),(\d+)\): error (TS\d+): (.+)/);
       if (match) {
         violations.push({
           file: match[1],
           line: parseInt(match[2]),
           column: parseInt(match[3]),
           code: match[4],
           message: match[5],
           severity: this.categorizeTypeScriptError(match[4])
         });
       }
     }
   }
   
   return violations;
 }

 /**
  * Categorize TypeScript errors by production impact
  */
 categorizeTypeScriptError(errorCode: string): string {
   const criticalErrors: string[] = ['TS2304', 'TS2339', 'TS2345', 'TS2322', 'TS2532'];
   const highErrors: string[] = ['TS2531', 'TS2538', 'TS2571', 'TS2749'];
   
   if (criticalErrors.includes(errorCode)) {return 'CRITICAL';}
   if (highErrors.includes(errorCode)) {return 'HIGH';}
   return 'MEDIUM';
 }

 /**
  * Check for security violations
  */
 checkSecurityViolations(): CheckResult {
   console.log('üîí Checking security violations...');
   
   try {
     execSync('npm run security:check', { stdio: 'pipe' });
     return {
       category: 'security',
       status: 'pass',
       violations: [],
       message: 'No security violations found'
     };
   } catch (error: any) {
     const output: string = error.stdout?.toString() || error.stderr?.toString() || '';
     return {
       category: 'security',
       status: 'fail',
       violations: this.parseESLintOutput(output, 'security'),
       message: 'Security violations found'
     };
   }
 }

 /**
  * Check critical ESLint violations
  */
 checkESLintViolations(): CheckResult {
   console.log('üìã Checking ESLint violations...');
   
   try {
     // Use a simplified ESLint config to avoid dependency issues
     const simpleConfig: string = this.createSimpleESLintConfig();
     const tempConfigPath: string = path.join(this.reportDir, 'temp-eslint.js');
     fs.writeFileSync(tempConfigPath, simpleConfig);
     
     execSync(`npx eslint . --config "${tempConfigPath}" --ext .ts,.tsx,.js,.jsx`, { stdio: 'pipe' });
     
     // Clean up temp config
     fs.unlinkSync(tempConfigPath);
     
     return {
       category: 'eslint',
       status: 'pass',
       violations: [],
       message: 'No ESLint violations found'
     };
   } catch (error: any) {
     const output: string = error.stdout?.toString() || error.stderr?.toString() || '';
     
     // Clean up temp config if it exists
     const tempConfigPath: string = path.join(this.reportDir, 'temp-eslint.js');
     if (fs.existsSync(tempConfigPath)) {
       fs.unlinkSync(tempConfigPath);
     }
     
     return {
       category: 'eslint',
       status: 'fail',
       violations: this.parseESLintOutput(output, 'eslint'),
       message: 'ESLint violations found'
     };
   }
 }

 /**
  * Create a simple ESLint config for production checks
  */
 createSimpleESLintConfig(): string {
   return `
module.exports = {
 env: {
   node: true,
   es2022: true
 },
 extends: ['eslint:recommended'],
 parserOptions: {
   ecmaVersion: 2022,
   sourceType: 'module'
 },
 rules: {
   'no-console': 'warn',
   'no-debugger': 'error',
   'no-alert': 'error',
   'no-eval': 'error',
   'no-implied-eval': 'error',
   'no-new-func': 'error',
   'no-script-url': 'error',
   'no-throw-literal': 'error',
   'no-unused-vars': 'error',
   'no-undef': 'error'
 },
 ignorePatterns: [
   'dist/',
   'build/',
   'node_modules/',
   'coverage/',
   '*.d.ts'
 ]
};`;
 }

 /**
  * Parse ESLint output to extract violations
  */
 parseESLintOutput(output: string, type: string): ESLintViolation[] {
   const violations: ESLintViolation[] = [];
   const lines: string[] = output.split('\n');
   
   for (const line of lines) {
     if (line.includes('error') || line.includes('warning')) {
       const match: RegExpMatchArray | null = line.match(/(.+):(\d+):(\d+): (error|warning) (.+) (.+)/);
       if (match) {
         violations.push({
           file: match[1],
           line: parseInt(match[2]),
           column: parseInt(match[3]),
           level: match[4],
           message: match[5],
           rule: match[6],
           severity: this.categorizeESLintViolation(match[6], match[4])
         });
       }
     }
   }
   
   return violations;
 }

 /**
  * Categorize ESLint violations by production impact
  */
 categorizeESLintViolation(rule: string, level: string): string {
   const criticalRules: string[] = ['no-eval', 'no-implied-eval', 'no-new-func', 'security/'];
   const highRules: string[] = ['no-debugger', 'no-alert', 'no-unused-vars', 'no-undef'];
   
   if (level === 'error' && criticalRules.some(r => rule.includes(r))) {return 'CRITICAL';}
   if (level === 'error' && highRules.some(r => rule.includes(r))) {return 'HIGH';}
   if (level === 'error') {return 'MEDIUM';}
   return 'LOW';
 }

 /**
  * Generate comprehensive production violations report
  */
 async generateProductionReport(): Promise<ProductionReport> {
   console.log('üìä Generating comprehensive production violations report...');
   
   const checks: CheckResult[] = [
     this.checkTypeScriptCompilation(),
     this.checkSecurityViolations(),
     this.checkESLintViolations()
   ];
   
   // Analyze duplication results from previous tasks
   const duplicationResults: DuplicationResults | null = this.loadDuplicationResults();
   
   const allViolations: any[] = [];
   const categoryStats: CategoryStats = {
     CRITICAL: 0,
     HIGH: 0,
     MEDIUM: 0,
     LOW: 0,
     INFO: 0
   };
   
   // Process each check
   for (const check of checks) {
     for (const violation of check.violations || []) {
       allViolations.push({
         ...violation,
         category: check.category,
         checkStatus: check.status
       });
       
       // Type-safe severity handling
       const severity = violation.severity?.toUpperCase() as keyof CategoryStats;
       if (severity && severity in categoryStats) {
         categoryStats[severity]++;
       } else {
         // Default to MEDIUM for unknown severities
         categoryStats.MEDIUM++;
       }
     }
   }
   
   // Add duplication violations from Task 41.2
   if (duplicationResults && duplicationResults.summary) {
     const dupCount: number = duplicationResults.summary.totalIssues || 0;
     if (dupCount > 0) {
       categoryStats.HIGH += dupCount; // Duplication is HIGH severity for production
     }
   }
   
   const report: ProductionReport = {
     timestamp: new Date().toISOString(),
     project: 'JBR Trading Platform',
     task: 'Task 41.4 - Identify and Categorize Production Violations',
     productionReadiness: this.assessProductionReadiness(categoryStats),
     checks,
