#!/usr/bin/env node

/**
 * Documentation Validation Script
 * Validates consistency across all project documentation files
 */

import * as fs from 'fs';
import * as path from 'path';

// Expected values for consistency checking
interface ExpectedValues {
  version: string;
  lastUpdated: string;
  totalTasks: number;
  completedTasks: number;
  completionPercentage: string;
}

const EXPECTED_VALUES: ExpectedValues = {  // check mcp get_tasks actuall tasks etc.
  version: '1.0.0',
  lastUpdated: 'July 3, 2025',
  totalTasks: 37,
  completedTasks: 20,
  completionPercentage: '54%'
};

// Files to validate
const DOCS_TO_VALIDATE: string[] = [
  'README.md',
  'PROJECT_STATUS.md', 
  'PROJECT_STATUS_UPDATE.md',
  'PRODUCTION_GUIDE.md',
  'CONFIGURATION_GUIDE.md',
  'docs/TASK_STATUS_REPORT.md',
  'docs/DOCUMENTATION_MAINTENANCE.md',
  'packages/backend/tests/README.md',
  'packages/backend/tests/TESTING_GUIDELINES.md'
];

class DocumentationValidator {
  private issues: string[] = [];
  private warnings: string[] = [];

  validateFile(filePath: string): void {
    console.log(`Validating: ${filePath}`);
    
    if (!fs.existsSync(filePath)) {
      this.issues.push(`File not found: ${filePath}`);
      return;
    }

    const content: string = fs.readFileSync(filePath, 'utf8');
    this.validateContent(filePath, content);
  }

  private validateContent(filePath: string, content: string): void {
    // Check for version consistency
    const versionMatches: RegExpMatchArray | null = content.match(/Version:?\s*([^\s\n]+)/gi);
    if (versionMatches) {
      versionMatches.forEach(match => {
        if (!match.includes(EXPECTED_VALUES.version)) {
          this.issues.push(`${filePath}: Inconsistent version found: ${match}`);
        }
      });
    }

    // Check for date consistency
    const dateMatches: RegExpMatchArray | null = content.match(/Last Updated:?\s*([^\n]+)/gi);
    if (dateMatches) {
      dateMatches.forEach(match => {
        if (!match.includes(EXPECTED_VALUES.lastUpdated)) {
          this.issues.push(`${filePath}: Inconsistent date found: ${match}`);
        }
      });
    }

    // Check for task completion consistency
    const taskMatches: RegExpMatchArray | null = content.match(/(\d+)\s*of\s*(\d+)\s*tasks?/gi);
    if (taskMatches) {
      taskMatches.forEach(match => {
        const numbers: RegExpMatchArray | null = match.match(/(\d+)/g);
        if (numbers && numbers.length >= 2) {
          const completed: number = parseInt(numbers[0]);
          const total: number = parseInt(numbers[1]);
          
          if (completed !== EXPECTED_VALUES.completedTasks || total !== EXPECTED_VALUES.totalTasks) {
            this.issues.push(`${filePath}: Inconsistent task count: ${match} (expected: ${EXPECTED_VALUES.completedTasks} of ${EXPECTED_VALUES.totalTasks})`);
          }
        }
      });
    }

    // Check for completion percentage consistency
    const percentMatches: RegExpMatchArray | null = content.match(/(\d+)%\s*complet/gi);
    if (percentMatches) {
      percentMatches.forEach(match => {
        if (!match.includes('54%')) {
          this.issues.push(`${filePath}: Inconsistent completion percentage: ${match} (expected: 54%)`);
        }
      });
    }

    // Check for future dates (warning)
    if (content.includes('2026') || content.includes('January 2025')) {
      this.warnings.push(`${filePath}: Contains future dates that may need review`);
    }

    // Check for broken links (basic check)
    const linkMatches: RegExpMatchArray | null = content.match(/\[([^\]]+)\]\(([^)]+)\)/g);
    if (linkMatches) {
      linkMatches.forEach(link => {
        const urlMatch: RegExpMatchArray | null = link.match(/\]\(([^)]+)\)/);
        if (urlMatch) {
          const url: string = urlMatch[1];
          if (url.startsWith('./') || url.startsWith('../') || !url.startsWith('http')) {
            const linkPath: string = path.resolve(path.dirname(filePath), url);
            if (!fs.existsSync(linkPath)) {
              this.warnings.push(`${filePath}: Potentially broken link: ${url}`);
            }
          }
        }
      });
    }
  }

  generateReport(): boolean {
    console.log('\n=== DOCUMENTATION VALIDATION REPORT ===\n');

    if (this.issues.length === 0 && this.warnings.length === 0) {
      console.log('‚úÖ All documentation appears consistent and valid!');
      return true;
    }

    if (this.issues.length > 0) {
      console.log(`‚ùå Found ${this.issues.length} issue(s):`);
      this.issues.forEach(issue => console.log(`  - ${issue}`));
      console.log('');
    }

    if (this.warnings.length > 0) {
      console.log(`‚ö†Ô∏è  Found ${this.warnings.length} warning(s):`);
      this.warnings.forEach(warning => console.log(`  - ${warning}`));
      console.log('');
    }

    return this.issues.length === 0;
  }

  validateAll(): boolean {
    console.log('Starting documentation validation...\n');
    
    DOCS_TO_VALIDATE.forEach(file => {
      this.validateFile(file);
    });

    return this.generateReport();
  }
}

// Run validation
const validator = new DocumentationValidator();
const isValid: boolean = validator.validateAll();

// Exit with appropriate code
process.exit(isValid ? 0 : 1);

/**
 * SMA Strategy Backtest
 * 
 * This script runs a backtest of the SMA signal processor against synthetic price data
 * to validate its performance and signal generation.
 */

import * as fs from 'fs';
import * as path from 'path';

import { generateSyntheticCandles, type Candle } from '../../../shared/src/test-utils/data-generators';
import type { SMASignalConfig } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.4,
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Main function to run the backtest
async function runBacktest() {
  console.log('Starting SMA Backtest');
  console.log(`Configuration: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}, Threshold: ${config.confidenceThreshold}`);
  
  // Generate test data using shared utility
  const candles = generateSyntheticCandles({
    count: 200,
    startPrice: 100,
    trend: 'mixed',
    timeframe: '15m'
  });
  
  const processor = new SMASignalProcessor(config);
  
  interface SignalRecord {
    timestamp: number;
    price: number;
    signal: 'buy' | 'sell';
    confidence: number;
    reason: string;
  }

  interface TradeRecord {
    type: string;
    timestamp: number;
    price: number;
    profit?: number;
    reason?: string;
  }
  
  const signals: SignalRecord[] = [];
  const trades: TradeRecord[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      if (!candle) {continue;} // Skip if candle is undefined
      
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell' && position.entryPrice !== null) {
          const profit = position.entryPrice - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy' && position.entryPrice !== null) {
          const profit = candle.close - position.entryPrice;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null && position.entryPrice !== null) {
    const lastCandle = candles[candles.length - 1];
    if (lastCandle) {
      let profit = 0;
      
      if (position.side === 'buy') {
        profit = lastCandle.close - position.entryPrice;
        trades.push({
          type: 'Close Long (End)',
          timestamp: lastCandle.timestamp,
          price: lastCandle.close,
          profit
        });
      } else if (position.side === 'sell') {
        profit = position.entryPrice - lastCandle.close;
        trades.push({
          type: 'Close Short (End)',
          timestamp: lastCandle.timestamp,
          price: lastCandle.close,
          profit
        });
      }
      
      pnl += profit;
    }
  }
  
  // Log results
  console.log(`Generated ${signals.length} signals and ${trades.length} trades`);
  console.log(`Final P&L: ${pnl.toFixed(2)}`);
  
  // Generate report
  const report = {
    config,
    summary: {
      signalCount: signals.length,
      tradeCount: trades.length,
      pnl
    },
    signals,
    trades
  };
  
  // Write report to file
  fs.writeFileSync(
    path.join(__dirname, 'sma-backtest-results.json'),
    JSON.stringify(report, null, 2)
  );
  
  console.log('Backtest complete. Results saved to sma-backtest-results.json');
}

// Run the backtest
runBacktest().catch(console.error);

/**
 * Compare original and improved SMA signal processors
 * 
 * This script runs both the original and improved SMA signal processors
 * on the same data and compares their signal generation.
 */

import * as fs from 'fs';
import * as path from 'path';

import { ImprovedSMASignalProcessor } from '../../src/JabbrLabs/signals/sma/improved-sma-signal-processor';
import type { SMASignalConfig} from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalOutput } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';
import type { Candle } from '../../src/JabbrLabs/target-reacher/interfaces';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.4, // Lowered threshold for comparison
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Generate synthetic price data for testing
function generateSyntheticCandles(options: {
  startPrice: number,
  dataPoints: number,
  volatility: number,
  trendStrength: number,
  trendChangeProbability: number
}): Candle[] {
  const candles: Candle[] = [];
  let currentPrice = options.startPrice;
  let trend = 1; // 1 = up, -1 = down

  for (let i = 0; i < options.dataPoints; i++) {
    // Potentially change trend
    if (Math.random() < options.trendChangeProbability) {
      trend *= -1;
    }
    
    // Calculate price movement
    const trendMovement = trend * options.trendStrength * currentPrice;
    const randomMovement = (Math.random() * 2 - 1) * options.volatility * currentPrice;
    const movement = trendMovement + randomMovement;
    
    // Calculate candle prices
    const open = currentPrice;
    const close = currentPrice + movement;
    const high = Math.max(open, close) + Math.random() * options.volatility * currentPrice;
    const low = Math.min(open, close) - Math.random() * options.volatility * currentPrice;
    const volume = Math.random() * 100000 + 10000;
    
    // Create candle
    candles.push({
      timestamp: Date.now() + i * 900000, // 15-minute intervals
      open,
      high,
      low,
      close,
      volume
    });
    
    // Update current price for next iteration
    currentPrice = close;
  }
  
  return candles;
}

// Process signals and execute trades
function processAndTrade(processor: SMASignalProcessor | ImprovedSMASignalProcessor, candles: Candle[]) {
  const signals: any[] = [];
  const trades: any[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell') {
          const profit = position.entryPrice! - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy') {
          const profit = candle.close - position.entryPrice!;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null) {
    const lastCandle = candles[candles.length - 1];
    let profit = 0;
    
    if (position.side === 'buy') {
      profit = lastCandle.close - position.entryPrice!;
      trades.push({
        type: 'Close Long (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    } else {
      profit = position.entryPrice! - lastCandle.close;
      trades.push({
        type: 'Close Short (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    }
    
    pnl += profit;
  }
  
  return {
    signals,
    trades,
    pnl
  };
}

// Main test function
async function runComparisonTest() {
  console.log('Starting SMA Signal Processor Comparison Test');
  console.log(`Configuration: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}`);
  
  // Generate test data
  const candles = generateSyntheticCandles({
