interface PerformanceTest {
  name: string;
  category: 'api' | 'database' | 'strategy' | 'websocket' | 'calculation' | 'other';
  testFunction: () => Promise<any>;
  iterations?: number;
  warmupIterations?: number;
}

class PerformanceAnalyzer {
  private systemMonitor: SystemMonitorService;
  private testResults: any[] = [];

  constructor() {
    this.systemMonitor = new SystemMonitorService({
      collectInterval: 1000, // 1 second for detailed monitoring
      enableAlerts: true
    });
  }

  /**
   * Run all performance tests
   */
  async runAnalysis(): Promise<void> {
    console.log('üöÄ Starting Performance Analysis...\n');
    
    // Start system monitoring
    this.systemMonitor.start();
    
    try {
      // Define test suites
      const testSuites: PerformanceTestSuite[] = [
        this.createCalculationTests(),
        this.createIndicatorTests(),
        this.createSignalProcessingTests(),
        this.createMemoryTests()
      ];

      // Run each test suite
      for (const suite of testSuites) {
        await this.runTestSuite(suite);
      }

      // Generate comprehensive report
      await this.generateReport();

    } finally {
      this.systemMonitor.stop();
    }
  }

  /**
   * Create calculation performance tests
   */
  private createCalculationTests(): PerformanceTestSuite {
    return {
      name: 'Mathematical Calculations',
      description: 'Tests performance of mathematical operations used in trading algorithms',
      tests: [
        {
          name: 'Simple Moving Average Calculation',
          category: 'calculation',
          testFunction: async () => {
            const data = Array.from({ length: 1000 }, (_, i) => i + Math.random());
            const period = 20;
            
            // Calculate SMA
            const sma: number[] = [];
            for (let i = period - 1; i < data.length; i++) {
              const sum = data.slice(i - period + 1, i + 1).reduce((a, b) => a + b, 0);
              sma.push(sum / period);
            }
            
            return sma.length;
          },
          iterations: 1000
        },
        {
          name: 'Exponential Moving Average Calculation',
          category: 'calculation',
          testFunction: async () => {
            const data = Array.from({ length: 1000 }, (_, i) => i + Math.random());
            const period = 20;
            const multiplier = 2 / (period + 1);
            
            // Calculate EMA
            let ema = data[0];
            for (let i = 1; i < data.length; i++) {
              ema = (data[i] * multiplier) + (ema * (1 - multiplier));
            }
            
            return ema;
          },
          iterations: 1000
        },
        {
          name: 'RSI Calculation',
          category: 'calculation',
          testFunction: async () => {
            const prices = Array.from({ length: 100 }, () => 100 + Math.random() * 50);
            const period = 14;
            
            // Calculate price changes
            const changes = prices.slice(1).map((price, i) => price - prices[i]);
            
            // Separate gains and losses
            const gains = changes.map(change => change > 0 ? change : 0);
            const losses = changes.map(change => change < 0 ? -change : 0);
            
            // Calculate average gain and loss
            const avgGain = gains.slice(0, period).reduce((a, b) => a + b) / period;
            const avgLoss = losses.slice(0, period).reduce((a, b) => a + b) / period;
            
            // Calculate RS and RSI
            const rs = avgGain / avgLoss;
            const rsi = 100 - (100 / (1 + rs));
            
            return rsi;
          },
          iterations: 500
        },
        {
          name: 'Large Array Processing',
          category: 'calculation',
          testFunction: async () => {
            const size = 100000;
            const data = Array.from({ length: size }, (_, i) => Math.random() * 100);
            
            // Perform multiple operations
            const filtered = data.filter(x => x > 50);
            const mapped = filtered.map(x => x * 2);
            const reduced = mapped.reduce((sum, x) => sum + x, 0);
            
            return reduced;
          },
          iterations: 100
        }
      ]
    };
  }

  /**
   * Create indicator service tests
   */
  private createIndicatorTests(): PerformanceTestSuite {
    return {
      name: 'Indicator Service Performance',
      description: 'Tests performance of the indicator calculation service',
      tests: [
        {
          name: 'Multiple Indicator Calculations',
          category: 'calculation',
          testFunction: async () => {
            const indicatorService = new IndicatorService();
            const prices = Array.from({ length: 200 }, () => ({
              timestamp: Date.now(),
              open: 100 + Math.random() * 10,
              high: 105 + Math.random() * 10,
              low: 95 + Math.random() * 10,
              close: 100 + Math.random() * 10,
              volume: 1000 + Math.random() * 500
            }));

            // Calculate multiple indicators
            const sma20 = indicatorService.calculateSMA(prices.map(p => p.close), 20);
            const sma50 = indicatorService.calculateSMA(prices.map(p => p.close), 50);
            const ema12 = indicatorService.calculateEMA(prices.map(p => p.close), 12);
            const rsi = indicatorService.calculateRSI(prices.map(p => p.close), 14);

            return { sma20: sma20.length, sma50: sma50.length, ema12: ema12.length, rsi: rsi.length };
          },
          iterations: 200
        }
      ]
    };
  }

  /**
   * Create signal processing tests
   */
  private createSignalProcessingTests(): PerformanceTestSuite {
    return {
      name: 'Signal Processing Performance',
      description: 'Tests performance of signal processing algorithms',
      tests: [
        {
          name: 'SMA Signal Generation',
          category: 'calculation',
          testFunction: async () => {
            const prices = Array.from({ length: 500 }, () => Math.random() * 100);
            const signals = [];
            
            for (let i = 20; i < prices.length; i++) {
              const sma20 = prices.slice(i - 20, i).reduce((a, b) => a + b) / 20;
              const sma50 = i >= 50 ? prices.slice(i - 50, i).reduce((a, b) => a + b) / 50 : 0;
              
              if (sma20 > sma50) {
                signals.push('BUY');
              } else if (sma20 < sma50) {
                signals.push('SELL');
              }
            }
            
            return signals.length;
          },
          iterations: 100
        }
      ]
    };
  }

  /**
   * Create memory usage tests
   */
  private createMemoryTests(): PerformanceTestSuite {
    return {
      name: 'Memory Usage Tests',
      description: 'Tests memory usage and garbage collection performance',
      tests: [
        {
          name: 'Large Array Processing',
          category: 'other',
          testFunction: async () => {
            const initialMemory = process.memoryUsage();
            const data = Array.from({ length: 100000 }, () => Math.random());
            const processed = data.map(x => x * 2).filter(x => x > 1);
            const finalMemory = process.memoryUsage();
            
            return {
              processed: processed.length,
              memoryDelta: finalMemory.heapUsed - initialMemory.heapUsed
            };
          },
          iterations: 10
        }
      ]
    };
  }

  /**
   * Run a test suite
   */
  private async runTestSuite(suite: PerformanceTestSuite): Promise<void> {
    console.log(`\nüìä Running ${suite.name}`);
    console.log(`   ${suite.description}\n`);

    for (const test of suite.tests) {
      await this.runTest(test);
    }
  }

  /**
   * Run an individual test
   */
  private async runTest(test: PerformanceTest): Promise<void> {
    const iterations = test.iterations || 100;
    const warmupIterations = test.warmupIterations || 10;
    
    console.log(`   üß™ ${test.name}`);
    
    // Warmup
    for (let i = 0; i < warmupIterations; i++) {
      await test.testFunction();
    }
    
    // Actual test
    const startTime = performanceProfiler.now();
    const results = [];
    
    for (let i = 0; i < iterations; i++) {
      const testStart = performanceProfiler.now();
      const result = await test.testFunction();
      const testEnd = performanceProfiler.now();
      
      results.push({
        duration: testEnd - testStart,
        result
      });
    }
    
    const endTime = performanceProfiler.now();
    const totalDuration = endTime - startTime;
    const avgDuration = totalDuration / iterations;
    
    this.testResults.push({
      suiteName: test.category,
      testName: test.name,
      iterations,
      totalDuration,
      avgDuration,
      minDuration: Math.min(...results.map(r => r.duration)),
      maxDuration: Math.max(...results.map(r => r.duration)),
      results
    });
    
    console.log(`      ‚è±Ô∏è  Average: ${avgDuration.toFixed(2)}ms`);
    console.log(`      üìà Total: ${totalDuration.toFixed(2)}ms`);
  }

  /**
   * Generate comprehensive report
   */
  private async generateReport(): Promise<void> {
    console.log('\nüìä Generating Performance Report...\n');
    
    const report = {
      timestamp: new Date().toISOString(),
      systemInfo: {
        nodeVersion: process.version,
        platform: process.platform,
        arch: process.arch,
        cpus: require('os').cpus().length,
        memory: process.memoryUsage()
      },
      testResults: this.testResults,
      summary: this.generateSummary()
    };
    
    // Save report
    const reportPath = path.join(__dirname, '../../reports/performance-report.json');
    await fs.writeFile(reportPath, JSON.stringify(report, null, 2));
    
    console.log(`üìã Performance report saved to: ${reportPath}`);
    console.log('\nüìä Performance Summary:');
    console.log(this.generateSummary());
  }

  /**
   * Generate performance summary
   */
  private generateSummary(): any {
    const categories = {};
    
    for (const result of this.testResults) {
      if (!categories[result.suiteName]) {
        categories[result.suiteName] = {
          testCount: 0,
          totalAvgDuration: 0,
          tests: []
        };
      }
      
      categories[result.suiteName].testCount++;
      categories[result.suiteName].totalAvgDuration += result.avgDuration;
      categories[result.suiteName].tests.push({
        name: result.testName,
        avgDuration: result.avgDuration,
        iterations: result.iterations
      });
    }
    
    return categories;
  }
}

// Run analysis if called directly
if (require.main === module) {
  const analyzer = new PerformanceAnalyzer();
  analyzer.runAnalysis().catch(console.error);
}

export default PerformanceAnalyzer;

import { execSync } from 'child_process';
import * as fs from 'fs';
import * as path from 'path';

interface ValidationResult {
  component: string;
  status: 'pass' | 'fail' | 'warning';
  message: string;
  details?: any;
}

interface ProductionReadinessReport {
  overall: 'ready' | 'needs-attention' | 'not-ready';
  validations: ValidationResult[];
  summary: {
    passed: number;
    failed: number;
    warnings: number;
    total: number;
  };
  recommendations: string[];
}

class ProductionReadinessValidator {
  private results: ValidationResult[] = [];
  private readonly projectRoot: string;

  constructor() {
    this.projectRoot = process.cwd();
  }

  private addResult(component: string, status: 'pass' | 'fail' | 'warning', message: string, details?: any): void {
    this.results.push({ component, status, message, details });
  }

  private async validateTypeScriptCompilation(): Promise<void> {
    console.log('1. Validating TypeScript Compilation...');
    try {
      // Check TypeScript compilation without building (faster for health check)
      execSync('npx tsc --noEmit', { 
        stdio: 'pipe',
        cwd: this.projectRoot,
        timeout: 30000
      });
      this.addResult('TypeScript Compilation', 'pass', 'All TypeScript files compile successfully');
    } catch (error: any) {
      this.addResult('TypeScript Compilation', 'fail', 'TypeScript compilation failed', {
        error: error.message
      });
    }
  }

  private async validateTestSuite(): Promise<void> {
    console.log('2. Validating Test Suite...');
    try {
      // Check if test files exist
      const testDirs = [
        path.join(this.projectRoot, 'packages/backend/tests'),
        path.join(this.projectRoot, 'packages/frontend/tests'),
        path.join(this.projectRoot, 'packages/shared/tests')
      ];

      const testFiles: string[] = [];
      testDirs.forEach(dir => {
        if (fs.existsSync(dir)) {
          const files = this.getFilesRecursively(dir, '.test.ts');
          testFiles.push(...files);
        }
      });

      if (testFiles.length > 0) {
        this.addResult('Test Suite', 'pass', `Found ${testFiles.length} test files`, {
          testFiles: testFiles.slice(0, 10) // Show first 10 for brevity
        });
      } else {
        this.addResult('Test Suite', 'warning', 'No test files found');
      }
    } catch (error: any) {
      this.addResult('Test Suite', 'fail', 'Test suite validation failed', {
        error: error.message
      });
    }
  }

  private async validateEnvironmentConfiguration(): Promise<void> {
    console.log('3. Validating Environment Configuration...');
    try {
      const envFile = path.join(this.projectRoot, '.env');
      const envExampleFile = path.join(this.projectRoot, '.env.example');

      if (fs.existsSync(envFile)) {
        const envContent = fs.readFileSync(envFile, 'utf8');
        const requiredVars = [
          'NODE_ENV',
          'DATABASE_URL',
          'BYBIT_API_KEY',
          'BYBIT_SECRET',
          'REDIS_URL'
        ];

        const missingVars = requiredVars.filter(varName => 
          !envContent.includes(`${varName}=`) || envContent.includes(`${varName}=`)
        );

        if (missingVars.length === 0) {
          this.addResult('Environment Configuration', 'pass', 'All required environment variables are configured');
        } else {
          this.addResult('Environment Configuration', 'warning', 'Some environment variables may need attention', {
            requiredVars,
            note: 'Please verify all environment variables are properly set'
          });
        }
      } else {
        this.addResult('Environment Configuration', 'fail', '.env file not found');
      }

      if (fs.existsSync(envExampleFile)) {
        this.addResult('Environment Documentation', 'pass', '.env.example file exists for reference');
      } else {
        this.addResult('Environment Documentation', 'warning', '.env.example file not found');
      }
    } catch (error: any) {
      this.addResult('Environment Configuration', 'fail', 'Environment validation failed', {
        error: error.message
      });
    }
  }

  private async validateDatabaseConnectivity(): Promise<void> {
    console.log('4. Validating Database Connectivity...');
    try {
      const scriptPath = path.join(this.projectRoot, 'dist/scripts/database-health-check.js');
      if (fs.existsSync(scriptPath)) {
        const output = execSync(`node "${scriptPath}"`, { 
          encoding: 'utf8',
          timeout: 30000,
          cwd: this.projectRoot
        });
        
        if (output.includes('Database Health Check Passed')) {
          this.addResult('Database Connectivity', 'pass', 'Database health check passed');
        } else {
          this.addResult('Database Connectivity', 'warning', 'Database health check completed with warnings');
        }
      } else {
        this.addResult('Database Connectivity', 'warning', 'Database health check script not found');
      }
    } catch (error: any) {
      this.addResult('Database Connectivity', 'fail', 'Database connectivity validation failed', {
        error: error.message
      });
    }
  }

  private async validateSignalProcessing(): Promise<void> {
    console.log('5. Validating Signal Processing...');
    try {
      const scriptPath = path.join(this.projectRoot, 'dist/scripts/signal-processing-validation.js');
      if (fs.existsSync(scriptPath)) {
        const output = execSync(`node "${scriptPath}"`, { 
          encoding: 'utf8',
          timeout: 30000,
          cwd: this.projectRoot
        });
        
        if (output.includes('All Signal Processing Validation Tests Passed')) {
          this.addResult('Signal Processing', 'pass', 'Signal processing validation passed');
        } else {
          this.addResult('Signal Processing', 'warning', 'Signal processing validation completed with issues');
        }
      } else {
        this.addResult('Signal Processing', 'warning', 'Signal processing validation script not found');
      }
    } catch (error: any) {
      this.addResult('Signal Processing', 'fail', 'Signal processing validation failed', {
        error: error.message
      });
    }
  }

  private async validateTradingIntegration(): Promise<void> {
    console.log('6. Validating Trading Integration...');
    try {
      const scriptPath = path.join(this.projectRoot, 'dist/scripts/bot-trading-cycle-validation.js');
      if (fs.existsSync(scriptPath)) {
        const output = execSync(`node "${scriptPath}"`, { 
          encoding: 'utf8',
