/**
 * Fixed SMA Crossover Strategy Backtesting Script
 * 
 * This script implements a fixed version of the SMA crossover strategy
 * and runs a backtesting simulation using historical price data.
 */

import fs from 'fs';
import path from 'path';

import { calculateSMA, calculateEMA, getMASignals } from '../../src/JabbrLabs/indicators/moving-averages';
import type { Candle} from '../../src/JabbrLabs/target-reacher/interfaces';
import { StrategyContext } from '../../src/JabbrLabs/target-reacher/interfaces';
import { generateSampleData } from '../../../shared/src/test-utils/data-generators';

/**
 * Fixed SMA Signal Processor that fixes the signal generation issues
 */
class FixedSMASignalProcessor {
  private fastPeriod: number;
  private slowPeriod: number;
  private useEMA: boolean;
  private priceSource: 'close' | 'open' | 'high' | 'low';
  
  constructor() {
    this.fastPeriod = 5;  // Faster period for more signals
    this.slowPeriod = 15; // Shorter slow period for more signals
    this.useEMA = true;   // EMA is more responsive than SMA
    this.priceSource = 'close';
  }
  
  process(candles: Candle[]): { signal: number; reason: string; confidence: number } | null {
    // Ensure we have enough candles
    if (candles.length < this.slowPeriod + 1) {
      return null;
    }
    
    // Get prices
    const prices = candles.map(c => c[this.priceSource]);
    
    // Calculate moving averages
    const calculateMA = this.useEMA ? calculateEMA : calculateSMA;
    const fastMA = calculateMA(prices, this.fastPeriod);
    const slowMA = calculateMA(prices, this.slowPeriod);
    
    // Calculate crossovers
    const lastFast = fastMA[fastMA.length - 1];
    const prevFast = fastMA[fastMA.length - 2];
    const lastSlow = slowMA[slowMA.length - 1];
    const prevSlow = slowMA[slowMA.length - 2];
    
    // Safety checks
    if (lastFast === undefined || prevFast === undefined || 
        lastSlow === undefined || prevSlow === undefined ||
        prices.length === 0) {
      return null;
    }
    
    const lastPrice = prices[prices.length - 1];
    if (lastPrice === undefined) {
      return null;
    }
    
    // Logging for debugging
    console.log(`Price: ${lastPrice.toFixed(2)}`);
    console.log(`Fast MA: ${lastFast.toFixed(2)} (prev: ${prevFast.toFixed(2)})`);
    console.log(`Slow MA: ${lastSlow.toFixed(2)} (prev: ${prevSlow.toFixed(2)})`);
    
    // Check for crossover - directly without using getMASignals
    if (lastFast > lastSlow && prevFast <= prevSlow) {
      // Bullish crossover
      return {
        signal: 1,
        reason: 'Bullish crossover detected',
        confidence: 0.8
      };
    } else if (lastFast < lastSlow && prevFast >= prevSlow) {
      // Bearish crossover
      return {
        signal: -1,
        reason: 'Bearish crossover detected',
        confidence: 0.8
      };
    } else if (Math.abs(lastFast - lastSlow) / lastFast < 0.01) {
      // MAs are very close - potential crossover soon
      if (lastFast > prevFast && lastSlow < prevSlow) {
        return {
          signal: 0.5, // Weak bullish signal
          reason: 'MAs converging with bullish bias',
          confidence: 0.6
        };
      } else if (lastFast < prevFast && lastSlow > prevSlow) {
        return {
          signal: -0.5, // Weak bearish signal
          reason: 'MAs converging with bearish bias',
          confidence: 0.6
        };
      }
    }
    
    return null;
  }
}

/**
 * Fixed backtest function
 */
const runFixedBacktest = () => {
  console.log('\n----- RUNNING FIXED SMA CROSSOVER BACKTEST -----');
  
  // Generate sample price data with clear trends for testing
  const candles = generateSampleData();
  console.log(`Generated ${candles.length} sample candles`);
  
  // Create the fixed signal processor
  const processor = new FixedSMASignalProcessor();
  
  // Trading state
  let position: 'long' | 'short' | null = null;
  let entryPrice = 0;
  let totalProfit = 0;
  let trades = 0;
  
  // Process candles with sliding window
  const windowSize = 30; // Enough data for calculations
  
  console.log('\nProcessing price data...');
  for (let i = windowSize; i < candles.length; i++) {
    // Get current window
    const window = candles.slice(i - windowSize, i + 1);
    
    // Safety check
    if (window.length === 0) {
      continue;
    }
    
    const lastCandle = window[window.length - 1];
    if (!lastCandle) {
      continue;
    }
    
    const currentPrice = lastCandle.close;
    console.log(`\nWindow ${i}: Price ${currentPrice.toFixed(2)}`);
    
    // Process signal
    const result = processor.process(window);
    
    // Track position and calculate P&L
    if (result) {
      if (result.signal > 0.7) { // Strong buy signal
        if (position === 'short') {
          // Close short position
          const profit = entryPrice - currentPrice;
          totalProfit += profit;
          console.log(`Close SHORT position at ${currentPrice.toFixed(2)}, profit: ${profit.toFixed(2)}`);
          position = null;
          trades++;
        }
        
        if (!position) {
          // Open long position
          position = 'long';
          entryPrice = currentPrice;
          console.log(`Open LONG position at ${currentPrice.toFixed(2)}`);
          trades++;
        }
      } else if (result.signal < -0.7) { // Strong sell signal
        if (position === 'long') {
          // Close long position
          const profit = currentPrice - entryPrice;
          totalProfit += profit;
          console.log(`Close LONG position at ${currentPrice.toFixed(2)}, profit: ${profit.toFixed(2)}`);
          position = null;
          trades++;
        }
        
        if (!position) {
          // Open short position
          position = 'short';
          entryPrice = currentPrice;
          console.log(`Open SHORT position at ${currentPrice.toFixed(2)}`);
          trades++;
        }
      }
    }
  }
  
  // Close any open position
  if (position && candles.length > 0) {
    const lastCandle = candles[candles.length - 1];
    if (lastCandle) {
      const lastPrice = lastCandle.close;
      const profit = position === 'long' ? 
        (lastPrice - entryPrice) : (entryPrice - lastPrice);
      totalProfit += profit;
      console.log(`Close final ${position} position at ${lastPrice.toFixed(2)}, profit: ${profit.toFixed(2)}`);
    }
  }
  
  // Print results
  console.log('\n----- BACKTEST RESULTS -----');
  console.log(`Total trades: ${trades}`);
  console.log(`Total profit: ${totalProfit.toFixed(2)}`);
  console.log(`Final position: ${position || 'none'}`);
  console.log('---------------------------\n');
};

// Run the backtest
runFixedBacktest();

/**
 * SMA Strategy Backtest
 * 
 * This script runs a backtest of the SMA signal processor against synthetic price data
 * to validate its performance and signal generation.
 */

import * as fs from 'fs';
import * as path from 'path';

import { generateSyntheticCandles, type Candle } from '../../../shared/src/test-utils/data-generators';
import type { SMASignalConfig } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.4,
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Main function to run the backtest
async function runBacktest() {
  console.log('Starting SMA Backtest');
  console.log(`Configuration: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}, Threshold: ${config.confidenceThreshold}`);
  
  // Generate test data using shared utility
  const candles = generateSyntheticCandles({
    count: 200,
    startPrice: 100,
    trend: 'mixed',
    timeframe: '15m'
  });
  
  const processor = new SMASignalProcessor(config);
  
  interface SignalRecord {
    timestamp: number;
    price: number;
    signal: 'buy' | 'sell';
    confidence: number;
    reason: string;
  }

  interface TradeRecord {
    type: string;
    timestamp: number;
    price: number;
    profit?: number;
    reason?: string;
  }
  
  const signals: SignalRecord[] = [];
  const trades: TradeRecord[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      if (!candle) {continue;} // Skip if candle is undefined
      
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell' && position.entryPrice !== null) {
          const profit = position.entryPrice - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy' && position.entryPrice !== null) {
          const profit = candle.close - position.entryPrice;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null && position.entryPrice !== null) {
    const lastCandle = candles[candles.length - 1];
    if (lastCandle) {
      let profit = 0;
      
      if (position.side === 'buy') {
        profit = lastCandle.close - position.entryPrice;
        trades.push({
          type: 'Close Long (End)',
          timestamp: lastCandle.timestamp,
          price: lastCandle.close,
          profit
        });
      } else if (position.side === 'sell') {
        profit = position.entryPrice - lastCandle.close;
        trades.push({
          type: 'Close Short (End)',
          timestamp: lastCandle.timestamp,
          price: lastCandle.close,
          profit
        });
      }
      
      pnl += profit;
    }
  }
  
  // Log results
  console.log(`Generated ${signals.length} signals and ${trades.length} trades`);
  console.log(`Final P&L: ${pnl.toFixed(2)}`);
  
  // Generate report
  const report = {
    config,
    summary: {
      signalCount: signals.length,
      tradeCount: trades.length,
      pnl
    },
    signals,
    trades
  };
  
  // Write report to file
  fs.writeFileSync(
    path.join(__dirname, 'sma-backtest-results.json'),
    JSON.stringify(report, null, 2)
  );
  
  console.log('Backtest complete. Results saved to sma-backtest-results.json');
}

// Run the backtest
runBacktest().catch(console.error);

/**
 * Compare original and improved SMA signal processors
 * 
 * This script runs both the original and improved SMA signal processors
 * on the same data and compares their signal generation.
 */

import * as fs from 'fs';
import * as path from 'path';

import { ImprovedSMASignalProcessor } from '../../src/JabbrLabs/signals/sma/improved-sma-signal-processor';
import type { SMASignalConfig} from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalOutput } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';
import type { Candle } from '../../src/JabbrLabs/target-reacher/interfaces';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.4, // Lowered threshold for comparison
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Generate synthetic price data for testing
function generateSyntheticCandles(options: {
  startPrice: number,
  dataPoints: number,
  volatility: number,
  trendStrength: number,
  trendChangeProbability: number
}): Candle[] {
  const candles: Candle[] = [];
  let currentPrice = options.startPrice;
  let trend = 1; // 1 = up, -1 = down

  for (let i = 0; i < options.dataPoints; i++) {
    // Potentially change trend
    if (Math.random() < options.trendChangeProbability) {
      trend *= -1;
    }
    
    // Calculate price movement
    const trendMovement = trend * options.trendStrength * currentPrice;
    const randomMovement = (Math.random() * 2 - 1) * options.volatility * currentPrice;
    const movement = trendMovement + randomMovement;
    
    // Calculate candle prices
    const open = currentPrice;
    const close = currentPrice + movement;
    const high = Math.max(open, close) + Math.random() * options.volatility * currentPrice;
    const low = Math.min(open, close) - Math.random() * options.volatility * currentPrice;
    const volume = Math.random() * 100000 + 10000;
    
    // Create candle
    candles.push({
      timestamp: Date.now() + i * 900000, // 15-minute intervals
      open,
      high,
      low,
      close,
      volume
    });
    
    // Update current price for next iteration
    currentPrice = close;
  }
  
  return candles;
}

// Process signals and execute trades
function processAndTrade(processor: SMASignalProcessor | ImprovedSMASignalProcessor, candles: Candle[]) {
  const signals: any[] = [];
  const trades: any[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell') {
          const profit = position.entryPrice! - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy') {
          const profit = candle.close - position.entryPrice!;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null) {
    const lastCandle = candles[candles.length - 1];
    let profit = 0;
    
    if (position.side === 'buy') {
      profit = lastCandle.close - position.entryPrice!;
      trades.push({
        type: 'Close Long (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    } else {
      profit = position.entryPrice! - lastCandle.close;
      trades.push({
        type: 'Close Short (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    }
    
    pnl += profit;
  }
  
  return {
    signals,
    trades,
    pnl
  };
}

// Main test function
async function runComparisonTest() {
  console.log('Starting SMA Signal Processor Comparison Test');
  console.log(`Configuration: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}`);
  
  // Generate test data
  const candles = generateSyntheticCandles({
    startPrice: 100,
    dataPoints: 200,
    volatility: 0.01,
    trendStrength: 0.005,
    trendChangeProbability: 0.05
  });
  
  // Create processors
  const originalProcessor = new SMASignalProcessor(config);
  const improvedProcessor = new ImprovedSMASignalProcessor(config);
  
  // Process with both implementations
  console.log('\nRunning Original SMA Signal Processor...');
  const originalResults = processAndTrade(originalProcessor, candles);
  
  console.log('\nRunning Improved SMA Signal Processor...');
  const improvedResults = processAndTrade(improvedProcessor, candles);
  
  // Log comparison results
  console.log('\n===== COMPARISON RESULTS =====');
  console.log(`Original: ${originalResults.signals.length} signals, ${originalResults.trades.length} trades, P&L: ${originalResults.pnl.toFixed(2)}`);
  console.log(`Improved: ${improvedResults.signals.length} signals, ${improvedResults.trades.length} trades, P&L: ${improvedResults.pnl.toFixed(2)}`);
  
  // Generate comparison report
  const report = {
    config,
    summary: {
      original: {
        signalCount: originalResults.signals.length,
        tradeCount: originalResults.trades.length,
        pnl: originalResults.pnl
      },
      improved: {
        signalCount: improvedResults.signals.length,
        tradeCount: improvedResults.trades.length,
        pnl: improvedResults.pnl
      },
      comparison: {
        signalDifference: improvedResults.signals.length - originalResults.signals.length,
        pnlDifference: improvedResults.pnl - originalResults.pnl
      }
    },
    details: {
      original: {
        signals: originalResults.signals,
        trades: originalResults.trades
      },
      improved: {
        signals: improvedResults.signals,
        trades: improvedResults.trades
      }
    }
  };
  
  // Write report to file
  fs.writeFileSync(
    path.join(__dirname, 'sma-comparison-report.json'),
    JSON.stringify(report, null, 2)
  );
  
  console.log('Test complete. Comparison report saved to sma-comparison-report.json');
}

runComparisonTest().catch(console.error);

/**
 * SMA Signal Generator Debug Script
 * 
 * A detailed debug script to identify issues with SMA signal generation
 */

import fs from 'fs';
import path from 'path';

import { calculateSMA } from '../../src/JabbrLabs/indicators/moving-averages';
import { SMASignalProcessor } from '../../src/JabbrLabs/signals/sma/sma-signal-processor';
import type { Candle } from '../../src/JabbrLabs/target-reacher/interfaces';

// Create a function to check and fix SMA signal processor issues
const findAndFixIssues = () => {
  console.log('\n----- SMA SIGNAL PROCESSOR ISSUE FINDER -----');
  
  // Create test data with predictable pattern
  const candles: Candle[] = [];
  
  // Start with a flat pattern
  for (let i = 0; i < 20; i++) {
    candles.push({
      timestamp: Date.now() + i * 3600000,
      open: 100,
      high: 102,
      low: 98,
      close: 100,
      volume: 1000
    });
  }
  
  // Add a clear uptrend - this should cause a bullish crossover
  for (let i = 0; i < 10; i++) {
    candles.push({
      timestamp: Date.now() + (i + 20) * 3600000,
      open: 100 + i * 2,
      high: 102 + i * 2,
      low: 98 + i * 2,
      close: 100 + i * 2.5,
      volume: 2000
    });
  }
  
  // Add a clear downtrend - this should cause a bearish crossover
  for (let i = 0; i < 10; i++) {
    candles.push({
      timestamp: Date.now() + (i + 30) * 3600000,
      open: 125 - i * 2,
      high: 127 - i * 2,
      low: 123 - i * 2,
      close: 125 - i * 3,
      volume: 3000
    });
  }
  
  console.log(`Created ${candles.length} test candles with clear trend patterns`);
  
  // Configuration with very low thresholds to ensure we get signals
  const config = {
    fastPeriod: 5,
    slowPeriod: 15,
    priceSource: 'close' as 'close',
    signalMode: 'crossover' as 'crossover',
    useEMA: false,
    confidenceThreshold: 0.01, // Almost no threshold
    minChangePercent: 0.01 // Almost no minimum change
  };
  
  // Manually check when crossovers should happen
  console.log('\nAnalyzing data for expected crossover points:');
  
  for (let i = 20; i < candles.length; i++) {
    // Calculate SMA values ourselves
    const prices = candles.slice(0, i + 1).map(c => c.close);
    const fastMA = calculateSMA(prices, config.fastPeriod);
    const slowMA = calculateSMA(prices, config.slowPeriod);
    
    if (fastMA.length < 2 || slowMA.length < 2) {continue;}
    
    // Get current and previous values
    const currFast = fastMA[fastMA.length - 1]; 
    const prevFast = fastMA[fastMA.length - 2];
    const currSlow = slowMA[slowMA.length - 1];
    const prevSlow = slowMA[slowMA.length - 2];
    
    // Check for crossovers manually
    if (currFast && prevFast && currSlow && prevSlow) {
      if (currFast > currSlow && prevFast <= prevSlow) {
        console.log(`Candle ${i}: BULLISH CROSSOVER DETECTED - Should generate BUY signal`);
        console.log(`  Fast: ${prevFast.toFixed(2)} -> ${currFast.toFixed(2)}`);
        console.log(`  Slow: ${prevSlow.toFixed(2)} -> ${currSlow.toFixed(2)}`);
      } else if (currFast < currSlow && prevFast >= prevSlow) {
        console.log(`Candle ${i}: BEARISH CROSSOVER DETECTED - Should generate SELL signal`);
        console.log(`  Fast: ${prevFast.toFixed(2)} -> ${currFast.toFixed(2)}`);
        console.log(`  Slow: ${prevSlow.toFixed(2)} -> ${currSlow.toFixed(2)}`);
      }
    }
  }
  
  // Now test using the SMA processor
  console.log('\nTesting SMA processor with the same data:');
  const processor = new SMASignalProcessor(config);
  
  // Create a log of raw data for analysis
  const logData: {
    configuration: typeof config;
    candleData: Array<{
      index: number;
      price: number;
      fastMA: number;
      slowMA: number;
      difference: number;
    }>;
    signals: Array<{
      index: number;
      type: string;
      confidence: number;
      reason: string;
    }>;
  } = {
    configuration: config,
    candleData: [],
    signals: []
  };
  
  // Process each window
  for (let i = 15; i < candles.length; i++) {
    const window = candles.slice(0, i + 1);
    
    try {
      const result = processor.process(window);
      
      // Calculate MAs directly for reference
      const prices = window.map(c => c.close);
      const fastMA = calculateSMA(prices, config.fastPeriod);
      const slowMA = calculateSMA(prices, config.slowPeriod);
      
      const lastFastMA = fastMA.length > 0 ? fastMA[fastMA.length - 1] : null;
      const lastSlowMA = slowMA.length > 0 ? slowMA[slowMA.length - 1] : null;
      
      console.log(`\nCandle ${i}: Fast MA = ${lastFastMA?.toFixed(2) || 'N/A'}, Slow MA = ${lastSlowMA?.toFixed(2) || 'N/A'}`);
      
      // Save data for analysis
      if (lastFastMA !== null && lastSlowMA !== null && window.length > 0) {
        const lastCandle = window[window.length - 1];
        if (lastCandle) {
          logData.candleData.push({
            index: i,
            price: lastCandle.close,
            fastMA: lastFastMA,
            slowMA: lastSlowMA,
            difference: lastFastMA - lastSlowMA
          });
        }
      }
      
      if (result) {
        const signalType = result.signal > 0 ? 'BUY' : result.signal < 0 ? 'SELL' : 'NONE';
        console.log(`*** SIGNAL: ${signalType} - Confidence: ${result.confidence.toFixed(2)} ***`);
        console.log(`Reason: ${result.reason}`);
        
        logData.signals.push({
          index: i,
          type: signalType,
          confidence: result.confidence,
          reason: result.reason
        });
      } else {
        console.log('No signal generated');
      }
    } catch (error) {
      console.error(`Error processing window at index ${i}:`, error);
    }
  }
  
  // Save data for analysis
  const outputDir = path.join(__dirname, 'debug-output');
  try {
    if (!fs.existsSync(outputDir)) {
      fs.mkdirSync(outputDir, { recursive: true });
    }
    
    const filename = path.join(outputDir, 'sma-debug-analysis.json');
    fs.writeFileSync(filename, JSON.stringify(logData, null, 2));
    console.log(`\nDetailed analysis data saved to ${filename}`);
  } catch (error) {
    console.error('Error saving analysis data:', error);
  }
  
  // Check the SMASignalProcessor implementation for issues
  console.log('\nChecking SMASignalProcessor implementation:');
  console.log('1. Configuration used:', config);
  console.log('2. Signals generated:', logData.signals.length);
  
  if (logData.signals.length === 0) {
    console.log('\nPOTENTIAL ISSUES IDENTIFIED:');
    console.log(`- Confidence threshold may be too high (current: ${  config.confidenceThreshold  })`);
    console.log('- Signal generation logic may have bugs');
    console.log('- Crossover detection might not be working as expected');
    console.log('\nRECOMMENDATIONS:');
    console.log('- Review the generateSignal method in SMASignalProcessor');
    console.log('- Ensure crossover detection is correctly implemented');
    console.log('- Lower the confidence threshold or modify confidence calculation');
  }
};

// Run the test
findAndFixIssues();

