    enableRiskManagement: boolean;
    maxPositionSize: number;
    stopLossPercent: number;
    takeProfitPercent: number;
  };
  
  // General settings
  enableAutoStart: boolean;
  enableMonitoring: boolean;
  enableBackups: boolean;
}

/**
 * Bot Trading Cycle Status
 */
export interface BotTradingCycleStatus {
  isRunning: boolean;
  strategiesActive: number;
  signalsProcessed: number;
  ordersExecuted: number;
  healthStatus: 'healthy' | 'warning' | 'critical';
  lastActivity: number;
  uptime: number;
  errors: string[];
}

/**
 * Bot Trading Cycle Integration Service
 */
export class BotTradingCycleIntegration extends EventEmitter {
  private strategyExecution: StrategyExecutionIntegration;
  private signalProcessing: SignalProcessingManager;
  private smaProcessor: SMASignalProcessor;
  
  private config: BotTradingCycleConfig;
  private isInitialized = false;
  private isRunning = false;
  private startTime = 0;
  private errors: string[] = [];
  
  // Statistics
  private stats = {
    strategiesLoaded: 0,
    signalsProcessed: 0,
    ordersExecuted: 0,
    errorsEncountered: 0,
    lastActivity: 0
  };

  constructor(config?: Partial<BotTradingCycleConfig>) {
    super();
    
    this.config = {
      strategyConfig: {
        maxActiveStrategies: 5,
        performanceThreshold: 0.6,
        enableHotSwapping: true,
        metricsCollectionInterval: 30000
      },
      signalConfig: {
        batchSize: 10,
        maxSignalsPerMinute: 60,
        minConfidence: 0.3,
        minStrength: 0.2,
        maxHighRiskSignals: 5
      },
      tradingConfig: {
        enableRiskManagement: true,
        maxPositionSize: 1000,
        stopLossPercent: 5,
        takeProfitPercent: 10
      },
      enableAutoStart: false,
      enableMonitoring: true,
      enableBackups: true,
      ...config
    };

    this.signalProcessing = new SignalProcessingManager(this.config.signalConfig);
    this.smaProcessor = new SMASignalProcessor();
    
    // Initialize strategy execution with proper configuration
    const strategyExecutionConfig: StrategyExecutionConfig = {
      botId: 'default-bot',
      strategyType: 'sma-crossover',
      strategyConfig: {
        type: 'sma-crossover',
        parameters: {}
      },
      executionInterval: this.config.strategyConfig.metricsCollectionInterval || 30000,
      enableDynamicLoading: this.config.strategyConfig.enableHotSwapping || false,
      enablePerformanceTracking: true,
      maxExecutionTime: 30000,
      retryAttempts: 3
    };
    
    // Create a minimal strategy context
    const strategyContext: StrategyContext = {
      config: strategyExecutionConfig.strategyConfig,
      botConfig: {} as BotConfig, // Will be set later during initialization
      symbol: 'BTC/USDT',
      marketData: {} as MarketDataProvider, // Will be set later
      tradeExecutor: {} as TradeExecutorProvider, // Will be set later
      logger: {} as LoggerProvider, // Will be set later
      storage: {} as StorageProvider, // Will be set later
      eventEmitter: {} as EventEmitterProvider // Will be set later
    };
    
    this.strategyExecution = new StrategyExecutionIntegration(
      strategyExecutionConfig,
      strategyContext
    );
    
    logger.info('ðŸš€ Bot Trading Cycle Integration initialized', {
      config: this.config
    });
  }

  /**
   * Set up event listeners for coordination
   */
  private setupEventListeners(): void {
    // Ensure strategy execution is initialized before setting up listeners
    if (!this.strategyExecution) {
      logger.error('âŒ Cannot setup event listeners: strategy execution not initialized');
      return;
    }

    // Strategy execution events
    this.strategyExecution.on('strategy-loaded', (data) => {
      logger.info('ðŸ“ˆ Strategy loaded in trading cycle', data);
      this.stats.strategiesLoaded++;
      this.stats.lastActivity = Date.now();
      this.emit('strategy-loaded', data);
    });

    this.strategyExecution.on('strategy-executed', (data) => {
      logger.debug('âš¡ Strategy executed in trading cycle', data);
      this.stats.lastActivity = Date.now();
      
      // If strategy produces signals, process them through signal manager
      if (data.result && data.result.action && data.result.action !== 'hold') {
        const signal: StandardSignal = {
          id: `strategy-${data.strategyId}-${Date.now()}`,
          source: SignalSource.STRATEGY,
          action: data.result.action,
          confidence: data.result.confidence || 0.5,
          strength: data.result.confidence || 0.5,
          symbol: data.context?.symbol || 'UNKNOWN',
          timestamp: Date.now(),
          reason: data.result.reason || 'Strategy execution result',
          metadata: data.result.metadata || {},
          riskLevel: 'medium',
          urgency: 'medium'
        };
        
        this.signalProcessing.addSignal(signal, 3); // High priority for strategy signals
      }
      
      this.emit('strategy-executed', data);
    });

    this.strategyExecution.on('error', (error) => {
      logger.error('âŒ Strategy execution error in trading cycle', error);
      this.errors.push(`Strategy: ${error.message || error}`);
      this.stats.errorsEncountered++;
      this.emit('error', { source: 'strategy', error });
    });

    // Signal processing events
    this.signalProcessing.on('signal-processed', (data) => {
      logger.debug('ðŸ“¡ Signal processed in trading cycle', {
        id: data.signal.id,
        source: data.signal.source,
        action: data.signal.action
      });
      this.stats.signalsProcessed++;
      this.stats.lastActivity = Date.now();
      this.emit('signal-processed', data);
    });

    this.signalProcessing.on('source-error', (error) => {
      logger.error('âŒ Signal processing error in trading cycle', error);
      this.errors.push(`Signal: ${error.error || error}`);
      this.stats.errorsEncountered++;
      this.emit('error', { source: 'signal', error });
    });

    this.signalProcessing.on('batch-processed', (data) => {
      logger.debug('ðŸ“Š Signal batch processed in trading cycle', data);
      this.emit('batch-processed', data);
    });
  }

  /**
   * Initialize the bot trading cycle
   */
  async initialize(): Promise<void> {
    try {
      logger.info('ðŸ”„ Initializing Bot Trading Cycle Integration...');

      if (this.isInitialized) {
        logger.warn('âš ï¸ Bot Trading Cycle already initialized');
        return;
      }

      // Initialize strategy execution
      await this.strategyExecution.initialize();
      logger.info('âœ… Strategy execution initialized');

      // Initialize signal processing with SMA processor
      await this.signalProcessing.initialize({
        smaProcessor: this.smaProcessor,
        tradingEngine: {
          processAdvancedSignals: async (signals: StandardSignal[]) => {
            // Mock trading engine integration for now
            for (const signal of signals) {
              logger.info('ðŸŽ¯ Processing signal in trading engine', {
                id: signal.id,
                action: signal.action,
                symbol: signal.symbol,
                confidence: signal.confidence
              });
              this.stats.ordersExecuted++;
            }
            this.emit('orders-executed', { count: signals.length, signals });
          }
        }
      });
      logger.info('âœ… Signal processing initialized');

      this.isInitialized = true;
      this.startTime = Date.now();

      this.emit('initialized', {
        timestamp: this.startTime,
        config: this.config
      });

      logger.info('ðŸŽ¯ Bot Trading Cycle Integration fully initialized');

      // Auto-start if configured
      if (this.config.enableAutoStart) {
        await this.start();
      }

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to initialize Bot Trading Cycle Integration', { error: errorMessage });
      this.errors.push(`Initialization: ${errorMessage}`);
      throw error;
    }
  }

  /**
   * Start the bot trading cycle
   */
  async start(): Promise<void> {
    try {
      if (!this.isInitialized) {
        throw new Error('Bot Trading Cycle not initialized. Call initialize() first.');
      }

      if (this.isRunning) {
        logger.warn('âš ï¸ Bot Trading Cycle already running');
        return;
      }

      logger.info('ðŸš€ Starting Bot Trading Cycle...');

      // Load default strategies
      await this.loadDefaultStrategies();

      this.isRunning = true;
      this.stats.lastActivity = Date.now();

      this.emit('started', {
        timestamp: Date.now(),
        strategiesActive: this.stats.strategiesLoaded
      });

      logger.info('âœ… Bot Trading Cycle started successfully');

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to start Bot Trading Cycle', { error: errorMessage });
      this.errors.push(`Start: ${errorMessage}`);
      throw error;
    }
  }

  /**
   * Stop the bot trading cycle
   */
  async stop(): Promise<void> {
    try {
      if (!this.isRunning) {
        logger.warn('âš ï¸ Bot Trading Cycle not running');
        return;
      }

      logger.info('â¹ï¸ Stopping Bot Trading Cycle...');

      // Stop signal processing
      this.signalProcessing.stop();

      // Stop strategy execution
      // Note: StrategyExecutionIntegration doesn't have a stop method yet
      // This would be implemented in a future enhancement

      this.isRunning = false;

      this.emit('stopped', {
        timestamp: Date.now(),
        uptime: Date.now() - this.startTime,
        stats: this.stats
      });

      logger.info('âœ… Bot Trading Cycle stopped successfully');

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to stop Bot Trading Cycle', { error: errorMessage });
      this.errors.push(`Stop: ${errorMessage}`);
      throw error;
    }
  }

  /**
   * Load default trading strategies
   */
  private async loadDefaultStrategies(): Promise<void> {
    try {
      logger.info('ðŸ“š Loading default strategies...');

      // Load a basic SMA crossover strategy
      const smaStrategy = {
        type: 'sma-crossover',
        parameters: {
          fastPeriod: 9,
          slowPeriod: 21,
          confidenceThreshold: 0.6
        }
      };

      await this.strategyExecution.loadStrategy('sma-crossover', smaStrategy);
      logger.info('âœ… Default SMA strategy loaded');

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.warn('âš ï¸ Failed to load default strategies', { error: errorMessage });
      // Don't throw here as it's not critical for operation
    }
  }

  /**
   * Process market data through SMA and generate signals
   */
  async processMarketData(symbol: string, candles: any[]): Promise<void> {
    try {
      if (!this.isRunning) {
        logger.warn('âš ï¸ Bot Trading Cycle not running, ignoring market data');
        return;
      }

      logger.debug('ðŸ“Š Processing market data', { symbol, candleCount: candles.length });

      // Process through SMA signal processor
      await this.signalProcessing.processSMASignals(candles, symbol);

      this.stats.lastActivity = Date.now();

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to process market data', { error: errorMessage, symbol });
      this.errors.push(`Market data: ${errorMessage}`);
    }
  }

  /**
   * Execute strategy manually
   */
  async executeStrategy(botId: string, strategyId: string, context: any): Promise<any> {
    try {
      if (!this.isRunning) {
        throw new Error('Bot Trading Cycle not running');
      }

      logger.info('ðŸŽ¯ Executing strategy manually', { botId, strategyId });

      const result = await this.strategyExecution.executeStrategy();
      this.stats.lastActivity = Date.now();

      return result;

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to execute strategy', { error: errorMessage, botId, strategyId });
      this.errors.push(`Strategy execution: ${errorMessage}`);
      throw error;
    }
  }

  /**
   * Get trading cycle status
   */
  getStatus(): BotTradingCycleStatus {
    const now = Date.now();
    const timeSinceLastActivity = now - this.stats.lastActivity;
    
    let healthStatus: 'healthy' | 'warning' | 'critical' = 'healthy';
    
    if (!this.isRunning) {
      healthStatus = 'critical';
    } else if (this.errors.length > 5) {
      healthStatus = 'critical';
    } else if (timeSinceLastActivity > 300000 || this.errors.length > 2) { // 5 minutes
      healthStatus = 'warning';
    }

    return {
      isRunning: this.isRunning,
      strategiesActive: this.stats.strategiesLoaded,
      signalsProcessed: this.stats.signalsProcessed,
      ordersExecuted: this.stats.ordersExecuted,
      healthStatus,
      lastActivity: this.stats.lastActivity,
      uptime: this.isRunning ? now - this.startTime : 0,
      errors: [...this.errors] // Copy to prevent external modification
    };
  }

  /**
   * Get detailed statistics
   */
  getStatistics(): any {
    return {
      ...this.stats,
      signalProcessingStats: this.signalProcessing.getStats(),
      queueStatus: this.signalProcessing.getQueueStatus(),
      strategyExecutionMetrics: this.strategyExecution.getPerformanceMetrics(),
      uptime: this.isRunning ? Date.now() - this.startTime : 0,
      errorCount: this.errors.length
    };
  }

  /**
   * Update configuration
   */
  updateConfig(newConfig: Partial<BotTradingCycleConfig>): void {
    this.config = { ...this.config, ...newConfig };
    
    // Update sub-component configs
    if (newConfig.signalConfig) {
      this.signalProcessing.updateConfig(newConfig.signalConfig);
    }
    
    logger.info('âš™ï¸ Bot Trading Cycle configuration updated', { config: this.config });
    this.emit('config-updated', this.config);
  }

  /**
   * Clear error history
   */
  clearErrors(): void {
    this.errors = [];
    this.stats.errorsEncountered = 0;
    logger.info('ðŸ—‘ï¸ Bot Trading Cycle errors cleared');
    this.emit('errors-cleared');
  }

  /**
   * Get health check information
   */
  getHealthCheck(): {
    overall: 'healthy' | 'warning' | 'critical';
    components: Record<string, any>;
    recommendations: string[];
  } {
    const strategyMetrics = this.strategyExecution.getPerformanceMetrics();
    const signalHealth = this.signalProcessing.getHealthStatus();
    const status = this.getStatus();

    const components = {
      strategyExecution: strategyMetrics,
      signalProcessing: signalHealth,
      tradingCycle: {
        healthy: status.healthStatus === 'healthy',
        running: this.isRunning,
        initialized: this.isInitialized,
        uptime: status.uptime,
        errors: this.errors.length
      }
    };

    const recommendations: string[] = [];
    
    if (!this.isRunning) {
      recommendations.push('Start the bot trading cycle');
    }
    
    if (this.errors.length > 0) {
      recommendations.push('Review and address recent errors');
    }
    
    if (strategyMetrics.errorCount > 0) {
      recommendations.push('Check strategy execution health');
    }
    
    if (!signalHealth.healthy) {
      recommendations.push('Check signal processing health');
    }

    let overall: 'healthy' | 'warning' | 'critical' = 'healthy';
    if (!this.isRunning || strategyMetrics.errorCount > 0 || !signalHealth.healthy) {
      overall = 'critical';
    } else if (this.errors.length > 2 || status.healthStatus !== 'healthy') {
      overall = 'warning';
    }

    return {
      overall,
      components,
      recommendations
    };
  }
}

/**
 * Default bot trading cycle integration instance
 */
export const botTradingCycleIntegration = new BotTradingCycleIntegration();

/**
 * Average True Range (ATR) Indicator
 *
 * Calculates market volatility by decomposing the entire range of an asset price for a period.
 *
 * @example
 * // Calculate 14-period ATR for price series
 * const atr = new ATRIndicator({ period: 14 });
 * const result = atr.calculate({ highs, lows, closes });
 */
import { BaseIndicator, type IndicatorMetadata, type IIndicator } from './indicator-interface';

const ATR_METADATA: IndicatorMetadata = {
  name: 'ATR',
  description: 'Average True Range - measures market volatility over a specified period',
  category: 'volatility',
  parameters: [
    {
      name: 'period',
      type: 'number',
      description: 'Number of periods for ATR calculation',
      default: 14,
      min: 1,
      max: 200,
      required: true
    }
  ],
  outputs: [
    {
      name: 'atr',
      type: 'array',
      description: 'Array of ATR values'
    }
  ],
  minimumDataPoints: 2,
  version: '1.0.0'
};

/**
 * ATRIndicator class (preferred usage)
 */
export class ATRIndicator extends BaseIndicator<number[]> {
  /**
   * Create a new ATRIndicator
   * @param parameters Object with ATR parameters
   * @param parameters.period Number of periods for ATR calculation (default: 14)
   * @throws If period is less than 1
   */
  constructor(parameters?: Record<string, unknown>) {
    super(ATR_METADATA, parameters);
    const period = this.getNumericParameter('period', 14);
    if (period < 1) {
      throw new Error('ATR period must be at least 1');
    }
  }

  /**
   * Update ATR parameters
   * @param parameters Object with updated parameters
   * @throws If period is less than 1
   */
  override updateParameters(parameters: Record<string, unknown>): void {
    if ('period' in parameters) {
      const period = Number(parameters.period);
      if (isNaN(period) || period < 1) {
        throw new Error('ATR period must be at least 1');
      }
    }
    super.updateParameters(parameters);
  }

  /**
   * Calculate ATR values for the given price data (expects [high, low, close, high, low, close, ...])
   * or throws if not possible. For full object input, use calculateRaw.
   * @param data Flat array: [high, low, close, ...] (length must be multiple of 3)
   * @returns Array of ATR values
   * @throws If not enough data points or mismatched array lengths
   */
  calculate(data: number[]): number[] {
    // Validate input is a flat array of [high, low, close, ...]
    if (!Array.isArray(data) || data.length % 3 !== 0) {
      throw new Error('ATRIndicator.calculate expects a flat array of [high, low, close, ...]');
    }
    // Validate all values are finite numbers
    this.validateNumericArray(data);
    const highs: number[] = [];
    const lows: number[] = [];
    const closes: number[] = [];
    for (let i = 0; i < data.length; i += 3) {
      // Ensure we have enough data for a complete OHLC set
      if (i + 2 < data.length) {
        const high = data.at(i);
        const low = data.at(i + 1);
        const close = data.at(i + 2);
        if (high !== undefined && low !== undefined && close !== undefined) {
          highs.push(high);
          lows.push(low);
          closes.push(close);
        }
      }
    }
    return this.calculateRaw({ highs, lows, closes });
  }

  /**
   * Calculate ATR values for the given price data (object input)
   * @param data Object with highs, lows, closes arrays
   * @returns Array of ATR values
   * @throws If not enough data points or mismatched array lengths
   */
  calculateRaw(data: {highs: number[], lows: number[], closes: number[]}): number[] {
    const highs = this.validateNumericArray(data.highs);
    const lows = this.validateNumericArray(data.lows);
    const closes = this.validateNumericArray(data.closes);
    if (highs.length !== lows.length || highs.length !== closes.length) {
      throw new Error('High, low, and close arrays must be of the same length');
    }
    const period = this.getNumericParameter('period', 14);
    if (highs.length < period + 1) {
      throw new Error(`Insufficient data for ATR calculation. Need at least ${period + 1} data points.`);
    }
    const trValues: number[] = [];
    const atrValues: number[] = [];
    function assertIsNumber(val: number | undefined, name: string, idx: number): asserts val is number {
      if (val === undefined) {throw new Error(`Missing or invalid ${name} at index ${idx} for ATR calculation`);}
    }
    for (let i = 1; i < highs.length; i++) {
      const highVal = highs.at(i);
      const lowVal = lows.at(i);
      const prevCloseVal = closes.at(i - 1);
      assertIsNumber(highVal, 'high', i);
      assertIsNumber(lowVal, 'low', i);
      assertIsNumber(prevCloseVal, 'prevClose', i);
      const high = highVal as number;
      const low = lowVal as number;
      const prevClose = prevCloseVal as number;
      trValues.push(ATRIndicator.calculateTrueRange(high, low, prevClose));
    }
    const firstATR = trValues.slice(0, period).reduce((sum, tr) => sum + tr, 0) / period;
    atrValues.push(firstATR);
    for (let i = period; i < trValues.length; i++) {
      const prevAtr = atrValues.at(-1);
      const currentTr = trValues.at(i);
      if (typeof prevAtr !== 'number' || typeof currentTr !== 'number') {
        throw new Error(`Missing or invalid data at index ${i} for ATR smoothing calculation`);
      }
      const atr = (prevAtr * (period - 1) + currentTr) / period;
      atrValues.push(atr);
    }
    return atrValues;
  }

/**
   * Clone the indicator with the same parameters
   * @returns New ATRIndicator instance
   */
  clone(): IIndicator<number[]> {
    return new ATRIndicator(this.getParameters());
  }

  /**
   * Set the period for the ATR calculation
   * @param period Number of periods (must be >= 1)
   * @throws If period is less than 1
   */
  setPeriod(period: number): void {
    if (period < 1) {
      throw new Error('ATR period must be at least 1');
    }
    this.updateParameters({ period });
  }

  /**
   * Get the current ATR period
   * @returns Current ATR period
   */
  getPeriod(): number {
    return this.getNumericParameter('period', 14);
  }

  /**
   * Calculate the true range for a single period
 * @param high Current high price
 * @param low Current low price
 * @param prevClose Previous close price
 * @returns True range value
 */
  static calculateTrueRange(high: number, low: number, prevClose: number): number {
    const range1 = high - low;
    const range2 = Math.abs(high - prevClose);
    const range3 = Math.abs(low - prevClose);
    return Math.max(range1, range2, range3);
  }
}

/**
 * @deprecated Use ATRIndicator class instead.
 */
export const calculateTrueRange = ATRIndicator.calculateTrueRange;

/**
 * @deprecated Use ATRIndicator class instead.
 */
export function calculateATR(highs: number[], lows: number[], closes: number[], period = 14): number[] {
  if (!Array.isArray(highs) || !Array.isArray(lows) || !Array.isArray(closes)) {
    throw new Error('ATR input must be object with highs, lows, closes arrays');
  }
  if (highs.length !== lows.length || highs.length !== closes.length) {
    throw new Error('High, low, and close arrays must be of the same length');
  }
  // Flatten to [high, low, close, ...] format
  const flat: number[] = [];
  for (let i = 0; i < highs.length; i++) {
    const h = highs.at(i) ?? NaN;
    const l = lows.at(i) ?? NaN;
    const c = closes.at(i) ?? NaN;
    if (isNaN(h) || isNaN(l) || isNaN(c)) {
      throw new Error(`Missing or invalid data at index ${i} for ATR calculation`);
    }
    flat.push(h, l, c);
  }
  return new ATRIndicator({ period }).calculate(flat);
}

/**
 * Calculate ATR-based stop loss levels (utility, not part of indicator interface)
 *
 * @param closes Array of close prices
 * @param atrValues Array of ATR values
 * @param multiplier ATR multiplier for stop loss (default: 2)
 * @param isLong Boolean indicating if position is long (true) or short (false)
 * @returns Array of stop loss levels
 */
export function calculateATRStopLoss(closes: number[], atrValues: number[], multiplier = 2, isLong = true): number[] {
  if (closes.length < atrValues.length) {
    throw new Error('Close prices array must be at least as long as ATR values array');
  }
  const stopLevels: number[] = [];
  const alignedCloses = closes.slice(closes.length - atrValues.length);
  for (let i = 0; i < atrValues.length; i++) {
    const close = alignedCloses.at(i);
    const atr = atrValues.at(i);
    if (close === undefined || atr === undefined) {
      throw new Error(`Missing data at index ${i} for ATR stop loss calculation`);
    }
    if (isLong) {
      stopLevels.push(close - atr * multiplier);
    } else {
      stopLevels.push(close + atr * multiplier);
    }
  }
  return stopLevels;
}

/**
 * Average Price Indicator
 *
 * Provides a function to calculate the average (mean) price from an array of candle data.
 *
 * Usage:
 *   import { calculateAveragePrice } from './average-price'
 *
 *   const avg = calculateAveragePrice(candles)
 */

// NOTE: This file is self-contained and does NOT import itself. There is no real circular dependency here. If flagged, this is a tool limitation, not a code issue.

export interface Candle {
  open: number
  high: number
  low: number
  close: number
  volume?: number
  timestamp?: number
}

/**
 * Calculate the average (mean) price from an array of candles.
 * Uses the average of the low prices by default, but can be extended.
 *
 * @param candles Array of candle objects
 * @param priceType 'low' | 'close' | 'high' | 'open' (default: 'low')
 * @returns Average price
 */
export function calculateAveragePrice(
  candles: Candle[],
  priceType: 'low' | 'close' | 'high' | 'open' = 'low'
): number {
  if (!candles.length) {throw new Error('No candle data provided')}
  const sum = candles.reduce((acc, candle) => {
    const validPriceTypes = ['low', 'close', 'high', 'open'] as const;
    if (!validPriceTypes.includes(priceType)) {
      throw new Error(`Invalid price type: ${priceType}`);
    }
    return acc + (candle[priceType] ?? 0);
  }, 0);
  return sum / candles.length
}

/**
 * Bollinger Bands Indicator Class
 * 
 * Class-based implementation of Bollinger Bands indicator conforming to the unified interface
 */

import type { BollingerBandsResult } from './bollinger-bands';
import { calculateBollingerBands } from './bollinger-bands';
import type { IndicatorMetadata } from './indicator-interface';
import { BaseIndicator } from './indicator-interface';

/**
 * Bollinger Bands Indicator Class
 */
export class BollingerBandsIndicator extends BaseIndicator<BollingerBandsResult> {
  constructor(parameters: Record<string, unknown> = {}) {
    const metadata: IndicatorMetadata = {
      name: 'Bollinger Bands',
      description: 'Bollinger Bands - volatility indicator with upper and lower bands around a moving average',
      category: 'volatility',
      parameters: [
        {
          name: 'period',
          type: 'number',
          description: 'Period for SMA calculation',
          default: 20,
          min: 2,
          max: 100,
          required: false
        },
        {
          name: 'multiplier',
          type: 'number',
          description: 'Standard deviation multiplier',
          default: 2,
          min: 0.1,
          max: 5,
          required: false
        }
      ],
      outputs: [
        {
          name: 'upper',
          type: 'array',
          description: 'Upper band values'
        },
        {
          name: 'middle',
          type: 'array',
          description: 'Middle band (SMA) values'
        },
        {
          name: 'lower',
          type: 'array',
          description: 'Lower band values'
        },
        {
          name: 'bandwidth',
          type: 'array',
          description: 'Bandwidth values'
        },
        {
          name: 'percentB',
          type: 'array',
          description: 'Percent B values'
        }
      ],
      minimumDataPoints: 20, // Default period
      version: '1.0.0'
    };
    
    super(metadata, parameters);
  }
  
  calculate(data: number[]): BollingerBandsResult {
    const validData = this.validateNumericArray(data);
    
    const period = this.getNumericParameter('period', 20);
    const multiplier = this.getNumericParameter('multiplier', 2);
    
    this.validateDataLength(validData, period);
    
    return calculateBollingerBands(validData, period, multiplier);
  }
  
  clone(): BollingerBandsIndicator {
    return new BollingerBandsIndicator(this.getParameters());
  }
  
  /**
   * Get the latest Bollinger Bands signal
   * 
   * @param data Input price data
   * @returns Signal value (1 for buy signal, -1 for sell signal, 0 for no signal)
   */
  getLatestSignal(data: number[]): number {
    const result = this.calculate(data);
    const { upper, lower } = result;
    
    if (data.length < 2 || upper.length < 2 || lower.length < 2) {
      return 0;
    }
    
    const currentPrice = data[data.length - 1];
    const prevPrice = data[data.length - 2];
    const currentUpper = upper[upper.length - 1];
    const prevUpper = upper[upper.length - 2];
    const currentLower = lower[lower.length - 1];
    const prevLower = lower[lower.length - 2];
    
    if (currentPrice === undefined || prevPrice === undefined ||
        currentUpper === undefined || prevUpper === undefined ||
        currentLower === undefined || prevLower === undefined) {
      return 0;
    }
    
    // Price crosses below lower band - potential buy signal
    if (currentPrice < currentLower && prevPrice >= prevLower) {
      return 1;
    }
    
    // Price crosses above upper band - potential sell signal
    if (currentPrice > currentUpper && prevPrice <= prevUpper) {
      return -1;
    }
    
    return 0;
  }
  
  /**
   * Get current position relative to bands
   * 
   * @param data Input price data
   * @returns Position (-1 to 1, where -1 is at lower band, 0 is at middle, 1 is at upper band)
   */
  getPosition(data: number[]): number {
    const result = this.calculate(data);
    const { upper, lower } = result;
    
    if (data.length === 0 || upper.length === 0 || lower.length === 0) {
      return 0;
    }
    
    const currentPrice = data[data.length - 1];
    const currentUpper = upper[upper.length - 1];
    const currentLower = lower[lower.length - 1];
    
    if (currentPrice === undefined || currentUpper === undefined || currentLower === undefined) {
      return 0;
    }
    
    const bandWidth = currentUpper - currentLower;
    if (bandWidth === 0) {
      return 0;
    }
    
    // Calculate position within bands
    const position = (currentPrice - currentLower) / bandWidth;
    
    // Scale to -1 to 1 range
    return position * 2 - 1;
  }
  
  /**
   * Check if price is in squeeze condition (low volatility)
   * 
   * @param data Input price data
   * @param lookbackPeriod Period to compare bandwidth against
   * @returns True if in squeeze condition
   */
  isSqueeze(data: number[], lookbackPeriod = 20): boolean {
    const result = this.calculate(data);
    const { bandwidth } = result;
    
    if (bandwidth.length < lookbackPeriod) {
      return false;
    }
    
    const currentBandwidth = bandwidth[bandwidth.length - 1];
    if (currentBandwidth === undefined) {
      return false;
    }
    
    // Compare current bandwidth to recent average
    const recentBandwidth = bandwidth.slice(-lookbackPeriod);
    const avgBandwidth = recentBandwidth.reduce((sum, val) => sum + val, 0) / recentBandwidth.length;
    
    // Squeeze if current bandwidth is significantly lower than recent average
    return currentBandwidth < avgBandwidth * 0.8;
  }
  
  /**
   * Get %B value (position within bands as percentage)
   * 
   * @param data Input price data
   * @returns %B value (0 to 1, where 0 is at lower band, 1 is at upper band)
   */
  getPercentB(data: number[]): number {
    const result = this.calculate(data);
    const { percentB } = result;
    
    if (percentB.length === 0) {
      return 0.5; // Default to middle
    }
    
    const latestPercentB = percentB[percentB.length - 1];
    return latestPercentB !== undefined ? latestPercentB : 0.5;
  }
  
  /**
   * Check if bands are expanding (increasing volatility)
   * 
   * @param data Input price data
   * @param periods Number of periods to compare
   * @returns True if bands are expanding
   */
  isExpanding(data: number[], periods = 3): boolean {
    const result = this.calculate(data);
    const { bandwidth } = result;
    
    if (bandwidth.length < periods + 1) {
      return false;
    }
    
    const recentBandwidth = bandwidth.slice(-periods - 1);
    
    // Check if bandwidth is generally increasing
    for (let i = 1; i < recentBandwidth.length; i++) {
      const current = recentBandwidth.at(i);
      const previous = recentBandwidth.at(i - 1);
      if (current === undefined || previous === undefined || current <= previous) {
        return false;
      }
    }
    
    return true;
  }
}

/**
 * Exponential Moving Average (EMA) Indicator
 * 
 * Calculates the exponentially weighted moving average of prices over a specified period.
 *
 * @example
 * // Calculate 3-period EMA for a price series
 * const ema = new EMAIndicator({ period: 3, smoothing: 2 });
 * const result = ema.calculate([2, 4, 6, 8, 10]); // [2, 3, 4.5, 6.25, 8.125]
 */

import { BaseIndicator, type IndicatorMetadata, type IIndicator } from './indicator-interface';

/**
 * EMA Indicator Metadata
 */
const EMA_METADATA: IndicatorMetadata = {
  name: 'EMA',
  description: 'Exponential Moving Average - gives more weight to recent prices with exponential decay',
  category: 'trend',
  parameters: [
    {
      name: 'period',
      type: 'number',
      description: 'Number of periods for EMA calculation',
      default: 14,
      min: 1,
      max: 200,
      required: true
    },
    {
      name: 'smoothing',
      type: 'number',
      description: 'Smoothing factor (2 = standard EMA)',
      default: 2,
      min: 0.1,
      max: 10,
      required: false
    }
  ],
  outputs: [
    {
      name: 'ema',
      type: 'array',
      description: 'Array of EMA values'
    }
  ],
  minimumDataPoints: 1,
  version: '1.0.0'
};

/**
 * Exponential Moving Average (EMA) indicator
 */
export class EMAIndicator extends BaseIndicator<number[]> {
  /**
   * Create a new EMAIndicator
   * @param parameters Object with EMA parameters
   * @param parameters.period Number of periods for EMA calculation (default: 14)
   * @param parameters.smoothing Smoothing factor (default: 2)
   * @throws If period is less than 1 or smoothing is not positive
   */
  constructor(parameters?: Record<string, unknown>) {
    super(EMA_METADATA, parameters);
    // Validate period and smoothing on instantiation
    const period = this.getNumericParameter('period', 14);
    const smoothing = this.getNumericParameter('smoothing', 2);
    if (period < 1) {
      throw new Error('EMA period must be at least 1');
    }
    if (smoothing <= 0) {
      throw new Error('EMA smoothing factor must be positive');
    }
  }
  
  /**
   * Update EMA parameters
   * @param parameters Object with updated parameters
   * @throws If period is less than 1 or smoothing is not positive
   */
  override updateParameters(parameters: Record<string, unknown>): void {
    if ('period' in parameters) {
      const period = Number(parameters.period);
      if (isNaN(period) || period < 1) {
        throw new Error('EMA period must be at least 1');
      }
    }
    if ('smoothing' in parameters) {
      const smoothing = Number(parameters.smoothing);
      if (isNaN(smoothing) || smoothing <= 0) {
        throw new Error('EMA smoothing factor must be positive');
      }
    }
    super.updateParameters(parameters);
  }
  
  /**
   * Calculate EMA values for the given price data
   * @param data Array of price data
   * @returns Array of EMA values
   * @throws If not enough data points are provided
   * @example
   * const ema = new EMAIndicator({ period: 3, smoothing: 2 });
   * ema.calculate([2, 4, 6, 8, 10]); // [2, 3, 4.5, 6.25, 8.125]
   */
  calculate(data: number[]): number[] {
    const validData = this.validateNumericArray(data);
    const period = this.getNumericParameter('period', 14);
    const smoothing = this.getNumericParameter('smoothing', 2);
    
    this.validateDataLength(validData, period);
    
    if (validData.length === 0) {
      return [];
    }
    
    const multiplier = smoothing / (period + 1);
    const emaValues: number[] = [];
    
    // First EMA value is the first price
    const firstPrice = validData[0];
    if (firstPrice !== undefined) {
      emaValues.push(firstPrice);
    } else {
      throw new Error('First price is undefined');
    }
    
    // Calculate subsequent EMA values
    for (let i = 1; i < validData.length; i++) {
      const currentPrice = validData.at(i);
      const previousEma = emaValues.at(i - 1);
      
      if (currentPrice !== undefined && previousEma !== undefined) {
        const ema = (currentPrice * multiplier) + (previousEma * (1 - multiplier));
        emaValues.push(ema);
      }
    }
    
    return emaValues;
  }
  
  /**
   * Clone the indicator with the same parameters
   * @returns New EMAIndicator instance
   */
  clone(): IIndicator<number[]> {
    return new EMAIndicator(this.getParameters());
  }
  
  /**
   * Set the period for the EMA calculation
   * @param period Number of periods (must be >= 1)
   * @throws If period is less than 1
   */
  setPeriod(period: number): void {
    if (period < 1) {
      throw new Error('EMA period must be at least 1');
    }
    this.updateParameters({ period });
  }
  
  /**
   * Get the current EMA period
   * @returns Current EMA period
   */
  getPeriod(): number {
    return this.getNumericParameter('period', 14);
  }
  
  /**
   * Set the smoothing factor for the EMA calculation
   * @param smoothing Smoothing factor (must be > 0)
   * @throws If smoothing is not positive
   */
  setSmoothing(smoothing: number): void {
    if (smoothing <= 0) {
      throw new Error('EMA smoothing factor must be positive');
    }
    this.updateParameters({ smoothing });
  }
  
  /**
   * Get the current EMA smoothing factor
   * @returns Current EMA smoothing factor
   */
  getSmoothing(): number {
    return this.getNumericParameter('smoothing', 2);
  }
}

