/**
 * Unified Indicator Interface
 * 
 * Provides a standard interface for technical indicators to ensure
 * consistent usage across the platform.
 */

import type { Candle } from '../target-reacher/interfaces';

/**
 * Indicator metadata for documentation and validation
 */
export interface IndicatorMetadata {
  name: string;
  description: string;
  category: 'trend' | 'momentum' | 'volatility' | 'volume' | 'overlay' | 'oscillator';
  parameters: Array<{
    name: string;
    type: 'number' | 'boolean' | 'string';
    description: string;
    default: any;
    min?: number;
    max?: number;
    required?: boolean;
  }>;
  outputs: Array<{
    name: string;
    type: 'number' | 'array';
    description: string;
  }>;
  minimumDataPoints: number;
  version: string;
}

/**
 * Indicator calculation result with metadata
 */
export interface IndicatorResult<T = number | number[]> {
  value: T;
  metadata?: {
    timestamp?: number;
    confidence?: number;
    parameters?: Record<string, unknown>;
    dataPoints?: number;
  };
}

/**
 * Base interface for all indicators
 */
export interface IIndicator<T = number | number[]> {
  /**
   * Get indicator metadata
   */
  getMetadata(): IndicatorMetadata;
  
  /**
   * Calculate indicator value(s) from price data
   * 
   * @param data Input price data
   * @returns Indicator value(s) or result with metadata
   */
  calculate(data: number[]): T;
  
  /**
   * Calculate indicator value(s) from candle data
   * 
   * @param candles Input candle data
   * @param priceSource Which price to use from candles
   * @returns Indicator value(s) or result with metadata
   */
  calculateFromCandles(candles: Candle[], priceSource?: 'open' | 'high' | 'low' | 'close'): T;
  
  /**
   * Calculate with detailed result including metadata
   * 
   * @param data Input price data
   * @returns Detailed result with metadata
   */
  calculateWithMetadata(data: number[]): IndicatorResult<T>;
  
  /**
   * Validate input parameters
   * 
   * @param parameters Parameters to validate
   * @returns Validation result
   */
  validateParameters(parameters: Record<string, unknown>): { valid: boolean; errors: string[] };
  
  /**
   * Get indicator name
   */
  getName(): string;
  
  /**
   * Get indicator parameters
   */
  getParameters(): Record<string, unknown>;
  
  /**
   * Update indicator parameters
   * 
   * @param parameters New parameters to update
   */
  updateParameters(parameters: Record<string, unknown>): void;
  
  /**
   * Reset indicator state (for stateful indicators)
   */
  reset(): void;
  
  /**
   * Clone the indicator with same parameters
   */
  clone(): IIndicator<T>;
}

/**
 * Base abstract class for indicators
 */
export abstract class BaseIndicator<T = number | number[]> implements IIndicator<T> {
  protected name: string;
  protected parameters: Record<string, unknown>;
  protected metadata: IndicatorMetadata;
  
  constructor(metadata: IndicatorMetadata, parameters: Record<string, unknown> = {}) {
    this.metadata = metadata;
    this.name = metadata.name;
    this.parameters = this.setDefaultParameters(parameters);
  }
  
  abstract calculate(data: number[]): T;
  
  getMetadata(): IndicatorMetadata {
    return { ...this.metadata };
  }
  
  calculateFromCandles(candles: Candle[], priceSource: 'open' | 'high' | 'low' | 'close' = 'close'): T {
    const validPriceSources = ['open', 'high', 'low', 'close'] as const;
    if (!validPriceSources.includes(priceSource)) {
      throw new Error(`Invalid price source: ${priceSource}`);
    }
    const prices = candles.map(candle => candle[priceSource]);
    return this.calculate(prices);
  }
  
  calculateWithMetadata(data: number[]): IndicatorResult<T> {
    const value = this.calculate(data);
    // Check for NaN/Infinity in result (supports number or array)
    const checkFinite = (v: any) => {
      if (typeof v === 'number') {return isFinite(v) && !isNaN(v);}
      if (Array.isArray(v)) {return v.every(x => typeof x === 'number' && isFinite(x) && !isNaN(x));}
      return false;
    };
    if (!checkFinite(value)) {
      throw new Error(`Indicator ${this.name} calculation returned non-finite value(s). Check input data and parameters.`);
    }
    return {
      value,
      metadata: {
        timestamp: Date.now(),
        parameters: this.getParameters(),
        dataPoints: data.length
      }
    };
  }
  
  validateParameters(parameters: Record<string, unknown>): { valid: boolean; errors: string[] } {
    const errors: string[] = [];
    
    for (const paramDef of this.metadata.parameters) {
      const value = parameters[paramDef.name];
      
      // Check required parameters
      if (paramDef.required && (value === undefined || value === null)) {
        errors.push(`Parameter '${paramDef.name}' is required`);
        continue;
      }
      
      if (value !== undefined && value !== null) {
        // Type validation
        if (paramDef.type === 'number' && typeof value !== 'number') {
          errors.push(`Parameter '${paramDef.name}' must be a number`);
          continue;
        }
        
        if (paramDef.type === 'boolean' && typeof value !== 'boolean') {
          errors.push(`Parameter '${paramDef.name}' must be a boolean`);
          continue;
        }
        
        if (paramDef.type === 'string' && typeof value !== 'string') {
          errors.push(`Parameter '${paramDef.name}' must be a string`);
          continue;
        }
        
        // Range validation for numbers
        if (paramDef.type === 'number' && typeof value === 'number') {
          if (paramDef.min !== undefined && value < paramDef.min) {
            errors.push(`Parameter '${paramDef.name}' must be at least ${paramDef.min}`);
          }
          
          if (paramDef.max !== undefined && value > paramDef.max) {
            errors.push(`Parameter '${paramDef.name}' must be at most ${paramDef.max}`);
          }
        }
      }
    }
    
    return { valid: errors.length === 0, errors };
  }
  
  getName(): string {
    return this.name;
  }
  
  getParameters(): Record<string, unknown> {
    return { ...this.parameters };
  }
  
  updateParameters(parameters: Record<string, unknown>): void {
    const validation = this.validateParameters(parameters);
    if (!validation.valid) {
      throw new Error(`Invalid parameters: ${validation.errors.join(', ')}`);
    }
    
    this.parameters = { ...this.parameters, ...parameters };
  }
  
  reset(): void {
    // Default implementation - override in stateful indicators
  }
  
  abstract clone(): IIndicator<T>;
  
  /**
   * Set default parameters from metadata
   */
  private setDefaultParameters(parameters: Record<string, unknown>): Record<string, unknown> {
    const result = { ...parameters };
    
    for (const paramDef of this.metadata.parameters) {
      if (result[paramDef.name] === undefined) {
        result[paramDef.name] = paramDef.default;
      }
    }
    
    return result;
  }
  
  /**
   * Validate that enough data points are available
   * 
   * @param data Input data array
   * @param minLength Minimum required length (optional, uses metadata if not provided)
   */
  protected validateDataLength(data: number[], minLength?: number): void {
    const requiredLength = minLength || this.metadata.minimumDataPoints;
    if (data.length < requiredLength) {
      throw new Error(`Insufficient data for ${this.name} calculation. Need at least ${requiredLength} data points, got ${data.length}.`);
    }
  }
  
  /**
   * Get numeric parameter value with validation
   * 
   * @param key Parameter key
   * @param defaultValue Default value if parameter is not set
   * @returns Parameter value
   */
  protected getNumericParameter(key: string, defaultValue: number): number {
    if (typeof key !== 'string' || !key) {
      throw new Error('Parameter key must be a non-empty string');
    }
    const value = Object.prototype.hasOwnProperty.call(this.parameters, key) ? this.parameters[key as keyof typeof this.parameters] : undefined;
    if (value === undefined) {
      return defaultValue;
    }
    
    const numValue = Number(value);
    if (isNaN(numValue)) {
      throw new Error(`Invalid parameter value for ${key}: ${value} is not a number`);
    }
    
    return numValue;
  }
  
  /**
   * Get boolean parameter value with validation
   * 
   * @param key Parameter key
   * @param defaultValue Default value if parameter is not set
   * @returns Parameter value
   */
  protected getBooleanParameter(key: string, defaultValue: boolean): boolean {
    if (typeof key !== 'string' || !key) {
      throw new Error('Parameter key must be a non-empty string');
    }
    const value = Object.prototype.hasOwnProperty.call(this.parameters, key) ? this.parameters[key as keyof typeof this.parameters] : undefined;
    if (value === undefined) {
      return defaultValue;
    }
    
    return Boolean(value);
  }
  
  /**
   * Validate numeric array input
   * 
   * @param data Input data array
   * @returns Validated data array
   */
  protected validateNumericArray(data: number[]): number[] {
    if (!Array.isArray(data)) {
      throw new Error(`Input data must be an array for ${this.name}`);
    }
    // Reject NaN, Infinity, -Infinity, and non-numbers
    const validData = data.filter(value => typeof value === 'number' && !isNaN(value) && isFinite(value));
    if (validData.length !== data.length) {
      throw new Error(`Input data contains invalid values for ${this.name}. All values must be finite numbers.`);
    }
    return validData;
  }
}

/**
 * MACD (Moving Average Convergence Divergence) Indicator Class
 * 
 * Class-based implementation of MACD indicator conforming to the unified interface
 */

import type { IndicatorMetadata } from './indicator-interface';
import { BaseIndicator } from './indicator-interface';
import type { MACDResult } from './macd';
import { calculateMACD } from './macd';

/**
 * MACD Indicator Class
 */
export class MACDIndicator extends BaseIndicator<MACDResult> {
  constructor(parameters: Record<string, unknown> = {}) {
    const metadata: IndicatorMetadata = {
      name: 'MACD',
      description: 'Moving Average Convergence Divergence - trend-following momentum indicator',
      category: 'momentum',
      parameters: [
        {
          name: 'fastPeriod',
          type: 'number',
          description: 'Fast EMA period',
          default: 12,
          min: 1,
          max: 100,
          required: false
        },
        {
          name: 'slowPeriod',
          type: 'number',
          description: 'Slow EMA period',
          default: 26,
          min: 1,
          max: 200,
          required: false
        },
        {
          name: 'signalPeriod',
          type: 'number',
          description: 'Signal EMA period',
          default: 9,
          min: 1,
          max: 100,
          required: false
        }
      ],
      outputs: [
        {
          name: 'macd',
          type: 'array',
          description: 'MACD line values'
        },
        {
          name: 'signal',
          type: 'array',
          description: 'Signal line values'
        },
        {
          name: 'histogram',
          type: 'array',
          description: 'Histogram values (MACD - Signal)'
        }
      ],
      minimumDataPoints: 34, // Default: 26 + 9 - 1
      version: '1.0.0'
    };
    
    super(metadata, parameters);
  }
  
  calculate(data: number[]): MACDResult {
    const validData = this.validateNumericArray(data);
    
    const fastPeriod = this.getNumericParameter('fastPeriod', 12);
    const slowPeriod = this.getNumericParameter('slowPeriod', 26);
    const signalPeriod = this.getNumericParameter('signalPeriod', 9);
    
    // Update minimum data points based on actual parameters
    const minDataPoints = Math.max(fastPeriod, slowPeriod) + signalPeriod;
    this.validateDataLength(validData, minDataPoints);
    
    return calculateMACD(validData, fastPeriod, slowPeriod, signalPeriod);
  }
  
  clone(): MACDIndicator {
    return new MACDIndicator(this.getParameters());
  }
  
  /**
   * Get the latest MACD signal (crossover detection)
   * 
   * @param data Input price data
   * @returns Latest signal value (1 for bullish, -1 for bearish, 0 for no signal)
   */
  getLatestSignal(data: number[]): number {
    const result = this.calculate(data);
    const { macd, signal } = result;
    
    if (macd.length < 2 || signal.length < 2) {
      return 0;
    }
    
    const latestMacd = macd[macd.length - 1];
    const prevMacd = macd[macd.length - 2];
    const latestSignal = signal[signal.length - 1];
    const prevSignal = signal[signal.length - 2];
    
    if (latestMacd === undefined || prevMacd === undefined || 
        latestSignal === undefined || prevSignal === undefined) {
      return 0;
    }
    
    // Bullish crossover: MACD crosses above signal
    if (latestMacd > latestSignal && prevMacd <= prevSignal) {
      return 1;
    }
    
    // Bearish crossover: MACD crosses below signal
    if (latestMacd < latestSignal && prevMacd >= prevSignal) {
      return -1;
    }
    
    return 0;
  }
  
  /**
   * Check if MACD is currently bullish (above signal line)
   * 
   * @param data Input price data
   * @returns True if MACD is above signal line
   */
  isBullish(data: number[]): boolean {
    const result = this.calculate(data);
    const { macd, signal } = result;
    
    if (macd.length === 0 || signal.length === 0) {
      return false;
    }
    
    const latestMacd = macd[macd.length - 1];
    const latestSignal = signal[signal.length - 1];
    
    if (latestMacd === undefined || latestSignal === undefined) {
      return false;
    }
    
    return latestMacd > latestSignal;
  }
  
  /**
   * Get MACD divergence strength (normalized histogram value)
   * 
   * @param data Input price data
   * @returns Divergence strength (-1 to 1)
   */
  getDivergenceStrength(data: number[]): number {
    const result = this.calculate(data);
    const { histogram } = result;
    
    if (histogram.length === 0) {
      return 0;
    }
    
    const latestHistogram = histogram[histogram.length - 1];
    
    if (latestHistogram === undefined) {
      return 0;
    }
    
    // Normalize based on recent histogram range
    const recentHistogram = histogram.slice(-20); // Last 20 values
    const maxHist = Math.max(...recentHistogram);
    const minHist = Math.min(...recentHistogram);
    const range = maxHist - minHist;
    
    if (range === 0) {
      return 0;
    }
    
    return (latestHistogram - minHist) / range * 2 - 1; // Scale to -1 to 1
  }
}

/**
 * Relative Strength Index (RSI) Indicator
 *
 * Calculates the momentum oscillator that measures the speed and change of price movements.
 *
 * @example
 * // Calculate 14-period RSI for a price series
 * const rsi = new RSIIndicator({ period: 14 });
 * const result = rsi.calculate([1, 2, 3, 2, 1, 2, 3, 4, 5]);
 */
import { BaseIndicator, type IndicatorMetadata, type IIndicator } from './indicator-interface';

const RSI_METADATA: IndicatorMetadata = {
  name: 'RSI',
  description: 'Relative Strength Index - momentum oscillator for overbought/oversold conditions',
  category: 'momentum',
  parameters: [
    {
      name: 'period',
      type: 'number',
      description: 'Number of periods for RSI calculation',
      default: 14,
      min: 1,
      max: 200,
      required: true
    }
  ],
  outputs: [
    {
      name: 'rsi',
      type: 'array',
      description: 'Array of RSI values'
    }
  ],
  minimumDataPoints: 2,
  version: '1.0.0'
};

/**
 * RSIIndicator class (preferred usage)
 */
export class RSIIndicator extends BaseIndicator<number[]> {
  /**
   * Create a new RSIIndicator
   * @param parameters Object with RSI parameters
   * @param parameters.period Number of periods for RSI calculation (default: 14)
   * @throws If period is less than 1
   */
  constructor(parameters?: Record<string, unknown>) {
    super(RSI_METADATA, parameters);
    const period = this.getNumericParameter('period', 14);
    if (period < 1) {
      throw new Error('RSI period must be at least 1');
    }
  }

  /**
   * Update RSI parameters
   * @param parameters Object with updated parameters
   * @throws If period is less than 1
   */
  override updateParameters(parameters: Record<string, unknown>): void {
    if ('period' in parameters) {
      const period = Number(parameters.period);
      if (isNaN(period) || period < 1) {
        throw new Error('RSI period must be at least 1');
      }
    }
    super.updateParameters(parameters);
  }

  /**
   * Calculate RSI values for the given price data
   * @param data Array of price values
   * @returns Array of RSI values
   * @throws If not enough data points
   * @example
   * const rsi = new RSIIndicator({ period: 14 });
   * rsi.calculate([1, 2, 3, 2, 1, 2, 3, 4, 5]);
   */
  calculate(data: number[]): number[] {
    const prices = this.validateNumericArray(data);
    const period = this.getNumericParameter('period', 14);
    if (prices.length < period + 1) {
      throw new Error(`Insufficient data for RSI calculation. Need at least ${period + 1} data points.`);
    }
    const rsiValues: number[] = [];
    const gains: number[] = [];
    const losses: number[] = [];
    for (let i = 1; i < prices.length; i++) {
      const price = prices.at(i);
      const prevPrice = prices.at(i - 1);
      if (price === undefined || prevPrice === undefined) {
        throw new Error(`Missing price at index ${i} for RSI calculation`);
      }
      const change = price - prevPrice;
      gains.push(change > 0 ? change : 0);
      losses.push(change < 0 ? Math.abs(change) : 0);
    }
    let avgGain = gains.slice(0, period).reduce((sum, gain) => sum + gain, 0) / period;
    let avgLoss = losses.slice(0, period).reduce((sum, loss) => sum + loss, 0) / period;
    let rs = avgGain / (avgLoss === 0 ? 0.001 : avgLoss);
    rsiValues.push(100 - 100 / (1 + rs));
    for (let i = period; i < gains.length; i++) {
      const gain = gains.at(i);
      const loss = losses.at(i);
      if (gain === undefined || loss === undefined) {
        throw new Error(`Missing gain/loss at index ${i} for RSI smoothing calculation`);
      }
      avgGain = (avgGain * (period - 1) + gain) / period;
      avgLoss = (avgLoss * (period - 1) + loss) / period;
      rs = avgGain / (avgLoss === 0 ? 0.001 : avgLoss);
      rsiValues.push(100 - 100 / (1 + rs));
    }
    return rsiValues;
  }

  /**
   * Clone the indicator with the same parameters
   * @returns New RSIIndicator instance
   */
  clone(): IIndicator<number[]> {
    return new RSIIndicator(this.getParameters());
  }

  /**
   * Set the period for the RSI calculation
   * @param period Number of periods (must be >= 1)
   * @throws If period is less than 1
   */
  setPeriod(period: number): void {
    if (period < 1) {
      throw new Error('RSI period must be at least 1');
    }
    this.updateParameters({ period });
  }

  /**
   * Get the current RSI period
   * @returns Current RSI period
   */
  getPeriod(): number {
    return this.getNumericParameter('period', 14);
  }
}

/**
 * @deprecated Use RSIIndicator class instead.
 */
export function calculateRSI(prices: number[], period = 14): number[] {
  return new RSIIndicator({ period }).calculate(prices);
}

/**
 * Get RSI signals based on overbought/oversold levels (utility, not part of indicator interface)
 *
 * @param rsiValues Array of RSI values
 * @param overboughtLevel Overbought threshold (default: 70)
 * @param oversoldLevel Oversold threshold (default: 30)
 * @returns Array of signals (1 for buy signal, -1 for sell signal, 0 for no signal)
 */
export function getRSISignals(rsiValues: number[], overboughtLevel = 70, oversoldLevel = 30): number[] {
  const signals: number[] = [];
  for (let i = 1; i < rsiValues.length; i++) {
    const rsi = rsiValues.at(i);
    const rsiPrev = rsiValues.at(i - 1);
    if (rsi === undefined || rsiPrev === undefined) {
      throw new Error(`Missing RSI value at index ${i} for signal calculation`);
    }
    if (rsi < oversoldLevel && rsiPrev >= oversoldLevel) {
      signals.push(1);
    } else if (rsi > overboughtLevel && rsiPrev <= overboughtLevel) {
      signals.push(-1);
    } else {
      signals.push(0);
    }
  }
  return signals;
}

/**
 * Simple Moving Average (SMA) Indicator
 * 
 * Calculates the arithmetic mean of prices over a specified period.
 *
 * @example
 * // Calculate 3-period SMA for a price series
 * const sma = new SMAIndicator({ period: 3 });
 * const result = sma.calculate([1, 2, 3, 4, 5, 6]); // [2, 3, 4, 5]
 */

import { BaseIndicator, type IndicatorMetadata, type IIndicator } from './indicator-interface';

/**
 * SMA Indicator Metadata
 */
const SMA_METADATA: IndicatorMetadata = {
  name: 'SMA',
  description: 'Simple Moving Average - calculates the arithmetic mean of prices over a specified period',
  category: 'trend',
  parameters: [
    {
      name: 'period',
      type: 'number',
      description: 'Number of periods for SMA calculation',
      default: 14,
      min: 1,
      max: 200,
      required: true
    }
  ],
  outputs: [
    {
      name: 'sma',
      type: 'array',
      description: 'Array of SMA values'
    }
  ],
  minimumDataPoints: 1,
  version: '1.0.0'
};

/**
 * Simple Moving Average (SMA) indicator
 */
export class SMAIndicator extends BaseIndicator<number[]> {
  /**
   * Create a new SMAIndicator
   * @param parameters Object with SMA parameters
   * @param parameters.period Number of periods for SMA calculation (default: 14)
   * @throws If period is less than 1
   */
  constructor(parameters?: Record<string, unknown>) {
    super(SMA_METADATA, parameters);
    // Validate period on instantiation
    const period = this.getNumericParameter('period', 14);
    if (period < 1) {
      throw new Error('SMA period must be at least 1');
    }
  }
  
  /**
   * Update SMA parameters
   * @param parameters Object with updated parameters
   * @throws If period is less than 1
   */
  override updateParameters(parameters: Record<string, unknown>): void {
    if ('period' in parameters) {
      const period = Number(parameters.period);
      if (isNaN(period) || period < 1) {
        throw new Error('SMA period must be at least 1');
      }
    }
    super.updateParameters(parameters);
  }
  
  /**
   * Calculate SMA values for the given price data
   * @param data Array of price data
   * @returns Array of SMA values
   * @throws If not enough data points are provided
   * @example
   * const sma = new SMAIndicator({ period: 3 });
   * sma.calculate([1, 2, 3, 4, 5, 6]); // [2, 3, 4, 5]
   */
  calculate(data: number[]): number[] {
    const validData = this.validateNumericArray(data);
    const period = this.getNumericParameter('period', 14);
    
    this.validateDataLength(validData, period);
    
    const smaValues: number[] = [];
    
    for (let i = period - 1; i < validData.length; i++) {
      const windowPrices = validData.slice(i - period + 1, i + 1);
      const sum = windowPrices.reduce((total, price) => total + price, 0);
      smaValues.push(sum / period);
    }
    
    return smaValues;
  }
  
  /**
   * Clone the indicator with the same parameters
   * @returns New SMAIndicator instance
   */
  clone(): IIndicator<number[]> {
    return new SMAIndicator(this.getParameters());
  }
  
  /**
   * Set the period for the SMA calculation
   * @param period Number of periods (must be >= 1)
   * @throws If period is less than 1
   */
  setPeriod(period: number): void {
    if (period < 1) {
      throw new Error('SMA period must be at least 1');
    }
    this.updateParameters({ period });
  }
  
  /**
   * Get the current SMA period
   * @returns Current SMA period
   */
  getPeriod(): number {
    return this.getNumericParameter('period', 14);
  }
}

