        marketData?.symbol || 'UNKNOWN',
        marketData?.price
      );

      if (result.success && result.signal) {
        this.queueSignal(result.signal, 2); // Medium priority
        this.stats.aetherSignals++;
      } else {
        logger.warn('âš ï¸ Failed to translate Aether signal', { error: result.error });
        this.stats.failed++;
      }
    });

    this.aetherGenerator.on('error', (error: any) => {
      logger.error('âŒ Aether Signal Generator error', { error });
      this.emit('source-error', { source: 'aether', error });
    });
  }

  /**
   * Set up SMA signal listeners (manual polling since SMASignalProcessor doesn't extend EventEmitter)
   */
  private setupSMAListeners(): void {
    if (!this.smaProcessor) {return;}

    // Since SMASignalProcessor doesn't emit events, we'll need to implement polling or modify it
    // For now, we'll create a simple wrapper that can be called manually
    logger.info('âœ… SMA Signal Processor connected (manual processing mode)');
  }

  /**
   * Manually process SMA signals
   */
  async processSMASignals(candles: any[], symbol: string): Promise<void> {
    if (!this.smaProcessor) {return;}

    try {
      const signal = this.smaProcessor.process(candles);
      if (signal) {
        logger.debug('ðŸ“Š Generated SMA signal', { 
          signal: signal.signal, 
          confidence: signal.confidence,
          symbol 
        });

        const result = this.signalTranslator.translateSMASignal(signal, symbol);

        if (result.success && result.signal) {
          this.queueSignal(result.signal, 2); // Medium priority
          this.stats.smaSignals++;
        } else {
          logger.warn('âš ï¸ Failed to translate SMA signal', { error: result.error });
          this.stats.failed++;
        }
      }
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ SMA Signal Processor error', { error: errorMessage });
      this.emit('source-error', { source: 'sma', error: errorMessage });
    }
  }

  /**
   * Set up Target Reacher listeners
   */
  private setupTargetReacherListeners(): void {
    if (!this.targetReacher) {return;}

    this.targetReacher.on('strategy-result', (result: StrategyResult, context: any) => {
      logger.debug('ðŸŽ¯ Received Target Reacher signal', { 
        action: result.action, 
        confidence: result.confidence,
        symbol: context?.symbol || 'unknown' 
      });

      const result2 = this.signalTranslator.translateStrategyResult(
        result,
        context?.symbol || 'UNKNOWN',
        SignalSource.TARGET_REACHER
      );

      if (result2.success && result2.signal) {
        this.queueSignal(result2.signal, 3); // High priority for strategy results
        this.stats.strategySignals++;
      } else {
        logger.warn('âš ï¸ Failed to translate strategy result', { error: result2.error });
        this.stats.failed++;
      }
    });

    this.targetReacher.on('error', (error: any) => {
      logger.error('âŒ Target Reacher Service error', { error });
      this.emit('source-error', { source: 'target-reacher', error });
    });
  }

  /**
   * Queue a signal for processing
   */
  private queueSignal(signal: StandardSignal, priority = 1): void {
    // Check if signal passes quality filters
    if (!this.passesQualityFilter(signal)) {
      logger.debug('ðŸ” Signal filtered out', { 
        id: signal.id, 
        confidence: signal.confidence, 
        strength: signal.strength 
      });
      this.stats.filtered++;
      return;
    }

    // Check risk limits
    if (!this.passesRiskFilter(signal)) {
      logger.warn('âš ï¸ Signal blocked by risk filter', { 
        id: signal.id, 
        riskLevel: signal.riskLevel 
      });
      this.stats.filtered++;
      return;
    }

    const queuedSignal: QueuedSignal = {
      id: signal.id,
      signal,
      addedAt: Date.now(),
      priority
    };

    this.signalQueue.push(queuedSignal);
    
    // Sort by priority (higher first) then by timestamp (older first)
    this.signalQueue.sort((a, b) => {
      if (a.priority !== b.priority) {
        return b.priority - a.priority;
      }
      return a.addedAt - b.addedAt;
    });

    logger.debug('ðŸ“ Signal queued', { 
      id: signal.id, 
      priority, 
      queueSize: this.signalQueue.length 
    });

    this.emit('signal-queued', { signal, priority, queueSize: this.signalQueue.length });
  }

  /**
   * Check if signal passes quality filters
   */
  private passesQualityFilter(signal: StandardSignal): boolean {
    return signal.confidence >= this.config.minConfidence && 
           signal.strength >= this.config.minStrength;
  }

  /**
   * Check if signal passes risk filters
   */
  private passesRiskFilter(signal: StandardSignal): boolean {
    // Reset high risk counter if timeout passed
    if (Date.now() - this.lastRiskReset > this.config.riskTimeout) {
      this.highRiskSignalCount = 0;
      this.lastRiskReset = Date.now();
    }

    // Check high risk signal limit
    if (signal.riskLevel === 'high') {
      if (this.highRiskSignalCount >= this.config.maxHighRiskSignals) {
        return false;
      }
      this.highRiskSignalCount++;
    }

    return true;
  }

  /**
   * Start signal processing
   */
  private startProcessing(): void {
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
    }

    // Process signals every second
    this.processingInterval = setInterval(() => {
      this.processSignalQueue();
    }, 1000);

    logger.info('ðŸ”„ Signal processing started');
  }

  /**
   * Process queued signals
   */
  private async processSignalQueue(): Promise<void> {
    if (this.signalQueue.length === 0 || !this.tradingEngine) {
      return;
    }

    const startTime = Date.now();
    const batchSize = Math.min(this.config.batchSize, this.signalQueue.length);
    const batch = this.signalQueue.splice(0, batchSize);

    try {
      logger.debug('âš¡ Processing signal batch', { size: batch.length });

      for (const queuedSignal of batch) {
        await this.processSignal(queuedSignal.signal);
      }

      this.stats.processingTimeMs = Date.now() - startTime;
      this.stats.lastProcessed = Date.now();

      this.emit('batch-processed', {
        processed: batch.length,
        remaining: this.signalQueue.length,
        processingTime: this.stats.processingTimeMs
      });

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Signal batch processing failed', { error: errorMessage });
      
      this.emit('processing-error', { error: errorMessage, batchSize });
    }
  }

  /**
   * Process individual signal
   */
  private async processSignal(signal: StandardSignal): Promise<void> {
    try {
      // Check if signal has expired
      if (signal.validUntil && signal.validUntil <= Date.now()) {
        logger.debug('â° Signal expired, skipping', { id: signal.id });
        return;
      }

      // Validate signal before processing
      const validation = this.signalTranslator.validateSignal(signal);
      if (!validation.valid) {
        logger.warn('âš ï¸ Invalid signal skipped', { 
          id: signal.id, 
          errors: validation.errors 
        });
        this.stats.failed++;
        return;
      }

      logger.info('ðŸŽ¯ Processing signal', {
        id: signal.id,
        source: signal.source,
        action: signal.action,
        confidence: signal.confidence,
        symbol: signal.symbol
      });

      // Send to trading engine
      if (this.tradingEngine) {
        await this.tradingEngine.processAdvancedSignals([signal]);
        logger.debug('âœ… Signal sent to trading engine', { id: signal.id });
      }

      // Update statistics
      this.updateStats(signal);
      this.stats.successful++;

      this.emit('signal-processed', { signal });

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to process signal', { 
        id: signal.id, 
        error: errorMessage 
      });
      
      this.stats.failed++;
      this.emit('signal-error', { signal, error: errorMessage });
    }
  }

  /**
   * Update processing statistics
   */
  private updateStats(signal: StandardSignal): void {
    this.stats.totalProcessed++;

    // Update action counts
    switch (signal.action) {
      case 'buy':
        this.stats.buySignals++;
        break;
      case 'sell':
        this.stats.sellSignals++;
        break;
      case 'hold':
        this.stats.holdSignals++;
        break;
      case 'close':
        this.stats.closeSignals++;
        break;
    }

    // Update averages
    const total = this.stats.successful + 1;
    this.stats.averageConfidence = 
      (this.stats.averageConfidence * this.stats.successful + signal.confidence) / total;
    this.stats.averageStrength = 
      (this.stats.averageStrength * this.stats.successful + signal.strength) / total;
  }

  /**
   * Get current statistics
   */
  getStats(): SignalProcessingStats {
    return { ...this.stats };
  }

  /**
   * Get queue status
   */
  getQueueStatus(): {
    size: number;
    oldestSignalAge: number;
    priorityDistribution: Record<number, number>;
  } {
    const priorityDistribution: Record<number, number> = {};
    let oldestSignalAge = 0;

    for (const queuedSignal of this.signalQueue) {
      priorityDistribution[queuedSignal.priority] = 
        (priorityDistribution[queuedSignal.priority] || 0) + 1;
      
      const age = Date.now() - queuedSignal.addedAt;
      if (age > oldestSignalAge) {
        oldestSignalAge = age;
      }
    }

    return {
      size: this.signalQueue.length,
      oldestSignalAge,
      priorityDistribution
    };
  }

  /**
   * Manually add signal to processing queue
   */
  addSignal(signal: StandardSignal, priority = 1): void {
    logger.info('âž• Manually adding signal to queue', { 
      id: signal.id, 
      source: signal.source,
      priority 
    });
    
    this.queueSignal(signal, priority);
  }

  /**
   * Update configuration
   */
  updateConfig(newConfig: Partial<SignalProcessingConfig>): void {
    this.config = { ...this.config, ...newConfig };
    
    logger.info('âš™ï¸ Signal processing configuration updated', { 
      config: this.config 
    });
    
    this.emit('config-updated', this.config);
  }

  /**
   * Clear signal queue
   */
  clearQueue(): void {
    const clearedCount = this.signalQueue.length;
    this.signalQueue = [];
    
    logger.info('ðŸ—‘ï¸ Signal queue cleared', { clearedCount });
    this.emit('queue-cleared', { clearedCount });
  }

  /**
   * Stop signal processing
   */
  stop(): void {
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
      this.processingInterval = undefined;
    }

    this.clearQueue();
    
    logger.info('â¹ï¸ Signal Processing Manager stopped');
    this.emit('stopped');
  }

  /**
   * Get health status
   */
  getHealthStatus(): {
    healthy: boolean;
    issues: string[];
    uptime: number;
    lastActivity: number;
  } {
    const issues: string[] = [];
    const now = Date.now();

    // Check if we have any signal sources
    if (!this.aetherGenerator && !this.smaProcessor && !this.targetReacher) {
      issues.push('No signal sources connected');
    }

    // Check if trading engine is connected
    if (!this.tradingEngine) {
      issues.push('Trading engine not connected');
    }

    // Check if processing is stuck
    if (this.stats.lastProcessed > 0 && now - this.stats.lastProcessed > 60000) {
      issues.push('No signals processed in last minute');
    }

    // Check queue size
    if (this.signalQueue.length > this.config.batchSize * 5) {
      issues.push('Signal queue growing too large');
    }

    return {
      healthy: issues.length === 0,
      issues,
      uptime: now - this.lastRiskReset,
      lastActivity: this.stats.lastProcessed
    };
  }
}

/**
 * Default signal processing manager instance
 */
export const signalProcessingManager = new SignalProcessingManager();

/**
 * Signal Translator Module
 * 
 * Translates signals from various sources (Aether Signal Generator, Target Reacher, SMA) 
 * into a standardized format for the trading engine.
 */

import logger from '../../services/logging.service';
import type { SignalOutput as AetherSignalOutput} from '../signals/aether/models';
import { AetherParameters, MarketRegime } from '../signals/aether/models';
import type { SMASignalOutput } from '../signals/sma/models';
import type { StrategyResult } from '../target-reacher/interfaces';

/**
 * Standard Signal Format for the Trading Engine
 */
export interface StandardSignal {
  // Core signal properties
  id: string;
  source: SignalSource;
  action: 'buy' | 'sell' | 'hold' | 'close';
  confidence: number; // 0-1
  strength: number; // 0-1
  
  // Market context
  symbol: string;
  price?: number;
  timestamp: number;
  
  // Signal metadata
  reason: string;
  metadata: Record<string, unknown>;
  
  // Risk and execution hints
  riskLevel: 'low' | 'medium' | 'high';
  urgency: 'low' | 'medium' | 'high';
  validUntil?: number; // Timestamp when signal expires
}

/**
 * Supported signal sources
 */
export enum SignalSource {
  AETHER = 'aether',
  SMA = 'sma',
  TARGET_REACHER = 'target-reacher',
  STRATEGY = 'strategy',
  MANUAL = 'manual'
}

/**
 * Signal translation result
 */
export interface SignalTranslationResult {
  success: boolean;
  signal?: StandardSignal;
  error?: string;
  warnings?: string[];
}

/**
 * Signal Translator Class
 * Converts various signal formats into standardized signals
 */
export class SignalTranslator {
  private signalCounter = 0;

  /**
   * Generate unique signal ID
   */
  private generateSignalId(source: SignalSource): string {
    this.signalCounter++;
    return `${source}_${Date.now()}_${this.signalCounter}`;
  }

  /**
   * Translate Aether Signal Generator output to standard format
   */
  translateAetherSignal(
    aetherSignal: AetherSignalOutput,
    symbol: string,
    price?: number
  ): SignalTranslationResult {
    try {
      logger.info('ðŸ”„ Translating Aether signal to standard format', {
        value: aetherSignal.value,
        confidence: aetherSignal.confidence,
        regime: aetherSignal.regime
      });

      // Determine action based on signal value
      let action: 'buy' | 'sell' | 'hold';
      if (aetherSignal.value > 0.2) {
        action = 'buy';
      } else if (aetherSignal.value < -0.2) {
        action = 'sell';
      } else {
        action = 'hold';
      }

      // Calculate signal strength from absolute value
      const strength = Math.abs(aetherSignal.value);

      // Determine risk level based on market regime and confidence
      let riskLevel: 'low' | 'medium' | 'high';
      if (aetherSignal.regime === MarketRegime.VOLATILE || aetherSignal.confidence < 0.4) {
        riskLevel = 'high';
      } else if (aetherSignal.confidence > 0.7) {
        riskLevel = 'low';
      } else {
        riskLevel = 'medium';
      }

      // Determine urgency based on signal strength and regime
      let urgency: 'low' | 'medium' | 'high';
      if (strength > 0.7 && aetherSignal.regime !== MarketRegime.SIDEWAYS) {
        urgency = 'high';
      } else if (strength > 0.4) {
        urgency = 'medium';
      } else {
        urgency = 'low';
      }

      const standardSignal: StandardSignal = {
        id: this.generateSignalId(SignalSource.AETHER),
        source: SignalSource.AETHER,
        action,
        confidence: aetherSignal.confidence,
        strength,
        symbol,
        price,
        timestamp: aetherSignal.timestamp,
        reason: `Aether signal: ${action} with ${Math.round(aetherSignal.confidence * 100)}% confidence in ${aetherSignal.regime} market`,
        metadata: {
          originalValue: aetherSignal.value,
          regime: aetherSignal.regime,
          components: aetherSignal.components
        },
        riskLevel,
        urgency,
        validUntil: aetherSignal.timestamp + (5 * 60 * 1000) // Valid for 5 minutes
      };

      return {
        success: true,
        signal: standardSignal
      };

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to translate Aether signal', { error: errorMessage });
      
      return {
        success: false,
        error: `Aether signal translation failed: ${errorMessage}`
      };
    }
  }

  /**
   * Translate SMA signal to standard format
   */
  translateSMASignal(
    smaSignal: SMASignalOutput,
    symbol: string,
    price?: number
  ): SignalTranslationResult {
    try {
      logger.info('ðŸ”„ Translating SMA signal to standard format', {
        signal: smaSignal.signal,
        confidence: smaSignal.confidence,
        reason: smaSignal.reason
      });

      // Determine action from SMA signal value
      let action: 'buy' | 'sell' | 'hold';
      if (smaSignal.signal > 0.5) {
        action = 'buy';
      } else if (smaSignal.signal < -0.5) {
        action = 'sell';
      } else {
        action = 'hold';
      }

      // Use SMA strength directly
      const strength = Math.abs(smaSignal.strength);

      // Determine risk level based on confidence and signal strength
      let riskLevel: 'low' | 'medium' | 'high';
      if (smaSignal.confidence > 0.7 && strength > 0.6) {
        riskLevel = 'low';
      } else if (smaSignal.confidence < 0.4 || strength < 0.3) {
        riskLevel = 'high';
      } else {
        riskLevel = 'medium';
      }

      // Determine urgency based on signal strength
      let urgency: 'low' | 'medium' | 'high';
      if (strength > 0.8) {
        urgency = 'high';
      } else if (strength > 0.5) {
        urgency = 'medium';
      } else {
        urgency = 'low';
      }

      const standardSignal: StandardSignal = {
        id: this.generateSignalId(SignalSource.SMA),
        source: SignalSource.SMA,
        action,
        confidence: smaSignal.confidence,
        strength,
        symbol,
        price: price || smaSignal.lastPrice,
        timestamp: smaSignal.timestamp,
        reason: smaSignal.reason,
        metadata: {
          originalSignal: smaSignal.signal,
          fastMA: smaSignal.fastMA,
          slowMA: smaSignal.slowMA,
          lastPrice: smaSignal.lastPrice,
          ...(smaSignal.metadata || {})
        },
        riskLevel,
        urgency,
        validUntil: smaSignal.timestamp + (3 * 60 * 1000) // Valid for 3 minutes
      };

      return {
        success: true,
        signal: standardSignal
      };

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to translate SMA signal', { error: errorMessage });
      
      return {
        success: false,
        error: `SMA signal translation failed: ${errorMessage}`
      };
    }
  }

  /**
   * Translate Target Reacher strategy result to standard format
   */
  translateStrategyResult(
    strategyResult: StrategyResult,
    symbol: string,
    source: SignalSource = SignalSource.TARGET_REACHER,
    price?: number
  ): SignalTranslationResult {
    try {
      logger.info('ðŸ”„ Translating strategy result to standard format', {
        action: strategyResult.action,
        confidence: strategyResult.confidence,
        success: strategyResult.success
      });

      if (!strategyResult.success) {
        return {
          success: false,
          error: `Strategy execution failed: ${strategyResult.error || 'Unknown error'}`
        };
      }

      if (!strategyResult.action || strategyResult.action === 'hold') {
        // For hold signals, create a low-urgency hold signal
        const standardSignal: StandardSignal = {
          id: this.generateSignalId(source),
          source,
          action: 'hold',
          confidence: strategyResult.confidence || 0.5,
          strength: 0.1,
          symbol,
          price,
          timestamp: Date.now(),
          reason: strategyResult.reason || 'Strategy recommends holding position',
          metadata: strategyResult.metadata || {},
          riskLevel: 'low',
          urgency: 'low'
        };

        return {
          success: true,
          signal: standardSignal
        };
      }

      const confidence = strategyResult.confidence || 0.5;
      const strength = confidence; // Use confidence as strength for strategies

      // Determine risk level based on confidence
      let riskLevel: 'low' | 'medium' | 'high';
      if (confidence > 0.8) {
        riskLevel = 'low';
      } else if (confidence < 0.4) {
        riskLevel = 'high';
      } else {
        riskLevel = 'medium';
      }

      // Determine urgency based on confidence and action
      let urgency: 'low' | 'medium' | 'high';
      if (strategyResult.action === 'close' || confidence > 0.8) {
        urgency = 'high';
      } else if (confidence > 0.6) {
        urgency = 'medium';
      } else {
        urgency = 'low';
      }

      const standardSignal: StandardSignal = {
        id: this.generateSignalId(source),
        source,
        action: strategyResult.action,
        confidence,
        strength,
        symbol,
        price,
        timestamp: Date.now(),
        reason: strategyResult.reason || `Strategy signal: ${strategyResult.action}`,
        metadata: strategyResult.metadata || {},
        riskLevel,
        urgency,
        validUntil: Date.now() + (2 * 60 * 1000) // Valid for 2 minutes
      };

      return {
        success: true,
        signal: standardSignal
      };

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('âŒ Failed to translate strategy result', { error: errorMessage });
      
      return {
        success: false,
        error: `Strategy result translation failed: ${errorMessage}`
      };
    }
  }

  /**
   * Validate and sanitize a standard signal
   */
  validateSignal(signal: StandardSignal): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    // Check required fields
    if (!signal.id) {errors.push('Signal ID is required');}
    if (!signal.source) {errors.push('Signal source is required');}
    if (!signal.action) {errors.push('Signal action is required');}
    if (!signal.symbol) {errors.push('Symbol is required');}
    if (!signal.timestamp) {errors.push('Timestamp is required');}

    // Validate ranges
    if (signal.confidence < 0 || signal.confidence > 1) {
      errors.push('Confidence must be between 0 and 1');
    }
    if (signal.strength < 0 || signal.strength > 1) {
      errors.push('Strength must be between 0 and 1');
    }

    // Validate enums
    const validActions = ['buy', 'sell', 'hold', 'close'];
    if (!validActions.includes(signal.action)) {
      errors.push(`Action must be one of: ${validActions.join(', ')}`);
    }

    const validRiskLevels = ['low', 'medium', 'high'];
    if (!validRiskLevels.includes(signal.riskLevel)) {
      errors.push(`Risk level must be one of: ${validRiskLevels.join(', ')}`);
    }

    const validUrgencyLevels = ['low', 'medium', 'high'];
    if (!validUrgencyLevels.includes(signal.urgency)) {
      errors.push(`Urgency must be one of: ${validUrgencyLevels.join(', ')}`);
    }

    // Check expiration
    if (signal.validUntil && signal.validUntil <= Date.now()) {
      errors.push('Signal has expired');
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }

  /**
   * Batch translate multiple signals
   */
  async translateBatch(
    signals: Array<{
      type: 'aether' | 'sma' | 'strategy';
      data: AetherSignalOutput | SMASignalOutput | StrategyResult;
      symbol: string;
      price?: number;
      source?: SignalSource;
    }>
  ): Promise<{
    successful: StandardSignal[];
    failed: Array<{ error: string; originalData: any }>;
  }> {
    const successful: StandardSignal[] = [];
    const failed: Array<{ error: string; originalData: any }> = [];

    for (const signalInput of signals) {
      try {
        let result: SignalTranslationResult;

        switch (signalInput.type) {
          case 'aether':
            result = this.translateAetherSignal(
              signalInput.data as AetherSignalOutput,
              signalInput.symbol,
              signalInput.price
            );
            break;
          
          case 'sma':
            result = this.translateSMASignal(
              signalInput.data as SMASignalOutput,
              signalInput.symbol,
              signalInput.price
            );
            break;
          
          case 'strategy':
            result = this.translateStrategyResult(
              signalInput.data as StrategyResult,
              signalInput.symbol,
              signalInput.source || SignalSource.STRATEGY,
              signalInput.price
            );
            break;
          
          default:
            failed.push({
              error: `Unsupported signal type: ${signalInput.type}`,
              originalData: signalInput.data
            });
            continue;
        }

        if (result.success && result.signal) {
          const validation = this.validateSignal(result.signal);
          if (validation.valid) {
            successful.push(result.signal);
          } else {
            failed.push({
              error: `Signal validation failed: ${validation.errors.join(', ')}`,
              originalData: signalInput.data
            });
          }
        } else {
          failed.push({
            error: result.error || 'Translation failed',
            originalData: signalInput.data
          });
        }

      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : String(error);
        failed.push({
          error: `Batch translation error: ${errorMessage}`,
          originalData: signalInput.data
        });
      }
    }

    logger.info('ðŸ“Š Signal batch translation completed', {
      successful: successful.length,
      failed: failed.length,
      total: signals.length
    });

    return { successful, failed };
  }
}

/**
 * Default signal translator instance
 */
export const signalTranslator = new SignalTranslator();

/**
 * Aether Signal Generator - Core Implementation
 *
 * Advanced trading signal generator based on:
 * - Fractional Partial Differential Equations
 * - Reflected Backward Stochastic Differential Equations
 * - Mean-Field Game Theory
