 */

import {
  calculateSMA,
  calculateEMA,
  calculateMACD,
  calculateRSI,
  getMASignals
} from '../../indicators';

import type { AetherParameters, MarketData, SignalOutput, SignalComponents } from "./models"
import { MarketRegime } from "./models"
import { DEFAULT_AETHER_PARAMETERS } from "./parameters"
import {
