export class AetherSignalGenerator {
  private parameters: AetherParameters
  private previousSignal = 0
  private marketHistory: MarketData[] = []

  constructor(parameters: AetherParameters = DEFAULT_AETHER_PARAMETERS) {
    this.parameters = parameters
  }

  /**
   * Calculate trading signal based on market data
   */
  calculateSignal(orderBookImbalance: number, volatility: number, crowdingScore: number, priceHistory: number[]): SignalOutput {
    // Input validation
    if (!Number.isFinite(orderBookImbalance) || !Number.isFinite(volatility) || !Number.isFinite(crowdingScore)) {
      throw new Error('Invalid market data: orderBookImbalance, volatility, and crowdingScore must be finite numbers');
    }
    
    if (!Array.isArray(priceHistory) || priceHistory.length === 0) {
      throw new Error('Invalid price history: must be a non-empty array');
    }
    
    if (!priceHistory.every(price => Number.isFinite(price))) {
      throw new Error('Invalid price history: all prices must be finite numbers');
    }
    
    const marketData: MarketData = {
      orderBookImbalance,
      volatility,
      crowdingScore,
      timestamp: Date.now(),
    }

    // logger.debug('Aether signal input data analysis', LogCategory.TRADING, {
    //   orderBookImbalance,
    //   volatility,
    //   crowdingScore,
    //   previousSignal: this.previousSignal
    // })

    // Add to market history
    this.marketHistory.push(marketData)
    if (this.marketHistory.length > 100) {
      this.marketHistory.shift()
    }

    // Calculate signal components
    const components = this.calculateSignalComponents(marketData, priceHistory)

    // logger.debug('Aether signal component analysis', LogCategory.TRADING, {
    //   fractionalPDE: components.fractionalPDE.toFixed(4),
    //   reflectedBSDE: components.reflectedBSDE.toFixed(4),
    //   meanFieldGame: components.meanFieldGame.toFixed(4),
    //   malliavinDerivative: components.malliavinDerivative.toFixed(4)
    // })

    // Combine components into final signal
    const rawSignal = this.combineSignalComponents(components)

    // Apply smoothing
    const smoothedSignal = exponentialSmoothing(rawSignal, this.previousSignal, 0.3)
    this.previousSignal = smoothedSignal

    // Determine market regime
    const regime = this.determineMarketRegime(marketData, smoothedSignal)

    // Calculate confidence
    const confidence = this.calculateConfidence(components, volatility)

    // const signalDirection = smoothedSignal > 0.6 ? 'STRONG BUY' : 
    //                        smoothedSignal > 0.1 ? 'WEAK BUY' : 
    //                        smoothedSignal < -0.6 ? 'STRONG SELL' : 
    //                        smoothedSignal < -0.1 ? 'WEAK SELL' : 'NEUTRAL'

    // logger.debug('Aether signal final output', LogCategory.TRADING, {
    //   rawSignal: rawSignal.toFixed(4),
    //   smoothedSignal: smoothedSignal.toFixed(4),
    //   signalDirection,
    //   confidence: confidence.toFixed(4),
    //   regime
    // })

    return {
      value: smoothedSignal,
      confidence,
      regime,
      timestamp: Date.now(),
      components,
    }
  }

  /**
   * Calculate individual signal components (now includes canonical indicators)
   */
  private calculateSignalComponents(marketData: MarketData, priceHistory: number[]): SignalComponents & {
    sma: number[];
    ema: number[];
    macd: number[];
    rsi: number[];
    maSignals: number[];
  } {
    // Canonical indicators (require sufficient data)
    const sma = priceHistory.length >= 20 ? calculateSMA(priceHistory, 20) : [];
    const ema = priceHistory.length >= 20 ? calculateEMA(priceHistory, 20) : [];
    // TODO: Fix MACD calculation indexing issue - temporarily disabled
    const macdResult = { macd: [], signal: [], histogram: [] };
    const rsi = priceHistory.length >= 14 ? calculateRSI(priceHistory, 14) : [];
    const maSignals = (sma.length && ema.length && sma.length === ema.length) ? getMASignals(ema, sma) : [];
    // Print indicator values for debugging
    console.log('Aether Indicators:', {
      sma: sma.slice(-3),
      ema: ema.slice(-3),
      macd: macdResult.macd.slice(-3),
      rsi: rsi.slice(-3),
      maSignals: maSignals.slice(-3)
    });
    // Original components
    const fractionalPDE = this.calculateFractionalPDE(marketData)
    const reflectedBSDE = this.calculateReflectedBSDE(marketData)
    const meanFieldGame = this.calculateMeanFieldGame(marketData)
    const malliavinDerivative = this.calculateMalliavinDerivative(marketData)
    return {
      fractionalPDE,
      reflectedBSDE,
      meanFieldGame,
      malliavinDerivative,
      sma,
      ema,
      macd: macdResult.macd,
      rsi,
      maSignals
    }
  }

  /**
   * Fractional PDE calculation
   */
  private calculateFractionalPDE(marketData: MarketData): number {
    const { hurstExponent, fractionalOrder, diffusionCoefficient } = this.parameters

    // Simulate fractional Brownian motion
    const fbm = fractionalBrownianMotion(hurstExponent, 10)
    const lastFbm = fbm.at(-1) ?? 0

    // Apply fractional differential operator
    const fractionalDiff = Math.pow(Math.abs(marketData.orderBookImbalance), fractionalOrder)

    // Combine with diffusion
    const signal = diffusionCoefficient * fractionalDiff * sigmoidNormalized(lastFbm)

    return tanhNormalized(signal)
  }

  /**
   * Reflected BSDE calculation
   */
  private calculateReflectedBSDE(marketData: MarketData): number {
    const { reflectionBarrier, driftCoefficient, volatilityScaling } = this.parameters

    // Calculate drift term
    const drift = driftCoefficient * marketData.orderBookImbalance

    // PATCH: Use deterministic random for tests
    const volatilityTerm = volatilityScaling * marketData.volatility * getAetherRandom()

    // Combine and apply reflection
    const rawValue = drift + volatilityTerm
    const reflected = reflectedBarrier(rawValue, -reflectionBarrier)

    return tanhNormalized(reflected)
  }

  /**
   * Mean-Field Game calculation
   */
  private calculateMeanFieldGame(marketData: MarketData): number {
    const { playerInteraction, socialOptimum, nashEquilibrium } = this.parameters

    // Calculate average market action (crowding score as proxy)
    const averageAction = marketData.crowdingScore

    // Calculate player action based on order book imbalance
    const playerAction = marketData.orderBookImbalance

    // Apply mean-field interaction
    const interaction = meanFieldInteraction(playerAction, averageAction, playerInteraction)

    // Balance between social optimum and Nash equilibrium
    const signal = socialOptimum * interaction + nashEquilibrium * (1 - interaction)

    return tanhNormalized(signal)
  }

  /**
   * Malliavin derivative calculation
   */
  private calculateMalliavinDerivative(marketData: MarketData): number {
    const { malliavinSensitivity, hedgingRatio, riskAversion } = this.parameters

    // Calculate Malliavin derivative
    const derivative = malliavinDerivative(marketData.orderBookImbalance, malliavinSensitivity, marketData.volatility)

    // Apply hedging ratio and risk aversion
    const hedgedSignal = (hedgingRatio * derivative) / riskAversion

    return tanhNormalized(hedgedSignal)
  }

  /**
   * Combine signal components
   */
  private combineSignalComponents(components: SignalComponents): number {
    const weights = {
      fractionalPDE: 0.3,
      reflectedBSDE: 0.25,
      meanFieldGame: 0.25,
      malliavinDerivative: 0.2,
    }

    return (
      weights.fractionalPDE * components.fractionalPDE +
      weights.reflectedBSDE * components.reflectedBSDE +
      weights.meanFieldGame * components.meanFieldGame +
      weights.malliavinDerivative * components.malliavinDerivative
    )
  }

  /**
   * Determine market regime
   */
  private determineMarketRegime(marketData: MarketData, signal: number): MarketRegime {
    const { volatility, orderBookImbalance } = marketData

    if (volatility > 0.03) {
      return MarketRegime.VOLATILE
    } else if (volatility < 0.01) {
      return MarketRegime.CALM
    } else if (signal > 0.3 && orderBookImbalance > 0.1) {
      return MarketRegime.BULLISH
    } else if (signal < -0.3 && orderBookImbalance < -0.1) {
      return MarketRegime.BEARISH
    } 
      return MarketRegime.SIDEWAYS
    
  }

  /**
   * Calculate signal confidence
   */
  private calculateConfidence(components: SignalComponents, volatility: number): number {
    // Calculate component agreement
    const componentValues = Object.values(components).filter(val => Number.isFinite(val))
    
    if (componentValues.length === 0) {
      return 0.1; // Minimum confidence if no valid components
    }
    
    const mean = componentValues.reduce((sum, val) => sum + val, 0) / componentValues.length
    const variance = componentValues.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / componentValues.length

    // Lower variance = higher confidence
    const agreementConfidence = Number.isFinite(variance) ? 1 / (1 + variance) : 0.1

    // Lower volatility = higher confidence
    const volatilityConfidence = Number.isFinite(volatility) ? 1 / (1 + volatility * 10) : 0.1

    // Combine confidences
    const rawConfidence = (agreementConfidence + volatilityConfidence) / 2

    return Math.max(0.1, Math.min(1.0, Number.isFinite(rawConfidence) ? rawConfidence : 0.1))
  }

  /**
   * Update parameters
   */
  updateParameters(newParameters: Partial<AetherParameters>): void {
    this.parameters = { ...this.parameters, ...newParameters }
  }

  /**
   * Get current parameters
   */
  getParameters(): AetherParameters {
    return { ...this.parameters }
  }

  /**
   * Reset signal history
   */
  reset(): void {
    this.previousSignal = 0
    this.marketHistory = []
  }
}

/**
 * Aether Signal Generator - Utility Functions
 */

/**
 * Sigmoid function
 */
export function sigmoid(x: number): number {
  return 1 / (1 + Math.exp(-x))
}

/**
 * Normalized sigmoid function (range: -1 to 1)
 */
export function sigmoidNormalized(x: number): number {
  return 2 * sigmoid(x) - 1
}

/**
 * Normalized tanh function (range: -1 to 1)
 */
export function tanhNormalized(x: number): number {
  return Math.tanh(x)
}

/**
 * Exponential smoothing
 */
export function exponentialSmoothing(current: number, previous: number, alpha: number): number {
  return alpha * current + (1 - alpha) * previous
}

// --- TESTING HOOK: Deterministic mode for tests ---
let _aetherTestDeterministic = false;
let _aetherTestRandomValue = 0.0;
export function __setAetherDeterministicMode(enabled: boolean, randomValue = 0.0) {
  _aetherTestDeterministic = enabled;
  _aetherTestRandomValue = randomValue;
}

/**
 * Fractional Brownian motion simulation
 */
export function fractionalBrownianMotion(hurstExponent: number, steps: number): number[] {
  const result: number[] = [0]
  let value = 0
  for (let i = 1; i < steps; i++) {
    // Simple approximation of fBm
    const randomIncrement = ((_aetherTestDeterministic ? _aetherTestRandomValue : Math.random()) * 2 - 1) * Math.pow(1 / steps, hurstExponent)
    value += randomIncrement
    result.push(value)
  }
  return result
}

/**
 * Malliavin derivative approximation
 */
export function malliavinDerivative(orderBookImbalance: number, sensitivity: number, volatility: number): number {
  // Simple approximation of Malliavin derivative
  return sensitivity * orderBookImbalance * (1 + volatility)
}

/**
 * Mean-field interaction
 */
export function meanFieldInteraction(playerAction: number, averageAction: number, interactionStrength: number): number {
  // Simple mean-field interaction model
  return interactionStrength * playerAction + (1 - interactionStrength) * averageAction
}

/**
 * Reflected barrier
 */
export function reflectedBarrier(value: number, barrier: number): number {
  if (value < barrier) {
    return barrier + (barrier - value)
  }
  return value
}

// Patch for deterministic volatility term in ReflectedBSDE
export function getAetherRandom() {
  return _aetherTestDeterministic ? _aetherTestRandomValue : Math.random();
}

/**
 * SMA Signal Processor with Fixes
 * 
 * This is an improved version of the SMA Signal Processor that fixes issues
 * with signal generation and confidence calculation.
 */

import { calculateSMA, calculateEMA, getMASignals } from '../../indicators/moving-averages';
import type { Candle, TradeSignal } from '../../target-reacher/interfaces';

import type { SMASignalConfig, SMASignalOutput } from './models';

export class ImprovedSMASignalProcessor {
  private config: SMASignalConfig;
  
  constructor(config?: Partial<SMASignalConfig>) {
    // Default configuration
    this.config = {
      fastPeriod: 9,
      slowPeriod: 21,
      minChangePercent: 0.5,
      confidenceThreshold: 0.4, // Lower threshold to generate more signals
      priceSource: 'close',
      signalMode: 'crossover', 
      useEMA: false,
      ...config
    };
    
    this.validateConfig();
  }
  
  /**
   * Validate configuration parameters
   */
  private validateConfig() {
    const { fastPeriod, slowPeriod } = this.config;
    
    if (fastPeriod >= slowPeriod) {
      throw new Error('Fast period must be less than slow period');
    }
    
    if (fastPeriod < 2) {
      throw new Error('Fast period must be at least 2');
    }
    
    if (slowPeriod < 3) {
      throw new Error('Slow period must be at least 3');
    }
  }
  
  /**
   * Update configuration
   */
  updateConfig(newConfig: Partial<SMASignalConfig>): void {
    this.config = {
      ...this.config,
      ...newConfig
    };
    
    this.validateConfig();
  }
  
  /**
   * Get current configuration
   */
  getConfig(): SMASignalConfig {
    return { ...this.config };
  }
  
  /**
   * Process candle data and generate SMA signals
   * 
   * @param candles Array of candle data
   * @returns Signal output or null if no signal
   */
  process(candles: Candle[]): SMASignalOutput | null {
    if (candles.length < this.config.slowPeriod + 1) {
      console.warn(`Insufficient data for SMA signal processing. Need at least ${this.config.slowPeriod + 1} candles.`);
      return null;
    }
    
    // Extract price data from candles based on configured source
    const prices = candles.map(candle => candle[this.config.priceSource]);
    
    // Calculate moving averages
    const calculateMA = this.config.useEMA ? calculateEMA : calculateSMA;
    let fastMA: number[];
    let slowMA: number[];
    
    try {
      fastMA = calculateMA(prices, this.config.fastPeriod);
      slowMA = calculateMA(prices, this.config.slowPeriod);
    } catch (error) {
      console.error('Error calculating moving averages:', error);
      return null;
    }
    
    // Ensure we have enough data points after calculation
    if (fastMA.length === 0 || slowMA.length === 0) {
      return null;
    }
    
    // Get latest values
    const lastPrice = prices[prices.length - 1];
    const lastFastMA = fastMA[fastMA.length - 1];
    const lastSlowMA = slowMA[slowMA.length - 1];
    
    // Generate signal based on configuration
    const lastCandle = candles[candles.length - 1];
    const timestamp = lastCandle ? lastCandle.timestamp : Date.now();
    
    // Adjust arrays to same length for signal generation
    // Fast MA will have more values than slow MA due to different periods
    const adjustedFastMA = fastMA.slice(fastMA.length - slowMA.length);
    
    return this.generateSignal(prices, adjustedFastMA, slowMA, timestamp);
  }
  
  /**
   * Generate trading signal based on MA values - FIXED VERSION
   */
  private generateSignal(prices: number[], fastMA: number[], slowMA: number[], timestamp: number): SMASignalOutput | null {
    // Ensure arrays are not empty and have data
    if (!prices.length || !fastMA.length || !slowMA.length) {
      return null;
    }
    
    // Ensure arrays are the same length
    if (fastMA.length !== slowMA.length || !fastMA.length) {
      console.warn('Moving average arrays must be of the same length for signal generation');
      return null;
    }
    
    // Get crossover signals 
    const crossoverSignals = getMASignals(fastMA, slowMA);
    
    // Last values with null checks
    const lastPrice = prices[prices.length - 1];
    const lastFastMA = fastMA[fastMA.length - 1];
    const lastSlowMA = slowMA[slowMA.length - 1];
    const lastCrossoverSignal = crossoverSignals[crossoverSignals.length - 1] || 0;
    
    if (lastPrice === undefined || lastFastMA === undefined || lastSlowMA === undefined) {
      return null;
    }
    
    let signal = 0;
    let reason = 'No signal detected';
    let confidence = 0;
    
    // Calculate strength based on price distance from MA
    const priceDistance = Math.abs((lastPrice - lastSlowMA) / lastSlowMA) * 100;
    const strength = Math.min(priceDistance / 3, 1); // Normalize to 0-1, more sensitive
    
    // FIX: More sensitive confidence calculation
    // Calculate confidence based on the distance between MAs as percentage of price
    const maDistance = Math.abs(lastFastMA - lastSlowMA) / lastPrice * 100;
    confidence = Math.min(maDistance * 5, 1); // Scale up to get more reasonable values
    
    // Determine signal based on mode
    if (this.config.signalMode === 'crossover' || this.config.signalMode === 'combined') {
      // Check if we have a crossover signal
      if (lastCrossoverSignal !== 0) {
        signal = lastCrossoverSignal;
        
        if (signal > 0) {
          reason = `Bullish crossover: Fast MA (${lastFastMA.toFixed(2)}) crossed above Slow MA (${lastSlowMA.toFixed(2)})`;
        } else {
          reason = `Bearish crossover: Fast MA (${lastFastMA.toFixed(2)}) crossed below Slow MA (${lastSlowMA.toFixed(2)})`;
        }
      }
    }
    
    // Check trend if mode is trend or combined
    if ((this.config.signalMode === 'trend' || this.config.signalMode === 'combined') && signal === 0) {
      // Check the slope of fast MA (using last 3 points)
      const fastMASlope = fastMA.length >= 3 ? 
        ((fastMA[fastMA.length - 1] || 0) - (fastMA[fastMA.length - 3] || 0)) / 2 : 0;
      
      // Price is above both MAs and fast MA is rising = strong bullish
      if (lastPrice > lastFastMA && lastPrice > lastSlowMA && fastMASlope > 0) {
        signal = 1;
        reason = `Strong bullish trend: Price (${lastPrice.toFixed(2)}) above both MAs with rising Fast MA`;
        confidence = Math.min(0.8, strength); // Cap at 0.8 since it's not a crossover
      } 
      // Price is below both MAs and fast MA is falling = strong bearish
      else if (lastPrice < lastFastMA && lastPrice < lastSlowMA && fastMASlope < 0) {
        signal = -1;
        reason = `Strong bearish trend: Price (${lastPrice.toFixed(2)}) below both MAs with falling Fast MA`;
        confidence = Math.min(0.8, strength); // Cap at 0.8 since it's not a crossover
      }
    }
    
    // Return null if confidence threshold not met
    const threshold = this.config.confidenceThreshold || 0.6;
    if (confidence < threshold) {
      return null;
    }
    
    // Return signal output
    return {
      signal,
      confidence,
      lastPrice,
      fastMA: lastFastMA,
      slowMA: lastSlowMA,
      reason,
      strength,
      timestamp,
      metadata: {
        fastPeriod: this.config.fastPeriod,
        slowPeriod: this.config.slowPeriod,
        useEMA: this.config.useEMA
      }
    };
  }
  
  /**
   * Create a trade signal from the SMA signal output
   */
  createTradeSignal(output: SMASignalOutput, botId: string, symbol: string): TradeSignal {
    return {
      id: `sma-${Date.now()}-${Math.floor(Math.random() * 1000)}`,
      botId,
      symbol,
      side: output.signal > 0 ? 'buy' : 'sell',
      confidence: output.confidence,
      price: output.lastPrice,
      timestamp: output.timestamp,
      reason: output.reason
    };
  }
  
  /**
   * Get default SMA configuration
   */
  static getDefaultConfig(): SMASignalConfig {
    return {
      fastPeriod: 9,
      slowPeriod: 21,
      minChangePercent: 0.5,
      confidenceThreshold: 0.4, // Lower default threshold
      priceSource: 'close',
      signalMode: 'crossover',
      useEMA: false
    };
  }
}

/**
 * SMA Crossover Strategy
 * 
 * Implements a trading strategy based on SMA crossover signals.
 * This is a simple demonstration strategy that follows the IStrategy interface.
 */

import { z } from 'zod';

import type { 
  IStrategy, 
  StrategyConfig, 
  StrategyContext, 
  ConfigValidationResult,
  StrategyResult,
  StrategyState
} from '../../target-reacher/interfaces';
import type { SMASignalConfig } from '../sma/models';
import { SMASignalProcessor } from '../sma/sma-signal-processor';

// Configuration schema for validation
const SMACrossoverConfigSchema = z.object({
  fastPeriod: z.number().int().min(2).max(50).default(9),
  slowPeriod: z.number().int().min(3).max(200).default(21),
  priceSource: z.enum(['close', 'open', 'high', 'low']).default('close'),
  signalMode: z.enum(['crossover', 'trend', 'combined']).default('crossover'),
  useEMA: z.boolean().default(false),
  minChangePercent: z.number().min(0).max(100).default(0.5),
  confidenceThreshold: z.number().min(0).max(1).default(0.6)
});

export class SMACrossoverStrategy implements IStrategy {
  readonly name = 'SMA Crossover Strategy';
  readonly version = '1.0.0';
  readonly description = 'Trading strategy based on SMA crossover signals';
  readonly supportedMarkets = ['spot', 'futures'];

  private processor: SMASignalProcessor;
  private config: SMASignalConfig;
  private state: StrategyState = {
    isRunning: false,
    totalProfit: 0,
    tradesExecuted: 0,
    lastUpdate: new Date(),
    customState: {}
  };

  constructor() {
    // Initialize with default config (will be updated in initialize)
    this.config = SMASignalProcessor.getDefaultConfig();
    this.processor = new SMASignalProcessor(this.config);
  }

  /**
   * Initialize the strategy with context
   */
  async initialize(context: StrategyContext): Promise<void> {
    const { config } = context;
    
    // Update config from context
    this.config = {
      fastPeriod: config.parameters.fastPeriod as number,
      slowPeriod: config.parameters.slowPeriod as number,
      priceSource: config.parameters.priceSource as 'close' | 'open' | 'high' | 'low',
      signalMode: config.parameters.signalMode as 'crossover' | 'trend' | 'combined',
      useEMA: config.parameters.useEMA as boolean,
      minChangePercent: config.parameters.minChangePercent as number,
      confidenceThreshold: config.parameters.confidenceThreshold as number
    };

    // Initialize processor with config
    this.processor = new SMASignalProcessor(this.config);
    
    // Update state
    this.state.isRunning = true;
    this.state.lastUpdate = new Date();
    
    // Log initialization
    context.logger.info(`SMA Crossover Strategy initialized for ${context.symbol}`, {
      fastPeriod: this.config.fastPeriod,
      slowPeriod: this.config.slowPeriod,
      signalMode: this.config.signalMode
    });
  }

  /**
   * Execute the strategy
   */
  async execute(context: StrategyContext): Promise<StrategyResult> {
    try {
      // Get candle data
      const timeframe = context.config.execution?.timeframe || '1h';
      const candles = await context.marketData.getCandles(context.symbol, timeframe, 100);
      
      if (!candles || candles.length < this.config.slowPeriod) {
        return {
          success: false,
          action: 'hold',
          reason: `Insufficient data: need at least ${this.config.slowPeriod} candles`,
          error: 'Insufficient data'
        };
      }
      
      // Process candles with SMA processor
      const signal = this.processor.process(candles);
      
      // No signal
      if (!signal || signal.signal === 0) {
        return {
          success: true,
          action: 'hold',
          reason: 'No signal detected',
          confidence: 0
        };
      }
      
      // Got a signal
      const action = signal.signal > 0 ? 'buy' : 'sell';
      
      // Check if signal meets confidence threshold
      if (signal.confidence < (context.config.execution?.minimumConfidence || 0.6)) {
        return {
          success: true,
          action: 'hold',
          confidence: signal.confidence,
          reason: `${action.toUpperCase()} signal detected but confidence below threshold`
        };
      }
      
      // Check current position
      const position = await context.tradeExecutor.getPosition(
        context.botConfig.id,
        context.symbol
      );
      
      // If we already have a position in the same direction, hold
      if (position) {
        const positionSide = position.side;
        if ((positionSide === 'long' && action === 'buy') || 
            (positionSide === 'short' && action === 'sell')) {
          return {
            success: true,
            action: 'hold',
            confidence: signal.confidence,
            reason: `Already have a ${positionSide} position`
          };
        }
        
        // If we have a position in the opposite direction, close it first
        await context.tradeExecutor.closePosition(context.botConfig.id, context.symbol);
        this.state.tradesExecuted++;
      }
      
      // Create trade signal
      const tradeSignal = this.processor.createTradeSignal(
        signal,
        context.botConfig.id,
        context.symbol
      );
      
      // Execute the trade
      const order = await context.tradeExecutor.executeSignal(tradeSignal, context.botConfig);
      
      // Update state
      this.state.tradesExecuted++;
      this.state.lastUpdate = new Date();
      
      // Log the signal
      context.logger.info(`SMA Crossover Signal: ${action.toUpperCase()}`, {
        symbol: context.symbol,
        confidence: signal.confidence,
        reason: signal.reason,
        fastMA: signal.fastMA,
        slowMA: signal.slowMA
      });
      
      // Emit event
      context.eventEmitter.emit('strategy:signal', {
        type: 'signal',
        botId: context.botConfig.id,
        symbol: context.symbol,
        strategy: this.name,
        signal: action,
        confidence: signal.confidence,
        timestamp: Date.now()
      });
      
      return {
        success: true,
        action,
        confidence: signal.confidence,
        reason: signal.reason,
        metadata: {
          fastMA: signal.fastMA,
          slowMA: signal.slowMA,
          lastPrice: signal.lastPrice,
          orderId: order.orderId
        }
      };
    } catch (error) {
      context.logger.error(`SMA Crossover Strategy error: ${error instanceof Error ? error.message : String(error)}`, {
        botId: context.botConfig.id,
        symbol: context.symbol
      });
      
      return {
        success: false,
        action: 'hold',
        error: error instanceof Error ? error.message : String(error),
        reason: 'Strategy execution failed'
      };
    }
  }

  /**
   * Clean up resources
   */
  async cleanup(context: StrategyContext): Promise<void> {
    // Nothing to clean up for this strategy
    this.state.isRunning = false;
    context.logger.info('SMA Crossover Strategy cleaned up');
  }

  /**
   * Validate configuration
   */
  validateConfig(config: Record<string, unknown>): ConfigValidationResult {
    try {
      const validation = SMACrossoverConfigSchema.safeParse(config);
      
      if (validation.success) {
        const data = validation.data;
        
        // Additional validation
        const warnings: { field: string, message: string, suggestion?: string }[] = [];
        
        if (data.fastPeriod >= data.slowPeriod) {
          return {
            valid: false,
            errors: [
              {
                field: 'fastPeriod',
                message: 'Fast period must be less than slow period',
                code: 'INVALID_VALUE'
              }
            ],
            warnings
          };
        }
        
        // Add warnings for unusual values
        if (data.fastPeriod < 5) {
          warnings.push({
            field: 'fastPeriod',
            message: 'Fast period is very short, may generate many false signals',
            suggestion: 'Consider using a value between 5 and 20'
          });
        }
        
        if (data.slowPeriod > 100) {
          warnings.push({
            field: 'slowPeriod',
            message: 'Slow period is very long, may generate signals too late',
            suggestion: 'Consider using a value between 20 and 100'
          });
        }
        
        if (data.confidenceThreshold < 0.5) {
          warnings.push({
            field: 'confidenceThreshold',
            message: 'Low confidence threshold may generate more false signals',
            suggestion: 'Consider using a value above 0.5 for better signal quality'
          });
        }
        
        return {
          valid: true,
          errors: [],
          warnings
        };
      }
      
      // Format Zod errors
      return {
        valid: false,
        errors: validation.error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message,
          code: 'VALIDATION_ERROR'
        })),
        warnings: []
      };
    } catch (error) {
      return {
        valid: false,
        errors: [
          {
            field: 'validation',
            message: error instanceof Error ? error.message : String(error),
            code: 'VALIDATION_ERROR'
          }
        ],
        warnings: []
      };
    }
  }

  /**
   * Get default configuration
   */
  getDefaultConfig(): StrategyConfig {
    return {
      type: 'sma-crossover',
      parameters: {
        fastPeriod: 9,
        slowPeriod: 21,
        priceSource: 'close',
        signalMode: 'crossover',
        useEMA: false,
        minChangePercent: 0.5,
        confidenceThreshold: 0.6
      },
      riskManagement: {
        stopLossPercentage: 2,
        takeProfitPercentage: 4
      },
      execution: {
        timeframe: '1h',
        minimumConfidence: 0.6
      }
    };
  }

  /**
   * Get current state
   */
  getState(): StrategyState {
    return { ...this.state };
  }

  /**
   * Set state
   */
  setState(state: Partial<StrategyState>): void {
    this.state = {
      ...this.state,
      ...state,
      lastUpdate: new Date()
    };
  }
}

