/**
 * SMA Signal Processor
 * 
 * Processes price data using Simple Moving Averages (SMA) to generate
 * buy/sell signals based on crossovers and trend analysis.
 */

import { calculateSMA, calculateEMA, getMASignals } from '../../indicators/moving-averages';
import type { Candle, TradeSignal } from '../../target-reacher/interfaces';

import type { SMASignalConfig, SMASignalOutput } from './models';

export class SMASignalProcessor {
  private config: SMASignalConfig;
  
  constructor(config?: Partial<SMASignalConfig>) {
    // Default configuration
    this.config = {
      fastPeriod: 9,
      slowPeriod: 21,
      minChangePercent: 0.5,
      confidenceThreshold: 0.4, // Lowered threshold to generate more signals
      priceSource: 'close',
      signalMode: 'crossover',
      useEMA: false,
      ...config
    };
    
    this.validateConfig();
  }
  
  /**
   * Validate configuration parameters
   */
  private validateConfig() {
    const { fastPeriod, slowPeriod } = this.config;
    
    if (fastPeriod >= slowPeriod) {
      throw new Error('Fast period must be less than slow period');
    }
    
    if (fastPeriod < 2) {
      throw new Error('Fast period must be at least 2');
    }
    
    if (slowPeriod < 3) {
      throw new Error('Slow period must be at least 3');
    }
  }
  
  /**
   * Update configuration
   */
  updateConfig(newConfig: Partial<SMASignalConfig>): void {
    this.config = {
      ...this.config,
      ...newConfig
    };
    
    this.validateConfig();
  }
  
  /**
   * Get current configuration
   */
  getConfig(): SMASignalConfig {
    return { ...this.config };
  }
  
  /**
   * Process candle data and generate SMA signals
   * 
   * @param candles Array of candle data
   * @returns Signal output or null if no signal
   */
  process(candles: Candle[]): SMASignalOutput | null {
    if (candles.length < this.config.slowPeriod + 1) {
      console.warn(`Insufficient data for SMA signal processing. Need at least ${this.config.slowPeriod + 1} candles.`);
      return null;
    }
    
    // Extract price data from candles based on configured source
    const prices = candles.map(candle => candle[this.config.priceSource]);
    
    // Calculate moving averages
    const calculateMA = this.config.useEMA ? calculateEMA : calculateSMA;
    let fastMA: number[];
    let slowMA: number[];
    
    try {
      fastMA = calculateMA(prices, this.config.fastPeriod);
      slowMA = calculateMA(prices, this.config.slowPeriod);
    } catch (error) {
      console.error('Error calculating moving averages:', error);
      return null;
    }
    
    // Ensure we have enough data points after calculation
    if (fastMA.length === 0 || slowMA.length === 0) {
      return null;
    }
    
    // Get latest values
    const lastPrice = prices[prices.length - 1];
    const lastFastMA = fastMA[fastMA.length - 1];
    const lastSlowMA = slowMA[slowMA.length - 1];
    
    // Generate signal based on configuration
    const lastCandle = candles[candles.length - 1];
    const timestamp = lastCandle ? lastCandle.timestamp : Date.now();
    
    // Adjust arrays to same length for signal generation
    // Fast MA will have more values than slow MA due to different periods
    const adjustedFastMA = fastMA.slice(fastMA.length - slowMA.length);
    
    return this.generateSignal(prices, adjustedFastMA, slowMA, timestamp);
  }
  
  /**
   * Generate trading signal based on MA values
   */
  private generateSignal(prices: number[], fastMA: number[], slowMA: number[], timestamp: number): SMASignalOutput | null {
    // Ensure arrays are not empty and have data
    if (!prices.length || !fastMA.length || !slowMA.length) {
      return null;
    }
    
    // Ensure arrays are the same length
    if (fastMA.length !== slowMA.length || !fastMA.length) {
      console.warn('Moving average arrays must be of the same length for signal generation');
      return null;
    }
    
    const crossoverSignals = getMASignals(fastMA, slowMA);
    
    // Last values with null checks
    const lastPrice = prices[prices.length - 1];
    const lastFastMA = fastMA[fastMA.length - 1];
    const lastSlowMA = slowMA[slowMA.length - 1];
    const lastCrossoverSignal = crossoverSignals[crossoverSignals.length - 1] || 0;
    
    if (lastPrice === undefined || lastFastMA === undefined || lastSlowMA === undefined) {
      return null;
    }
    
    let signal = 0;
    let reason = 'No signal detected';
    let confidence = 0;
    
    // Calculate strength based on price distance from MA
    const priceDistance = Math.abs((lastPrice - lastSlowMA) / lastSlowMA) * 100;
    const strength = Math.min(priceDistance / 3, 1); // Normalize to 0-1, more sensitive
    
    // FIX: More sensitive confidence calculation
    // Calculate confidence based on the distance between MAs as percentage of price
    const maDistance = Math.abs(lastFastMA - lastSlowMA) / lastPrice * 100;
    confidence = Math.min(maDistance * 5, 1); // Scale up to get more reasonable values
    
    // Determine signal based on mode
    if (this.config.signalMode === 'crossover' || this.config.signalMode === 'combined') {
      // Check if we have a crossover signal
      if (lastCrossoverSignal !== 0) {
        signal = lastCrossoverSignal;
        
        if (signal > 0) {
          reason = `Bullish crossover: Fast MA (${lastFastMA.toFixed(2)}) crossed above Slow MA (${lastSlowMA.toFixed(2)})`;
        } else {
          reason = `Bearish crossover: Fast MA (${lastFastMA.toFixed(2)}) crossed below Slow MA (${lastSlowMA.toFixed(2)})`;
        }
      }
    }
    
    // Check trend if mode is trend or combined
    if ((this.config.signalMode === 'trend' || this.config.signalMode === 'combined') && signal === 0) {
      // Check the slope of fast MA (using last 3 points)
      const fastMASlope = fastMA.length >= 3 ? 
        ((fastMA[fastMA.length - 1] || 0) - (fastMA[fastMA.length - 3] || 0)) / 2 : 0;
      
      // Price is above both MAs and fast MA is rising = strong bullish
      if (lastPrice > lastFastMA && lastPrice > lastSlowMA && fastMASlope > 0) {
        signal = 1;
        reason = `Strong bullish trend: Price (${lastPrice.toFixed(2)}) above both MAs with rising Fast MA`;
        confidence = Math.min(0.8, strength); // Cap at 0.8 since it's not a crossover
      } 
      // Price is below both MAs and fast MA is falling = strong bearish
      else if (lastPrice < lastFastMA && lastPrice < lastSlowMA && fastMASlope < 0) {
        signal = -1;
        reason = `Strong bearish trend: Price (${lastPrice.toFixed(2)}) below both MAs with falling Fast MA`;
        confidence = Math.min(0.8, strength); // Cap at 0.8 since it's not a crossover
      }
    }
    
    // Return null if confidence threshold not met
    if (confidence < (this.config.confidenceThreshold || 0.6)) {
      return null;
    }
    
    // Return signal output
    return {
      signal,
      confidence,
      lastPrice,
      fastMA: lastFastMA,
      slowMA: lastSlowMA,
      reason,
      strength,
      timestamp,
      metadata: {
        fastPeriod: this.config.fastPeriod,
        slowPeriod: this.config.slowPeriod,
        useEMA: this.config.useEMA
      }
    };
  }
  
  /**
   * Create a trade signal from the SMA signal output
   */
  createTradeSignal(output: SMASignalOutput, botId: string, symbol: string): TradeSignal {
    return {
      id: `sma-${Date.now()}-${Math.floor(Math.random() * 1000)}`,
      botId,
      symbol,
      side: output.signal > 0 ? 'buy' : 'sell',
      confidence: output.confidence,
      price: output.lastPrice,
      timestamp: output.timestamp,
      reason: output.reason
    };
  }
  
  /**
   * Get default SMA configuration
   */
  static getDefaultConfig(): SMASignalConfig {
    return {
      fastPeriod: 9,
      slowPeriod: 21,
      minChangePercent: 0.5,
      confidenceThreshold: 0.4, // Lowered default threshold for more realistic signal generation
      priceSource: 'close',
      signalMode: 'crossover',
      useEMA: false
    };
  }
}

// Canonical config validator for Target Reacher. No duplications.
import type {
  ConfigValidationResult,
  ValidationError,
  ValidationWarning
} from './interfaces'

export class ConfigValidator {
  private errors: ValidationError[] = [];
  private warnings: ValidationWarning[] = [];

  reset(): void {
    this.errors = [];
    this.warnings = [];
  }

  addError(field: string, message: string, code = 'VALIDATION_ERROR'): void {
    this.errors.push({ field, message, code });
  }

  addWarning(field: string, message: string, suggestion?: string): void {
    this.warnings.push({ field, message, suggestion });
  }

  required(obj: Record<string, unknown>, field: string, fieldName?: string): boolean {
    const name = fieldName ?? field;
    if (obj[field] === undefined || obj[field] === null) {
      this.addError(field, `${name} is required`, 'REQUIRED');
      return false;
    }
    return true;
  }

  number(obj: Record<string, unknown>, field: string, options: {
    min?: number;
    max?: number;
    integer?: boolean;
    fieldName?: string;
  } = {}): boolean {
    const name = options.fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (typeof value !== 'number' || isNaN(value)) {
      this.addError(field, `${name} must be a number`, 'INVALID_TYPE');
      return false;
    }
    if (options.integer && !Number.isInteger(value)) {
      this.addError(field, `${name} must be an integer`, 'INVALID_TYPE');
      return false;
    }
    if (options.min !== undefined && value < options.min) {
      this.addError(field, `${name} must be at least ${options.min}`, 'OUT_OF_RANGE');
      return false;
    }
    if (options.max !== undefined && value > options.max) {
      this.addError(field, `${name} must be at most ${options.max}`, 'OUT_OF_RANGE');
      return false;
    }
    return true;
  }

  string(obj: Record<string, unknown>, field: string, options: {
    minLength?: number;
    maxLength?: number;
    pattern?: RegExp;
    enum?: string[];
    fieldName?: string;
  } = {}): boolean {
    const name = options.fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (typeof value !== 'string') {
      this.addError(field, `${name} must be a string`, 'INVALID_TYPE');
      return false;
    }
    if (options.minLength !== undefined && value.length < options.minLength) {
      this.addError(field, `${name} must be at least ${options.minLength} characters`, 'TOO_SHORT');
      return false;
    }
    if (options.maxLength !== undefined && value.length > options.maxLength) {
      this.addError(field, `${name} must be at most ${options.maxLength} characters`, 'TOO_LONG');
      return false;
    }
    if (options.pattern && !options.pattern.test(value)) {
      this.addError(field, `${name} format is invalid`, 'INVALID_FORMAT');
      return false;
    }
    if (options.enum && !options.enum.includes(value)) {
      this.addError(field, `${name} must be one of: ${options.enum.join(', ')}`, 'INVALID_VALUE');
      return false;
    }
    return true;
  }

  object(obj: Record<string, unknown>, field: string, validator: (value: unknown) => boolean, fieldName?: string): boolean {
    const name = fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (typeof value !== 'object' || value === null || Array.isArray(value)) {
      this.addError(field, `${name} must be an object`, 'INVALID_TYPE');
      return false;
    }
    return validator(value);
  }

  array(obj: Record<string, unknown>, field: string, options: {
    minLength?: number;
    maxLength?: number;
    itemValidator?: (item: unknown, index: number) => boolean;
    fieldName?: string;
  } = {}): boolean {
    const name = options.fieldName ?? field;
    const value = obj[field];
    if (value === undefined) {return true;}
    if (!Array.isArray(value)) {
      this.addError(field, `${name} must be an array`, 'INVALID_TYPE');
      return false;
    }
    if (options.minLength !== undefined && value.length < options.minLength) {
      this.addError(field, `${name} must have at least ${options.minLength} items`, 'TOO_SHORT');
      return false;
    }
    if (options.maxLength !== undefined && value.length > options.maxLength) {
      this.addError(field, `${name} must have at most ${options.maxLength} items`, 'TOO_LONG');
      return false;
    }
    if (options.itemValidator) {
      for (let i = 0; i < value.length; i++) {
        if (!options.itemValidator(value.at(i), i)) {
          return false;
        }
      }
    }
    return true;
  }

  warn(condition: boolean, field: string, message: string, suggestion?: string): void {
    if (condition) {
      this.addWarning(field, message, suggestion);
    }
  }

  getResult(): ConfigValidationResult {
    return {
      valid: this.errors.length === 0,
      errors: [...this.errors],
      warnings: [...this.warnings],
    };
  }
}

export function validateConfig(config: Record<string, unknown>, validatorFn: (validator: ConfigValidator) => void): ConfigValidationResult {
  const validator = new ConfigValidator();
  validator.reset();
  validatorFn(validator);
  return validator.getResult();
}

/**
 * Modular Target Reacher
 * 
 * This module provides modular components for target reaching strategies.
 * It works in conjunction with the main target-reacher implementation.
 */

// Re-export the main target reacher for compatibility
export { ModularTargetReacherStrategy as TargetReacher } from './target-reacher'

// Modular components for target reaching
export interface ModularTargetConfig {
  // Target configuration
  targetPrice?: number
  targetPercentage?: number
  timeframe?: string
  
  // Risk management
  maxDrawdown?: number
  stopLoss?: number
  
  // Strategy parameters
  aggressiveness?: 'conservative' | 'moderate' | 'aggressive'
}

/**
 * Modular Target Reacher Component
 * Provides configurable target reaching functionality
 */
export class ModularTargetReacher {
  private readonly config: ModularTargetConfig
  
  constructor(config: ModularTargetConfig = {}) {
    this.config = {
      aggressiveness: 'moderate',
      maxDrawdown: 5,
      ...config
    }
  }
    /**
   * Calculate target metrics based on current market conditions and configuration
   */
  calculateTargets(currentPrice?: number, marketData?: any): ModularTargetConfig {
    const baseConfig = { ...this.config };
    
    if (currentPrice && !baseConfig.targetPrice && baseConfig.targetPercentage) {
      // Calculate target price from percentage
      baseConfig.targetPrice = currentPrice * (1 + (baseConfig.targetPercentage / 100));
    }
    
    // Adjust targets based on aggressiveness
    switch (baseConfig.aggressiveness) {
      case 'conservative':
        baseConfig.maxDrawdown = Math.min(baseConfig.maxDrawdown ?? 5, 3);
        baseConfig.stopLoss = baseConfig.stopLoss ?? 2;
        break;
      case 'aggressive':
        baseConfig.maxDrawdown = Math.max(baseConfig.maxDrawdown ?? 5, 10);
        baseConfig.stopLoss = baseConfig.stopLoss ?? 8;
        break;
      default: // moderate
        baseConfig.maxDrawdown = baseConfig.maxDrawdown ?? 5;
        baseConfig.stopLoss = baseConfig.stopLoss ?? 5;
    }
    
    // Adjust for market volatility if available
    if (marketData?.volatility) {
      const volatilityMultiplier = Math.min(1.5, 1 + marketData.volatility);
      baseConfig.stopLoss = (baseConfig.stopLoss ?? 5) * volatilityMultiplier;
    }
    
    return baseConfig;
  }
  
  /**
   * Apply modular strategy based on current position and market conditions
   */
  async applyStrategy(position?: any, marketData?: any): Promise<{ action: string; confidence: number; reason: string }> {
    const targets = this.calculateTargets(position?.currentPrice, marketData);
    
    if (!position || !targets.targetPrice) {
      return { 
        action: 'hold', 
        confidence: 0.5, 
        reason: 'Insufficient data for strategy application' 
      };
    }
    
    const currentPrice = position.currentPrice ?? position.markPrice ?? 0;
    const entryPrice = position.entryPrice ?? currentPrice;
    
    // Calculate current performance
    const currentReturn = ((currentPrice - entryPrice) / entryPrice) * 100;
    
    // Check if target is reached
    if (targets.targetPrice && currentPrice >= targets.targetPrice) {
      return { 
        action: 'close', 
        confidence: 0.9, 
        reason: `Target price ${targets.targetPrice} reached at ${currentPrice}` 
      };
    }
    
    // Check stop loss
    if (targets.stopLoss && currentReturn <= -(targets.stopLoss)) {
      return { 
        action: 'close', 
        confidence: 0.8, 
        reason: `Stop loss triggered at ${currentReturn.toFixed(2)}%` 
      };
    }
    
    // Check max drawdown
    if (targets.maxDrawdown && currentReturn <= -(targets.maxDrawdown)) {
      return { 
        action: 'reduce', 
        confidence: 0.7, 
        reason: `Max drawdown reached at ${currentReturn.toFixed(2)}%` 
      };
    }
    
    // Continue holding if within acceptable range
    return { 
      action: 'hold', 
      confidence: 0.6, 
      reason: `Position performing within parameters (${currentReturn.toFixed(2)}%)` 
    };
  }
}

// [Canonical parameter mapping for Target Reacher. No duplications.]

// Utility to map between camelCase and snake_case for strategy parameters

export function camelToSnake(obj: Record<string, unknown>): Record<string, unknown> {
  const out: Record<string, unknown> = {};
  for (const key in obj) {
    if (!Object.prototype.hasOwnProperty.call(obj, key)) {continue;}
    const snake = key.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
    out[snake] = obj[key];
  }
  return out;
}

export function snakeToCamel(obj: Record<string, unknown>): Record<string, unknown> {
  if (!obj || typeof obj !== 'object') {return obj;}
  const result: Record<string, unknown> = {};
  for (const key in obj) {
    if (!Object.prototype.hasOwnProperty.call(obj, key)) {continue;}
    const camelKey = key.replace(/_([a-z])/g, (_, letter) => letter.toUpperCase());
    const value = obj[key];
    result[camelKey] = (value && typeof value === 'object' && !Array.isArray(value))
      ? snakeToCamel(value as Record<string, unknown>)
      : value;
  }
  return result;
}

/**
 * Target Reacher Parameters
 * 
 * This module defines parameter interfaces and types for target reaching strategies.
 */

// Parameter configuration interfaces
export interface TargetReacherParameters {
  // Basic target parameters
  targetPrice?: number
  targetPercentage?: number
  maxTargets?: number
  
  // Timing parameters
  timeframe?: string
  duration?: number
  
  // Risk parameters
  maxRisk?: number
  riskTolerance?: 'low' | 'medium' | 'high'
  
  // Strategy parameters
  approach?: 'conservative' | 'aggressive' | 'balanced'
  rebalanceFrequency?: number
}

// Default parameter values
export const DEFAULT_TARGET_PARAMETERS: TargetReacherParameters = {
  targetPercentage: 5,
  maxTargets: 3,
  timeframe: '1h',
  duration: 24,
  maxRisk: 2,
  riskTolerance: 'medium',
  approach: 'balanced',
  rebalanceFrequency: 4
}

/**
 * Validate target reacher parameters
 * @param params Parameters to validate
 * @returns Validated parameters with defaults
 */
export function validateTargetParameters(params: Partial<TargetReacherParameters>): TargetReacherParameters {
  return {
    ...DEFAULT_TARGET_PARAMETERS,
    ...params
  }
}

/**
 * Calculate risk-adjusted parameters
 * @param baseParams Base parameters
 * @param riskLevel Risk adjustment level
 * @returns Risk-adjusted parameters
 */
export function adjustParametersForRisk(
  baseParams: TargetReacherParameters,
  riskLevel: number
): TargetReacherParameters {
  const riskMultiplier = Math.max(0.1, Math.min(2.0, riskLevel))
  
  return {
    ...baseParams,
    targetPercentage: (baseParams.targetPercentage || 5) * riskMultiplier,
    maxRisk: (baseParams.maxRisk || 2) / riskMultiplier
  }
}

/**
 * Modular Target Reacher Strategy
 * 
 * Redesigned Target Reacher strategy using the new modular system
 */

import type { ConfigValidator } from './config-validator';
import { validateConfig } from './config-validator'
import type { 
  IStrategy, 
  StrategyConfig, 
  StrategyContext, 
  StrategyResult, 
  StrategyState,
  ConfigValidationResult,
  Candle
} from './interfaces'

export class ModularTargetReacherStrategy implements IStrategy {
  readonly name = 'target-reacher'
  readonly version = '2.0.0'
  readonly description = 'Modular Target Reacher strategy with fixed and average price sources (pure signal generator)'
  readonly supportedMarkets = ['spot', 'futures', 'cross-margin'] as const

  constructor(private readonly config: StrategyConfig, private readonly context: StrategyContext) {}

  async initialize(): Promise<void> {
    // No-op for pure signal strategy
  }

  async execute(): Promise<StrategyResult> {
    const { priceSource, fixedPrice, averagePeriod, averageType } = this.config.parameters
    const currentPriceRaw = await this.context.marketData.getCurrentPrice(this.context.symbol)
    const currentPrice = typeof currentPriceRaw === 'number' ? currentPriceRaw : Number(currentPriceRaw) || 0
    let targetPrice: number | undefined

    if (priceSource === 'fixed') {
      targetPrice = typeof fixedPrice === 'number' ? fixedPrice : Number(fixedPrice) || undefined
    } else if (priceSource === 'average') {
      const avgPeriod = typeof averagePeriod === 'number' && !isNaN(averagePeriod) ? averagePeriod : 20;
      const candles: Candle[] = await this.context.marketData.getCandles(this.context.symbol, '1h', avgPeriod)
      const priceKey: keyof Candle = (averageType ?? 'low') as keyof Candle;
      targetPrice = candles.length > 0
        ? candles.reduce((sum, c) => {
            const val = c[priceKey];
            return sum + (typeof val === 'number' ? val : Number(val) || 0);
          }, 0) / candles.length
        : undefined;
    } else {
      throw new Error('Unsupported price source')
    }

    if (typeof targetPrice === 'number') {
