import { CONSTANTS } from '@jabbr/shared';
import type { Request, Response } from 'express';

import { userRepository } from '../users/user.repository';

import { AuthService } from './auth.service';

export class AuthController {
  private authService: AuthService;

  constructor() {
    this.authService = new AuthService();
  }

  /**
   * User registration endpoint
   */
  register = async (req: Request, res: Response): Promise<void> => {
    try {
      // Validate request data
      const registerData = this.authService.validateRegisterRequest(req.body);

      // Check if passwords match
      if (registerData.password !== registerData.confirmPassword) {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Passwords do not match',
            details: 'password and confirmPassword must be identical'
          },
          timestamp: new Date()
        });
        return;
      }

      // Check password strength
      const passwordValidation = this.authService.validatePasswordStrength(registerData.password);
      if (!passwordValidation.isValid) {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Password does not meet security requirements',
            details: passwordValidation.errors.join(', ')
          },
          timestamp: new Date()
        });
        return;
      }

      // Check if email already exists
      const existingUser = await userRepository.findByEmail(registerData.email);
      if (existingUser) {
        res.status(409).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Email already registered',
            details: 'A user with this email address already exists'
          },
          timestamp: new Date()
        });
        return;
      }

      // Hash password
      const hashedPassword = await this.authService.hashPassword(registerData.password);

      // Create user
      const newUser = await userRepository.create({
        email: registerData.email,
        passwordHash: hashedPassword,
        role: 'user',
        apiKeys: [],
        preferences: {
          timezone: 'UTC',
          currency: 'USD',
          notifications: {
            email: true,
            browser: true,
            tradingAlerts: true,
            systemAlerts: true,
            riskAlerts: true
          },
          dashboard: {
            theme: 'dark',
            layout: 'standard',
            refreshRate: 30000
          }
        },
        isEmailVerified: false,
        lastLoginAt: null
      });

      // Generate tokens
      const tokens = this.authService.generateTokenPair(newUser.id, newUser.email);

      // Update last login
      await userRepository.updateLastLogin(newUser.id);

      // Prepare response (exclude sensitive data)
      const userResponse = {
        id: newUser.id,
        email: newUser.email,
        role: newUser.role,
        preferences: newUser.preferences,
        createdAt: newUser.createdAt,
        updatedAt: newUser.updatedAt
      };

      res.status(201).json({
        success: true,
        data: {
          user: userResponse,
          accessToken: tokens.accessToken,
          refreshToken: tokens.refreshToken,
          expiresIn: tokens.expiresIn
        },
        timestamp: new Date()
      });

    } catch (error) {
      console.error('Registration error:', error);
      
      if (error instanceof Error && error.name === 'ZodError') {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Invalid request data',
            details: error.message
          },
          timestamp: new Date()
        });
        return;
      }

      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Registration failed',
          details: 'An error occurred during user registration'
        },
        timestamp: new Date()
      });
    }
  };

  /**
   * User login endpoint
   */
  login = async (req: Request, res: Response): Promise<void> => {
    try {
      // Validate request data
      const loginData = this.authService.validateLoginRequest(req.body);

      // Find user by email
      const user = await userRepository.findByEmail(loginData.email);
      if (!user) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid credentials',
            details: 'Email or password is incorrect'
          },
          timestamp: new Date()
        });
        return;
      }

      // Verify password
      const isPasswordValid = await this.authService.verifyPassword(
        loginData.password, 
        user.passwordHash
      );

      if (!isPasswordValid) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid credentials',
            details: 'Email or password is incorrect'
          },
          timestamp: new Date()
        });
        return;
      }

      // Generate tokens
      const tokens = this.authService.generateTokenPair(user.id, user.email);

      // Update last login
      await userRepository.updateLastLogin(user.id);

      // Prepare response (exclude sensitive data)
      const userResponse = {
        id: user.id,
        email: user.email,
        role: user.role,
        preferences: user.preferences,
        createdAt: user.createdAt,
        updatedAt: user.updatedAt
      };

      res.status(200).json({
        success: true,
        data: {
          user: userResponse,
          accessToken: tokens.accessToken,
          refreshToken: tokens.refreshToken,
          expiresIn: tokens.expiresIn
        },
        timestamp: new Date()
      });

    } catch (error) {
      console.error('Login error:', error);
      
      if (error instanceof Error && error.name === 'ZodError') {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Invalid request data',
            details: error.message
          },
          timestamp: new Date()
        });
        return;
      }

      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Login failed',
          details: 'An error occurred during authentication'
        },
        timestamp: new Date()
      });
    }
  };

  /**
   * Token refresh endpoint
   */
  refresh = async (req: Request, res: Response): Promise<void> => {
    try {
      // User ID is attached by requireRefreshToken middleware
      const userId = req.user?.userId;
      if (!userId) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid refresh token',
            details: 'User information not found in token'
          },
          timestamp: new Date()
        });
        return;
      }

      // Find user to get current email
      const user = await userRepository.findById(userId);
      if (!user) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.USER_NOT_FOUND,
            message: 'User not found',
            details: 'The user associated with this token no longer exists'
          },
          timestamp: new Date()
        });
        return;
      }

      // Generate new tokens
      const tokens = this.authService.generateTokenPair(user.id, user.email);

      res.status(200).json({
        success: true,
        data: {
          accessToken: tokens.accessToken,
          refreshToken: tokens.refreshToken,
          expiresIn: tokens.expiresIn
        },
        timestamp: new Date()
      });

    } catch (error) {
      console.error('Token refresh error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Token refresh failed',
          details: 'An error occurred while refreshing the token'
        },
        timestamp: new Date()
      });
    }
  };

  /**
   * Get current user profile
   */
  profile = async (req: Request, res: Response): Promise<void> => {
    try {
      const userId = req.user?.userId;
      if (!userId) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Authentication required',
            details: 'User information not found in request'
          },
          timestamp: new Date()
        });
        return;
      }

      const user = await userRepository.findById(userId);
      if (!user) {
        res.status(404).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.USER_NOT_FOUND,
            message: 'User not found',
            details: 'The authenticated user no longer exists'
          },
          timestamp: new Date()
        });
        return;
      }

      // Prepare response (exclude sensitive data)
      const userResponse = {
        id: user.id,
        email: user.email,
        role: user.role,
        preferences: user.preferences,
        isEmailVerified: user.isEmailVerified,
        lastLoginAt: user.lastLoginAt,
        createdAt: user.createdAt,
        updatedAt: user.updatedAt
      };

      res.status(200).json({
        success: true,
        data: userResponse,
        timestamp: new Date()
      });

    } catch (error) {
      console.error('Profile fetch error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Failed to fetch profile',
          details: 'An error occurred while retrieving user profile'
        },
        timestamp: new Date()
      });
    }
  };

  /**
   * Logout endpoint (client-side token invalidation)
   * In a production system, you might want to maintain a token blacklist
   */
  logout = async (req: Request, res: Response): Promise<void> => {
    try {
      // For now, logout is handled client-side by removing tokens
      // In production, you might want to:
      // 1. Add tokens to a blacklist in Redis
      // 2. Track active sessions in database
      // 3. Implement token revocation

      res.status(200).json({
        success: true,
        data: {
          message: 'Logged out successfully'
        },
        timestamp: new Date()
      });

    } catch (error) {
      console.error('Logout error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Logout failed',
          details: 'An error occurred during logout'
        },
        timestamp: new Date()
      });
    }
  };
}

// Export singleton instance
export const authController = new AuthController();

import { CONSTANTS } from '@jabbr/shared';
import type { Request, Response, NextFunction } from 'express';

import { AuthService } from './auth.service';

// Extend Express Request type to include user information
declare global {
  namespace Express {
    interface Request {
      user?: {
        userId: string;
        email: string;
      };
    }
  }
}

export class AuthMiddleware {
  private authService: AuthService;

  constructor() {
    this.authService = new AuthService();
  }

  /**
   * Middleware to require authentication
   * Verifies JWT token and attaches user info to request
   */
  requireAuth = (req: Request, res: Response, next: NextFunction): void => {
    try {
      const authHeader = req.headers.authorization;
      const token = this.authService.extractTokenFromHeader(authHeader);

      if (!token) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'No authentication token provided',
            details: 'Authorization header with Bearer token is required'
          }
        });
        return;
      }

      const decoded = this.authService.verifyAccessToken(token);

      if (!decoded) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid or expired token',
            details: 'Please login again to get a new token'
          }
        });
        return;
      }

      // Attach user info to request for use in route handlers
      req.user = {
        userId: decoded.userId,
        email: decoded.email
      };

      next();
    } catch (error) {
      console.error('Auth middleware error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Authentication error',
          details: 'An error occurred while verifying authentication'
        }
      });
    }
  };

  /**
   * Optional authentication middleware
   * Attaches user info if token is valid, but doesn't require it
   */
  optionalAuth = (req: Request, res: Response, next: NextFunction): void => {
    try {
      const authHeader = req.headers.authorization;
      const token = this.authService.extractTokenFromHeader(authHeader);

      if (token) {
        const decoded = this.authService.verifyAccessToken(token);
        if (decoded) {
          req.user = {
            userId: decoded.userId,
            email: decoded.email
          };
        }
      }

      next();
    } catch (error) {
      // Log error but don't block the request
      console.error('Optional auth middleware error:', error);
      next();
    }
  };

  /**
   * Middleware to check if user owns a resource
   * Must be used after requireAuth middleware
   */
  requireOwnership = (resourceUserIdField = 'userId') => {
    return (req: Request, res: Response, next: NextFunction): void => {
      if (!req.user) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Authentication required',
            details: 'This middleware must be used after requireAuth'
          }
        });
        return;
      }

      // Check if the resource belongs to the authenticated user
      // This can be used with req.params, req.body, or req.query
      // Validate the field name to prevent object injection
      if (typeof resourceUserIdField !== 'string' || !resourceUserIdField) {
        res.status(500).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHORIZATION_ERROR,
            message: 'Invalid resource field configuration'
          }
        });
        return;
      }

      const resourceUserId = (req.params && Object.prototype.hasOwnProperty.call(req.params, resourceUserIdField) ? req.params[resourceUserIdField] : null) || 
                           (req.body && Object.prototype.hasOwnProperty.call(req.body, resourceUserIdField) ? req.body[resourceUserIdField] : null) || 
                           (req.query && Object.prototype.hasOwnProperty.call(req.query, resourceUserIdField) ? req.query[resourceUserIdField] : null);

      if (!resourceUserId) {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Resource user ID not found',
            details: `${resourceUserIdField} must be provided in request parameters, body, or query`
          }
        });
        return;
      }

      if (req.user.userId !== resourceUserId) {
        res.status(403).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHORIZATION_ERROR,
            message: 'Access denied',
            details: 'You can only access your own resources'
          }
        });
        return;
      }

      next();
    };
  };

  /**
   * Rate limiting middleware for authentication endpoints
   * Simple in-memory rate limiter (in production, use Redis)
   */
  createRateLimiter = (maxAttempts = 5, windowMs: number = 15 * 60 * 1000) => {
    const attempts = new Map<string, { count: number; resetTime: number }>();

    return (req: Request, res: Response, next: NextFunction): void => {
      const clientIp = req.ip || req.connection.remoteAddress || 'unknown';
      const now = Date.now();

      // Clean up old entries
      for (const [ip, data] of attempts.entries()) {
        if (now > data.resetTime) {
          attempts.delete(ip);
        }
      }

      const clientAttempts = attempts.get(clientIp);

      if (!clientAttempts) {
        attempts.set(clientIp, { count: 1, resetTime: now + windowMs });
        next();
        return;
      }

      if (now > clientAttempts.resetTime) {
        // Reset window
        attempts.set(clientIp, { count: 1, resetTime: now + windowMs });
        next();
        return;
      }

      if (clientAttempts.count >= maxAttempts) {
        const remainingTime = Math.ceil((clientAttempts.resetTime - now) / 1000);
        res.status(429).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.RATE_LIMIT_ERROR,
            message: 'Too many attempts',
            details: `Please try again in ${remainingTime} seconds`
          }
        });
        return;
      }

      clientAttempts.count++;
      next();
    };
  };

  /**
   * Middleware to validate refresh token
   */
  requireRefreshToken = (req: Request, res: Response, next: NextFunction): void => {
    try {
      const { refreshToken } = req.body;

      if (!refreshToken) {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Refresh token required',
            details: 'refreshToken must be provided in request body'
          }
        });
        return;
      }

      const decoded = this.authService.verifyRefreshToken(refreshToken);

      if (!decoded) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid refresh token',
            details: 'Please login again to get new tokens'
          }
        });
        return;
      }

      // Attach userId to request for use in refresh endpoint
      req.user = {
        userId: decoded.userId,
        email: '' // Will be filled by the refresh endpoint
      };

      next();
    } catch (error) {
      console.error('Refresh token middleware error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Token verification error',
          details: 'An error occurred while verifying refresh token'
        }
      });
    }
  };
}

// Export singleton instance
export const authMiddleware = new AuthMiddleware();

import { Router } from 'express';

import { authController } from './auth.controller';
import { authMiddleware } from './auth.middleware';

/**
 * Authentication routes
 * All routes are prefixed with /auth in the main app
 */
export const authRoutes = Router();

// Rate limiting for auth endpoints (5 attempts per 15 minutes)
const authRateLimit = authMiddleware.createRateLimiter(5, 15 * 60 * 1000);

/**
 * POST /auth/register
 * Register a new user account
 * 
 * Body: { email: string, password: string, confirmPassword: string }
 * Response: { success: boolean, data: { user, accessToken, refreshToken, expiresIn }, timestamp }
 */
authRoutes.post('/register', authRateLimit, authController.register);

/**
 * POST /auth/login
 * Authenticate user and get tokens
 * 
 * Body: { email: string, password: string }
 * Response: { success: boolean, data: { user, accessToken, refreshToken, expiresIn }, timestamp }
 */
authRoutes.post('/login', authRateLimit, authController.login);

/**
 * POST /auth/refresh
 * Refresh access token using refresh token
 * 
 * Body: { refreshToken: string }
 * Response: { success: boolean, data: { accessToken, refreshToken, expiresIn }, timestamp }
 */
authRoutes.post('/refresh', 
  authMiddleware.requireRefreshToken, 
  authController.refresh
);

/**
 * GET /auth/profile
 * Get current user profile (requires authentication)
 * 
 * Headers: { Authorization: "Bearer <token>" }
 * Response: { success: boolean, data: User, timestamp }
 */
authRoutes.get('/profile', 
  authMiddleware.requireAuth, 
  authController.profile
);

/**
 * POST /auth/logout
 * Logout user (client-side token invalidation)
 * 
 * Headers: { Authorization: "Bearer <token>" } (optional)
 * Response: { success: boolean, data: { message }, timestamp }
 */
authRoutes.post('/logout', authController.logout);

/**
 * GET /auth/verify
 * Verify if current token is valid (requires authentication)
 * 
 * Headers: { Authorization: "Bearer <token>" }
 * Response: { success: boolean, data: { valid: true, userId, email }, timestamp }
 */
authRoutes.get('/verify', 
  authMiddleware.requireAuth, 
  (req, res) => {
    res.json({
      success: true,
      data: {
        valid: true,
        userId: req.user?.userId,
        email: req.user?.email
      },
      timestamp: new Date()
    });
  }
);

export default authRoutes;

import type {
  LoginRequest,
  RegisterRequest} from '@jabbr/shared';
import {
  LoginRequestSchema,
  RegisterRequestSchema,
  CONSTANTS
} from '@jabbr/shared';
import bcrypt from 'bcryptjs';
import jwt from 'jsonwebtoken';
import { z } from 'zod';

// Environment variables validation
const AuthConfigSchema = z.object({
  JWT_SECRET: z.string().min(CONSTANTS.LIMITS.JWT_SECRET_MIN_LENGTH),
  JWT_REFRESH_SECRET: z.string().min(CONSTANTS.LIMITS.JWT_SECRET_MIN_LENGTH),
  JWT_EXPIRES_IN: z.string().default('15m'),
  JWT_REFRESH_EXPIRES_IN: z.string().default('7d'),
  BCRYPT_ROUNDS: z.number().min(8).max(CONSTANTS.LIMITS.BCRYPT_MAX_ROUNDS).default(12)
});

type AuthConfig = z.infer<typeof AuthConfigSchema>;

export class AuthService {
  private config: AuthConfig;

  constructor() {
    // Validate environment configuration
    this.config = AuthConfigSchema.parse({
      JWT_SECRET: process.env.JWT_SECRET,
      JWT_REFRESH_SECRET: process.env.JWT_REFRESH_SECRET,
      JWT_EXPIRES_IN: process.env.JWT_EXPIRES_IN,
      JWT_REFRESH_EXPIRES_IN: process.env.JWT_REFRESH_EXPIRES_IN,
      BCRYPT_ROUNDS: process.env.BCRYPT_ROUNDS ? parseInt(process.env.BCRYPT_ROUNDS) : undefined
    });
  }

  /**
   * Hash a password using bcrypt
   */
  async hashPassword(password: string): Promise<string> {
    return bcrypt.hash(password, this.config.BCRYPT_ROUNDS);
  }

  /**
   * Verify a password against its hash
   */
  async verifyPassword(password: string, hash: string): Promise<boolean> {
    return bcrypt.compare(password, hash);
  }

  /**
   * Generate JWT access token
   */
  generateAccessToken(userId: string, email: string): string {
    const payload = { 
      userId, 
      email, 
      type: 'access' 
    };
    
    const options: any = { 
      expiresIn: this.config.JWT_EXPIRES_IN,
      issuer: 'jabbr-trading-bot',
      audience: 'jabbr-users'
    };
    
    return jwt.sign(payload, this.config.JWT_SECRET, options);
  }

  /**
   * Generate JWT refresh token
   */
  generateRefreshToken(userId: string): string {
    const payload = { 
      userId, 
      type: 'refresh' 
    };
    
    const options: any = { 
      expiresIn: this.config.JWT_REFRESH_EXPIRES_IN,
      issuer: 'jabbr-trading-bot',
      audience: 'jabbr-users'
    };
    
    return jwt.sign(payload, this.config.JWT_REFRESH_SECRET, options);
  }

  /**
   * Verify and decode JWT access token
   */
  verifyAccessToken(token: string): { userId: string; email: string } | null {
    try {
      const decoded = jwt.verify(token, this.config.JWT_SECRET, {
        issuer: 'jabbr-trading-bot',
        audience: 'jabbr-users'
      }) as any;

      if (decoded.type !== 'access') {
        return null;
      }

      return {
        userId: decoded.userId,
        email: decoded.email
      };
    } catch (error) {
      return null;
    }
  }

  /**
   * Verify and decode JWT refresh token
   */
  verifyRefreshToken(token: string): { userId: string } | null {
    try {
      const decoded = jwt.verify(token, this.config.JWT_REFRESH_SECRET, {
        issuer: 'jabbr-trading-bot',
        audience: 'jabbr-users'
      }) as any;

      if (decoded.type !== 'refresh') {
        return null;
      }

      return {
        userId: decoded.userId
      };
    } catch (error) {
      return null;
    }
  }

  /**
   * Generate both access and refresh tokens
   */
  generateTokenPair(userId: string, email: string): {
    accessToken: string;
    refreshToken: string;
    expiresIn: string;
  } {
    return {
      accessToken: this.generateAccessToken(userId, email),
      refreshToken: this.generateRefreshToken(userId),
      expiresIn: this.config.JWT_EXPIRES_IN
    };
  }

  /**
   * Validate login request data
   */
  validateLoginRequest(data: unknown): LoginRequest {
    return LoginRequestSchema.parse(data);
  }

  /**
   * Validate register request data
   */
  validateRegisterRequest(data: unknown): RegisterRequest {
    return RegisterRequestSchema.parse(data);
  }

  /**
   * Extract token from Authorization header
   */
  extractTokenFromHeader(authHeader: string | undefined): string | null {
    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return null;
    }
    return authHeader.substring(7); // Remove 'Bearer ' prefix
  }

  /**
   * Validate password strength (additional validation beyond Zod)
   */
  validatePasswordStrength(password: string): {
    isValid: boolean;
    errors: string[];
  } {
    const errors: string[] = [];

    if (password.length < 8) {
      errors.push('Password must be at least 8 characters long');
    }

    if (password.length > 128) {
      errors.push('Password must be less than 128 characters');
    }

    if (!/[a-z]/.test(password)) {
      errors.push('Password must contain at least one lowercase letter');
    }

    if (!/[A-Z]/.test(password)) {
      errors.push('Password must contain at least one uppercase letter');
    }

    if (!/\d/.test(password)) {
      errors.push('Password must contain at least one number');
    }

    if (!/[!@#$%^&*()_+\-=[\]{};':"\\|,.<>/?]/.test(password)) {
      errors.push('Password must contain at least one special character');
    }

    // Check for common weak passwords
    const commonPasswords = [
      'password', '123456', '123456789', 'qwerty', 'abc123',
      'password123', 'admin', 'letmein', 'welcome', 'monkey'
    ];

    if (commonPasswords.includes(password.toLowerCase())) {
      errors.push('Password is too common');
    }

    return {
      isValid: errors.length === 0,
      errors
    };
  }

  /**
   * Generate a secure random token for password reset, email verification, etc.
   */
  generateSecureToken(): string {
    return require('crypto').randomBytes(32).toString('hex');
  }

  /**
   * Check if token is expired based on timestamp
   */
  isTokenExpired(timestamp: Date, expirationHours = 24): boolean {
    const now = new Date();
    const expirationTime = new Date(timestamp.getTime() + (expirationHours * 60 * 60 * 1000));
    return now > expirationTime;
  }
}

/**
 * Bot Manager - Production Bot Lifecycle Management
 * 
 * Manages multiple bot instances with:
 * - Dynamic strategy loading
 * - Bot lifecycle coordination
 * - Error recovery and monitoring
 * - WebSocket event broadcasting
 * - Performance tracking
 */

import { EventEmitter } from 'events';

import { ExchangeManager } from '../exchanges/exchange-manager';
import type { IStrategy, StrategyContext, BotConfig, StrategyConfig } from '../JabbrLabs/target-reacher/interfaces';
import type { StrategyType } from '../strategies/strategy-factory';
import { webSocketService } from '../websocket/websocket.service';

import type { BotRuntimeConfig, BotState } from './bot-runtime';
import { BotRuntime } from './bot-runtime';
import type { Bot} from './bots.service';
import { botService } from './bots.service';

// import { logger, LogCategory } from '../utils/logging-utils';

import type { StrategySwapOptions } from './dynamic-strategy-loader';
import { dynamicStrategyLoader } from './dynamic-strategy-loader';
import { TradeExecutor } from './trade-executor';
// import { WebSocketService } from '../websocket/websocket.service';

export interface BotManagerConfig {
  maxConcurrentBots: number;
  defaultCheckInterval: number;
  defaultMaxRetries: number;
  defaultRetryDelay: number;
  monitoringInterval: number;
}

export class BotManager extends EventEmitter {
  private static instance: BotManager;
  private config: BotManagerConfig;
  private activeBots: Map<string, BotRuntime> = new Map();
  private monitoringInterval?: NodeJS.Timeout;
  private exchangeManager: ExchangeManager;
  private tradeExecutor: TradeExecutor;
  private enableRealTrading: boolean;

  private constructor(config: BotManagerConfig, enableRealTrading = false) {
    super();
    this.config = config;
    this.enableRealTrading = enableRealTrading;

    // Initialize exchange manager
    this.exchangeManager = new ExchangeManager({
      autoReconnect: true,
      reconnectInterval: 30000,
      healthCheckInterval: 60000,
      maxReconnectAttempts: 5
    });

    // Initialize trade executor
    this.tradeExecutor = new TradeExecutor(this.exchangeManager, {
      maxRetries: 3,
      retryDelay: 1000,
      orderTimeout: 30000,
      enableMockMode: !enableRealTrading, // Use mock mode if real trading is disabled
      validateBalance: true,
      maxSlippagePercent: 0.5
    });

    this.setupEventListeners();

    this.setupMonitoring();
    this.initializeDynamicStrategyLoader();

    console.log('ðŸ¤– BotManager initialized', {
      maxConcurrentBots: this.config.maxConcurrentBots,
      enableRealTrading
    });
  }

  /**
   * Get singleton instance
   */
  static getInstance(config?: BotManagerConfig, enableRealTrading?: boolean): BotManager {
    if (!BotManager.instance) {
      const defaultConfig: BotManagerConfig = {
        maxConcurrentBots: 10,
        defaultCheckInterval: 5000, // 5 seconds
        defaultMaxRetries: 5,
        defaultRetryDelay: 1000, // 1 second
        monitoringInterval: 30000 // 30 seconds
      };

      BotManager.instance = new BotManager(config || defaultConfig, enableRealTrading);
    }
    return BotManager.instance;
  }

  /**
   * Initialize dynamic strategy loader
   */
  private async initializeDynamicStrategyLoader(): Promise<void> {
    try {
      await dynamicStrategyLoader.initialize();
      
      // Set up event listeners for strategy events
      dynamicStrategyLoader.on('strategy-loaded', (data) => {
        // logger.info('ðŸ“¡ Strategy loaded event', LogCategory.STRATEGY, data);
        this.broadcastStrategyUpdate(data.botId, 'loaded', data);
      });

      dynamicStrategyLoader.on('strategy-switched', (data) => {
        // logger.info('ðŸ“¡ Strategy switched event', LogCategory.STRATEGY, data);
        this.broadcastStrategyUpdate(data.botId, 'switched', data);
      });

      dynamicStrategyLoader.on('strategy-rollback', (data) => {
        // logger.info('ðŸ“¡ Strategy rollback event', LogCategory.STRATEGY, data);
        this.broadcastStrategyUpdate(data.botId, 'rollback', data);
      });

      dynamicStrategyLoader.on('strategy-underperforming', (data) => {
        // logger.warn('ðŸ“¡ Strategy underperforming event', LogCategory.STRATEGY, data);
        this.handleUnderperformingStrategy(data.botId, data.performance);
      });

      // logger.info('âœ… Dynamic Strategy Loader integrated with Bot Manager');
    } catch (error) {
      // logger.error('âŒ Failed to initialize Dynamic Strategy Loader', LogCategory.STRATEGY, {
      //   error: error instanceof Error ? error.message : String(error)
      // });
    }
  }

  /**
   * Start a bot by ID
   */
  async startBot(userId: string, botId: string): Promise<void> {
    try {
      // Check if bot is already running
      if (this.activeBots.has(botId)) {
        const runtime = this.activeBots.get(botId)!;
        const state = runtime.getState();
        
        if (state.status === 'running') {
          // logger.warn('âš ï¸ Bot is already running', LogCategory.BOT, { botId });
          return;
        }
        
        if (state.status === 'paused') {
          await runtime.resume();
          return;
        }
      }

      // Check concurrent bot limit
      const runningBots = Array.from(this.activeBots.values()).filter(
        runtime => runtime.getState().status === 'running'
      );

      if (runningBots.length >= this.config.maxConcurrentBots) {
        throw new Error(`Maximum concurrent bots limit reached (${this.config.maxConcurrentBots})`);
      }

      // Load bot configuration from database
      const bot = await botService.getBotById(userId, botId);
      if (!bot) {
        throw new Error('Bot not found');
      }

      if (bot.status === 'running') {
        throw new Error('Bot is already marked as running in database');
      }

      // Create bot runtime
      const runtime = await this.createBotRuntime(bot);
      
      // Initialize and start the bot
      await runtime.initialize();
      await runtime.start();

      // Store in active bots
      this.activeBots.set(botId, runtime);

      // Set up event listeners
      this.setupBotEventListeners(runtime);

      // logger.info('âœ… Bot started successfully', LogCategory.BOT, {
      //   botId,
      //   userId,
      //   strategy: bot.strategy
      // });

      // Broadcast status update
      this.broadcastBotStatusUpdate(botId, 'running');

    } catch (error) {
      // logger.error('âŒ Failed to start bot', LogCategory.BOT, {
      //   botId,
      //   userId,
      //   error: error instanceof Error ? error.message : String(error)
      // });

      // Update database status to error
      await botService.updateBotStatus(userId, botId, 'error');
      this.broadcastBotStatusUpdate(botId, 'error');
      
      throw error;
    }
  }

  /**
   * Stop a bot by ID
   */
  async stopBot(userId: string, botId: string): Promise<void> {
    try {
      const runtime = this.activeBots.get(botId);
      if (!runtime) {
        // logger.warn('âš ï¸ Bot runtime not found, updating database status only', LogCategory.BOT, { botId });
        await botService.updateBotStatus(userId, botId, 'stopped');
        this.broadcastBotStatusUpdate(botId, 'stopped');
        return;
      }

      await runtime.stop();
      this.activeBots.delete(botId);

      // logger.info('âœ… Bot stopped successfully', LogCategory.BOT, {
      //   botId,
      //   userId
      // });

      this.broadcastBotStatusUpdate(botId, 'stopped');

    } catch (error) {
      // logger.error('âŒ Failed to stop bot', LogCategory.BOT, {
      //   botId,
      //   userId,
      //   error: error instanceof Error ? error.message : String(error)
      // });
      throw error;
    }
  }

