      // Broadcast update
      this.broadcastBotStatusUpdate(botId, runtime.getState().status);

      return { success: true };

    } catch (error) {
      // logger.error('âŒ Failed to rollback bot strategy', LogCategory.BOT, {
      //   botId,
      //   userId,
      //   error: error instanceof Error ? error.message : String(error)
      // });

      return {
        success: false,
        error: error instanceof Error ? error.message : String(error)
      };
    }
  }

