  }

  /**
   * POST /api/bots/:id/strategy/switch
   * Switch bot strategy at runtime
   */
  async switchBotStrategy(req: AuthenticatedRequest, res: Response): Promise<void> {
    try {
      if (!req.user?.userId) {
        res.status(401).json({
          success: false,
          error: 'User not authenticated',
          timestamp: new Date().toISOString()
        });
        return;
      }

      const botId = req.params.id;
      if (!botId) {
        res.status(400).json({
          success: false,
          error: 'Bot ID is required',
          timestamp: new Date().toISOString()
        });
        return;
      }

      const { strategyType, config, pluginId, options } = req.body;

      if (!strategyType || !config) {
        res.status(400).json({
          success: false,
          error: 'Strategy type and config are required',
          timestamp: new Date().toISOString()
        });
        return;
      }

      console.log('ðŸ”„ API: Switching bot strategy', {
        botId,
        userId: req.user.userId,
        strategyType,
        options
      });

      const result = await botManager.switchBotStrategy(
        req.user.userId,
        botId,
        strategyType,
        {
          type: strategyType,
          parameters: config,
          execution: {
            timeframe: '1m',
            minimumConfidence: 0.7
          }
        },
        options || {
          preserveState: true,
          validateFirst: true,
          rollbackOnError: true
        },
        pluginId
      );

      if (!result.success) {
        res.status(400).json({
          success: false,
          error: result.error || 'Failed to switch strategy',
          timestamp: new Date().toISOString()
        });
        return;
      }

      res.json({
        success: true,
        message: 'Strategy switched successfully',
        warnings: result.warnings,
        timestamp: new Date().toISOString()
      });

    } catch (error) {
      console.error('âŒ API: Failed to switch bot strategy', {
        botId: req.params.id,
        error: error instanceof Error ? error.message : String(error)
      });

      res.status(500).json({
        success: false,
        error: error instanceof Error ? error.message : 'Failed to switch strategy',
        timestamp: new Date().toISOString()
      });
    }
  }

