/**
 * Error Recovery Manager
 * 
 * Comprehensive error handling and recovery system for bot operations.
 * Provides intelligent error classification, recovery strategies, and
 * automated recovery mechanisms.
 */

import { EventEmitter } from 'events';

import logger from '../services/logging.service';

interface Logger {
  info(message: string, meta?: any): void;
  warn(message: string, meta?: any): void;
  error(message: string, meta?: any): void;
}

class LoggerWrapper implements Logger {
  constructor(private context: string) {}

  info(message: string, meta?: any): void {
    logger.info(`[${this.context}] ${message}`, meta);
  }

  warn(message: string, meta?: any): void {
    logger.warn(`[${this.context}] ${message}`, meta);
  }

  error(message: string, meta?: any): void {
    logger.error(`[${this.context}] ${message}`, meta);
  }
}

export type ErrorType = 
  | 'NETWORK_ERROR'
  | 'EXCHANGE_ERROR' 
  | 'STRATEGY_ERROR'
  | 'DATABASE_ERROR'
  | 'VALIDATION_ERROR'
  | 'RATE_LIMIT_ERROR'
  | 'AUTHENTICATION_ERROR'
  | 'CONFIGURATION_ERROR'
  | 'SYSTEM_ERROR'
  | 'UNKNOWN_ERROR';

export type RecoveryStrategy = 
  | 'RETRY'
  | 'EXPONENTIAL_BACKOFF'
  | 'CIRCUIT_BREAKER'
  | 'FALLBACK'
  | 'RESTART'
  | 'ALERT_ADMIN'
  | 'GRACEFUL_SHUTDOWN'
  | 'NO_ACTION';

export interface ErrorContext {
  botId: string;
  operation: string;
  timestamp: Date;
  errorMessage: string;
  errorStack?: string;
  metadata?: Record<string, unknown>;
}

export interface RecoveryConfig {
  maxRetries: number;
  baseRetryDelay: number; // milliseconds
  maxRetryDelay: number; // milliseconds
  circuitBreakerThreshold: number;
  circuitBreakerTimeout: number; // milliseconds
  enableAutoRestart: boolean;
  enableFallbackMode: boolean;
  alertThreshold: number; // error count before alerting admin
}

export interface ErrorClassification {
  type: ErrorType;
  severity: 'LOW' | 'MEDIUM' | 'HIGH' | 'CRITICAL';
  recoveryStrategy: RecoveryStrategy;
  retryable: boolean;
  requiresImmedateAction: boolean;
}

export interface RecoveryAttempt {
  attemptNumber: number;
  strategy: RecoveryStrategy;
  timestamp: Date;
  success: boolean;
  errorMessage?: string;
}

export interface ErrorRecord {
  id: string;
  context: ErrorContext;
  classification: ErrorClassification;
  recoveryAttempts: RecoveryAttempt[];
  resolved: boolean;
  resolvedAt?: Date;
  finalOutcome: 'RECOVERED' | 'FAILED' | 'ESCALATED' | 'PENDING';
}

export class ErrorRecoveryManager extends EventEmitter {
  private config: RecoveryConfig;
  private errorHistory: Map<string, ErrorRecord[]> = new Map();
  private circuitBreakers: Map<string, CircuitBreaker> = new Map();
  private recoveryCallbacks: Map<RecoveryStrategy, Function> = new Map();
  private logger: Logger;

  constructor(config: Partial<RecoveryConfig> = {}) {
    super();
    
    this.config = {
      maxRetries: 3,
      baseRetryDelay: 1000,
      maxRetryDelay: 30000,
      circuitBreakerThreshold: 5,
      circuitBreakerTimeout: 60000,
      enableAutoRestart: true,
      enableFallbackMode: true,
      alertThreshold: 10,
      ...config
    };

    this.logger = new LoggerWrapper('ErrorRecoveryManager');
    this.initializeRecoveryCallbacks();
  }

  /**
   * Handle an error with intelligent recovery
   */
  async handleError(
    error: Error, 
    context: Omit<ErrorContext, 'timestamp' | 'errorMessage' | 'errorStack'>
  ): Promise<boolean> {
    const fullContext: ErrorContext = {
      ...context,
      timestamp: new Date(),
      errorMessage: error.message,
      errorStack: error.stack
    };

    const classification = this.classifyError(error, fullContext);
    const errorRecord = this.createErrorRecord(fullContext, classification);

    // Store error in history
    this.addToErrorHistory(context.botId, errorRecord);

    // Check circuit breaker
    const circuitBreaker = this.getCircuitBreaker(context.botId);
    if (circuitBreaker.isOpen()) {
      this.logger.warn('Circuit breaker is open, skipping recovery attempt', {
        botId: context.botId,
        operation: context.operation
      });
      return false;
    }

    // Attempt recovery
    const recovered = await this.attemptRecovery(errorRecord);

    // Update circuit breaker
    if (recovered) {
      circuitBreaker.recordSuccess();
    } else {
      circuitBreaker.recordFailure();
    }

    // Check if we should alert admin
    this.checkAlertThreshold(context.botId);

    // Emit events
    this.emit('error', errorRecord);
    if (recovered) {
      this.emit('recovery', errorRecord);
    } else {
      this.emit('recovery-failed', errorRecord);
    }

    return recovered;
  }

  /**
   * Classify error and determine recovery strategy
   */
  private classifyError(error: Error, context: ErrorContext): ErrorClassification {
    const message = error.message.toLowerCase();
    const stack = error.stack?.toLowerCase() || '';

    // Network errors
    if (message.includes('network') || message.includes('timeout') || 
        message.includes('connection') || message.includes('enotfound')) {
      return {
        type: 'NETWORK_ERROR',
        severity: 'MEDIUM',
        recoveryStrategy: 'EXPONENTIAL_BACKOFF',
        retryable: true,
        requiresImmedateAction: false
      };
    }

    // Exchange API errors
    if (message.includes('api') || message.includes('rate limit') || 
        message.includes('exchange') || message.includes('order')) {
      const severity = message.includes('rate limit') ? 'LOW' : 'MEDIUM';
      const strategy = message.includes('rate limit') ? 'EXPONENTIAL_BACKOFF' : 'RETRY';
      
      return {
        type: message.includes('rate limit') ? 'RATE_LIMIT_ERROR' : 'EXCHANGE_ERROR',
        severity,
        recoveryStrategy: strategy,
        retryable: true,
        requiresImmedateAction: false
      };
    }

    // Authentication errors
    if (message.includes('auth') || message.includes('unauthorized') || 
        message.includes('forbidden') || message.includes('token')) {
      return {
        type: 'AUTHENTICATION_ERROR',
        severity: 'HIGH',
        recoveryStrategy: 'ALERT_ADMIN',
        retryable: false,
        requiresImmedateAction: true
      };
    }

    // Database errors
    if (message.includes('database') || message.includes('sql') || 
        message.includes('connection pool') || stack.includes('pg')) {
      return {
        type: 'DATABASE_ERROR',
        severity: 'HIGH',
        recoveryStrategy: 'EXPONENTIAL_BACKOFF',
        retryable: true,
        requiresImmedateAction: false
      };
    }

    // Strategy errors
    if (context.operation.includes('strategy') || message.includes('strategy')) {
      return {
        type: 'STRATEGY_ERROR',
        severity: 'MEDIUM',
        recoveryStrategy: 'FALLBACK',
        retryable: true,
        requiresImmedateAction: false
      };
    }

    // Configuration errors
    if (message.includes('config') || message.includes('missing') || 
        message.includes('invalid') || message.includes('required')) {
      return {
        type: 'CONFIGURATION_ERROR',
        severity: 'HIGH',
        recoveryStrategy: 'ALERT_ADMIN',
        retryable: false,
        requiresImmedateAction: true
      };
    }

    // System errors
    if (message.includes('memory') || message.includes('cpu') || 
        message.includes('disk') || message.includes('system')) {
      return {
        type: 'SYSTEM_ERROR',
        severity: 'CRITICAL',
        recoveryStrategy: 'RESTART',
        retryable: false,
        requiresImmedateAction: true
      };
    }

    // Default classification
    return {
      type: 'UNKNOWN_ERROR',
      severity: 'MEDIUM',
      recoveryStrategy: 'RETRY',
      retryable: true,
      requiresImmedateAction: false
    };
  }

  /**
   * Attempt recovery based on classification
   */
  private async attemptRecovery(errorRecord: ErrorRecord): Promise<boolean> {
    const { classification, context } = errorRecord;
    const callback = this.recoveryCallbacks.get(classification.recoveryStrategy);

    if (!callback) {
      this.logger.error('No recovery callback found for strategy', {
        strategy: classification.recoveryStrategy,
        botId: context.botId
      });
      return false;
    }

    const attempt: RecoveryAttempt = {
      attemptNumber: errorRecord.recoveryAttempts.length + 1,
      strategy: classification.recoveryStrategy,
      timestamp: new Date(),
      success: false
    };

    try {
      this.logger.info('Attempting recovery', {
        botId: context.botId,
        strategy: classification.recoveryStrategy,
        attempt: attempt.attemptNumber
      });

      const success = await callback(errorRecord, attempt);
      attempt.success = success;

      if (success) {
        errorRecord.resolved = true;
        errorRecord.resolvedAt = new Date();
        errorRecord.finalOutcome = 'RECOVERED';
      }

      return success;
    } catch (recoveryError) {
      const errorMessage = recoveryError instanceof Error ? 
        recoveryError.message : String(recoveryError);
      
      attempt.errorMessage = errorMessage;
      
      this.logger.error('Recovery attempt failed', {
        botId: context.botId,
        strategy: classification.recoveryStrategy,
        attempt: attempt.attemptNumber,
        error: errorMessage
      });

      return false;
    } finally {
      errorRecord.recoveryAttempts.push(attempt);
    }
  }

  /**
   * Initialize recovery strategy callbacks
   */
  private initializeRecoveryCallbacks(): void {
    this.recoveryCallbacks.set('RETRY', this.handleRetry.bind(this));
    this.recoveryCallbacks.set('EXPONENTIAL_BACKOFF', this.handleExponentialBackoff.bind(this));
    this.recoveryCallbacks.set('CIRCUIT_BREAKER', this.handleCircuitBreaker.bind(this));
    this.recoveryCallbacks.set('FALLBACK', this.handleFallback.bind(this));
    this.recoveryCallbacks.set('RESTART', this.handleRestart.bind(this));
    this.recoveryCallbacks.set('ALERT_ADMIN', this.handleAlertAdmin.bind(this));
    this.recoveryCallbacks.set('GRACEFUL_SHUTDOWN', this.handleGracefulShutdown.bind(this));
    this.recoveryCallbacks.set('NO_ACTION', this.handleNoAction.bind(this));
  }

  /**
   * Recovery strategy implementations
   */
  private async handleRetry(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    if (attempt.attemptNumber > this.config.maxRetries) {
      this.logger.warn('Max retries exceeded', {
        botId: errorRecord.context.botId,
        attempts: attempt.attemptNumber
      });
      return false;
    }

    await this.delay(this.config.baseRetryDelay);
    this.emit('retry', errorRecord, attempt);
    return true; // Caller should retry the operation
  }

  private async handleExponentialBackoff(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    if (attempt.attemptNumber > this.config.maxRetries) {
      return false;
    }

    const delay = Math.min(
      this.config.baseRetryDelay * Math.pow(2, attempt.attemptNumber - 1),
      this.config.maxRetryDelay
    );

    await this.delay(delay);
    this.emit('retry', errorRecord, attempt);
    return true;
  }

  private async handleCircuitBreaker(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    const circuitBreaker = this.getCircuitBreaker(errorRecord.context.botId);
    
    if (circuitBreaker.isOpen()) {
      this.logger.info('Circuit breaker is open, waiting for cooldown', {
        botId: errorRecord.context.botId
      });
      return false;
    }

    return true;
  }

  private async handleFallback(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    if (!this.config.enableFallbackMode) {
      return false;
    }

    this.emit('fallback', errorRecord, attempt);
    this.logger.info('Activating fallback mode', {
      botId: errorRecord.context.botId
    });

    return true;
  }

  private async handleRestart(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    if (!this.config.enableAutoRestart) {
      this.logger.warn('Auto-restart is disabled', {
        botId: errorRecord.context.botId
      });
      return false;
    }

    this.emit('restart-required', errorRecord, attempt);
    this.logger.info('Requesting bot restart', {
      botId: errorRecord.context.botId
    });

    return true;
  }

  private async handleAlertAdmin(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    this.emit('admin-alert', errorRecord, attempt);
    this.logger.error('Admin intervention required', {
      botId: errorRecord.context.botId,
      errorType: errorRecord.classification.type,
      severity: errorRecord.classification.severity
    });

    return false; // Requires manual intervention
  }

  private async handleGracefulShutdown(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    this.emit('shutdown-required', errorRecord, attempt);
    this.logger.warn('Graceful shutdown required', {
      botId: errorRecord.context.botId
    });

    return false;
  }

  private async handleNoAction(errorRecord: ErrorRecord, attempt: RecoveryAttempt): Promise<boolean> {
    this.logger.info('No recovery action required', {
      botId: errorRecord.context.botId
    });

    return true;
  }

  /**
   * Helper methods
   */
  private createErrorRecord(context: ErrorContext, classification: ErrorClassification): ErrorRecord {
    return {
      id: `${context.botId}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      context,
      classification,
      recoveryAttempts: [],
      resolved: false,
      finalOutcome: 'PENDING'
    };
  }

  private addToErrorHistory(botId: string, errorRecord: ErrorRecord): void {
    if (!this.errorHistory.has(botId)) {
      this.errorHistory.set(botId, []);
    }

    const history = this.errorHistory.get(botId)!;
    history.push(errorRecord);

    // Keep only recent errors (last 100)
    if (history.length > 100) {
      history.splice(0, history.length - 100);
    }
  }

  private getCircuitBreaker(botId: string): CircuitBreaker {
    if (!this.circuitBreakers.has(botId)) {
      this.circuitBreakers.set(botId, new CircuitBreaker(
        this.config.circuitBreakerThreshold,
        this.config.circuitBreakerTimeout
      ));
    }

    return this.circuitBreakers.get(botId)!;
  }

  private checkAlertThreshold(botId: string): void {
    const history = this.errorHistory.get(botId) || [];
    const recentErrors = history.filter(
      record => Date.now() - record.context.timestamp.getTime() < 3600000 // Last hour
    );

    if (recentErrors.length >= this.config.alertThreshold) {
      this.emit('alert-threshold-exceeded', {
        botId,
        errorCount: recentErrors.length,
        threshold: this.config.alertThreshold
      });
    }
  }

  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Public methods for monitoring and management
   */
  getErrorHistory(botId: string): ErrorRecord[] {
    return this.errorHistory.get(botId) || [];
  }

  getCircuitBreakerStatus(botId: string): { isOpen: boolean; failureCount: number; lastFailureTime?: Date } {
    const circuitBreaker = this.circuitBreakers.get(botId);
    if (!circuitBreaker) {
      return { isOpen: false, failureCount: 0 };
    }

    return {
      isOpen: circuitBreaker.isOpen(),
      failureCount: circuitBreaker.getFailureCount(),
      lastFailureTime: circuitBreaker.getLastFailureTime()
    };
  }

  clearErrorHistory(botId: string): void {
    this.errorHistory.delete(botId);
  }

  resetCircuitBreaker(botId: string): void {
    const circuitBreaker = this.circuitBreakers.get(botId);
    if (circuitBreaker) {
      circuitBreaker.reset();
    }
  }

  getStats(): {
    totalErrors: number;
    resolvedErrors: number;
    activeCircuitBreakers: number;
    errorsByType: Record<ErrorType, number>;
  } {
    let totalErrors = 0;
    let resolvedErrors = 0;
    const errorsByType: Record<ErrorType, number> = {} as Record<ErrorType, number>;

    for (const history of this.errorHistory.values()) {
      totalErrors += history.length;
      resolvedErrors += history.filter(record => record.resolved).length;
      
      for (const record of history) {
        errorsByType[record.classification.type] = 
          (errorsByType[record.classification.type] || 0) + 1;
      }
    }

    const activeCircuitBreakers = Array.from(this.circuitBreakers.values())
      .filter(cb => cb.isOpen()).length;

    return {
      totalErrors,
      resolvedErrors,
      activeCircuitBreakers,
      errorsByType
    };
  }
}

/**
 * Circuit Breaker Implementation
 */
class CircuitBreaker {
  private failureCount = 0;
  private lastFailureTime?: Date;
  private state: 'CLOSED' | 'OPEN' | 'HALF_OPEN' = 'CLOSED';

  constructor(
    private threshold: number,
    private timeout: number
  ) {}

  recordSuccess(): void {
    this.failureCount = 0;
    this.state = 'CLOSED';
  }

  recordFailure(): void {
    this.failureCount++;
    this.lastFailureTime = new Date();

    if (this.failureCount >= this.threshold) {
      this.state = 'OPEN';
    }
  }

  isOpen(): boolean {
    if (this.state === 'CLOSED') {
      return false;
    }

    if (this.state === 'OPEN') {
      if (this.lastFailureTime && 
          Date.now() - this.lastFailureTime.getTime() > this.timeout) {
        this.state = 'HALF_OPEN';
        return false;
      }
      return true;
    }

    // HALF_OPEN state
    return false;
  }

  getFailureCount(): number {
    return this.failureCount;
  }

  getLastFailureTime(): Date | undefined {
    return this.lastFailureTime;
  }

  reset(): void {
    this.failureCount = 0;
    this.lastFailureTime = undefined;
    this.state = 'CLOSED';
  }
}

export default ErrorRecoveryManager;

/**
 * Enhanced Graceful Shutdown Manager
 * 
 * Provides comprehensive shutdown procedures for bots including:
 * - Position closure and order cancellation
 * - State persistence and backup creation
 * - Resource cleanup and service termination
 * - Configurable shutdown timeouts and priorities
 */

import { EventEmitter } from 'events';

import logger from '../services/logging.service';

import type StateManager from './state-manager';

export interface ShutdownConfig {
  gracePeriod: number; // Total time allowed for graceful shutdown (ms)
  positionTimeout: number; // Time to close positions (ms)
  orderTimeout: number; // Time to cancel orders (ms)
  backupTimeout: number; // Time to create backup (ms)
  forceKillDelay: number; // Delay before force termination (ms)
  enablePositionClosure: boolean; // Whether to close positions on shutdown
  enableOrderCancellation: boolean; // Whether to cancel orders on shutdown
  enableEmergencyBackup: boolean; // Whether to create emergency backup
  retryAttempts: number; // Number of retry attempts for operations
}

export interface ShutdownStep {
  name: string;
  priority: number; // Lower numbers execute first
  timeout: number;
  retryable: boolean;
  execute: () => Promise<void>;
}

export interface ShutdownContext {
  botId: string;
  reason: string;
  signal?: string;
  startedAt: Date;
  steps: ShutdownStep[];
  completedSteps: string[];
  failedSteps: string[];
  currentState: any;
}

export class GracefulShutdownManager extends EventEmitter {
  private config: ShutdownConfig;
  private stateManager: StateManager;
  private shutdownContexts: Map<string, ShutdownContext> = new Map();
  private isShuttingDown = false;

  constructor(
    stateManager: StateManager,
    config: Partial<ShutdownConfig> = {}
  ) {
    super();

    this.stateManager = stateManager;
    this.config = {
      gracePeriod: 60000, // 1 minute
      positionTimeout: 30000, // 30 seconds
      orderTimeout: 15000, // 15 seconds
      backupTimeout: 10000, // 10 seconds
      forceKillDelay: 5000, // 5 seconds
      enablePositionClosure: true,
      enableOrderCancellation: true,
      enableEmergencyBackup: true,
      retryAttempts: 3,
      ...config
    };

    this.setupSignalHandlers();
  }

  /**
   * Initiate graceful shutdown for a bot
   */
  async initiateShutdown(
    botId: string,
    reason: string,
    signal?: string,
    currentState?: any
  ): Promise<void> {
    if (this.shutdownContexts.has(botId)) {
      logger.warn('[GracefulShutdown] Shutdown already in progress', { botId });
      return;
    }

    logger.info('[GracefulShutdown] Initiating graceful shutdown', {
      botId,
      reason,
      signal
    });

    const context: ShutdownContext = {
      botId,
      reason,
      signal,
      startedAt: new Date(),
      steps: this.createShutdownSteps(botId, currentState),
      completedSteps: [],
      failedSteps: [],
      currentState: currentState || {}
    };

    this.shutdownContexts.set(botId, context);
    this.emit('shutdown-initiated', context);

    try {
      await this.executeShutdownSequence(context);
      
      logger.info('[GracefulShutdown] Graceful shutdown completed', {
        botId,
        duration: Date.now() - context.startedAt.getTime(),
        completedSteps: context.completedSteps.length,
        failedSteps: context.failedSteps.length
      });

      this.emit('shutdown-completed', context);
    } catch (error) {
      logger.error('[GracefulShutdown] Shutdown failed', {
        botId,
        error: error instanceof Error ? error.message : String(error),
        context
      });

      this.emit('shutdown-failed', { context, error });
      
      // Force termination after delay
      setTimeout(() => {
        this.forceTermination(botId, 'Graceful shutdown failed');
      }, this.config.forceKillDelay);
    } finally {
      this.shutdownContexts.delete(botId);
    }
  }

  /**
   * Execute shutdown sequence with timeout and retry handling
   */
  private async executeShutdownSequence(context: ShutdownContext): Promise<void> {
    const { botId, steps } = context;
    
    // Sort steps by priority
    const sortedSteps = steps.sort((a, b) => a.priority - b.priority);
    
    const overallTimeout = setTimeout(() => {
      throw new Error(`Shutdown sequence timeout after ${this.config.gracePeriod}ms`);
    }, this.config.gracePeriod);

    try {
      for (const step of sortedSteps) {
        await this.executeStep(context, step);
      }
    } finally {
      clearTimeout(overallTimeout);
    }
  }

  /**
   * Execute individual shutdown step with timeout and retry
   */
  private async executeStep(context: ShutdownContext, step: ShutdownStep): Promise<void> {
    const { botId } = context;
    let attempts = 0;
    let lastError: Error | null = null;

    logger.info('[GracefulShutdown] Executing step', {
      botId,
      step: step.name,
      priority: step.priority,
      timeout: step.timeout
    });

    this.emit('step-started', { context, step });

    while (attempts <= this.config.retryAttempts) {
      const stepTimeout = setTimeout(() => {
        throw new Error(`Step '${step.name}' timeout after ${step.timeout}ms`);
      }, step.timeout);

      try {
        await step.execute();
        clearTimeout(stepTimeout);
        
        context.completedSteps.push(step.name);
        
        logger.info('[GracefulShutdown] Step completed', {
          botId,
          step: step.name,
          attempts: attempts + 1
        });

        this.emit('step-completed', { context, step });
        return;
      } catch (error) {
        clearTimeout(stepTimeout);
        lastError = error instanceof Error ? error : new Error(String(error));
        attempts++;

        logger.warn('[GracefulShutdown] Step failed', {
          botId,
          step: step.name,
          attempt: attempts,
          error: lastError.message,
          retryable: step.retryable
        });

        if (!step.retryable || attempts > this.config.retryAttempts) {
          break;
        }

        // Exponential backoff for retries
        await new Promise(resolve => setTimeout(resolve, Math.pow(2, attempts - 1) * 1000));
      }
    }

    // Step failed after all attempts
    context.failedSteps.push(step.name);
    
    logger.error('[GracefulShutdown] Step failed permanently', {
      botId,
      step: step.name,
      attempts,
      error: lastError?.message
    });

    this.emit('step-failed', { context, step, error: lastError });

    // Continue with other steps unless this is critical
    if (step.name.includes('backup') || step.name.includes('state')) {
      // Non-critical steps, continue
      return;
    }

    throw lastError || new Error(`Step '${step.name}' failed after ${attempts} attempts`);
  }

  /**
   * Create shutdown steps based on configuration
   */
  private createShutdownSteps(botId: string, currentState: any): ShutdownStep[] {
    const steps: ShutdownStep[] = [];

    // Step 1: Cancel all pending orders
    if (this.config.enableOrderCancellation) {
      steps.push({
        name: 'cancel-orders',
        priority: 1,
        timeout: this.config.orderTimeout,
        retryable: true,
        execute: async () => {
          await this.cancelAllOrders(botId, currentState);
        }
      });
    }

    // Step 2: Close all positions
    if (this.config.enablePositionClosure) {
      steps.push({
        name: 'close-positions',
        priority: 2,
        timeout: this.config.positionTimeout,
        retryable: true,
        execute: async () => {
          await this.closeAllPositions(botId, currentState);
        }
      });
    }

    // Step 3: Create emergency backup
    if (this.config.enableEmergencyBackup) {
      steps.push({
        name: 'create-backup',
        priority: 3,
        timeout: this.config.backupTimeout,
        retryable: true,
        execute: async () => {
          await this.createEmergencyBackup(botId, currentState);
        }
      });
    }

    // Step 4: Persist final state
    steps.push({
      name: 'persist-state',
      priority: 4,
      timeout: this.config.backupTimeout,
      retryable: true,
      execute: async () => {
        await this.persistFinalState(botId, currentState);
      }
    });

    // Step 5: Cleanup resources
    steps.push({
      name: 'cleanup-resources',
      priority: 5,
      timeout: 5000,
      retryable: false,
      execute: async () => {
        await this.cleanupResources(botId);
      }
    });

    // Step 6: Stop services
    steps.push({
      name: 'stop-services',
      priority: 6,
      timeout: 5000,
      retryable: false,
      execute: async () => {
        await this.stopServices(botId);
      }
    });

    return steps;
  }

  /**
   * Cancel all pending orders
   */
  private async cancelAllOrders(botId: string, currentState: any): Promise<void> {
    try {
      const pendingOrders = currentState.orders?.filter(
        (order: any) => order.status === 'pending' || order.status === 'open'
      ) || [];

      if (pendingOrders.length === 0) {
        logger.info('[GracefulShutdown] No pending orders to cancel', { botId });
        return;
      }

      logger.info('[GracefulShutdown] Cancelling orders', {
        botId,
        orderCount: pendingOrders.length
      });

      // This would integrate with the exchange service
      // For now, we'll simulate the cancellation
      for (const order of pendingOrders) {
        logger.info('[GracefulShutdown] Cancelling order', {
          botId,
          orderId: order.id,
          symbol: order.symbol
        });
        
        // TODO: Integrate with actual exchange cancellation
        // await exchangeService.cancelOrder(order.id);
      }

      this.emit('orders-cancelled', { botId, orderCount: pendingOrders.length });
    } catch (error) {
      logger.error('[GracefulShutdown] Failed to cancel orders', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Close all open positions
   */
  private async closeAllPositions(botId: string, currentState: any): Promise<void> {
    try {
      const openPositions = currentState.positions?.filter(
        (position: any) => position.size !== 0
      ) || [];

      if (openPositions.length === 0) {
        logger.info('[GracefulShutdown] No open positions to close', { botId });
        return;
      }

      logger.info('[GracefulShutdown] Closing positions', {
        botId,
        positionCount: openPositions.length
      });

      // This would integrate with the exchange service
      for (const position of openPositions) {
        logger.info('[GracefulShutdown] Closing position', {
          botId,
          symbol: position.symbol,
          size: position.size,
          pnl: position.pnl
        });
        
        // TODO: Integrate with actual position closure
        // await exchangeService.closePosition(position.symbol, position.size);
      }

      this.emit('positions-closed', { botId, positionCount: openPositions.length });
    } catch (error) {
      logger.error('[GracefulShutdown] Failed to close positions', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Create emergency backup
   */
  private async createEmergencyBackup(botId: string, currentState: any): Promise<void> {
    try {
      logger.info('[GracefulShutdown] Creating emergency backup', { botId });
      
      await this.stateManager.createEmergencyBackup(
        botId,
        currentState,
        'Graceful shutdown emergency backup'
      );

      this.emit('backup-created', { botId });
    } catch (error) {
      logger.error('[GracefulShutdown] Failed to create emergency backup', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Persist final state
   */
  private async persistFinalState(botId: string, currentState: any): Promise<void> {
    try {
      logger.info('[GracefulShutdown] Persisting final state', { botId });
      
      const finalState = {
        ...currentState,
        status: 'shutdown',
        shutdownAt: new Date(),
        runtimeMetrics: {
          ...currentState.runtimeMetrics,
          lastTickAt: new Date(),
          shutdownAt: new Date()
        }
      };

      const snapshot = await this.stateManager.createSnapshot(botId, finalState);
      await this.stateManager.saveSnapshot(snapshot);

      this.emit('state-persisted', { botId });
    } catch (error) {
      logger.error('[GracefulShutdown] Failed to persist final state', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Cleanup resources
   */
  private async cleanupResources(botId: string): Promise<void> {
    try {
      logger.info('[GracefulShutdown] Cleaning up resources', { botId });
      
      // Clear timers, intervals, subscriptions, etc.
      // This would be bot-specific cleanup
      
      this.emit('resources-cleaned', { botId });
    } catch (error) {
      logger.error('[GracefulShutdown] Failed to cleanup resources', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Stop services
   */
  private async stopServices(botId: string): Promise<void> {
    try {
      logger.info('[GracefulShutdown] Stopping services', { botId });
      
      // Stop websocket connections, database connections, etc.
      // This would integrate with service managers
      
      this.emit('services-stopped', { botId });
    } catch (error) {
      logger.error('[GracefulShutdown] Failed to stop services', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Force termination as last resort
   */
  private forceTermination(botId: string, reason: string): void {
    logger.error('[GracefulShutdown] Force terminating bot', { botId, reason });
    
    this.emit('force-termination', { botId, reason });
    
    // This would forcefully terminate the bot process
    // Implementation depends on how bots are managed (child processes, workers, etc.)
  }

  /**
   * Setup signal handlers for process-wide shutdown
   */
  private setupSignalHandlers(): void {
    const signals: NodeJS.Signals[] = ['SIGINT', 'SIGTERM', 'SIGQUIT'];
    
    signals.forEach(signal => {
      process.on(signal, () => {
        if (this.isShuttingDown) {return;}
        
        this.isShuttingDown = true;
        logger.info('[GracefulShutdown] Process signal received', { signal });
        
        this.emit('process-shutdown-signal', { signal });
        
        // Initiate shutdown for all active bots
        const activeBots = Array.from(this.shutdownContexts.keys());
        
        Promise.all(
          activeBots.map(botId =>
            this.initiateShutdown(botId, `Process ${signal}`, signal)
          )
        ).finally(() => {
          logger.info('[GracefulShutdown] All bots shutdown complete');
          process.exit(0);
        });
      });
    });
  }

  /**
   * Get shutdown status for a bot
   */
  getShutdownStatus(botId: string): ShutdownContext | null {
    return this.shutdownContexts.get(botId) || null;
  }

  /**
   * Check if shutdown is in progress
   */
  isShutdownInProgress(botId?: string): boolean {
    if (botId) {
      return this.shutdownContexts.has(botId);
    }
    return this.shutdownContexts.size > 0 || this.isShuttingDown;
  }

  /**
   * Emergency shutdown (skip graceful procedures)
   */
  async emergencyShutdown(botId: string, reason: string): Promise<void> {
    logger.warn('[GracefulShutdown] Emergency shutdown initiated', { botId, reason });
    
    try {
      // Only perform critical operations
      const currentState = {}; // Get from bot runtime
      
      await this.createEmergencyBackup(botId, currentState);
      await this.persistFinalState(botId, currentState);
      
      this.emit('emergency-shutdown', { botId, reason });
    } catch (error) {
      logger.error('[GracefulShutdown] Emergency shutdown failed', {
        botId,
        reason,
        error: error instanceof Error ? error.message : String(error)
      });
    }
  }

  /**
   * Cleanup and shutdown the manager itself
   */
  shutdown(): void {
    logger.info('[GracefulShutdown] Shutting down graceful shutdown manager');
    
    // Cancel any ongoing shutdowns
    this.shutdownContexts.clear();
    this.isShuttingDown = true;
    
    this.removeAllListeners();
    
    logger.info('[GracefulShutdown] Graceful shutdown manager stopped');
  }
}

export default GracefulShutdownManager;

/**
 * Health Check Service
 * 
 * Provides comprehensive system health validation endpoints and monitoring.
 * Validates database connections, exchange connectivity, system resources,
 * and overall application health.
 */

import type { Request, Response } from 'express';

import { database } from '../services/database.service';
import logger from '../services/logging.service';

import type { BotManager } from './bot-manager';
import type { BotWatchdog } from './bot-watchdog';
import type { ErrorRecoveryManager } from './error-recovery-manager';

export interface HealthStatus {
  status: 'healthy' | 'degraded' | 'unhealthy' | 'critical';
  timestamp: string;
  uptime: number;
  version: string;
  environment: string;
}

export interface ComponentHealth {
  name: string;
  status: 'healthy' | 'degraded' | 'unhealthy' | 'critical';
  responseTime: number;
  message?: string;
  details?: Record<string, unknown>;
  lastChecked: string;
}

export interface SystemMetrics {
  memory: {
    used: number;
    total: number;
    percentage: number;
  };
  cpu: {
    usage: number;
    load: number[];
  };
  disk: {
    used: number;
    total: number;
    percentage: number;
  };
  uptime: number;
}

export interface HealthCheckResponse {
  overall: HealthStatus;
  components: ComponentHealth[];
  metrics: SystemMetrics;
  bots: {
    total: number;
    healthy: number;
    unhealthy: number;
    dead: number;
  };
  errors: {
    total: number;
    resolved: number;
    activeCircuitBreakers: number;
  };
}

export class HealthCheckService {
  private botManager: BotManager;
  private errorRecoveryManager: ErrorRecoveryManager;
  private botWatchdog: BotWatchdog;
  private startTime: Date;

  constructor(
    botManager: BotManager,
    errorRecoveryManager: ErrorRecoveryManager,
    botWatchdog: BotWatchdog
  ) {
    this.botManager = botManager;
    this.errorRecoveryManager = errorRecoveryManager;
    this.botWatchdog = botWatchdog;
    this.startTime = new Date();
  }

  /**
   * Main health check endpoint
   */
  async getHealthStatus(): Promise<HealthCheckResponse> {
    const components: ComponentHealth[] = [];
    
    // Check all components
    const databaseHealth = await this.checkDatabaseHealth();
    components.push(databaseHealth);

    const exchangeHealth = await this.checkExchangeHealth();
    components.push(exchangeHealth);

    const systemHealth = await this.checkSystemHealth();
    components.push(systemHealth);

    const watchdogHealth = await this.checkWatchdogHealth();
    components.push(watchdogHealth);

    // Get system metrics
    const metrics = await this.getSystemMetrics();

    // Get bot statistics
    const botStats = this.getBotStatistics();

    // Get error statistics
    const errorStats = this.getErrorStatistics();

    // Determine overall health
    const overall = this.determineOverallHealth(components, metrics);

    return {
      overall,
      components,
      metrics,
      bots: botStats,
      errors: errorStats
    };
  }

  /**
   * Simple liveness probe (for Kubernetes)
   */
  async liveness(): Promise<{ status: 'alive' | 'dead'; timestamp: string }> {
    try {
      // Basic application responsiveness check
      const testResponse = Date.now();
      
      return {
        status: 'alive',
        timestamp: new Date().toISOString()
      };
    } catch (error) {
      return {
        status: 'dead',
        timestamp: new Date().toISOString()
      };
    }
  }

  /**
   * Readiness probe (for Kubernetes)
   */
  async readiness(): Promise<{ 
    status: 'ready' | 'not-ready'; 
    timestamp: string; 
    checks: Record<string, boolean> 
  }> {
    const checks = {
      database: false,
      watchdog: false,
      errorRecovery: false
    };

    try {
      // Check database connectivity
      checks.database = await this.isDatabaseReady();
      
      // Check watchdog service
      checks.watchdog = this.botWatchdog.isRunning();
      
      // Check error recovery service
      checks.errorRecovery = true; // ErrorRecoveryManager is always ready if instantiated

      const allReady = Object.values(checks).every(check => check);

      return {
        status: allReady ? 'ready' : 'not-ready',
        timestamp: new Date().toISOString(),
        checks
      };
    } catch (error) {
      logger.error('[HealthCheck] Readiness check failed', { error });
      
      return {
        status: 'not-ready',
        timestamp: new Date().toISOString(),
        checks
      };
    }
  }

  /**
   * Component health checks
   */
  private async checkDatabaseHealth(): Promise<ComponentHealth> {
    const startTime = Date.now();
    
    try {
      // Test database connection with a simple query
      const result = await database.query('SELECT 1 as health_check');
      const responseTime = Date.now() - startTime;
      
      if (result && result.length > 0 && result.at(0).health_check === 1) {
        return {
          name: 'database',
          status: responseTime > 1000 ? 'degraded' : 'healthy',
          responseTime,
          message: responseTime > 1000 ? 'Slow response time' : 'Connected',
          lastChecked: new Date().toISOString()
        };
      } 
        return {
          name: 'database',
          status: 'unhealthy',
          responseTime,
          message: 'Invalid response from database',
          lastChecked: new Date().toISOString()
        };
      
    } catch (error) {
      const responseTime = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      return {
        name: 'database',
        status: 'critical',
        responseTime,
        message: `Database connection failed: ${errorMessage}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private async checkExchangeHealth(): Promise<ComponentHealth> {
    const startTime = Date.now();
    
    try {
      // This would check exchange connectivity
      // For now, we'll simulate based on error rates
      const errorStats = this.errorRecoveryManager.getStats();
      const exchangeErrors = errorStats.errorsByType.EXCHANGE_ERROR || 0;
      const responseTime = Date.now() - startTime;
      
      let status: ComponentHealth['status'] = 'healthy';
      let message = 'Exchange connections healthy';
      
      if (exchangeErrors > 50) {
        status = 'critical';
        message = `High exchange error count: ${exchangeErrors}`;
      } else if (exchangeErrors > 20) {
        status = 'degraded';
        message = `Moderate exchange error count: ${exchangeErrors}`;
      } else if (exchangeErrors > 10) {
        status = 'degraded';
        message = `Some exchange errors detected: ${exchangeErrors}`;
      }

      return {
        name: 'exchanges',
        status,
        responseTime,
        message,
        details: {
          exchangeErrors,
          activeCircuitBreakers: errorStats.activeCircuitBreakers
        },
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      const responseTime = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      return {
        name: 'exchanges',
        status: 'critical',
        responseTime,
        message: `Exchange health check failed: ${errorMessage}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private async checkSystemHealth(): Promise<ComponentHealth> {
    const startTime = Date.now();
    
    try {
      const metrics = await this.getSystemMetrics();
      const responseTime = Date.now() - startTime;
      
      let status: ComponentHealth['status'] = 'healthy';
      const issues: string[] = [];
      
      if (metrics.memory.percentage > 90) {
        status = 'critical';
        issues.push(`High memory usage: ${metrics.memory.percentage.toFixed(1)}%`);
      } else if (metrics.memory.percentage > 80) {
        status = 'degraded';
        issues.push(`Elevated memory usage: ${metrics.memory.percentage.toFixed(1)}%`);
      }
      
      if (metrics.cpu.usage > 90) {
        status = 'critical';
        issues.push(`High CPU usage: ${metrics.cpu.usage.toFixed(1)}%`);
      } else if (metrics.cpu.usage > 80) {
        status = 'degraded';
        issues.push(`Elevated CPU usage: ${metrics.cpu.usage.toFixed(1)}%`);
      }
      
      if (metrics.disk.percentage > 95) {
        status = 'critical';
        issues.push(`High disk usage: ${metrics.disk.percentage.toFixed(1)}%`);
      } else if (metrics.disk.percentage > 85) {
        status = 'degraded';
        issues.push(`Elevated disk usage: ${metrics.disk.percentage.toFixed(1)}%`);
      }

      return {
        name: 'system',
        status,
        responseTime,
        message: issues.length > 0 ? issues.join(', ') : 'System resources normal',
        details: JSON.parse(JSON.stringify(metrics)),
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      const responseTime = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      return {
        name: 'system',
        status: 'critical',
        responseTime,
        message: `System health check failed: ${errorMessage}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private async checkWatchdogHealth(): Promise<ComponentHealth> {
    const startTime = Date.now();
    
    try {
      const watchdogStats = this.botWatchdog.getWatchdogStats();
      const responseTime = Date.now() - startTime;
      
      let status: ComponentHealth['status'] = 'healthy';
      let message = 'Watchdog service operational';
      
      if (!watchdogStats.running) {
        status = 'critical';
        message = 'Watchdog service is not running';
      } else if (watchdogStats.deadBots > 0) {
        status = 'degraded';
        message = `${watchdogStats.deadBots} dead bots detected`;
      } else if (watchdogStats.unhealthyBots > 0) {
        status = 'degraded';
        message = `${watchdogStats.unhealthyBots} unhealthy bots detected`;
      }

      return {
        name: 'watchdog',
        status,
        responseTime,
        message,
        details: watchdogStats,
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      const responseTime = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      return {
        name: 'watchdog',
        status: 'critical',
        responseTime,
        message: `Watchdog health check failed: ${errorMessage}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  /**
   * Get system metrics
   */
  private async getSystemMetrics(): Promise<SystemMetrics> {
    try {
      const process = await import('process');
      const os = await import('os');
      const fs = await import('fs');
      
      // Memory metrics
      const memoryUsage = process.memoryUsage();
      const totalMemory = os.totalmem();
      const usedMemory = memoryUsage.heapUsed + memoryUsage.external;
      
      // CPU metrics
      const cpus = os.cpus();
      const loadAvg = os.loadavg();
      
      // Disk metrics (approximate)
      let diskUsed = 0;
      let diskTotal = 1;
      try {
        const stats = fs.statSync('.');
        // This is a simplified disk check
        diskTotal = 1000000000; // 1GB default
        diskUsed = 100000000; // 100MB default
      } catch (error) {
        // Use defaults if disk check fails
      }

      return {
        memory: {
          used: Math.round(usedMemory / 1024 / 1024), // MB
          total: Math.round(totalMemory / 1024 / 1024), // MB
          percentage: (usedMemory / totalMemory) * 100
        },
        cpu: {
          usage: cpus.length > 0 ? ((loadAvg.at(0) || 0) * 100 / cpus.length) : 0, // Approximate CPU usage
          load: loadAvg
        },
        disk: {
          used: Math.round(diskUsed / 1024 / 1024), // MB
          total: Math.round(diskTotal / 1024 / 1024), // MB
          percentage: (diskUsed / diskTotal) * 100
        },
        uptime: Date.now() - this.startTime.getTime()
      };
    } catch (error) {
      // Return default metrics if system check fails
      return {
        memory: { used: 0, total: 0, percentage: 0 },
        cpu: { usage: 0, load: [0, 0, 0] },
        disk: { used: 0, total: 0, percentage: 0 },
        uptime: Date.now() - this.startTime.getTime()
      };
    }
  }

  /**
   * Get bot statistics
   */
  private getBotStatistics(): {
    total: number;
    healthy: number;
    unhealthy: number;
    dead: number;
  } {
    const watchdogStats = this.botWatchdog.getWatchdogStats();
    
    return {
      total: watchdogStats.monitoredBots,
      healthy: watchdogStats.healthyBots,
      unhealthy: watchdogStats.unhealthyBots,
      dead: watchdogStats.deadBots
    };
  }

  /**
   * Get error statistics
   */
  private getErrorStatistics(): {
    total: number;
    resolved: number;
    activeCircuitBreakers: number;
  } {
    const errorStats = this.errorRecoveryManager.getStats();
    
    return {
      total: errorStats.totalErrors,
      resolved: errorStats.resolvedErrors,
      activeCircuitBreakers: errorStats.activeCircuitBreakers
    };
  }

  /**
   * Determine overall health status
   */
  private determineOverallHealth(
    components: ComponentHealth[], 
    metrics: SystemMetrics
  ): HealthStatus {
    const criticalComponents = components.filter(c => c.status === 'critical');
    const unhealthyComponents = components.filter(c => c.status === 'unhealthy');
    const degradedComponents = components.filter(c => c.status === 'degraded');
    
    let status: HealthStatus['status'] = 'healthy';
    
    if (criticalComponents.length > 0) {
      status = 'critical';
    } else if (unhealthyComponents.length > 0) {
      status = 'unhealthy';
    } else if (degradedComponents.length > 0) {
      status = 'degraded';
    }
    
    // Also consider system metrics
    if (metrics.memory.percentage > 95 || metrics.cpu.usage > 95) {
      status = 'critical';
    } else if (metrics.memory.percentage > 85 || metrics.cpu.usage > 85) {
      if (status === 'healthy') {
        status = 'degraded';
      }
    }

    return {
      status,
      timestamp: new Date().toISOString(),
      uptime: metrics.uptime,
      version: process.env.npm_package_version || '1.0.0',
      environment: process.env.NODE_ENV || 'development'
    };
  }

  /**
   * Check if database is ready
   */
  private async isDatabaseReady(): Promise<boolean> {
    try {
      const result = await database.query('SELECT 1');
      return result.length > 0;
    } catch (error) {
      return false;
    }
  }

  /**
   * Express route handlers
   */
  healthHandler = async (req: Request, res: Response): Promise<void> => {
    try {
      const healthStatus = await this.getHealthStatus();
      
      const statusCode = this.getHttpStatusCode(healthStatus.overall.status);
      res.status(statusCode).json(healthStatus);
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('[HealthCheck] Health check failed', { error: errorMessage });
      
      res.status(503).json({
        overall: {
          status: 'critical',
          timestamp: new Date().toISOString(),
          uptime: Date.now() - this.startTime.getTime(),
          version: process.env.npm_package_version || '1.0.0',
          environment: process.env.NODE_ENV || 'development'
        },
        error: errorMessage
      });
    }
  };

  livenessHandler = async (req: Request, res: Response): Promise<void> => {
    try {
      const liveness = await this.liveness();
      const statusCode = liveness.status === 'alive' ? 200 : 503;
      res.status(statusCode).json(liveness);
    } catch (error) {
      res.status(503).json({
        status: 'dead',
        timestamp: new Date().toISOString()
      });
    }
  };

  readinessHandler = async (req: Request, res: Response): Promise<void> => {
    try {
      const readiness = await this.readiness();
      const statusCode = readiness.status === 'ready' ? 200 : 503;
      res.status(statusCode).json(readiness);
    } catch (error) {
      res.status(503).json({
        status: 'not-ready',
        timestamp: new Date().toISOString(),
        checks: {}
      });
    }
  };

  /**
   * Get HTTP status code for health status
   */
  private getHttpStatusCode(status: HealthStatus['status']): number {
    switch (status) {
      case 'healthy':
        return 200;
      case 'degraded':
        return 200; // Still operational
      case 'unhealthy':
        return 503;
      case 'critical':
        return 503;
      default:
        return 503;
    }
  }
}

