  signalId: string;
  validationsPassed: string[];
  validationsFailed: string[];
}

export class SignalProcessor extends EventEmitter {
  private config: SignalProcessingConfig;
  private signalHistory: Map<string, Date[]> = new Map();
  private processingStats = {
    totalProcessed: 0,
    approved: 0,
    rejected: 0,
    errors: 0
  };

  constructor(config?: Partial<SignalProcessingConfig>) {
    super();
    
    this.config = {
      enableRiskValidation: true,
      enableSignalEnrichment: true,
      minConfidenceThreshold: 0.6,
      maxSignalsPerMinute: 10,
      signalTimeoutMs: 5000,
      ...config
    };

    // Clean up old signal history every minute
    setInterval(() => this.cleanupSignalHistory(), 60000);
  }

  /**
   * Process a strategy result into a validated trade signal
   */
  async processSignal(
    strategyResult: StrategyResult,
    bot: Bot,
    context: StrategyContext
  ): Promise<ProcessedSignal | null> {
    const startTime = Date.now();
    const signalId = this.generateSignalId(bot.id);

    try {
      console.log('ðŸ”„ Processing strategy signal', {
        botId: bot.id,
        signalId,
        action: strategyResult.action,
        confidence: strategyResult.confidence
      });

      this.processingStats.totalProcessed++;

      // Step 1: Basic validation
      const basicValidation = this.validateBasicSignal(strategyResult, bot);
      if (!basicValidation.valid) {
        console.warn('âš ï¸ Basic signal validation failed', {
          botId: bot.id,
          signalId,
          reason: basicValidation.reason
        });
        this.processingStats.rejected++;
        return null;
      }

      // Step 2: Rate limiting check
      const rateLimitCheck = this.checkRateLimit(bot.id);
      if (!rateLimitCheck.allowed) {
        console.warn('âš ï¸ Signal rate limit exceeded', {
          botId: bot.id,
          signalId,
          reason: rateLimitCheck.reason
        });
        this.processingStats.rejected++;
        return null;
      }

      // Step 3: Convert to trade signal
      const tradeSignal = await this.convertToTradeSignal(strategyResult, bot, context);

      // Step 4: Enrich with market context
      let marketContext: MarketContext | null = null;
      if (this.config.enableSignalEnrichment) {
        marketContext = await this.enrichWithMarketData(tradeSignal, context);
      }

      // Step 5: Risk assessment
      let riskAssessment: RiskAssessment | null = null;
      if (this.config.enableRiskValidation) {
        riskAssessment = await this.assessRisk(tradeSignal, bot, marketContext);
        
        if (!riskAssessment.approved) {
          console.warn('âš ï¸ Signal rejected by risk assessment', {
            botId: bot.id,
            signalId,
            reason: riskAssessment.rejectionReason,
            warnings: riskAssessment.warnings
          });
          this.processingStats.rejected++;
          return null;
        }
      }

      // Step 6: Record signal for rate limiting
      this.recordSignal(bot.id);

      const processingTime = Date.now() - startTime;
      const processedSignal: ProcessedSignal = {
        original: strategyResult,
        tradeSignal,
        riskAssessment: riskAssessment || {
          approved: true,
          riskScore: 0.5,
          warnings: []
        },
        marketContext: marketContext || {
          currentPrice: tradeSignal.price,
          spread: 0,
          volume: 0,
          volatility: 0,
          marketHours: true,
          liquidityScore: 1
        },
        processingMetadata: {
          processedAt: new Date(),
          processingTimeMs: processingTime,
          signalId,
          validationsPassed: ['basic', 'rate-limit'],
          validationsFailed: []
        }
      };

      this.processingStats.approved++;

      console.log('âœ… Signal processed successfully', {
        botId: bot.id,
        signalId,
        processingTimeMs: processingTime,
        riskScore: riskAssessment?.riskScore
      });

      this.emit('signal-processed', {
        botId: bot.id,
        signalId,
        approved: true,
        processingTime
      });

      return processedSignal;

    } catch (error) {
      this.processingStats.errors++;
      
      console.error('âŒ Error processing signal', {
        botId: bot.id,
        signalId,
        error: error instanceof Error ? error.message : String(error)
      });

      this.emit('signal-error', {
        botId: bot.id,
        signalId,
        error: error instanceof Error ? error.message : String(error)
      });

      return null;
    }
  }

  /**
   * Basic signal validation
   */
  private validateBasicSignal(
    result: StrategyResult,
    bot: Bot
  ): { valid: boolean; reason?: string } {
    // Check if result is successful
    if (!result.success) {
      return { valid: false, reason: `Strategy execution failed: ${result.error}` };
    }

    // Check if action is valid
    if (!result.action || result.action === 'hold') {
      return { valid: false, reason: 'No actionable signal (hold or undefined)' };
    }

    // Check confidence threshold
    const confidence = result.confidence || 0;
    if (confidence < this.config.minConfidenceThreshold) {
      return { 
        valid: false, 
        reason: `Confidence ${confidence} below threshold ${this.config.minConfidenceThreshold}` 
      };
    }

    // Check bot configuration
    if (!bot.configuration) {
      return { valid: false, reason: 'Bot configuration missing' };
    }

    return { valid: true };
  }

  /**
   * Check rate limiting for signals
   */
  private checkRateLimit(botId: string): { allowed: boolean; reason?: string } {
    const now = new Date();
    const oneMinuteAgo = new Date(now.getTime() - 60000);
    
    const recentSignals = this.signalHistory.get(botId) || [];
    const signalsInLastMinute = recentSignals.filter(time => time > oneMinuteAgo);

    if (signalsInLastMinute.length >= this.config.maxSignalsPerMinute) {
      return {
        allowed: false,
        reason: `Rate limit exceeded: ${signalsInLastMinute.length}/${this.config.maxSignalsPerMinute} signals in last minute`
      };
    }

    return { allowed: true };
  }

  /**
   * Convert strategy result to trade signal
   */
  private async convertToTradeSignal(
    result: StrategyResult,
    bot: Bot,
    context: StrategyContext
  ): Promise<TradeSignal> {
    // Get current market price
    const currentPrice = await context.marketData.getCurrentPrice(context.symbol);

    const tradeSignal: TradeSignal = {
      id: this.generateSignalId(bot.id),
      botId: bot.id,
      symbol: context.symbol,
      side: result.action === 'buy' ? 'buy' : 'sell',
      confidence: result.confidence || 0.5,
      price: currentPrice,
      timestamp: Date.now(),
      reason: result.reason || 'Strategy generated signal'
    };

    return tradeSignal;
  }

  /**
   * Enrich signal with market data
   */
  private async enrichWithMarketData(
    signal: TradeSignal,
    context: StrategyContext
  ): Promise<MarketContext> {
    try {
      const [ticker, orderBook] = await Promise.all([
        context.marketData.getTicker(signal.symbol),
        context.marketData.getOrderBook(signal.symbol, 10)
      ]);

      const spread = ticker.ask - ticker.bid;
      const spreadPercent = (spread / ticker.last) * 100;

      return {
        currentPrice: ticker.last,
        spread: spreadPercent,
        volume: ticker.volume,
        volatility: this.calculateVolatility(ticker),
        marketHours: this.isMarketHours(),
        liquidityScore: this.calculateLiquidityScore(orderBook)
      };
    } catch (error) {
      console.warn('âš ï¸ Failed to enrich signal with market data', {
        signalId: signal.id,
        error: error instanceof Error ? error.message : String(error)
      });

      return {
        currentPrice: signal.price,
        spread: 0,
        volume: 0,
        volatility: 0,
        marketHours: true,
        liquidityScore: 1
      };
    }
  }

  /**
   * Assess risk for the signal
   */
  private async assessRisk(
    signal: TradeSignal,
    bot: Bot,
    marketContext: MarketContext | null
  ): Promise<RiskAssessment> {
    const warnings: string[] = [];
    let riskScore = 0;

    // Base risk from confidence (lower confidence = higher risk)
    riskScore += (1 - signal.confidence) * 0.3;

    // Market context risks
    if (marketContext) {
      // High spread risk
      if (marketContext.spread > 0.1) { // 0.1%
        riskScore += 0.2;
        warnings.push(`High spread: ${marketContext.spread.toFixed(3)}%`);
      }

      // Low liquidity risk
      if (marketContext.liquidityScore < 0.5) {
        riskScore += 0.15;
        warnings.push(`Low liquidity score: ${marketContext.liquidityScore}`);
      }

      // High volatility risk
      if (marketContext.volatility > 0.05) { // 5%
        riskScore += 0.25;
        warnings.push(`High volatility: ${marketContext.volatility.toFixed(3)}%`);
      }

      // After hours trading risk
      if (!marketContext.marketHours) {
        riskScore += 0.1;
        warnings.push('Trading outside market hours');
      }
    }

    // Bot-specific risk management
    const riskConfig = bot.riskManagement;
    if (riskConfig) {
      // Check position size limits
      const recommendedSize = this.calculatePositionSize(signal, riskConfig, marketContext);
      
      if (recommendedSize <= 0) {
        return {
          approved: false,
          riskScore: 1,
          warnings,
          rejectionReason: 'Position size calculation resulted in zero or negative size'
        };
      }
    }

    // Risk approval logic
    const maxAllowedRisk = 0.7;
    const approved = riskScore <= maxAllowedRisk;

    return {
      approved,
      riskScore,
      warnings,
      rejectionReason: approved ? undefined : `Risk score ${riskScore.toFixed(3)} exceeds maximum ${maxAllowedRisk}`,
      recommendedPositionSize: this.calculatePositionSize(signal, riskConfig, marketContext)
    };
  }

  /**
   * Calculate recommended position size
   */
  private calculatePositionSize(
    signal: TradeSignal,
    riskConfig?: any,
    marketContext?: MarketContext | null
  ): number {
    // Default position size
    let baseSize = 100; // Default $100

    // Apply risk management rules
    if (riskConfig?.maxPositionSize) {
      baseSize = Math.min(baseSize, riskConfig.maxPositionSize);
    }

    // Adjust for confidence
    const confidenceMultiplier = signal.confidence;
    baseSize *= confidenceMultiplier;

    // Adjust for market conditions
    if (marketContext) {
      // Reduce size for high volatility
      if (marketContext.volatility > 0.03) {
        baseSize *= 0.7;
      }

      // Reduce size for low liquidity
      if (marketContext.liquidityScore < 0.7) {
        baseSize *= marketContext.liquidityScore;
      }
    }

    return Math.max(1, Math.floor(baseSize));
  }

  /**
   * Helper methods
   */
  private generateSignalId(botId: string): string {
    return `${botId}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  private recordSignal(botId: string): void {
    const signals = this.signalHistory.get(botId) || [];
    signals.push(new Date());
    this.signalHistory.set(botId, signals);
  }

  private cleanupSignalHistory(): void {
    const fiveMinutesAgo = new Date(Date.now() - 5 * 60000);
    
    for (const [botId, signals] of this.signalHistory.entries()) {
      const recentSignals = signals.filter(time => time > fiveMinutesAgo);
      this.signalHistory.set(botId, recentSignals);
    }
  }

  private calculateVolatility(ticker: any): number {
    // Simple volatility estimation based on spread
    const spread = ticker.ask - ticker.bid;
    return (spread / ticker.last) * 100;
  }

  private isMarketHours(): boolean {
    // Simple check - in a real implementation, this would check actual market hours
    const hour = new Date().getHours();
    return hour >= 9 && hour <= 16; // 9 AM to 4 PM
  }

  private calculateLiquidityScore(orderBook: any): number {
    // Calculate liquidity based on order book depth
    const bidDepth = orderBook.bids.reduce((sum: number, bid: [number, number]) => sum + (bid.at(1) ?? 0), 0);
    const askDepth = orderBook.asks.reduce((sum: number, ask: [number, number]) => sum + (ask.at(1) ?? 0), 0);
    
    const totalDepth = bidDepth + askDepth;
    
    // Normalize to 0-1 scale (assuming 1000+ total volume is good liquidity)
    return Math.min(totalDepth / 1000, 1);
  }

  /**
   * Get processing statistics
   */
  getStats() {
    return { ...this.processingStats };
  }

  /**
   * Reset statistics
   */
  resetStats() {
    this.processingStats = {
      totalProcessed: 0,
      approved: 0,
      rejected: 0,
      errors: 0
    };
  }
}

/**
 * Enhanced State Persistence Manager
 * 
 * Provides comprehensive state persistence and recovery capabilities for bots.
 * Handles state snapshots, incremental updates, backup management, and
 * recovery from various failtaure scenarios.
 */

import { EventEmitter } from 'events';

import { database } from '../services/database.service';
import logger from '../services/logging.service';

export interface BotStateSnapshot {
  botId: string;
  timestamp: Date;
  version: number;
  state: {
    status: string;
    performance: {
      totalTrades: number;
      successfulTrades: number;
      totalProfit: number;
      totalLoss: number;
      winRate: number;
      avgTradeTime: number;
      maxDrawdown: number;
      errorCount: number;
    };
    configuration: Record<string, unknown>;
    positions: Array<{
      symbol: string;
      size: number;
      entryPrice: number;
      currentPrice: number;
      pnl: number;
      openedAt: Date;
    }>;
    orders: Array<{
      id: string;
      symbol: string;
      type: string;
      status: string;
      size: number;
      price: number;
      createdAt: Date;
    }>;
    strategy: {
      type: string;
      parameters: Record<string, unknown>;
      indicators: Record<string, unknown>;
      signals: Array<{
        type: string;
        strength: number;
        timestamp: Date;
      }>;
    };
    runtimeMetrics: {
      startedAt: Date;
      lastTickAt: Date;
      tickCount: number;
      errorCount: number;
      lastError?: string;
      memoryUsage: number;
      cpuUsage: number;
    };
  };
  checksum: string;
}

export interface StateBackup {
  id: string;
  botId: string;
  createdAt: Date;
  type: 'scheduled' | 'manual' | 'pre-restart' | 'emergency';
  reason: string;
  snapshot: BotStateSnapshot;
  compressed: boolean;
  size: number;
}

export interface RecoveryOptions {
  backupId?: string;
  targetTimestamp?: Date;
  restorePositions: boolean;
  restoreOrders: boolean;
  restoreConfiguration: boolean;
  restoreStrategy: boolean;
  validateIntegrity: boolean;
}

export interface PersistenceConfig {
  enableIncrementalUpdates: boolean;
  enableCompression: boolean;
  backupInterval: number; // milliseconds
  maxBackups: number;
  enableIntegrityChecks: boolean;
  autoRecovery: boolean;
  emergencyBackupThreshold: number; // errors before emergency backup
}

export class StateManager extends EventEmitter {
  private config: PersistenceConfig;
  private backupInterval: NodeJS.Timeout | null = null;
  private lastBackupTime: Map<string, Date> = new Map();
  private stateVersions: Map<string, number> = new Map();

  constructor(config: Partial<PersistenceConfig> = {}) {
    super();

    this.config = {
      enableIncrementalUpdates: true,
      enableCompression: true,
      backupInterval: 300000, // 5 minutes
      maxBackups: 48, // Keep 48 backups (8 hours at 10min intervals)
      enableIntegrityChecks: true,
      autoRecovery: true,
      emergencyBackupThreshold: 5,
      ...config
    };

    this.startBackupScheduler();
  }

  /**
   * Create a complete state snapshot
   */
  async createSnapshot(
    botId: string,
    currentState: any,
    additionalData: Partial<BotStateSnapshot['state']> = {}
  ): Promise<BotStateSnapshot> {
    const version = (this.stateVersions.get(botId) || 0) + 1;
    this.stateVersions.set(botId, version);

    const snapshot: BotStateSnapshot = {
      botId,
      timestamp: new Date(),
      version,
      state: {
        status: currentState.status || 'unknown',
        performance: currentState.performance || {
          totalTrades: 0,
          successfulTrades: 0,
          totalProfit: 0,
          totalLoss: 0,
          winRate: 0,
          avgTradeTime: 0,
          maxDrawdown: 0,
          errorCount: 0
        },
        configuration: currentState.configuration || {},
        positions: additionalData.positions || [],
        orders: additionalData.orders || [],
        strategy: additionalData.strategy || {
          type: 'unknown',
          parameters: {},
          indicators: {},
          signals: []
        },
        runtimeMetrics: {
          startedAt: currentState.startedAt || new Date(),
          lastTickAt: currentState.lastTickAt || new Date(),
          tickCount: currentState.tickCount || 0,
          errorCount: currentState.errorCount || 0,
          lastError: currentState.lastError,
          memoryUsage: process.memoryUsage().heapUsed / 1024 / 1024, // MB
          cpuUsage: process.cpuUsage().user / 1000000 // seconds
        },
        ...additionalData
      },
      checksum: ''
    };

    // Calculate checksum for integrity verification
    snapshot.checksum = this.calculateChecksum(snapshot);

    return snapshot;
  }

  /**
   * Save state snapshot to database
   */
  async saveSnapshot(snapshot: BotStateSnapshot): Promise<void> {
    try {
      let stateData = JSON.stringify(snapshot.state);
      
      // Compress if enabled and data is large
      if (this.config.enableCompression && stateData.length > 1024) {
        stateData = await this.compressData(stateData);
      }

      await database.query(`
        INSERT INTO bot_states (
          bot_id, state, version, checksum, created_at, compressed
        ) VALUES ($1, $2, $3, $4, $5, $6)
        ON CONFLICT (bot_id)
        DO UPDATE SET 
          state = $2, 
          version = $3, 
          checksum = $4, 
          updated_at = NOW(),
          compressed = $6
      `, [
        snapshot.botId,
        stateData,
        snapshot.version,
        snapshot.checksum,
        snapshot.timestamp,
        this.config.enableCompression && stateData.length > 1024
      ]);

      logger.info('[StateManager] State snapshot saved', {
        botId: snapshot.botId,
        version: snapshot.version,
        size: stateData.length
      });

      this.emit('snapshot-saved', snapshot);
    } catch (error) {
      logger.error('[StateManager] Failed to save state snapshot', {
        botId: snapshot.botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Load latest state snapshot
   */
  async loadSnapshot(botId: string): Promise<BotStateSnapshot | null> {
    try {
      const result = await database.query(
        'SELECT * FROM bot_states WHERE bot_id = $1 ORDER BY version DESC LIMIT 1',
        [botId]
      );

      if (result.length === 0) {
        return null;
      }

      const row = result.at(0);
      let stateData = row.state;

      // Decompress if necessary
      if (row.compressed) {
        stateData = await this.decompressData(stateData);
      }

      const snapshot: BotStateSnapshot = {
        botId: row.bot_id,
        timestamp: new Date(row.created_at),
        version: row.version,
        state: JSON.parse(stateData),
        checksum: row.checksum
      };

      // Verify integrity if enabled
      if (this.config.enableIntegrityChecks) {
        const calculatedChecksum = this.calculateChecksum(snapshot);
        if (calculatedChecksum !== snapshot.checksum) {
          throw new Error('State integrity check failed - checksum mismatch');
        }
      }

      logger.info('[StateManager] State snapshot loaded', {
        botId: snapshot.botId,
        version: snapshot.version,
        timestamp: snapshot.timestamp
      });

      this.emit('snapshot-loaded', snapshot);
      return snapshot;
    } catch (error) {
      logger.error('[StateManager] Failed to load state snapshot', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Create backup with metadata
   */
  async createBackup(
    botId: string,
    type: StateBackup['type'],
    reason: string,
    currentState: any,
    additionalData?: Partial<BotStateSnapshot['state']>
  ): Promise<StateBackup> {
    try {
      const snapshot = await this.createSnapshot(botId, currentState, additionalData);
      const backupId = `${botId}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
      
      let snapshotData = JSON.stringify(snapshot);
      const originalSize = snapshotData.length;
      let compressed = false;

      if (this.config.enableCompression && originalSize > 1024) {
        snapshotData = await this.compressData(snapshotData);
        compressed = true;
      }

      const backup: StateBackup = {
        id: backupId,
        botId,
        createdAt: new Date(),
        type,
        reason,
        snapshot,
        compressed,
        size: snapshotData.length
      };

      // Save backup to database
      await database.query(`
        INSERT INTO bot_state_backups (
          id, bot_id, type, reason, snapshot_data, compressed, size, created_at
        ) VALUES ($1, $2, $3, $4, $5, $6, $7, $8)
      `, [
        backupId,
        botId,
        type,
        reason,
        snapshotData,
        compressed,
        backup.size,
        backup.createdAt
      ]);

      this.lastBackupTime.set(botId, backup.createdAt);

      logger.info('[StateManager] Backup created', {
        backupId,
        botId,
        type,
        reason,
        size: backup.size,
        compressed
      });

      this.emit('backup-created', backup);

      // Clean up old backups
      await this.cleanupOldBackups(botId);

      return backup;
    } catch (error) {
      logger.error('[StateManager] Failed to create backup', {
        botId,
        type,
        reason,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Recover state from backup
   */
  async recoverFromBackup(
    botId: string,
    options: RecoveryOptions = {
      restorePositions: true,
      restoreOrders: true,
      restoreConfiguration: true,
      restoreStrategy: true,
      validateIntegrity: true
    }
  ): Promise<BotStateSnapshot> {
    try {
      let backup: StateBackup;

      if (options.backupId) {
        // Recover from specific backup
        const result = await database.query(
          'SELECT * FROM bot_state_backups WHERE id = $1',
          [options.backupId]
        );
        
        if (result.length === 0) {
          throw new Error(`Backup not found: ${options.backupId}`);
        }

        backup = await this.deserializeBackup(result.at(0));
      } else if (options.targetTimestamp) {
        // Find backup closest to target timestamp
        const result = await database.query(`
          SELECT * FROM bot_state_backups 
          WHERE bot_id = $1 AND created_at <= $2 
          ORDER BY created_at DESC 
          LIMIT 1
        `, [botId, options.targetTimestamp]);

        if (result.length === 0) {
          throw new Error('No backup found for target timestamp');
        }

        backup = await this.deserializeBackup(result.at(0));
      } else {
        // Use latest backup
        const result = await database.query(`
          SELECT * FROM bot_state_backups 
          WHERE bot_id = $1 
          ORDER BY created_at DESC 
          LIMIT 1
        `, [botId]);

        if (result.length === 0) {
          throw new Error('No backups found for bot');
        }

        backup = await this.deserializeBackup(result.at(0));
      }

      // Validate integrity if requested
      if (options.validateIntegrity && this.config.enableIntegrityChecks) {
        const calculatedChecksum = this.calculateChecksum(backup.snapshot);
        if (calculatedChecksum !== backup.snapshot.checksum) {
          throw new Error('Backup integrity check failed');
        }
      }

      // Create recovery snapshot with selective restore
      const recoverySnapshot: BotStateSnapshot = {
        ...backup.snapshot,
        timestamp: new Date(),
        version: (this.stateVersions.get(botId) || 0) + 1
      };

      // Selective restoration based on options
      if (!options.restorePositions) {
        recoverySnapshot.state.positions = [];
      }
      
      if (!options.restoreOrders) {
        recoverySnapshot.state.orders = [];
      }
      
      if (!options.restoreConfiguration) {
        recoverySnapshot.state.configuration = {};
      }
      
      if (!options.restoreStrategy) {
        recoverySnapshot.state.strategy = {
          type: 'unknown',
          parameters: {},
          indicators: {},
          signals: []
        };
      }

      // Update checksum after modifications
      recoverySnapshot.checksum = this.calculateChecksum(recoverySnapshot);

      // Save recovered state
      await this.saveSnapshot(recoverySnapshot);

      logger.info('[StateManager] State recovered from backup', {
        botId,
        backupId: backup.id,
        backupCreatedAt: backup.createdAt,
        options
      });

      this.emit('state-recovered', { backup, recoverySnapshot, options });

      return recoverySnapshot;
    } catch (error) {
      logger.error('[StateManager] Failed to recover from backup', {
        botId,
        options,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Incremental state update
   */
  async updateState(
    botId: string,
    updates: Partial<BotStateSnapshot['state']>
  ): Promise<void> {
    if (!this.config.enableIncrementalUpdates) {
      return;
    }

    try {
      const currentSnapshot = await this.loadSnapshot(botId);
      if (!currentSnapshot) {
        throw new Error('No existing state found for incremental update');
      }

      // Merge updates with existing state
      const updatedState = {
        ...currentSnapshot.state,
        ...updates,
        runtimeMetrics: {
          ...currentSnapshot.state.runtimeMetrics,
          ...updates.runtimeMetrics,
          lastTickAt: new Date()
        }
      };

      const newSnapshot: BotStateSnapshot = {
        ...currentSnapshot,
        timestamp: new Date(),
        version: currentSnapshot.version + 1,
        state: updatedState
      };

      newSnapshot.checksum = this.calculateChecksum(newSnapshot);
      await this.saveSnapshot(newSnapshot);

      this.emit('state-updated', { botId, updates, newSnapshot });
    } catch (error) {
      logger.error('[StateManager] Failed to update state incrementally', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Emergency backup (triggered by errors)
   */
  async createEmergencyBackup(
    botId: string,
    currentState: any,
    errorContext: string
  ): Promise<void> {
    try {
      await this.createBackup(
        botId,
        'emergency',
        `Emergency backup due to: ${errorContext}`,
        currentState
      );

      logger.warn('[StateManager] Emergency backup created', {
        botId,
        errorContext
      });
    } catch (error) {
      logger.error('[StateManager] Failed to create emergency backup', {
        botId,
        errorContext,
        error: error instanceof Error ? error.message : String(error)
      });
    }
  }

  /**
   * Get backup history
   */
  async getBackupHistory(
    botId: string,
    limit = 10
  ): Promise<Array<Omit<StateBackup, 'snapshot'>>> {
    try {
      const result = await database.query(`
        SELECT id, bot_id, type, reason, size, compressed, created_at
        FROM bot_state_backups 
        WHERE bot_id = $1 
        ORDER BY created_at DESC 
        LIMIT $2
      `, [botId, limit]);

      return result.map(row => ({
        id: row.id,
        botId: row.bot_id,
        createdAt: new Date(row.created_at),
        type: row.type,
        reason: row.reason,
        compressed: row.compressed,
        size: row.size,
        snapshot: null as any // Exclude snapshot data for performance
      }));
    } catch (error) {
      logger.error('[StateManager] Failed to get backup history', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Private helper methods
   */
  private startBackupScheduler(): void {
    if (this.backupInterval) {
      clearInterval(this.backupInterval);
    }

    this.backupInterval = setInterval(async () => {
      // This would be triggered by the bot runtime to create scheduled backups
      this.emit('scheduled-backup-required');
    }, this.config.backupInterval);
  }

  private async cleanupOldBackups(botId: string): Promise<void> {
    try {
      await database.query(`
        DELETE FROM bot_state_backups 
        WHERE bot_id = $1 
        AND id NOT IN (
          SELECT id FROM bot_state_backups 
          WHERE bot_id = $1 
          ORDER BY created_at DESC 
          LIMIT $2
        )
      `, [botId, this.config.maxBackups]);
    } catch (error) {
      logger.warn('[StateManager] Failed to cleanup old backups', {
        botId,
        error: error instanceof Error ? error.message : String(error)
      });
    }
  }

  private calculateChecksum(snapshot: BotStateSnapshot): string {
    const crypto = require('crypto');
    const data = JSON.stringify({
      ...snapshot,
      checksum: undefined // Exclude checksum from checksum calculation
    });
    return crypto.createHash('sha256').update(data).digest('hex');
  }

  private async compressData(data: string): Promise<string> {
    const zlib = require('zlib');
    const compressed = zlib.gzipSync(data);
    return compressed.toString('base64');
  }

  private async decompressData(compressedData: string): Promise<string> {
    const zlib = require('zlib');
    const buffer = Buffer.from(compressedData, 'base64');
    const decompressed = zlib.gunzipSync(buffer);
    return decompressed.toString();
  }

  private async deserializeBackup(row: any): Promise<StateBackup> {
    let snapshotData = row.snapshot_data;
    
    if (row.compressed) {
      snapshotData = await this.decompressData(snapshotData);
    }

    return {
      id: row.id,
      botId: row.bot_id,
      createdAt: new Date(row.created_at),
      type: row.type,
      reason: row.reason,
      snapshot: JSON.parse(snapshotData),
      compressed: row.compressed,
      size: row.size
    };
  }

  /**
   * Cleanup and shutdown
   */
  shutdown(): void {
    if (this.backupInterval) {
      clearInterval(this.backupInterval);
      this.backupInterval = null;
    }

    this.removeAllListeners();
    logger.info('[StateManager] State manager shutdown complete');
  }
}

export default StateManager;

/**
 * Strategy Execution Integration Service
 * 
 * Integrates the strategy framework with the unified trading engine,
 * providing dynamic strategy loading, execution coordination, and
 * performance monitoring.
 */

import { EventEmitter } from 'events';

import { EnhancedTradingEngine } from '../JabbrLabs/bot-cycle/unified-trading-engine';
import type { IStrategy, StrategyResult, StrategyContext, StrategyConfig } from '../JabbrLabs/target-reacher/interfaces';
import logger from '../services/logging.service';
import type { StrategyType } from '../strategies/strategy-factory';

import { Bot } from './bots.service';
import type { StrategyLoadResult, StrategySwapOptions } from './dynamic-strategy-loader';
import { dynamicStrategyLoader } from './dynamic-strategy-loader';




export interface StrategyExecutionConfig {
  botId: string;
  strategyType: StrategyType;
  strategyConfig: StrategyConfig;
  executionInterval: number; // milliseconds
  enableDynamicLoading: boolean;
  enablePerformanceTracking: boolean;
  maxExecutionTime: number; // milliseconds
  retryAttempts: number;
}

export interface StrategyExecutionMetrics {
  executionCount: number;
  successCount: number;
  errorCount: number;
  averageExecutionTime: number;
  lastExecutionTime: number;
  totalExecutionTime: number;
  successRate: number;
  signalsGenerated: number;
  lastError?: string;
  lastErrorAt?: Date;
}

export interface StrategyExecutionResult {
  success: boolean;
  result?: StrategyResult;
  executionTime: number;
  error?: string;
  retryAttempt?: number;
  metrics: StrategyExecutionMetrics;
}

/**
 * Strategy Execution Integration Service
 * 
 * Coordinates strategy execution with the trading engine, providing:
 * - Dynamic strategy loading and hot-swapping
 * - Strategy execution monitoring and metrics
 * - Error handling and recovery
 * - Performance optimization
 */
export class StrategyExecutionIntegration extends EventEmitter {
  private config: StrategyExecutionConfig;
  private currentStrategy?: IStrategy;
  private tradingEngine: EnhancedTradingEngine;
  private context: StrategyContext;
  private metrics: StrategyExecutionMetrics;
  private isExecuting = false;
  private executionTimeoutId?: NodeJS.Timeout;

  constructor(
    config: StrategyExecutionConfig,
    context: StrategyContext,
    tradingEngine?: EnhancedTradingEngine
  ) {
    super();
    this.config = config;
    this.context = context;
    this.tradingEngine = tradingEngine || new EnhancedTradingEngine();
    
    this.metrics = {
      executionCount: 0,
      successCount: 0,
      errorCount: 0,
      averageExecutionTime: 0,
      lastExecutionTime: 0,
      totalExecutionTime: 0,
      successRate: 0,
      signalsGenerated: 0
    };
  }

  /**
   * Initialize the strategy execution integration
   */
  async initialize(): Promise<void> {
    try {
      logger.info('ðŸš€ Initializing strategy execution integration', {
        botId: this.config.botId,
        strategyType: this.config.strategyType
      });

      // Load initial strategy
      const loadResult = await this.loadStrategy(this.config.strategyType, this.config.strategyConfig);
      
      if (!loadResult.success) {
        throw new Error(loadResult.error || 'Failed to load strategy during initialization');
      }

      logger.info('âœ… Strategy execution integration initialized', {
        botId: this.config.botId,
        strategyType: this.config.strategyType
      });

      this.emit('initialized', {
        botId: this.config.botId,
        strategyType: this.config.strategyType
      });

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      logger.error('âŒ Failed to initialize strategy execution integration', {
        botId: this.config.botId,
        error: errorMessage
      });

      this.emit('initialization-error', {
        botId: this.config.botId,
        error: errorMessage
      });

      throw error;
    }
  }

  /**
   * Load a strategy dynamically
   */
  async loadStrategy(
    strategyType: StrategyType, 
    strategyConfig: StrategyConfig,
    options?: StrategySwapOptions
  ): Promise<StrategyLoadResult> {
    try {
      logger.info('ðŸ“¦ Loading strategy', {
        botId: this.config.botId,
        strategyType,
        swapOptions: options
      });

      const loadResult = await dynamicStrategyLoader.loadStrategy(
        this.config.botId,
        strategyType,
        strategyConfig,
        this.context,
        options?.preserveState ? undefined : 'fresh-load'
      );

      if (!loadResult.success || !loadResult.strategy) {
        throw new Error(loadResult.error || 'Failed to load strategy');
      }

      // Store previous strategy for potential rollback
      const previousStrategy = this.currentStrategy;

      // Set new strategy
      this.currentStrategy = loadResult.strategy;

      // Update configuration
      this.config.strategyType = strategyType;
      this.config.strategyConfig = strategyConfig;

      logger.info('âœ… Strategy loaded successfully', {
        botId: this.config.botId,
        strategyType,
        version: loadResult.version?.version
      });

      this.emit('strategy-loaded', {
        botId: this.config.botId,
        strategyType,
        version: loadResult.version,
        previousStrategy: previousStrategy ? 'replaced' : 'none'
      });

      return loadResult;

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      logger.error('âŒ Failed to load strategy', {
        botId: this.config.botId,
        strategyType,
        error: errorMessage
      });

      this.emit('strategy-load-error', {
        botId: this.config.botId,
        strategyType,
        error: errorMessage
      });

      return {
        success: false,
        error: errorMessage
      };
    }
  }

  /**
   * Execute the current strategy
   */
  async executeStrategy(): Promise<StrategyExecutionResult> {
    const startTime = Date.now();
    
    if (this.isExecuting) {
      return {
        success: false,
        executionTime: 0,
        error: 'Strategy execution already in progress',
        metrics: this.metrics
      };
    }

    if (!this.currentStrategy) {
      return {
        success: false,
        executionTime: 0,
        error: 'No strategy loaded',
        metrics: this.metrics
      };
    }

    this.isExecuting = true;
    this.metrics.executionCount++;

    try {
      // Set execution timeout
      const timeoutPromise = new Promise((_, reject) => {
        this.executionTimeoutId = setTimeout(() => {
          reject(new Error(`Strategy execution timeout after ${this.config.maxExecutionTime}ms`));
        }, this.config.maxExecutionTime);
      });

      // Execute strategy with timeout
      const executionPromise = this.executeWithRetries();
      
      const result = await Promise.race([executionPromise, timeoutPromise]) as StrategyResult;

      // Clear timeout
      if (this.executionTimeoutId) {
        clearTimeout(this.executionTimeoutId);
        this.executionTimeoutId = undefined;
      }

      const executionTime = Math.max(Date.now() - startTime, 1); // Ensure minimum 1ms
      
      // Update metrics
      this.updateMetrics(true, executionTime, result);

      // Process result through trading engine
      if (result.action !== 'hold') {
        await this.tradingEngine.processAdvancedSignals([{
          action: result.action,
          confidence: result.confidence,
          reason: result.reason,
          symbol: this.context.symbol,
          price: 0, // Will be fetched by trading engine
          timestamp: Date.now(),
          strategyType: this.config.strategyType,
          botId: this.config.botId
        }]);
      }

      const executionResult: StrategyExecutionResult = {
        success: true,
        result,
        executionTime,
        metrics: { ...this.metrics }
      };

      logger.info('âœ… Strategy executed successfully', {
        botId: this.config.botId,
        strategyType: this.config.strategyType,
        action: result.action,
        confidence: result.confidence,
        executionTime
      });

      this.emit('strategy-executed', {
        botId: this.config.botId,
        result: executionResult
      });

      return executionResult;

    } catch (error) {
      const executionTime = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      // Clear timeout
      if (this.executionTimeoutId) {
        clearTimeout(this.executionTimeoutId);
        this.executionTimeoutId = undefined;
      }

      // Update metrics
      this.updateMetrics(false, executionTime, undefined, errorMessage);

      const executionResult: StrategyExecutionResult = {
        success: false,
        executionTime,
        error: errorMessage,
        metrics: { ...this.metrics }
      };

      logger.error('âŒ Strategy execution failed', {
        botId: this.config.botId,
        strategyType: this.config.strategyType,
        error: errorMessage,
        executionTime
      });

      this.emit('strategy-execution-error', {
        botId: this.config.botId,
        error: errorMessage,
        executionTime
      });

      return executionResult;

    } finally {
      this.isExecuting = false;
    }
  }

  /**
   * Execute strategy with retry logic
   */
  private async executeWithRetries(attempt = 1): Promise<StrategyResult> {
    try {
      if (!this.currentStrategy) {
        throw new Error('No strategy available for execution');
      }

      // Execute the strategy
      const result = await this.currentStrategy.execute(this.context);

      // Validate result
      if (!result || typeof result.action !== 'string') {
        throw new Error('Invalid strategy result format');
      }

      return result;

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);

      if (attempt < this.config.retryAttempts) {
        logger.warn('âš ï¸ Strategy execution failed, retrying', {
          botId: this.config.botId,
          attempt,
          maxAttempts: this.config.retryAttempts,
          error: errorMessage
        });

        // Wait before retry
        await new Promise(resolve => setTimeout(resolve, 1000 * attempt));
        
        return this.executeWithRetries(attempt + 1);
      }

      throw error;
    }
  }

  /**
   * Update execution metrics
   */
  private updateMetrics(
    success: boolean, 
    executionTime: number, 
    result?: StrategyResult, 
    error?: string
  ): void {
    if (success) {
      this.metrics.successCount++;
      if (result && result.action !== 'hold') {
        this.metrics.signalsGenerated++;
      }
    } else {
      this.metrics.errorCount++;
      this.metrics.lastError = error;
      this.metrics.lastErrorAt = new Date();
    }

    this.metrics.lastExecutionTime = executionTime;
    this.metrics.totalExecutionTime += executionTime;
    this.metrics.averageExecutionTime = this.metrics.totalExecutionTime / this.metrics.executionCount;
    this.metrics.successRate = this.metrics.successCount / this.metrics.executionCount;

    // Emit performance metrics updated event
    this.emit('performance-metrics-updated', this.metrics);
  }

  /**
   * Get current strategy information
   */
  getCurrentStrategyInfo(): {
    loaded: boolean;
    type?: StrategyType;
    config?: StrategyConfig;
    metrics: StrategyExecutionMetrics;
  } {
    return {
      loaded: !!this.currentStrategy,
      type: this.currentStrategy ? this.config.strategyType : undefined,
      config: this.currentStrategy ? this.config.strategyConfig : undefined,
      metrics: { ...this.metrics }
    };
  }

  /**
   * Hot-swap strategy without stopping execution
   */
  async hotSwapStrategy(
    newStrategyType: StrategyType,
    newStrategyConfig: StrategyConfig
  ): Promise<StrategyLoadResult> {
    logger.info('ðŸ”„ Initiating hot strategy swap', {
      botId: this.config.botId,
      currentStrategy: this.config.strategyType,
      newStrategy: newStrategyType
    });

    const swapOptions: StrategySwapOptions = {
      preserveState: true,
      validateFirst: true,
      rollbackOnError: true
    };

    const result = await this.loadStrategy(newStrategyType, newStrategyConfig, swapOptions);

    if (result.success) {
      logger.info('âœ… Hot strategy swap completed', {
        botId: this.config.botId,
        newStrategy: newStrategyType,
        version: result.version?.version
      });

      this.emit('strategy-swapped', {
        botId: this.config.botId,
        oldStrategy: this.config.strategyType,
        newStrategy: newStrategyType,
        version: result.version
      });

      // Emit hot-swap-complete event for test compatibility
      this.emit('hot-swap-complete', {
        botId: this.config.botId,
        oldVersion: '1.0.0', // This would normally come from previous version
        newVersion: result.version?.version || '1.1.0'
      });
    } else {
      logger.error('âŒ Hot strategy swap failed', {
        botId: this.config.botId,
        newStrategy: newStrategyType,
        error: result.error
      });
    }

    return result;
  }

  /**
   * Update strategy configuration
   */
  async updateStrategyConfig(newConfig: Partial<StrategyConfig>): Promise<boolean> {
    try {
      if (!this.currentStrategy) {
        throw new Error('No strategy loaded');
      }

      // Merge with existing config
      const updatedConfig = { ...this.config.strategyConfig, ...newConfig };

      // Validate new configuration
      if (this.currentStrategy.validateConfig) {
        const validation = this.currentStrategy.validateConfig(updatedConfig);
        
        if (!validation.valid) {
          throw new Error(`Invalid configuration: ${validation.errors?.map(e => e.message).join(', ')}`);
        }
      }

      // Update configuration
      this.config.strategyConfig = updatedConfig;

      logger.info('âœ… Strategy configuration updated', {
        botId: this.config.botId,
        strategyType: this.config.strategyType,
        updatedFields: Object.keys(newConfig)
      });

      this.emit('strategy-config-updated', {
        botId: this.config.botId,
        updatedConfig: newConfig
      });

      // Emit config-updated event for test compatibility
      this.emit('config-updated', {
        botId: this.config.botId,
        newConfig: updatedConfig
      });

      return true;

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      logger.error('âŒ Failed to update strategy configuration', {
        botId: this.config.botId,
        error: errorMessage
      });

      this.emit('strategy-config-error', {
        botId: this.config.botId,
        error: errorMessage
      });

      return false;
    }
  }

  /**
   * Get strategy performance metrics
   */
  getPerformanceMetrics(): StrategyExecutionMetrics {
    return { ...this.metrics };
  }

  /**
   * Reset performance metrics
   */
  resetMetrics(): void {
    this.metrics = {
      executionCount: 0,
      successCount: 0,
      errorCount: 0,
      averageExecutionTime: 0,
      lastExecutionTime: 0,
      totalExecutionTime: 0,
      successRate: 0,
      signalsGenerated: 0
    };

    logger.info('ðŸ“Š Strategy execution metrics reset', {
      botId: this.config.botId,
      strategyType: this.config.strategyType
    });

    this.emit('metrics-reset', {
      botId: this.config.botId
    });
  }

  /**
   * Shutdown the strategy execution integration
   */
  async shutdown(): Promise<void> {
    try {
      logger.info('ðŸ›‘ Shutting down strategy execution integration', {
        botId: this.config.botId
      });

      // Wait for current execution to complete
      while (this.isExecuting) {
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      // Clear timeout if set
      if (this.executionTimeoutId) {
        clearTimeout(this.executionTimeoutId);
        this.executionTimeoutId = undefined;
      }

      // Remove all listeners
      this.removeAllListeners();

      logger.info('âœ… Strategy execution integration shutdown complete', {
        botId: this.config.botId
      });

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      logger.error('âŒ Error during strategy execution integration shutdown', {
        botId: this.config.botId,
        error: errorMessage
      });

      throw error;
    }
  }
}

export default StrategyExecutionIntegration;

/**
 * Trade Decision Engine - Comprehensive Trade Execution System
 * 
 * Handles the complete trade execution workflow:
 * - Trade signal validation and enrichment
 * - Order creation and execution
 * - Position management and tracking
 * - Risk management enforcement
 * - Stop-loss and take-profit management
 * - Order lifecycle management
 */

import { EventEmitter } from 'events';

import type { TradeSignal, TradeOrder, Position, StrategyContext } from '../JabbrLabs/target-reacher/interfaces';

import type { Bot } from './bots.service';
import type { ProcessedSignal } from './signal-processor';
import type { TradeExecutor, ExecutionRequest} from './trade-executor';
import { ExecutionResponse } from './trade-executor';

export interface TradeDecisionConfig {
  enablePositionManagement: boolean;
  enableStopLoss: boolean;
  enableTakeProfit: boolean;
  maxPositionsPerBot: number;
  orderTimeoutMs: number;
  retryAttempts: number;
  slippageTolerance: number; // percentage
}

export interface TradeDecision {
  approved: boolean;
  action: 'execute' | 'reject' | 'defer';
  reason: string;
  modifiedSignal?: TradeSignal;
  riskAdjustments?: RiskAdjustments;
  estimatedCost?: number;
}

export interface RiskAdjustments {
  adjustedPositionSize?: number;
  stopLossPrice?: number;
  takeProfitPrice?: number;
  maxSlippage?: number;
}

export interface ExecutionResult {
  success: boolean;
  order?: TradeOrder;
  position?: Position;
  error?: string;
  warnings?: string[];
  executionTime: number;
}

export interface PositionUpdate {
  positionId: string;
  botId: string;
  symbol: string;
  status: 'opening' | 'open' | 'closing' | 'closed' | 'failed';
  currentPrice: number;
  unrealizedPnl: number;
  stopLossTriggered?: boolean;
  takeProfitTriggered?: boolean;
}

export class TradeDecisionEngine extends EventEmitter {
  private config: TradeDecisionConfig;
  private activePositions: Map<string, Position> = new Map();
  private pendingOrders: Map<string, TradeOrder> = new Map();
  private tradeExecutor?: TradeExecutor;
  private executionStats = {
    totalDecisions: 0,
    approved: 0,
    rejected: 0,
    executed: 0,
    failed: 0
  };

  constructor(config?: Partial<TradeDecisionConfig>, tradeExecutor?: TradeExecutor) {
    super();
    
    this.config = {
      enablePositionManagement: true,
      enableStopLoss: true,
      enableTakeProfit: true,
      maxPositionsPerBot: 3,
      orderTimeoutMs: 30000,
      retryAttempts: 3,
      slippageTolerance: 0.1, // 0.1%
      ...config
    };

    this.tradeExecutor = tradeExecutor;

    // Set up periodic position monitoring
    setInterval(() => this.monitorPositions(), 10000); // Every 10 seconds
  }

  /**
   * Set trade executor
   */
  setTradeExecutor(tradeExecutor: TradeExecutor): void {
    this.tradeExecutor = tradeExecutor;
  }

  /**
   * Make a trade decision based on processed signal
   */
  async makeTradeDecision(
    processedSignal: ProcessedSignal,
    bot: Bot,
    context: StrategyContext
  ): Promise<TradeDecision> {
    try {
      console.log('ðŸŽ¯ Making trade decision', {
        botId: bot.id,
        signalId: processedSignal.processingMetadata.signalId,
        action: processedSignal.tradeSignal.side,
        confidence: processedSignal.tradeSignal.confidence
      });

      this.executionStats.totalDecisions++;

      // Step 1: Check position limits
      const positionCheck = await this.checkPositionLimits(bot.id, processedSignal.tradeSignal);
      if (!positionCheck.allowed) {
        this.executionStats.rejected++;
        return {
          approved: false,
          action: 'reject',
          reason: positionCheck.reason || 'Position limit check failed'
        };
      }

      // Step 2: Validate market conditions
      const marketValidation = await this.validateMarketConditions(
        processedSignal.tradeSignal,
        processedSignal.marketContext,
        context
      );
      if (!marketValidation.valid) {
        this.executionStats.rejected++;
        return {
          approved: false,
          action: 'reject',
          reason: marketValidation.reason || 'Market conditions unfavorable'
        };
      }

      // Step 3: Calculate risk adjustments
      const riskAdjustments = await this.calculateRiskAdjustments(
        processedSignal,
        bot,
        context
      );

      // Step 4: Estimate execution cost
      const estimatedCost = await this.estimateExecutionCost(
        processedSignal.tradeSignal,
        riskAdjustments,
        processedSignal.marketContext
      );

      // Step 5: Final approval logic
      const finalApproval = this.makeFinalDecision(
        processedSignal,
        riskAdjustments,
        estimatedCost,
        bot
      );

      if (finalApproval.approved) {
        this.executionStats.approved++;
        
        console.log('âœ… Trade decision approved', {
          botId: bot.id,
          signalId: processedSignal.processingMetadata.signalId,
          estimatedCost,
          riskAdjustments
        });

        return {
          approved: true,
          action: 'execute',
          reason: 'Trade approved by decision engine',
          modifiedSignal: this.applySignalModifications(processedSignal.tradeSignal, riskAdjustments),
          riskAdjustments,
          estimatedCost
        };
      } 
        this.executionStats.rejected++;
        return {
          approved: false,
          action: 'reject',
          reason: finalApproval.reason || 'Final approval failed'
        };
      

    } catch (error) {
      this.executionStats.rejected++;
      
      console.error('âŒ Error making trade decision', {
        botId: bot.id,
        signalId: processedSignal.processingMetadata.signalId,
        error: error instanceof Error ? error.message : String(error)
      });

      return {
        approved: false,
        action: 'reject',
        reason: `Decision engine error: ${error instanceof Error ? error.message : String(error)}`
      };
    }
  }

  /**
   * Execute a trade based on approved decision
   */
  async executeTrade(
    decision: TradeDecision,
    processedSignal: ProcessedSignal,
    bot: Bot,
    context: StrategyContext
  ): Promise<ExecutionResult> {
    const startTime = Date.now();

    try {
      if (!decision.approved || !decision.modifiedSignal) {
        throw new Error('Cannot execute unapproved trade decision');
      }

      console.log('ðŸš€ Executing trade', {
        botId: bot.id,
        signalId: processedSignal.processingMetadata.signalId,
        signal: decision.modifiedSignal
      });

      // Use real trade executor if available
      if (this.tradeExecutor) {
        // Prepare execution request
        const executionRequest: ExecutionRequest = {
          bot,
          signal: decision.modifiedSignal,
          orderType: 'market', // Default to market order, can be enhanced
          amount: decision.riskAdjustments?.adjustedPositionSize || 100,
          price: decision.modifiedSignal.price,
          leverage: bot.configuration?.leverage
        };

        // Execute on real exchange
        const executionResponse = await this.tradeExecutor.executeTrade(executionRequest);

        if (!executionResponse.success) {
          throw new Error(executionResponse.error || 'Trade execution failed');
        }

        // Create trade order record
        const order: TradeOrder = {
          id: executionResponse.orderId!,
          orderId: executionResponse.exchangeOrderId!,
          botId: bot.id,
          symbol: decision.modifiedSignal.symbol,
          type: 'market',
          side: decision.modifiedSignal.side,
          amount: executionRequest.amount,
          price: executionResponse.executedPrice,
          filled: executionResponse.executedAmount || executionRequest.amount,
          remaining: 0,
          status: 'filled',
          timestamp: executionResponse.timestamp?.getTime() || Date.now(),
          updatedAt: Date.now()
        };

        // Store order in pending orders
        this.pendingOrders.set(order.id, order);

        // Set up position management
        let position: Position | null = null;
        if (this.config.enablePositionManagement) {
          position = await this.createPosition(order, decision, bot);
          if (position) {
            this.activePositions.set(position.symbol, position);
          }
        }

        // Set up stop-loss and take-profit if enabled
        if (position && (this.config.enableStopLoss || this.config.enableTakeProfit)) {
          await this.setupRiskManagementOrders(position, decision, bot, context);
        }

        const executionTime = Date.now() - startTime;
        this.executionStats.executed++;

        console.log('âœ… Trade executed successfully', {
          botId: bot.id,
          orderId: order.id,
          executionTime,
          positionCreated: !!position
        });

        this.emit('trade-executed', {
          botId: bot.id,
          order,
          position,
          executionTime
        });

        return {
          success: true,
          order,
          position: position || undefined,
          executionTime
        };

      } 
        // Fallback to mock execution if no trade executor
        console.warn('âš ï¸ No trade executor configured, using mock execution');
        
        // Mock execution logic (existing code)
        const mockOrderId = `mock-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
        const mockPrice = decision.modifiedSignal.price;
        
        const order: TradeOrder = {
          id: mockOrderId,
          orderId: mockOrderId,
          botId: bot.id,
          symbol: decision.modifiedSignal.symbol,
          type: 'market',
          side: decision.modifiedSignal.side,
          amount: decision.riskAdjustments?.adjustedPositionSize || 100,
          price: mockPrice,
          filled: decision.riskAdjustments?.adjustedPositionSize || 100,
          remaining: 0,
          status: 'filled',
          timestamp: Date.now(),
          updatedAt: Date.now()
        };

        this.pendingOrders.set(order.id, order);

        let position: Position | null = null;
        if (this.config.enablePositionManagement) {
          position = await this.createPosition(order, decision, bot);
          if (position) {
            this.activePositions.set(position.symbol, position);
          }
        }

        const executionTime = Date.now() - startTime;
        this.executionStats.executed++;

        console.log('ðŸ§ª Mock trade executed', {
          botId: bot.id,
          orderId: order.id,
          executionTime
        });

        return {
          success: true,
          order,
          position: position || undefined,
          executionTime
        };
      

    } catch (error) {
      this.executionStats.failed++;
      const executionTime = Date.now() - startTime;

      console.error('âŒ Trade execution failed', {
        botId: bot.id,
        signalId: processedSignal.processingMetadata.signalId,
        error: error instanceof Error ? error.message : String(error),
        executionTime
      });

      this.emit('trade-failed', {
        botId: bot.id,
        error: error instanceof Error ? error.message : String(error),
        executionTime
      });

      return {
        success: false,
        error: error instanceof Error ? error.message : String(error),
        executionTime
      };
    }
  }

  /**
   * Check position limits for the bot
   */
  private async checkPositionLimits(
    botId: string,
    signal: TradeSignal
  ): Promise<{ allowed: boolean; reason?: string }> {
    // Count current positions for this bot
    const botPositions = Array.from(this.activePositions.values())
      .filter(pos => pos.symbol.includes(botId)); // Simple check, would be more sophisticated in real implementation

    if (botPositions.length >= this.config.maxPositionsPerBot) {
      return {
        allowed: false,
        reason: `Maximum positions reached: ${botPositions.length}/${this.config.maxPositionsPerBot}`
      };
    }

    // Check if there's already a position for this symbol
    const existingPosition = this.activePositions.get(signal.symbol);
    if (existingPosition) {
      return {
        allowed: false,
        reason: `Position already exists for ${signal.symbol}`
      };
    }

    return { allowed: true };
  }

  /**
   * Validate market conditions for execution
   */
  private async validateMarketConditions(
    signal: TradeSignal,
    marketContext: any,
    _context: StrategyContext
  ): Promise<{ valid: boolean; reason?: string }> {
    // Check if market is open
    if (!marketContext.marketHours) {
      return {
        valid: false,
        reason: 'Market is closed'
      };
    }

    // Check for excessive spread
    if (marketContext.spread > this.config.slippageTolerance) {
      return {
        valid: false,
        reason: `Spread too high: ${marketContext.spread}% > ${this.config.slippageTolerance}%`
      };
    }

    // Check liquidity
    if (marketContext.liquidityScore < 0.3) {
      return {
        valid: false,
        reason: `Low liquidity: ${marketContext.liquidityScore}`
      };
    }

    return { valid: true };
  }

  /**
   * Calculate risk adjustments for the trade
   */
  private async calculateRiskAdjustments(
    processedSignal: ProcessedSignal,
    bot: Bot,
    _context: StrategyContext
  ): Promise<RiskAdjustments> {
    const signal = processedSignal.tradeSignal;
    const riskConfig = bot.riskManagement;
    const adjustments: RiskAdjustments = {};

    // Adjust position size
    adjustments.adjustedPositionSize = processedSignal.riskAssessment.recommendedPositionSize || 100;

    // Calculate stop-loss price
    if (this.config.enableStopLoss && riskConfig?.stopLossPercentage) {
      const stopLossPercent = riskConfig.stopLossPercentage / 100;
      if (signal.side === 'buy') {
        adjustments.stopLossPrice = signal.price * (1 - stopLossPercent);
      } else {
        adjustments.stopLossPrice = signal.price * (1 + stopLossPercent);
      }
    }

    // Calculate take-profit price
    if (this.config.enableTakeProfit && riskConfig?.takeProfitPercentage) {
      const takeProfitPercent = riskConfig.takeProfitPercentage / 100;
      if (signal.side === 'buy') {
        adjustments.takeProfitPrice = signal.price * (1 + takeProfitPercent);
      } else {
        adjustments.takeProfitPrice = signal.price * (1 - takeProfitPercent);
      }
    }

    // Set slippage tolerance
    adjustments.maxSlippage = this.config.slippageTolerance;

    return adjustments;
  }

  /**
   * Estimate execution cost
   */
  private async estimateExecutionCost(
    signal: TradeSignal,
    _riskAdjustments: RiskAdjustments,
    marketContext: any
  ): Promise<number> {
    const positionSize = _riskAdjustments.adjustedPositionSize || 100;
    const price = signal.price;
    
    // Base cost
    const baseCost = positionSize * price;
    
    // Add spread cost
    const spreadCost = baseCost * (marketContext.spread / 100);
    
    // Add potential slippage cost
    const slippageCost = baseCost * (_riskAdjustments.maxSlippage || 0) / 100;
    
    return baseCost + spreadCost + slippageCost;
  }

  /**
   * Make final trade approval decision
   */
  private makeFinalDecision(
    processedSignal: ProcessedSignal,
    _riskAdjustments: RiskAdjustments,
    estimatedCost: number,
    bot: Bot
  ): { approved: boolean; reason?: string } {
    // Check if estimated cost is within bot's limits
    const maxCost = bot.configuration?.maxTradeAmount || 1000;
    if (estimatedCost > maxCost) {
      return {
        approved: false,
        reason: `Estimated cost ${estimatedCost} exceeds maximum ${maxCost}`
      };
    }

    // Check overall risk score
    if (processedSignal.riskAssessment.riskScore > 0.8) {
      return {
        approved: false,
        reason: `Risk score too high: ${processedSignal.riskAssessment.riskScore}`
      };
    }

    return { approved: true };
  }

  /**
   * Apply signal modifications based on risk adjustments
   */
  private applySignalModifications(
    originalSignal: TradeSignal,
    _riskAdjustments: RiskAdjustments
  ): TradeSignal {
    return {
      ...originalSignal,
      // Apply any modifications needed
      id: `${originalSignal.id}-modified`,
      timestamp: Date.now()
    };
  }

