import fs from 'fs/promises';
import path from 'path';

import { database } from './database.config';

/**
 * Migration record interface
 */
interface Migration {
  id: number;
  name: string;
  filename: string;
  executed_at: Date;
  checksum: string;
}

/**
 * Database Migration Runner
 * Handles executing SQL migration files and tracking migration history
 */
export class MigrationRunner {
  private migrationsPath: string;

  constructor(migrationsPath?: string) {
    this.migrationsPath = migrationsPath || path.join(__dirname, 'migrations');
  }

  /**
   * Initialize migration tracking table
   */
  async initializeMigrationTable(): Promise<void> {
    const createTableSQL = `
      CREATE TABLE IF NOT EXISTS migrations (
        id SERIAL PRIMARY KEY,
        name VARCHAR(255) NOT NULL UNIQUE,
        filename VARCHAR(255) NOT NULL,
        executed_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
        checksum VARCHAR(64) NOT NULL
      );
      
      CREATE INDEX IF NOT EXISTS idx_migrations_name ON migrations(name);
      CREATE INDEX IF NOT EXISTS idx_migrations_executed_at ON migrations(executed_at);
    `;

    try {
      await database.query(createTableSQL);
      console.log('âœ… Migration tracking table initialized');
    } catch (error) {
      console.error('âŒ Failed to initialize migration table:', error);
      throw error;
    }
  }

  /**
   * Get all executed migrations from database
   */
  async getExecutedMigrations(): Promise<Migration[]> {
    try {
      return await database.query<Migration>(`
        SELECT id, name, filename, executed_at, checksum 
        FROM migrations 
        ORDER BY id ASC
      `);
    } catch (error) {
      // If table doesn't exist, return empty array
      if (error instanceof Error && error.message.includes('relation "migrations" does not exist')) {
        return [];
      }
      throw error;
    }
  }

  /**
   * Get all migration files from filesystem
   */
  async getMigrationFiles(): Promise<{ name: string; filename: string; content: string; checksum: string }[]> {
    try {
      const files = await fs.readdir(this.migrationsPath);
      const migrationFiles = files
        .filter(file => file.endsWith('.sql'))
        .sort(); // Ensure consistent ordering

      const migrations = [];
      for (const filename of migrationFiles) {
        const filePath = path.join(this.migrationsPath, filename);
        const content = await fs.readFile(filePath, 'utf-8');
        const name = this.extractMigrationName(filename);
        const checksum = this.calculateChecksum(content);

        migrations.push({
          name,
          filename,
          content,
          checksum
        });
      }

      return migrations;
    } catch (error) {
      console.error('âŒ Failed to read migration files:', error);
      throw error;
    }
  }

  /**
   * Execute pending migrations
   */
  async runMigrations(): Promise<void> {
    console.log('ðŸ”„ Starting database migrations...');

    // Ensure database is connected
    if (!database.isConnectionActive()) {
      await database.connect();
    }

    // Initialize migration table
    await this.initializeMigrationTable();

    // Get executed migrations and available migration files
    const [executedMigrations, migrationFiles] = await Promise.all([
      this.getExecutedMigrations(),
      this.getMigrationFiles()
    ]);

    // Find pending migrations
    const executedNames = new Set(executedMigrations.map(m => m.name));
    const pendingMigrations = migrationFiles.filter(m => !executedNames.has(m.name));

    if (pendingMigrations.length === 0) {
      console.log('âœ… No pending migrations');
      return;
    }

    console.log(`ðŸ“‹ Found ${pendingMigrations.length} pending migrations:`);
    pendingMigrations.forEach(m => console.log(`   - ${m.name}`));

    // Execute pending migrations
    for (const migration of pendingMigrations) {
      await this.executeMigration(migration);
    }

    console.log('âœ… All migrations completed successfully');
  }

  /**
   * Execute a single migration
   */
  private async executeMigration(migration: { name: string; filename: string; content: string; checksum: string }): Promise<void> {
    console.log(`ðŸ”„ Executing migration: ${migration.name}`);

    try {
      await database.transaction(async (client) => {
        // Execute the migration SQL
        await client.query(migration.content);

        // Record the migration in the tracking table
        await client.query(`
          INSERT INTO migrations (name, filename, checksum)
          VALUES ($1, $2, $3)
        `, [migration.name, migration.filename, migration.checksum]);
      });

      console.log(`âœ… Migration completed: ${migration.name}`);
    } catch (error) {
      console.error(`âŒ Migration failed: ${migration.name}`, error);
      throw error;
    }
  }

  /**
   * Verify migration integrity
   */
  async verifyMigrations(): Promise<{ valid: boolean; issues: string[] }> {
    const issues: string[] = [];

    try {
      const [executedMigrations, migrationFiles] = await Promise.all([
        this.getExecutedMigrations(),
        this.getMigrationFiles()
      ]);

      // Check for missing migration files
      for (const executed of executedMigrations) {
        const file = migrationFiles.find(f => f.name === executed.name);
        if (!file) {
          issues.push(`Migration file missing: ${executed.filename}`);
          continue;
        }

        // Check checksum integrity
        if (file.checksum !== executed.checksum) {
          issues.push(`Migration checksum mismatch: ${executed.name} (file may have been modified)`);
        }
      }

      // Check for out-of-order migrations
      const executedNames = executedMigrations.map(m => m.name);
      const fileNames = migrationFiles.map(f => f.name);
      
      for (let i = 0; i < executedNames.length; i++) {
        const executedName = executedNames.at(i);
        const expectedName = fileNames.at(i);
        
        if (executedName !== expectedName) {
          issues.push(`Migration order mismatch at position ${i}: expected ${expectedName}, found ${executedName}`);
        }
      }

      return {
        valid: issues.length === 0,
        issues
      };
    } catch (error) {
      issues.push(`Verification failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      return { valid: false, issues };
    }
  }

  /**
   * Get migration status
   */
  async getStatus(): Promise<{
    executed: number;
    pending: number;
    total: number;
    migrations: Array<{
      name: string;
      status: 'executed' | 'pending';
      executed_at?: Date;
    }>;
  }> {
    const [executedMigrations, migrationFiles] = await Promise.all([
      this.getExecutedMigrations(),
      this.getMigrationFiles()
    ]);

    const executedMap = new Map(executedMigrations.map(m => [m.name, m]));
    
    const migrations = migrationFiles.map(file => {
      const executed = executedMap.get(file.name);
      return {
        name: file.name,
        status: executed ? 'executed' as const : 'pending' as const,
        executed_at: executed?.executed_at
      };
    });

    return {
      executed: executedMigrations.length,
      pending: migrationFiles.length - executedMigrations.length,
      total: migrationFiles.length,
      migrations
    };
  }

  /**
   * Extract migration name from filename
   */
  private extractMigrationName(filename: string): string {
    // Remove .sql extension and use the filename as the name
    return filename.replace(/\.sql$/, '');
  }

  /**
   * Calculate checksum for migration content
   */
  private calculateChecksum(content: string): string {
    const crypto = require('crypto');
    return crypto.createHash('sha256').update(content).digest('hex');
  }

  /**
   * Create a new migration file
   */
  async createMigration(name: string): Promise<string> {
    const timestamp = new Date().toISOString().replace(/[:\-T]/g, '').split('.')[0];
    const filename = `${timestamp}_${name.replace(/\s+/g, '_').toLowerCase()}.sql`;
    const filepath = path.join(this.migrationsPath, filename);

    const template = `-- ============================================================================
-- Migration: ${filename}
-- Description: ${name}
-- Created: ${new Date().toISOString()}
-- ============================================================================

-- Add your SQL statements here

-- Example:
-- CREATE TABLE example (
--     id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
--     name VARCHAR(255) NOT NULL,
--     created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW()
-- );

-- Log migration completion
INSERT INTO logs (level, message, category, metadata)
VALUES ('info', 'Migration ${filename} completed', 'migration', '{"migration": "${filename}"}');
`;

    await fs.writeFile(filepath, template, 'utf-8');
    console.log(`âœ… Created migration file: ${filename}`);
    return filepath;
  }
}

// Export singleton instance
export const migrationRunner = new MigrationRunner();

/**
 * Convenience function to run migrations
 */
export async function runMigrations(): Promise<void> {
  await migrationRunner.runMigrations();
}

/**
 * Convenience function to check migration status
 */
export async function getMigrationStatus() {
  return migrationRunner.getStatus();
}

/**
 * Convenience function to verify migrations
 */
export async function verifyMigrations() {
  return migrationRunner.verifyMigrations();
}

import { EventEmitter } from 'events';

import { MarketType } from '@jabbr/shared';
import type {
  Exchange,
  TradeSide,
  TradeType,
  TradeStatus,
  ExchangeApiKey
} from '@jabbr/shared';

/**
 * Order request interface
 */
export interface OrderRequest {
  symbol: string;
  side: TradeSide;
  type: TradeType;
  amount: number;
  price?: number;
  leverage?: number;
  reduceOnly?: boolean;
  timeInForce?: 'GTC' | 'IOC' | 'FOK';
  clientOrderId?: string;
  stopPrice?: number;
  marketType: MarketType;
}

/**
 * Order response interface
 */
export interface OrderResponse {
  orderId: string;
  clientOrderId?: string;
  symbol: string;
  side: TradeSide;
  type: TradeType;
  amount: number;
  price?: number;
  filled: number;
  remaining: number;
  status: TradeStatus;
  fee: number;
  timestamp: Date;
  marketType: MarketType;
}

/**
 * Market data interface
 */
export interface MarketData {
  symbol: string;
  price: number;
  bid: number;
  ask: number;
  volume: number;
  change24h: number;
  high24h: number;
  low24h: number;
  timestamp: Date;
  marketType: MarketType;
}

/**
 * Position information interface
 */
export interface PositionInfo {
  symbol: string;
  side: TradeSide;
  size: number;
  entryPrice: number;
  markPrice: number;
  unrealizedPnl: number;
  realizedPnl: number;
  leverage: number;
  margin: number;
  marginMode: 'isolated' | 'cross';
  liquidationPrice?: number;
  timestamp: Date;
  marketType: MarketType;
}

/**
 * Account balance interface
 */
export interface AccountBalance {
  currency: string;
  total: number;
  available: number;
  locked: number;
  marketType: MarketType;
}

/**
 * Exchange capabilities interface
 */
export interface ExchangeCapabilities {
  spot: boolean;
  futures: boolean;
  options: boolean;
  margin: boolean;
  maxLeverage: {
    spot: number;
    futures: number;
  };
  supportedOrderTypes: TradeType[];
  supportedTimeframes: string[];
  rateLimits: {
    requests: number;
    window: number; // milliseconds
  };
}

/**
 * Base Exchange Abstract Class
 * Defines the interface that all exchange implementations must follow
 */
export abstract class BaseExchange extends EventEmitter {
  protected apiKey: ExchangeApiKey;
  protected isTestnet: boolean;
  protected isConnected = false;
  protected rateLimitCounter: Map<string, number> = new Map();
  
  constructor(apiKey: ExchangeApiKey, isTestnet = true) {
    super();
    this.apiKey = apiKey;
    this.isTestnet = isTestnet;
  }

  /**
   * Get exchange name
   */
  abstract getName(): Exchange;

  /**
   * Get exchange capabilities
   */
  abstract getCapabilities(): ExchangeCapabilities;

  /**
   * Initialize connection to exchange
   */
  abstract connect(): Promise<void>;

  /**
   * Disconnect from exchange
   */
  abstract disconnect(): Promise<void>;

  /**
   * Test API connectivity and permissions
   */
  abstract testConnection(): Promise<boolean>;

  // ============================================================================
  // MARKET DATA METHODS
  // ============================================================================

  /**
   * Get current market data for a symbol
   */
  abstract getMarketData(symbol: string, marketType: MarketType): Promise<MarketData>;

  /**
   * Get order book for a symbol
   */
  abstract getOrderBook(symbol: string, marketType: MarketType, depth?: number): Promise<{
    bids: [number, number][];
    asks: [number, number][];
    timestamp: Date;
  }>;

  /**
   * Get recent trades for a symbol
   */
  abstract getRecentTrades(symbol: string, marketType: MarketType, limit?: number): Promise<{
    id: string;
    price: number;
    amount: number;
    side: TradeSide;
    timestamp: Date;
  }[]>;

  /**
   * Get candlestick/kline data
   */
  abstract getKlines(
    symbol: string, 
    interval: string, 
    marketType: MarketType,
    startTime?: Date, 
    endTime?: Date, 
    limit?: number
  ): Promise<{
    timestamp: Date;
    open: number;
    high: number;
    low: number;
    close: number;
    volume: number;
  }[]>;

  // ============================================================================
  // TRADING METHODS
  // ============================================================================

  /**
   * Place a new order
   */
  abstract placeOrder(orderRequest: OrderRequest): Promise<OrderResponse>;

  /**
   * Cancel an existing order
   */
  abstract cancelOrder(orderId: string, symbol: string, marketType: MarketType): Promise<boolean>;

  /**
   * Cancel all orders for a symbol
   */
  abstract cancelAllOrders(symbol?: string, marketType?: MarketType): Promise<boolean>;

  /**
   * Get order status
   */
  abstract getOrder(orderId: string, symbol: string, marketType: MarketType): Promise<OrderResponse>;

  /**
   * Get open orders
   */
  abstract getOpenOrders(symbol?: string, marketType?: MarketType): Promise<OrderResponse[]>;

  /**
   * Get order history
   */
  abstract getOrderHistory(
    symbol?: string, 
    marketType?: MarketType,
    startTime?: Date, 
    endTime?: Date, 
    limit?: number
  ): Promise<OrderResponse[]>;

  // ============================================================================
  // POSITION METHODS (FUTURES)
  // ============================================================================

  /**
   * Get current positions
   */
  abstract getPositions(symbol?: string): Promise<PositionInfo[]>;

  /**
   * Set leverage for a symbol
   */
  abstract setLeverage(symbol: string, leverage: number): Promise<boolean>;

  /**
   * Set margin mode for a symbol
   */
  abstract setMarginMode(symbol: string, mode: 'isolated' | 'cross'): Promise<boolean>;

  /**
   * Set position mode (one-way or hedge)
   */
  abstract setPositionMode(mode: 'one-way' | 'hedge'): Promise<boolean>;

  // ============================================================================
  // ACCOUNT METHODS
  // ============================================================================

  /**
   * Get account balance
   */
  abstract getBalance(marketType?: MarketType): Promise<AccountBalance[]>;

  /**
   * Get trading fees
   */
  abstract getTradingFees(symbol?: string, marketType?: MarketType): Promise<{
    maker: number;
    taker: number;
  }>;

  // ============================================================================
  // UTILITY METHODS
  // ============================================================================

  /**
   * Check if connected to exchange
   */
  isConnectedToExchange(): boolean {
    return this.isConnected;
  }

  /**
   * Get testnet status
   */
  isTestnetMode(): boolean {
    return this.isTestnet;
  }

  /**
   * Rate limit check
   */
  protected checkRateLimit(endpoint: string): boolean {
    const now = Date.now();
    const windowStart = now - this.getCapabilities().rateLimits.window;
    const key = `${endpoint}_${Math.floor(now / this.getCapabilities().rateLimits.window)}`;
    const count = this.rateLimitCounter.get(key) || 0;
    if (count >= this.getCapabilities().rateLimits.requests) {
      return false;
    }
    this.rateLimitCounter.set(key, count + 1);
    // Clean up old entries
    for (const [k] of this.rateLimitCounter.entries()) {
      const keyParts = k.split('_');
      const timestampStr = keyParts.at(1);
      if (timestampStr && parseInt(timestampStr) < windowStart) {
        this.rateLimitCounter.delete(k);
      }
    }
    return true;
  }

  /**
   * Validate order request
   */
  protected validateOrderRequest(orderRequest: OrderRequest): void {
    if (!orderRequest.symbol) {
      throw new Error('Symbol is required');
    }
    
    if (!orderRequest.side) {
      throw new Error('Side is required');
    }
    
    if (!orderRequest.type) {
      throw new Error('Order type is required');
    }
    
    if (!orderRequest.amount || orderRequest.amount <= 0) {
      throw new Error('Amount must be positive');
    }
    
    if (orderRequest.type === 'limit' && (!orderRequest.price || orderRequest.price <= 0)) {
      throw new Error('Price is required for limit orders');
    }
    
    const capabilities = this.getCapabilities();
    
    // Check if market type is supported
    if (orderRequest.marketType === MarketType.SPOT && !capabilities.spot) {
      throw new Error('Spot trading not supported');
    }
    
    if (orderRequest.marketType === MarketType.FUTURES && !capabilities.futures) {
      throw new Error('Futures trading not supported');
    }
    
    // Check leverage limits
    if (orderRequest.leverage) {
      const maxLeverage = orderRequest.marketType === MarketType.SPOT 
        ? capabilities.maxLeverage.spot 
        : capabilities.maxLeverage.futures;
        
      if (orderRequest.leverage > maxLeverage) {
        throw new Error(`Leverage cannot exceed ${maxLeverage}x for ${orderRequest.marketType}`);
      }
    }
    
    // Check if order type is supported
    if (!capabilities.supportedOrderTypes.includes(orderRequest.type)) {
      throw new Error(`Order type ${orderRequest.type} not supported`);
    }
  }

  /**
   * Format symbol for exchange
   */
  protected abstract formatSymbol(symbol: string, marketType: MarketType): string;

  /**
   * Parse symbol from exchange format
   */
  protected abstract parseSymbol(exchangeSymbol: string, marketType: MarketType): string;

  /**
   * Get server time from exchange
   */
  abstract getServerTime(): Promise<Date>;

  /**
   * Subscribe to real-time data (WebSocket)
   */
  abstract subscribeToMarketData(symbol: string, marketType: MarketType): Promise<void>;

  /**
   * Unsubscribe from real-time data
   */
  abstract unsubscribeFromMarketData(symbol: string, marketType: MarketType): Promise<void>;

  /**
   * Subscribe to order updates
   */
  abstract subscribeToOrderUpdates(): Promise<void>;

  /**
   * Subscribe to position updates (futures)
   */
  abstract subscribeToPositionUpdates(): Promise<void>;
}

