/**
 * Exchange Manager - Centralized Exchange Connection Management
 * 
 * Manages multiple exchange connections and provides a unified interface
 * for trading across different exchanges. Handles:
 * - Exchange connection lifecycle
 * - API key management
 * - Exchange selection based on trading pairs
 * - Connection health monitoring
 * - Automatic reconnection
 */

import { EventEmitter } from 'events';

import type { 
  Exchange, 
  ExchangeApiKey} from '@jabbr/shared';
import { 
  MarketType
} from '@jabbr/shared';

import { database } from '../services/database.service';

import type { BaseExchange } from './base-exchange';
import type { ExchangeCapabilities } from './base-exchange';
import { BybitExchange } from './bybit-exchange';
// import { BinanceExchange } from './binance-exchange'; // TODO: Implement BinanceExchange


export interface ExchangeConnection {
  exchange: Exchange;
  instance: BaseExchange;
  apiKeyId: string;
  isConnected: boolean;
  lastConnectedAt?: Date;
  lastError?: string;
  capabilities: ExchangeCapabilities;
}

export interface ExchangeManagerConfig {
  autoReconnect: boolean;
  reconnectInterval: number; // milliseconds
  healthCheckInterval: number; // milliseconds
  maxReconnectAttempts: number;
}

export class ExchangeManager extends EventEmitter {
  private exchanges: Map<string, ExchangeConnection> = new Map();
  private config: ExchangeManagerConfig;
  private healthCheckInterval?: NodeJS.Timeout;
  private reconnectAttempts: Map<string, number> = new Map();

  constructor(config?: Partial<ExchangeManagerConfig>) {
    super();
    
    this.config = {
      autoReconnect: true,
      reconnectInterval: 30000, // 30 seconds
      healthCheckInterval: 60000, // 1 minute
      maxReconnectAttempts: 5,
      ...config
    };

    // Start health monitoring
    this.startHealthMonitoring();
  }

  /**
   * Initialize exchange connection
   */
  async initializeExchange(
    exchange: Exchange,
    apiKeyId: string,
    isTestnet = false
  ): Promise<ExchangeConnection> {
    try {
      console.log('ðŸ”Œ Initializing exchange connection', {
        exchange,
        apiKeyId,
        isTestnet
      });

      // Load API key from database
      const apiKey = await this.loadApiKey(apiKeyId);
      if (!apiKey) {
        throw new Error(`API key ${apiKeyId} not found`);
      }

      // Create exchange instance
      const exchangeInstance = this.createExchangeInstance(exchange, apiKey, isTestnet);
      
      // Connect to exchange
      await exchangeInstance.connect();
      
      // Test connection
      const isConnected = await exchangeInstance.testConnection();
      if (!isConnected) {
        throw new Error('Failed to establish connection to exchange');
      }

      // Get exchange capabilities
      const capabilities = exchangeInstance.getCapabilities();

      // Create connection object
      const connection: ExchangeConnection = {
        exchange,
        instance: exchangeInstance,
        apiKeyId,
        isConnected: true,
        lastConnectedAt: new Date(),
        capabilities
      };

      // Store connection
      const connectionKey = this.getConnectionKey(exchange, apiKeyId);
      this.exchanges.set(connectionKey, connection);

      console.log('âœ… Exchange connection established', {
        exchange,
        apiKeyId,
        capabilities
      });

      this.emit('exchange-connected', {
        exchange,
        apiKeyId,
        capabilities
      });

      return connection;

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      console.error('âŒ Failed to initialize exchange', {
        exchange,
        apiKeyId,
        error: errorMessage
      });

      // Store failed connection for retry
      const connectionKey = this.getConnectionKey(exchange, apiKeyId);
      this.exchanges.set(connectionKey, {
        exchange,
        instance: null as any,
        apiKeyId,
        isConnected: false,
        lastError: errorMessage,
        capabilities: {} as any
      });

      throw new Error(`Exchange initialization failed: ${errorMessage}`);
    }
  }

  /**
   * Get exchange connection for trading
   */
  async getExchange(
    exchange: Exchange,
    apiKeyId?: string
  ): Promise<BaseExchange> {
    // If apiKeyId provided, get specific connection
    if (apiKeyId) {
      const connectionKey = this.getConnectionKey(exchange, apiKeyId);
      const connection = this.exchanges.get(connectionKey);
      
      if (!connection) {
        throw new Error(`No connection found for ${exchange} with API key ${apiKeyId}`);
      }

      if (!connection.isConnected) {
        throw new Error(`Exchange ${exchange} is not connected`);
      }

      return connection.instance;
    }

    // Otherwise, get any connected instance for the exchange
    for (const connection of this.exchanges.values()) {
      if (connection.exchange === exchange && connection.isConnected) {
        return connection.instance;
      }
    }

    throw new Error(`No connected instance found for ${exchange}`);
  }

  /**
   * Get all connected exchanges
   */
  getConnectedExchanges(): ExchangeConnection[] {
    return Array.from(this.exchanges.values()).filter(conn => conn.isConnected);
  }

  /**
   * Get exchange for a specific trading pair
   */
  async getExchangeForSymbol(
    symbol: string,
    marketType: MarketType
  ): Promise<BaseExchange | null> {
    // Find exchanges that support this symbol and market type
    const supportedExchanges: BaseExchange[] = [];

    for (const connection of this.exchanges.values()) {
      if (!connection.isConnected) {continue;}

      // Check if exchange supports the market type
      if (marketType === MarketType.SPOT && !connection.capabilities.spot) {continue;}
      if (marketType === MarketType.FUTURES && !connection.capabilities.futures) {continue;}

      try {
        // Test if symbol is available
        await connection.instance.getMarketData(symbol, marketType);
        supportedExchanges.push(connection.instance);
      } catch (error) {
        // Symbol not available on this exchange
        continue;
      }
    }

    // Return the first available exchange (could be enhanced with selection logic)
    return supportedExchanges.length > 0 ? supportedExchanges.at(0) || null : null;
  }

  /**
   * Disconnect from an exchange
   */
  async disconnectExchange(exchange: Exchange, apiKeyId: string): Promise<void> {
    const connectionKey = this.getConnectionKey(exchange, apiKeyId);
    const connection = this.exchanges.get(connectionKey);

    if (!connection) {
      console.warn('âš ï¸ No connection found to disconnect', {
        exchange,
        apiKeyId
      });
      return;
    }

    try {
      if (connection.instance && connection.isConnected) {
        await connection.instance.disconnect();
      }

      connection.isConnected = false;
      
      console.log('ðŸ”Œ Exchange disconnected', {
        exchange,
        apiKeyId
      });

      this.emit('exchange-disconnected', {
        exchange,
        apiKeyId
      });

    } catch (error) {
      console.error('âŒ Error disconnecting from exchange', {
        exchange,
        apiKeyId,
        error: error instanceof Error ? error.message : String(error)
      });
    }
  }

  /**
   * Disconnect from all exchanges
   */
  async disconnectAll(): Promise<void> {
    console.log('ðŸ”Œ Disconnecting from all exchanges');

    const disconnectPromises: Promise<void>[] = [];

    for (const connection of this.exchanges.values()) {
      if (connection.isConnected) {
        disconnectPromises.push(
          this.disconnectExchange(connection.exchange, connection.apiKeyId)
        );
      }
    }

    await Promise.all(disconnectPromises);

    // Clear health check interval
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = undefined;
    }

    console.log('âœ… All exchanges disconnected');
  }

  /**
   * Create exchange instance based on type
   */
  private createExchangeInstance(
    exchange: Exchange,
    apiKey: ExchangeApiKey,
    isTestnet: boolean
  ): BaseExchange {
    switch (exchange) {
      case 'bybit':
        return new BybitExchange(apiKey, isTestnet);
      
      case 'binance':
        // TODO: Implement BinanceExchange
        throw new Error('Binance exchange not yet implemented');
        // return new BinanceExchange(apiKey, isTestnet);
      
      default:
        throw new Error(`Unsupported exchange: ${exchange}`);
    }
  }

  /**
   * Load API key from database
   */
  private async loadApiKey(apiKeyId: string): Promise<ExchangeApiKey | null> {
    try {
      const result = await database.query(
        'SELECT * FROM exchange_api_keys WHERE id = $1 AND is_active = true',
        [apiKeyId]
      );

      if (result.length === 0) {
        return null;
      }

      const row = result.at(0);
      
      return {
        id: row.id,
        userId: row.user_id,
        exchange: row.exchange,
        keyName: row.label,
        apiKey: row.api_key,
        apiSecret: row.api_secret,
        passphrase: row.passphrase,
        sandbox: row.is_testnet,
        permissions: row.permissions,
        isActive: row.is_active,
        createdAt: new Date(row.created_at),
        updatedAt: new Date(row.updated_at)
      };

    } catch (error) {
      console.error('âŒ Failed to load API key', {
        apiKeyId,
        error: error instanceof Error ? error.message : String(error)
      });
      return null;
    }
  }

  /**
   * Start health monitoring for all connections
   */
  private startHealthMonitoring(): void {
    this.healthCheckInterval = setInterval(
      () => this.checkConnectionHealth(),
      this.config.healthCheckInterval
    );
  }

  /**
   * Check health of all connections
   */
  private async checkConnectionHealth(): Promise<void> {
    for (const [key, connection] of this.exchanges.entries()) {
      if (!connection.instance) {continue;}

      try {
        const isHealthy = await connection.instance.testConnection();
        
        if (isHealthy && !connection.isConnected) {
          // Connection restored
          connection.isConnected = true;
          connection.lastConnectedAt = new Date();
          connection.lastError = undefined;
          
          console.log('âœ… Exchange connection restored', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId
          });

          this.emit('exchange-reconnected', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId
          });

          // Reset reconnect attempts
          this.reconnectAttempts.delete(key);

        } else if (!isHealthy && connection.isConnected) {
          // Connection lost
          connection.isConnected = false;
          connection.lastError = 'Health check failed';
          
          console.warn('âš ï¸ Exchange connection lost', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId
          });

          this.emit('exchange-connection-lost', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId
          });

          // Attempt reconnection if enabled
          if (this.config.autoReconnect) {
            this.scheduleReconnect(connection);
          }
        }

      } catch (error) {
        if (connection.isConnected) {
          connection.isConnected = false;
          connection.lastError = error instanceof Error ? error.message : String(error);
          
          console.error('âŒ Exchange health check failed', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId,
            error: connection.lastError
          });

          if (this.config.autoReconnect) {
            this.scheduleReconnect(connection);
          }
        }
      }
    }
  }

  /**
   * Schedule reconnection attempt
   */
  private async scheduleReconnect(connection: ExchangeConnection): Promise<void> {
    const key = this.getConnectionKey(connection.exchange, connection.apiKeyId);
    const attempts = this.reconnectAttempts.get(key) || 0;

    if (attempts >= this.config.maxReconnectAttempts) {
      console.error('âŒ Max reconnection attempts reached', {
        exchange: connection.exchange,
        apiKeyId: connection.apiKeyId,
        attempts
      });
      return;
    }

    this.reconnectAttempts.set(key, attempts + 1);

    setTimeout(async () => {
      try {
        console.log('ðŸ”„ Attempting to reconnect', {
          exchange: connection.exchange,
          apiKeyId: connection.apiKeyId,
          attempt: attempts + 1
        });

        await connection.instance.connect();
        const isConnected = await connection.instance.testConnection();

        if (isConnected) {
          connection.isConnected = true;
          connection.lastConnectedAt = new Date();
          connection.lastError = undefined;
          this.reconnectAttempts.delete(key);

          console.log('âœ… Reconnection successful', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId
          });

          this.emit('exchange-reconnected', {
            exchange: connection.exchange,
            apiKeyId: connection.apiKeyId
          });
        } else {
          throw new Error('Connection test failed');
        }

      } catch (error) {
        console.error('âŒ Reconnection failed', {
          exchange: connection.exchange,
          apiKeyId: connection.apiKeyId,
          attempt: attempts + 1,
          error: error instanceof Error ? error.message : String(error)
        });

        // Schedule next attempt
        this.scheduleReconnect(connection);
      }
    }, this.config.reconnectInterval);
  }

  /**
   * Get connection key
   */
  private getConnectionKey(exchange: Exchange, apiKeyId: string): string {
    return `${exchange}:${apiKeyId}`;
  }

  /**
   * Get exchange manager statistics
   */
  getStats() {
    const connections = Array.from(this.exchanges.values());
    
    return {
      totalConnections: connections.length,
      connectedExchanges: connections.filter(c => c.isConnected).length,
      disconnectedExchanges: connections.filter(c => !c.isConnected).length,
      exchanges: connections.map(c => ({
        exchange: c.exchange,
        apiKeyId: c.apiKeyId,
        isConnected: c.isConnected,
        lastConnectedAt: c.lastConnectedAt,
        lastError: c.lastError
      }))
    };
  }
}

/**
 * Express Middleware for Application Performance Monitoring
 * 
 * Automatically tracks all HTTP requests, response times, error rates,
 * and endpoint performance for the ApplicationMonitorService
 */

import { performance } from 'perf_hooks';

import type { Request, Response, NextFunction } from 'express';

import ApplicationMonitorService from '../services/application-monitor.service';
import logger from '../services/logging.service';

/**
 * Extended request interface with monitoring data
 */
interface MonitoredRequest extends Request {
  startTime?: number;
  requestId?: string;
}

/**
 * Application Performance Monitoring Middleware
 */
class AppMonitoringMiddleware {
  private applicationMonitor: ApplicationMonitorService;

  constructor() {
    this.applicationMonitor = new ApplicationMonitorService();
  }

  /**
   * Express middleware to track request performance
   */
  public trackRequests() {
    return (req: MonitoredRequest, res: Response, next: NextFunction) => {
      // Generate unique request ID
      req.requestId = `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      req.startTime = performance.now();

      // Track request start
      logger.debug('Request started', {
        requestId: req.requestId,
        method: req.method,
        path: req.path,
        ip: req.ip,
        userAgent: req.get('User-Agent')
      });

      // Override res.end to capture response data
      const originalEnd = res.end;
      const self = this;
      res.end = function(this: Response, chunk?: any, encoding?: any): Response {
        const endTime = performance.now();
        const responseTime = endTime - (req.startTime || endTime);

        // Log the request to ApplicationMonitorService
        self.applicationMonitor.logRequest({
          method: req.method,
          path: req.path,
          statusCode: res.statusCode,
          responseTime: Math.round(responseTime * 100) / 100, // Round to 2 decimal places
          userAgent: req.get('User-Agent'),
          ip: req.ip
        });

        // Log completion
        logger.debug('Request completed', {
          requestId: req.requestId,
          statusCode: res.statusCode,
          responseTime: Math.round(responseTime * 100) / 100
        });

        // Call original end method
        return originalEnd.call(this, chunk, encoding);
      };

      // Store references for the response handler
      (req as any)._applicationMonitor = this.applicationMonitor;

      next();
    };
  }

  /**
   * Error handling middleware to track application errors
   */
  public trackErrors() {
    return (err: Error, req: MonitoredRequest, res: Response, next: NextFunction) => {
      // Log error to ApplicationMonitorService
      this.applicationMonitor.logError({
        type: err.name || 'UnknownError',
        message: err.message,
        stack: err.stack,
        level: 'error',
        context: {
          requestId: req.requestId,
          method: req.method,
          path: req.path,
          ip: req.ip,
          userAgent: req.get('User-Agent')
        }
      });

      next(err);
    };
  }

  /**
   * Middleware to track database queries
   */
  public trackDatabaseQueries() {
    return (queryTime: number, isSlowQuery?: boolean) => {
      this.applicationMonitor.updateDatabaseMetrics({
        queryTime,
        isSlowQuery
      });
    };
  }

  /**
   * Track WebSocket connections
   */
  public trackWebSocketConnection(connected = true) {
    const currentConnections = this.getWebSocketConnectionCount();
    
    this.applicationMonitor.updateWebSocketMetrics({
      activeConnections: connected ? currentConnections + 1 : Math.max(0, currentConnections - 1),
      disconnection: !connected
    });
  }

  /**
   * Track WebSocket messages
   */
  public trackWebSocketMessage(messageCount = 1) {
    this.applicationMonitor.updateWebSocketMetrics({
      messagesSent: messageCount
    });
  }

  /**
   * Get current WebSocket connection count (placeholder - would be implemented with actual WebSocket server)
   */
  private getWebSocketConnectionCount(): number {
    // This would be implemented to get actual WebSocket connection count
    // from the WebSocket server instance
    return 0;
  }

  /**
   * Initialize monitoring middleware
   */
  public initialize() {
    // Start the ApplicationMonitorService
    this.applicationMonitor.start();

    logger.info('Application monitoring middleware initialized');

    return {
      trackRequests: this.trackRequests.bind(this),
      trackErrors: this.trackErrors.bind(this),
      trackDatabaseQueries: this.trackDatabaseQueries.bind(this),
      trackWebSocketConnection: this.trackWebSocketConnection.bind(this),
      trackWebSocketMessage: this.trackWebSocketMessage.bind(this)
    };
  }

  /**
   * Get current application metrics
   */
  public getMetrics() {
    return this.applicationMonitor.getCurrentMetrics();
  }

  /**
   * Get monitoring statistics
   */
  public getStats() {
    return this.applicationMonitor.getStats();
  }

  /**
   * Shutdown monitoring
   */
  public shutdown() {
    this.applicationMonitor.shutdown();
    logger.info('Application monitoring middleware shut down');
  }
}

// Export singleton instance
export const appMonitoringMiddleware = new AppMonitoringMiddleware();

// Export class for custom instances
export default AppMonitoringMiddleware;

/**
 * Health Check API Routes
 * 
 * Provides REST API endpoints for system health monitoring
 * including overall health, component-specific health,
 * and readiness/liveness probes for Kubernetes.
 */

import type { Request, Response } from 'express';
import { Router } from 'express';

import logger from '../services/logging.service';
import SystemHealthService from '../services/system-health.service';

const router = Router();
const healthService = new SystemHealthService();

/**
 * @route GET /health
 * @desc Get overall system health status
 * @access Public
 */
router.get('/', async (req: Request, res: Response) => {
  const startTime = Date.now();
  
  try {
    const useCache = req.query.cache !== 'false';
    const health = await healthService.getSystemHealth(useCache);
    const responseTime = Date.now() - startTime;

    // Record metrics
    healthService.recordRequest(responseTime, health.status === 'unhealthy');

    // Set appropriate HTTP status code
    let statusCode = 200;
    if (health.status === 'degraded') {
      statusCode = 503; // Service Unavailable
    } else if (health.status === 'unhealthy') {
      statusCode = 503; // Service Unavailable
    }

    res.status(statusCode).json({
      success: health.status === 'healthy',
      data: health,
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/health'
      }
    });

    logger.debug('[HealthAPI] Health check completed', {
      status: health.status,
      responseTime,
      cached: useCache
    });
  } catch (error) {
    const responseTime = Date.now() - startTime;
    const errorMessage = error instanceof Error ? error.message : String(error);

    healthService.recordRequest(responseTime, true);

    logger.error('[HealthAPI] Health check failed', {
      error: errorMessage,
      responseTime
    });

    res.status(500).json({
      success: false,
      error: {
        message: 'Health check system failure',
        details: errorMessage
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/health'
      }
    });
  }
});

/**
 * @route GET /health/quick
 * @desc Get quick health status (minimal checks)
 * @access Public
 */
router.get('/quick', async (req: Request, res: Response) => {
  const startTime = Date.now();
  
  try {
    const health = await healthService.getQuickHealth();
    const responseTime = Date.now() - startTime;

    healthService.recordRequest(responseTime, health.status !== 'healthy');

    const statusCode = health.status === 'healthy' ? 200 : 503;

    res.status(statusCode).json({
      success: health.status === 'healthy',
      data: {
        status: health.status,
        uptime: health.uptime,
        timestamp: new Date().toISOString()
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/health/quick'
      }
    });
  } catch (error) {
    const responseTime = Date.now() - startTime;
    const errorMessage = error instanceof Error ? error.message : String(error);

    healthService.recordRequest(responseTime, true);

    res.status(500).json({
      success: false,
      error: {
        message: 'Quick health check failed',
        details: errorMessage
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/health/quick'
      }
    });
  }
});

/**
 * @route GET /health/detailed
 * @desc Get detailed health information with all components
 * @access Public
 */
router.get('/detailed', async (req: Request, res: Response) => {
  const startTime = Date.now();
  
  try {
    const health = await healthService.getSystemHealth(false); // Force fresh check
    const responseTime = Date.now() - startTime;

    healthService.recordRequest(responseTime, health.status === 'unhealthy');

    res.status(200).json({
      success: true,
      data: {
        ...health,
        checkDuration: responseTime,
        detailedCheck: true
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/health/detailed'
      }
    });
  } catch (error) {
    const responseTime = Date.now() - startTime;
    const errorMessage = error instanceof Error ? error.message : String(error);

    healthService.recordRequest(responseTime, true);

    res.status(500).json({
      success: false,
      error: {
        message: 'Detailed health check failed',
        details: errorMessage
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: '/health/detailed'
      }
    });
  }
});

/**
 * @route GET /health/components/:component
 * @desc Get health status for a specific component
 * @access Public
 */
router.get('/components/:component', async (req: Request, res: Response): Promise<void> => {
  const startTime = Date.now();
  const componentName = req.params.component;
  
  try {
    const health = await healthService.getSystemHealth();
    const responseTime = Date.now() - startTime;

    const component = health.components[componentName as keyof typeof health.components];
    
    if (!component) {
      res.status(404).json({
        success: false,
        error: {
          message: `Component '${componentName}' not found`,
          availableComponents: Object.keys(health.components)
        },
        meta: {
          responseTime,
          timestamp: new Date().toISOString(),
          endpoint: `/health/components/${componentName}`
        }
      });
      return;
    }

    healthService.recordRequest(responseTime, component.status === 'unhealthy');

    const statusCode = component.status === 'healthy' ? 200 : 503;

    res.status(statusCode).json({
      success: component.status === 'healthy',
      data: {
        component: componentName,
        ...component
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: `/health/components/${componentName}`
      }
    });
  } catch (error) {
    const responseTime = Date.now() - startTime;
    const errorMessage = error instanceof Error ? error.message : String(error);

    healthService.recordRequest(responseTime, true);

    res.status(500).json({
      success: false,
      error: {
        message: `Component health check failed for '${componentName}'`,
        details: errorMessage
      },
      meta: {
        responseTime,
        timestamp: new Date().toISOString(),
        endpoint: `/health/components/${componentName}`
      }
    });
  }
});

/**
 * @route GET /health/readiness
 * @desc Kubernetes readiness probe - checks if service is ready to accept traffic
 * @access Public
 */
router.get('/readiness', async (req: Request, res: Response) => {
  const startTime = Date.now();
  
  try {
    const health = await healthService.getSystemHealth();
    const responseTime = Date.now() - startTime;

    // Service is ready if database is healthy and no critical components are unhealthy
    const isReady = health.components.database.status !== 'unhealthy' &&
                   health.status !== 'unhealthy';

    healthService.recordRequest(responseTime, !isReady);

    const statusCode = isReady ? 200 : 503;
    const status = isReady ? 'ready' : 'not_ready';

    res.status(statusCode).json({
      status,
      timestamp: new Date().toISOString(),
      checks: {
        database: health.components.database.status,
        overall: health.status
      },
      uptime: health.uptime,
      responseTime
    });

    logger.debug('[HealthAPI] Readiness probe', {
      status,
      responseTime,
      isReady
    });
  } catch (error) {
    const responseTime = Date.now() - startTime;
    const errorMessage = error instanceof Error ? error.message : String(error);

    healthService.recordRequest(responseTime, true);

    logger.error('[HealthAPI] Readiness probe failed', {
      error: errorMessage,
      responseTime
    });

    res.status(503).json({
      status: 'not_ready',
      timestamp: new Date().toISOString(),
      error: errorMessage,
      responseTime
    });
  }
});

/**
