 */
router.get('/exchanges/:exchangeName', async (req: Request, res: Response): Promise<Response> => {
  const startTime = Date.now();
  const { exchangeName } = req.params;
  
  if (!exchangeName) {
    return res.status(400).json({
      success: false,
      error: {
        message: 'Exchange name is required'
