/**
 * Alert Manager Service
 * 
 * Centralized alerting system that aggregates alerts from all monitoring services
 * and provides notification mechanisms for critical issues.
 */

import { EventEmitter } from 'events';

import { CONSTANTS } from '@jabbr/shared';

import type { JabbrWebSocketServer } from '../websocket/websocket-server';

import logger from './logging.service';

export interface Alert {
  id: string;
  type: 'system' | 'application' | 'trading' | 'security' | 'custom';
  category: string; // cpu, memory, response_time, error_rate, etc.
  level: 'info' | 'warning' | 'error' | 'critical';
  title: string;
  message: string;
  source: string; // which service generated the alert
  value?: number;
  threshold?: number;
  metadata?: any;
  timestamp: Date;
  acknowledged: boolean;
  acknowledgedBy?: string;
  acknowledgedAt?: Date;
  resolved: boolean;
  resolvedAt?: Date;
  escalated: boolean;
  escalatedAt?: Date;
  notificationsSent: string[]; // channels where notifications were sent
}

export interface AlertRule {
  id: string;
  name: string;
  type: Alert['type'];
  category: string;
  condition: string; // expression to evaluate
  threshold: number;
  level: Alert['level'];
  enabled: boolean;
  cooldownPeriod: number; // milliseconds
  escalationDelay: number; // milliseconds
  notificationChannels: string[];
  lastTriggered?: Date;
}

export interface NotificationChannel {
  id: string;
  name: string;
  type: 'websocket' | 'email' | 'webhook' | 'console';
  config: any;
  enabled: boolean;
  lastUsed?: Date;
}

export interface AlertStats {
  total: number;
  byLevel: Record<Alert['level'], number>;
  byType: Record<Alert['type'], number>;
  byCategory: Record<string, number>;
  acknowledged: number;
  resolved: number;
  active: number;
  escalated: number;
}

export class AlertManagerService extends EventEmitter {
  private alerts: Map<string, Alert> = new Map();
  private alertRules: Map<string, AlertRule> = new Map();
  private notificationChannels: Map<string, NotificationChannel> = new Map();
  private wsServer?: JabbrWebSocketServer;
  private alertHistory: Alert[] = [];
  private readonly MAX_HISTORY = 1000;
  private readonly CLEANUP_INTERVAL = 60 * 60 * 1000; // 1 hour
  private cleanupInterval?: NodeJS.Timeout;

  constructor(wsServer?: JabbrWebSocketServer) {
    super();
    this.wsServer = wsServer;
    
    // Setup default notification channels
    this.setupDefaultChannels();
    
    // Setup default alert rules
    this.setupDefaultRules();
    
    // Start cleanup routine
    this.cleanupInterval = setInterval(() => {
      this.cleanupOldAlerts();
    }, this.CLEANUP_INTERVAL);

    logger.info('Alert Manager Service initialized');
  }

  /**
   * Create a new alert
   */
  public createAlert(alertData: Omit<Alert, 'id' | 'timestamp' | 'acknowledged' | 'resolved' | 'escalated' | 'notificationsSent'>): Alert {
    const alert: Alert = {
      id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      acknowledged: false,
      resolved: false,
      escalated: false,
      notificationsSent: [],
      ...alertData
    };

    // Check for duplicates or similar alerts
    const similarAlert = this.findSimilarAlert(alert);
    if (similarAlert && !similarAlert.resolved) {
      // Update existing alert instead of creating duplicate
      logger.debug('Similar alert found, updating existing', {
        existingId: similarAlert.id,
        newAlert: alert.title
      });
      return this.updateAlert(similarAlert.id, {
        message: alert.message,
        value: alert.value,
        timestamp: alert.timestamp,
        metadata: { ...similarAlert.metadata, ...alert.metadata }
      });
    }

    // Store the alert
    this.alerts.set(alert.id, alert);
    this.alertHistory.push({ ...alert });

    // Trim history if needed
    if (this.alertHistory.length > this.MAX_HISTORY) {
      this.alertHistory = this.alertHistory.slice(-this.MAX_HISTORY / 2);
    }

    logger.info('Alert created', {
      id: alert.id,
      type: alert.type,
      level: alert.level,
      title: alert.title
    });

    // Send notifications
    this.sendNotifications(alert);

    // Schedule escalation if needed
    this.scheduleEscalation(alert);

    // Emit alert event
    this.emit('alert', alert);

    return alert;
  }

  /**
   * Update an existing alert
   */
  public updateAlert(alertId: string, updates: Partial<Alert>): Alert {
    const alert = this.alerts.get(alertId);
    if (!alert) {
      throw new Error(`Alert not found: ${alertId}`);
    }

    const updatedAlert = { ...alert, ...updates };
    this.alerts.set(alertId, updatedAlert);

    logger.debug('Alert updated', {
      id: alertId,
      updates: Object.keys(updates)
    });

    this.emit('alertUpdated', updatedAlert);
    return updatedAlert;
  }

  /**
   * Acknowledge an alert
   */
  public acknowledgeAlert(alertId: string, acknowledgedBy: string): Alert {
    const alert = this.alerts.get(alertId);
    if (!alert) {
      throw new Error(`Alert not found: ${alertId}`);
    }

    const updatedAlert = {
      ...alert,
      acknowledged: true,
      acknowledgedBy,
      acknowledgedAt: new Date()
    };

    this.alerts.set(alertId, updatedAlert);

    logger.info('Alert acknowledged', {
      id: alertId,
      acknowledgedBy,
      title: alert.title
    });

    // Send acknowledgment notification
    this.sendAcknowledgmentNotification(updatedAlert);

    this.emit('alertAcknowledged', updatedAlert);
    return updatedAlert;
  }

  /**
   * Resolve an alert
   */
  public resolveAlert(alertId: string): Alert {
    const alert = this.alerts.get(alertId);
    if (!alert) {
      throw new Error(`Alert not found: ${alertId}`);
    }

    const updatedAlert = {
      ...alert,
      resolved: true,
      resolvedAt: new Date()
    };

    this.alerts.set(alertId, updatedAlert);

    logger.info('Alert resolved', {
      id: alertId,
      title: alert.title,
      duration: Date.now() - alert.timestamp.getTime()
    });

    // Send resolution notification
    this.sendResolutionNotification(updatedAlert);

    this.emit('alertResolved', updatedAlert);
    return updatedAlert;
  }

  /**
   * Escalate an alert
   */
  public escalateAlert(alertId: string): Alert {
    const alert = this.alerts.get(alertId);
    if (!alert) {
      throw new Error(`Alert not found: ${alertId}`);
    }

    if (alert.escalated) {
      logger.warn('Alert already escalated', { id: alertId });
      return alert;
    }

    const updatedAlert = {
      ...alert,
      escalated: true,
      escalatedAt: new Date(),
      level: 'critical' as const
    };

    this.alerts.set(alertId, updatedAlert);

    logger.warn('Alert escalated', {
      id: alertId,
      title: alert.title,
      originalLevel: alert.level
    });

    // Send escalation notification
    this.sendEscalationNotification(updatedAlert);

    this.emit('alertEscalated', updatedAlert);
    return updatedAlert;
  }

  /**
   * Get all active alerts
   */
  public getActiveAlerts(): Alert[] {
    return Array.from(this.alerts.values()).filter(alert => !alert.resolved);
  }

  /**
   * Get all alerts (including resolved)
   */
  public getAllAlerts(): Alert[] {
    return Array.from(this.alerts.values());
  }

  /**
   * Get alerts by criteria
   */
  public getAlerts(criteria: {
    type?: Alert['type'];
    level?: Alert['level'];
    category?: string;
    acknowledged?: boolean;
    resolved?: boolean;
    escalated?: boolean;
    since?: Date;
  }): Alert[] {
    return Array.from(this.alerts.values()).filter(alert => {
      if (criteria.type && alert.type !== criteria.type) {return false;}
      if (criteria.level && alert.level !== criteria.level) {return false;}
      if (criteria.category && alert.category !== criteria.category) {return false;}
      if (criteria.acknowledged !== undefined && alert.acknowledged !== criteria.acknowledged) {return false;}
      if (criteria.resolved !== undefined && alert.resolved !== criteria.resolved) {return false;}
      if (criteria.escalated !== undefined && alert.escalated !== criteria.escalated) {return false;}
      if (criteria.since && alert.timestamp < criteria.since) {return false;}
      return true;
    });
  }

  /**
   * Get alert statistics
   */
  public getAlertStats(): AlertStats {
    const alerts = Array.from(this.alerts.values());
    
    const stats: AlertStats = {
      total: alerts.length,
      byLevel: { info: 0, warning: 0, error: 0, critical: 0 },
      byType: { system: 0, application: 0, trading: 0, security: 0, custom: 0 },
      byCategory: {},
      acknowledged: 0,
      resolved: 0,
      active: 0,
      escalated: 0
    };

    for (const alert of alerts) {
      stats.byLevel[alert.level]++;
      stats.byType[alert.type]++;
      stats.byCategory[alert.category] = (stats.byCategory[alert.category] || 0) + 1;
      
      if (alert.acknowledged) {stats.acknowledged++;}
      if (alert.resolved) {stats.resolved++;}
      if (!alert.resolved) {stats.active++;}
      if (alert.escalated) {stats.escalated++;}
    }

    return stats;
  }

  /**
   * Add a notification channel
   */
  public addNotificationChannel(channel: NotificationChannel): void {
    this.notificationChannels.set(channel.id, channel);
    logger.info('Notification channel added', {
      id: channel.id,
      name: channel.name,
      type: channel.type
    });
  }

  /**
   * Add an alert rule
   */
  public addAlertRule(rule: AlertRule): void {
    this.alertRules.set(rule.id, rule);
    logger.info('Alert rule added', {
      id: rule.id,
      name: rule.name,
      condition: rule.condition
    });
  }

  /**
   * Process incoming system alert
   */
  public processSystemAlert(alertData: {
    category: string;
    message: string;
    value?: number;
    threshold?: number;
    level: Alert['level'];
    source: string;
    metadata?: any;
  }): Alert {
    return this.createAlert({
      type: 'system',
      category: alertData.category,
      title: `System Alert: ${alertData.category}`,
      message: alertData.message,
      source: alertData.source,
      value: alertData.value,
      threshold: alertData.threshold,
      level: alertData.level,
      metadata: alertData.metadata
    });
  }

  /**
   * Process incoming application alert
   */
  public processApplicationAlert(alertData: {
    category: string;
    message: string;
    value?: number;
    threshold?: number;
    level: Alert['level'];
    source: string;
    metadata?: any;
  }): Alert {
    return this.createAlert({
      type: 'application',
      category: alertData.category,
      title: `Application Alert: ${alertData.category}`,
      message: alertData.message,
      source: alertData.source,
      value: alertData.value,
      threshold: alertData.threshold,
      level: alertData.level,
      metadata: alertData.metadata
    });
  }

  /**
   * Process incoming trading alert
   */
  public processTradingAlert(alertData: {
    category: string;
    message: string;
    value?: number;
    threshold?: number;
    level: Alert['level'];
    source: string;
    metadata?: any;
  }): Alert {
    return this.createAlert({
      type: 'trading',
      category: alertData.category,
      title: `Trading Alert: ${alertData.category}`,
      message: alertData.message,
      source: alertData.source,
      value: alertData.value,
      threshold: alertData.threshold,
      level: alertData.level,
      metadata: alertData.metadata
    });
  }

  /**
   * Find similar alerts to prevent duplicates
   */
  private findSimilarAlert(newAlert: Alert): Alert | undefined {
    const recentCutoff = new Date(Date.now() - 5 * 60 * 1000); // 5 minutes
    
    return Array.from(this.alerts.values()).find(alert => 
      alert.type === newAlert.type &&
      alert.category === newAlert.category &&
      alert.source === newAlert.source &&
      alert.timestamp >= recentCutoff &&
      !alert.resolved
    );
  }

  /**
   * Send notifications for an alert
   */
  private async sendNotifications(alert: Alert): Promise<void> {
    const channels = Array.from(this.notificationChannels.values())
      .filter(channel => channel.enabled);

    for (const channel of channels) {
      try {
        await this.sendNotificationToChannel(alert, channel);
        alert.notificationsSent.push(channel.id);
        channel.lastUsed = new Date();
      } catch (error) {
        logger.error('Failed to send notification', {
          alertId: alert.id,
          channelId: channel.id,
          error: error instanceof Error ? error.message : String(error)
        });
      }
    }
  }

  /**
   * Send notification to a specific channel
   */
  private async sendNotificationToChannel(alert: Alert, channel: NotificationChannel): Promise<void> {
    switch (channel.type) {
      case 'websocket':
        if (this.wsServer) {
          this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.ALERTS, {
            type: 'alert',
            data: alert,
            timestamp: new Date().toISOString()
          });
        }
        break;

      case 'console':
        const icon = this.getAlertIcon(alert.level);
        console.log(`${icon} [${alert.level.toUpperCase()}] ${alert.title}: ${alert.message}`);
        break;

      case 'email':
        // Email implementation would go here
        logger.debug('Email notification not implemented', { alertId: alert.id });
        break;

      case 'webhook':
        // Webhook implementation would go here
        logger.debug('Webhook notification not implemented', { alertId: alert.id });
        break;

      default:
        logger.warn('Unknown notification channel type', { 
          channelType: channel.type,
          alertId: alert.id 
        });
    }
  }

  /**
   * Send acknowledgment notification
   */
  private async sendAcknowledgmentNotification(alert: Alert): Promise<void> {
    if (this.wsServer) {
      this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.ALERTS, {
        type: 'alert_acknowledged',
        data: {
          alertId: alert.id,
          acknowledgedBy: alert.acknowledgedBy,
          acknowledgedAt: alert.acknowledgedAt
        },
        timestamp: new Date().toISOString()
      });
    }
  }

  /**
   * Send resolution notification
   */
  private async sendResolutionNotification(alert: Alert): Promise<void> {
    if (this.wsServer) {
      this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.ALERTS, {
        type: 'alert_resolved',
        data: {
          alertId: alert.id,
          resolvedAt: alert.resolvedAt,
          duration: alert.resolvedAt ? alert.resolvedAt.getTime() - alert.timestamp.getTime() : 0
        },
        timestamp: new Date().toISOString()
      });
    }
  }

  /**
   * Send escalation notification
   */
  private async sendEscalationNotification(alert: Alert): Promise<void> {
    if (this.wsServer) {
      this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.ALERTS, {
        type: 'alert_escalated',
        data: {
          alertId: alert.id,
          escalatedAt: alert.escalatedAt,
          previousLevel: 'warning', // This could be tracked better
          newLevel: alert.level
        },
        timestamp: new Date().toISOString()
      });
    }
  }

  /**
   * Schedule escalation for an alert
   */
  private scheduleEscalation(alert: Alert): void {
    // Find applicable rule
    const rule = Array.from(this.alertRules.values()).find(r => 
      r.type === alert.type && 
      r.category === alert.category && 
      r.enabled
    );

    if (!rule || rule.escalationDelay <= 0) {
      return;
    }

    setTimeout(() => {
      const currentAlert = this.alerts.get(alert.id);
      if (currentAlert && !currentAlert.acknowledged && !currentAlert.resolved && !currentAlert.escalated) {
        this.escalateAlert(alert.id);
      }
    }, rule.escalationDelay);
  }

  /**
   * Get alert icon for console output
   */
  private getAlertIcon(level: Alert['level']): string {
    switch (level) {
      case 'info': return 'â„¹ï¸';
      case 'warning': return 'âš ï¸';
      case 'error': return 'âŒ';
      case 'critical': return 'ðŸš¨';
      default: return 'ðŸ“¢';
    }
  }

  /**
   * Setup default notification channels
   */
  private setupDefaultChannels(): void {
    // WebSocket channel
    this.addNotificationChannel({
      id: 'websocket',
      name: 'WebSocket Broadcast',
      type: 'websocket',
      config: {},
      enabled: true
    });

    // Console channel
    this.addNotificationChannel({
      id: 'console',
      name: 'Console Output',
      type: 'console',
      config: {},
      enabled: true
    });
  }

  /**
   * Setup default alert rules
   */
  private setupDefaultRules(): void {
    const defaultRules: Omit<AlertRule, 'id'>[] = [
      {
        name: 'Critical CPU Usage',
        type: 'system',
        category: 'cpu',
        condition: 'cpu_usage > 90',
        threshold: 90,
        level: 'critical',
        enabled: true,
        cooldownPeriod: 5 * 60 * 1000, // 5 minutes
        escalationDelay: 10 * 60 * 1000, // 10 minutes
        notificationChannels: ['websocket', 'console']
      },
      {
        name: 'High Memory Usage',
        type: 'system',
        category: 'memory',
        condition: 'memory_usage > 85',
        threshold: 85,
        level: 'warning',
        enabled: true,
        cooldownPeriod: 5 * 60 * 1000,
        escalationDelay: 15 * 60 * 1000,
        notificationChannels: ['websocket', 'console']
      },
      {
        name: 'High Error Rate',
        type: 'application',
        category: 'error_rate',
        condition: 'error_rate > 10',
        threshold: 10,
        level: 'error',
        enabled: true,
        cooldownPeriod: 2 * 60 * 1000,
        escalationDelay: 5 * 60 * 1000,
        notificationChannels: ['websocket', 'console']
      }
    ];

    for (const rule of defaultRules) {
      this.addAlertRule({
        id: `rule_${rule.name.toLowerCase().replace(/\s+/g, '_')}`,
        ...rule
      });
    }
  }

  /**
   * Clean up old resolved alerts
   */
  private cleanupOldAlerts(): void {
    const cutoff = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000); // 7 days
    const initialCount = this.alerts.size;
    
    for (const [id, alert] of this.alerts) {
      if (alert.resolved && alert.resolvedAt && alert.resolvedAt < cutoff) {
        this.alerts.delete(id);
      }
    }
    
    const removed = initialCount - this.alerts.size;
    if (removed > 0) {
      logger.debug(`Cleaned up ${removed} old resolved alerts`);
    }
  }

  /**
   * Get alert summary for dashboard
   */
  public getAlertSummary(): {
    criticalCount: number;
    errorCount: number;
    warningCount: number;
    infoCount: number;
    totalActive: number;
    recentAlerts: Alert[];
  } {
    const activeAlerts = this.getActiveAlerts();
    const recentAlerts = [...activeAlerts]
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime())
      .slice(0, 5);

    return {
      criticalCount: activeAlerts.filter(a => a.level === 'critical').length,
      errorCount: activeAlerts.filter(a => a.level === 'error').length,
      warningCount: activeAlerts.filter(a => a.level === 'warning').length,
      infoCount: activeAlerts.filter(a => a.level === 'info').length,
      totalActive: activeAlerts.length,
      recentAlerts
    };
  }

  /**
   * Shutdown the alert manager
   */
  public shutdown(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = undefined;
    }

    // Resolve all active alerts
    for (const alert of this.getActiveAlerts()) {
      this.resolveAlert(alert.id);
    }

    this.alerts.clear();
    this.alertRules.clear();
    this.notificationChannels.clear();
    this.alertHistory = [];
    this.removeAllListeners();
    
    logger.info('Alert Manager shut down');
  }
}

export default AlertManagerService;

/**
 * Application Monitor Service
 * 
 * Monitors application performance including response times, throughput, error rates,
 * and API endpoint metrics. Provides detailed performance analytics for the trading platform.
 */

import { EventEmitter } from 'events';

import logger from './logging.service';

export interface EndpointMetrics {
  path: string;
  method: string;
  count: number;
  totalTime: number;
  averageTime: number;
  minTime: number;
  maxTime: number;
  errorCount: number;
  errorRate: number;
  lastCall: Date;
  statusCodes: Record<number, number>;
}

export interface ApplicationMetrics {
  performance: {
    responseTime: {
      average: number;
      p50: number;
      p95: number;
      p99: number;
    };
    throughput: {
      requestsPerSecond: number;
      requestsPerMinute: number;
    };
    errorRate: number;
    uptime: number;
  };
  endpoints: Record<string, EndpointMetrics>;
  errors: {
    total: number;
    byType: Record<string, number>;
    recent: ErrorLog[];
  };
  database: {
    queryCount: number;
    averageQueryTime: number;
    slowQueries: number;
    connectionPoolSize: number;
    activeConnections: number;
  };
  websocket: {
    activeConnections: number;
    messagesPerSecond: number;
    totalMessages: number;
    disconnections: number;
  };
  timestamp: Date;
}

export interface RequestLog {
  id: string;
  method: string;
  path: string;
  statusCode: number;
  responseTime: number;
  userAgent?: string;
  ip?: string;
  timestamp: Date;
  error?: string;
}

export interface ErrorLog {
  id: string;
  type: string;
  message: string;
  stack?: string;
  context?: any;
  timestamp: Date;
  level: 'error' | 'warning';
}

export interface PerformanceAlert {
  type: 'response_time' | 'error_rate' | 'throughput' | 'database' | 'memory';
  level: 'warning' | 'critical';
  message: string;
  value: number;
  threshold: number;
  timestamp: Date;
}

export interface ApplicationMonitorConfig {
  collectInterval: number; // milliseconds
  requestRetentionPeriod: number; // milliseconds
  errorRetentionPeriod: number; // milliseconds
  maxRequestLogs: number;
  maxErrorLogs: number;
  alertThresholds: {
    responseTime: { warning: number; critical: number }; // milliseconds
    errorRate: { warning: number; critical: number }; // percentage
    throughput: { warning: number; critical: number }; // requests per second
    databaseQueryTime: { warning: number; critical: number }; // milliseconds
  };
  enableDetailedLogging: boolean;
}

export class ApplicationMonitorService extends EventEmitter {
  private config: ApplicationMonitorConfig;
  private isRunning = false;
  private monitorInterval?: NodeJS.Timeout;
  private startTime: Date;
  
  // Metrics storage
  private requestLogs: RequestLog[] = [];
  private errorLogs: ErrorLog[] = [];
  private endpointMetrics: Map<string, EndpointMetrics> = new Map();
  private responseTimes: number[] = [];
  private requestsPerInterval = 0;
  private errorsPerInterval = 0;
  
  // Database metrics
  private databaseMetrics = {
    queryCount: 0,
    totalQueryTime: 0,
    slowQueries: 0,
    connectionPoolSize: 0,
    activeConnections: 0
  };
  
  // WebSocket metrics
  private websocketMetrics = {
    activeConnections: 0,
    messagesPerSecond: 0,
    totalMessages: 0,
    disconnections: 0
  };

  constructor(config: Partial<ApplicationMonitorConfig> = {}) {
    super();
    
    this.config = {
      collectInterval: 10000, // 10 seconds
      requestRetentionPeriod: 60 * 60 * 1000, // 1 hour
      errorRetentionPeriod: 24 * 60 * 60 * 1000, // 24 hours
      maxRequestLogs: 10000,
      maxErrorLogs: 1000,
      alertThresholds: {
        responseTime: { warning: 500, critical: 2000 },
        errorRate: { warning: 5, critical: 10 },
        throughput: { warning: 10, critical: 5 },
        databaseQueryTime: { warning: 100, critical: 500 }
      },
      enableDetailedLogging: true,
      ...config
    };

    this.startTime = new Date();
    logger.info('Application Monitor Service initialized', { config: this.config });
  }

  /**
   * Start application monitoring
   */
  public start(): void {
    if (this.isRunning) {
      logger.warn('Application Monitor is already running');
      return;
    }

    this.isRunning = true;
    
    // Start periodic collection and analysis
    this.monitorInterval = setInterval(() => {
      this.collectMetrics();
      this.checkAlerts();
      this.cleanupOldLogs();
    }, this.config.collectInterval);

    logger.info('Application Monitor started', {
      interval: this.config.collectInterval
    });

    this.emit('started');
  }

  /**
   * Stop application monitoring
   */
  public stop(): void {
    if (!this.isRunning) {
      return;
    }

    this.isRunning = false;
    
    if (this.monitorInterval) {
      clearInterval(this.monitorInterval);
      this.monitorInterval = undefined;
    }

    logger.info('Application Monitor stopped');
    this.emit('stopped');
  }

  /**
   * Log a request
   */
  public logRequest(log: Omit<RequestLog, 'id' | 'timestamp'>): void {
    const requestLog: RequestLog = {
      id: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      ...log
    };

    // Add to request logs
    this.requestLogs.push(requestLog);
    
    // Update endpoint metrics
    this.updateEndpointMetrics(requestLog);
    
    // Track response times
    this.responseTimes.push(requestLog.responseTime);
    this.requestsPerInterval++;
    
    // Track errors
    if (requestLog.statusCode >= 400) {
      this.errorsPerInterval++;
    }

    // Emit request event
    this.emit('request', requestLog);

    if (this.config.enableDetailedLogging) {
      logger.debug('Request logged', {
        method: requestLog.method,
        path: requestLog.path,
        statusCode: requestLog.statusCode,
        responseTime: requestLog.responseTime
      });
    }
  }

  /**
   * Log an error
   */
  public logError(error: Omit<ErrorLog, 'id' | 'timestamp'>): void {
    const errorLog: ErrorLog = {
      id: `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      ...error
    };

    this.errorLogs.push(errorLog);
    this.emit('error', errorLog);

    logger.error('Application error logged', {
      type: errorLog.type,
      message: errorLog.message,
      level: errorLog.level
    });
  }

  /**
   * Update database metrics
   */
  public updateDatabaseMetrics(metrics: {
    queryTime?: number;
    connectionPoolSize?: number;
    activeConnections?: number;
    isSlowQuery?: boolean;
  }): void {
    if (metrics.queryTime !== undefined) {
      this.databaseMetrics.queryCount++;
      this.databaseMetrics.totalQueryTime += metrics.queryTime;
      
      if (metrics.isSlowQuery) {
        this.databaseMetrics.slowQueries++;
      }
    }
    
    if (metrics.connectionPoolSize !== undefined) {
      this.databaseMetrics.connectionPoolSize = metrics.connectionPoolSize;
    }
    
    if (metrics.activeConnections !== undefined) {
      this.databaseMetrics.activeConnections = metrics.activeConnections;
    }
  }

  /**
   * Update WebSocket metrics
   */
  public updateWebSocketMetrics(metrics: {
    activeConnections?: number;
    messagesSent?: number;
    disconnection?: boolean;
  }): void {
    if (metrics.activeConnections !== undefined) {
      this.websocketMetrics.activeConnections = metrics.activeConnections;
    }
    
    if (metrics.messagesSent !== undefined) {
      this.websocketMetrics.totalMessages += metrics.messagesSent;
    }
    
    if (metrics.disconnection) {
      this.websocketMetrics.disconnections++;
    }
  }

  /**
   * Get current application metrics
   */
  public getCurrentMetrics(): ApplicationMetrics {
    const now = new Date();
    const uptime = (now.getTime() - this.startTime.getTime()) / 1000;
    const recentRequests = this.getRecentRequests(60000); // Last minute
    const recentErrors = this.getRecentErrors(60000); // Last minute

    // Calculate response time percentiles
    const sortedResponseTimes = [...this.responseTimes].sort((a, b) => a - b);
    const responseTimeStats = this.calculatePercentiles(sortedResponseTimes);

    // Calculate throughput
    const requestsPerMinute = recentRequests.length;
    const requestsPerSecond = requestsPerMinute / 60;

    // Calculate error rate
    const errorRate = recentRequests.length > 0 
      ? (recentErrors.length / recentRequests.length) * 100 
      : 0;

    // Calculate database averages
    const averageQueryTime = this.databaseMetrics.queryCount > 0
      ? this.databaseMetrics.totalQueryTime / this.databaseMetrics.queryCount
      : 0;

    // Calculate WebSocket messages per second
    const messagesPerSecond = this.websocketMetrics.totalMessages / uptime;

    return {
      performance: {
        responseTime: {
          average: responseTimeStats.average,
          p50: responseTimeStats.p50,
          p95: responseTimeStats.p95,
          p99: responseTimeStats.p99
        },
        throughput: {
          requestsPerSecond,
          requestsPerMinute
        },
        errorRate,
        uptime
      },
      endpoints: this.getEndpointMetricsSummary(),
      errors: {
        total: this.errorLogs.length,
        byType: this.getErrorsByType(),
        recent: this.getRecentErrors(3600000).slice(0, 10) // Last hour, top 10
      },
      database: {
        queryCount: this.databaseMetrics.queryCount,
        averageQueryTime,
        slowQueries: this.databaseMetrics.slowQueries,
        connectionPoolSize: this.databaseMetrics.connectionPoolSize,
        activeConnections: this.databaseMetrics.activeConnections
      },
      websocket: {
        activeConnections: this.websocketMetrics.activeConnections,
        messagesPerSecond,
        totalMessages: this.websocketMetrics.totalMessages,
        disconnections: this.websocketMetrics.disconnections
      },
      timestamp: now
    };
  }

  /**
   * Get endpoint metrics summary
   */
  private getEndpointMetricsSummary(): Record<string, EndpointMetrics> {
    const summary: Record<string, EndpointMetrics> = {};
    
    for (const [key, metrics] of this.endpointMetrics) {
      summary[key] = { ...metrics };
    }
    
    return summary;
  }

  /**
   * Update endpoint metrics
   */
  private updateEndpointMetrics(request: RequestLog): void {
    const key = `${request.method}:${request.path}`;
    const existing = this.endpointMetrics.get(key);
    
    if (existing) {
      existing.count++;
      existing.totalTime += request.responseTime;
      existing.averageTime = existing.totalTime / existing.count;
      existing.minTime = Math.min(existing.minTime, request.responseTime);
      existing.maxTime = Math.max(existing.maxTime, request.responseTime);
      existing.lastCall = request.timestamp;
      existing.statusCodes[request.statusCode] = (existing.statusCodes[request.statusCode] || 0) + 1;
      
      if (request.statusCode >= 400) {
        existing.errorCount++;
        existing.errorRate = (existing.errorCount / existing.count) * 100;
      }
    } else {
      const newMetrics: EndpointMetrics = {
        path: request.path,
        method: request.method,
        count: 1,
        totalTime: request.responseTime,
        averageTime: request.responseTime,
        minTime: request.responseTime,
        maxTime: request.responseTime,
        errorCount: request.statusCode >= 400 ? 1 : 0,
        errorRate: request.statusCode >= 400 ? 100 : 0,
        lastCall: request.timestamp,
        statusCodes: { [request.statusCode]: 1 }
      };
      
      this.endpointMetrics.set(key, newMetrics);
    }
  }

  /**
   * Calculate response time percentiles
   */
  private calculatePercentiles(responseTimes: number[]): {
    average: number;
    p50: number;
    p95: number;
    p99: number;
  } {
    if (responseTimes.length === 0) {
      return { average: 0, p50: 0, p95: 0, p99: 0 };
    }

    const average = responseTimes.reduce((sum, time) => sum + time, 0) / responseTimes.length;
    const p50 = responseTimes[Math.floor(responseTimes.length * 0.5)] || 0;
    const p95 = responseTimes[Math.floor(responseTimes.length * 0.95)] || 0;
    const p99 = responseTimes[Math.floor(responseTimes.length * 0.99)] || 0;

    return { average, p50, p95, p99 };
  }

  /**
   * Get recent requests
   */
  private getRecentRequests(duration: number): RequestLog[] {
    const cutoff = new Date(Date.now() - duration);
    return this.requestLogs.filter(log => log.timestamp >= cutoff);
  }

  /**
   * Get recent errors
   */
  private getRecentErrors(duration: number): ErrorLog[] {
    const cutoff = new Date(Date.now() - duration);
    return this.errorLogs.filter(log => log.timestamp >= cutoff);
  }

  /**
   * Get errors by type
   */
  private getErrorsByType(): Record<string, number> {
    const byType: Record<string, number> = {};
    
    for (const error of this.errorLogs) {
      byType[error.type] = (byType[error.type] || 0) + 1;
    }
    
    return byType;
  }

  /**
   * Collect metrics and reset interval counters
   */
  private collectMetrics(): void {
    const metrics = this.getCurrentMetrics();
    
    // Reset interval counters
    this.requestsPerInterval = 0;
    this.errorsPerInterval = 0;
    
    // Update WebSocket messages per second
    this.websocketMetrics.messagesPerSecond = 
      this.websocketMetrics.totalMessages / metrics.performance.uptime;
    
    this.emit('metrics', metrics);
    
    logger.debug('Application metrics collected', {
      requestsPerSecond: metrics.performance.throughput.requestsPerSecond,
      errorRate: metrics.performance.errorRate,
      averageResponseTime: metrics.performance.responseTime.average
    });
  }

  /**
   * Check for performance alerts
   */
  private checkAlerts(): void {
    const metrics = this.getCurrentMetrics();
    const alerts: PerformanceAlert[] = [];

    // Response time alerts
    const avgResponseTime = metrics.performance.responseTime.average;
    if (avgResponseTime >= this.config.alertThresholds.responseTime.critical) {
      alerts.push({
        type: 'response_time',
        level: 'critical',
        message: `Critical response time: ${avgResponseTime.toFixed(1)}ms`,
        value: avgResponseTime,
        threshold: this.config.alertThresholds.responseTime.critical,
        timestamp: new Date()
      });
    } else if (avgResponseTime >= this.config.alertThresholds.responseTime.warning) {
      alerts.push({
        type: 'response_time',
        level: 'warning',
        message: `High response time: ${avgResponseTime.toFixed(1)}ms`,
        value: avgResponseTime,
        threshold: this.config.alertThresholds.responseTime.warning,
        timestamp: new Date()
      });
    }

    // Error rate alerts
    const errorRate = metrics.performance.errorRate;
    if (errorRate >= this.config.alertThresholds.errorRate.critical) {
      alerts.push({
        type: 'error_rate',
        level: 'critical',
        message: `Critical error rate: ${errorRate.toFixed(1)}%`,
        value: errorRate,
        threshold: this.config.alertThresholds.errorRate.critical,
        timestamp: new Date()
      });
    } else if (errorRate >= this.config.alertThresholds.errorRate.warning) {
      alerts.push({
        type: 'error_rate',
        level: 'warning',
        message: `High error rate: ${errorRate.toFixed(1)}%`,
        value: errorRate,
        threshold: this.config.alertThresholds.errorRate.warning,
        timestamp: new Date()
      });
    }

    // Throughput alerts (low throughput)
    const throughput = metrics.performance.throughput.requestsPerSecond;
    if (throughput <= this.config.alertThresholds.throughput.critical && throughput > 0) {
      alerts.push({
        type: 'throughput',
        level: 'critical',
        message: `Low throughput: ${throughput.toFixed(1)} req/s`,
        value: throughput,
        threshold: this.config.alertThresholds.throughput.critical,
        timestamp: new Date()
      });
    } else if (throughput <= this.config.alertThresholds.throughput.warning && throughput > 0) {
      alerts.push({
        type: 'throughput',
        level: 'warning',
        message: `Low throughput: ${throughput.toFixed(1)} req/s`,
        value: throughput,
        threshold: this.config.alertThresholds.throughput.warning,
        timestamp: new Date()
      });
    }

    // Database alerts
    const dbQueryTime = metrics.database.averageQueryTime;
    if (dbQueryTime >= this.config.alertThresholds.databaseQueryTime.critical) {
      alerts.push({
        type: 'database',
        level: 'critical',
        message: `Critical database query time: ${dbQueryTime.toFixed(1)}ms`,
        value: dbQueryTime,
        threshold: this.config.alertThresholds.databaseQueryTime.critical,
        timestamp: new Date()
      });
    } else if (dbQueryTime >= this.config.alertThresholds.databaseQueryTime.warning) {
      alerts.push({
        type: 'database',
        level: 'warning',
        message: `High database query time: ${dbQueryTime.toFixed(1)}ms`,
        value: dbQueryTime,
        threshold: this.config.alertThresholds.databaseQueryTime.warning,
        timestamp: new Date()
      });
    }

    // Emit alerts
    for (const alert of alerts) {
      this.emit('alert', alert);
      logger.warn('Performance alert triggered', alert);
    }
  }

  /**
   * Clean up old logs
   */
  private cleanupOldLogs(): void {
    const now = Date.now();
    
    // Clean up request logs
    const requestCutoff = new Date(now - this.config.requestRetentionPeriod);
    const initialRequestCount = this.requestLogs.length;
    this.requestLogs = this.requestLogs.filter(log => log.timestamp >= requestCutoff);
    
    // Limit request logs by count
    if (this.requestLogs.length > this.config.maxRequestLogs) {
      this.requestLogs = this.requestLogs.slice(-this.config.maxRequestLogs);
    }
    
    // Clean up error logs
    const errorCutoff = new Date(now - this.config.errorRetentionPeriod);
    const initialErrorCount = this.errorLogs.length;
    this.errorLogs = this.errorLogs.filter(log => log.timestamp >= errorCutoff);
    
    // Limit error logs by count
    if (this.errorLogs.length > this.config.maxErrorLogs) {
      this.errorLogs = this.errorLogs.slice(-this.config.maxErrorLogs);
    }
    
    // Clean up response times array
    if (this.responseTimes.length > 10000) {
      this.responseTimes = this.responseTimes.slice(-5000);
    }
    
    const requestsRemoved = initialRequestCount - this.requestLogs.length;
    const errorsRemoved = initialErrorCount - this.errorLogs.length;
    
    if (requestsRemoved > 0 || errorsRemoved > 0) {
      logger.debug('Cleaned up old logs', {
        requestsRemoved,
        errorsRemoved
      });
    }
  }

  /**
   * Get monitoring statistics
   */
  public getStats(): {
    isRunning: boolean;
    uptime: number;
    requestsLogged: number;
    errorsLogged: number;
    endpointsTracked: number;
  } {
    return {
      isRunning: this.isRunning,
      uptime: Date.now() - this.startTime.getTime(),
      requestsLogged: this.requestLogs.length,
      errorsLogged: this.errorLogs.length,
      endpointsTracked: this.endpointMetrics.size
    };
  }

  /**
   * Reset all metrics
   */
  public reset(): void {
    this.requestLogs = [];
    this.errorLogs = [];
    this.endpointMetrics.clear();
    this.responseTimes = [];
    this.requestsPerInterval = 0;
    this.errorsPerInterval = 0;
    
    this.databaseMetrics = {
      queryCount: 0,
      totalQueryTime: 0,
      slowQueries: 0,
      connectionPoolSize: 0,
      activeConnections: 0
    };
    
    this.websocketMetrics = {
      activeConnections: 0,
      messagesPerSecond: 0,
      totalMessages: 0,
      disconnections: 0
    };

    logger.info('Application Monitor metrics reset');
    this.emit('reset');
  }

  /**
   * Shutdown the monitor
   */
  public shutdown(): void {
    this.stop();
    this.reset();
    this.removeAllListeners();
    logger.info('Application Monitor shut down');
  }
}

export default ApplicationMonitorService;

