  timeframe: string,
  startTime: number,
  endTime: number,
  cacheDir: string = DEFAULT_DATA_DIR
): Promise<Candle[]> {
  // Validate and sanitize inputs
  if (!symbol || typeof symbol !== 'string') {
    throw new Error('Invalid symbol provided');
  }
  if (!timeframe || typeof timeframe !== 'string') {
    throw new Error('Invalid timeframe provided');
  }
  
  // Normalize symbol for file naming (remove dangerous characters)
  const normalizedSymbol = symbol.replace(/[^a-zA-Z0-9_-]/g, '_');
  const normalizedTimeframe = timeframe.replace(/[^a-zA-Z0-9_-]/g, '_');
  const cacheFileName = `${normalizedSymbol}_${normalizedTimeframe}_${startTime}_${endTime}.json`;
  
  // Validate that cache directory is within allowed bounds
  const resolvedCacheDir = path.resolve(cacheDir);
  const resolvedBasePath = path.resolve(DEFAULT_DATA_DIR);
  if (!resolvedCacheDir.startsWith(resolvedBasePath)) {
    throw new Error('Invalid cache directory path');
  }
  
  const cachePath = path.join(resolvedCacheDir, path.basename(cacheFileName));
  
  // Try to load from cache first
  try {
    await fs.mkdir(cacheDir, { recursive: true });
    
    try {
      const cacheFile = await fs.readFile(cachePath, 'utf8');
      const cachedData = JSON.parse(cacheFile);
      logger.info(`Loaded ${cachedData.length} candles from cache for ${symbol} ${timeframe}`);
      return cachedData;
    } catch (err) {
      // Cache file doesn't exist, continue to download
      logger.debug(`No cache found for ${symbol} ${timeframe}, downloading...`);
    }
  } catch (err) {
    logger.warn(`Could not create cache directory: ${err instanceof Error ? err.message : String(err)}`);
  }
  
  try {
    // For this example, we'll generate mock data
    // In production, you would fetch from an exchange API
    const candles = generateMockCandles(symbol, timeframe, startTime, endTime);
    
    // Save to cache
    try {
      await fs.writeFile(cachePath, JSON.stringify(candles));
      logger.debug(`Saved ${candles.length} candles to cache for ${symbol} ${timeframe}`);
    } catch (err) {
      logger.warn(`Could not save to cache: ${err instanceof Error ? err.message : String(err)}`);
    }
    
    return candles;
  } catch (error) {
    logger.error(`Failed to download historical data: ${error instanceof Error ? error.message : String(error)}`);
    throw new Error(`Failed to download historical data for ${symbol} ${timeframe}`);
  }
}

/**
 * Generate mock price data for testing and examples
 */
function generateMockCandles(
  symbol: string,
  timeframe: string,
  startTime: number,
  endTime: number
): Candle[] {
  const candles: Candle[] = [];
  let currentTime = startTime;
  
  // Calculate time increment based on timeframe
  const timeIncrement = getTimeframeMilliseconds(timeframe);
  
  // Initial price
  let price = 30000; // Starting price (e.g., for BTC)
  
  while (currentTime <= endTime) {
    // Generate some realistic price movement with trends and volatility
    const trend = Math.sin(currentTime / (86400000 * 30)) * 5000; // 30-day cycle
    const volatility = (Math.random() - 0.5) * 500; // Random noise
    
    price = Math.max(100, price + trend / 100 + volatility);
    
    // Calculate OHLC
    const open = price;
    const high = open * (1 + Math.random() * 0.02); // Up to 2% higher
    const low = open * (1 - Math.random() * 0.02); // Up to 2% lower
    const close = (open + high + low) / 3 + (Math.random() - 0.5) * (high - low);
    
    // Random volume
    const volume = 10 + Math.random() * 100;
    
    candles.push({
      timestamp: currentTime,
      open,
      high,
      low,
      close,
      volume
    });
    
    currentTime += timeIncrement;
  }
  
  return candles;
}

/**
 * Convert timeframe string to milliseconds
 */
function getTimeframeMilliseconds(timeframe: string): number {
  const unit = timeframe.charAt(timeframe.length - 1);
  const value = parseInt(timeframe.substring(0, timeframe.length - 1));
  
  switch (unit) {
    case 'm': return value * 60 * 1000;
    case 'h': return value * 60 * 60 * 1000;
    case 'd': return value * 24 * 60 * 60 * 1000;
    case 'w': return value * 7 * 24 * 60 * 60 * 1000;
    default: throw new Error(`Invalid timeframe unit: ${unit}`);
  }
}

/**
 * Save candle data to a CSV file for external analysis
 */
export async function exportCandlesToCsv(
  candles: Candle[],
  filePath: string
): Promise<void> {
  try {
    const headers = 'timestamp,datetime,open,high,low,close,volume\n';
    const rows = candles.map(c => 
      `${c.timestamp},${new Date(c.timestamp).toISOString()},${c.open},${c.high},${c.low},${c.close},${c.volume}`
    ).join('\n');
    
    const csvContent = headers + rows;
    await fs.writeFile(filePath, csvContent);
    
    logger.info(`Exported ${candles.length} candles to ${filePath}`);
  } catch (error) {
    logger.error(`Failed to export candles to CSV: ${error instanceof Error ? error.message : String(error)}`);
    throw error;
  }
}

/**
 * Database Monitor Service
 * 
 * Monitors database performance, connections, query patterns, and health.
 * Provides insights into database bottlenecks and optimization opportunities.
 */

import { EventEmitter } from 'events';
import { performance } from 'perf_hooks';

import type { JabbrWebSocketServer } from '../websocket/websocket.service';

import { database } from './database.service';
import logger from './logging.service';

export interface DatabaseMetrics {
  connections: {
    active: number;
    idle: number;
    total: number;
    maxAllowed: number;
    utilizationPercentage: number;
  };
  queries: {
    totalExecuted: number;
    averageTime: number;
    slowQueries: number;
    failedQueries: number;
    queriesPerSecond: number;
    longestQuery: QueryMetric | null;
  };
  tables: {
    mostAccessed: TableMetric[];
    tableStats: Map<string, TableStatistic>;
  };
  performance: {
    transactionsPerSecond: number;
    lockWaitTime: number;
    cacheHitRatio: number;
    indexEfficiency: number;
  };
  health: {
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
    lastCheck: Date;
    uptime: number;
  };
  timestamp: Date;
}

export interface QueryMetric {
  id: string;
  query: string;
  duration: number;
  timestamp: Date;
  tableName?: string;
  operation: 'SELECT' | 'INSERT' | 'UPDATE' | 'DELETE' | 'OTHER';
  rowsAffected?: number;
  error?: string;
}

export interface TableMetric {
  tableName: string;
  accessCount: number;
  totalTime: number;
  averageTime: number;
  operations: {
    select: number;
    insert: number;
    update: number;
    delete: number;
  };
}

export interface TableStatistic {
  tableName: string;
  rowCount: number;
  sizeInMB: number;
  indexCount: number;
  lastAnalyzed: Date;
  fragmentationPercentage: number;
}

export interface DatabaseMonitorConfig {
  metricsRetentionHours: number;
  slowQueryThresholdMs: number;
  enableQueryLogging: boolean;
  healthCheckInterval: number;
  connectionThresholds: {
    warning: number;
    critical: number;
  };
  performanceThresholds: {
    slowQueryMs: number;
    highLockWaitMs: number;
    lowCacheHitRatio: number;
  };
}

export class DatabaseMonitorService extends EventEmitter {
  private config: DatabaseMonitorConfig;
  private websocketServer?: JabbrWebSocketServer;
  private queryHistory: QueryMetric[] = [];
  private tableMetrics: Map<string, TableMetric> = new Map();
  private metricsHistory: DatabaseMetrics[] = [];
  private isMonitoring = false;
  private healthCheckInterval?: NodeJS.Timeout;
  private cleanupInterval?: NodeJS.Timeout;
  private queryCounter = 0;
  private startTime: Date;
  private lastMetricsCalculation = Date.now();

  constructor(
    config: Partial<DatabaseMonitorConfig> = {},
    websocketServer?: JabbrWebSocketServer
  ) {
    super();
    
    this.config = {
      metricsRetentionHours: 24,
      slowQueryThresholdMs: 1000,
      enableQueryLogging: true,
      healthCheckInterval: 30000, // 30 seconds
      connectionThresholds: {
        warning: 80, // 80% of max connections
        critical: 95 // 95% of max connections
      },
      performanceThresholds: {
        slowQueryMs: 1000,
        highLockWaitMs: 100,
        lowCacheHitRatio: 0.8
      },
      ...config
    };

    this.websocketServer = websocketServer;
    this.startTime = new Date();

    logger.info('Database Monitor Service initialized', { config: this.config });
  }

  /**
   * Start database monitoring
   */
  public start(): void {
    if (this.isMonitoring) {
      logger.warn('Database Monitor Service is already running');
      return;
    }

    this.isMonitoring = true;

    // Setup query monitoring
    if (this.config.enableQueryLogging) {
      this.setupQueryMonitoring();
    }

    // Start health checks
    this.startHealthChecks();

    // Cleanup old metrics
    this.scheduleMetricsCleanup();

    logger.info('Database Monitor Service started');
    this.emit('started');
  }

  /**
   * Stop database monitoring
   */
  public stop(): void {
    if (!this.isMonitoring) {
      return;
    }

    this.isMonitoring = false;

    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = undefined;
    }

    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = undefined;
    }

    logger.info('Database Monitor Service stopped');
    this.emit('stopped');
  }

  /**
   * Get current database metrics
   */
  public async getCurrentMetrics(): Promise<DatabaseMetrics> {
    try {
      const connections = await this.getConnectionMetrics();
      const queries = this.getQueryMetrics();
      const tables = await this.getTableMetrics();
      const performance = await this.getPerformanceMetrics();
      const health = await this.getHealthMetrics();

      const metrics: DatabaseMetrics = {
        connections,
        queries,
        tables,
        performance,
        health,
        timestamp: new Date()
      };

      // Store metrics history
      this.metricsHistory.push(metrics);

      // Emit metrics
      this.emit('metricsUpdate', metrics);        // Broadcast via WebSocket
        if (this.websocketServer) {
          this.websocketServer.broadcast('system', {
            type: 'database_metrics',
            data: metrics
          });
        }

      return metrics;

    } catch (error) {
      logger.error('Failed to collect database metrics', { error });
      throw error;
    }
  }

  /**
   * Record a query execution
   */
  public recordQuery(
    query: string,
    duration: number,
    error?: string,
    rowsAffected?: number
  ): void {
    if (!this.isMonitoring || !this.config.enableQueryLogging) {
      return;
    }

    this.queryCounter++;

    const queryMetric: QueryMetric = {
      id: `query_${this.queryCounter}_${Date.now()}`,
      query: this.sanitizeQuery(query),
      duration,
      timestamp: new Date(),
      operation: this.determineOperation(query),
      rowsAffected,
      error,
      tableName: this.extractTableName(query)
    };

    this.queryHistory.push(queryMetric);

    // Update table metrics
    if (queryMetric.tableName) {
      this.updateTableMetrics(queryMetric);
    }

    // Check for slow queries
    if (duration > this.config.slowQueryThresholdMs) {
      this.handleSlowQuery(queryMetric);
    }

    // Check for query errors
    if (error) {
      this.handleQueryError(queryMetric);
    }

    this.emit('queryExecuted', queryMetric);
  }

  /**
   * Get query history
   */
  public getQueryHistory(limit = 100): QueryMetric[] {
    return this.queryHistory
      .slice(-limit)
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Get slow queries
   */
  public getSlowQueries(limit = 50): QueryMetric[] {
    return this.queryHistory
      .filter(query => query.duration > this.config.slowQueryThresholdMs)
      .sort((a, b) => b.duration - a.duration)
      .slice(0, limit);
  }

  /**
   * Get table access patterns
   */
  public getTableAccessPatterns(): TableMetric[] {
    return Array.from(this.tableMetrics.values())
      .sort((a, b) => b.accessCount - a.accessCount);
  }

  /**
   * Get database health summary
   */
  public async getHealthSummary(): Promise<{
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
    uptime: number;
    lastCheck: Date;
  }> {
    try {
      return await this.getHealthMetrics();
    } catch (error) {
      logger.error('Failed to get database health summary', { error });
      return {
        status: 'critical',
        issues: ['Failed to check database health'],
        uptime: 0,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Get connection metrics
   */
  private async getConnectionMetrics(): Promise<DatabaseMetrics['connections']> {
    try {
      // Get current connection stats
      const connectionQuery = `
        SELECT 
          count(*) as total_connections,
          count(*) FILTER (WHERE state = 'active') as active_connections,
          count(*) FILTER (WHERE state = 'idle') as idle_connections
        FROM pg_stat_activity 
        WHERE datname = current_database()
      `;

      const result = await database.query(connectionQuery) as any;
      const connectionStats = result.rows?.[0] || {};

      // Get max connections setting
      const maxConnResult = await database.query('SHOW max_connections') as any;
      const maxConnections = parseInt(maxConnResult.rows?.[0]?.max_connections) || 100;

      const active = parseInt(connectionStats.active_connections) || 0;
      const idle = parseInt(connectionStats.idle_connections) || 0;
      const total = parseInt(connectionStats.total_connections) || 0;

      return {
        active,
        idle,
        total,
        maxAllowed: maxConnections,
        utilizationPercentage: (total / maxConnections) * 100
      };

    } catch (error) {
      logger.error('Failed to get connection metrics', { error });
      return {
        active: 0,
        idle: 0,
        total: 0,
        maxAllowed: 100,
        utilizationPercentage: 0
      };
    }
  }

  /**
   * Get query metrics
   */
  private getQueryMetrics(): DatabaseMetrics['queries'] {
    const now = Date.now();
    const timeWindow = now - this.lastMetricsCalculation;
    const recentQueries = this.queryHistory.filter(
      q => now - q.timestamp.getTime() < timeWindow
    );

    const totalExecuted = this.queryHistory.length;
    const averageTime = recentQueries.length > 0 
      ? recentQueries.reduce((sum, q) => sum + q.duration, 0) / recentQueries.length
      : 0;

    const slowQueries = this.queryHistory.filter(
      q => q.duration > this.config.slowQueryThresholdMs
    ).length;

    const failedQueries = this.queryHistory.filter(q => q.error).length;

    const queriesPerSecond = timeWindow > 0 
      ? (recentQueries.length / (timeWindow / 1000))
      : 0;

    const longestQuery = this.queryHistory.length > 0
      ? this.queryHistory.reduce((longest, current) => 
          current.duration > longest.duration ? current : longest
        )
      : null;

    this.lastMetricsCalculation = now;

    return {
      totalExecuted,
      averageTime,
      slowQueries,
      failedQueries,
      queriesPerSecond,
      longestQuery
    };
  }

  /**
   * Get table metrics
   */
  private async getTableMetrics(): Promise<DatabaseMetrics['tables']> {
    try {
      // Get table statistics from PostgreSQL
      const tableStatsQuery = `
        SELECT 
          schemaname,
          tablename,
          n_tup_ins as inserts,
          n_tup_upd as updates,
          n_tup_del as deletes,
          seq_scan as sequential_scans,
          seq_tup_read as sequential_reads,
          idx_scan as index_scans,
          idx_tup_fetch as index_reads
        FROM pg_stat_user_tables
        ORDER BY (seq_scan + idx_scan) DESC
        LIMIT 10
      `;

      const tableStatsResult = await database.query(tableStatsQuery) as any;
      const tableStats = new Map<string, TableStatistic>();

      // Get table sizes
      const tableSizeQuery = `
        SELECT 
          tablename,
          pg_total_relation_size(schemaname||'.'||tablename) as size_bytes
        FROM pg_tables 
        WHERE schemaname = 'public'
      `;

      const tableSizeResult = await database.query(tableSizeQuery) as any;
      const tableSizes = new Map<string, number>();

      (tableSizeResult.rows || []).forEach((row: any) => {
        tableSizes.set(row.tablename, parseInt(row.size_bytes) || 0);
      });

      // Process table statistics
      for (const row of (tableStatsResult.rows || [])) {
        const tableName = row.tablename;
        const sizeBytes = tableSizes.get(tableName) || 0;

        tableStats.set(tableName, {
          tableName,
          rowCount: parseInt(row.sequential_reads) + parseInt(row.index_reads),
          sizeInMB: sizeBytes / (1024 * 1024),
          indexCount: parseInt(row.index_scans) > 0 ? 1 : 0, // Simplified
          lastAnalyzed: new Date(), // Would need pg_stat_user_tables.last_analyze
          fragmentationPercentage: 0 // Would need more complex query
        });
      }

      // Get most accessed tables from our metrics
      const mostAccessed = Array.from(this.tableMetrics.values())
        .sort((a, b) => b.accessCount - a.accessCount)
        .slice(0, 10);

      return {
        mostAccessed,
        tableStats
      };

    } catch (error) {
      logger.error('Failed to get table metrics', { error });
      return {
        mostAccessed: [],
        tableStats: new Map()
      };
    }
  }

  /**
   * Get performance metrics
   */
  private async getPerformanceMetrics(): Promise<DatabaseMetrics['performance']> {
    try {
      // Get database statistics
      const statsQuery = `
        SELECT 
          xact_commit,
          xact_rollback,
          blks_read,
          blks_hit,
          tup_returned,
          tup_fetched,
          tup_inserted,
          tup_updated,
          tup_deleted
        FROM pg_stat_database 
        WHERE datname = current_database()
      `;

      const result = await database.query(statsQuery) as any;
      const stats = result.rows?.[0] || {};

      // Calculate cache hit ratio
      const blksRead = parseInt(stats.blks_read) || 0;
      const blksHit = parseInt(stats.blks_hit) || 0;
      const cacheHitRatio = (blksRead + blksHit) > 0 
        ? blksHit / (blksRead + blksHit) 
        : 1;

      // Calculate transactions per second (approximation)
      const commits = parseInt(stats.xact_commit) || 0;
      const rollbacks = parseInt(stats.xact_rollback) || 0;
      const uptime = (Date.now() - this.startTime.getTime()) / 1000;
      const transactionsPerSecond = uptime > 0 
        ? (commits + rollbacks) / uptime 
        : 0;

      // Get lock information
      const lockQuery = `
        SELECT count(*) as lock_count
        FROM pg_locks 
        WHERE NOT granted
      `;

      const lockResult = await database.query(lockQuery) as any;
      const lockCount = parseInt(lockResult.rows?.[0]?.lock_count) || 0;

      return {
        transactionsPerSecond,
        lockWaitTime: lockCount * 10, // Simplified estimate
        cacheHitRatio,
        indexEfficiency: cacheHitRatio // Simplified - would need more complex calculation
      };

    } catch (error) {
      logger.error('Failed to get performance metrics', { error });
      return {
        transactionsPerSecond: 0,
        lockWaitTime: 0,
        cacheHitRatio: 1,
        indexEfficiency: 1
      };
    }
  }

  /**
   * Get health metrics
   */
  private async getHealthMetrics(): Promise<DatabaseMetrics['health']> {
    const issues: string[] = [];
    let status: 'healthy' | 'warning' | 'critical' = 'healthy';

    try {
      // Check connections
      const connections = await this.getConnectionMetrics();
      if (connections.utilizationPercentage > this.config.connectionThresholds.critical) {
        issues.push(`Critical connection usage: ${connections.utilizationPercentage.toFixed(1)}%`);
        status = 'critical';
      } else if (connections.utilizationPercentage > this.config.connectionThresholds.warning) {
        issues.push(`High connection usage: ${connections.utilizationPercentage.toFixed(1)}%`);
        if (status === 'healthy') {status = 'warning';}
      }

      // Check performance
      const performance = await this.getPerformanceMetrics();
      if (performance.cacheHitRatio < this.config.performanceThresholds.lowCacheHitRatio) {
        issues.push(`Low cache hit ratio: ${(performance.cacheHitRatio * 100).toFixed(1)}%`);
        if (status === 'healthy') {status = 'warning';}
      }

      if (performance.lockWaitTime > this.config.performanceThresholds.highLockWaitMs) {
        issues.push(`High lock wait time: ${performance.lockWaitTime.toFixed(0)}ms`);
        if (status === 'healthy') {status = 'warning';}
      }

      // Check for too many slow queries
      const slowQueries = this.getSlowQueries(10);
      if (slowQueries.length > 5) {
        issues.push(`Many slow queries: ${slowQueries.length} recent slow queries`);
        if (status === 'healthy') {status = 'warning';}
      }

      // Check connectivity
      try {
        await database.query('SELECT 1');
      } catch (connectError) {
        issues.push(`Database connectivity error: ${connectError instanceof Error ? connectError.message : String(connectError)}`);
        status = 'critical';
      }
      
      return {
        status,
        issues,
        lastCheck: new Date(),
        uptime: (Date.now() - this.startTime.getTime()) / 1000
      };

    } catch (error) {
      return {
        status: 'critical',
        issues: [`Health check error: ${error instanceof Error ? error.message : String(error)}`],
        lastCheck: new Date(),
        uptime: (Date.now() - this.startTime.getTime()) / 1000
      };
    }
  }

  /**
   * Setup query monitoring hooks
   */
  private setupQueryMonitoring(): void {
    // Note: This would typically hook into the database client
    // For now, we'll rely on manual recordQuery calls
    logger.info('Query monitoring setup completed');
  }

  /**
   * Start periodic health checks
   */
  private startHealthChecks(): void {
    this.healthCheckInterval = setInterval(async () => {
      try {
        await this.getCurrentMetrics();
      } catch (error) {
        logger.error('Database health check failed', { error });
      }
    }, this.config.healthCheckInterval);
  }

  /**
   * Schedule metrics cleanup
   */
  private scheduleMetricsCleanup(): void {
    const cleanupIntervalMs = 60 * 60 * 1000; // 1 hour

    // Clear existing interval if any
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }

    this.cleanupInterval = setInterval(() => {
      this.cleanupOldMetrics();
    }, cleanupIntervalMs);
  }

  /**
   * Clean up old metrics data
   */
  private cleanupOldMetrics(): void {
    const cutoffTime = Date.now() - (this.config.metricsRetentionHours * 60 * 60 * 1000);

    // Clean query history
    this.queryHistory = this.queryHistory.filter(
      query => query.timestamp.getTime() > cutoffTime
    );

    // Clean metrics history
    this.metricsHistory = this.metricsHistory.filter(
      metrics => metrics.timestamp.getTime() > cutoffTime
    );

    logger.debug('Cleaned up old database metrics', {
      queryHistorySize: this.queryHistory.length,
      metricsHistorySize: this.metricsHistory.length
    });
  }

  /**
   * Handle slow query detection
   */
  private handleSlowQuery(queryMetric: QueryMetric): void {
    logger.warn('Slow query detected', {
      queryId: queryMetric.id,
      duration: queryMetric.duration,
      query: queryMetric.query,
      tableName: queryMetric.tableName
    });

    this.emit('slowQuery', queryMetric);

    // Broadcast slow query alert
    if (this.websocketServer) {
      this.websocketServer.broadcast('alerts', {
        type: 'slow_query',
        data: {
          queryId: queryMetric.id,
          duration: queryMetric.duration,
          query: queryMetric.query,
          timestamp: queryMetric.timestamp
        }
      });
    }
  }

  /**
   * Handle query error
   */
  private handleQueryError(queryMetric: QueryMetric): void {
    logger.error('Database query error', {
      queryId: queryMetric.id,
      error: queryMetric.error,
      query: queryMetric.query
    });

    this.emit('queryError', queryMetric);
  }

  /**
   * Update table metrics
   */
  private updateTableMetrics(queryMetric: QueryMetric): void {
    if (!queryMetric.tableName) {return;}

    const existing = this.tableMetrics.get(queryMetric.tableName) || {
      tableName: queryMetric.tableName,
      accessCount: 0,
      totalTime: 0,
      averageTime: 0,
      operations: {
        select: 0,
        insert: 0,
        update: 0,
        delete: 0
      }
    };

    existing.accessCount++;
    existing.totalTime += queryMetric.duration;
    existing.averageTime = existing.totalTime / existing.accessCount;

    // Update operation counts
    const operation = queryMetric.operation.toLowerCase();
    if (operation in existing.operations) {
      (existing.operations as any)[operation]++;
    }

    this.tableMetrics.set(queryMetric.tableName, existing);
  }

  /**
   * Utility functions
   */
  private sanitizeQuery(query: string): string {
    // Remove sensitive data and normalize query
    return query
      .replace(/VALUES\s*\([^)]*\)/gi, 'VALUES (...)')
      .replace(/=\s*'[^']*'/gi, "= '***'")
      .replace(/=\s*\d+/gi, '= ***')
      .substring(0, 200);
  }

  private determineOperation(query: string): QueryMetric['operation'] {
    const normalizedQuery = query.trim().toUpperCase();
    
    if (normalizedQuery.startsWith('SELECT')) {return 'SELECT';}
    if (normalizedQuery.startsWith('INSERT')) {return 'INSERT';}
    if (normalizedQuery.startsWith('UPDATE')) {return 'UPDATE';}
    if (normalizedQuery.startsWith('DELETE')) {return 'DELETE';}
    
    return 'OTHER';
  }

  private extractTableName(query: string): string | undefined {
    // Simple table name extraction
    const patterns = [
      /FROM\s+(\w+)/i,
      /UPDATE\s+(\w+)/i,
      /INSERT\s+INTO\s+(\w+)/i,
      /DELETE\s+FROM\s+(\w+)/i
    ];

    for (const pattern of patterns) {
      const match = query.match(pattern);
      if (match && match[1]) {
        return match[1].toLowerCase();
      }
    }

    return undefined;
  }

  /**
   * Get metrics for specific time range
   */
  public getMetricsHistory(
    startTime: Date,
    endTime: Date = new Date()
  ): DatabaseMetrics[] {
    return this.metricsHistory.filter(
      metrics => metrics.timestamp >= startTime && metrics.timestamp <= endTime
    );
  }

  /**
   * Export metrics data
   */
  public exportMetrics(): {
    queries: QueryMetric[];
    tables: TableMetric[];
    metricsHistory: DatabaseMetrics[];
    summary: {
      totalQueries: number;
      slowQueries: number;
      errorQueries: number;
      monitoringDuration: number;
    };
  } {
    return {
      queries: this.queryHistory,
      tables: Array.from(this.tableMetrics.values()),
      metricsHistory: this.metricsHistory,
      summary: {
        totalQueries: this.queryHistory.length,
        slowQueries: this.queryHistory.filter(q => q.duration > this.config.slowQueryThresholdMs).length,
        errorQueries: this.queryHistory.filter(q => q.error).length,
        monitoringDuration: (Date.now() - this.startTime.getTime()) / 1000
      }
    };
  }

  /**
   * Shutdown the database monitor service
   */
  public shutdown(): void {
    this.stop();
    this.queryHistory = [];
    this.tableMetrics.clear();
    this.metricsHistory = [];
    this.removeAllListeners();
    
    logger.info('Database Monitor Service shut down');
  }
}

export default DatabaseMonitorService;

import * as crypto from 'crypto';

import { z } from 'zod';

/**
 * Encryption Configuration Schema
 */
const EncryptionConfigSchema = z.object({
  ENCRYPTION_KEY: z.string().min(32, 'Encryption key must be at least 32 characters'),
  ENCRYPTION_ALGORITHM: z.string().default('aes-256-cbc')
});

type EncryptionConfig = z.infer<typeof EncryptionConfigSchema>;

/**
 * Encrypted data structure
 */
export interface EncryptedData {
  encrypted: string;
  iv: string;
  algorithm: string;
}

/**
 * Encryption Service
 * Provides secure encryption/decryption for sensitive data like API keys
 */
export class EncryptionService {
  private config: EncryptionConfig;
  private key: Buffer;

  constructor() {
    // Validate environment configuration
    this.config = EncryptionConfigSchema.parse({
      ENCRYPTION_KEY: process.env.ENCRYPTION_KEY,
      ENCRYPTION_ALGORITHM: process.env.ENCRYPTION_ALGORITHM
    });

    // Derive a 32-byte key from the provided key
    this.key = this.deriveKey(this.config.ENCRYPTION_KEY);
  }

  /**
   * Encrypt sensitive data
   */
  encrypt(plaintext: string): EncryptedData {
    try {
      // Generate a random initialization vector
      const iv = crypto.randomBytes(16);
      
      // Create cipher with IV
      const cipher = crypto.createCipheriv(this.config.ENCRYPTION_ALGORITHM, this.key, iv);
      
      // Encrypt the data
      let encrypted = cipher.update(plaintext, 'utf8', 'hex');
      encrypted += cipher.final('hex');

      return {
        encrypted,
        iv: iv.toString('hex'),
        algorithm: this.config.ENCRYPTION_ALGORITHM
      };
    } catch (error) {
      throw new Error(`Encryption failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Decrypt sensitive data
   */
  decrypt(encryptedData: EncryptedData): string {
    try {
      // Validate algorithm
      if (encryptedData.algorithm !== this.config.ENCRYPTION_ALGORITHM) {
        throw new Error(`Unsupported encryption algorithm: ${encryptedData.algorithm}`);
      }

      // Convert hex strings back to buffers
      const iv = Buffer.from(encryptedData.iv, 'hex');
      
      // Create decipher with IV
      const decipher = crypto.createDecipheriv(this.config.ENCRYPTION_ALGORITHM, this.key, iv);
      
      // Decrypt the data
      let decrypted = decipher.update(encryptedData.encrypted, 'hex', 'utf8');
      decrypted += decipher.final('utf8');
      
      return decrypted;
    } catch (error) {
      throw new Error(`Decryption failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Encrypt API key credentials
   */
  encryptApiKey(apiKey: string): string {
    const encrypted = this.encrypt(apiKey);
    return JSON.stringify(encrypted);
  }

  /**
   * Decrypt API key credentials
   */
  decryptApiKey(encryptedApiKey: string): string {
    try {
      const encryptedData: EncryptedData = JSON.parse(encryptedApiKey);
      return this.decrypt(encryptedData);
    } catch (error) {
      throw new Error(`Failed to decrypt API key: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Encrypt API secret credentials
   */
  encryptApiSecret(apiSecret: string): string {
    const encrypted = this.encrypt(apiSecret);
    return JSON.stringify(encrypted);
  }

  /**
   * Decrypt API secret credentials
   */
  decryptApiSecret(encryptedApiSecret: string): string {
    try {
      const encryptedData: EncryptedData = JSON.parse(encryptedApiSecret);
      return this.decrypt(encryptedData);
    } catch (error) {
      throw new Error(`Failed to decrypt API secret: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Encrypt passphrase (for exchanges like OKX)
   */
  encryptPassphrase(passphrase: string): string {
    const encrypted = this.encrypt(passphrase);
    return JSON.stringify(encrypted);
  }

  /**
   * Decrypt passphrase
   */
  decryptPassphrase(encryptedPassphrase: string): string {
    try {
      const encryptedData: EncryptedData = JSON.parse(encryptedPassphrase);
      return this.decrypt(encryptedData);
    } catch (error) {
      throw new Error(`Failed to decrypt passphrase: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Generate a secure random key
   */
  static generateKey(): string {
    return crypto.randomBytes(32).toString('hex');
  }

  /**
   * Hash sensitive data (one-way, for comparison purposes)
   */
  hash(data: string): string {
    return crypto.createHash('sha256').update(data).digest('hex');
  }

  /**
   * Verify hashed data
   */
  verifyHash(data: string, hash: string): boolean {
    const dataHash = this.hash(data);
    return crypto.timingSafeEqual(Buffer.from(dataHash), Buffer.from(hash));
  }

  /**
   * Test encryption/decryption functionality
   */
  test(): { success: boolean; error?: string } {
    try {
      const testData = 'test-api-key-12345';
      const encrypted = this.encrypt(testData);
      const decrypted = this.decrypt(encrypted);
      
      if (decrypted !== testData) {
        return { success: false, error: 'Decrypted data does not match original' };
      }
      
      return { success: true };
    } catch (error) {
      return { 
        success: false, 
        error: error instanceof Error ? error.message : 'Unknown error' 
      };
    }
  }

  /**
   * Derive a consistent key from the provided encryption key
   */
  private deriveKey(inputKey: string): Buffer {
    // Use PBKDF2 to derive a consistent 32-byte key
    return crypto.pbkdf2Sync(inputKey, 'jabbr-salt', 100000, 32, 'sha256');
  }

  /**
   * Get encryption configuration (without sensitive data)
   */
  getConfig(): Omit<EncryptionConfig, 'ENCRYPTION_KEY'> {
    const safeConfig = { ...this.config };
    delete (safeConfig as any).ENCRYPTION_KEY;
    return safeConfig;
  }
}

// Export singleton instance
export const encryptionService = new EncryptionService();

/**
 * Convenience functions for common encryption operations
 */
export const encryptApiCredentials = (apiKey: string, apiSecret: string, passphrase?: string) => {
  return {
    apiKey: encryptionService.encryptApiKey(apiKey),
    apiSecret: encryptionService.encryptApiSecret(apiSecret),
    passphrase: passphrase ? encryptionService.encryptPassphrase(passphrase) : undefined
  };
};

export const decryptApiCredentials = (encryptedApiKey: string, encryptedApiSecret: string, encryptedPassphrase?: string) => {
  return {
    apiKey: encryptionService.decryptApiKey(encryptedApiKey),
    apiSecret: encryptionService.decryptApiSecret(encryptedApiSecret),
    passphrase: encryptedPassphrase ? encryptionService.decryptPassphrase(encryptedPassphrase) : undefined
  };
};

/**
 * Exchange Monitor Service
 * 
 * Monitors exchange connectivity, API performance, rate limits, and trading operations.
 * Provides real-time insights into exchange health and performance metrics.
 */

import { EventEmitter } from 'events';

import type { JabbrWebSocketServer } from '../websocket/websocket.service';

import logger from './logging.service';

export interface ExchangeMetrics {
  exchange: string;
  connectivity: {
    status: 'connected' | 'disconnected' | 'degraded';
    lastConnected: Date | null;
    uptime: number;
    connectionAttempts: number;
    failedConnections: number;
    ping: number; // milliseconds
  };
  api: {
    requestCount: number;
    requestsPerSecond: number;
    averageResponseTime: number;
    errorRate: number;
    successRate: number;
    slowRequests: number;
    timeouts: number;
  };
  rateLimits: {
    current: number;
    maximum: number;
    resetTime: Date | null;
    utilizationPercentage: number;
    violations: number;
    weight: number;
  };
  trading: {
    activeOrders: number;
    completedOrders: number;
    failedOrders: number;
    orderFillRate: number;
    averageExecutionTime: number;
    slippage: {
      average: number;
      maximum: number;
      count: number;
    };
  };
  websocket: {
    status: 'connected' | 'disconnected' | 'reconnecting';
    lastMessage: Date | null;
    messagesReceived: number;
    reconnectCount: number;
    latency: number;
  };
  health: {
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
    lastCheck: Date;
    score: number; // 0-100
  };
  timestamp: Date;
}

export interface ApiRequestMetric {
  id: string;
  endpoint: string;
  method: string;
  startTime: Date;
  endTime?: Date;
  duration?: number;
  success: boolean;
  error?: string;
  statusCode?: number;
  rateLimitRemaining?: number;
  weight?: number;
}

export interface OrderExecutionMetric {
  orderId: string;
  symbol: string;
  side: 'buy' | 'sell';
  type: 'market' | 'limit' | 'stop';
  quantity: number;
  price?: number;
  executedPrice?: number;
  executedQuantity?: number;
  slippage?: number;
  executionTime: number;
  status: 'filled' | 'partial' | 'failed' | 'cancelled';
  timestamp: Date;
}

export interface ExchangeMonitorConfig {
  pingInterval: number;
  healthCheckInterval: number;
  metricsRetentionHours: number;
  thresholds: {
    slowRequestMs: number;
    highErrorRate: number;
    criticalPing: number;
    rateLimitWarning: number;
    rateLimitCritical: number;
  };
  enableDetailedLogging: boolean;
}

export class ExchangeMonitorService extends EventEmitter {
  private config: ExchangeMonitorConfig;
  private websocketServer?: JabbrWebSocketServer;
  private exchangeMetrics: Map<string, ExchangeMetrics> = new Map();
  private apiRequestHistory: Map<string, ApiRequestMetric[]> = new Map();
  private orderHistory: Map<string, OrderExecutionMetric[]> = new Map();
  private isMonitoring = false;
  private pingIntervals: Map<string, NodeJS.Timeout> = new Map();
  private healthCheckInterval?: NodeJS.Timeout;
  private startTime: Date;

  constructor(
    config: Partial<ExchangeMonitorConfig> = {},
    websocketServer?: JabbrWebSocketServer
  ) {
    super();
    
    this.config = {
      pingInterval: 30000, // 30 seconds
      healthCheckInterval: 60000, // 60 seconds
      metricsRetentionHours: 24,
      thresholds: {
        slowRequestMs: 5000,
        highErrorRate: 10, // 10%
        criticalPing: 2000, // 2 seconds
        rateLimitWarning: 80, // 80%
        rateLimitCritical: 95 // 95%
      },
      enableDetailedLogging: true,
      ...config
    };

    this.websocketServer = websocketServer;
    this.startTime = new Date();

    logger.info('Exchange Monitor Service initialized', { config: this.config });
  }

  /**
   * Start monitoring an exchange
   */
  public startMonitoring(exchangeName: string): void {
    if (this.isMonitoring && this.exchangeMetrics.has(exchangeName)) {
      logger.warn(`Already monitoring exchange: ${exchangeName}`);
      return;
    }

    // Initialize metrics for the exchange
    this.initializeExchangeMetrics(exchangeName);

    // Start ping monitoring
    this.startPingMonitoring(exchangeName);

    // Start health checks if not already running
    if (!this.healthCheckInterval) {
      this.startHealthChecks();
    }

    this.isMonitoring = true;

    logger.info(`Started monitoring exchange: ${exchangeName}`);
    this.emit('monitoringStarted', exchangeName);
  }

  /**
   * Stop monitoring an exchange
   */
  public stopMonitoring(exchangeName: string): void {
    const pingInterval = this.pingIntervals.get(exchangeName);
    if (pingInterval) {
      clearInterval(pingInterval);
      this.pingIntervals.delete(exchangeName);
    }

    this.exchangeMetrics.delete(exchangeName);
    this.apiRequestHistory.delete(exchangeName);
    this.orderHistory.delete(exchangeName);

    logger.info(`Stopped monitoring exchange: ${exchangeName}`);
    this.emit('monitoringStopped', exchangeName);

    // Stop health checks if no exchanges are being monitored
    if (this.exchangeMetrics.size === 0 && this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = undefined;
      this.isMonitoring = false;
    }
  }

  /**
   * Record an API request
   */
  public recordApiRequest(
    exchangeName: string,
    endpoint: string,
    method: string,
    duration: number,
    success: boolean,
    error?: string,
    statusCode?: number,
    rateLimitRemaining?: number,
    weight?: number
  ): void {
    const requestMetric: ApiRequestMetric = {
      id: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      endpoint,
      method,
      startTime: new Date(Date.now() - duration),
      endTime: new Date(),
      duration,
      success,
      error,
      statusCode,
      rateLimitRemaining,
      weight
    };

    // Store request history
    if (!this.apiRequestHistory.has(exchangeName)) {
      this.apiRequestHistory.set(exchangeName, []);
    }
    this.apiRequestHistory.get(exchangeName)!.push(requestMetric);

    // Update exchange metrics
    this.updateApiMetrics(exchangeName, requestMetric);

    // Check for issues
    this.checkApiIssues(exchangeName, requestMetric);

    this.emit('apiRequest', exchangeName, requestMetric);
  }

  /**
   * Record order execution
   */
  public recordOrderExecution(
    exchangeName: string,
    orderId: string,
    symbol: string,
    side: 'buy' | 'sell',
    type: 'market' | 'limit' | 'stop',
    quantity: number,
    price: number | undefined,
    executedPrice: number | undefined,
    executedQuantity: number | undefined,
    executionTime: number,
    status: 'filled' | 'partial' | 'failed' | 'cancelled'
  ): void {
    const slippage = price && executedPrice 
      ? Math.abs((executedPrice - price) / price) * 100
      : undefined;

    const orderMetric: OrderExecutionMetric = {
      orderId,
      symbol,
      side,
      type,
      quantity,
      price,
      executedPrice,
      executedQuantity,
      slippage,
      executionTime,
      status,
      timestamp: new Date()
    };

    // Store order history
    if (!this.orderHistory.has(exchangeName)) {
      this.orderHistory.set(exchangeName, []);
    }
    this.orderHistory.get(exchangeName)!.push(orderMetric);

    // Update trading metrics
    this.updateTradingMetrics(exchangeName, orderMetric);

    this.emit('orderExecution', exchangeName, orderMetric);
  }

  /**
   * Update WebSocket connection status
   */
  public updateWebSocketStatus(
    exchangeName: string,
    status: 'connected' | 'disconnected' | 'reconnecting',
    latency?: number
  ): void {
    const metrics = this.exchangeMetrics.get(exchangeName);
    if (!metrics) {return;}

    metrics.websocket.status = status;
    metrics.websocket.latency = latency || 0;

    if (status === 'connected') {
      metrics.websocket.lastMessage = new Date();
    } else if (status === 'reconnecting') {
      metrics.websocket.reconnectCount++;
    }

    this.emit('websocketStatusChange', exchangeName, status);
  }

  /**
   * Record WebSocket message
   */
  public recordWebSocketMessage(exchangeName: string): void {
    const metrics = this.exchangeMetrics.get(exchangeName);
    if (!metrics) {return;}

    metrics.websocket.messagesReceived++;
    metrics.websocket.lastMessage = new Date();
  }

  /**
   * Get current metrics for an exchange
   */
  public getExchangeMetrics(exchangeName: string): ExchangeMetrics | null {
    return this.exchangeMetrics.get(exchangeName) || null;
  }

  /**
   * Get metrics for all monitored exchanges
   */
  public getAllMetrics(): Map<string, ExchangeMetrics> {
    return new Map(this.exchangeMetrics);
  }

  /**
   * Get API request history
   */
  public getApiRequestHistory(
    exchangeName: string,
    limit = 100
  ): ApiRequestMetric[] {
    const history = this.apiRequestHistory.get(exchangeName) || [];
    return history.slice(-limit).reverse();
  }

  /**
   * Get order execution history
   */
  public getOrderHistory(
    exchangeName: string,
    limit = 100
  ): OrderExecutionMetric[] {
    const history = this.orderHistory.get(exchangeName) || [];
    return history.slice(-limit).reverse();
  }

  /**
   * Get exchange health summary
   */
  public getHealthSummary(exchangeName: string): {
    status: 'healthy' | 'warning' | 'critical';
    score: number;
    issues: string[];
    uptime: number;
  } {
    const metrics = this.getExchangeMetrics(exchangeName);
    
    if (!metrics) {
      return {
        status: 'critical',
        score: 0,
        issues: ['Exchange not monitored'],
        uptime: 0
      };
    }

    return {
      status: metrics.health.status,
      score: metrics.health.score,
      issues: metrics.health.issues,
      uptime: metrics.connectivity.uptime
    };
  }

  /**
   * Initialize metrics for a new exchange
   */
  private initializeExchangeMetrics(exchangeName: string): void {
    const metrics: ExchangeMetrics = {
      exchange: exchangeName,
      connectivity: {
        status: 'disconnected',
        lastConnected: null,
        uptime: 0,
        connectionAttempts: 0,
        failedConnections: 0,
        ping: 0
      },
      api: {
        requestCount: 0,
        requestsPerSecond: 0,
        averageResponseTime: 0,
        errorRate: 0,
        successRate: 100,
        slowRequests: 0,
        timeouts: 0
      },
      rateLimits: {
        current: 0,
        maximum: 1000, // Default, will be updated
        resetTime: null,
        utilizationPercentage: 0,
        violations: 0,
        weight: 0
      },
      trading: {
        activeOrders: 0,
        completedOrders: 0,
        failedOrders: 0,
        orderFillRate: 0,
        averageExecutionTime: 0,
        slippage: {
          average: 0,
          maximum: 0,
          count: 0
        }
      },
      websocket: {
        status: 'disconnected',
        lastMessage: null,
        messagesReceived: 0,
        reconnectCount: 0,
        latency: 0
      },
      health: {
        status: 'healthy',
        issues: [],
        lastCheck: new Date(),
        score: 100
      },
      timestamp: new Date()
    };

    this.exchangeMetrics.set(exchangeName, metrics);
    this.apiRequestHistory.set(exchangeName, []);
    this.orderHistory.set(exchangeName, []);
  }

  /**
   * Start ping monitoring for an exchange
   */
  private startPingMonitoring(exchangeName: string): void {
    const pingInterval = setInterval(async () => {
      await this.performPing(exchangeName);
    }, this.config.pingInterval);

    this.pingIntervals.set(exchangeName, pingInterval);

    // Perform initial ping
    this.performPing(exchangeName);
  }

  /**
   * Perform ping test to exchange
   */
  private async performPing(exchangeName: string): Promise<void> {
    const metrics = this.exchangeMetrics.get(exchangeName);
    if (!metrics) {return;}

    const startTime = Date.now();
    metrics.connectivity.connectionAttempts++;

    try {
      // This would be replaced with actual exchange ping/health check
      // For now, we'll simulate it
      const ping = await this.simulateExchangePing(exchangeName);
      
      metrics.connectivity.ping = ping;
      metrics.connectivity.status = ping < this.config.thresholds.criticalPing ? 'connected' : 'degraded';
      metrics.connectivity.lastConnected = new Date();
      metrics.connectivity.uptime = (Date.now() - this.startTime.getTime()) / 1000;

    } catch (error) {
      metrics.connectivity.failedConnections++;
      metrics.connectivity.status = 'disconnected';
      metrics.connectivity.ping = Date.now() - startTime;

      logger.error(`Exchange ping failed for ${exchangeName}`, { error });
    }

    this.emit('pingResult', exchangeName, metrics.connectivity);
  }

  /**
   * Simulate exchange ping (to be replaced with actual implementation)
   */
  private async simulateExchangePing(exchangeName: string): Promise<number> {
    return new Promise((resolve, reject) => {
      const startTime = Date.now();
      
      // Simulate network delay
      setTimeout(() => {
        const ping = Date.now() - startTime;
        
        // Simulate occasional failures
        if (Math.random() < 0.05) { // 5% failure rate
          reject(new Error('Connection timeout'));
        } else {
          resolve(ping);
        }
      }, Math.random() * 500 + 50); // 50-550ms delay
    });
  }

  /**
   * Update API metrics based on request
   */
  private updateApiMetrics(exchangeName: string, request: ApiRequestMetric): void {
    const metrics = this.exchangeMetrics.get(exchangeName);
    if (!metrics) {return;}

    metrics.api.requestCount++;

    // Update response time
    if (request.duration) {
      const totalTime = metrics.api.averageResponseTime * (metrics.api.requestCount - 1) + request.duration;
      metrics.api.averageResponseTime = totalTime / metrics.api.requestCount;

      // Count slow requests
      if (request.duration > this.config.thresholds.slowRequestMs) {
        metrics.api.slowRequests++;
      }
    }

    // Update success/error rates
    if (request.success) {
      metrics.api.successRate = ((metrics.api.successRate * (metrics.api.requestCount - 1)) + 100) / metrics.api.requestCount;
    } else {
      metrics.api.errorRate = ((metrics.api.errorRate * (metrics.api.requestCount - 1)) + 100) / metrics.api.requestCount;
      metrics.api.successRate = 100 - metrics.api.errorRate;
    }

    // Update rate limit info
    if (request.rateLimitRemaining !== undefined) {
      metrics.rateLimits.current = request.rateLimitRemaining;
      if (metrics.rateLimits.maximum > 0) {
        metrics.rateLimits.utilizationPercentage = 
          ((metrics.rateLimits.maximum - request.rateLimitRemaining) / metrics.rateLimits.maximum) * 100;
      }
    }

    if (request.weight !== undefined) {
      metrics.rateLimits.weight += request.weight;
    }

    // Calculate requests per second
    const timeWindow = 60000; // 1 minute
    const recentRequests = this.apiRequestHistory.get(exchangeName)?.filter(
      req => req.startTime.getTime() > Date.now() - timeWindow
    ) || [];
    metrics.api.requestsPerSecond = recentRequests.length / 60;

    metrics.timestamp = new Date();
  }

  /**
   * Update trading metrics based on order execution
   */
  private updateTradingMetrics(exchangeName: string, order: OrderExecutionMetric): void {
    const metrics = this.exchangeMetrics.get(exchangeName);
    if (!metrics) {return;}

    // Update order counts
    if (order.status === 'filled') {
      metrics.trading.completedOrders++;
    } else if (order.status === 'failed') {
      metrics.trading.failedOrders++;
    }

    // Update execution time
    const totalOrders = metrics.trading.completedOrders + metrics.trading.failedOrders;
    if (totalOrders > 0) {
      const totalTime = metrics.trading.averageExecutionTime * (totalOrders - 1) + order.executionTime;
      metrics.trading.averageExecutionTime = totalTime / totalOrders;
    }

    // Update order fill rate
    const totalOrdersAttempted = metrics.trading.completedOrders + metrics.trading.failedOrders;
    if (totalOrdersAttempted > 0) {
      metrics.trading.orderFillRate = (metrics.trading.completedOrders / totalOrdersAttempted) * 100;
    }

    // Update slippage
    if (order.slippage !== undefined) {
      const currentCount = metrics.trading.slippage.count;
      const currentAverage = metrics.trading.slippage.average;
      
      metrics.trading.slippage.count++;
      metrics.trading.slippage.average = (currentAverage * currentCount + order.slippage) / metrics.trading.slippage.count;
      metrics.trading.slippage.maximum = Math.max(metrics.trading.slippage.maximum, order.slippage);
    }

    metrics.timestamp = new Date();
  }

  /**
   * Check for API issues and alerts
   */
  private checkApiIssues(exchangeName: string, request: ApiRequestMetric): void {
    // Check for rate limit violations
    if (request.rateLimitRemaining !== undefined) {
      const metrics = this.exchangeMetrics.get(exchangeName);
      if (metrics && metrics.rateLimits.utilizationPercentage > this.config.thresholds.rateLimitCritical) {
        this.emit('rateLimitViolation', exchangeName, metrics.rateLimits);
      }
    }

    // Check for slow requests
    if (request.duration && request.duration > this.config.thresholds.slowRequestMs) {
      this.emit('slowApiRequest', exchangeName, request);
    }

    // Check for API errors
    if (!request.success) {
      this.emit('apiError', exchangeName, request);
    }
  }

  /**
   * Start periodic health checks
   */
  private startHealthChecks(): void {
    this.healthCheckInterval = setInterval(() => {
      this.performHealthChecks();
    }, this.config.healthCheckInterval);
  }

  /**
   * Perform health checks for all exchanges
   */
  private performHealthChecks(): void {
    for (const [exchangeName, metrics] of this.exchangeMetrics) {
      this.updateHealthScore(exchangeName, metrics);
    }

    // Broadcast health update
    if (this.websocketServer) {
      this.websocketServer.broadcast('exchange-health', {
        type: 'exchange_health_update',
        data: Object.fromEntries(this.exchangeMetrics)
      });
    }
  }

  /**
   * Update health score for an exchange
   */
  private updateHealthScore(exchangeName: string, metrics: ExchangeMetrics): void {
    const issues: string[] = [];
    let score = 100;

    // Check connectivity
    if (metrics.connectivity.status === 'disconnected') {
      score -= 30;
      issues.push('Exchange disconnected');
    } else if (metrics.connectivity.status === 'degraded') {
      score -= 15;
      issues.push(`High ping: ${metrics.connectivity.ping}ms`);
    }

    // Check API performance
    if (metrics.api.errorRate > this.config.thresholds.highErrorRate) {
      score -= 20;
      issues.push(`High API error rate: ${metrics.api.errorRate.toFixed(1)}%`);
    }

    if (metrics.api.averageResponseTime > this.config.thresholds.slowRequestMs) {
      score -= 10;
      issues.push(`Slow API responses: ${metrics.api.averageResponseTime.toFixed(0)}ms`);
    }

    // Check rate limits
    if (metrics.rateLimits.utilizationPercentage > this.config.thresholds.rateLimitCritical) {
      score -= 25;
      issues.push(`Critical rate limit usage: ${metrics.rateLimits.utilizationPercentage.toFixed(1)}%`);
    } else if (metrics.rateLimits.utilizationPercentage > this.config.thresholds.rateLimitWarning) {
      score -= 10;
      issues.push(`High rate limit usage: ${metrics.rateLimits.utilizationPercentage.toFixed(1)}%`);
    }

    // Check WebSocket
    if (metrics.websocket.status === 'disconnected') {
      score -= 15;
      issues.push('WebSocket disconnected');
    } else if (metrics.websocket.status === 'reconnecting') {
      score -= 5;
      issues.push('WebSocket reconnecting');
    }

    // Check trading performance
    if (metrics.trading.orderFillRate < 95 && metrics.trading.completedOrders > 10) {
      score -= 10;
      issues.push(`Low order fill rate: ${metrics.trading.orderFillRate.toFixed(1)}%`);
    }

    // Determine status
    let status: 'healthy' | 'warning' | 'critical';
    if (score >= 90) {
      status = 'healthy';
    } else if (score >= 70) {
      status = 'warning';
    } else {
      status = 'critical';
    }

    // Update metrics
    metrics.health = {
      status,
      issues,
      lastCheck: new Date(),
      score: Math.max(0, score)
    };

    // Emit health change events
    this.emit('healthUpdate', exchangeName, metrics.health);

    if (status === 'critical') {
      this.emit('exchangeCritical', exchangeName, issues);
    } else if (status === 'warning') {
      this.emit('exchangeWarning', exchangeName, issues);
    }
  }

  /**
   * Clean up old metrics data
   */
  private cleanupOldMetrics(): void {
    const cutoffTime = Date.now() - (this.config.metricsRetentionHours * 60 * 60 * 1000);

    for (const [exchangeName, requests] of this.apiRequestHistory) {
      this.apiRequestHistory.set(
        exchangeName,
        requests.filter(req => req.startTime.getTime() > cutoffTime)
      );
    }

    for (const [exchangeName, orders] of this.orderHistory) {
      this.orderHistory.set(
        exchangeName,
        orders.filter(order => order.timestamp.getTime() > cutoffTime)
      );
    }
  }

  /**
   * Export metrics data
   */
  public exportMetrics(): {
    exchanges: Record<string, ExchangeMetrics>;
    apiRequests: Record<string, ApiRequestMetric[]>;
    orders: Record<string, OrderExecutionMetric[]>;
    summary: {
      totalExchanges: number;
      healthyExchanges: number;
      totalApiRequests: number;
      totalOrders: number;
    };
  } {
    const exchanges = Object.fromEntries(this.exchangeMetrics);
    const apiRequests = Object.fromEntries(this.apiRequestHistory);
    const orders = Object.fromEntries(this.orderHistory);

    const healthyExchanges = Array.from(this.exchangeMetrics.values())
      .filter(m => m.health.status === 'healthy').length;

    const totalApiRequests = Array.from(this.apiRequestHistory.values())
      .reduce((sum, requests) => sum + requests.length, 0);

    const totalOrders = Array.from(this.orderHistory.values())
      .reduce((sum, orders) => sum + orders.length, 0);

    return {
      exchanges,
      apiRequests,
      orders,
      summary: {
        totalExchanges: this.exchangeMetrics.size,
        healthyExchanges,
        totalApiRequests,
        totalOrders
      }
    };
  }

  /**
   * Shutdown the exchange monitor service
   */
  public shutdown(): void {
    // Stop all ping intervals
    for (const [exchangeName, interval] of this.pingIntervals) {
      clearInterval(interval);
    }
    this.pingIntervals.clear();

    // Stop health checks
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = undefined;
    }

    // Clear data
    this.exchangeMetrics.clear();
    this.apiRequestHistory.clear();
    this.orderHistory.clear();

    this.isMonitoring = false;
    this.removeAllListeners();

    logger.info('Exchange Monitor Service shut down');
  }
}

export default ExchangeMonitorService;

/**
 * Health Check Service
 * 
 * Provides system health validation and monitoring endpoints.
 * Aggregates health information from all system components.
 */

import { EventEmitter } from 'events';

import type ApplicationMonitorService from './application-monitor.service';
import { database } from './database.service';
import logger from './logging.service';
import type SystemMonitorService from './system-monitor.service';

export interface HealthStatus {
  status: 'healthy' | 'degraded' | 'unhealthy';
  timestamp: Date;
  uptime: number;
  version: string;
  environment: string;
  components: Record<string, ComponentHealth>;
  summary: {
    healthy: number;
    degraded: number;
    unhealthy: number;
    total: number;
  };
}

export interface ComponentHealth {
  status: 'healthy' | 'degraded' | 'unhealthy';
  message: string;
  lastCheck: Date;
  responseTime?: number;
  metadata?: any;
  dependencies?: string[];
}

export interface HealthCheckConfig {
  checkInterval: number; // milliseconds
  timeoutPerCheck: number; // milliseconds
  enableDetailedChecks: boolean;
  criticalComponents: string[];
}

export class HealthCheckService extends EventEmitter {
  private config: HealthCheckConfig;
  private systemMonitor?: SystemMonitorService;
  private applicationMonitor?: ApplicationMonitorService;
  private componentChecks: Map<string, () => Promise<ComponentHealth>> = new Map();
  private lastHealthStatus?: HealthStatus;
  private checkInterval?: NodeJS.Timeout;
  private isRunning = false;
  private startTime: Date;

  constructor(
    config: Partial<HealthCheckConfig> = {},
    systemMonitor?: SystemMonitorService,
    applicationMonitor?: ApplicationMonitorService
  ) {
    super();
    
    this.config = {
      checkInterval: 30000, // 30 seconds
      timeoutPerCheck: 5000, // 5 seconds
      enableDetailedChecks: true,
      criticalComponents: ['database', 'system', 'application'],
      ...config
    };

    this.systemMonitor = systemMonitor;
    this.applicationMonitor = applicationMonitor;
    this.startTime = new Date();

    this.setupDefaultChecks();
    logger.info('Health Check Service initialized', { config: this.config });
  }

  /**
   * Start health monitoring
   */
  public start(): void {
    if (this.isRunning) {
      logger.warn('Health Check Service is already running');
      return;
    }

    this.isRunning = true;

    // Perform initial health check
    this.performHealthCheck();

    // Start periodic health checks
    this.checkInterval = setInterval(() => {
      this.performHealthCheck();
    }, this.config.checkInterval);

    logger.info('Health Check Service started', {
      interval: this.config.checkInterval
    });

    this.emit('started');
  }

  /**
   * Stop health monitoring
   */
  public stop(): void {
    if (!this.isRunning) {
      return;
    }

    this.isRunning = false;

    if (this.checkInterval) {
      clearInterval(this.checkInterval);
      this.checkInterval = undefined;
    }

    logger.info('Health Check Service stopped');
    this.emit('stopped');
  }

  /**
   * Get current health status
   */
  public async getCurrentHealth(): Promise<HealthStatus> {
    return this.performHealthCheck();
  }

  /**
   * Get cached health status
   */
  public getCachedHealth(): HealthStatus | null {
    return this.lastHealthStatus || null;
  }

  /**
   * Add a custom component health check
   */
  public addComponentCheck(name: string, checkFunction: () => Promise<ComponentHealth>): void {
    this.componentChecks.set(name, checkFunction);
    logger.info('Component health check added', { component: name });
  }

  /**
   * Remove a component health check
   */
  public removeComponentCheck(name: string): void {
    this.componentChecks.delete(name);
    logger.info('Component health check removed', { component: name });
  }

  /**
   * Check if system is healthy
   */
  public isHealthy(): boolean {
    return this.lastHealthStatus?.status === 'healthy';
  }

  /**
   * Get uptime in seconds
   */
  public getUptime(): number {
    return (Date.now() - this.startTime.getTime()) / 1000;
  }

  /**
   * Perform comprehensive health check
   */
  private async performHealthCheck(): Promise<HealthStatus> {
    const startTime = Date.now();
    const components: Record<string, ComponentHealth> = {};

    try {
      // Run all component checks
      const checkPromises = Array.from(this.componentChecks.entries()).map(
        async ([name, checkFn]) => {
          try {
            const result = await Promise.race([
              checkFn(),
              this.createTimeoutPromise(name)
            ]);
            if (typeof name !== 'string' || !name) {
              throw new Error('Health check component name must be a non-empty string');
            }
            components[name as keyof typeof components] = result;
          } catch (error) {
            if (typeof name !== 'string' || !name) {
              throw new Error('Health check component name must be a non-empty string');
            }
            components[name as keyof typeof components] = {
              status: 'unhealthy',
              message: error instanceof Error ? error.message : String(error),
              lastCheck: new Date(),
              responseTime: Date.now() - startTime
            };
          }
        }
      );

      await Promise.all(checkPromises);

      // Calculate overall status
      const summary = {
        healthy: 0,
        degraded: 0,
        unhealthy: 0,
        total: Object.keys(components).length
      };

      let overallStatus: HealthStatus['status'] = 'healthy';

      for (const component of Object.values(components)) {
        summary[component.status]++;
        
        // If any critical component is unhealthy, mark overall as unhealthy
        if (component.status === 'unhealthy' && 
            this.config.criticalComponents.includes(component.status)) {
          overallStatus = 'unhealthy';
        } else if (component.status === 'degraded' && overallStatus === 'healthy') {
          overallStatus = 'degraded';
        }
      }

      const healthStatus: HealthStatus = {
        status: overallStatus,
        timestamp: new Date(),
        uptime: this.getUptime(),
        version: process.env.npm_package_version || '1.0.0',
        environment: process.env.NODE_ENV || 'development',
        components,
        summary
      };

      this.lastHealthStatus = healthStatus;

      // Emit health status change if different
      if (!this.lastHealthStatus || this.lastHealthStatus.status !== healthStatus.status) {
        this.emit('healthStatusChange', healthStatus);
      }

      this.emit('healthCheck', healthStatus);

      logger.debug('Health check completed', {
        status: healthStatus.status,
        componentCount: summary.total,
        responseTime: Date.now() - startTime
      });

      return healthStatus;

    } catch (error) {
      logger.error('Health check failed', { error });
      
      const errorStatus: HealthStatus = {
        status: 'unhealthy',
        timestamp: new Date(),
        uptime: this.getUptime(),
        version: process.env.npm_package_version || '1.0.0',
        environment: process.env.NODE_ENV || 'development',
        components,
        summary: {
          healthy: 0,
          degraded: 0,
          unhealthy: 1,
          total: 1
        }
      };

      this.lastHealthStatus = errorStatus;
      this.emit('healthCheck', errorStatus);
      
      return errorStatus;
    }
  }

  /**
   * Create a timeout promise for health checks
   */
  private createTimeoutPromise(componentName: string): Promise<ComponentHealth> {
    return new Promise((_, reject) => {
      setTimeout(() => {
        reject(new Error(`Health check timeout for ${componentName}`));
      }, this.config.timeoutPerCheck);
    });
  }

  /**
   * Setup default health checks
   */
  private setupDefaultChecks(): void {
    // Database health check
    this.addComponentCheck('database', async (): Promise<ComponentHealth> => {
      const startTime = Date.now();
      
      try {
        // Simple query to test database connectivity
        await database.query('SELECT 1 as health_check');
        
        return {
          status: 'healthy',
          message: 'Database connection successful',
          lastCheck: new Date(),
          responseTime: Date.now() - startTime
        };
      } catch (error) {
        return {
          status: 'unhealthy',
          message: `Database connection failed: ${error instanceof Error ? error.message : String(error)}`,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime
        };
      }
    });

    // System health check
    this.addComponentCheck('system', async (): Promise<ComponentHealth> => {
      const startTime = Date.now();
      
      try {
        if (!this.systemMonitor) {
          return {
            status: 'degraded',
            message: 'System monitor not available',
            lastCheck: new Date(),
            responseTime: Date.now() - startTime
          };
        }

        const systemHealth = this.systemMonitor.getHealthSummary();
        
        return {
          status: systemHealth.status === 'healthy' ? 'healthy' : 
                  systemHealth.status === 'warning' ? 'degraded' : 'unhealthy',
          message: systemHealth.issues.length > 0 ? 
                   systemHealth.issues.join(', ') : 'System resources healthy',
          lastCheck: new Date(),
          responseTime: Date.now() - startTime,
          metadata: {
            uptime: systemHealth.uptime,
            lastSystemCheck: systemHealth.lastCheck,
            issues: systemHealth.issues
          }
        };
      } catch (error) {
        return {
          status: 'unhealthy',
          message: `System health check failed: ${error instanceof Error ? error.message : String(error)}`,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime
        };
      }
    });

    // Application health check
    this.addComponentCheck('application', async (): Promise<ComponentHealth> => {
      const startTime = Date.now();
      
      try {
        if (!this.applicationMonitor) {
          return {
            status: 'degraded',
            message: 'Application monitor not available',
            lastCheck: new Date(),
            responseTime: Date.now() - startTime
          };
        }

        const appMetrics = this.applicationMonitor.getCurrentMetrics();
        const issues: string[] = [];

        // Check error rate
        if (appMetrics.performance.errorRate > 10) {
          issues.push(`High error rate: ${appMetrics.performance.errorRate.toFixed(1)}%`);
        }

        // Check response time
        if (appMetrics.performance.responseTime.average > 2000) {
          issues.push(`High response time: ${appMetrics.performance.responseTime.average.toFixed(0)}ms`);
        }

        // Check database
        if (appMetrics.database.averageQueryTime > 1000) {
          issues.push(`Slow database queries: ${appMetrics.database.averageQueryTime.toFixed(0)}ms`);
        }

        const status = issues.length === 0 ? 'healthy' : 
                      issues.length <= 2 ? 'degraded' : 'unhealthy';

        return {
          status,
          message: issues.length > 0 ? issues.join(', ') : 'Application metrics healthy',
          lastCheck: new Date(),
          responseTime: Date.now() - startTime,
          metadata: {
            errorRate: appMetrics.performance.errorRate,
            avgResponseTime: appMetrics.performance.responseTime.average,
            throughput: appMetrics.performance.throughput.requestsPerSecond,
            dbQueryTime: appMetrics.database.averageQueryTime
          }
        };
      } catch (error) {
        return {
          status: 'unhealthy',
          message: `Application health check failed: ${error instanceof Error ? error.message : String(error)}`,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime
        };
      }
    });

    // Memory health check
    this.addComponentCheck('memory', async (): Promise<ComponentHealth> => {
      const startTime = Date.now();
      
      try {
        const memUsage = process.memoryUsage();
        const heapUsedMB = memUsage.heapUsed / 1024 / 1024;
        const heapTotalMB = memUsage.heapTotal / 1024 / 1024;
        const usage = (heapUsedMB / heapTotalMB) * 100;

        let status: ComponentHealth['status'] = 'healthy';
        let message = `Memory usage: ${heapUsedMB.toFixed(1)}MB (${usage.toFixed(1)}%)`;

        if (usage > 90) {
          status = 'unhealthy';
          message = `Critical memory usage: ${usage.toFixed(1)}%`;
        } else if (usage > 80) {
          status = 'degraded';
          message = `High memory usage: ${usage.toFixed(1)}%`;
        }

        return {
          status,
          message,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime,
          metadata: {
            heapUsed: heapUsedMB,
            heapTotal: heapTotalMB,
            usage,
            external: memUsage.external / 1024 / 1024,
            rss: memUsage.rss / 1024 / 1024
          }
        };
      } catch (error) {
        return {
          status: 'unhealthy',
          message: `Memory health check failed: ${error instanceof Error ? error.message : String(error)}`,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime
        };
      }
    });

    // Process health check
    this.addComponentCheck('process', async (): Promise<ComponentHealth> => {
      const startTime = Date.now();
      
      try {
        const uptime = process.uptime();
        const pid = process.pid;
        const version = process.version;

        // Check if process has been running for a reasonable time
        const status: ComponentHealth['status'] = uptime > 10 ? 'healthy' : 'degraded';
        const message = `Process ${pid} running for ${Math.floor(uptime)}s on Node.js ${version}`;

        return {
          status,
          message,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime,
          metadata: {
            pid,
            uptime,
            nodeVersion: version,
            platform: process.platform,
            arch: process.arch
          }
        };
      } catch (error) {
        return {
          status: 'unhealthy',
          message: `Process health check failed: ${error instanceof Error ? error.message : String(error)}`,
          lastCheck: new Date(),
          responseTime: Date.now() - startTime
        };
      }
    });

    logger.info('Default health checks setup completed');
  }

  /**
   * Get health check summary for quick status
   */
  public getHealthSummary(): {
    status: 'healthy' | 'degraded' | 'unhealthy';
    uptime: number;
    lastCheck: Date | null;
    criticalIssues: string[];
  } {
    const health = this.getCachedHealth();
    
    if (!health) {
      return {
        status: 'unhealthy',
        uptime: this.getUptime(),
        lastCheck: null,
        criticalIssues: ['No health data available']
      };
    }

    const criticalIssues: string[] = [];
    
    for (const [name, component] of Object.entries(health.components)) {
      if (component.status === 'unhealthy' && this.config.criticalComponents.includes(name)) {
        criticalIssues.push(`${name}: ${component.message}`);
      }
    }

    return {
      status: health.status,
      uptime: health.uptime,
      lastCheck: health.timestamp,
      criticalIssues
    };
  }

  /**
   * Express middleware for health check endpoint
   */
  public getHealthMiddleware() {
    return async (req: any, res: any) => {
      try {
        const health = await this.getCurrentHealth();
        
        const statusCode = health.status === 'healthy' ? 200 :
                          health.status === 'degraded' ? 200 : 503;

        res.status(statusCode).json(health);
      } catch (error) {
        logger.error('Health check endpoint error', { error });
        res.status(503).json({
          status: 'unhealthy',
          message: 'Health check failed',
          timestamp: new Date().toISOString()
        });
      }
    };
  }

  /**
   * Express middleware for readiness check
   */
  public getReadinessMiddleware() {
    return async (req: any, res: any) => {
      try {
        const health = await this.getCurrentHealth();
        
        // Ready if system is healthy or degraded (but not unhealthy)
        const isReady = health.status !== 'unhealthy';
        
        if (isReady) {
          res.status(200).json({
            status: 'ready',
            timestamp: new Date().toISOString()
          });
        } else {
          res.status(503).json({
            status: 'not_ready',
            reason: 'Critical components unhealthy',
            timestamp: new Date().toISOString()
          });
        }
      } catch (error) {
        logger.error('Readiness check endpoint error', { error });
        res.status(503).json({
          status: 'not_ready',
          reason: 'Readiness check failed',
          timestamp: new Date().toISOString()
        });
      }
    };
  }

  /**
   * Express middleware for liveness check
   */
  public getLivenessMiddleware() {
    return (req: any, res: any) => {
      // Simple liveness check - if we can respond, we're alive
      res.status(200).json({
        status: 'alive',
        uptime: this.getUptime(),
        timestamp: new Date().toISOString()
      });
    };
  }

  /**
   * Shutdown the health check service
   */
  public shutdown(): void {
    this.stop();
    this.componentChecks.clear();
    this.lastHealthStatus = undefined;
    this.removeAllListeners();
    
    logger.info('Health Check Service shut down');
  }
}

export default HealthCheckService;

/**
 * Metrics Collector Service
 * 
 * Centralized metrics collection and aggregation service that gathers metrics
 * from all monitoring services and provides unified metrics storage and querying.
 */

import { EventEmitter } from 'events';

import type { ApplicationMetrics } from './application-monitor.service';
import logger from './logging.service';
import type { SystemMetrics } from './system-monitor.service';

export interface MetricValue {
  timestamp: Date;
  value: number;
  labels?: Record<string, string>;
}

export interface MetricSeries {
  name: string;
  type: 'counter' | 'gauge' | 'histogram' | 'summary';
  unit?: string;
  description?: string;
  values: MetricValue[];
}

export interface AggregatedMetrics {
  system: {
    cpu: {
      usage: MetricSeries;
      loadAverage: MetricSeries;
    };
    memory: {
      usage: MetricSeries;
      percentage: MetricSeries;
    };
    disk: {
      usage: MetricSeries;
      percentage: MetricSeries;
    };
    process: {
      uptime: MetricSeries;
      memoryUsage: MetricSeries;
    };
  };
  application: {
    performance: {
      responseTime: MetricSeries;
      throughput: MetricSeries;
      errorRate: MetricSeries;
    };
    database: {
      queryTime: MetricSeries;
      queryCount: MetricSeries;
      connectionPool: MetricSeries;
    };
    websocket: {
      activeConnections: MetricSeries;
      messagesPerSecond: MetricSeries;
    };
  };
  trading: {
    bots: {
      activeCount: MetricSeries;
      totalTrades: MetricSeries;
      totalPnL: MetricSeries;
      winRate: MetricSeries;
    };
    orders: {
      placed: MetricSeries;
      filled: MetricSeries;
      cancelled: MetricSeries;
      failed: MetricSeries;
    };
    positions: {
      open: MetricSeries;
      unrealizedPnL: MetricSeries;
    };
  };
  custom: Record<string, MetricSeries>;
}

export interface MetricsQuery {
  metric: string;
  startTime?: Date;
  endTime?: Date;
  labels?: Record<string, string>;
  aggregation?: 'avg' | 'sum' | 'min' | 'max' | 'count' | 'last';
  groupBy?: string[];
  interval?: number; // milliseconds for time-based grouping
}

export interface QueryResult {
  metric: string;
  values: Array<{
    timestamp: Date;
    value: number;
    labels?: Record<string, string>;
  }>;
  aggregation?: string;
  interval?: number;
}

export interface MetricsCollectorConfig {
  retentionPeriod: number; // milliseconds
  maxMetricsPerSeries: number;
  aggregationInterval: number; // milliseconds
  enablePersistence: boolean;
  persistenceInterval: number; // milliseconds
}

export class MetricsCollectorService extends EventEmitter {
  private config: MetricsCollectorConfig;
  private metrics: Map<string, MetricSeries> = new Map();
  private aggregatedMetrics: AggregatedMetrics;
  private aggregationInterval?: NodeJS.Timeout;
  private persistenceInterval?: NodeJS.Timeout;
  private isRunning = false;

  constructor(config: Partial<MetricsCollectorConfig> = {}) {
    super();
    
    this.config = {
      retentionPeriod: 24 * 60 * 60 * 1000, // 24 hours
      maxMetricsPerSeries: 10000,
      aggregationInterval: 60000, // 1 minute
      enablePersistence: true,
      persistenceInterval: 5 * 60 * 1000, // 5 minutes
      ...config
    };

    this.aggregatedMetrics = this.initializeAggregatedMetrics();
    logger.info('Metrics Collector Service initialized', { config: this.config });
  }

  /**
   * Start metrics collection
   */
  public start(): void {
    if (this.isRunning) {
      logger.warn('Metrics Collector is already running');
      return;
    }

    this.isRunning = true;

    // Start aggregation interval
    this.aggregationInterval = setInterval(() => {
      this.performAggregation();
      this.cleanupOldMetrics();
    }, this.config.aggregationInterval);

    // Start persistence interval if enabled
    if (this.config.enablePersistence) {
      this.persistenceInterval = setInterval(() => {
        this.persistMetrics();
      }, this.config.persistenceInterval);
    }

    logger.info('Metrics Collector started', {
      aggregationInterval: this.config.aggregationInterval,
      persistenceEnabled: this.config.enablePersistence
    });

    this.emit('started');
  }

  /**
   * Stop metrics collection
   */
  public stop(): void {
    if (!this.isRunning) {
      return;
    }

    this.isRunning = false;

    if (this.aggregationInterval) {
      clearInterval(this.aggregationInterval);
      this.aggregationInterval = undefined;
    }

    if (this.persistenceInterval) {
      clearInterval(this.persistenceInterval);
      this.persistenceInterval = undefined;
    }

    logger.info('Metrics Collector stopped');
    this.emit('stopped');
  }

  /**
   * Record a metric value
   */
  public recordMetric(
    name: string,
    value: number,
    type: MetricSeries['type'] = 'gauge',
    options: {
      unit?: string;
      description?: string;
      labels?: Record<string, string>;
      timestamp?: Date;
    } = {}
  ): void {
    if (!this.isRunning) {
      throw new Error('Metrics collector is not running. Call start() first.');
    }

    const metricValue: MetricValue = {
      timestamp: options.timestamp || new Date(),
      value,
      labels: options.labels
    };

    let series = this.metrics.get(name);
    
    if (!series) {
      series = {
        name,
        type,
        unit: options.unit,
        description: options.description,
        values: []
      };
      this.metrics.set(name, series);
    }

    // Add the value
    series.values.push(metricValue);

    // Trim if exceeding max size
    if (series.values.length > this.config.maxMetricsPerSeries) {
      series.values = series.values.slice(-this.config.maxMetricsPerSeries / 2);
    }

    // Emit metric event
    this.emit('metric', { name, value: metricValue, series });

    logger.debug('Metric recorded', {
      name,
      value,
      type,
      labels: options.labels
    });
  }

  /**
   * Record counter increment
   */
  public incrementCounter(name: string, amount = 1, labels?: Record<string, string>): void {
    const existing = this.getLatestValue(name);
    const newValue = (existing?.value || 0) + amount;
    
    this.recordMetric(name, newValue, 'counter', { labels });
  }

  /**
   * Record gauge value
   */
  public recordGauge(name: string, value: number, labels?: Record<string, string>): void {
    this.recordMetric(name, value, 'gauge', { labels });
  }

  /**
   * Record histogram value (simplified implementation)
   */
  public recordHistogram(name: string, value: number, labels?: Record<string, string>): void {
    // For simplicity, we'll store histogram values as individual points
    // In a production system, you'd want proper histogram buckets
    this.recordMetric(`${name}_value`, value, 'histogram', { labels });
    this.incrementCounter(`${name}_count`, 1, labels);
  }

  /**
   * Process system metrics
   */
  public processSystemMetrics(metrics: SystemMetrics): void {
    const timestamp = metrics.timestamp;

    // CPU metrics
    this.recordMetric('system.cpu.usage', metrics.cpu.usage, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'CPU usage percentage'
    });

    this.recordMetric('system.cpu.load_average_1m', metrics.cpu.loadAverage.at(0) || 0, 'gauge', {
      unit: 'load',
      timestamp,
      description: '1-minute load average'
    });

    // Memory metrics
    this.recordMetric('system.memory.usage', metrics.memory.used, 'gauge', {
      unit: 'bytes',
      timestamp,
      description: 'Memory usage in bytes'
    });

    this.recordMetric('system.memory.percentage', metrics.memory.percentage, 'gauge', {
      unit: 'percent',
      timestamp,
      description: 'Memory usage percentage'
    });

    // Disk metrics
    this.recordMetric('system.disk.usage', metrics.disk.used, 'gauge', {
