  /**
   * Query metrics
   */
  public queryMetrics(query: MetricsQuery): QueryResult {
    const series = this.metrics.get(query.metric);
    
    if (!series) {
      return {
        metric: query.metric,
        values: []
      };
    }

    let values = [...series.values];

    // Filter by time range
    if (query.startTime) {
      values = values.filter(v => v.timestamp >= query.startTime!);
    }
    
    if (query.endTime) {
      values = values.filter(v => v.timestamp <= query.endTime!);
    }

    // Filter by labels
    if (query.labels) {
      values = values.filter(v => {
        if (!v.labels) {return false;}
        
        for (const [key, value] of Object.entries(query.labels!)) {
          if (v.labels[key] !== value) {return false;}
        }
        
        return true;
      });
    }

    // Apply aggregation
    if (query.aggregation && values.length > 0) {
      const aggregatedValue = this.aggregateValues(values, query.aggregation);
      const lastValue = values.at(-1);
      if (lastValue) {
        return {
          metric: query.metric,
          values: [{
            timestamp: lastValue.timestamp,
            value: aggregatedValue,
            labels: query.labels
          }],
          aggregation: query.aggregation
        };
      }
    }

    // Group by interval if specified
    if (query.interval && query.interval > 0) {
      values = this.groupByInterval(values, query.interval);
    }

    return {
      metric: query.metric,
      values: values.map(v => ({
        timestamp: v.timestamp,
        value: v.value,
        labels: v.labels
      })),
      interval: query.interval
    };
  }

  /**
   * Get all metric names
   */
  public getMetricNames(): string[] {
    return Array.from(this.metrics.keys());
  }

  /**
   * Get metric series
   */
  public getMetricSeries(name: string): MetricSeries | undefined {
    return this.metrics.get(name);
  }

  /**
   * Get latest value for a metric
   */
  public getLatestValue(name: string): MetricValue | undefined {
    const series = this.metrics.get(name);
    if (!series || series.values.length === 0) {
      return undefined;
    }
    
    return series.values[series.values.length - 1];
  }

  /**
   * Get aggregated metrics
   */
  public getAggregatedMetrics(): AggregatedMetrics {
    return this.aggregatedMetrics;
  }

  /**
   * Get metrics summary for dashboard
   */
  public getMetricsSummary(): {
    totalMetrics: number;
    totalDataPoints: number;
    oldestDataPoint: Date | null;
    newestDataPoint: Date | null;
    memoryUsage: number;
  } {
    let totalDataPoints = 0;
    let oldestDataPoint: Date | null = null;
    let newestDataPoint: Date | null = null;

    for (const series of this.metrics.values()) {
      totalDataPoints += series.values.length;
      
      if (series.values.length > 0) {
        const firstValue = series.values.at(0);
        const lastValue = series.values[series.values.length - 1];
        
        if (firstValue && lastValue) {
          const oldest = firstValue.timestamp;
          const newest = lastValue.timestamp;
          
          if (!oldestDataPoint || oldest < oldestDataPoint) {
            oldestDataPoint = oldest;
          }
          
          if (!newestDataPoint || newest > newestDataPoint) {
            newestDataPoint = newest;
          }
        }
      }
    }

    return {
      totalMetrics: this.metrics.size,
      totalDataPoints,
      oldestDataPoint,
      newestDataPoint,
      memoryUsage: process.memoryUsage().heapUsed
    };
  }

  /**
   * Initialize aggregated metrics structure
   */
  private initializeAggregatedMetrics(): AggregatedMetrics {
    const createSeries = (name: string, type: MetricSeries['type'] = 'gauge'): MetricSeries => ({
      name,
      type,
      values: []
    });

    return {
      system: {
        cpu: {
          usage: createSeries('system.cpu.usage'),
          loadAverage: createSeries('system.cpu.load_average_1m')
        },
        memory: {
          usage: createSeries('system.memory.usage'),
          percentage: createSeries('system.memory.percentage')
        },
        disk: {
          usage: createSeries('system.disk.usage'),
          percentage: createSeries('system.disk.percentage')
        },
        process: {
          uptime: createSeries('system.process.uptime'),
          memoryUsage: createSeries('system.process.memory')
        }
      },
      application: {
        performance: {
          responseTime: createSeries('app.performance.response_time_avg'),
          throughput: createSeries('app.performance.throughput'),
          errorRate: createSeries('app.performance.error_rate')
        },
        database: {
          queryTime: createSeries('app.database.query_time_avg'),
          queryCount: createSeries('app.database.query_count', 'counter'),
          connectionPool: createSeries('app.database.active_connections')
        },
        websocket: {
          activeConnections: createSeries('app.websocket.active_connections'),
          messagesPerSecond: createSeries('app.websocket.messages_per_second')
        }
      },
      trading: {
        bots: {
          activeCount: createSeries('trading.bots.active'),
          totalTrades: createSeries('trading.bots.total_trades', 'counter'),
          totalPnL: createSeries('trading.bots.total_pnl'),
          winRate: createSeries('trading.bots.win_rate')
        },
        orders: {
          placed: createSeries('trading.orders.placed', 'counter'),
          filled: createSeries('trading.orders.filled', 'counter'),
          cancelled: createSeries('trading.orders.cancelled', 'counter'),
          failed: createSeries('trading.orders.failed', 'counter')
        },
        positions: {
          open: createSeries('trading.positions.open'),
          unrealizedPnL: createSeries('trading.positions.unrealized_pnl')
        }
      },
      custom: {}
    };
  }

  /**
   * Perform aggregation
   */
  private performAggregation(): void {
    // Update aggregated metrics with latest values
    for (const [key, series] of this.metrics) {
      if (series.values.length === 0) {continue;}
      
      const latestValue = series.values[series.values.length - 1];
      
      // Update aggregated structure
      if (key.startsWith('system.cpu.usage') && latestValue) {
        this.aggregatedMetrics.system.cpu.usage.values.push(latestValue);
      } else if (key.startsWith('system.memory.percentage') && latestValue) {
        this.aggregatedMetrics.system.memory.percentage.values.push(latestValue);
      }
      // Add more mappings as needed...
    }

    this.emit('aggregation', this.aggregatedMetrics);
  }

  /**
   * Aggregate values using specified method
   */
  private aggregateValues(values: MetricValue[], method: string): number {
    if (values.length === 0) {return 0;}

    switch (method) {
      case 'avg':
        return values.reduce((sum, v) => sum + v.value, 0) / values.length;
      case 'sum':
        return values.reduce((sum, v) => sum + v.value, 0);
      case 'min':
        return Math.min(...values.map(v => v.value));
      case 'max':
        return Math.max(...values.map(v => v.value));
      case 'count':
        return values.length;
      case 'last':
        return values.length > 0 ? values.at(-1)?.value ?? 0 : 0;
      default:
        return values.length > 0 ? values.at(-1)?.value ?? 0 : 0;
    }
  }

  /**
   * Group values by time interval
   */
  private groupByInterval(values: MetricValue[], interval: number): MetricValue[] {
    if (values.length === 0) {return [];}

    const firstValue = values.at(0);
    if (!firstValue) {return [];}

    const grouped: MetricValue[] = [];
    const startTime = firstValue.timestamp.getTime();
    
    let currentBucket = Math.floor((firstValue.timestamp.getTime() - startTime) / interval);
    let bucketValues: MetricValue[] = [];

    for (const value of values) {
      const bucket = Math.floor((value.timestamp.getTime() - startTime) / interval);
      
      if (bucket === currentBucket) {
        bucketValues.push(value);
      } else {
        // Process current bucket
        if (bucketValues.length > 0) {
          const avgValue = bucketValues.reduce((sum, v) => sum + v.value, 0) / bucketValues.length;
          const firstBucketValue = bucketValues.at(0);
          grouped.push({
            timestamp: new Date(startTime + currentBucket * interval),
            value: avgValue,
            labels: firstBucketValue?.labels
          });
        }
        
        // Start new bucket
        currentBucket = bucket;
        bucketValues = [value];
      }
    }

    // Process last bucket
    if (bucketValues.length > 0) {
      const avgValue = bucketValues.reduce((sum, v) => sum + v.value, 0) / bucketValues.length;
      const firstBucketValue = bucketValues.at(0);
      grouped.push({
        timestamp: new Date(startTime + currentBucket * interval),
        value: avgValue,
        labels: firstBucketValue?.labels
      });
    }

    return grouped;
  }

  /**
   * Clean up old metrics
   */
  private cleanupOldMetrics(): void {
    const cutoff = new Date(Date.now() - this.config.retentionPeriod);
    let totalRemoved = 0;

    for (const [name, series] of this.metrics) {
      const initialLength = series.values.length;
      series.values = series.values.filter(v => v.timestamp >= cutoff);
      totalRemoved += initialLength - series.values.length;
    }

    if (totalRemoved > 0) {
      logger.debug(`Cleaned up ${totalRemoved} old metric values`);
    }
  }

  /**
   * Persist metrics to storage (placeholder implementation)
   */
  private async persistMetrics(): Promise<void> {
    try {
      // In a real implementation, this would save metrics to a database
      // For now, we'll just log the operation
      const summary = this.getMetricsSummary();
      logger.debug('Metrics persistence checkpoint', {
        totalMetrics: summary.totalMetrics,
        totalDataPoints: summary.totalDataPoints
      });
      
      this.emit('persistence', summary);
    } catch (error) {
      logger.error('Failed to persist metrics', { error });
    }
  }

  /**
   * Reset all metrics
   */
  public reset(): void {
    this.metrics.clear();
    this.aggregatedMetrics = this.initializeAggregatedMetrics();
    
    logger.info('Metrics Collector reset');
    this.emit('reset');
  }

  /**
   * Shutdown the metrics collector
   */
  public shutdown(): void {
    this.stop();
    
    // Perform final persistence if enabled
    if (this.config.enablePersistence) {
      this.persistMetrics();
    }
    
    this.reset();
    this.removeAllListeners();
    
    logger.info('Metrics Collector shut down');
  }
}

export default MetricsCollectorService;

/**
 * Comprehensive Monitoring Service
 * 
 * Central monitoring service that coordinates all monitoring components
 * and provides unified monitoring capabilities for the bot lifecycle system.
 */

import { EventEmitter } from 'events';

import AlertManagerService from './alert-manager.service';
import ApplicationMonitorService from './application-monitor.service';
import DatabaseMonitorService from './database-monitor.service';
import ExchangeMonitorService from './exchange-monitor.service';
import HealthCheckService from './health-check.service';
import logger from './logging.service';
import MetricsCollectorService from './metrics-collector.service';
import SystemMonitorService from './system-monitor.service';

export interface MonitoringConfig {
  enableSystemMonitoring: boolean;
  enableApplicationMonitoring: boolean;
  enableDatabaseMonitoring: boolean;
  enableExchangeMonitoring: boolean;
  enableHealthChecks: boolean;
  enableMetricsCollection: boolean;
  enableAlerting: boolean;
  metricsRetentionHours: number;
  alertingEnabled: boolean;
  broadcastMetrics: boolean;
}

export interface MonitoringStatus {
  isRunning: boolean;
  services: {
    system: boolean;
    application: boolean;
    database: boolean;
    exchange: boolean;
    health: boolean;
    metrics: boolean;
    alerts: boolean;
  };
  startTime: Date;
  uptime: number;
  lastHealthCheck: Date | null;
  totalAlerts: number;
  criticalIssues: string[];
}

export class MonitoringService extends EventEmitter {
  private config: MonitoringConfig;
  private systemMonitor?: SystemMonitorService;
  private applicationMonitor?: ApplicationMonitorService;
  private alertManager?: AlertManagerService;
  private metricsCollector?: MetricsCollectorService;
  private healthCheck?: HealthCheckService;
  private databaseMonitor?: DatabaseMonitorService;
  private exchangeMonitor?: ExchangeMonitorService;
  
  private isRunning = false;
  private startTime?: Date;
  private websocketServer?: any; // JabbrWebSocketServer type

  constructor(
    config: Partial<MonitoringConfig> = {},
    websocketServer?: any
  ) {
    super();
    
    this.config = {
      enableSystemMonitoring: true,
      enableApplicationMonitoring: true,
      enableDatabaseMonitoring: true,
      enableExchangeMonitoring: true,
      enableHealthChecks: true,
      enableMetricsCollection: true,
      enableAlerting: true,
      metricsRetentionHours: 24,
      alertingEnabled: true,
      broadcastMetrics: true,
      ...config
    };

    this.websocketServer = websocketServer;

    logger.info('Monitoring Service initialized', { 
      config: this.config 
    });
  }

  /**
   * Start comprehensive monitoring
   */
  public async start(): Promise<void> {
    if (this.isRunning) {
      logger.warn('Monitoring Service is already running');
      return;
    }

    this.startTime = new Date();
    this.isRunning = true;

    try {
      // Initialize all monitoring services
      await this.initializeServices();

      // Start individual services
      await this.startServices();

      // Setup cross-service communication
      this.setupServiceIntegration();

      logger.info('Comprehensive Monitoring Service started successfully');
      this.emit('started');

    } catch (error) {
      logger.error('Failed to start Monitoring Service', { error });
      this.isRunning = false;
      throw error;
    }
  }

  /**
   * Stop comprehensive monitoring
   */
  public async stop(): Promise<void> {
    if (!this.isRunning) {
      return;
    }

    logger.info('Stopping Monitoring Service...');

    try {
      // Stop all services
      await this.stopServices();

      this.isRunning = false;
      this.startTime = undefined;

      logger.info('Monitoring Service stopped successfully');
      this.emit('stopped');

    } catch (error) {
      logger.error('Error stopping Monitoring Service', { error });
      throw error;
    }
  }

  /**
   * Get current monitoring status
   */
  public getStatus(): MonitoringStatus {
    const totalAlerts = this.alertManager?.getActiveAlerts().length || 0;
    const criticalIssues: string[] = [];

    // Collect critical issues from all services
    if (this.systemMonitor) {
      const systemHealth = this.systemMonitor.getHealthSummary();
      if (systemHealth.status === 'critical') {
        criticalIssues.push(...systemHealth.issues);
      }
    }

    if (this.databaseMonitor) {
      this.databaseMonitor.getHealthSummary().then(dbHealth => {
        if (dbHealth.status === 'critical') {
          criticalIssues.push(...dbHealth.issues);
        }
      });
    }

    if (this.healthCheck) {
      const healthSummary = this.healthCheck.getHealthSummary();
      if (healthSummary.status === 'unhealthy') {
        criticalIssues.push(...healthSummary.criticalIssues);
      }
    }

    return {
      isRunning: this.isRunning,
      services: {
        system: !!this.systemMonitor,
        application: !!this.applicationMonitor,
        database: !!this.databaseMonitor,
        exchange: !!this.exchangeMonitor,
        health: !!this.healthCheck,
        metrics: !!this.metricsCollector,
        alerts: !!this.alertManager
      },
      startTime: this.startTime || new Date(),
      uptime: this.startTime ? (Date.now() - this.startTime.getTime()) / 1000 : 0,
      lastHealthCheck: this.healthCheck?.getCachedHealth()?.timestamp || null,
      totalAlerts,
      criticalIssues
    };
  }

  /**
   * Get comprehensive health report
   */
  public async getHealthReport(): Promise<{
    overall: 'healthy' | 'warning' | 'critical';
    services: Record<string, any>;
    summary: {
      totalServices: number;
      healthyServices: number;
      warningServices: number;
      criticalServices: number;
    };
    recommendations: string[];
  }> {
    const services: Record<string, any> = {};
    let healthyCount = 0;
    let warningCount = 0;
    let criticalCount = 0;
    const recommendations: string[] = [];

    // System monitoring
    if (this.systemMonitor) {
      const systemHealth = this.systemMonitor.getHealthSummary();
      services.system = systemHealth;
      
      if (systemHealth.status === 'healthy') {healthyCount++;}
      else if (systemHealth.status === 'warning') {warningCount++;}
      else {criticalCount++;}

      if (systemHealth.status !== 'healthy') {
        recommendations.push('Check system resources and optimize performance');
      }
    }

    // Application monitoring
    if (this.applicationMonitor) {
      const appMetrics = this.applicationMonitor.getCurrentMetrics();
      services.application = {
        status: appMetrics.performance.errorRate > 10 ? 'critical' : 
                appMetrics.performance.errorRate > 5 ? 'warning' : 'healthy',
        errorRate: appMetrics.performance.errorRate,
        responseTime: appMetrics.performance.responseTime.average,
        throughput: appMetrics.performance.throughput.requestsPerSecond
      };

      if (services.application.status === 'healthy') {healthyCount++;}
      else if (services.application.status === 'warning') {warningCount++;}
      else {criticalCount++;}

      if (appMetrics.performance.errorRate > 5) {
        recommendations.push('Investigate application errors and improve error handling');
      }
    }

    // Database monitoring
    if (this.databaseMonitor) {
      const dbHealth = await this.databaseMonitor.getHealthSummary();
      services.database = dbHealth;

      if (dbHealth.status === 'healthy') {healthyCount++;}
      else if (dbHealth.status === 'warning') {warningCount++;}
      else {criticalCount++;}

      if (dbHealth.status !== 'healthy') {
        recommendations.push('Optimize database queries and check connection pool');
      }
    }

    // Exchange monitoring
    if (this.exchangeMonitor) {
      const exchanges = this.exchangeMonitor.getAllMetrics();
      const exchangeStatuses = Array.from(exchanges.values()).map(e => e.health.status);
      
      const healthyExchanges = exchangeStatuses.filter(s => s === 'healthy').length;
      const warningExchanges = exchangeStatuses.filter(s => s === 'warning').length;
      const criticalExchanges = exchangeStatuses.filter(s => s === 'critical').length;

      services.exchanges = {
        total: exchanges.size,
        healthy: healthyExchanges,
        warning: warningExchanges,
        critical: criticalExchanges,
        status: criticalExchanges > 0 ? 'critical' : 
                warningExchanges > 0 ? 'warning' : 'healthy'
      };

      if (services.exchanges.status === 'healthy') {healthyCount++;}
      else if (services.exchanges.status === 'warning') {warningCount++;}
      else {criticalCount++;}

      if (criticalExchanges > 0) {
        recommendations.push('Check exchange connectivity and API rate limits');
      }
    }

    // Health checks
    if (this.healthCheck) {
      const healthSummary = this.healthCheck.getHealthSummary();
      services.healthCheck = healthSummary;

      if (healthSummary.status === 'healthy') {healthyCount++;}
      else if (healthSummary.status === 'degraded') {warningCount++;}
      else {criticalCount++;}
    }

    // Determine overall status
    const totalServices = healthyCount + warningCount + criticalCount;
    let overall: 'healthy' | 'warning' | 'critical';

    if (criticalCount > 0) {
      overall = 'critical';
    } else if (warningCount > 0) {
      overall = 'warning';
    } else {
      overall = 'healthy';
    }

    return {
      overall,
      services,
      summary: {
        totalServices,
        healthyServices: healthyCount,
        warningServices: warningCount,
        criticalServices: criticalCount
      },
      recommendations
    };
  }

  /**
   * Get monitoring metrics summary
   */
  public async getMetricsSummary(): Promise<{
    system?: any;
    application?: any;
    database?: any;
    exchanges?: any;
    timestamp: Date;
  }> {
    const summary: any = {
      timestamp: new Date()
    };

    if (this.systemMonitor) {
      summary.system = this.systemMonitor.getCurrentMetrics();
    }

    if (this.applicationMonitor) {
      summary.application = this.applicationMonitor.getCurrentMetrics();
    }

    if (this.databaseMonitor) {
      summary.database = await this.databaseMonitor.getCurrentMetrics();
    }

    if (this.exchangeMonitor) {
      summary.exchanges = Object.fromEntries(this.exchangeMonitor.getAllMetrics());
    }

    return summary;
  }

  /**
   * Record a custom metric
   */
  public recordMetric(
    name: string,
    value: number,
    tags?: Record<string, string>,
    timestamp?: Date
  ): void {
    if (this.metricsCollector) {
      this.metricsCollector.recordMetric(name, value, 'gauge', { 
        labels: tags, 
        timestamp 
      });
    }
  }

  /**
   * Create a custom alert
   */
  public createAlert(
    type: string,
    severity: 'low' | 'medium' | 'high' | 'critical',
    message: string,
    source?: string,
    metadata?: any
  ): void {
    if (this.alertManager) {
      this.alertManager.createAlert({
        type: 'custom',
        category: type,
        level: severity === 'low' ? 'info' : severity === 'medium' ? 'warning' : severity === 'high' ? 'error' : 'critical',
        title: `${type} Alert`,
        message,
        source: source || 'monitoring-service',
        metadata
      });
    }
  }

  /**
   * Add exchange to monitoring
   */
  public addExchange(exchangeName: string): void {
    if (this.exchangeMonitor) {
      this.exchangeMonitor.startMonitoring(exchangeName);
      logger.info(`Added exchange to monitoring: ${exchangeName}`);
    }
  }

  /**
   * Remove exchange from monitoring
   */
  public removeExchange(exchangeName: string): void {
    if (this.exchangeMonitor) {
      this.exchangeMonitor.stopMonitoring(exchangeName);
      logger.info(`Removed exchange from monitoring: ${exchangeName}`);
    }
  }

  /**
   * Record API request for exchange monitoring
   */
  public recordExchangeApiRequest(
    exchangeName: string,
    endpoint: string,
    method: string,
    duration: number,
    success: boolean,
    error?: string,
    statusCode?: number,
    rateLimitRemaining?: number,
    weight?: number
  ): void {
    if (this.exchangeMonitor) {
      this.exchangeMonitor.recordApiRequest(
        exchangeName,
        endpoint,
        method,
        duration,
        success,
        error,
        statusCode,
        rateLimitRemaining,
        weight
      );
    }
  }

  /**
   * Record order execution for exchange monitoring
   */
  public recordOrderExecution(
    exchangeName: string,
    orderId: string,
    symbol: string,
    side: 'buy' | 'sell',
    type: 'market' | 'limit' | 'stop',
    quantity: number,
    price: number | undefined,
    executedPrice: number | undefined,
    executedQuantity: number | undefined,
    executionTime: number,
    status: 'filled' | 'partial' | 'failed' | 'cancelled'
  ): void {
    if (this.exchangeMonitor) {
      this.exchangeMonitor.recordOrderExecution(
        exchangeName,
        orderId,
        symbol,
        side,
        type,
        quantity,
        price,
        executedPrice,
        executedQuantity,
        executionTime,
        status
      );
    }
  }

  /**
   * Initialize all monitoring services
   */
  private async initializeServices(): Promise<void> {
    if (this.config.enableAlerting) {
      this.alertManager = new AlertManagerService(this.websocketServer);
    }

    if (this.config.enableMetricsCollection) {
      this.metricsCollector = new MetricsCollectorService({
        retentionPeriod: this.config.metricsRetentionHours * 60 * 60 * 1000 // convert hours to milliseconds
      });
    }

    if (this.config.enableSystemMonitoring) {
      this.systemMonitor = new SystemMonitorService({});
    }

    if (this.config.enableApplicationMonitoring) {
      this.applicationMonitor = new ApplicationMonitorService({});
    }

    if (this.config.enableDatabaseMonitoring) {
      this.databaseMonitor = new DatabaseMonitorService({
        metricsRetentionHours: this.config.metricsRetentionHours
      }, this.websocketServer);
    }

    if (this.config.enableExchangeMonitoring) {
      this.exchangeMonitor = new ExchangeMonitorService({
        metricsRetentionHours: this.config.metricsRetentionHours
      }, this.websocketServer);
    }

    if (this.config.enableHealthChecks) {
      this.healthCheck = new HealthCheckService(
        {},
        this.systemMonitor,
        this.applicationMonitor
      );
    }
  }

  /**
   * Start all monitoring services
   */
  private async startServices(): Promise<void> {
    // AlertManager starts automatically in constructor

    if (this.systemMonitor) {
      this.systemMonitor.start();
    }

    if (this.applicationMonitor) {
      this.applicationMonitor.start();
    }

    if (this.databaseMonitor) {
      this.databaseMonitor.start();
    }

    if (this.healthCheck) {
      this.healthCheck.start();
    }

    // Exchange monitor doesn't have a general start method
    // Exchanges are added individually via addExchange()
  }

  /**
   * Stop all monitoring services
   */
  private async stopServices(): Promise<void> {
    if (this.healthCheck) {
      this.healthCheck.stop();
    }

    if (this.databaseMonitor) {
      this.databaseMonitor.stop();
    }

    if (this.applicationMonitor) {
      this.applicationMonitor.stop();
    }

    if (this.systemMonitor) {
      this.systemMonitor.stop();
    }

    // AlertManager stops automatically

    if (this.exchangeMonitor) {
      this.exchangeMonitor.shutdown();
    }

    if (this.metricsCollector) {
      this.metricsCollector.shutdown();
    }
  }

  /**
   * Setup integration between services
   */
  private setupServiceIntegration(): void {
    // Connect system monitoring to alerting
    if (this.systemMonitor && this.alertManager) {
      this.systemMonitor.on('criticalAlert', (alert) => {
        this.alertManager!.createAlert({
          type: 'system',
          category: 'system_critical',
          level: 'critical',
          title: 'System Critical Alert',
          message: alert.message,
          source: 'system_monitor',
          metadata: alert
        });
      });

      this.systemMonitor.on('warningAlert', (alert) => {
        this.alertManager!.createAlert({
          type: 'system',
          category: 'system_warning',
          level: 'warning',
          title: 'System Warning Alert',
          message: alert.message,
          source: 'system_monitor',
          metadata: alert
        });
      });
    }

    // Connect application monitoring to alerting
    if (this.applicationMonitor && this.alertManager) {
      this.applicationMonitor.on('highErrorRate', (metrics) => {
        this.alertManager!.createAlert({
          type: 'application',
          category: 'high_error_rate',
          level: 'error',
          title: 'High Error Rate Detected',
          message: `High error rate detected: ${metrics.errorRate.toFixed(1)}%`,
          source: 'application_monitor',
          metadata: metrics
        });
      });

      this.applicationMonitor.on('slowResponse', (metrics) => {
        this.alertManager!.createAlert({
          type: 'application',
          category: 'slow_response',
          level: 'warning',
          title: 'Slow Response Time',
          message: `Slow response time: ${metrics.responseTime.toFixed(0)}ms`,
          source: 'application_monitor',
          metadata: metrics
        });
      });
    }

    // Connect database monitoring to alerting
    if (this.databaseMonitor && this.alertManager) {
      this.databaseMonitor.on('slowQuery', (query) => {
        this.alertManager!.createAlert({
          type: 'application',
          category: 'slow_query',
          level: 'warning',
          title: 'Slow Database Query',
          message: `Slow database query: ${query.duration}ms`,
          source: 'database_monitor',
          metadata: query
        });
      });

      this.databaseMonitor.on('queryError', (query) => {
        this.alertManager!.createAlert({
          type: 'application',
          category: 'database_error',
          level: 'error',
          title: 'Database Query Error',
          message: `Database query error: ${query.error}`,
          source: 'database_monitor',
          metadata: query
        });
      });
    }

    // Connect exchange monitoring to alerting
    if (this.exchangeMonitor && this.alertManager) {
      this.exchangeMonitor.on('exchangeCritical', (exchangeName, issues) => {
        this.alertManager!.createAlert({
          type: 'trading',
          category: 'exchange_critical',
          level: 'critical',
          title: 'Exchange Critical Issue',
          message: `Exchange ${exchangeName} critical: ${issues.join(', ')}`,
          source: 'exchange_monitor',
          metadata: { exchangeName, issues }
        });
      });

      this.exchangeMonitor.on('rateLimitViolation', (exchangeName, rateLimits) => {
        this.alertManager!.createAlert({
          type: 'trading',
          category: 'rate_limit_violation',
          level: 'error',
          title: 'Rate Limit Violation',
          message: `Rate limit violation on ${exchangeName}: ${rateLimits.utilizationPercentage}%`,
          source: 'exchange_monitor',
          metadata: { exchangeName, rateLimits }
        });
      });
    }

    // Connect health checks to alerting
    if (this.healthCheck && this.alertManager) {
      this.healthCheck.on('healthStatusChange', (healthStatus) => {
        if (healthStatus.status === 'unhealthy') {
          this.alertManager!.createAlert({
            type: 'system',
            category: 'health_check_failed',
            level: 'critical',
            title: 'System Health Check Failed',
            message: 'System health check failed',
            source: 'health_check',
            metadata: healthStatus
          });
        }
      });
    }

    // Connect metrics collection to all services
    if (this.metricsCollector) {
      // System metrics
      if (this.systemMonitor) {
        this.systemMonitor.on('metricsUpdate', (metrics) => {
          this.metricsCollector!.recordMetric('system.cpu_usage', metrics.cpu.usage);
          this.metricsCollector!.recordMetric('system.memory_usage', metrics.memory.usage);
          this.metricsCollector!.recordMetric('system.disk_usage', metrics.disk.usage);
        });
      }

      // Application metrics
      if (this.applicationMonitor) {
        this.applicationMonitor.on('metricsUpdate', (metrics) => {
          this.metricsCollector!.recordMetric('app.error_rate', metrics.performance.errorRate);
          this.metricsCollector!.recordMetric('app.response_time', metrics.performance.responseTime.average);
          this.metricsCollector!.recordMetric('app.throughput', metrics.performance.throughput.requestsPerSecond);
        });
      }

      // Database metrics
      if (this.databaseMonitor) {
        this.databaseMonitor.on('metricsUpdate', (metrics) => {
          this.metricsCollector!.recordMetric('db.connections', metrics.connections.active);
          this.metricsCollector!.recordMetric('db.query_time', metrics.queries.averageTime);
          this.metricsCollector!.recordMetric('db.queries_per_second', metrics.queries.queriesPerSecond);
        });
      }
    }

    logger.info('Service integration setup completed');
  }

  /**
   * Get service instances for external access
   */
  public getServices() {
    return {
      system: this.systemMonitor,
      application: this.applicationMonitor,
      database: this.databaseMonitor,
      exchange: this.exchangeMonitor,
      health: this.healthCheck,
      metrics: this.metricsCollector,
      alerts: this.alertManager
    };
  }

  /**
   * Shutdown the monitoring service
   */
  public async shutdown(): Promise<void> {
    await this.stop();
    this.removeAllListeners();
    logger.info('Monitoring Service shut down completely');
  }
}

export default MonitoringService;

import { EventEmitter } from 'events';

import type { BybitExchange } from '../exchanges/bybit-exchange';

interface TP_SL_Config {
  symbol: string;
  side: 'buy' | 'sell';
  stopLossPercent: number; // e.g. 0.02 for 2%
  takeProfitPercent: number; // e.g. 0.05 for 5%
}

export class PositionMonitorService extends EventEmitter {
  private exchange: BybitExchange;
  private config: TP_SL_Config;
  private interval: NodeJS.Timeout | null = null;
  private readonly CHECK_INTERVAL = 10000; // 10 seconds

  constructor(exchange: BybitExchange, config: TP_SL_Config) {
    super();
    this.exchange = exchange;
    this.config = config;
  }

  start() {
    if (this.interval) {return;}
    this.interval = setInterval(() => this.checkPositions(), this.CHECK_INTERVAL);
    console.log('ðŸ›¡ï¸ PositionMonitorService started');
  }

  stop() {
    if (this.interval) {
      clearInterval(this.interval);
      this.interval = null;
      console.log('ðŸ›¡ï¸ PositionMonitorService stopped');
    }
  }

  async checkPositions() {
    try {
      const positions = await this.exchange.getPositions(this.config.symbol);
      const pos = positions.find(p => p.symbol === this.config.symbol && p.side === this.config.side);
      if (!pos || pos.size === 0) {
        // No open position
        return;
      }
      // Calculate TP/SL prices
      const stopLoss = pos.side === 'buy'
        ? pos.entryPrice * (1 - this.config.stopLossPercent)
        : pos.entryPrice * (1 + this.config.stopLossPercent);
      const takeProfit = pos.side === 'buy'
        ? pos.entryPrice * (1 + this.config.takeProfitPercent)
        : pos.entryPrice * (1 - this.config.takeProfitPercent);
      // Check for existing TP/SL orders (not implemented in this example)
      // Place/modify TP/SL orders as needed
      console.log(`ðŸ›¡ï¸ Monitoring ${pos.symbol} ${pos.side} position: size=${pos.size} entry=${pos.entryPrice}`);
      console.log(`   Should have SL at $${stopLoss.toFixed(2)}, TP at $${takeProfit.toFixed(2)}`);
      // TODO: Place/modify stop-loss and take-profit orders using exchange.placeOrder()
      // Emit event for monitoring
      this.emit('positionMonitored', { pos, stopLoss, takeProfit });
    } catch (error) {
      console.error('âŒ PositionMonitorService error:', error);
    }
  }
}

