/**
 * Strategy Monitor Service
 * 
 * Provides real-time monitoring of strategy performance, signals, and execution.
 * Integrates with the WebSocket system to broadcast real-time updates to the frontend.
 */

import type { SignalSummary, StrategyUpdateMessage, StrategyPerformanceMetrics, PositionSummary } from '@jabbr/shared';
import { CONSTANTS, RiskMetrics } from '@jabbr/shared';

import type { 
  IStrategy, 
  StrategyState, 
  StrategyEvent,
  StrategyResult,
  TradeSignal 
} from '../JabbrLabs/target-reacher/interfaces';
import type { JabbrWebSocketServer } from '../websocket/websocket-server';

import logger from './logging.service';


// Real-time strategy update messages

export class StrategyMonitorService {
  private wsServer: JabbrWebSocketServer;
  private activeStrategies: Map<string, IStrategy> = new Map();
  private strategyMetrics: Map<string, StrategyPerformanceMetrics> = new Map();
  private strategyStates: Map<string, StrategyState> = new Map();
  private monitoringIntervals: Map<string, NodeJS.Timeout> = new Map();
  private signalHistory: Map<string, SignalSummary[]> = new Map();
  
  // Configuration
  private readonly MONITORING_INTERVAL = 5000; // 5 seconds
  private readonly SIGNAL_HISTORY_LIMIT = 50;
  private readonly PERFORMANCE_UPDATE_INTERVAL = 10000; // 10 seconds

  constructor(wsServer: JabbrWebSocketServer) {
    this.wsServer = wsServer;
    this.setupPerformanceMonitoring();
    logger.info('Strategy Monitor Service initialized');
  }

  /**
   * Register a strategy for monitoring
   */
  public registerStrategy(botId: string, strategy: IStrategy): void {
    try {
      this.activeStrategies.set(botId, strategy);
      
      // Initialize metrics
      const metrics: StrategyPerformanceMetrics = {
        botId,
        strategyName: strategy.name,
        strategyVersion: strategy.version,
        isRunning: false,
        uptime: 0,
        totalTrades: 0,
        winningTrades: 0,
        losingTrades: 0,
        totalPnL: 0,
        dailyPnL: 0,
        winRate: 0,
        maxDrawdown: 0,
        currentDrawdown: 0,
        averageTradeTime: 0,
        currentPositions: [],
        recentSignals: [],
        riskMetrics: {
          currentDrawdown: 0,
          maxDailyLoss: 0,
          riskScore: 1,
          leverageUtilization: 0,
          exposurePercentage: 0,
          stopLossDistance: 0
        },
        timestamp: new Date()
      };
      
      this.strategyMetrics.set(botId, metrics);
      this.strategyStates.set(botId, strategy.getState());
      this.signalHistory.set(botId, []);
      
      // Start monitoring this strategy
      this.startStrategyMonitoring(botId);
      
      logger.info(`Strategy registered for monitoring: ${strategy.name} (Bot: ${botId})`);
      
      // Broadcast registration
      this.broadcastStrategyUpdate({
        type: 'state_change',
        botId,
        strategyName: strategy.name,
        data: {
          action: 'registered',
          metrics
        },
        timestamp: new Date()
      });
      
    } catch (error) {
      logger.error(`Failed to register strategy for monitoring: ${botId}`, { error });
    }
  }

  /**
   * Unregister a strategy from monitoring
   */
  public unregisterStrategy(botId: string): void {
    try {
      // Stop monitoring
      this.stopStrategyMonitoring(botId);
      
      // Get strategy info for broadcast
      const strategy = this.activeStrategies.get(botId);
      const strategyName = strategy?.name || 'Unknown';
      
      // Clean up
      this.activeStrategies.delete(botId);
      this.strategyMetrics.delete(botId);
      this.strategyStates.delete(botId);
      this.signalHistory.delete(botId);
      
      logger.info(`Strategy unregistered from monitoring: ${strategyName} (Bot: ${botId})`);
      
      // Broadcast unregistration
      this.broadcastStrategyUpdate({
        type: 'state_change',
        botId,
        strategyName,
        data: {
          action: 'unregistered'
        },
        timestamp: new Date()
      });
      
    } catch (error) {
      logger.error(`Failed to unregister strategy from monitoring: ${botId}`, { error });
    }
  }

  /**
   * Update strategy state
   */
  public updateStrategyState(botId: string, newState: Partial<StrategyState>): void {
    try {
      const currentState = this.strategyStates.get(botId);
      if (!currentState) {
        logger.warn(`Strategy state not found for bot: ${botId}`);
        return;
      }

      const updatedState = { ...currentState, ...newState, lastUpdate: new Date() };
      this.strategyStates.set(botId, updatedState);
      
      // Update metrics
      const metrics = this.strategyMetrics.get(botId);
      if (metrics) {
        metrics.isRunning = updatedState.isRunning;
        metrics.totalPnL = updatedState.totalProfit;
        metrics.totalTrades = updatedState.tradesExecuted;
        metrics.timestamp = new Date();
        
        // Update positions if available
        if (updatedState.currentPosition) {
          const position: PositionSummary = {
            symbol: 'BTCUSDT', // This should come from bot config
            side: updatedState.currentPosition.side === 'buy' ? 'long' : 'short',
            size: updatedState.currentPosition.amount,
            entryPrice: updatedState.currentPosition.entryPrice,
            currentPrice: updatedState.currentPosition.entryPrice, // This should be updated with real current price
            unrealizedPnL: updatedState.currentPosition.unrealizedPnl || 0,
            leverage: 1, // This should come from bot config
            timestamp: updatedState.currentPosition.timestamp
          };
          metrics.currentPositions = [position];
        } else {
          metrics.currentPositions = [];
        }
      }

      const strategy = this.activeStrategies.get(botId);
      
      // Broadcast state change
      this.broadcastStrategyUpdate({
        type: 'state_change',
        botId,
        strategyName: strategy?.name || 'Unknown',
        data: {
          state: updatedState,
          metrics: this.strategyMetrics.get(botId)
        },
        timestamp: new Date()
      });
      
    } catch (error) {
      logger.error(`Failed to update strategy state: ${botId}`, { error });
    }
  }

  /**
   * Record a new trading signal
   */
  public recordSignal(botId: string, signal: TradeSignal): void {
    try {
      const signalSummary: SignalSummary = {
        id: signal.id,
        symbol: signal.symbol,
        side: signal.side,
        strength: signal.confidence, // Using confidence as strength
        confidence: signal.confidence,
        price: signal.price,
        timestamp: new Date(signal.timestamp),
        executed: false,
        result: 'pending'
      };

      // Add to history
      const history = this.signalHistory.get(botId) || [];
      history.unshift(signalSummary);
      
      // Limit history size
      if (history.length > this.SIGNAL_HISTORY_LIMIT) {
        history.splice(this.SIGNAL_HISTORY_LIMIT);
      }
      
      this.signalHistory.set(botId, history);

      // Update metrics
      const metrics = this.strategyMetrics.get(botId);
      if (metrics) {
        metrics.recentSignals = history.slice(0, 10); // Keep last 10 for metrics
        metrics.lastSignalTime = new Date();
        metrics.timestamp = new Date();
      }

      const strategy = this.activeStrategies.get(botId);
      
      // Broadcast signal
      this.broadcastStrategyUpdate({
        type: 'signal',
        botId,
        strategyName: strategy?.name || 'Unknown',
        data: {
          signal: signalSummary,
          recentSignals: history.slice(0, 5) // Last 5 signals
        },
        timestamp: new Date()
      });
      
      logger.info(`Signal recorded for bot ${botId}: ${signal.side} ${signal.symbol} @ ${signal.price}`);
      
    } catch (error) {
      logger.error(`Failed to record signal for bot: ${botId}`, { error });
    }
  }

  /**
   * Record a trade execution
   */
  public recordTrade(botId: string, trade: {
    id: string;
    symbol: string;
    side: 'buy' | 'sell';
    amount: number;
    price: number;
    pnl?: number;
    fees: number;
    signalId?: string;
  }): void {
    try {
      // Update signal execution status if signal ID provided
      if (trade.signalId) {
        const history = this.signalHistory.get(botId) || [];
        const signalIndex = history.findIndex(s => s.id === trade.signalId);
        if (signalIndex !== -1 && history.at(signalIndex)) {
          const signal = history.at(signalIndex);
          if (signal) {
            signal.executed = true;
            if (trade.pnl !== undefined) {
              signal.result = trade.pnl > 0 ? 'win' : 'loss';
            }
          }
        }
      }

      // Update metrics
      const metrics = this.strategyMetrics.get(botId);
      if (metrics) {
        metrics.totalTrades += 1;
        metrics.lastTradeTime = new Date();
        
        if (trade.pnl !== undefined) {
          metrics.totalPnL += trade.pnl;
          metrics.dailyPnL += trade.pnl; // Simplified daily calculation
          
          if (trade.pnl > 0) {
            metrics.winningTrades += 1;
          } else {
            metrics.losingTrades += 1;
          }
          
          // Update win rate
          metrics.winRate = (metrics.winningTrades / metrics.totalTrades) * 100;
        }
        
        metrics.timestamp = new Date();
      }

      const strategy = this.activeStrategies.get(botId);
      
      // Broadcast trade
      this.broadcastStrategyUpdate({
        type: 'trade',
        botId,
        strategyName: strategy?.name || 'Unknown',
        data: {
          trade,
          metrics: this.strategyMetrics.get(botId)
        },
        timestamp: new Date()
      });
      
      logger.info(`Trade recorded for bot ${botId}: ${trade.side} ${trade.amount} ${trade.symbol} @ ${trade.price}`);
      
    } catch (error) {
      logger.error(`Failed to record trade for bot: ${botId}`, { error });
    }
  }

  /**
   * Get strategy performance metrics
   */
  public getStrategyMetrics(botId: string): StrategyPerformanceMetrics | undefined {
    return this.strategyMetrics.get(botId);
  }

  /**
   * Get all active strategies
   */
  public getActiveStrategies(): Array<{ botId: string; strategy: IStrategy; metrics: StrategyPerformanceMetrics }> {
    const result: Array<{ botId: string; strategy: IStrategy; metrics: StrategyPerformanceMetrics }> = [];
    
    for (const [botId, strategy] of this.activeStrategies) {
      const metrics = this.strategyMetrics.get(botId);
      if (metrics) {
        result.push({ botId, strategy, metrics });
      }
    }
    
    return result;
  }

  /**
   * Start monitoring a specific strategy
   */
  private startStrategyMonitoring(botId: string): void {
    // Stop existing monitoring if any
    this.stopStrategyMonitoring(botId);
    
    const interval = setInterval(() => {
      this.updateStrategyMonitoring(botId);
    }, this.MONITORING_INTERVAL);
    
    this.monitoringIntervals.set(botId, interval);
  }

  /**
   * Stop monitoring a specific strategy
   */
  private stopStrategyMonitoring(botId: string): void {
    const interval = this.monitoringIntervals.get(botId);
    if (interval) {
      clearInterval(interval);
      this.monitoringIntervals.delete(botId);
    }
  }

  /**
   * Update strategy monitoring data
   */
  private updateStrategyMonitoring(botId: string): void {
    try {
      const strategy = this.activeStrategies.get(botId);
      const metrics = this.strategyMetrics.get(botId);
      
      if (!strategy || !metrics) {return;}

      // Get current state from strategy
      const currentState = strategy.getState();
      this.strategyStates.set(botId, currentState);
      
      // Update uptime if running
      if (currentState.isRunning) {
        metrics.uptime += this.MONITORING_INTERVAL;
      }
      
      // Update metrics timestamp
      metrics.timestamp = new Date();
      
      // Check for risk alerts
      this.checkRiskAlerts(botId, metrics);
      
    } catch (error) {
      logger.error(`Failed to update strategy monitoring for bot: ${botId}`, { error });
    }
  }

  /**
   * Check for risk alerts
   */
  private checkRiskAlerts(botId: string, metrics: StrategyPerformanceMetrics): void {
    const alerts: string[] = [];
    
    // Check drawdown
    if (metrics.currentDrawdown > 20) { // 20% drawdown alert
      alerts.push(`High drawdown: ${metrics.currentDrawdown.toFixed(2)}%`);
    }
    
    // Check daily loss
    if (metrics.dailyPnL < -1000) { // $1000 daily loss alert
      alerts.push(`High daily loss: $${Math.abs(metrics.dailyPnL).toFixed(2)}`);
    }
    
    // Check win rate
    if (metrics.totalTrades > 10 && metrics.winRate < 30) { // Low win rate alert
      alerts.push(`Low win rate: ${metrics.winRate.toFixed(1)}%`);
    }
    
    // Broadcast alerts if any
    if (alerts.length > 0) {
      this.broadcastStrategyUpdate({
        type: 'risk_alert',
        botId,
        strategyName: metrics.strategyName,
        data: {
          alerts,
          metrics
        },
        timestamp: new Date()
      });
    }
  }

  /**
   * Setup periodic performance monitoring
   */
  private setupPerformanceMonitoring(): void {
    setInterval(() => {
      for (const [botId] of this.activeStrategies) {
        const metrics = this.strategyMetrics.get(botId);
        if (metrics) {
          this.broadcastStrategyUpdate({
            type: 'performance',
            botId,
            strategyName: metrics.strategyName,
            data: {
              metrics
            },
            timestamp: new Date()
          });
        }
      }
    }, this.PERFORMANCE_UPDATE_INTERVAL);
  }

  /**
   * Broadcast strategy update via WebSocket
   */
  private broadcastStrategyUpdate(update: StrategyUpdateMessage): void {
    try {
      this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.SIGNALS, {
        type: 'data',
        data: update,
        timestamp: new Date().toISOString()
      });
      
      // Also broadcast to bot status channel for general bot updates
      if (update.type === 'performance' || update.type === 'state_change') {
        this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.BOT_STATUS, {
          type: 'data',
          data: {
            botId: update.botId,
            strategy: update.data.metrics || update.data.state,
            timestamp: update.timestamp
          },
          timestamp: new Date().toISOString()
        });
      }
      
    } catch (error) {
      logger.error('Failed to broadcast strategy update', { error, update });
    }
  }

  /**
   * Get strategy summary for dashboard
   */
  public getStrategySummary(): {
    totalStrategies: number;
    runningStrategies: number;
    totalPnL: number;
    dailyPnL: number;
    totalTrades: number;
    averageWinRate: number;
  } {
    const allMetrics = Array.from(this.strategyMetrics.values());
    
    return {
      totalStrategies: allMetrics.length,
      runningStrategies: allMetrics.filter(m => m.isRunning).length,
      totalPnL: allMetrics.reduce((sum, m) => sum + m.totalPnL, 0),
      dailyPnL: allMetrics.reduce((sum, m) => sum + m.dailyPnL, 0),
      totalTrades: allMetrics.reduce((sum, m) => sum + m.totalTrades, 0),
      averageWinRate: allMetrics.length > 0 
        ? allMetrics.reduce((sum, m) => sum + m.winRate, 0) / allMetrics.length 
        : 0
    };
  }

  /**
   * Cleanup on shutdown
   */
  public shutdown(): void {
    // Stop all monitoring intervals
    for (const interval of this.monitoringIntervals.values()) {
      clearInterval(interval);
    }
    
    this.monitoringIntervals.clear();
    this.activeStrategies.clear();
    this.strategyMetrics.clear();
    this.strategyStates.clear();
    this.signalHistory.clear();
    
    logger.info('Strategy Monitor Service shut down');
  }
}

export default StrategyMonitorService;

/**
 * System Health Check Service
 * 
 * Provides comprehensive health monitoring for all system components
 * including database, exchange connections, bot runtime status,
 * memory usage, and overall system health.
 */

import { EventEmitter } from 'events';

import { database } from '../services/database.service';
import logger from '../services/logging.service';

import SystemMonitorService from './system-monitor.service';

export interface HealthStatus {
  status: 'healthy' | 'degraded' | 'unhealthy';
  timestamp: Date;
  uptime: number;
  version: string;
  components: {
    database: ComponentHealth;
    memory: ComponentHealth;
    cpu: ComponentHealth;
    bots: ComponentHealth;
    exchanges: ComponentHealth;
    websockets: ComponentHealth;
    logging: ComponentHealth;
  };
  metrics: {
    totalRequests: number;
    activeConnections: number;
    errorRate: number;
    responseTime: number;
  };
  details?: string;
}

export interface ComponentHealth {
  status: 'healthy' | 'degraded' | 'unhealthy';
  message: string;
  lastCheck: Date;
  responseTime?: number;
  details?: Record<string, any>;
}

export interface HealthCheckConfig {
  enableDetailedChecks: boolean;
  checkInterval: number; // milliseconds
  timeoutThreshold: number; // milliseconds for checks
  memoryThreshold: number; // percentage
  cpuThreshold: number; // percentage
  errorRateThreshold: number; // percentage
  responseTimeThreshold: number; // milliseconds
}

export class SystemHealthService extends EventEmitter {
  private config: HealthCheckConfig;
  private startTime: Date;
  private healthCache: HealthStatus | null = null;
  private cacheExpiry = 0;
  private checkInterval: NodeJS.Timeout | null = null;
  private systemMonitor: SystemMonitorService;
  private metrics = {
    totalRequests: 0,
    totalErrors: 0,
    responseTimeSum: 0,
    requestCount: 0,
    activeConnections: 0
  };

  constructor(config: Partial<HealthCheckConfig> = {}) {
    super();
    
    this.config = {
      enableDetailedChecks: true,
      checkInterval: 30000, // 30 seconds
      timeoutThreshold: 5000, // 5 seconds
      memoryThreshold: 85, // 85%
      cpuThreshold: 90, // 90%
      errorRateThreshold: 5, // 5%
      responseTimeThreshold: 1000, // 1 second
      ...config
    };

    this.startTime = new Date();
    this.systemMonitor = new SystemMonitorService({
      collectInterval: 5000, // 5 seconds
      alertThresholds: {
        cpu: { warning: this.config.cpuThreshold - 20, critical: this.config.cpuThreshold },
        memory: { warning: this.config.memoryThreshold - 15, critical: this.config.memoryThreshold },
        disk: { warning: 80, critical: 90 }
      },
      enableAlerts: true
    });

    // Start system monitoring
    this.systemMonitor.start();
    this.startPeriodicChecks();
  }

  /**
   * Get overall system health status
   */
  async getSystemHealth(useCache = true): Promise<HealthStatus> {
    // Return cached result if still valid
    if (useCache && this.healthCache && Date.now() < this.cacheExpiry) {
      return this.healthCache;
    }

    const startTime = Date.now();
    
    try {
      logger.debug('[HealthCheck] Starting system health assessment');

      // Perform all health checks in parallel
      const [
        databaseHealth,
        memoryHealth,
        cpuHealth,
        botsHealth,
        exchangesHealth,
        websocketsHealth,
        loggingHealth
      ] = await Promise.allSettled([
        this.checkDatabaseHealth(),
        this.checkMemoryHealth(),
        this.checkCpuHealth(),
        this.checkBotsHealth(),
        this.checkExchangesHealth(),
        this.checkWebsocketsHealth(),
        this.checkLoggingHealth()
      ]);

      // Extract results, defaulting to unhealthy if promise rejected
      const components = {
        database: this.extractHealthResult(databaseHealth, 'Database check failed'),
        memory: this.extractHealthResult(memoryHealth, 'Memory check failed'),
        cpu: this.extractHealthResult(cpuHealth, 'CPU check failed'),
        bots: this.extractHealthResult(botsHealth, 'Bots check failed'),
        exchanges: this.extractHealthResult(exchangesHealth, 'Exchanges check failed'),
        websockets: this.extractHealthResult(websocketsHealth, 'WebSockets check failed'),
        logging: this.extractHealthResult(loggingHealth, 'Logging check failed')
      };

      // Calculate overall health status
      const overallStatus = this.calculateOverallStatus(components);
      const checkDuration = Date.now() - startTime;

      const healthStatus: HealthStatus = {
        status: overallStatus,
        timestamp: new Date(),
        uptime: Date.now() - this.startTime.getTime(),
        version: process.env.npm_package_version || '1.0.0',
        components,
        metrics: {
          totalRequests: this.metrics.totalRequests,
          activeConnections: this.metrics.activeConnections,
          errorRate: this.calculateErrorRate(),
          responseTime: this.calculateAverageResponseTime()
        },
        details: overallStatus !== 'healthy' ? this.getUnhealthyDetails(components) : undefined
      };

      // Cache the result for 10 seconds
      this.healthCache = healthStatus;
      this.cacheExpiry = Date.now() + 10000;

      logger.debug('[HealthCheck] Health assessment completed', {
        status: overallStatus,
        duration: checkDuration,
        componentsChecked: Object.keys(components).length
      });

      this.emit('health-check-completed', healthStatus);

      return healthStatus;
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      
      logger.error('[HealthCheck] Health assessment failed', {
        error: errorMessage,
        duration: Date.now() - startTime
      });

      const unhealthyStatus: HealthStatus = {
        status: 'unhealthy',
        timestamp: new Date(),
        uptime: Date.now() - this.startTime.getTime(),
        version: process.env.npm_package_version || '1.0.0',
        components: {
          database: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() },
          memory: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() },
          cpu: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() },
          bots: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() },
          exchanges: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() },
          websockets: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() },
          logging: { status: 'unhealthy', message: 'Check failed', lastCheck: new Date() }
        },
        metrics: {
          totalRequests: this.metrics.totalRequests,
          activeConnections: this.metrics.activeConnections,
          errorRate: 100,
          responseTime: -1
        },
        details: `Health check system failure: ${errorMessage}`
      };

      this.emit('health-check-failed', { error: errorMessage, status: unhealthyStatus });

      return unhealthyStatus;
    }
  }

  /**
   * Check database connectivity and performance
   */
  private async checkDatabaseHealth(): Promise<ComponentHealth> {
    const startTime = Date.now();
    
    try {
      // Test basic connectivity
      const result = await Promise.race([
        database.query('SELECT 1 as health_check'),
        new Promise((_, reject) => 
          setTimeout(() => reject(new Error('Database timeout')), this.config.timeoutThreshold)
        )
      ]) as any[];

      if (!result || result.length === 0 || result.at(0).health_check !== 1) {
        throw new Error('Invalid database response');
      }

      // Get additional database metrics if detailed checks enabled
      let details = {};
      if (this.config.enableDetailedChecks) {
        try {
          const [connectionInfo, tableInfo] = await Promise.all([
            database.query('SELECT COUNT(*) as active_connections FROM pg_stat_activity WHERE state = $1', ['active']),
            database.query('SELECT schemaname, tablename FROM pg_tables WHERE schemaname = $1 LIMIT 5', ['public'])
          ]);

          details = {
            activeConnections: connectionInfo.at(0)?.active_connections || 0,
            tablesCount: tableInfo.length,
            tables: tableInfo.map(t => `${t.schemaname}.${t.tablename}`)
          };
        } catch (detailError) {
          details = { detailError: 'Could not fetch detailed metrics' };
        }
      }

      const responseTime = Date.now() - startTime;

      return {
        status: responseTime > this.config.responseTimeThreshold ? 'degraded' : 'healthy',
        message: `Database responsive (${responseTime}ms)`,
        lastCheck: new Date(),
        responseTime,
        details
      };
    } catch (error) {
      const responseTime = Date.now() - startTime;
      const errorMessage = error instanceof Error ? error.message : String(error);

      return {
        status: 'unhealthy',
        message: `Database error: ${errorMessage}`,
        lastCheck: new Date(),
        responseTime,
        details: { error: errorMessage }
      };
    }
  }

  /**
   * Check memory usage
   */
  private async checkMemoryHealth(): Promise<ComponentHealth> {
    try {
      // Get system memory metrics from SystemMonitorService
      const systemMetrics = this.systemMonitor.getCurrentMetrics();
      const processMemory = process.memoryUsage();

      if (!systemMetrics) {
        return {
          status: 'degraded',
          message: 'Memory metrics unavailable',
          lastCheck: new Date(),
          details: { reason: 'System monitor not ready' }
        };
      }

      const systemMemoryPercentage = systemMetrics.memory.percentage;
      const processHeapPercentage = (processMemory.heapUsed / processMemory.heapTotal) * 100;

      const details = {
        system: {
          total: Math.round(systemMetrics.memory.total / 1024 / 1024 / 1024 * 100) / 100, // GB
          used: Math.round(systemMetrics.memory.used / 1024 / 1024 / 1024 * 100) / 100, // GB
          percentage: Math.round(systemMemoryPercentage * 100) / 100
        },
        process: {
          heapUsed: Math.round(processMemory.heapUsed / 1024 / 1024), // MB
          heapTotal: Math.round(processMemory.heapTotal / 1024 / 1024), // MB
          external: Math.round(processMemory.external / 1024 / 1024), // MB
          rss: Math.round(processMemory.rss / 1024 / 1024), // MB
          heapPercentage: Math.round(processHeapPercentage * 100) / 100
        }
      };

      let status: ComponentHealth['status'];
      let message: string;

      // Use system memory percentage as primary indicator
      if (systemMemoryPercentage > this.config.memoryThreshold) {
        status = 'unhealthy';
        message = `Critical system memory usage: ${details.system.percentage}%`;
      } else if (systemMemoryPercentage > this.config.memoryThreshold * 0.8) {
        status = 'degraded';
        message = `High system memory usage: ${details.system.percentage}%`;
      } else if (processHeapPercentage > 90) {
        status = 'degraded';
        message = `High process heap usage: ${details.process.heapPercentage}%`;
      } else {
        status = 'healthy';
        message = `Memory usage normal: ${details.system.percentage}% system, ${details.process.heapPercentage}% heap`;
      }

      return {
        status,
        message,
        lastCheck: new Date(),
        details
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        message: `Memory check failed: ${error instanceof Error ? error.message : String(error)}`,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Check CPU usage
   */
  private async checkCpuHealth(): Promise<ComponentHealth> {
    try {
      // Get CPU metrics from SystemMonitorService
      const systemMetrics = this.systemMonitor.getCurrentMetrics();
      const processCpuUsage = process.cpuUsage();
      const uptime = process.uptime();

      if (!systemMetrics) {
        return {
          status: 'degraded',
          message: 'CPU metrics unavailable',
          lastCheck: new Date(),
          details: { reason: 'System monitor not ready' }
        };
      }

      const systemCpuPercentage = systemMetrics.cpu.usage;
      const loadAverage = systemMetrics.cpu.loadAverage;
      
      const details = {
        system: {
          usage: Math.round(systemCpuPercentage * 100) / 100,
          cores: systemMetrics.cpu.cores,
          loadAverage: loadAverage.map(avg => Math.round(avg * 100) / 100)
        },
        process: {
          user: Math.round(processCpuUsage.user / 1000000), // seconds
          system: Math.round(processCpuUsage.system / 1000000), // seconds
          uptime: Math.round(uptime)
        }
      };

      let status: ComponentHealth['status'];
      let message: string;

      // Use system CPU usage as primary indicator
      if (systemCpuPercentage > this.config.cpuThreshold) {
        status = 'unhealthy';
        message = `Critical CPU usage: ${details.system.usage}%`;
      } else if (systemCpuPercentage > this.config.cpuThreshold * 0.8) {
        status = 'degraded';
        message = `High CPU usage: ${details.system.usage}%`;
      } else if (loadAverage && loadAverage.length > 0 && (loadAverage.at(0) || 0) > (systemMetrics?.cpu.cores || 1) * 0.8) {
        status = 'degraded';
        message = `High system load: ${details.system.loadAverage.at(0) || 0} (${systemMetrics?.cpu.cores || 1} cores)`;
      } else {
        status = 'healthy';
        message = `CPU usage normal: ${details.system.usage}%, load: ${details.system.loadAverage.at(0) || 'N/A'}`;
      }

      return {
        status,
        message,
        lastCheck: new Date(),
        details
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        message: `CPU check failed: ${error instanceof Error ? error.message : String(error)}`,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Check bot runtime health
   */
  private async checkBotsHealth(): Promise<ComponentHealth> {
    try {
      // Query bot status from database
      const botsResult = await database.query(`
        SELECT 
          status, 
          COUNT(*) as count 
        FROM bots 
        GROUP BY status
      `);

      const botCounts = botsResult.reduce((acc: Record<string, number>, row: any) => {
        acc[row.status] = parseInt(row.count);
        return acc;
      }, {});

      const totalBots = Object.values(botCounts).reduce((sum: number, count: number) => sum + count, 0);
      const runningBots = botCounts.running || 0;
      const errorBots = botCounts.error || 0;

      const details = {
        total: totalBots,
        running: runningBots,
        stopped: botCounts.stopped || 0,
        paused: botCounts.paused || 0,
        error: errorBots,
        distribution: botCounts
      };

      let status: ComponentHealth['status'];
      let message: string;

      if (totalBots === 0) {
        status = 'healthy';
        message = 'No bots configured';
      } else if (errorBots > 0) {
        status = 'degraded';
        message = `${errorBots} bots in error state`;
      } else if (runningBots === 0) {
        status = 'degraded';
        message = 'No bots currently running';
      } else {
        status = 'healthy';
        message = `${runningBots}/${totalBots} bots running`;
      }

      return {
        status,
        message,
        lastCheck: new Date(),
        details
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        message: `Bots check failed: ${error instanceof Error ? error.message : String(error)}`,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Check exchange connectivity
   */
  private async checkExchangesHealth(): Promise<ComponentHealth> {
    try {
      // This would integrate with actual exchange services
      // For now, we'll simulate the check
      const details = {
        binance: 'connected',
        bybit: 'connected',
        lastPing: new Date().toISOString()
      };

      return {
        status: 'healthy',
        message: 'Exchange connections healthy',
        lastCheck: new Date(),
        details
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        message: `Exchange check failed: ${error instanceof Error ? error.message : String(error)}`,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Check WebSocket connections
   */
  private async checkWebsocketsHealth(): Promise<ComponentHealth> {
    try {
      // This would integrate with actual WebSocket manager
      // For now, we'll simulate the check
      const details = {
        activeConnections: this.metrics.activeConnections,
        totalConnections: this.metrics.totalRequests
      };

      return {
        status: 'healthy',
        message: `WebSocket connections healthy (${details.activeConnections} active)`,
        lastCheck: new Date(),
        details
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        message: `WebSocket check failed: ${error instanceof Error ? error.message : String(error)}`,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Check logging system
   */
  private async checkLoggingHealth(): Promise<ComponentHealth> {
    try {
      // Test logging system
      const testMessage = `Health check test - ${Date.now()}`;
      logger.debug(testMessage);

      return {
        status: 'healthy',
        message: 'Logging system operational',
        lastCheck: new Date()
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        message: `Logging check failed: ${error instanceof Error ? error.message : String(error)}`,
        lastCheck: new Date()
      };
    }
  }

  /**
   * Extract health result from promise result
   */
  private extractHealthResult(
    result: PromiseSettledResult<ComponentHealth>,
    defaultError: string
  ): ComponentHealth {
    if (result.status === 'fulfilled') {
      return result.value;
    } 
      return {
        status: 'unhealthy',
        message: defaultError,
        lastCheck: new Date(),
        details: { error: result.reason?.message || result.reason }
      };
    
  }

  /**
   * Calculate overall system status
   */
  private calculateOverallStatus(components: HealthStatus['components']): HealthStatus['status'] {
    const statuses = Object.values(components).map(c => c.status);
    
    if (statuses.some(s => s === 'unhealthy')) {
      return 'unhealthy';
    } else if (statuses.some(s => s === 'degraded')) {
      return 'degraded';
    } 
      return 'healthy';
    
  }

  /**
   * Get details about unhealthy components
   */
  private getUnhealthyDetails(components: HealthStatus['components']): string {
    const unhealthyComponents = Object.entries(components)
      .filter(([_, health]) => health.status !== 'healthy')
      .map(([name, health]) => `${name}: ${health.message}`)
      .join('; ');

    return unhealthyComponents || 'Unknown health issues detected';
  }

  /**
   * Calculate error rate
   */
  private calculateErrorRate(): number {
    if (this.metrics.totalRequests === 0) {return 0;}
    return (this.metrics.totalErrors / this.metrics.totalRequests) * 100;
  }

  /**
   * Calculate average response time
   */
  private calculateAverageResponseTime(): number {
    if (this.metrics.requestCount === 0) {return 0;}
    return this.metrics.responseTimeSum / this.metrics.requestCount;
  }

  /**
   * Record request metrics
   */
  recordRequest(responseTime: number, isError = false): void {
    this.metrics.totalRequests++;
    this.metrics.requestCount++;
    this.metrics.responseTimeSum += responseTime;
    
    if (isError) {
      this.metrics.totalErrors++;
    }
  }

  /**
   * Update active connections count
   */
  updateActiveConnections(count: number): void {
    this.metrics.activeConnections = count;
  }

  /**
   * Start periodic health checks
   */
  private startPeriodicChecks(): void {
    if (this.checkInterval) {
      clearInterval(this.checkInterval);
    }

    this.checkInterval = setInterval(async () => {
      try {
        const health = await this.getSystemHealth(false); // Force fresh check
        
        if (health.status !== 'healthy') {
          logger.warn('[HealthCheck] System health degraded', {
            status: health.status,
            details: health.details
          });
          
          this.emit('health-degraded', health);
        }
      } catch (error) {
        logger.error('[HealthCheck] Periodic health check failed', {
          error: error instanceof Error ? error.message : String(error)
        });
      }
    }, this.config.checkInterval);
  }

  /**
   * Get simplified health status for quick checks
   */
  async getQuickHealth(): Promise<{ status: string; uptime: number }> {
    try {
      // Just check if we can connect to database
      await database.query('SELECT 1');
      
      return {
        status: 'healthy',
        uptime: Date.now() - this.startTime.getTime()
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        uptime: Date.now() - this.startTime.getTime()
      };
    }
  }

  /**
   * Cleanup and shutdown
   */
  shutdown(): void {
    if (this.checkInterval) {
      clearInterval(this.checkInterval);
      this.checkInterval = null;
    }

    // Stop system monitoring
    if (this.systemMonitor) {
      this.systemMonitor.shutdown();
    }

    this.removeAllListeners();
    this.healthCache = null;
    
    logger.info('[HealthCheck] System health service shutdown complete');
  }
}

export default SystemHealthService;

/**
 * System Monitor Service
 * 
 * Monitors system resources including CPU, memory, disk usage, and process health.
 * Provides real-time metrics and alerts for system performance issues.
 */

import { EventEmitter } from 'events';
import * as fs from 'fs/promises';
import * as os from 'os';
import * as path from 'path';

import logger from './logging.service';

export interface SystemMetrics {
  cpu: {
    usage: number; // percentage
    loadAverage: number[];
    cores: number;
  };
  memory: {
    total: number; // bytes
    used: number; // bytes
    free: number; // bytes
    percentage: number;
  };
  disk: {
    total: number; // bytes
    used: number; // bytes
    free: number; // bytes
    percentage: number;
  };
  process: {
    pid: number;
    uptime: number; // seconds
    memoryUsage: NodeJS.MemoryUsage;
    cpuUsage: NodeJS.CpuUsage;
  };
  network: {
    interfaces: NetworkInterface[];
  };
  timestamp: Date;
}

export interface NetworkInterface {
  name: string;
  address: string;
  family: string;
  internal: boolean;
  mac: string;
}

export interface SystemAlert {
  type: 'cpu' | 'memory' | 'disk' | 'process';
  level: 'warning' | 'critical';
  message: string;
  value: number;
  threshold: number;
  timestamp: Date;
}

export interface SystemMonitorConfig {
  collectInterval: number; // milliseconds
  alertThresholds: {
    cpu: { warning: number; critical: number };
    memory: { warning: number; critical: number };
    disk: { warning: number; critical: number };
  };
  enableAlerts: boolean;
  retentionPeriod: number; // milliseconds
}

export class SystemMonitorService extends EventEmitter {
  private config: SystemMonitorConfig;
  private metrics: SystemMetrics[] = [];
  private isRunning = false;
  private monitorInterval?: NodeJS.Timeout;
  private previousCpuUsage?: NodeJS.CpuUsage;
  private startTime: Date;

  constructor(config: Partial<SystemMonitorConfig> = {}) {
    super();
    
    this.config = {
      collectInterval: 5000, // 5 seconds
      alertThresholds: {
        cpu: { warning: 70, critical: 90 },
        memory: { warning: 80, critical: 95 },
        disk: { warning: 85, critical: 95 }
      },
      enableAlerts: true,
      retentionPeriod: 24 * 60 * 60 * 1000, // 24 hours
      ...config
    };

    this.startTime = new Date();
    logger.info('System Monitor Service initialized', { config: this.config });
  }

  /**
   * Start system monitoring
   */
  public start(): void {
    if (this.isRunning) {
      logger.warn('System Monitor is already running');
      return;
    }

    this.isRunning = true;
    this.previousCpuUsage = process.cpuUsage();
    
    // Collect initial metrics
    this.collectMetrics();
    
    // Start periodic collection
    this.monitorInterval = setInterval(() => {
      this.collectMetrics();
    }, this.config.collectInterval);

    // Start cleanup routine (every hour)
    setInterval(() => {
      this.cleanupOldMetrics();
    }, 60 * 60 * 1000);

    logger.info('System Monitor started', {
      interval: this.config.collectInterval,
      alertsEnabled: this.config.enableAlerts
    });

    this.emit('started');
  }

  /**
   * Stop system monitoring
   */
  public stop(): void {
    if (!this.isRunning) {
      return;
    }

    this.isRunning = false;
    
    if (this.monitorInterval) {
      clearInterval(this.monitorInterval);
      this.monitorInterval = undefined;
    }

    logger.info('System Monitor stopped');
    this.emit('stopped');
  }

  /**
   * Get current system metrics
   */
  public getCurrentMetrics(): SystemMetrics | null {
    return this.metrics.length > 0 ? (this.metrics[this.metrics.length - 1] || null) : null;
  }

  /**
   * Get metrics history
   */
  public getMetricsHistory(duration?: number): SystemMetrics[] {
    if (!duration) {
      return [...this.metrics];
    }

    const cutoff = new Date(Date.now() - duration);
    return this.metrics.filter(m => m.timestamp >= cutoff);
  }

  /**
   * Get system health summary
   */
  public getHealthSummary(): {
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
    uptime: number;
    lastCheck: Date | null;
  } {
    const current = this.getCurrentMetrics();
    
    if (!current) {
      return {
        status: 'critical',
        issues: ['No metrics available'],
        uptime: 0,
        lastCheck: null
      };
    }

    const issues: string[] = [];
    let status: 'healthy' | 'warning' | 'critical' = 'healthy';

    // Check CPU
    if (current.cpu.usage >= this.config.alertThresholds.cpu.critical) {
      issues.push(`Critical CPU usage: ${current.cpu.usage.toFixed(1)}%`);
      status = 'critical';
    } else if (current.cpu.usage >= this.config.alertThresholds.cpu.warning) {
      issues.push(`High CPU usage: ${current.cpu.usage.toFixed(1)}%`);
      status = 'warning';
    }

    // Check Memory
    if (current.memory.percentage >= this.config.alertThresholds.memory.critical) {
      issues.push(`Critical memory usage: ${current.memory.percentage.toFixed(1)}%`);
      status = 'critical';
    } else if (current.memory.percentage >= this.config.alertThresholds.memory.warning) {
      issues.push(`High memory usage: ${current.memory.percentage.toFixed(1)}%`);
      if (status !== 'critical') {status = 'warning';}
    }

    // Check Disk
    if (current.disk.percentage >= this.config.alertThresholds.disk.critical) {
      issues.push(`Critical disk usage: ${current.disk.percentage.toFixed(1)}%`);
      status = 'critical';
    } else if (current.disk.percentage >= this.config.alertThresholds.disk.warning) {
      issues.push(`High disk usage: ${current.disk.percentage.toFixed(1)}%`);
      if (status !== 'critical') {status = 'warning';}
    }

    return {
      status,
      issues,
      uptime: current.process.uptime,
      lastCheck: current.timestamp
    };
  }

  /**
   * Collect system metrics
   */
  private async collectMetrics(): Promise<void> {
    try {
      const metrics: SystemMetrics = {
        cpu: await this.getCpuMetrics(),
        memory: this.getMemoryMetrics(),
        disk: await this.getDiskMetrics(),
        process: this.getProcessMetrics(),
        network: this.getNetworkMetrics(),
        timestamp: new Date()
      };

      this.metrics.push(metrics);
      
      // Check for alerts
      if (this.config.enableAlerts) {
        this.checkAlerts(metrics);
      }

      // Emit metrics event
      this.emit('metrics', metrics);

      logger.debug('System metrics collected', {
        cpu: metrics.cpu.usage,
        memory: metrics.memory.percentage,
        disk: metrics.disk.percentage
      });

    } catch (error) {
      logger.error('Failed to collect system metrics', { error });
      this.emit('error', error);
    }
  }

  /**
   * Get CPU metrics
   */
  private async getCpuMetrics(): Promise<SystemMetrics['cpu']> {
    const cpus = os.cpus();
    const loadAverage = os.loadavg();
    
    // Calculate CPU usage using process.cpuUsage()
    const currentUsage = process.cpuUsage(this.previousCpuUsage);
    this.previousCpuUsage = process.cpuUsage();
    
    // Calculate percentage (approximation)
    const totalUsage = currentUsage.user + currentUsage.system;
    const intervalMs = this.config.collectInterval;
    const intervalMicroseconds = intervalMs * 1000;
    const usage = Math.min((totalUsage / intervalMicroseconds) * 100, 100);

    return {
      usage: Math.max(0, usage), // Ensure non-negative
      loadAverage,
      cores: cpus.length
    };
  }

  /**
   * Get memory metrics
   */
  private getMemoryMetrics(): SystemMetrics['memory'] {
    const total = os.totalmem();
    const free = os.freemem();
    const used = total - free;
    const percentage = (used / total) * 100;

    return {
      total,
      used,
      free,
      percentage
    };
  }

  /**
   * Get disk metrics
   */
  private async getDiskMetrics(): Promise<SystemMetrics['disk']> {
    try {
      // For cross-platform compatibility, we'll use a simple approach
      // In production, you might want to use a more sophisticated disk monitoring library
      const stats = await fs.stat(process.cwd());
      
      // Simplified disk metrics (this is a basic implementation)
      // In a real scenario, you'd use platform-specific tools or libraries
      return {
        total: 100 * 1024 * 1024 * 1024, // 100GB placeholder
        used: 50 * 1024 * 1024 * 1024,   // 50GB placeholder
        free: 50 * 1024 * 1024 * 1024,   // 50GB placeholder
        percentage: 50 // 50% placeholder
      };
    } catch (error) {
      logger.warn('Failed to get disk metrics, using defaults', { error });
      return {
        total: 0,
        used: 0,
        free: 0,
        percentage: 0
      };
    }
  }

  /**
   * Get process metrics
   */
  private getProcessMetrics(): SystemMetrics['process'] {
    return {
      pid: process.pid,
      uptime: process.uptime(),
      memoryUsage: process.memoryUsage(),
      cpuUsage: process.cpuUsage()
    };
  }

  /**
   * Get network metrics
   */
  private getNetworkMetrics(): SystemMetrics['network'] {
    const interfaces = os.networkInterfaces();
    const result: NetworkInterface[] = [];

    for (const [name, addresses] of Object.entries(interfaces)) {
      if (addresses) {
        for (const addr of addresses) {
          result.push({
            name,
            address: addr.address,
            family: addr.family,
            internal: addr.internal,
            mac: addr.mac
          });
        }
      }
    }

    return { interfaces: result };
  }

  /**
   * Check for alerts based on thresholds
   */
  private checkAlerts(metrics: SystemMetrics): void {
    const alerts: SystemAlert[] = [];

    // CPU alerts
    if (metrics.cpu.usage >= this.config.alertThresholds.cpu.critical) {
      alerts.push({
        type: 'cpu',
        level: 'critical',
        message: `Critical CPU usage: ${metrics.cpu.usage.toFixed(1)}%`,
        value: metrics.cpu.usage,
        threshold: this.config.alertThresholds.cpu.critical,
        timestamp: metrics.timestamp
      });
    } else if (metrics.cpu.usage >= this.config.alertThresholds.cpu.warning) {
      alerts.push({
        type: 'cpu',
        level: 'warning',
        message: `High CPU usage: ${metrics.cpu.usage.toFixed(1)}%`,
        value: metrics.cpu.usage,
        threshold: this.config.alertThresholds.cpu.warning,
        timestamp: metrics.timestamp
      });
    }

    // Memory alerts
    if (metrics.memory.percentage >= this.config.alertThresholds.memory.critical) {
      alerts.push({
        type: 'memory',
        level: 'critical',
        message: `Critical memory usage: ${metrics.memory.percentage.toFixed(1)}%`,
        value: metrics.memory.percentage,
        threshold: this.config.alertThresholds.memory.critical,
        timestamp: metrics.timestamp
      });
    } else if (metrics.memory.percentage >= this.config.alertThresholds.memory.warning) {
      alerts.push({
        type: 'memory',
        level: 'warning',
        message: `High memory usage: ${metrics.memory.percentage.toFixed(1)}%`,
        value: metrics.memory.percentage,
        threshold: this.config.alertThresholds.memory.warning,
        timestamp: metrics.timestamp
      });
    }

    // Disk alerts
    if (metrics.disk.percentage >= this.config.alertThresholds.disk.critical) {
      alerts.push({
        type: 'disk',
        level: 'critical',
        message: `Critical disk usage: ${metrics.disk.percentage.toFixed(1)}%`,
        value: metrics.disk.percentage,
        threshold: this.config.alertThresholds.disk.critical,
        timestamp: metrics.timestamp
      });
    } else if (metrics.disk.percentage >= this.config.alertThresholds.disk.warning) {
      alerts.push({
        type: 'disk',
        level: 'warning',
        message: `High disk usage: ${metrics.disk.percentage.toFixed(1)}%`,
        value: metrics.disk.percentage,
        threshold: this.config.alertThresholds.disk.warning,
        timestamp: metrics.timestamp
      });
    }

    // Emit alerts
    for (const alert of alerts) {
      this.emit('alert', alert);
      logger.warn('System alert triggered', alert);
    }
  }

  /**
   * Clean up old metrics to prevent memory leaks
   */
  private cleanupOldMetrics(): void {
    const cutoff = new Date(Date.now() - this.config.retentionPeriod);
    const initialCount = this.metrics.length;
    
    this.metrics = this.metrics.filter(m => m.timestamp >= cutoff);
    
    const removed = initialCount - this.metrics.length;
    if (removed > 0) {
      logger.debug(`Cleaned up ${removed} old system metrics`);
    }
  }

  /**
   * Get monitoring statistics
   */
  public getStats(): {
    isRunning: boolean;
    uptime: number;
    metricsCollected: number;
    collectInterval: number;
    memoryUsage: number;
  } {
    return {
      isRunning: this.isRunning,
      uptime: Date.now() - this.startTime.getTime(),
      metricsCollected: this.metrics.length,
      collectInterval: this.config.collectInterval,
      memoryUsage: process.memoryUsage().heapUsed
    };
  }

  /**
   * Shutdown the monitor
   */
  public shutdown(): void {
    this.stop();
    this.metrics = [];
    this.removeAllListeners();
    logger.info('System Monitor shut down');
  }
}

export default SystemMonitorService;

import { EventEmitter } from 'events';

import type { TimeSyncMessage } from '@jabbr/shared';
import ntp from 'ntp-client';
import { z } from 'zod';

/**
 * Time synchronization configuration schema
 */
const TimeSyncConfigSchema = z.object({
  NTP_SERVER: z.string().default('pool.ntp.org'),
  TIME_SYNC_INTERVAL: z.coerce.number().min(30000).default(300000), // 5 minutes
  MAX_TIME_DRIFT: z.coerce.number().min(100).default(30000), // 30 seconds (increased for tolerance)
  NTP_TIMEOUT: z.coerce.number().min(1000).default(10000), // 10 seconds
  ENABLE_TIME_SYNC: z.coerce.boolean().default(true)
});

type TimeSyncConfig = z.infer<typeof TimeSyncConfigSchema>;

/**
 * Time synchronization statistics
 */
interface TimeSyncStats {
  lastNtpSync: Date | null;
  lastExchangeSync: Date | null;
  ntpOffset: number; // milliseconds
  exchangeOffset: number; // milliseconds
  totalDrift: number; // milliseconds
  syncCount: number;
  errorCount: number;
  isHealthy: boolean;
  uptime: number; // seconds
}

/**
 * Exchange time information
 */
interface ExchangeTimeInfo {
  exchange: string;
  serverTime: Date;
  localTime: Date;
  offset: number; // milliseconds
  lastSync: Date;
}

/**
 * Time Synchronization Service
 * Handles NTP synchronization, exchange time alignment, and drift detection
 */
export class TimeSyncService extends EventEmitter {
  private config: TimeSyncConfig;
  private ntpSyncInterval: NodeJS.Timeout | null = null;
  private exchangeSyncInterval: NodeJS.Timeout | null = null;
  private isRunning = false;
  private startTime: Date;

  // Time synchronization state
  private ntpOffset = 0; // milliseconds
  private exchangeOffsets: Map<string, number> = new Map(); // exchange -> offset in ms
  private lastNtpSync: Date | null = null;
  private lastExchangeSync: Date | null = null;
  private syncCount = 0;
  private errorCount = 0;

  // Exchange time tracking
  private exchangeTimeInfo: Map<string, ExchangeTimeInfo> = new Map();

  constructor() {
    super();
    this.startTime = new Date();
    
    // Validate and parse configuration
    this.config = TimeSyncConfigSchema.parse({
      NTP_SERVER: process.env.NTP_SERVER || 'pool.ntp.org',
      TIME_SYNC_INTERVAL: process.env.TIME_SYNC_INTERVAL || '300000',
      MAX_TIME_DRIFT: process.env.MAX_TIME_DRIFT || '30000',
      NTP_TIMEOUT: process.env.NTP_TIMEOUT || '10000',
      ENABLE_TIME_SYNC: process.env.ENABLE_TIME_SYNC || 'true'
    });

    console.log('ðŸ• Time Synchronization Service initialized');
    console.log(`ðŸ“¡ NTP Server: ${this.config.NTP_SERVER}`);
    console.log(`â±ï¸ Sync Interval: ${this.config.TIME_SYNC_INTERVAL}ms`);
    console.log(`âš ï¸ Max Drift: ${this.config.MAX_TIME_DRIFT}ms`);
  }

  /**
   * Start time synchronization service
   */
  async start(): Promise<void> {
    if (this.isRunning) {
      console.log('ðŸ• Time sync service already running');
      return;
    }

    if (!this.config.ENABLE_TIME_SYNC) {
      console.log('â¸ï¸ Time synchronization disabled by configuration');
      return;
    }

    console.log('ðŸš€ Starting time synchronization service...');

    try {
      // Perform initial NTP synchronization
      await this.performNtpSync();

      // Start periodic NTP synchronization
      this.startNtpSync();

      // Start exchange time monitoring
      this.startExchangeTimeMonitoring();

      this.isRunning = true;
      console.log('âœ… Time synchronization service started');
      
      this.emit('started');

    } catch (error) {
      console.error('âŒ Failed to start time sync service:', error);
      this.emit('error', error);
      throw error;
    }
  }

  /**
   * Stop time synchronization service
   */
  async stop(): Promise<void> {
    if (!this.isRunning) {
      console.log('ðŸ• Time sync service not running');
      return;
    }

    console.log('ðŸ›‘ Stopping time synchronization service...');

    // Clear intervals
    if (this.ntpSyncInterval) {
      clearInterval(this.ntpSyncInterval);
      this.ntpSyncInterval = null;
    }

    if (this.exchangeSyncInterval) {
      clearInterval(this.exchangeSyncInterval);
      this.exchangeSyncInterval = null;
    }

    this.isRunning = false;
    console.log('âœ… Time synchronization service stopped');
    
    this.emit('stopped');
  }

  /**
   * Get current synchronized time
   */
  now(): Date {
    const localTime = new Date();
    return new Date(localTime.getTime() + this.ntpOffset);
  }

  /**
   * Get current timestamp in milliseconds (synchronized)
   */
  timestamp(): number {
    return this.now().getTime();
  }

  /**
   * Get current timestamp in ISO string format (synchronized)
   */
  toISOString(): string {
    return this.now().toISOString();
  }

  /**
   * Get exchange-synchronized time
   */
  getExchangeTime(exchange: string): Date {
    const baseTime = this.now();
    const exchangeOffset = this.exchangeOffsets.get(exchange) || 0;
    return new Date(baseTime.getTime() + exchangeOffset);
  }

  /**
   * Perform NTP synchronization
   */
  private async performNtpSync(): Promise<void> {
    try {
      console.log(`ðŸ• Syncing with NTP server: ${this.config.NTP_SERVER}`);

      const ntpData = await new Promise<any>((resolve, reject) => {
        ntp.getNetworkTime(this.config.NTP_SERVER, 123, (err: Error | null, date?: Date) => {
          if (err) {
            reject(err);
          } else if (date) {
            resolve({ date, offset: date.getTime() - Date.now() });
          } else {
            reject(new Error('No date received from NTP server'));
          }
        });
      });

      const previousOffset = this.ntpOffset;
      this.ntpOffset = ntpData.offset;
      this.lastNtpSync = new Date();
      this.syncCount++;

      const drift = Math.abs(this.ntpOffset - previousOffset);
      
      console.log(`âœ… NTP sync complete:`);
      console.log(`   ðŸ“Š Offset: ${this.ntpOffset}ms`);
      console.log(`   ðŸ”„ Drift: ${drift}ms`);
      console.log(`   ðŸ• Server time: ${ntpData.date.toISOString()}`);

      // Check for significant drift
      if (drift > this.config.MAX_TIME_DRIFT) {
        console.warn(`âš ï¸ High time drift detected: ${drift}ms (max: ${this.config.MAX_TIME_DRIFT}ms)`);
        this.emit('highDrift', { drift, offset: this.ntpOffset });
      }

      // Emit sync event
      this.emit('ntpSync', {
        offset: this.ntpOffset,
        drift,
        serverTime: ntpData.date,
        localTime: new Date()
      });

    } catch (error) {
      this.errorCount++;
      console.error('âŒ NTP synchronization failed:', error);
      this.emit('ntpError', error);
      throw error;
    }
  }

  /**
   * Start periodic NTP synchronization
   */
  private startNtpSync(): void {
    this.ntpSyncInterval = setInterval(async () => {
      try {
        await this.performNtpSync();
      } catch (error) {
        console.error('âŒ Periodic NTP sync failed:', error);
      }
    }, this.config.TIME_SYNC_INTERVAL);

    console.log(`â° NTP sync scheduled every ${this.config.TIME_SYNC_INTERVAL / 1000}s`);
  }

  /**
   * Start exchange time monitoring
   */
  private startExchangeTimeMonitoring(): void {
    // Monitor exchange time every 30 seconds
    this.exchangeSyncInterval = setInterval(() => {
      this.checkExchangeTimeHealth();
    }, 30000);

    console.log('ðŸ“¡ Exchange time monitoring started');
  }

  /**
   * Sync with exchange server time
   */
  async syncWithExchange(exchange: string, exchangeServerTime: Date): Promise<void> {
    try {
      const localTime = this.now();
      const offset = exchangeServerTime.getTime() - localTime.getTime();
      
      // Store exchange offset
      const previousOffset = this.exchangeOffsets.get(exchange) || 0;
      this.exchangeOffsets.set(exchange, offset);
      
      // Update exchange time info
      this.exchangeTimeInfo.set(exchange, {
        exchange,
        serverTime: exchangeServerTime,
        localTime,
        offset,
        lastSync: new Date()
      });

      this.lastExchangeSync = new Date();

      const drift = Math.abs(offset - previousOffset);
      
      console.log(`ðŸ”„ Exchange sync (${exchange}):`);
      console.log(`   ðŸ“Š Offset: ${offset}ms`);
      console.log(`   ðŸ”„ Drift: ${drift}ms`);
      console.log(`   ðŸ• Exchange time: ${exchangeServerTime.toISOString()}`);

      // Check for significant exchange drift
      if (Math.abs(offset) > this.config.MAX_TIME_DRIFT) {
        console.warn(`âš ï¸ High exchange drift detected (${exchange}): ${offset}ms`);
        this.emit('exchangeDrift', { exchange, offset, drift });
      }

      // Emit exchange sync event
      this.emit('exchangeSync', {
        exchange,
        offset,
        drift,
        exchangeTime: exchangeServerTime,
        localTime
      });

    } catch (error) {
      console.error(`âŒ Exchange sync failed (${exchange}):`, error);
      this.emit('exchangeError', { exchange, error });
    }
  }

  /**
   * Check exchange time health
   */
  private checkExchangeTimeHealth(): void {
    const staleThreshold = 5 * 60 * 1000; // 5 minutes

    for (const [exchange, info] of this.exchangeTimeInfo) {
      const timeSinceSync = Date.now() - info.lastSync.getTime();
      
      if (timeSinceSync > staleThreshold) {
        console.warn(`âš ï¸ Stale exchange time data (${exchange}): ${timeSinceSync / 1000}s old`);
        this.emit('staleExchangeTime', { exchange, timeSinceSync });
      }
    }
  }

  /**
   * Create time sync message for WebSocket broadcasting
   */
  createTimeSyncMessage(): TimeSyncMessage {
    const syncedTime = this.now();
    const exchangeKeys = Array.from(this.exchangeTimeInfo.keys());
    const firstExchange = exchangeKeys.at(0);

    return {
      serverTime: syncedTime,
      exchangeTime: firstExchange 
        ? this.getExchangeTime(firstExchange)
        : syncedTime,
      drift: this.getTotalDrift()
    };
  }

  /**
   * Get total time drift across all sources
   */
  getTotalDrift(): number {
    const exchangeDrifts = Array.from(this.exchangeOffsets.values());
    const maxExchangeDrift = exchangeDrifts.length > 0 ? Math.max(...exchangeDrifts.map(Math.abs)) : 0;
    return Math.max(Math.abs(this.ntpOffset), maxExchangeDrift);
  }

  /**
   * Get time synchronization statistics
   */
  getStats(): TimeSyncStats {
    const exchangeOffsetValues = Array.from(this.exchangeOffsets.values());
    const firstExchangeOffset = exchangeOffsetValues.at(0);
    
    return {
      lastNtpSync: this.lastNtpSync,
      lastExchangeSync: this.lastExchangeSync,
      ntpOffset: this.ntpOffset,
      exchangeOffset: firstExchangeOffset !== undefined 
        ? firstExchangeOffset 
        : 0,
      totalDrift: this.getTotalDrift(),
      syncCount: this.syncCount,
      errorCount: this.errorCount,
      isHealthy: this.isHealthy(),
      uptime: Math.floor((Date.now() - this.startTime.getTime()) / 1000)
    };
  }

  /**
   * Check if time synchronization is healthy
   */
  isHealthy(): boolean {
    const now = Date.now();
    const ntpStale = this.lastNtpSync ? (now - this.lastNtpSync.getTime()) > (this.config.TIME_SYNC_INTERVAL * 2) : true;
    const highDrift = this.getTotalDrift() > this.config.MAX_TIME_DRIFT;
    const tooManyErrors = this.errorCount > 10;

    return !ntpStale && !highDrift && !tooManyErrors;
  }

  /**
   * Get exchange time information
   */
  getExchangeTimeInfo(): ExchangeTimeInfo[] {
    return Array.from(this.exchangeTimeInfo.values());
  }

  /**
   * Force immediate synchronization
   */
  async forcSync(): Promise<void> {
    console.log('ðŸ”„ Forcing immediate time synchronization...');
    await this.performNtpSync();
  }

  /**
   * Get configuration (without sensitive data)
   */
  getConfig(): Omit<TimeSyncConfig, 'NTP_SERVER'> {
    const safeConfig = { ...this.config };
    delete (safeConfig as any).NTP_SERVER;
    return safeConfig;
  }

  /**
   * Calculate precise trading timestamp
   * This is the timestamp that should be used for all trading operations
   */
  getTradingTimestamp(): number {
    return this.timestamp();
  }

  /**
   * Calculate precise trading timestamp for specific exchange
   */
  getExchangeTradingTimestamp(exchange: string): number {
    return this.getExchangeTime(exchange).getTime();
  }

  /**
   * Format timestamp for logging with microsecond precision
   */
  formatPreciseTimestamp(date?: Date): string {
    const timestamp = date || this.now();
    const microseconds = (timestamp.getTime() % 1000) * 1000;
    return `${timestamp.toISOString().slice(0, -1)}${microseconds.toString().padStart(6, '0')}Z`;
  }

  /**
   * Validate timestamp is within acceptable range
   */
  validateTimestamp(timestamp: number, maxAgeMs = 60000): boolean {
    const now = this.timestamp();
    const age = Math.abs(now - timestamp);
    return age <= maxAgeMs;
  }
}

