} from '../JabbrLabs/target-reacher/interfaces';
import logger from '../services/logging.service';

import { strategyFactory } from './strategy-factory';
import { createBasicContext } from './strategy-utils';

/**
 * Backtesting results structure
 */
export interface BacktestResult {
  totalTrades: number;
  winningTrades: number;
  losingTrades: number;
  totalPnl: number;
  winRate: number;
  profitFactor: number;
  averageWin: number;
  averageLoss: number;
  maxDrawdown: number;
  sharpeRatio: number;
  trades: Array<{
    type: 'ENTRY' | 'EXIT';
    side: 'BUY' | 'SELL';
    price: number;
    amount: number;
    timestamp: number;
    pnl?: number;
  }>;
  equityCurve: Array<{
    timestamp: number;
    equity: number;
  }>;
  strategyState: Record<string, unknown>;
}

/**
 * Backtesting options
 */
export interface BacktestOptions {
  initialCapital?: number;
  fees?: number;
  slippage?: number;
  enableLogs?: boolean;
  saveTradeDetails?: boolean;
  commission?: number;
}

/**
 * Mock market data provider for backtesting
 */
class BacktestMarketDataProvider {
  private candles: Candle[];
  private currentIndex = 0;
  
  constructor(candles: Candle[]) {
    this.candles = candles;
  }
  
  async getCurrentPrice(symbol: string): Promise<number> {
    if (this.currentIndex >= this.candles.length) {
      throw new Error('No more data available');
    }
    const candle = this.candles[this.currentIndex];
    if (!candle) {
      throw new Error('Invalid candle data');
    }
    return candle.close;
  }
  
  async getCandles(symbol: string, timeframe: string, limit?: number): Promise<Candle[]> {
    if (this.currentIndex >= this.candles.length) {
      throw new Error('No more data available');
    }
    
    const endIndex = this.currentIndex + 1;
    const startIndex = Math.max(0, endIndex - (limit || this.candles.length));
    
    return this.candles.slice(startIndex, endIndex);
  }
  
  async getOrderBook(symbol: string, limit?: number): Promise<any> {
    if (this.currentIndex >= this.candles.length) {
      throw new Error('No more data available');
    }
    
    const currentCandle = this.candles[this.currentIndex];
    if (!currentCandle) {
      throw new Error('Invalid candle data');
    }
    
    return {
      bids: [[currentCandle.close * 0.999, 10]],
      asks: [[currentCandle.close * 1.001, 10]],
      timestamp: currentCandle.timestamp
    };
  }
  
  async getTicker(symbol: string): Promise<any> {
    if (this.currentIndex >= this.candles.length) {
      throw new Error('No more data available');
    }
    
    const currentCandle = this.candles[this.currentIndex];
    if (!currentCandle) {
      throw new Error('Invalid candle data');
    }
    
    return {
      symbol,
      last: currentCandle.close,
      bid: currentCandle.close * 0.999,
      ask: currentCandle.close * 1.001,
      volume: currentCandle.volume,
      timestamp: currentCandle.timestamp
    };
  }
  
  setCurrentIndex(index: number): void {
    if (index < 0 || index >= this.candles.length) {
      throw new Error(`Invalid candle index: ${index}`);
    }
    this.currentIndex = index;
  }
  
  getCurrentCandle(): Candle {
    const candle = this.candles[this.currentIndex];
    if (!candle) {
      throw new Error('Invalid candle index or data');
    }
    return candle;
  }
  
  hasMoreData(): boolean {
    return this.currentIndex < this.candles.length - 1;
  }
  
  advanceToNextCandle(): boolean {
    if (this.hasMoreData()) {
      this.currentIndex++;
      return true;
    }
    return false;
  }
}

/**
 * Mock trade executor for backtesting
 */
class BacktestTradeExecutor {
  private position: Position | null = null;
  private orders: TradeOrder[] = [];
  private trades: any[] = [];
  private equity: number;
  private initialCapital: number;
  private fees: number;
  private slippage: number;
  private pnl = 0;
  
  constructor(options: BacktestOptions) {
    this.initialCapital = options.initialCapital || 10000;
    this.equity = this.initialCapital;
    this.fees = options.fees || 0.001; // 0.1%
    this.slippage = options.slippage || 0.001; // 0.1%
  }
  
  async executeSignal(signal: TradeSignal, botConfig?: any): Promise<TradeOrder> {
    // Apply slippage
    const price = signal.side === 'buy' 
      ? signal.price * (1 + this.slippage)
      : signal.price * (1 - this.slippage);
      
    // Calculate position size (simple for backtesting)
    const amount = 1; // Fixed position size for now
    
    // Create an order
    const order: TradeOrder = {
      id: `order-${Date.now()}-${Math.floor(Math.random() * 1000)}`,
      orderId: `ord-${Date.now()}`,
      botId: signal.botId,
      symbol: signal.symbol,
      type: 'market',
      side: signal.side,
      amount,
      price,
      status: 'filled',
      filled: amount,
      remaining: 0,
      timestamp: signal.timestamp,
      updatedAt: signal.timestamp
    };
    
    this.orders.push(order);
    
    // Update position
    if (this.position === null) {
      // Opening a new position
      this.position = {
        symbol: signal.symbol,
        side: signal.side === 'buy' ? 'long' : 'short',
        size: amount,
        entryPrice: price,
        timestamp: signal.timestamp
      };
      
      this.trades.push({
        type: 'ENTRY',
        side: signal.side === 'buy' ? 'BUY' : 'SELL',
        price,
        amount,
        timestamp: signal.timestamp
      });
      
      // Apply fees
      this.equity -= price * amount * this.fees;
    } else {
      // Closing existing position
      const exitPrice = price;
      const entryPrice = this.position.entryPrice;
      let profit = 0;
      
      if (this.position.side === 'long' && signal.side === 'sell') {
        profit = (exitPrice - entryPrice) * this.position.size;
      } else if (this.position.side === 'short' && signal.side === 'buy') {
        profit = (entryPrice - exitPrice) * this.position.size;
      }
      
      // Apply fees
      profit -= price * amount * this.fees;
      
      this.pnl += profit;
      this.equity += profit;
      
      this.trades.push({
        type: 'EXIT',
        side: signal.side === 'buy' ? 'BUY' : 'SELL',
        price,
        amount,
        timestamp: signal.timestamp,
        pnl: profit
      });
      
      // Reset position
      const oldPosition = this.position;
      this.position = null;
      
      // Open new position if opposite direction
      if ((signal.side === 'buy' && oldPosition.side === 'short') ||
          (signal.side === 'sell' && oldPosition.side === 'long')) {
        this.position = {
          symbol: signal.symbol,
          side: signal.side === 'buy' ? 'long' : 'short',
          size: amount,
          entryPrice: price,
          timestamp: signal.timestamp
        };
        
        this.trades.push({
          type: 'ENTRY',
          side: signal.side === 'buy' ? 'BUY' : 'SELL',
          price,
          amount,
          timestamp: signal.timestamp
        });
        
        // Apply fees again for new position
        this.equity -= price * amount * this.fees;
      }
    }
    
    return order;
  }
  
  async getPosition(botId: string, symbol: string): Promise<Position | null> {
    return this.position;
  }
  
  async closePosition(botId: string, symbol: string): Promise<void> {
    if (!this.position) {return;}
    
    // Simulate market close at current price
    const signal: TradeSignal = {
      id: `close-${Date.now()}`,
      botId,
      symbol,
      side: this.position.side === 'long' ? 'sell' : 'buy',
      confidence: 1,
      price: this.position.entryPrice, // Just for initialization, will be updated
      timestamp: Date.now(),
      reason: 'Position close requested'
    };
    
    await this.executeSignal(signal);
  }
  
  getEquity(): number {
    return this.equity;
  }
  
  getPnL(): number {
    return this.pnl;
  }
  
  getTrades(): any[] {
    return this.trades;
  }
  
  reset(): void {
    this.position = null;
    this.orders = [];
    this.trades = [];
    this.equity = this.initialCapital;
    this.pnl = 0;
  }
}

/**
 * Strategy Backtester class
 */
export class StrategyBacktester {
  private strategy: IStrategy | null = null;
  private candles: Candle[];
  private marketData: BacktestMarketDataProvider;
  private tradeExecutor: BacktestTradeExecutor;
  private options: BacktestOptions;
  private context: StrategyContext;
  private equityCurve: Array<{ timestamp: number; equity: number }> = [];
  
  constructor(candles: Candle[], options: BacktestOptions = {}) {
    this.candles = [...candles].sort((a, b) => a.timestamp - b.timestamp);
    this.options = {
      initialCapital: 10000,
      fees: 0.001,
      slippage: 0.001,
      enableLogs: true,
      saveTradeDetails: true,
      commission: 0,
      ...options
    };
    
    this.marketData = new BacktestMarketDataProvider(this.candles);
    this.tradeExecutor = new BacktestTradeExecutor(this.options);
    
    // Create basic context with our backtesting trade executor already set
    this.context = {
      ...createBasicContext(
        'BTC/USDT',
        this.marketData,
        { type: 'backtest', parameters: {} }
      ),
      tradeExecutor: this.tradeExecutor
    };
  }
  
  /**
   * Load a strategy for backtesting
   */
  async loadStrategy(
    strategyType: string, 
    config: StrategyConfig
  ): Promise<void> {
    this.strategy = await strategyFactory.createStrategy(
      strategyType as any, 
      config, 
      this.context
    );
    
    // Initialize strategy
    await this.strategy.initialize(this.context);
  }
  
  /**
   * Run backtest on all candles
   */
  async runBacktest(): Promise<BacktestResult> {
    if (!this.strategy) {
      throw new Error('Strategy not loaded. Call loadStrategy() first.');
    }
    
    // Reset state
    this.tradeExecutor.reset();
    this.equityCurve = [];
    
    // Start from beginning
    this.marketData.setCurrentIndex(0);
    
    // Process each candle
    let candleCount = 0;
    
    while (this.marketData.hasMoreData()) {
      const candle = this.marketData.getCurrentCandle();
      
      try {
        // Execute strategy
        const result = await this.strategy.execute(this.context);
        
        // Record equity
        this.equityCurve.push({
          timestamp: candle.timestamp,
          equity: this.tradeExecutor.getEquity()
        });
        
        // Optional logging
        if (this.options.enableLogs && result.action && result.action !== 'hold') {
          logger.info(`Backtest [${new Date(candle.timestamp).toISOString()}]: ${result.action.toUpperCase()} signal generated`, {
            price: candle.close,
            confidence: result.confidence,
            reason: result.reason
          });
        }
      } catch (error) {
        logger.error(`Backtest error at candle ${candleCount}:`, { error });
      }
      
      // Move to next candle
      this.marketData.advanceToNextCandle();
      candleCount++;
    }
    
    // Ensure last position is closed
    await this.tradeExecutor.closePosition('backtest', 'BTC/USDT');
    
    // Calculate metrics
    return this.calculateResults();
  }
  
  /**
   * Calculate backtest results and metrics
   */
  private calculateResults(): BacktestResult {
    const trades = this.tradeExecutor.getTrades();
    const exitTrades = trades.filter(t => t.type === 'EXIT');
    
    const totalTrades = exitTrades.length;
    const winningTrades = exitTrades.filter(t => t.pnl > 0).length;
    const losingTrades = exitTrades.filter(t => t.pnl <= 0).length;
    
    const totalPnl = this.tradeExecutor.getPnL();
    const totalProfit = exitTrades
      .filter(t => t.pnl > 0)
      .reduce((sum, t) => sum + t.pnl, 0);
    const totalLoss = exitTrades
      .filter(t => t.pnl <= 0)
      .reduce((sum, t) => sum + t.pnl, 0);
    
    const winRate = totalTrades > 0 ? winningTrades / totalTrades : 0;
    const profitFactor = Math.abs(totalLoss) > 0 ? totalProfit / Math.abs(totalLoss) : totalProfit > 0 ? Infinity : 0;
    const averageWin = winningTrades > 0 ? totalProfit / winningTrades : 0;
    const averageLoss = losingTrades > 0 ? totalLoss / losingTrades : 0;
    
    // Calculate drawdown
    let maxDrawdown = 0;
    let peak = this.options.initialCapital || 10000;
    
    this.equityCurve.forEach(point => {
      if (point.equity > peak) {
        peak = point.equity;
      }
      
      const drawdown = peak > 0 ? (peak - point.equity) / peak : 0;
      if (drawdown > maxDrawdown) {
        maxDrawdown = drawdown;
      }
    });
    
    // Calculate Sharpe Ratio (simplified)
    const returns = this.equityCurve.map((point, i) => {
      if (i === 0) {return 0;}
      const prevPoint = this.equityCurve[i-1];
      if (!prevPoint || prevPoint.equity === 0) {return 0;}
      return (point.equity - prevPoint.equity) / prevPoint.equity;
    });
    
    const avgReturn = returns.reduce((sum, r) => sum + r, 0) / returns.length;
    const stdDev = Math.sqrt(returns.reduce((sum, r) => sum + Math.pow(r - avgReturn, 2), 0) / returns.length);
    const sharpeRatio = stdDev > 0 ? avgReturn / stdDev : 0;
    
    return {
      totalTrades,
      winningTrades,
      losingTrades,
      totalPnl,
      winRate,
      profitFactor,
      averageWin,
      averageLoss,
      maxDrawdown,
      sharpeRatio,
      trades: this.options.saveTradeDetails ? trades : [],
      equityCurve: this.options.saveTradeDetails ? this.equityCurve : [],
      strategyState: this.strategy ? { ...this.strategy.getState() } : {}
    };
  }
}

/**
 * Helper function to quickly backtest a strategy
 */
export async function quickBacktest(
  strategyType: string,
  config: StrategyConfig,
  candles: Candle[],
  options: BacktestOptions = {}
): Promise<BacktestResult> {
  const backtester = new StrategyBacktester(candles, options);
  await backtester.loadStrategy(strategyType, config);
  return await backtester.runBacktest();
}

/**
 * Unified Strategy Factory
 * 
 * Central factory for creating strategy instances, supporting both built-in
 * strategies and custom plugins.
 */

import { SMACrossoverStrategy } from '../JabbrLabs/signals/sma/sma-crossover-strategy';
import type { 
  IStrategy, 
  StrategyConfig, 
  StrategyContext, 
  ConfigValidationResult 
} from '../JabbrLabs/target-reacher/interfaces';
import { targetReacherFactory } from '../JabbrLabs/target-reacher/target-reacher-factory';
import logger from '../services/logging.service';

import { AetherSignalStrategy } from './aether-signal-strategy';
import { strategyPluginManager } from './plugin-manager';

export type StrategyType = 
  | 'target-reacher'
  | 'sma-crossover'
  | 'custom'
  | 'aether-signal'
  | 'aether';

// Extended strategy config for plugins
export interface ExtendedStrategyConfig extends StrategyConfig {
  pluginId?: string;
}

// Strategy factory interface
interface StrategyFactoryInterface {
  create(config: StrategyConfig, context: StrategyContext): IStrategy;
  validateConfig(config: Record<string, unknown>): ConfigValidationResult;
  getDefaultConfig(): StrategyConfig;
}

// SMA Crossover Strategy Factory
const smaCrossoverFactory: StrategyFactoryInterface = {
  create(__config: StrategyConfig, __context: StrategyContext): IStrategy {
    return new SMACrossoverStrategy();
  },
  
  validateConfig(config: Record<string, unknown>): ConfigValidationResult {
    const strategy = new SMACrossoverStrategy();
    return strategy.validateConfig(config);
  },
  
  getDefaultConfig(): StrategyConfig {
    const strategy = new SMACrossoverStrategy();
    return strategy.getDefaultConfig();
  }
};

// Aether Signal Strategy Factory
const aetherSignalFactory: StrategyFactoryInterface = {
  create(config: StrategyConfig, __context: StrategyContext): IStrategy {
    return new AetherSignalStrategy(config);
  },
  
  validateConfig(config: Record<string, unknown>): ConfigValidationResult {
    const strategy = new AetherSignalStrategy();
    return strategy.validateConfig(config);
  },
  
  getDefaultConfig(): StrategyConfig {
    const strategy = new AetherSignalStrategy();
    return strategy.getDefaultConfig();
  }
};

// Built-in strategy factories registry
const builtInFactories: Record<string, StrategyFactoryInterface> = {
  'target-reacher': targetReacherFactory,
  'sma-crossover': smaCrossoverFactory,
  'aether-signal': aetherSignalFactory,
  'aether': aetherSignalFactory, // Alternative name for compatibility
  // Add other built-in strategy factories here as they're implemented
};

export class StrategyFactory {
  private initialized = false;

  /**
   * Initialize the strategy factory and plugin manager
   */
  async initialize(): Promise<void> {
    if (this.initialized) {
      return;
    }

    try {
      // Initialize plugin manager
      await strategyPluginManager.initialize();
      
      this.initialized = true;
      logger.info('Strategy Factory initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize Strategy Factory', { error });
      throw error;
    }
  }

  /**
   * Create a strategy instance
   */
  async createStrategy(
    type: StrategyType,
    config: ExtendedStrategyConfig,
    context: StrategyContext
  ): Promise<IStrategy> {
    if (!this.initialized) {
      await this.initialize();
    }

    try {
      // Handle built-in strategies
      if (type !== 'custom' && Object.prototype.hasOwnProperty.call(builtInFactories, type)) {
        const factory = builtInFactories[type as keyof typeof builtInFactories];
        if (!factory) {
          throw new Error(`No factory found for built-in strategy type: ${type}`);
        }
        logger.info(`Creating built-in strategy: ${type}`, { botId: context.botConfig?.id });
        return factory.create(config, context);
      }

      // Handle custom plugin strategies
      if (type === 'custom' && config.pluginId) {
        logger.info(`Creating custom plugin strategy: ${config.pluginId}`, { botId: context.botConfig?.id });
        return await strategyPluginManager.loadPlugin(config.pluginId, config, context);
      }

      throw new Error(`Unsupported strategy type: ${type}`);
    } catch (error) {
      logger.error(`Failed to create strategy: ${type}`, { error, botId: context.botConfig?.id });
      throw error;
    }
  }

  /**
   * Validate strategy configuration
   */
  async validateConfig(type: StrategyType, config: Record<string, unknown>): Promise<ConfigValidationResult> {
    try {
      // Handle built-in strategies
      if (type !== 'custom' && Object.prototype.hasOwnProperty.call(builtInFactories, type)) {
        const factory = builtInFactories[type as keyof typeof builtInFactories];
        if (!factory) {
          return {
            valid: false,
            errors: [{ field: 'type', message: `No factory found for built-in strategy type: ${type}`, code: 'INVALID_TYPE' }],
            warnings: []
          };
        }
        return factory.validateConfig(config);
      }

      // Handle custom plugin strategies
      if (type === 'custom' && config.pluginId) {
        return await strategyPluginManager.validatePluginConfig(
          config.pluginId as string,
          config
        );
      }

      return {
        valid: false,
        errors: [{ field: 'type', message: `Unsupported strategy type: ${type}`, code: 'INVALID_TYPE' }],
        warnings: []
      };
    } catch (error) {
      return {
        valid: false,
        errors: [{ field: 'validation', message: error instanceof Error ? error.message : String(error), code: 'VALIDATION_ERROR' }],
        warnings: []
      };
    }
  }

  /**
   * Get default configuration for a strategy type
   */
  async getDefaultConfig(type: StrategyType, pluginId?: string): Promise<StrategyConfig> {
    try {
      // Handle built-in strategies
      if (type !== 'custom' && Object.prototype.hasOwnProperty.call(builtInFactories, type)) {
        const factory = builtInFactories[type as keyof typeof builtInFactories];
        if (!factory) {
          throw new Error(`No factory found for built-in strategy type: ${type}`);
        }
        return factory.getDefaultConfig();
      }

      // Handle custom plugin strategies
      if (type === 'custom' && pluginId) {
        const plugin = strategyPluginManager.getPlugin(pluginId);
        if (!plugin) {
          throw new Error(`Plugin not found: ${pluginId}`);
        }

        // Load plugin module to get default config
        const pluginModule = await import(plugin.filePath);
        const tempInstance = new pluginModule.default();
        return tempInstance.getDefaultConfig();
      }

      throw new Error(`Cannot get default config for strategy type: ${type}`);
    } catch (error) {
      logger.error(`Failed to get default config for strategy: ${type}`, { error, pluginId });
      throw error;
    }
  }

  /**
   * Get available built-in strategies
   */
  getBuiltInStrategies(): Array<{ type: StrategyType; name: string; description: string }> {
    return [
      {
        type: 'target-reacher',
        name: 'Target Reacher',
        description: 'Modular target reacher strategy with fixed and average price sources'
      },
      {
        type: 'sma-crossover',
        name: 'SMA Crossover',
        description: 'Trading strategy based on SMA crossover signals'
      },
      {
        type: 'aether-signal',
        name: 'Aether Signal Generator',
        description: 'Advanced mathematical signal generator using fractional calculus and stochastic processes'
      }
    ];
  }

  /**
   * Get available custom plugins
   */
  getAvailablePlugins() {
    return strategyPluginManager.getPlugins().map(plugin => ({
      id: plugin.id,
      name: plugin.metadata.name,
      version: plugin.metadata.version,
      description: plugin.metadata.description,
      author: plugin.metadata.author,
      supportedMarkets: plugin.metadata.supportedMarkets,
      riskLevel: plugin.metadata.riskLevel,
      category: plugin.metadata.category,
      tags: plugin.metadata.tags,
      isLoaded: plugin.isLoaded,
      error: plugin.error
    }));
  }

  /**
   * Search available plugins
   */
  searchPlugins(criteria: {
    name?: string;
    category?: string;
    riskLevel?: string;
    supportedMarket?: string;
    tags?: string[];
  }) {
    return strategyPluginManager.searchPlugins(criteria);
  }

  /**
   * Register a new plugin
   */
  async registerPlugin(filePath: string): Promise<string> {
    return await strategyPluginManager.registerPlugin(filePath);
  }

  /**
   * Unload a plugin
   */
  async unloadPlugin(pluginId: string): Promise<void> {
    return await strategyPluginManager.unloadPlugin(pluginId);
  }
}

// Export singleton instance
export const strategyFactory = new StrategyFactory();

/**
 * Strategy utility functions to simplify working with the strategy framework
 */

import type { 
  IStrategy, 
  StrategyContext, 
  StrategyConfig 
} from '../JabbrLabs/target-reacher/interfaces';
import logger from '../services/logging.service';

import type { StrategyType } from './strategy-factory';
import { StrategyFactory, strategyFactory } from './strategy-factory';


/**
 * Create a context for running strategies outside the bot system
 * Useful for testing and backtesting
 */
export function createBasicContext(
  symbol: string,
  marketDataProvider: any,
  config: StrategyConfig,
  botId = 'test-bot'
): StrategyContext {
  return {
    symbol,
    marketData: marketDataProvider,
    tradeExecutor: {
      executeSignal: async () => ({ id: 'test', botId, symbol, type: 'market', side: 'buy', amount: 0, status: 'open', filled: 0, remaining: 0, timestamp: Date.now(), updatedAt: Date.now() }),
      getPosition: async () => null,
      closePosition: async () => {}
    },
    logger: {
      info: (msg: string) => logger.info(`[Strategy] ${msg}`),
      warn: (msg: string) => logger.warn(`[Strategy] ${msg}`),
      error: (msg: string) => logger.error(`[Strategy] ${msg}`),
      debug: (msg: string) => logger.debug(`[Strategy] ${msg}`)
    },
    storage: {
      storeStrategyEvent: async () => {},
      getStrategyState: async () => null,
      saveStrategyState: async () => {}
    },
    eventEmitter: {
      emit: () => {},
      on: () => {},
      off: () => {}
    },
    config,
    botConfig: {
      id: botId,
      name: 'Test Bot',
      symbol,
      tradeType: 'market',
      amount: 100
    }
  };
}

/**
 * Helper to quickly create and initialize a strategy
 */
export async function createAndInitializeStrategy(
  type: StrategyType,
  config: StrategyConfig,
  context: StrategyContext
): Promise<IStrategy> {
  const strategy = await strategyFactory.createStrategy(type, config, context);
  await strategy.initialize(context);
  return strategy;
}

/**
 * Helper to safely run a strategy with proper error handling
 */
export async function runStrategyWithErrorHandling(
  strategy: IStrategy,
  context: StrategyContext
) {
  try {
    return await strategy.execute(context);
  } catch (error) {
    logger.error(`Strategy execution error: ${error instanceof Error ? error.message : String(error)}`);
    return {
      success: false,
      action: 'hold',
      error: error instanceof Error ? error.message : String(error),
      reason: 'Strategy execution failed with error'
    };
  }
}

/**
 * Clean up resources used by a strategy
 */
export async function cleanupStrategy(strategy: IStrategy, context: StrategyContext): Promise<void> {
  try {
    await strategy.cleanup(context);
  } catch (error) {
    logger.error(`Strategy cleanup error: ${error instanceof Error ? error.message : String(error)}`);
  }
}

/**
 * Get all available strategies (both built-in and plugins)
 */
export function getAllAvailableStrategies(): { builtIn: any[]; plugins: any[] } {
  const builtIn = strategyFactory.getBuiltInStrategies();
  const plugins = strategyFactory.getAvailablePlugins();
  
  return {
    builtIn,
    plugins
  };
}

/**
 * Search for strategies by name
 */
export function findStrategyByName(name: string): any | null {
  const builtIn = strategyFactory.getBuiltInStrategies()
    .find(s => s.name.toLowerCase().includes(name.toLowerCase()));
    
  const plugin = strategyFactory.getAvailablePlugins()
    .find(p => p.name.toLowerCase().includes(name.toLowerCase()));
    
  return builtIn || plugin || null;
}

/**
 * Target Reacher Strategy Plugin
 * 
 * Trading strategy that targets specific price levels using fixed prices or 
 * moving averages. Integrates the existing Target Reacher implementation 
 * with the strategy framework.
 */

import type { 
  IStrategy,
  StrategyContext, 
  StrategyConfig, 
  StrategyState
} from '../JabbrLabs/target-reacher/interfaces';
import { TargetReacherStrategy as CoreTargetReacher } from '../JabbrLabs/target-reacher/target-reacher';
import logger from '../services/logging.service';

/**
 * Target Reacher Strategy Plugin Metadata
 */
export const TargetReacherStrategyMetadata = {
  id: 'target-reacher',
  name: 'Target Reacher',
  version: '2.0.0',
  description: 'Price targeting strategy with fixed and average price sources',
  author: 'JabbrLabs',
  category: 'price-targeting',
  tags: ['price-target', 'average', 'fixed-price', 'beginner'],
  riskLevel: 'medium',
  supportedMarkets: ['spot', 'futures', 'margin'],
  requiredCapital: 100, // Minimum capital requirement
  maxDrawdown: 10, // Maximum expected drawdown percentage
  expectedReturn: 15, // Expected annual return percentage
  timeframe: ['1m', '5m', '15m', '1h', '4h'],
  dependencies: ['market-data', 'order-execution'],
  configSchema: {
    type: 'object',
    properties: {
      priceSource: { 
        type: 'string', 
        enum: ['fixed', 'average'], 
        default: 'fixed',
        description: 'Source for target price calculation'
      },
      fixedPrice: { 
        type: 'number', 
        minimum: 0.01, 
        default: 50000,
        description: 'Fixed target price (used when priceSource is "fixed")'
      },
      averagePeriod: { 
        type: 'integer', 
        minimum: 1, 
        maximum: 500, 
        default: 20,
        description: 'Period for average calculation (used when priceSource is "average")'
      },
      averageType: { 
        type: 'string', 
        enum: ['open', 'high', 'low', 'close'], 
        default: 'close',
        description: 'Price type for average calculation'
      },
      confidenceThreshold: { 
        type: 'number', 
        minimum: 0.1, 
        maximum: 1.0, 
        default: 0.8,
        description: 'Minimum confidence level for signal execution'
      }
    }
  }
} as const;

/**
 * Target Reacher Strategy Implementation
 */
export class TargetReacherStrategy implements IStrategy {
  // Strategy metadata
  readonly name = TargetReacherStrategyMetadata.name;
  readonly version = TargetReacherStrategyMetadata.version;
  readonly description = TargetReacherStrategyMetadata.description;
  readonly supportedMarkets = TargetReacherStrategyMetadata.supportedMarkets;

  // Static metadata for plugin system
  static readonly metadata = TargetReacherStrategyMetadata;

  // Internal components
  private coreStrategy: CoreTargetReacher;
  private state: StrategyState;
  private config: StrategyConfig;

  constructor(config?: StrategyConfig) {
    this.config = config || this.getDefaultConfig();
    
    // We'll initialize the core strategy later in the initialize method
    // since we need the actual context
    this.coreStrategy = null as any; // Temporary, will be set in initialize()

    // Initialize state
    this.state = {
      isRunning: false,
      totalProfit: 0,
      tradesExecuted: 0,
      lastUpdate: new Date(),
      customState: {
        targetPrice: null,
        lastSignal: null,
        priceHistory: [],
        signalHistory: []
      }
    };
  }

  /**
   * Initialize the strategy
   */
  async initialize(context: StrategyContext): Promise<void> {
    try {
      logger.info(`Initializing Target Reacher Strategy for bot ${context.botConfig.id}`, {
        symbol: context.symbol,
        config: this.config.parameters
      });

      // Now we can initialize the core strategy with the actual context
      this.coreStrategy = new CoreTargetReacher(this.config, context);
      await this.coreStrategy.initialize();
      
      this.state.isRunning = true;
      this.state.lastUpdate = new Date();
      
      logger.info(`Target Reacher Strategy initialized successfully for ${context.symbol}`);
    } catch (error) {
      logger.error(`Failed to initialize Target Reacher Strategy: ${error instanceof Error ? error.message : String(error)}`);
      throw error;
    }
  }

  /**
   * Execute the strategy logic and return a trading signal
   */
  async execute(context: StrategyContext): Promise<any> {
    try {
      // Execute the core strategy
      const result = await this.coreStrategy.execute();

      // Update state
      this.state.lastUpdate = new Date();
      if (this.state.customState) {
        this.state.customState.lastSignal = result;
        if (Array.isArray(this.state.customState.signalHistory)) {
          this.state.customState.signalHistory.push({
            action: result.action,
            reason: result.reason,
            timestamp: new Date()
          });
          
          // Keep only last 10 signals
          if (this.state.customState.signalHistory.length > 10) {
            this.state.customState.signalHistory.shift();
          }
        }
      }

      // Check confidence threshold
      const confidenceThreshold = (this.config.parameters.confidenceThreshold as number) || 0.8;
      const confidence = result.confidence || 1.0;

      if (confidence < confidenceThreshold) {
        return {
          success: true,
          action: 'hold',
          confidence,
          reason: `Signal confidence ${confidence.toFixed(2)} below threshold ${confidenceThreshold}`
        };
      }

      logger.debug(`Target Reacher Strategy signal: ${result.action}`, {
        botId: context.botConfig.id,
        symbol: context.symbol,
        confidence,
        reason: result.reason
      });

      return {
        success: result.success,
        action: result.action,
        confidence,
        reason: result.reason,
        metadata: {
          targetPrice: this.state.customState?.targetPrice,
          priceSource: this.config.parameters.priceSource,
          coreResult: result
        }
      };
    } catch (error) {
      logger.error(`Target Reacher Strategy execution failed: ${error instanceof Error ? error.message : String(error)}`);
      return {
        success: false,
        action: 'hold',
        confidence: 0,
        reason: `Strategy execution error: ${error instanceof Error ? error.message : String(error)}`,
        error: error instanceof Error ? error.message : String(error)
      };
    }
  }

  /**
   * Cleanup any resources used by the strategy
   */
  async cleanup(): Promise<void> {
    try {
      await this.coreStrategy.cleanup();
      this.state.isRunning = false;
      logger.info('Target Reacher Strategy cleaned up successfully');
    } catch (error) {
      logger.error(`Failed to cleanup Target Reacher Strategy: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Validate the strategy configuration
   */
  validateConfig(config: Record<string, unknown>): any {
    const errors: Array<{field: string; message: string; code: string}> = [];
    const warnings: Array<{field: string; message: string; suggestion?: string}> = [];

    // Validate required parameters
    if (!config.parameters || typeof config.parameters !== 'object') {
      errors.push({
        field: 'parameters',
        message: 'Parameters object is required',
        code: 'MISSING_PARAMETERS'
      });
      return { valid: false, errors, warnings };
    }

    const params = config.parameters as Record<string, unknown>;

    // Validate price source
    if (!params.priceSource || !['fixed', 'average'].includes(params.priceSource as string)) {
      errors.push({
        field: 'priceSource',
        message: 'Price source must be either "fixed" or "average"',
        code: 'INVALID_PRICE_SOURCE'
      });
    }

    // Validate fixed price if using fixed source
    if (params.priceSource === 'fixed') {
      if (typeof params.fixedPrice !== 'number' || params.fixedPrice <= 0) {
        errors.push({
          field: 'fixedPrice',
          message: 'Fixed price must be a positive number',
          code: 'INVALID_FIXED_PRICE'
        });
      }
    }

    // Validate average parameters if using average source
    if (params.priceSource === 'average') {
      if (typeof params.averagePeriod !== 'number' || params.averagePeriod < 1 || params.averagePeriod > 500) {
        errors.push({
          field: 'averagePeriod',
          message: 'Average period must be between 1 and 500',
          code: 'INVALID_AVERAGE_PERIOD'
        });
      }

      if (!params.averageType || !['open', 'high', 'low', 'close'].includes(params.averageType as string)) {
        errors.push({
          field: 'averageType',
          message: 'Average type must be one of: open, high, low, close',
          code: 'INVALID_AVERAGE_TYPE'
        });
      }
    }

    // Validate confidence threshold
    if (typeof params.confidenceThreshold === 'number') {
      if (params.confidenceThreshold < 0.1 || params.confidenceThreshold > 1.0) {
        warnings.push({
          field: 'confidenceThreshold',
          message: 'Confidence threshold should be between 0.1 and 1.0',
          suggestion: 'Consider using a value between 0.7 and 0.9 for optimal performance'
        });
      }
    }

    return { valid: errors.length === 0, errors, warnings };
  }

  /**
   * Get the default configuration for the strategy
   */
  getDefaultConfig(): StrategyConfig {
    return {
      type: 'target-reacher',
      parameters: {
        priceSource: 'fixed',
        fixedPrice: 50000,
        averagePeriod: 20,
        averageType: 'close',
        confidenceThreshold: 0.8
      },
      riskManagement: {
        stopLossPercentage: 5,
        takeProfitPercentage: 10,
        maxPositionSize: 10,
        maxDailyLoss: 20
      },
      execution: {
        timeframe: '1h',
        maxTrades: 10,
        minimumConfidence: 0.8,
        executionDelay: 1000
      }
    };
  }

  /**
   * Get the current state of the strategy
   */
  getState(): StrategyState {
    return this.state;
  }

  /**
   * Set the state of the strategy
   */
  setState(state: Partial<StrategyState>): void {
    this.state = { ...this.state, ...state };
  }
}

import type { User, UserPreferences } from '@jabbr/shared';

import { database } from '../database/database.config';

import type { IUserRepository } from './user.repository';

// Extended User interface for internal use (includes additional fields not in shared types)
interface InternalUser extends User {
  isEmailVerified: boolean;
  lastLoginAt: Date | null;
  emailVerificationToken?: string | null;
  emailVerificationExpiresAt?: Date | null;
  passwordResetToken?: string | null;
  passwordResetExpiresAt?: Date | null;
}

/**
 * PostgreSQL-based User Repository Implementation
 * Replaces the in-memory storage with proper database operations
 */
export class DatabaseUserRepository implements IUserRepository {
  
  /**
   * Find user by ID
   */
  async findById(id: string): Promise<InternalUser | null> {
    const result = await database.queryOne<any>(`
      SELECT 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
      FROM users 
      WHERE id = $1
    `, [id]);

    if (!result) {return null;}

    return this.mapDatabaseRowToUser(result);
  }

  /**
   * Find user by email
   */
  async findByEmail(email: string): Promise<InternalUser | null> {
    const result = await database.queryOne<any>(`
      SELECT 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
      FROM users 
      WHERE email = $1
    `, [email.toLowerCase()]);

    if (!result) {return null;}

    return this.mapDatabaseRowToUser(result);
  }

  /**
   * Create a new user
   */
  async create(userData: Omit<InternalUser, 'id' | 'createdAt' | 'updatedAt'>): Promise<InternalUser> {
    // Default user preferences matching the shared types
    const defaultPreferences: UserPreferences = {
      timezone: 'UTC',
      currency: 'USD',
      notifications: {
        email: true,
        browser: true,
        tradingAlerts: true,
        systemAlerts: true,
        riskAlerts: true
      },
      dashboard: {
        theme: 'dark',
        layout: 'standard',
        refreshRate: 30000 // 30 seconds in milliseconds
      }
    };

    const preferences = userData.preferences || defaultPreferences;

    const result = await database.queryOne<any>(`
      INSERT INTO users (
        email,
        password_hash,
        role,
        preferences,
        is_email_verified
      ) VALUES ($1, $2, $3, $4, $5)
      RETURNING 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
    `, [
      userData.email.toLowerCase(),
      userData.passwordHash,
      userData.role || 'user',
      JSON.stringify(preferences),
      userData.isEmailVerified || false
    ]);

    if (!result) {
      throw new Error('Failed to create user');
    }

    return this.mapDatabaseRowToUser(result);
  }

  /**
   * Update user data
   */
  async update(id: string, userData: Partial<InternalUser>): Promise<InternalUser | null> {
    // Build dynamic update query
    const updateFields: string[] = [];
    const values: any[] = [];
    let paramIndex = 1;

    if (userData.email !== undefined) {
      updateFields.push(`email = $${paramIndex++}`);
      values.push(userData.email.toLowerCase());
    }

    if (userData.passwordHash !== undefined) {
      updateFields.push(`password_hash = $${paramIndex++}`);
      values.push(userData.passwordHash);
    }

    if (userData.role !== undefined) {
      updateFields.push(`role = $${paramIndex++}`);
      values.push(userData.role);
    }

    if (userData.preferences !== undefined) {
      updateFields.push(`preferences = $${paramIndex++}`);
      values.push(JSON.stringify(userData.preferences));
    }

    if (userData.isEmailVerified !== undefined) {
      updateFields.push(`is_email_verified = $${paramIndex++}`);
      values.push(userData.isEmailVerified);
    }

    if (userData.emailVerificationToken !== undefined) {
      updateFields.push(`email_verification_token = $${paramIndex++}`);
      values.push(userData.emailVerificationToken);
    }

    if (userData.emailVerificationExpiresAt !== undefined) {
      updateFields.push(`email_verification_expires_at = $${paramIndex++}`);
      values.push(userData.emailVerificationExpiresAt);
    }

    if (userData.lastLoginAt !== undefined) {
      updateFields.push(`last_login_at = $${paramIndex++}`);
      values.push(userData.lastLoginAt);
    }

    if (userData.passwordResetToken !== undefined) {
      updateFields.push(`password_reset_token = $${paramIndex++}`);
      values.push(userData.passwordResetToken);
    }

    if (userData.passwordResetExpiresAt !== undefined) {
      updateFields.push(`password_reset_expires_at = $${paramIndex++}`);
      values.push(userData.passwordResetExpiresAt);
    }

    if (updateFields.length === 0) {
      // No fields to update, return current user
      return this.findById(id);
    }

    // Add user ID as the last parameter
    values.push(id);

    const result = await database.queryOne<any>(`
      UPDATE users 
      SET ${updateFields.join(', ')}, updated_at = NOW()
      WHERE id = $${paramIndex}
      RETURNING 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
    `, values);

    if (!result) {return null;}

    return this.mapDatabaseRowToUser(result);
  }

  /**
   * Delete user
   */
  async delete(id: string): Promise<boolean> {
    const result = await database.query(`
      DELETE FROM users 
      WHERE id = $1
    `, [id]);

    return Array.isArray(result) ? result.length > 0 : false;
  }

  /**
   * Update last login timestamp
   */
  async updateLastLogin(id: string): Promise<void> {
    await database.query(`
      UPDATE users 
      SET last_login_at = NOW(), updated_at = NOW()
      WHERE id = $1
    `, [id]);
  }

  /**
   * Update user password
   */
  async updatePassword(id: string, hashedPassword: string): Promise<boolean> {
    const result = await database.query(`
      UPDATE users 
      SET password_hash = $1, updated_at = NOW()
      WHERE id = $2
    `, [hashedPassword, id]);

    return Array.isArray(result) ? result.length > 0 : false;
  }

  /**
   * Check if email already exists
   */
  async emailExists(email: string): Promise<boolean> {
    const result = await database.queryOne<{ exists: boolean }>(`
      SELECT EXISTS(SELECT 1 FROM users WHERE email = $1) as exists
    `, [email.toLowerCase()]);

    return result?.exists || false;
  }

  /**
   * Set email verification token
   */
  async setEmailVerificationToken(id: string, token: string, expiresAt: Date): Promise<boolean> {
    const result = await database.query(`
      UPDATE users 
      SET 
        email_verification_token = $1,
        email_verification_expires_at = $2,
        updated_at = NOW()
      WHERE id = $3
    `, [token, expiresAt, id]);

    return Array.isArray(result) ? result.length > 0 : false;
  }

  /**
   * Verify email using verification token
   */
  async verifyEmail(token: string): Promise<InternalUser | null> {
    const result = await database.queryOne<any>(`
      UPDATE users 
      SET 
        is_email_verified = true,
        email_verification_token = NULL,
        email_verification_expires_at = NULL,
        updated_at = NOW()
      WHERE 
        email_verification_token = $1 
        AND email_verification_expires_at > NOW()
        AND is_email_verified = false
      RETURNING 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
    `, [token]);

    if (!result) {return null;}

    return this.mapDatabaseRowToUser(result);
  }

  /**
   * Set password reset token
   */
  async setPasswordResetToken(email: string, token: string, expiresAt: Date): Promise<boolean> {
    const result = await database.query(`
      UPDATE users 
      SET 
        password_reset_token = $1,
        password_reset_expires_at = $2,
        updated_at = NOW()
      WHERE email = $3
    `, [token, expiresAt, email.toLowerCase()]);

    return Array.isArray(result) ? result.length > 0 : false;
  }

  /**
   * Reset password using reset token
   */
  async resetPassword(token: string, newPasswordHash: string): Promise<InternalUser | null> {
    const result = await database.queryOne<any>(`
      UPDATE users 
      SET 
        password_hash = $1,
        password_reset_token = NULL,
        password_reset_expires_at = NULL,
        updated_at = NOW()
      WHERE 
        password_reset_token = $2 
        AND password_reset_expires_at > NOW()
      RETURNING 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
    `, [newPasswordHash, token]);

    if (!result) {return null;}

    return this.mapDatabaseRowToUser(result);
  }

  /**
   * Get all users (for testing/admin purposes)
   */
  async findAll(): Promise<InternalUser[]> {
    const results = await database.query<any>(`
      SELECT 
        id,
        email,
        password_hash as "passwordHash",
        role,
        preferences,
        is_email_verified as "isEmailVerified",
        email_verification_token as "emailVerificationToken",
        email_verification_expires_at as "emailVerificationExpiresAt",
        last_login_at as "lastLoginAt",
        password_reset_token as "passwordResetToken",
        password_reset_expires_at as "passwordResetExpiresAt",
        created_at as "createdAt",
        updated_at as "updatedAt"
      FROM users 
      ORDER BY created_at DESC
    `);

    return results.map(row => this.mapDatabaseRowToUser(row));
  }

  /**
   * Get user count (for stats)
   */
  async count(): Promise<number> {
    const result = await database.queryOne<{ count: string }>(`
      SELECT COUNT(*) as count FROM users
    `);

    return parseInt(result?.count || '0', 10);
  }

  /**
   * Clear all users (for testing purposes)
   */
  async clear(): Promise<void> {
    await database.query('DELETE FROM users');
  }

  /**
   * Map database row to InternalUser object
   */
  private mapDatabaseRowToUser(row: any): InternalUser {
    return {
      id: row.id,
      email: row.email,
      passwordHash: row.passwordHash,
      role: row.role,
      apiKeys: [], // API keys are stored in a separate table
      preferences: typeof row.preferences === 'string' ? JSON.parse(row.preferences) : row.preferences,
      isEmailVerified: row.isEmailVerified,
      emailVerificationToken: row.emailVerificationToken,
      emailVerificationExpiresAt: row.emailVerificationExpiresAt,
      lastLoginAt: row.lastLoginAt,
      passwordResetToken: row.passwordResetToken,
      passwordResetExpiresAt: row.passwordResetExpiresAt,
      createdAt: row.createdAt,
      updatedAt: row.updatedAt
    };
  }
}

// Export the database-backed repository
export const databaseUserRepository = new DatabaseUserRepository();

import type { User, UserPreferences } from '@jabbr/shared';

// Extended User interface for internal use (includes additional fields not in shared types)
interface InternalUser extends User {
  isEmailVerified: boolean;
  lastLoginAt: Date | null;
}

// For now, using in-memory storage. In production, this would connect to a real database
// This interface defines the contract for user data access
export interface IUserRepository {
  findById(_id: string): Promise<InternalUser | null>;
  findByEmail(_email: string): Promise<InternalUser | null>;
  create(_userData: Omit<InternalUser, 'id' | 'createdAt' | 'updatedAt'>): Promise<InternalUser>;
  update(_id: string, _userData: Partial<InternalUser>): Promise<InternalUser | null>;
  delete(_id: string): Promise<boolean>;
  updateLastLogin(_id: string): Promise<void>;
  updatePassword(_id: string, _hashedPassword: string): Promise<boolean>;
  emailExists(_email: string): Promise<boolean>;
}

/**
 * In-memory user repository implementation
 * In production, this would be replaced with a database implementation
 */
export class InMemoryUserRepository implements IUserRepository {
  private users: Map<string, InternalUser> = new Map();
  private emailIndex: Map<string, string> = new Map(); // email -> userId mapping

  /**
   * Find user by ID
   */
  async findById(_id: string): Promise<InternalUser | null> {
    const user = this.users.get(_id);
    return user || null;
  }

  /**
   * Find user by email
   */
  async findByEmail(_email: string): Promise<InternalUser | null> {
    const userId = this.emailIndex.get(_email.toLowerCase());
    if (!userId) {return null;}
    return this.findById(userId);
  }

  /**
   * Create a new user
   */
  async create(_userData: Omit<InternalUser, 'id' | 'createdAt' | 'updatedAt'>): Promise<InternalUser> {
    const id = this.generateId();
    const now = new Date();

    // Default user preferences matching the shared types
    const defaultPreferences: UserPreferences = {
      timezone: 'UTC',
      currency: 'USD',
      notifications: {
        email: true,
        browser: true,
        tradingAlerts: true,
        systemAlerts: true,
        riskAlerts: true
      },
      dashboard: {
        theme: 'dark',
        layout: 'standard',
        refreshRate: 30000 // 30 seconds in milliseconds
      }
    };

    const user: InternalUser = {
      id,
      email: _userData.email.toLowerCase(),
      passwordHash: _userData.passwordHash,
      role: _userData.role || 'user',
      apiKeys: [],
      preferences: defaultPreferences,
      isEmailVerified: false,
      lastLoginAt: null,
      createdAt: now,
      updatedAt: now
    };

    this.users.set(id, user);
    this.emailIndex.set(user.email, id);

    return user;
  }

  /**
   * Update user data
   */
  async update(_id: string, _userData: Partial<InternalUser>): Promise<InternalUser | null> {
    const existingUser = this.users.get(_id);
    if (!existingUser) {return null;}

    // If email is being updated, update the email index
    if (_userData.email && _userData.email !== existingUser.email) {
      this.emailIndex.delete(existingUser.email);
      this.emailIndex.set(_userData.email.toLowerCase(), _id);
    }

    const updatedUser: InternalUser = {
      ...existingUser,
      ..._userData,
      id: _id, // Ensure ID cannot be changed
      updatedAt: new Date()
    };

    this.users.set(_id, updatedUser);
    return updatedUser;
  }

  /**
   * Delete user
   */
  async delete(_id: string): Promise<boolean> {
    const user = this.users.get(_id);
    if (!user) {return false;}

    this.users.delete(_id);
    this.emailIndex.delete(user.email);
    return true;
  }

  /**
   * Update last login timestamp
   */
  async updateLastLogin(_id: string): Promise<void> {
    const user = this.users.get(_id);
    if (user) {
      user.lastLoginAt = new Date();
      user.updatedAt = new Date();
      this.users.set(_id, user);
    }
  }

  /**
   * Update user password
   */
  async updatePassword(_id: string, _hashedPassword: string): Promise<boolean> {
    const user = this.users.get(_id);
    if (!user) {return false;}

    user.passwordHash = _hashedPassword;
    user.updatedAt = new Date();
    this.users.set(_id, user);
    return true;
  }

  /**
   * Check if email already exists
   */
  async emailExists(_email: string): Promise<boolean> {
    return this.emailIndex.has(_email.toLowerCase());
  }

  /**
   * Generate a unique ID
   * In production, this would use UUID or database auto-increment
   */
  private generateId(): string {
    return `user_${Date.now()}_${Math.random().toString(36).substring(2)}`;
  }

  /**
   * Get all users (for testing/admin purposes)
   */
  async findAll(): Promise<InternalUser[]> {
    return Array.from(this.users.values());
  }

  /**
   * Clear all users (for testing purposes)
   */
  async clear(): Promise<void> {
    this.users.clear();
    this.emailIndex.clear();
  }

  /**
   * Get user count (for stats)
   */
  async count(): Promise<number> {
    return this.users.size;
  }
}

