  // Configuration
  private readonly BYBIT_WS_URL = 'wss://stream.bybit.com/v5/public/spot';
  private readonly BYBIT_WS_URL_TESTNET = 'wss://stream-testnet.bybit.com/v5/public/spot';
  private readonly MAX_RECONNECT_ATTEMPTS = 10;
  private readonly RECONNECT_DELAY = 5000; // 5 seconds
  private readonly HEARTBEAT_INTERVAL = 20000; // 20 seconds
  private readonly CONNECTION_TIMEOUT = 10000; // 10 seconds

  private isTestnet: boolean;

  constructor(isTestnet = true) {
    super();
    this.isTestnet = isTestnet;
  }

  /**
   * Connect to Bybit WebSocket
   */
  async connect(): Promise<void> {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      console.log('ðŸ“¡ Already connected to Bybit WebSocket');
      return;
    }

    if (this.isConnecting) {
      console.log('ðŸ“¡ Connection to Bybit WebSocket already in progress');
      return;
    }

    this.isConnecting = true;
    const wsUrl = this.isTestnet ? this.BYBIT_WS_URL_TESTNET : this.BYBIT_WS_URL;

    try {
      console.log(`ðŸ“¡ Connecting to Bybit WebSocket: ${wsUrl}`);
      
      this.ws = new WebSocket(wsUrl, {
        handshakeTimeout: this.CONNECTION_TIMEOUT
      });

      this.setupEventHandlers();

      // Wait for connection to be established
      await new Promise<void>((resolve, reject) => {
        const timeout = setTimeout(() => {
          reject(new Error('Connection timeout'));
        }, this.CONNECTION_TIMEOUT);

        this.ws!.once('open', () => {
          clearTimeout(timeout);
          resolve();
        });

        this.ws!.once('error', (error) => {
          clearTimeout(timeout);
          reject(error);
        });
      });

      this.isConnecting = false;
      this.reconnectAttempts = 0;
      console.log('âœ… Connected to Bybit WebSocket');
      
      // Start heartbeat
      this.startHeartbeat();

      // Re-subscribe to previous subscriptions
      this.resubscribeAll();

      this.emit('connected');

    } catch (error) {
      this.isConnecting = false;
      console.error('âŒ Failed to connect to Bybit WebSocket:', error);
      this.emit('error', error);
      
      // Schedule reconnection
      this.scheduleReconnect();
      throw error;
    }
  }

  /**
   * Disconnect from Bybit WebSocket
   */
  disconnect(): void {
    console.log('ðŸ“¡ Disconnecting from Bybit WebSocket');

    // Clear intervals and timeouts
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }

    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }

    // Remove all subscriptions
    this.subscriptions.clear();

    // Close WebSocket connection
    if (this.ws) {
      this.ws.removeAllListeners();
      if (this.ws.readyState === WebSocket.OPEN || this.ws.readyState === WebSocket.CONNECTING) {
        this.ws.close(1000, 'Client disconnecting');
      }
      this.ws = null;
    }

    this.isConnecting = false;
    this.reconnectAttempts = 0;
    
    console.log('âœ… Disconnected from Bybit WebSocket');
    this.emit('disconnected');
  }

  /**
   * Subscribe to market data
   */
  subscribe(topic: string, symbol?: string): void {
    const subscriptionKey = this.getSubscriptionKey(topic, symbol);
    const subscription: SubscriptionConfig = { topic, symbol };

    // Store subscription for reconnection
    this.subscriptions.set(subscriptionKey, subscription);

    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      console.log(`ðŸ“º Queued subscription: ${subscriptionKey} (not connected)`);
      return;
    }

    try {
      const subscribeMessage = {
        op: 'subscribe',
        args: symbol ? [`${topic}.${symbol}`] : [topic]
      };

      console.log(`ðŸ“º Subscribing to Bybit: ${subscriptionKey}`);
      this.ws.send(JSON.stringify(subscribeMessage));

    } catch (error) {
      console.error(`âŒ Failed to subscribe to ${subscriptionKey}:`, error);
      this.emit('error', error);
    }
  }

  /**
   * Unsubscribe from market data
   */
  unsubscribe(topic: string, symbol?: string): void {
    const subscriptionKey = this.getSubscriptionKey(topic, symbol);
    
    // Remove from stored subscriptions
    this.subscriptions.delete(subscriptionKey);

    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      console.log(`ðŸ“º Queued unsubscription: ${subscriptionKey} (not connected)`);
      return;
    }

    try {
      const unsubscribeMessage = {
        op: 'unsubscribe',
        args: symbol ? [`${topic}.${symbol}`] : [topic]
      };

      console.log(`ðŸ“º Unsubscribing from Bybit: ${subscriptionKey}`);
      this.ws.send(JSON.stringify(unsubscribeMessage));

    } catch (error) {
      console.error(`âŒ Failed to unsubscribe from ${subscriptionKey}:`, error);
      this.emit('error', error);
    }
  }

  /**
   * Subscribe to ticker data for a symbol
   */
  subscribeToTicker(symbol: string): void {
    this.subscribe('tickers', symbol);
  }

  /**
   * Subscribe to orderbook data for a symbol
   */
  subscribeToOrderbook(symbol: string, depth = 50): void {
    this.subscribe(`orderbook.${depth}`, symbol);
  }

  /**
   * Subscribe to trade data for a symbol
   */
  subscribeToTrades(symbol: string): void {
    this.subscribe('publicTrade', symbol);
  }

  /**
   * Subscribe to kline/candlestick data
   */
  subscribeToKline(symbol: string, interval = '1m'): void {
    this.subscribe(`kline.${interval}`, symbol);
  }

  /**
   * Get connection status
   */
  isConnected(): boolean {
    return this.ws?.readyState === WebSocket.OPEN;
  }

  /**
   * Get subscription count
   */
  getSubscriptionCount(): number {
    return this.subscriptions.size;
  }

  /**
   * Get active subscriptions
   */
  getSubscriptions(): string[] {
    return Array.from(this.subscriptions.keys());
  }

  /**
   * Setup WebSocket event handlers
   */
  private setupEventHandlers(): void {
    if (!this.ws) {return;}

    this.ws.on('open', this.handleOpen.bind(this));
    this.ws.on('message', this.handleMessage.bind(this));
    this.ws.on('close', this.handleClose.bind(this));
    this.ws.on('error', (error) => {
      this.handleError(error);
      // Emit error for bridge to handle
      this.emit('error', error);
    });
    this.ws.on('ping', this.handlePing.bind(this));
    this.ws.on('pong', this.handlePong.bind(this));
  }

  /**
   * Handle WebSocket open event
   */
  private handleOpen(): void {
    console.log('âœ… Bybit WebSocket connection opened');
  }

  /**
   * Handle incoming WebSocket message
   */
  private handleMessage(data: Buffer): void {
    try {
      const message: BybitWebSocketMessage = JSON.parse(data.toString());

      // Handle different message types
      if (message.success !== undefined) {
        // Subscription response
        this.handleSubscriptionResponse(message);
      } else if (message.topic) {
        // Market data update
        this.handleMarketData(message);
      } else if (message.type === 'pong') {
        // Pong response
        console.log('ðŸ’“ Received pong from Bybit');
      } else {
        console.log('ðŸ“¨ Unknown Bybit message:', message);
      }

    } catch (error) {
      console.error('âŒ Failed to parse Bybit message:', error);
    }
  }

  /**
   * Handle subscription response
   */
  private handleSubscriptionResponse(message: BybitWebSocketMessage): void {
    if (message.success) {
      console.log(`âœ… Bybit subscription successful: ${message.req_id}`);
    } else {
      console.error(`âŒ Bybit subscription failed: ${message.ret_msg}`);
    }
  }

  /**
   * Handle market data update
   */
  private handleMarketData(message: BybitWebSocketMessage): void {
    try {
      const topic = message.topic!;
      const data = message.data;
      const timestamp = message.ts ? new Date(message.ts) : new Date();
      const [topicType, symbol] = this.parseTopic(topic);

      if (topicType === 'tickers' && data) {
        this.handleTickerData(data, symbol, timestamp);
      } else if (topicType === 'publicTrade' && data) {
        this.handleTradeData(data, symbol, timestamp);
      } else if (topicType.startsWith('orderbook') && data) {
        this.handleOrderbookData(data, symbol, timestamp);
      } else if (topicType.startsWith('kline') && data) {
        this.handleKlineData(data, topicType, symbol, timestamp);
      }
    } catch (error) {
      console.error('âŒ Failed to process market data:', error);
    }
  }

  /**
   * Handle ticker data
   */
  private handleTickerData(data: unknown, symbol: string, timestamp: Date): void {
    const tickerData = Array.isArray(data) ? data[0] : data;
    if (tickerData && typeof tickerData === 'object') {
      const marketData: MarketDataMessage = {
        symbol: (tickerData as Record<string, unknown>).symbol as string || symbol,
        price: parseFloat((tickerData as Record<string, unknown>).lastPrice as string || (tickerData as Record<string, unknown>).price as string || '0'),
        volume: parseFloat((tickerData as Record<string, unknown>).volume24h as string || (tickerData as Record<string, unknown>).volume as string || '0'),
        timestamp,
        exchange: 'bybit' as Exchange
      };
      this.emit('marketData', marketData);
      this.emit('ticker', { symbol: marketData.symbol, data: tickerData, timestamp });
    }
  }

  /**
   * Handle trade data
   */
  private handleTradeData(data: unknown, symbol: string, timestamp: Date): void {
    const trades = Array.isArray(data) ? data : [data];
    for (const trade of trades) {
      if (trade && typeof trade === 'object') {
        this.emit('trade', {
          symbol: (trade as Record<string, unknown>).s as string || symbol,
          price: parseFloat((trade as Record<string, unknown>).p as string || '0'),
          quantity: parseFloat((trade as Record<string, unknown>).v as string || '0'),
          side: (trade as Record<string, unknown>).S as string || 'unknown',
          timestamp: new Date((trade as Record<string, unknown>).T as string || timestamp),
          tradeId: (trade as Record<string, unknown>).i
        });
      }
    }
  }

  /**
   * Handle orderbook data
   */
  private handleOrderbookData(data: unknown, symbol: string, timestamp: Date): void {
    if (typeof data === 'object' && data !== null) {
      this.emit('orderbook', {
        symbol: (data as Record<string, unknown>).s as string || symbol,
        bids: (data as Record<string, unknown>).b || [],
        asks: (data as Record<string, unknown>).a || [],
        timestamp: new Date((data as Record<string, unknown>).u as string || timestamp)
      });
    }
  }

  /**
   * Handle kline/candlestick data
   */
  private handleKlineData(data: unknown, topicType: string, symbol: string, timestamp: Date): void {
    const klines = Array.isArray(data) ? data : [data];
    for (const kline of klines) {
      if (kline && typeof kline === 'object') {
        this.emit('kline', {
          symbol: (kline as Record<string, unknown>).symbol as string || symbol,
          interval: topicType.split('.')[1],
          open: parseFloat((kline as Record<string, unknown>).open as string || '0'),
          high: parseFloat((kline as Record<string, unknown>).high as string || '0'),
          low: parseFloat((kline as Record<string, unknown>).low as string || '0'),
          close: parseFloat((kline as Record<string, unknown>).close as string || '0'),
          volume: parseFloat((kline as Record<string, unknown>).volume as string || '0'),
          timestamp: new Date((kline as Record<string, unknown>).start as string || timestamp)
        });
      }
    }
  }

  /**
   * Handle WebSocket close event
   */
  private handleClose(code: number, reason: Buffer): void {
    console.log(`âŒ Bybit WebSocket closed: ${code} - ${reason.toString()}`);
    
    // Clear heartbeat
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }

    // Clear reconnect timeout
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }

    // Clear subscriptions
    this.subscriptions.clear();

    this.emit('disconnected', { code, reason: reason.toString() });

    // Schedule reconnection if not intentional disconnect
    if (code !== 1000) {
      this.scheduleReconnect();
    }
  }

  /**
   * Handle WebSocket error
   */
  private handleError(error: Error): void {
    console.error('âŒ Bybit WebSocket error:', error);
    this.emit('error', error);
  }

  /**
   * Handle ping from server
   */
  private handlePing(data: Buffer): void {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.pong(data);
    }
  }

  /**
   * Handle pong from server
   */
  private handlePong(): void {
    console.log('ðŸ’“ Received pong from Bybit');
  }

  /**
   * Start heartbeat mechanism
   */
  private startHeartbeat(): void {
    this.heartbeatInterval = setInterval(() => {
      if (this.ws && this.ws.readyState === WebSocket.OPEN) {
        const pingMessage = { op: 'ping' };
        this.ws.send(JSON.stringify(pingMessage));
        console.log('ðŸ’“ Sent ping to Bybit');
      }
    }, this.HEARTBEAT_INTERVAL);

    console.log('ðŸ’“ Bybit heartbeat started');
  }

  /**
   * Schedule reconnection attempt
   */
  private scheduleReconnect(): void {
    if (this.reconnectAttempts >= this.MAX_RECONNECT_ATTEMPTS) {
      console.error(`âŒ Max reconnection attempts (${this.MAX_RECONNECT_ATTEMPTS}) reached`);
      this.emit('maxReconnectAttemptsReached');
      return;
    }

    this.reconnectAttempts++;
    const delay = this.RECONNECT_DELAY * Math.pow(2, Math.min(this.reconnectAttempts - 1, 5)); // Exponential backoff

    console.log(`ðŸ”„ Scheduling Bybit reconnection attempt ${this.reconnectAttempts}/${this.MAX_RECONNECT_ATTEMPTS} in ${delay}ms`);

    this.reconnectTimeout = setTimeout(async () => {
      try {
        await this.connect();
      } catch (error) {
        console.error('âŒ Reconnection attempt failed:', error);
      }
    }, delay);
  }

  /**
   * Re-subscribe to all stored subscriptions
   */
  private resubscribeAll(): void {
    if (this.subscriptions.size === 0) {return;}

    console.log(`ðŸ“º Re-subscribing to ${this.subscriptions.size} Bybit subscriptions`);

    for (const [key, subscription] of this.subscriptions) {
      try {
        this.subscribe(subscription.topic, subscription.symbol);
        // No await or delay needed for sync
      } catch (error) {
        console.error(`âŒ Failed to re-subscribe to ${key}:`, error);
      }
    }
  }

  /**
   * Generate subscription key
   */
  private getSubscriptionKey(topic: string, symbol?: string): string {
    return symbol ? `${topic}.${symbol}` : topic;
  }

  /**
   * Parse topic string to extract type and symbol
   */
  private parseTopic(topic: string): [string, string] {
    const parts = topic.split('.');
    if (parts.length >= 2) {
      return [parts[0] || '', parts[parts.length - 1] || ''];
    }
    return [topic, ''];
  }
}

// Export for use in other modules
export default BybitWebSocketClient;

import { EventEmitter } from 'events';

import type { MarketDataMessage} from '@jabbr/shared';
import { CONSTANTS } from '@jabbr/shared';

import BybitWebSocketClient from './bybit-websocket.client';
import type JabbrWebSocketServer from './websocket-server';

/**
 * Subscription tracking
 */
interface SubscriptionInfo {
  channel: string;
  symbol?: string;
  exchange: string;
  subscribers: Set<string>; // session IDs
}

/**
 * WebSocket Bridge
 * Connects internal WebSocket server with external exchange clients
 * Manages subscriptions and data flow between clients and exchanges
 */
export class WebSocketBridge extends EventEmitter {
  private wsServer: JabbrWebSocketServer;
  private bybitClient: BybitWebSocketClient;
  private subscriptions: Map<string, SubscriptionInfo> = new Map();
  private isInitialized = false;

  constructor(wsServer: JabbrWebSocketServer) {
    super();
    this.wsServer = wsServer;
    this.bybitClient = new BybitWebSocketClient(true); // Start with testnet
  }

  /**
   * Initialize the bridge and connect to exchanges
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) {
      console.log('ðŸŒ‰ WebSocket bridge already initialized');
      return;
    }

    console.log('ðŸŒ‰ Initializing WebSocket bridge...');

    try {
      // Setup event handlers for internal WebSocket server
      this.setupInternalServerHandlers();

      // Setup event handlers for exchange clients
      this.setupBybitHandlers();

      // Connect to Bybit
      await this.bybitClient.connect();

      this.isInitialized = true;
      console.log('âœ… WebSocket bridge initialized successfully');
      
      this.emit('initialized');

    } catch (error) {
      console.error('âŒ Failed to initialize WebSocket bridge:', error);
      this.emit('error', error);
      throw error;
    }
  }

  /**
   * Shutdown the bridge
   */
  shutdown(): void {
    console.log('ðŸŒ‰ Shutting down WebSocket bridge...');

    // Disconnect from exchanges
    if (this.bybitClient) {
      this.bybitClient.disconnect();
    }

    // Clear subscriptions
    this.subscriptions.clear();

    this.isInitialized = false;
    console.log('âœ… WebSocket bridge shutdown complete');
    
    this.emit('shutdown');
  }

  /**
   * Setup event handlers for internal WebSocket server
   */
  private setupInternalServerHandlers(): void {
    // Note: We'll hook into the WebSocket server's subscription system
    // For now, we'll create a simple interface
    console.log('ðŸ“¡ Setting up internal WebSocket server handlers');
  }

  /**
   * Setup event handlers for Bybit client
   */
  private setupBybitHandlers(): void {
    // Market data events
    this.bybitClient.on('marketData', (data: MarketDataMessage) => {
      this.handleMarketData('bybit', data);
    });

    this.bybitClient.on('ticker', (data: unknown) => {
      this.handleTickerData('bybit', data);
    });

    this.bybitClient.on('trade', (data: unknown) => {
      this.handleTradeData('bybit', data);
    });

    this.bybitClient.on('orderbook', (data: unknown) => {
      this.handleOrderbookData('bybit', data);
    });

    this.bybitClient.on('kline', (data: unknown) => {
      this.handleKlineData('bybit', data);
    });

    // Connection events
    this.bybitClient.on('connected', () => {
      console.log('âœ… Bybit client connected to bridge');
      this.broadcastSystemHealth();
    });

    this.bybitClient.on('disconnected', () => {
      console.log('âŒ Bybit client disconnected from bridge');
      this.broadcastSystemHealth();
    });

    // Ensure all errors are handled and broadcasted
    this.bybitClient.on('error', (error: Error) => {
      console.error('âŒ Bybit client error in bridge:', error);
      this.broadcastError('bybit', error.message);
      this.broadcastSystemHealth(); // Broadcast health on error
    });

    console.log('ðŸ“¡ Bybit client handlers configured');
  }

  /**
   * Handle market data from exchanges
   */
  private handleMarketData(exchange: string, data: MarketDataMessage): void {
    // Broadcast to subscribers of market-data channel
    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.MARKET_DATA, {
      type: 'data',
      data: {
        ...data,
        exchange,
        timestamp: new Date().toISOString()
      }
    });

    // Also broadcast to symbol-specific channel if subscribers exist
    const symbolChannel = `${CONSTANTS.WS_CHANNELS.MARKET_DATA}.${data.symbol}`;
    this.wsServer.broadcast(symbolChannel, {
      type: 'data',
      data: {
        ...data,
        exchange,
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Handle ticker data from exchanges
   */
  private handleTickerData(exchange: string, data: unknown): void {
    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.MARKET_DATA, {
      type: 'data',
      data: {
        type: 'ticker',
        exchange,
        symbol: (data as { symbol?: string })?.symbol,
        data: (data as { data?: unknown })?.data,
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Handle trade data from exchanges
   */
  private handleTradeData(exchange: string, data: unknown): void {
    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.TRADES, {
      type: 'data',
      data: {
        type: 'trade',
        exchange,
        ...(typeof data === 'object' && data !== null ? data : {}),
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Handle orderbook data from exchanges
   */
  private handleOrderbookData(exchange: string, data: unknown): void {
    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.MARKET_DATA, {
      type: 'data',
      data: {
        type: 'orderbook',
        exchange,
        ...(typeof data === 'object' && data !== null ? data : {}),
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Handle kline/candlestick data from exchanges
   */
  private handleKlineData(exchange: string, data: unknown): void {
    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.MARKET_DATA, {
      type: 'data',
      data: {
        type: 'kline',
        exchange,
        ...(typeof data === 'object' && data !== null ? data : {}),
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Subscribe to market data for a symbol
   */
  async subscribeToMarketData(symbol: string, exchange = 'bybit'): Promise<void> {
    const subscriptionKey = `market-data.${exchange}.${symbol}`;
    
    if (this.subscriptions.has(subscriptionKey)) {
      console.log(`ðŸ“º Already subscribed to ${subscriptionKey}`);
      return;
    }

    try {
      if (exchange === 'bybit') {
        // Subscribe to multiple data types for the symbol
        await Promise.all([
          this.bybitClient.subscribeToTicker(symbol),
          this.bybitClient.subscribeToTrades(symbol),
          this.bybitClient.subscribeToOrderbook(symbol, 50)
        ]);

        // Track subscription
        this.subscriptions.set(subscriptionKey, {
          channel: CONSTANTS.WS_CHANNELS.MARKET_DATA,
          symbol,
          exchange,
          subscribers: new Set()
        });

        console.log(`âœ… Subscribed to market data: ${symbol} on ${exchange}`);
      }
    } catch (error) {
      console.error(`âŒ Failed to subscribe to market data for ${symbol}:`, error);
      throw error;
    }
  }

  /**
   * Unsubscribe from market data for a symbol
   */
  async unsubscribeFromMarketData(symbol: string, exchange = 'bybit'): Promise<void> {
    const subscriptionKey = `market-data.${exchange}.${symbol}`;
    
    if (!this.subscriptions.has(subscriptionKey)) {
      console.log(`ðŸ“º Not subscribed to ${subscriptionKey}`);
      return;
    }

    try {
      if (exchange === 'bybit') {
        // Unsubscribe from all data types for the symbol
        await Promise.all([
          this.bybitClient.unsubscribe('tickers', symbol),
          this.bybitClient.unsubscribe('publicTrade', symbol),
          this.bybitClient.unsubscribe('orderbook.50', symbol)
        ]);

        // Remove subscription tracking
        this.subscriptions.delete(subscriptionKey);

        console.log(`âœ… Unsubscribed from market data: ${symbol} on ${exchange}`);
      }
    } catch (error) {
      console.error(`âŒ Failed to unsubscribe from market data for ${symbol}:`, error);
      throw error;
    }
  }

  /**
   * Subscribe to kline data for a symbol
   */
  async subscribeToKlineData(symbol: string, interval = '1m', exchange = 'bybit'): Promise<void> {
    try {
      if (exchange === 'bybit') {
        await this.bybitClient.subscribeToKline(symbol, interval);
        console.log(`âœ… Subscribed to kline data: ${symbol} ${interval} on ${exchange}`);
      }
    } catch (error) {
      console.error(`âŒ Failed to subscribe to kline data for ${symbol}:`, error);
      throw error;
    }
  }

  /**
   * Broadcast system health status
   */
  private broadcastSystemHealth(): void {
    const healthData = {
      exchanges: {
        bybit: {
          connected: this.bybitClient.isConnected(),
          subscriptions: this.bybitClient.getSubscriptionCount()
        }
      },
      bridge: {
        initialized: this.isInitialized,
        totalSubscriptions: this.subscriptions.size
      },
      timestamp: new Date().toISOString()
    };

    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH, {
      type: 'data',
      data: healthData
    });
  }

  /**
   * Broadcast error message
   */
  private broadcastError(source: string, message: string): void {
    this.wsServer.broadcast(CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH, {
      type: 'error',
      data: {
        source,
        error: message,
        timestamp: new Date().toISOString()
      }
    });
  }

  /**
   * Get bridge statistics
   */
  getStats(): {
    initialized: boolean;
    exchanges: Record<string, unknown>;
    subscriptions: number;
    activeSubscriptions: string[];
  } {
    return {
