    // Wait a moment for initialization
    await new Promise(resolve => setTimeout(resolve, 100));
  });

  afterAll(async () => {
    if (metricsCollector) {
      metricsCollector.stop();
    }
  });

  beforeEach(() => {
    // Reset metrics between tests
    metricsCollector.reset();
  });

