      const status = botCycle.getStatus();
      expect(status.isRunning).toBe(true);
    });

    it('should handle repeated stop calls', async () => {
      await botCycle.initialize();
      await botCycle.start();
      await botCycle.stop();
      await botCycle.stop(); // Should not throw
      await botCycle.stop(); // Should not throw
      
      const status = botCycle.getStatus();
      expect(status.isRunning).toBe(false);
    });

    it('should handle empty market data', async () => {
      await botCycle.initialize();
      await botCycle.start();
      
      await botCycle.processMarketData('BTCUSDT', []);
      
      // Should not crash
      const status = botCycle.getStatus();
      expect(status.isRunning).toBe(true);
    });

    it('should handle malformed market data', async () => {
      await botCycle.initialize();
      await botCycle.start();
      
      const malformedCandles = [
        { open: 'invalid', high: null, low: undefined, close: NaN },
        { timestamp: 'not-a-number' }
      ];
      
      await botCycle.processMarketData('BTCUSDT', malformedCandles);
      
      // Should handle gracefully
      const status = botCycle.getStatus();
      expect(status.isRunning).toBe(true);
    });
  });
});

