      }),
      getCandles: jest.fn().mockResolvedValue(candles),
      getTicker: jest.fn().mockResolvedValue({
        symbol: 'BTC/USDT',
        last: 100,
        bid: 99.5,
        ask: 100.5,
        volume: 10000,
        timestamp: Date.now()
      })
    } as any,
    tradeExecutor: {
