        price: 100,
        status: 'filled',
        filled: 0.01,
        remaining: 0,
        timestamp: Date.now(),
        updatedAt: Date.now()
      }),
      getPosition: jest.fn().mockResolvedValue(null),
      closePosition: jest.fn().mockResolvedValue(undefined)
    } as any,
    logger: {
      info: jest.fn(),
      warn: jest.fn(),
      error: jest.fn(),
      debug: jest.fn()
    } as any,
    storage: {
      get: jest.fn().mockResolvedValue(null),
