'use client';

import { CONSTANTS } from '@jabbr/shared';
import type { MarketDataMessage } from '@jabbr/shared/src/types';
import { useState, useEffect } from 'react';

import useWebSocket from '../hooks/useWebSocket';
import { getStatusColor, getStatusText } from '../utils/connectionStatus';


export default function HomePage() {
  const [messages, setMessages] = useState<string[]>([]);
  const [marketData, setMarketData] = useState<Record<string, MarketDataMessage>>({});

  const {
    isConnected,
    isConnecting,
    connectionError,
    sessionId,
    subscribe,
    reconnect
  } = useWebSocket({
    url: 'ws://localhost:3002/ws',
    // For now, we'll test without authentication
    // token: 'your-jwt-token-here',
    onOpen: () => {
      console.log('ðŸš€ WebSocket connected');
      setMessages((prev: string[]) => [...prev, 'âœ… Connected to WebSocket server']);
      // Subscribe to essential channels
      subscribe(CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH);
      subscribe(CONSTANTS.WS_CHANNELS.BOT_STATUS);
    },
    onMessage: (message) => {
      console.log('ðŸ“¡ Message received:', message);
      setMessages((prev: string[]) => [...prev, `ðŸ“¡ ${message.type}: ${message.channel}`]);
      
      // Handle market data
      if (message.channel === CONSTANTS.WS_CHANNELS.MARKET_DATA && message.data) {
        setMarketData((prev) => ({
          ...prev,
          [message.data.symbol]: message.data as MarketDataMessage
        }));
      }
    },
    onError: (error) => {
      console.error('âŒ WebSocket error:', error);
      setMessages((prev: string[]) => [...prev, 'âŒ Connection error occurred']);
    },
    onClose: (event) => {
      console.log('ðŸ”Œ WebSocket disconnected:', event);
      setMessages((prev: string[]) => [...prev, `ðŸ”Œ Disconnected (${event.code})`]);
    }
  });

  // Keep only last 10 messages
  useEffect(() => {
    if (messages.length > 10) {
      setMessages((prev: string[]) => prev.slice(-10));
    }
  }, [messages]);

  const connectionState = { isConnected, isConnecting, connectionError };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-6xl mx-auto">
        <header className="mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">
            ðŸš€ Jabbr Trading Bot Platform
          </h1>
          <p className="text-lg text-gray-600">
            Real-time cryptocurrency trading dashboard with WebSocket integration
          </p>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {/* WebSocket Status Card */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">WebSocket Status</h3>
            
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">Status:</span>
                <span className={`text-sm font-semibold ${getStatusColor(connectionState)}`}>
                  {getStatusText(connectionState)}
                </span>
              </div>
              
              {sessionId && (
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">Session:</span>
                  <span className="text-xs text-gray-600 font-mono">
                    {sessionId.slice(0, 8)}...
                  </span>
                </div>
              )}
              
              {connectionError && (
                <div className="text-sm text-red-600 bg-red-50 p-2 rounded">
                  {connectionError}
                </div>
              )}
              
              <div className="flex space-x-2 pt-2">
                <button
                  onClick={reconnect}
                  disabled={isConnecting}
                  className="px-3 py-1 text-xs bg-blue-500 text-white rounded hover:bg-blue-600 disabled:opacity-50"
                >
                  Reconnect
                </button>
                
                <button
                  onClick={() => subscribe(CONSTANTS.WS_CHANNELS.MARKET_DATA, 'BTCUSDT')}
                  disabled={!isConnected}
                  className="px-3 py-1 text-xs bg-green-500 text-white rounded hover:bg-green-600 disabled:opacity-50"
                >
                  Sub Market
                </button>
              </div>
            </div>
          </div>

          {/* Market Data Card */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Market Data</h3>
            
            {Object.keys(marketData).length === 0 ? (
              <p className="text-sm text-gray-500">No market data yet...</p>
            ) : (
              <div className="space-y-2">
                {Object.entries(marketData).map(([symbol, data]: [string, MarketDataMessage]) => (
                  <div key={symbol} className="flex justify-between items-center">
                    <span className="text-sm font-medium">{symbol}</span>
                    <div className="text-right">
                      <div className="text-sm font-semibold">${data.price}</div>
                      <div className="text-xs text-gray-500">{new Date(data.timestamp).toLocaleString()}</div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* System Info Card */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">System Info</h3>
            
            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-sm font-medium">Backend:</span>
                <span className="text-sm text-green-600">âœ… Operational</span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm font-medium">Trading Engine:</span>
                <span className="text-sm text-green-600">âœ… Active</span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm font-medium">WebSocket Server:</span>
                <span className={`text-sm ${isConnected ? 'text-green-600' : 'text-red-600'}`}>
                  {isConnected ? 'âœ… Connected' : 'âŒ Disconnected'}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Activity Log */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-4">WebSocket Activity Log</h3>
          
          <div className="bg-gray-50 rounded p-4 max-h-64 overflow-y-auto">
            {messages.length === 0 ? (
              <p className="text-sm text-gray-500">No activity yet...</p>
            ) : (
              messages.map((msg, index) => (
                <div key={index} className="text-sm text-gray-700 mb-1 font-mono">
                  {new Date().toLocaleTimeString()} - {msg}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="mt-8 bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
          
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <button
              onClick={() => subscribe(CONSTANTS.WS_CHANNELS.MARKET_DATA, 'BTCUSDT')}
              disabled={!isConnected}
              className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 disabled:opacity-50"
            >
              Subscribe BTC
            </button>
            
            <button
              onClick={() => subscribe(CONSTANTS.WS_CHANNELS.MARKET_DATA, 'ETHUSDT')}
              disabled={!isConnected}
              className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 disabled:opacity-50"
            >
              Subscribe ETH
            </button>
            
            <button
              onClick={() => subscribe(CONSTANTS.WS_CHANNELS.TRADES)}
              disabled={!isConnected}
              className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:opacity-50"
            >
              Subscribe Trades
            </button>
            
            <button
              onClick={() => subscribe(CONSTANTS.WS_CHANNELS.POSITIONS)}
              disabled={!isConnected}
              className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 disabled:opacity-50"
            >
              Subscribe Positions
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { CONSTANTS } from '@jabbr/shared';
import { useState, useEffect } from 'react';

import useWebSocket from '../hooks/useWebSocket';
import { getStatusColor, getStatusText } from '../utils/connectionStatus';

interface ConnectionStatusProps {
  wsUrl?: string;
  token?: string;
}

const ConnectionStatus = ({ 
  wsUrl = 'ws://localhost:3002/ws', 
  token 
}: ConnectionStatusProps) => {
  const [messages, setMessages] = useState<string[]>([]);

  const {
    isConnected,
    isConnecting,
    connectionError,
    sessionId,
    subscribe,
    reconnect
  } = useWebSocket({
    url: wsUrl,
    token,
    onOpen: () => {
      console.log('ðŸš€ WebSocket connected in ConnectionStatus');
      setMessages(prev => [...prev, 'âœ… Connected to WebSocket server']);
      // Subscribe to system health for basic testing
      subscribe(CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH);
    },
    onMessage: (message) => {
      console.log('ðŸ“¡ Message received:', message);
      setMessages(prev => [...prev, `ðŸ“¡ ${message.type}: ${message.channel}`]);
    },
    onError: (error) => {
      console.error('âŒ WebSocket error:', error);
      setMessages(prev => [...prev, 'âŒ Connection error occurred']);
    },
    onClose: (event) => {
      console.log('ðŸ”Œ WebSocket disconnected:', event);
      setMessages(prev => [...prev, `ðŸ”Œ Disconnected (${event.code})`]);
    }
  });

  // Keep only last 10 messages
  useEffect(() => {
    if (messages.length > 10) {
      setMessages(prev => prev.slice(-10));
    }
  }, [messages]);

  const connectionState = { isConnected, isConnecting, connectionError };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 max-w-md">
      <h3 className="text-lg font-semibold mb-4">WebSocket Status</h3>
      
      <div className="space-y-2">
        <div className="flex items-center justify-between">
          <span className="text-sm font-medium">Status:</span>
          <span className={`text-sm font-semibold ${getStatusColor(connectionState)}`}>
            {getStatusText(connectionState)}
          </span>
        </div>
        
        {sessionId && (
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium">Session:</span>
            <span className="text-xs text-gray-600 font-mono">
              {sessionId.slice(0, 8)}...
            </span>
          </div>
        )}
        
        {connectionError && (
          <div className="text-sm text-red-600 bg-red-50 p-2 rounded">
            {connectionError}
          </div>
        )}
        
        <div className="flex space-x-2 pt-2">
          <button
            onClick={reconnect}
            disabled={isConnecting}
            className="px-3 py-1 text-xs bg-blue-500 text-white rounded hover:bg-blue-600 disabled:opacity-50"
          >
            Reconnect
          </button>
          
          <button
            onClick={() => subscribe(CONSTANTS.WS_CHANNELS.MARKET_DATA, 'BTCUSDT')}
            disabled={!isConnected}
            className="px-3 py-1 text-xs bg-green-500 text-white rounded hover:bg-green-600 disabled:opacity-50"
          >
            Sub Market
          </button>
        </div>
      </div>
      
      <div className="mt-4">
        <h4 className="text-sm font-medium mb-2">Recent Activity:</h4>
        <div className="bg-gray-50 rounded p-2 max-h-32 overflow-y-auto">
          {messages.length === 0 ? (
            <p className="text-xs text-gray-500">No activity yet...</p>
          ) : (
            messages.map((msg, index) => (
              <div key={index} className="text-xs text-gray-700 mb-1">
                {msg}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default ConnectionStatus;

/**
 * Strategy Monitor Component
 * 
 * Real-time monitoring component for strategy performance, signals, and execution.
 * Integrates with WebSocket to display live strategy data.
 */

"use client";

import type { StrategyPerformanceMetrics, SignalSummary} from '@jabbr/shared';
import { CONSTANTS, PositionSummary, RiskMetrics } from '@jabbr/shared';
import React, { useState } from 'react';

import useWebSocket from '../hooks/useWebSocket';

// Temporary type definitions until shared types are fully available
interface StrategyUpdateMessage {
  type: 'performance' | 'signal' | 'trade' | 'position' | 'risk_alert' | 'state_change';
  botId: string;
  strategyName: string;
  data: any; // eslint-disable-line @typescript-eslint/no-explicit-any
  timestamp: Date;
}

interface StrategyMonitorProps {
  botId?: string; // If provided, monitor specific bot, otherwise monitor all
  className?: string;
}

interface StrategyData {
  [botId: string]: StrategyPerformanceMetrics;
}

export const StrategyMonitor: React.FC<StrategyMonitorProps> = ({ 
  botId, 
  className = "" 
}) => {
  const [strategies, setStrategies] = useState<StrategyData>({});
  const [recentSignals, setRecentSignals] = useState<SignalSummary[]>([]);
  const [riskAlerts, setRiskAlerts] = useState<Array<{ botId: string; alerts: string[]; timestamp: Date }>>([]);
  const [selectedBotId, setSelectedBotId] = useState<string | null>(botId || null);

  const {
    isConnected,
    subscribe
  } = useWebSocket({
    url: 'ws://localhost:3002/ws',
    onOpen: () => {
      // Subscribe to strategy monitoring channels
      subscribe(CONSTANTS.WS_CHANNELS.SIGNALS);
      subscribe(CONSTANTS.WS_CHANNELS.BOT_STATUS);
      subscribe(CONSTANTS.WS_CHANNELS.POSITIONS);
      subscribe(CONSTANTS.WS_CHANNELS.TRADES);
    },
    onMessage: (message: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
      handleWebSocketMessage(message);
    }
  });

  const handleWebSocketMessage = (message: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
    if (message.channel === CONSTANTS.WS_CHANNELS.SIGNALS && message.data) {
      const update: StrategyUpdateMessage = message.data;
      handleStrategyUpdate(update);
    } else if (message.channel === CONSTANTS.WS_CHANNELS.BOT_STATUS && message.data) {
      handleBotStatusUpdate(message.data);
    }
  };

  const handleStrategyUpdate = (update: StrategyUpdateMessage) => {
    const { type, botId: updateBotId, data, timestamp } = update;

    // Filter by specific bot if specified
    if (botId && updateBotId !== botId) {return;}

    switch (type) {
      case 'performance':
        if (data.metrics) {
          setStrategies(prev => ({
            ...prev,
            [updateBotId]: data.metrics
          }));
        }
        break;

      case 'signal':
        if (data.signal) {
          setRecentSignals(prev => {
            // Keep last 20 signals
            return [data.signal, ...prev].slice(0, 20);
          });
        }
        break;

      case 'trade':
        // Update strategy metrics if trade data includes metrics
        if (data.metrics) {
          setStrategies(prev => ({
            ...prev,
            [updateBotId]: data.metrics
          }));
        }
        break;

      case 'state_change':
        if (data.metrics) {
          setStrategies(prev => ({
            ...prev,
            [updateBotId]: data.metrics
          }));
        }
        break;

      case 'risk_alert':
        if (data.alerts) {
          setRiskAlerts(prev => {
            const newAlert = {
              botId: updateBotId,
              alerts: data.alerts,
              timestamp: new Date(timestamp)
            };
            return [newAlert, ...prev].slice(0, 10); // Keep last 10 alerts
          });
        }
        break;
    }
  };

  const handleBotStatusUpdate = (data: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
    if (data.botId && data.strategy) {
      // Filter by specific bot if specified
      if (botId && data.botId !== botId) {return;}

      setStrategies(prev => ({
        ...prev,
        [data.botId]: {
          ...prev[data.botId],
          ...data.strategy,
          timestamp: new Date()
        }
      }));
    }
  };

  const getStrategyMetrics = (botId: string): StrategyPerformanceMetrics | null => {
    return strategies[botId] || null;
  };

  const formatCurrency = (amount: number): string => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount);
  };

  const formatPercentage = (value: number): string => {
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const formatUptime = (uptime: number): string => {
    const seconds = Math.floor(uptime / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    
    if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } 
      return `${seconds}s`;
    
  };

  const getRiskColor = (riskScore: number): string => {
    if (riskScore <= 3) {return 'text-green-600 bg-green-100';}
    if (riskScore <= 6) {return 'text-yellow-600 bg-yellow-100';}
    return 'text-red-600 bg-red-100';
  };

  const getStatusColor = (isRunning: boolean): string => {
    return isRunning ? 'text-green-600 bg-green-100' : 'text-gray-600 bg-gray-100';
  };

  const selectedStrategy = selectedBotId ? getStrategyMetrics(selectedBotId) : null;
  const allStrategies = Object.entries(strategies);

  return (
    <div className={`strategy-monitor ${className}`}>
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">
            ðŸ“Š Strategy Monitor
          </h2>
          <p className="text-gray-600">
            Real-time strategy performance and signal monitoring
          </p>
        </div>
        
        <div className="flex items-center space-x-4">
          <span className={`text-sm font-semibold ${isConnected ? 'text-green-600' : 'text-red-600'}`}>
            {isConnected ? 'ðŸŸ¢ Live' : 'ðŸ”´ Offline'}
          </span>
          
          {allStrategies.length > 1 && (
            <select
              value={selectedBotId || ''}
              onChange={(e) => setSelectedBotId(e.target.value || null)}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm"
            >
              <option value="">All Strategies</option>
              {allStrategies.map(([botId, strategy]) => (
                <option key={botId} value={botId}>
                  {strategy.strategyName} ({botId})
                </option>
              ))}
            </select>
          )}
        </div>
      </div>

      {/* Risk Alerts */}
      {riskAlerts.length > 0 && (
        <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
          <h3 className="text-lg font-semibold text-red-800 mb-2">âš ï¸ Risk Alerts</h3>
          <div className="space-y-2">
            {riskAlerts.slice(0, 3).map((alert, index) => (
              <div key={index} className="flex justify-between items-center">
                <div>
                  <span className="font-medium text-red-700">Bot {alert.botId}:</span>
                  <span className="ml-2 text-red-600">{alert.alerts.join(', ')}</span>
                </div>
                <span className="text-xs text-red-500">
                  {alert.timestamp.toLocaleTimeString()}
                </span>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Strategy Performance Overview */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Strategy Performance</h3>
            
            {allStrategies.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-6xl mb-4">ðŸ“Š</div>
                <p className="text-gray-600">No active strategies found</p>
                <p className="text-sm text-gray-500 mt-2">
                  Start a bot to see real-time strategy monitoring
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {(selectedStrategy ? [[selectedBotId!, selectedStrategy]] : allStrategies)
                  .filter(([, strategy]) => typeof strategy === 'object' && strategy !== null)
                  .map(([strategyBotId, strategyData]) => {
                    const strategy = strategyData as StrategyPerformanceMetrics;
                    return (
                  <div key={String(strategyBotId)} className="border border-gray-200 rounded-lg p-4">
                    {/* Strategy Header */}
                    <div className="flex justify-between items-center mb-3">
                      <div>
                        <h4 className="font-semibold text-gray-900">
                          {strategy.strategyName} v{strategy.strategyVersion}
                        </h4>
                        <p className="text-sm text-gray-600">Bot ID: {String(strategyBotId)}</p>
                      </div>
                      
                      <div className="flex items-center space-x-3">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(strategy.isRunning)}`}>
                          {strategy.isRunning ? 'ðŸŸ¢ Running' : 'âš« Stopped'}
                        </span>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getRiskColor(strategy.riskMetrics.riskScore)}`}>
                          Risk: {strategy.riskMetrics.riskScore}/10
                        </span>
                      </div>
                    </div>

                    {/* Key Metrics */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                      <div className="text-center">
                        <div className={`text-xl font-bold ${strategy.totalPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {formatCurrency(strategy.totalPnL)}
                        </div>
                        <div className="text-xs text-gray-600">Total P&L</div>
                      </div>
                      
                      <div className="text-center">
                        <div className={`text-xl font-bold ${strategy.dailyPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {formatCurrency(strategy.dailyPnL)}
                        </div>
                        <div className="text-xs text-gray-600">Daily P&L</div>
                      </div>
                      
                      <div className="text-center">
                        <div className="text-xl font-bold text-gray-900">
                          {strategy.winRate.toFixed(1)}%
                        </div>
                        <div className="text-xs text-gray-600">Win Rate</div>
                      </div>
                      
                      <div className="text-center">
                        <div className="text-xl font-bold text-gray-900">
                          {strategy.totalTrades}
                        </div>
                        <div className="text-xs text-gray-600">Total Trades</div>
                      </div>
                    </div>

                    {/* Detailed Stats */}
                    <div className="grid grid-cols-3 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Uptime:</span>
                        <span className="ml-2 font-medium">{formatUptime(strategy.uptime)}</span>
                      </div>
                      
                      <div>
                        <span className="text-gray-600">Max Drawdown:</span>
                        <span className="ml-2 font-medium text-red-600">
                          {formatPercentage(strategy.maxDrawdown)}
                        </span>
                      </div>
                      
                      <div>
                        <span className="text-gray-600">Current Drawdown:</span>
                        <span className="ml-2 font-medium text-orange-600">
                          {formatPercentage(strategy.currentDrawdown)}
                        </span>
                      </div>
                    </div>

                    {/* Current Positions */}
                    {strategy.currentPositions && strategy.currentPositions.length > 0 && (
                      <div className="mt-4 pt-4 border-t border-gray-200">
                        <h5 className="font-medium text-gray-900 mb-2">Current Positions</h5>
                        <div className="space-y-2">
                          {strategy.currentPositions.map((position: any, index: number) => ( // eslint-disable-line @typescript-eslint/no-explicit-any
                            <div key={index} className="flex justify-between items-center text-sm">
                              <span className="font-medium">
                                {position.symbol} {position.side.toUpperCase()}
                              </span>
                              <span className="text-gray-600">
                                Size: {position.size} @ {formatCurrency(position.entryPrice)}
                              </span>
                              <span className={`font-medium ${position.unrealizedPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                                {formatCurrency(position.unrealizedPnL)}
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                    );
                  })}
              </div>
            )}
          </div>
        </div>

        {/* Recent Signals */}
        <div>
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Recent Signals</h3>
            
            {recentSignals.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-4xl mb-2">ðŸ“¡</div>
                <p className="text-gray-600 text-sm">No signals yet</p>
              </div>
            ) : (
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {recentSignals.map((signal) => (
                  <div key={signal.id} className="border border-gray-200 rounded p-3">
                    <div className="flex justify-between items-center mb-2">
                      <span className="font-medium text-gray-900">
                        {signal.symbol}
                      </span>
                      <span className={`px-2 py-1 rounded text-xs font-medium ${
                        signal.side === 'buy' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                      }`}>
                        {signal.side.toUpperCase()}
                      </span>
                    </div>
                    
                    <div className="text-sm text-gray-600 space-y-1">
                      <div className="flex justify-between">
                        <span>Price:</span>
                        <span className="font-medium">{formatCurrency(signal.price)}</span>
                      </div>
                      
                      <div className="flex justify-between">
                        <span>Confidence:</span>
                        <span className="font-medium">{(signal.confidence * 100).toFixed(1)}%</span>
                      </div>
                      
                      <div className="flex justify-between">
                        <span>Time:</span>
                        <span className="font-medium">
                          {signal.timestamp.toLocaleTimeString()}
                        </span>
                      </div>
                      
                      <div className="flex justify-between">
                        <span>Status:</span>
                        <span className={`font-medium ${
                          signal.executed 
                            ? signal.result === 'win' 
                              ? 'text-green-600' 
                              : signal.result === 'loss' 
                                ? 'text-red-600' 
                                : 'text-blue-600'
                            : 'text-yellow-600'
                        }`}>
                          {signal.executed 
                            ? signal.result?.toUpperCase() || 'EXECUTED' 
                            : 'PENDING'}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Summary Statistics */}
      {allStrategies.length > 1 && !selectedBotId && (
        <div className="mt-6 bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-4">Portfolio Summary</h3>
          <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">{allStrategies.length}</div>
              <div className="text-sm text-gray-600">Active Strategies</div>
            </div>
            
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">
                {allStrategies.filter(([, strategy]) => strategy.isRunning).length}
              </div>
              <div className="text-sm text-gray-600">Running</div>
            </div>
            
            <div className="text-center">
              <div className={`text-2xl font-bold ${
                allStrategies.reduce((sum, [, strategy]) => sum + strategy.totalPnL, 0) >= 0 ? 'text-green-600' : 'text-red-600'
              }`}>
                {formatCurrency(allStrategies.reduce((sum, [, strategy]) => sum + strategy.totalPnL, 0))}
              </div>
              <div className="text-sm text-gray-600">Total P&L</div>
            </div>
            
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {allStrategies.reduce((sum, [, strategy]) => sum + strategy.totalTrades, 0)}
              </div>
              <div className="text-sm text-gray-600">Total Trades</div>
            </div>
            
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {allStrategies.length > 0 
                  ? (allStrategies.reduce((sum, [, strategy]) => sum + strategy.winRate, 0) / allStrategies.length).toFixed(1)
                  : '0.0'
                }%
              </div>
              <div className="text-sm text-gray-600">Avg Win Rate</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StrategyMonitor;

'use client';

import type { WebSocketResponse} from '@jabbr/shared';
import { CONSTANTS } from '@jabbr/shared';
import React, { createContext, useContext, useState, useCallback } from 'react';

import useWebSocket from '../hooks/useWebSocket';

interface MarketData {
  symbol: string;
  price: number;
  volume: number;
  timestamp: string;
  change24h?: number;
  changePercent24h?: number;
}

interface BotStatus {
  botId: string;
  status: string;
  performance?: {
    totalPnL: number;
    winRate: number;
    totalTrades: number;
  };
  timestamp: string;
}

interface SystemHealth {
  status: 'healthy' | 'degraded' | 'unhealthy';
  services: Array<{
    name: string;
    status: 'up' | 'down' | 'degraded';
    responseTime?: number;
  }>;
  timestamp: string;
}

interface WebSocketContextValue {
  // Connection state
  isConnected: boolean;
  isConnecting: boolean;
  connectionError: string | null;
  sessionId: string | null;
  
  // Data state
  marketData: Record<string, MarketData>;
  botStatuses: Record<string, BotStatus>;
  systemHealth: SystemHealth | null;
  
  // Actions
  subscribe: (channel: string, symbol?: string) => void;
  unsubscribe: (channel: string, symbol?: string) => void;
  reconnect: () => void;
  
  // Bot actions
  startBot: (botId: string) => void;
  stopBot: (botId: string) => void;
  pauseBot: (botId: string) => void;
}

const WebSocketContext = createContext<WebSocketContextValue | undefined>(undefined);

interface WebSocketProviderProps {
  children: React.ReactNode;
  wsUrl?: string;
  token?: string;
}

export const WebSocketProvider: React.FC<WebSocketProviderProps> = ({ 
  children, 
  wsUrl = 'ws://localhost:3002/ws',
  token 
}) => {
  // Data state
  const [marketData, setMarketData] = useState<Record<string, MarketData>>({});
  const [botStatuses, setBotStatuses] = useState<Record<string, BotStatus>>({});
  const [systemHealth, setSystemHealth] = useState<SystemHealth | null>(null);

  // Message handler
  const handleMessage = useCallback((message: WebSocketResponse) => {
    console.log('ðŸ“¡ Received WebSocket message:', message);

    switch (message.channel) {
      case CONSTANTS.WS_CHANNELS.MARKET_DATA:
        if (message.type === 'data' && message.data) {
          const data = message.data as MarketData;
          setMarketData((prev: Record<string, MarketData>) => ({
            ...prev,
            [data.symbol]: data
          }));
        }
        break;

      case CONSTANTS.WS_CHANNELS.BOT_STATUS:
        if (message.type === 'data' && message.data) {
          const data = message.data as BotStatus;
          setBotStatuses((prev: Record<string, BotStatus>) => ({
            ...prev,
            [data.botId]: data
          }));
        }
        break;

      case CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH:
        if (message.type === 'data' && message.data) {
          setSystemHealth(message.data as SystemHealth);
        }
        break;

      case CONSTANTS.WS_CHANNELS.TRADES:
        // Handle trade updates
        console.log('ðŸ“ˆ Trade update:', message.data);
        break;

      case CONSTANTS.WS_CHANNELS.POSITIONS:
        // Handle position updates
        console.log('ðŸ“Š Position update:', message.data);
        break;

      case CONSTANTS.WS_CHANNELS.SIGNALS:
        // Handle trading signals
        console.log('ðŸŽ¯ Signal received:', message.data);
        break;

      case CONSTANTS.WS_CHANNELS.TIME_SYNC:
        // Handle time synchronization
        console.log('ðŸ• Time sync:', message.data);
        break;

      default:
        console.log('â“ Unknown channel:', message.channel, message.data);
    }
  }, []);

  // Initialize WebSocket connection
  const {
    isConnected,
    isConnecting,
    connectionError,
    sessionId,
    sendMessage,
    subscribe,
    unsubscribe,
    reconnect
  } = useWebSocket({
    url: wsUrl,
    token,
    onMessage: handleMessage,
    onOpen: () => {
      console.log('ðŸš€ WebSocket connected - subscribing to default channels');
      // Auto-subscribe to essential channels
      subscribe(CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH);
      subscribe(CONSTANTS.WS_CHANNELS.BOT_STATUS);
      subscribe(CONSTANTS.WS_CHANNELS.TIME_SYNC);
    },
    onError: (error) => {
      console.error('âŒ WebSocket error:', error);
    },
    onClose: (event) => {
      console.log('ðŸ”Œ WebSocket disconnected:', event.code, event.reason);
      // Clear data on disconnect
      setMarketData({});
      setBotStatuses({});
      setSystemHealth(null);
    }
  });

  // Bot control actions
  const startBot = useCallback((botId: string) => {
    sendMessage({
      type: 'bot_command',
      channel: CONSTANTS.WS_CHANNELS.BOT_STATUS,
      data: {
        action: 'start',
        botId
      }
    });
  }, [sendMessage]);

  const stopBot = useCallback((botId: string) => {
    sendMessage({
      type: 'bot_command',
      channel: CONSTANTS.WS_CHANNELS.BOT_STATUS,
      data: {
        action: 'stop',
        botId
      }
    });
  }, [sendMessage]);

  const pauseBot = useCallback((botId: string) => {
    sendMessage({
      type: 'bot_command',
      channel: CONSTANTS.WS_CHANNELS.BOT_STATUS,
      data: {
        action: 'pause',
        botId
      }
    });
  }, [sendMessage]);

  const contextValue: WebSocketContextValue = {
    // Connection state
    isConnected,
    isConnecting,
    connectionError,
    sessionId,
    
    // Data state
    marketData,
    botStatuses,
    systemHealth,
    
    // Actions
    subscribe,
    unsubscribe,
    reconnect,
    
    // Bot actions
    startBot,
    stopBot,
    pauseBot
  };

  return (
    <WebSocketContext.Provider value={contextValue}>
      {children}
    </WebSocketContext.Provider>
  );
};

export const useWebSocketContext = (): WebSocketContextValue => {
  const context = useContext(WebSocketContext);
  if (!context) {
    throw new Error('useWebSocketContext must be used within a WebSocketProvider');
  }
  return context;
};

export default WebSocketContext;

import type { WebSocketMessage, WebSocketResponse} from '@jabbr/shared';
import { CONSTANTS } from '@jabbr/shared';
import { useState, useEffect, useRef, useCallback } from 'react';

interface WebSocketOptions {
  url: string;
  token?: string;
  onOpen?: () => void;
  onMessage?: (message: WebSocketResponse) => void;
  onError?: (event: Event) => void;
  onClose?: (event: CloseEvent) => void;
  reconnectInterval?: number;
  maxReconnectAttempts?: number;
  heartbeatInterval?: number;
}

interface UseWebSocketReturn {
  socket: WebSocket | null;
  isConnected: boolean;
  isConnecting: boolean;
  connectionError: string | null;
  sessionId: string | null;
  sendMessage: (message: WebSocketMessage) => void;
  subscribe: (channel: string, symbol?: string) => void;
  unsubscribe: (channel: string, symbol?: string) => void;
  reconnect: () => void;
}

const useWebSocket = (options: WebSocketOptions): UseWebSocketReturn => {
  const {
    url,
    token,
    onOpen,
    onMessage,
    onError,
    onClose,
    reconnectInterval = 3000,
    maxReconnectAttempts = 10,
    heartbeatInterval = 30000
  } = options;

  const [socket, setSocket] = useState<WebSocket | null>(null);
  const [isConnected, setIsConnected] = useState<boolean>(false);
  const [isConnecting, setIsConnecting] = useState<boolean>(false);
  const [connectionError, setConnectionError] = useState<string | null>(null);
  const [sessionId, setSessionId] = useState<string | null>(null);

  const reconnectAttempts = useRef(0);
  const heartbeatTimer = useRef<NodeJS.Timeout | null>(null);
  const reconnectTimer = useRef<NodeJS.Timeout | null>(null);
  const isManualClose = useRef(false);

  const clearTimers = useCallback(() => {
    if (heartbeatTimer.current) {
      clearInterval(heartbeatTimer.current);
      heartbeatTimer.current = null;
    }
    if (reconnectTimer.current) {
      clearTimeout(reconnectTimer.current);
      reconnectTimer.current = null;
    }
  }, []);

  const startHeartbeat = useCallback(() => {
    clearTimers();
    heartbeatTimer.current = setInterval(() => {
      if (socket && socket.readyState === WebSocket.OPEN) {
        const pingMessage: WebSocketMessage = {
          type: 'ping',
          channel: CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH,
          data: { timestamp: new Date().toISOString() }
        };
        socket.send(JSON.stringify(pingMessage));
      }
    }, heartbeatInterval);
  }, [socket, heartbeatInterval, clearTimers]);

  const connect = useCallback(() => {
    if (isConnecting || (socket && socket.readyState === WebSocket.CONNECTING)) {
      return;
    }

    setIsConnecting(true);
    setConnectionError(null);
    isManualClose.current = false;

    try {
      const wsUrl = token ? `${url}?token=${encodeURIComponent(token)}` : url;
      const ws = new WebSocket(wsUrl);
      
      setSocket(ws);

      ws.onopen = () => {
        console.log('âœ… WebSocket connected');
        setIsConnected(true);
        setIsConnecting(false);
        setConnectionError(null);
        reconnectAttempts.current = 0;
        startHeartbeat();
        onOpen?.();
      };

      ws.onmessage = (event) => {
        try {
          const message: WebSocketResponse = JSON.parse(event.data);
          
          // Handle system messages
          if (message.type === 'connection' && message.channel === CONSTANTS.WS_CHANNELS.SYSTEM_HEALTH) {
            if (message.data?.sessionId) {
              setSessionId(message.data.sessionId);
            }
          } else if (message.type === 'pong') {
            // Handle pong response - connection is alive
            console.log('ðŸ“¡ Received pong from server');
          } else if (message.type === 'error') {
            console.error('âŒ WebSocket error message:', message.data);
            setConnectionError(message.data?.error || 'Unknown error');
          } else {
            // Pass other messages to the handler
            onMessage?.(message);
          }
        } catch (error) {
          console.error('âŒ Error parsing WebSocket message:', error);
        }
      };

      ws.onerror = (event) => {
        console.error('âŒ WebSocket error:', event);
        setConnectionError('Connection error occurred');
        setIsConnecting(false);
        onError?.(event);
      };

      ws.onclose = (event) => {
        console.log('ðŸ”Œ WebSocket disconnected:', event.code, event.reason);
        setIsConnected(false);
        setIsConnecting(false);
        setSocket(null);
        clearTimers();
        
        onClose?.(event);

        // Auto-reconnect unless it was a manual close
        if (!isManualClose.current && reconnectAttempts.current < maxReconnectAttempts) {
          const delay = Math.min(reconnectInterval * Math.pow(2, reconnectAttempts.current), 30000);
          console.log(`ðŸ”„ Attempting to reconnect in ${delay}ms (attempt ${reconnectAttempts.current + 1}/${maxReconnectAttempts})`);
          
          reconnectTimer.current = setTimeout(() => {
            reconnectAttempts.current++;
            connect();
          }, delay);
        } else if (reconnectAttempts.current >= maxReconnectAttempts) {
          console.warn('âŒ Max reconnect attempts reached');
          setConnectionError('Failed to reconnect after multiple attempts');
        }
      };

    } catch (error) {
      console.error('âŒ Failed to create WebSocket connection:', error);
      setConnectionError('Failed to create connection');
      setIsConnecting(false);
    }
  }, [url, token, onOpen, onMessage, onError, onClose, reconnectInterval, maxReconnectAttempts, startHeartbeat, clearTimers, isConnecting, socket]);

  const sendMessage = useCallback((message: WebSocketMessage) => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      try {
        const messageWithTimestamp = {
          ...message,
          timestamp: new Date()
        };
        socket.send(JSON.stringify(messageWithTimestamp));
      } catch (error) {
        console.error('âŒ Failed to send WebSocket message:', error);
      }
    } else {
      console.warn('âš ï¸ WebSocket not connected. Message not sent:', message);
    }
  }, [socket]);

  const subscribe = useCallback((channel: string, symbol?: string) => {
    const subscribeMessage: WebSocketMessage = {
      type: 'subscribe',
      channel,
      data: symbol ? { symbol } : {}
    };
    sendMessage(subscribeMessage);
  }, [sendMessage]);

  const unsubscribe = useCallback((channel: string, symbol?: string) => {
    const unsubscribeMessage: WebSocketMessage = {
      type: 'unsubscribe',
      channel,
      data: symbol ? { symbol } : {}
    };
    sendMessage(unsubscribeMessage);
  }, [sendMessage]);

  const reconnect = useCallback(() => {
    if (socket) {
      isManualClose.current = true;
      socket.close();
    }
    reconnectAttempts.current = 0;
    connect();
  }, [socket, connect]);

  const disconnect = useCallback(() => {
    isManualClose.current = true;
    clearTimers();
    if (socket) {
      socket.close();
    }
  }, [socket, clearTimers]);

  // Initialize connection
  useEffect(() => {
    connect();
    
    // Cleanup on unmount
    return () => {
      disconnect();
    };
  }, [connect, disconnect]);

  // Cleanup timers on unmount
  useEffect(() => {
    return () => {
      clearTimers();
    };
  }, [clearTimers]);

  return {
    socket,
    isConnected,
    isConnecting,
    connectionError,
    sessionId,
    sendMessage,
    subscribe,
    unsubscribe,
    reconnect
  };
};

export default useWebSocket;

