    }
    
    if (exactDuplicates.length === 0 && codeBlockDuplicates.length === 0) {
      recommendations.push('Code duplication levels are acceptable');
      recommendations.push('Continue monitoring with automated checks');
    }
    
    return recommendations;
  }

