    } else if (priceSource === 'average') {
      const avgPeriod = typeof averagePeriod === 'number' && !isNaN(averagePeriod) ? averagePeriod : 20;
      const candles: Candle[] = await this.context.marketData.getCandles(this.context.symbol, '1h', avgPeriod);
      const priceKey: keyof Candle = (averageType ?? 'low') as keyof Candle;
      targetPrice = candles.length > 0
        ? candles.reduce((sum, c) => {
            const val = c[priceKey];
            return sum + (typeof val === 'number' ? val : Number(val) || 0);
          }, 0) / candles.length
        : undefined;
    } else {
      throw new Error('Unsupported price source');
    }

    if (typeof targetPrice === 'number') {
      if (currentPrice <= targetPrice) {
        return { success: true, action: 'buy', reason: `Current price <= target (${targetPrice})` };
      } else if (currentPrice >= targetPrice) {
        return { success: true, action: 'sell', reason: `Current price >= target (${targetPrice})` };
      }
    }
    return { success: true, action: 'hold', reason: 'No signal' };
  }

  async cleanup(): Promise<void> {
    // No-op for pure signal strategy
  }

  validateConfig(config: Record<string, unknown>): ConfigValidationResult {
    return validateConfig(config, (validator: ConfigValidator) => {
      validator.required(config, 'parameters');
      const params = config.parameters as Record<string, unknown>;
      validator.string(params, 'priceSource', { enum: ['fixed', 'average'], fieldName: 'Price Source' });
      if (params.priceSource === 'fixed') {
        validator.number(params, 'fixedPrice', { min: 0, fieldName: 'Fixed Price' });
      } else if (params.priceSource === 'average') {
        validator.number(params, 'averagePeriod', { min: 1, max: 500, integer: true, fieldName: 'Average Period' });
        validator.string(params, 'averageType', { enum: ['low', 'close', 'high', 'open'], fieldName: 'Average Type' });
      }
    });
  }

  getDefaultConfig(): StrategyConfig {
    // Provide a unique default config for TargetReacherStrategy
    return {
      type: 'target-reacher',
      parameters: {
        priceSource: 'fixed',
        fixedPrice: 100,
        averagePeriod: 10,
        averageType: 'close',
      },
    };
  }

  // Fix: Implement getState to return a valid StrategyState
  getState(): StrategyState {
    return {
      isRunning: false,
      totalProfit: 0,
      tradesExecuted: 0,
      lastUpdate: new Date(),
      customState: {},
    };
  }
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  setState(_state: Partial<StrategyState>): void {
    // No-op for pure signal strategy
  }
}

import { CONSTANTS } from '@jabbr/shared';
import type { Request, Response, NextFunction } from 'express';

import { AuthService } from './auth.service';

// Extend Express Request type to include user information
declare global {
  namespace Express {
    interface Request {
      user?: {
        userId: string;
        email: string;
      };
    }
  }
}

export class AuthMiddleware {
  private authService: AuthService;

  constructor() {
    this.authService = new AuthService();
  }

  /**
   * Middleware to require authentication
   * Verifies JWT token and attaches user info to request
   */
  requireAuth = (req: Request, res: Response, next: NextFunction): void => {
    try {
      const authHeader = req.headers.authorization;
      const token = this.authService.extractTokenFromHeader(authHeader);

      if (!token) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'No authentication token provided',
            details: 'Authorization header with Bearer token is required'
          }
        });
        return;
      }

      const decoded = this.authService.verifyAccessToken(token);

      if (!decoded) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid or expired token',
            details: 'Please login again to get a new token'
          }
        });
        return;
      }

      // Attach user info to request for use in route handlers
      req.user = {
        userId: decoded.userId,
        email: decoded.email
      };

      next();
    } catch (error) {
      console.error('Auth middleware error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Authentication error',
          details: 'An error occurred while verifying authentication'
        }
      });
    }
  };

  /**
   * Optional authentication middleware
   * Attaches user info if token is valid, but doesn't require it
   */
  optionalAuth = (req: Request, res: Response, next: NextFunction): void => {
    try {
      const authHeader = req.headers.authorization;
      const token = this.authService.extractTokenFromHeader(authHeader);

      if (token) {
        const decoded = this.authService.verifyAccessToken(token);
        if (decoded) {
          req.user = {
            userId: decoded.userId,
            email: decoded.email
          };
        }
      }

      next();
    } catch (error) {
      // Log error but don't block the request
      console.error('Optional auth middleware error:', error);
      next();
    }
  };

  /**
   * Middleware to check if user owns a resource
   * Must be used after requireAuth middleware
   */
  requireOwnership = (resourceUserIdField = 'userId') => {
    return (req: Request, res: Response, next: NextFunction): void => {
      if (!req.user) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Authentication required',
            details: 'This middleware must be used after requireAuth'
          }
        });
        return;
      }

      // Check if the resource belongs to the authenticated user
      // This can be used with req.params, req.body, or req.query
      // Validate the field name to prevent object injection
      if (typeof resourceUserIdField !== 'string' || !resourceUserIdField) {
        res.status(500).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHORIZATION_ERROR,
            message: 'Invalid resource field configuration'
          }
        });
        return;
      }

      const resourceUserId = (req.params && Object.prototype.hasOwnProperty.call(req.params, resourceUserIdField) ? req.params[resourceUserIdField] : null) || 
                           (req.body && Object.prototype.hasOwnProperty.call(req.body, resourceUserIdField) ? req.body[resourceUserIdField] : null) || 
                           (req.query && Object.prototype.hasOwnProperty.call(req.query, resourceUserIdField) ? req.query[resourceUserIdField] : null);

      if (!resourceUserId) {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Resource user ID not found',
            details: `${resourceUserIdField} must be provided in request parameters, body, or query`
          }
        });
        return;
      }

      if (req.user.userId !== resourceUserId) {
        res.status(403).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHORIZATION_ERROR,
            message: 'Access denied',
            details: 'You can only access your own resources'
          }
        });
        return;
      }

      next();
    };
  };

  /**
   * Rate limiting middleware for authentication endpoints
   * Simple in-memory rate limiter (in production, use Redis)
   */
  createRateLimiter = (maxAttempts = 5, windowMs: number = 15 * 60 * 1000) => {
    const attempts = new Map<string, { count: number; resetTime: number }>();

    return (req: Request, res: Response, next: NextFunction): void => {
      const clientIp = req.ip || req.connection.remoteAddress || 'unknown';
      const now = Date.now();

      // Clean up old entries
      for (const [ip, data] of attempts.entries()) {
        if (now > data.resetTime) {
          attempts.delete(ip);
        }
      }

      const clientAttempts = attempts.get(clientIp);

      if (!clientAttempts) {
        attempts.set(clientIp, { count: 1, resetTime: now + windowMs });
        next();
        return;
      }

      if (now > clientAttempts.resetTime) {
        // Reset window
        attempts.set(clientIp, { count: 1, resetTime: now + windowMs });
        next();
        return;
      }

      if (clientAttempts.count >= maxAttempts) {
        const remainingTime = Math.ceil((clientAttempts.resetTime - now) / 1000);
        res.status(429).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.RATE_LIMIT_ERROR,
            message: 'Too many attempts',
            details: `Please try again in ${remainingTime} seconds`
          }
        });
        return;
      }

      clientAttempts.count++;
      next();
    };
  };

  /**
   * Middleware to validate refresh token
   */
  requireRefreshToken = (req: Request, res: Response, next: NextFunction): void => {
    try {
      const { refreshToken } = req.body;

      if (!refreshToken) {
        res.status(400).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.VALIDATION_ERROR,
            message: 'Refresh token required',
            details: 'refreshToken must be provided in request body'
          }
        });
        return;
      }

      const decoded = this.authService.verifyRefreshToken(refreshToken);

      if (!decoded) {
        res.status(401).json({
          success: false,
          error: {
            code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
            message: 'Invalid refresh token',
            details: 'Please login again to get new tokens'
          }
        });
        return;
      }

      // Attach userId to request for use in refresh endpoint
      req.user = {
        userId: decoded.userId,
        email: '' // Will be filled by the refresh endpoint
      };

      next();
    } catch (error) {
      console.error('Refresh token middleware error:', error);
      res.status(500).json({
        success: false,
        error: {
          code: CONSTANTS.ERROR_CODES.AUTHENTICATION_ERROR,
          message: 'Token verification error',
          details: 'An error occurred while verifying refresh token'
        }
      });
    }
  };
}

// Export singleton instance
export const authMiddleware = new AuthMiddleware();

/**
 * Bot Error Handling and Recovery Integration
 * 
 * Integrates all error handling, recovery, and monitoring components
 * to provide a comprehensive bot reliability system.
 */

import { EventEmitter } from 'events';

import logger from '../services/logging.service';

import type { BotManager } from './bot-manager';
import { BotRuntime } from './bot-runtime';
import type { WatchdogConfig } from './bot-watchdog';
import { BotWatchdog } from './bot-watchdog';
import type { RecoveryConfig } from './error-recovery-manager';
import { ErrorRecoveryManager } from './error-recovery-manager';
import HealthCheckService from './health-check.service';

export interface BotReliabilityConfig {
  errorRecovery: Partial<RecoveryConfig>;
  watchdog: Partial<WatchdogConfig>;
  integration: {
    enableAutoRestarts: boolean;
    enableFailoverMode: boolean;
    maxSystemErrors: number;
    systemErrorWindow: number; // milliseconds
    alertWebhookUrl?: string;
    enableMetricsCollection: boolean;
  };
}

export interface SystemAlert {
  level: 'info' | 'warning' | 'error' | 'critical';
  source: 'error-recovery' | 'watchdog' | 'health-check' | 'system';
  title: string;
  message: string;
  botId?: string;
  timestamp: Date;
  metadata?: Record<string, unknown>;
}

export class BotReliabilitySystem extends EventEmitter {
  private config: BotReliabilityConfig;
  private errorRecoveryManager!: ErrorRecoveryManager;
  private botWatchdog!: BotWatchdog;
  private healthCheckService!: HealthCheckService;
  private botManager: BotManager;
  
  private systemErrorCount = 0;
  private systemErrorWindow: NodeJS.Timeout | null = null;
  private alertHistory: SystemAlert[] = [];
  private running = false;

  constructor(
    botManager: BotManager,
    config: Partial<BotReliabilityConfig> = {}
  ) {
    super();

    this.botManager = botManager;
    this.config = {
      errorRecovery: {
        maxRetries: 3,
        baseRetryDelay: 1000,
        maxRetryDelay: 30000,
        enableAutoRestart: true,
        alertThreshold: 10,
        ...config.errorRecovery
      },
      watchdog: {
        healthCheckInterval: 30000,
        enableAutoRestart: true,
        restartThreshold: 3,
        alertOnRestart: true,
        ...config.watchdog
      },
      integration: {
        enableAutoRestarts: true,
        enableFailoverMode: false,
        maxSystemErrors: 20,
        systemErrorWindow: 300000, // 5 minutes
        enableMetricsCollection: true,
        ...config.integration
      }
    };

    this.initializeComponents();
    this.setupIntegrationEvents();
  }

  /**
   * Initialize all reliability components
   */
  private initializeComponents(): void {
    // Initialize Error Recovery Manager
    this.errorRecoveryManager = new ErrorRecoveryManager(this.config.errorRecovery);

    // Initialize Bot Watchdog
    this.botWatchdog = new BotWatchdog(
      this.botManager,
      this.errorRecoveryManager,
      this.config.watchdog
    );

    // Initialize Health Check Service
    this.healthCheckService = new HealthCheckService(
      this.botManager,
      this.errorRecoveryManager,
      this.botWatchdog
    );

    logger.info('[BotReliability] Initialized all reliability components');
  }

  /**
   * Setup integration event handlers
   */
  private setupIntegrationEvents(): void {
    // Error Recovery Manager Events
    this.errorRecoveryManager.on('error', (errorRecord) => {
      this.handleErrorRecoveryEvent('error', errorRecord);
    });

    this.errorRecoveryManager.on('recovery', (errorRecord) => {
      this.handleErrorRecoveryEvent('recovery', errorRecord);
    });

    this.errorRecoveryManager.on('recovery-failed', (errorRecord) => {
      this.handleErrorRecoveryEvent('recovery-failed', errorRecord);
    });

    this.errorRecoveryManager.on('admin-alert', (errorRecord) => {
      this.sendAlert({
        level: 'critical',
        source: 'error-recovery',
        title: 'Admin Intervention Required',
        message: `Bot ${errorRecord.context.botId} requires admin intervention: ${errorRecord.context.errorMessage}`,
        botId: errorRecord.context.botId,
        timestamp: new Date(),
        metadata: { errorRecord }
      });
    });

    this.errorRecoveryManager.on('restart-required', (errorRecord) => {
      this.handleBotRestartRequest(errorRecord.context.botId, 'error-recovery');
    });

    this.errorRecoveryManager.on('alert-threshold-exceeded', (data) => {
      this.sendAlert({
        level: 'error',
        source: 'error-recovery',
        title: 'Error Threshold Exceeded',
        message: `Bot ${data.botId} has exceeded error threshold: ${data.errorCount}/${data.threshold}`,
        botId: data.botId,
        timestamp: new Date(),
        metadata: data
      });
    });

    // Bot Watchdog Events
    this.botWatchdog.on('bot-unhealthy', (healthResult) => {
      this.sendAlert({
        level: 'warning',
        source: 'watchdog',
        title: 'Bot Health Degraded',
        message: `Bot ${healthResult.botId} is unhealthy: ${healthResult.issues.join(', ')}`,
        botId: healthResult.botId,
        timestamp: new Date(),
        metadata: { healthResult }
      });
    });

    this.botWatchdog.on('bot-restart-required', (data) => {
      this.handleBotRestartRequest(data.botId, 'watchdog');
    });

    this.botWatchdog.on('bot-restarted', (data) => {
      this.sendAlert({
        level: 'info',
        source: 'watchdog',
        title: 'Bot Restarted',
        message: `Bot ${data.botId} has been successfully restarted`,
        botId: data.botId,
        timestamp: new Date(),
        metadata: data
      });
    });

    this.botWatchdog.on('bot-restart-failed', (data) => {
      this.sendAlert({
        level: 'critical',
        source: 'watchdog',
        title: 'Bot Restart Failed',
        message: `Failed to restart bot ${data.botId}: ${data.error}`,
        botId: data.botId,
        timestamp: new Date(),
        metadata: data
      });

      this.incrementSystemErrorCount();
    });

    // Bot Manager Events
    this.botManager.on('bot-error', (data) => {
      this.handleBotError(data.botId, data.error);
    });

    this.botManager.on('bot-crashed', (data) => {
      this.sendAlert({
        level: 'critical',
        source: 'system',
        title: 'Bot Crashed',
        message: `Bot ${data.botId} has crashed: ${data.reason}`,
        botId: data.botId,
        timestamp: new Date(),
        metadata: data
      });

      this.incrementSystemErrorCount();
    });

    logger.info('[BotReliability] Setup integration event handlers');
  }

  /**
   * Start the reliability system
   */
  start(): void {
    if (this.running) {
      logger.warn('[BotReliability] System is already running');
      return;
    }

    this.running = true;
    
    logger.info('[BotReliability] Starting bot reliability system', {
      config: this.config
    });

    // Start all components
    this.botWatchdog.start();

    // Start system error window tracking
    this.resetSystemErrorWindow();

    this.sendAlert({
      level: 'info',
      source: 'system',
      title: 'Reliability System Started',
      message: 'Bot reliability system is now active and monitoring all bots',
      timestamp: new Date()
    });

    this.emit('started');
  }

  /**
   * Stop the reliability system
   */
  stop(): void {
    if (!this.running) {
      logger.warn('[BotReliability] System is not running');
      return;
    }

    this.running = false;

    logger.info('[BotReliability] Stopping bot reliability system');

    // Stop all components
    this.botWatchdog.stop();

    // Clear system error window
    if (this.systemErrorWindow) {
      clearTimeout(this.systemErrorWindow);
      this.systemErrorWindow = null;
    }

    this.sendAlert({
      level: 'info',
      source: 'system',
      title: 'Reliability System Stopped',
      message: 'Bot reliability system has been stopped',
      timestamp: new Date()
    });

    this.emit('stopped');
  }

  /**
   * Handle bot errors through the recovery system
   */
  async handleBotError(botId: string, error: Error): Promise<boolean> {
    logger.info('[BotReliability] Handling bot error', {
      botId,
      error: error.message
    });

    // Use error recovery manager to handle the error
    const recovered = await this.errorRecoveryManager.handleError(error, {
      botId,
      operation: 'bot-operation'
    });

    if (!recovered) {
      this.incrementSystemErrorCount();
    }

    return recovered;
  }

  /**
   * Handle error recovery events
   */
  private handleErrorRecoveryEvent(eventType: string, errorRecord: any): void {
    const alertLevel = this.getAlertLevelForRecoveryEvent(eventType);
    const message = this.getMessageForRecoveryEvent(eventType, errorRecord);

    if (alertLevel) {
      this.sendAlert({
        level: alertLevel,
        source: 'error-recovery',
        title: `Error Recovery: ${eventType}`,
        message,
        botId: errorRecord.context.botId,
        timestamp: new Date(),
        metadata: { errorRecord, eventType }
      });
    }

    // Emit for external listeners
    this.emit('error-recovery-event', { eventType, errorRecord });
  }

  /**
   * Handle bot restart requests
   */
  private async handleBotRestartRequest(botId: string, source: string): Promise<void> {
    if (!this.config.integration.enableAutoRestarts) {
      logger.warn('[BotReliability] Auto-restarts disabled, skipping restart', {
        botId,
        source
      });
      return;
    }

    logger.info('[BotReliability] Handling bot restart request', {
      botId,
      source
    });

    try {
      // The watchdog will handle the actual restart
      // We just track the request here
      this.emit('bot-restart-requested', { botId, source });
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      logger.error('[BotReliability] Failed to handle restart request', {
        botId,
        source,
        error: errorMessage
      });

      this.incrementSystemErrorCount();
    }
  }

  /**
   * Increment system error count and check thresholds
   */
  private incrementSystemErrorCount(): void {
    this.systemErrorCount++;

    logger.warn('[BotReliability] System error count incremented', {
      count: this.systemErrorCount,
      threshold: this.config.integration.maxSystemErrors
    });

    if (this.systemErrorCount >= this.config.integration.maxSystemErrors) {
      this.sendAlert({
        level: 'critical',
        source: 'system',
        title: 'System Error Threshold Exceeded',
        message: `System has exceeded maximum error threshold: ${this.systemErrorCount}/${this.config.integration.maxSystemErrors}`,
        timestamp: new Date(),
        metadata: {
          errorCount: this.systemErrorCount,
          threshold: this.config.integration.maxSystemErrors,
          window: this.config.integration.systemErrorWindow
        }
      });

      // Consider entering failover mode or alerting operations
      if (this.config.integration.enableFailoverMode) {
        this.enterFailoverMode();
      }
    }
  }

  /**
   * Reset system error window
   */
  private resetSystemErrorWindow(): void {
    if (this.systemErrorWindow) {
      clearTimeout(this.systemErrorWindow);
    }

    this.systemErrorWindow = setTimeout(() => {
      logger.info('[BotReliability] Resetting system error count', {
        previousCount: this.systemErrorCount
      });
      
      this.systemErrorCount = 0;
      this.resetSystemErrorWindow();
    }, this.config.integration.systemErrorWindow);
  }

  /**
   * Enter failover mode (emergency procedure)
   */
  private async enterFailoverMode(): Promise<void> {
    logger.error('[BotReliability] Entering failover mode due to system errors');

    this.sendAlert({
      level: 'critical',
      source: 'system',
      title: 'System Entering Failover Mode',
      message: 'Too many system errors detected, entering emergency failover mode',
      timestamp: new Date()
    });

    // Stop all bots to prevent further damage
    try {
      const allBots = this.botManager.getAllBotsStatus();
      for (const botId of allBots.keys()) {
        try {
          await this.botManager.stopBot('system', botId);
          logger.info('[BotReliability] Stopped bot in failover mode', { botId });
        } catch (error) {
          logger.error('[BotReliability] Failed to stop bot in failover mode', {
            botId,
            error: error instanceof Error ? error.message : String(error)
          });
        }
      }
    } catch (error) {
      logger.error('[BotReliability] Failed to execute failover procedure', {
        error: error instanceof Error ? error.message : String(error)
      });
    }

    this.emit('failover-mode-entered');
  }

  /**
   * Send system alert
   */
  private sendAlert(alert: SystemAlert): void {
    // Store alert in history
    this.alertHistory.push(alert);

    // Keep only recent alerts (last 1000)
    if (this.alertHistory.length > 1000) {
      this.alertHistory.splice(0, this.alertHistory.length - 1000);
    }

    // Log the alert
    const logLevel = alert.level === 'critical' ? 'error' : 
                    alert.level === 'error' ? 'error' :
                    alert.level === 'warning' ? 'warn' : 'info';

    logger[logLevel](`[BotReliability] ALERT: ${alert.title}`, {
      source: alert.source,
      message: alert.message,
      botId: alert.botId,
      metadata: alert.metadata
    });

    // Emit alert event
    this.emit('alert', alert);

    // Send webhook if configured
    if (this.config.integration.alertWebhookUrl) {
      this.sendWebhookAlert(alert);
    }
  }

  /**
   * Send webhook alert (placeholder implementation)
   */
  private async sendWebhookAlert(alert: SystemAlert): Promise<void> {
    try {
      // This would send a webhook to external alerting systems
      // For now, just log it
      logger.info('[BotReliability] Would send webhook alert', {
        url: this.config.integration.alertWebhookUrl,
        alert
      });
    } catch (error) {
      logger.error('[BotReliability] Failed to send webhook alert', {
        error: error instanceof Error ? error.message : String(error)
      });
    }
  }

  /**
   * Helper methods
   */
  private getAlertLevelForRecoveryEvent(eventType: string): SystemAlert['level'] | null {
    switch (eventType) {
      case 'error':
        return 'warning';
      case 'recovery':
        return 'info';
      case 'recovery-failed':
        return 'error';
      default:
        return null;
    }
  }

  private getMessageForRecoveryEvent(eventType: string, errorRecord: any): string {
    switch (eventType) {
      case 'error':
        return `Error detected in bot ${errorRecord.context.botId}: ${errorRecord.context.errorMessage}`;
      case 'recovery':
        return `Bot ${errorRecord.context.botId} successfully recovered from error`;
      case 'recovery-failed':
        return `Failed to recover bot ${errorRecord.context.botId} from error: ${errorRecord.context.errorMessage}`;
      default:
        return `Unknown recovery event: ${eventType}`;
    }
  }

  /**
   * Public methods for monitoring and management
   */
  getSystemStats(): {
    running: boolean;
    systemErrorCount: number;
    alertCount: number;
    errorRecoveryStats: any;
    watchdogStats: any;
  } {
    return {
      running: this.running,
      systemErrorCount: this.systemErrorCount,
      alertCount: this.alertHistory.length,
      errorRecoveryStats: this.errorRecoveryManager.getStats(),
      watchdogStats: this.botWatchdog.getWatchdogStats()
    };
  }

  getRecentAlerts(limit = 50): SystemAlert[] {
    return this.alertHistory.slice(-limit);
  }

  getErrorRecoveryManager(): ErrorRecoveryManager {
    return this.errorRecoveryManager;
  }

  getBotWatchdog(): BotWatchdog {
    return this.botWatchdog;
  }

  getHealthCheckService(): HealthCheckService {
    return this.healthCheckService;
  }

  isRunning(): boolean {
    return this.running;
  }

  /**
   * Force health check for all bots
   */
  async performSystemHealthCheck(): Promise<any> {
    return await this.healthCheckService.getHealthStatus();
  }

  /**
   * Clear error history for a bot
   */
  clearBotErrorHistory(botId: string): void {
    this.errorRecoveryManager.clearErrorHistory(botId);
    this.errorRecoveryManager.resetCircuitBreaker(botId);
  }

  /**
   * Update configuration
   */
  updateConfig(config: Partial<BotReliabilityConfig>): void {
    this.config = {
      ...this.config,
      ...config
    };

    logger.info('[BotReliability] Configuration updated', { config: this.config });
  }
}

export default BotReliabilitySystem;

/**
 * Production-Ready Bot Runtime System
 * 
 * This module handles the complete lifecycle of trading bots in production:
 * - Bot initialization and configuration loading
 * - Dynamic strategy loading and execution
 * - Signal processing and trade execution
 * - Error handling and recovery
 * - State persistence and monitoring
 * - Graceful shutdown and cleanup
 */

import { EventEmitter } from 'events';

import type { IStrategy, StrategyResult, StrategyContext, StrategyConfig } from '../JabbrLabs/target-reacher/interfaces';
import { database } from '../services/database.service';
import type { StrategyType } from '../strategies/strategy-factory';

import type { Bot } from './bots.service';
import type { StrategySwapOptions } from './dynamic-strategy-loader';
import { dynamicStrategyLoader } from './dynamic-strategy-loader';
import type { ErrorRecoveryManager } from './error-recovery-manager';
import GracefulShutdownManager from './graceful-shutdown-manager';
import type { ProcessedSignal } from './signal-processor';
import { SignalProcessor } from './signal-processor';
import StateManager from './state-manager';
import type { StrategyExecutionConfig, StrategyExecutionResult } from './strategy-execution-integration';
import StrategyExecutionIntegration from './strategy-execution-integration';
import { TradeDecisionEngine } from './trade-decision-engine';
import type { TradeDecision, ExecutionResult } from './trade-decision-engine';
import type { TradeExecutor } from './trade-executor';

export interface BotRuntimeConfig {
  bot: Bot;
  strategy: IStrategy;
  context: StrategyContext;
  checkInterval: number; // milliseconds
  maxRetries: number;
  retryDelay: number; // milliseconds
  tradeExecutor?: TradeExecutor; // Optional trade executor for real trading
  errorRecoveryManager?: ErrorRecoveryManager; // Optional error recovery manager
  stateManager?: StateManager; // Enhanced state persistence manager
  shutdownManager?: GracefulShutdownManager; // Enhanced graceful shutdown manager
  strategyExecutionIntegration?: StrategyExecutionIntegration; // Enhanced strategy execution
}

export interface BotState {
  botId: string;
  status: 'initializing' | 'running' | 'paused' | 'stopping' | 'stopped' | 'error';
  startedAt?: Date;
  lastTickAt?: Date;
  errorCount: number;
  lastError?: string;
  performance: {
    tickCount: number;
    signalCount: number;
    tradeCount: number;
    errorCount: number;
    totalProfit: number;
    totalLoss: number;
    winRate: number;
    avgTradeTime: number;
    maxDrawdown: number;
  };
  currentStrategy?: {
    name: string;
    version: string;
    type: string;
  };
  positions?: Array<{
    symbol: string;
    size: number;
    entryPrice: number;
    currentPrice: number;
    pnl: number;
    openedAt: Date;
  }>;
  orders?: Array<{
    id: string;
    symbol: string;
    type: string;
    status: string;
    size: number;
    price: number;
    createdAt: Date;
  }>;
  strategy?: {
    type: string;
    parameters: Record<string, unknown>;
    indicators: Record<string, unknown>;
    signals: Array<{
      type: string;
      strength: number;
      timestamp: Date;
    }>;
  };
}

export class BotRuntime extends EventEmitter {
  private config: BotRuntimeConfig;
  private state: BotState;
  private intervalId?: NodeJS.Timeout;
  private shutdownPromise?: Promise<void>;
  private isShuttingDown = false;
  private signalProcessor: SignalProcessor;
  private tradeDecisionEngine: TradeDecisionEngine;
  private stateManager: StateManager;
  private shutdownManager: GracefulShutdownManager;
  private strategyExecutionIntegration?: StrategyExecutionIntegration;

  constructor(config: BotRuntimeConfig) {
    super();
    this.config = config;
    this.state = {
      botId: config.bot.id,
      status: 'initializing',
      errorCount: 0,
      performance: {
        tickCount: 0,
        signalCount: 0,
        tradeCount: 0,
        errorCount: 0,
        totalProfit: 0,
        totalLoss: 0,
        winRate: 0,
        avgTradeTime: 0,
        maxDrawdown: 0
      },
      currentStrategy: {
        name: config.strategy.name,
        version: config.strategy.version,
        type: config.bot.strategy
      }
    };

    // Initialize signal processing and trade decision components
    this.signalProcessor = new SignalProcessor({
      enableRiskValidation: true,
      enableSignalEnrichment: true,
      minConfidenceThreshold: config.bot.configuration?.minConfidence || 0.6,
      maxSignalsPerMinute: config.bot.configuration?.maxSignalsPerMinute || 10
    });

    this.tradeDecisionEngine = new TradeDecisionEngine({
      enablePositionManagement: true,
      enableStopLoss: config.bot.riskManagement?.enableStopLoss ?? true,
      enableTakeProfit: config.bot.riskManagement?.enableTakeProfit ?? true,
      maxPositionsPerBot: config.bot.configuration?.maxPositions || 3,
      slippageTolerance: config.bot.riskManagement?.maxSlippage || 0.1
    }, config.tradeExecutor);

    // Initialize enhanced state management
    this.stateManager = config.stateManager || new StateManager({
      enableIncrementalUpdates: true,
      enableCompression: true,
      backupInterval: 300000, // 5 minutes
      maxBackups: 48,
      enableIntegrityChecks: true,
      autoRecovery: true,
      emergencyBackupThreshold: 5
    });

    // Initialize enhanced graceful shutdown management
    this.shutdownManager = config.shutdownManager || new GracefulShutdownManager(this.stateManager, {
      gracePeriod: 60000, // 1 minute
      positionTimeout: 30000,
      orderTimeout: 15000,
      backupTimeout: 10000,
      enablePositionClosure: true,
      enableOrderCancellation: true,
      enableEmergencyBackup: true
    });

    // Set up event listeners for signal processing
    this.setupSignalProcessingEvents();

    // Set up event listeners for enhanced state management
    this.setupStateManagementEvents();

    // Set up error handling
    this.on('error', (errorData) => {
      if (errorData && typeof errorData === 'object' && errorData.error) {
        // Already handled by explicit handleError call
        return;
      }
      // Handle direct error emissions
      this.handleError(errorData as Error, 'event-emission').catch(err => {
        console.error('Failed to handle error event:', err);
      });
    });
    process.on('SIGINT', this.gracefulShutdown.bind(this));
    process.on('SIGTERM', this.gracefulShutdown.bind(this));
  }

  /**
   * Initialize the bot runtime
   */
  async initialize(): Promise<void> {
    try {
      console.log('üöÄ Initializing bot runtime', {
        botId: this.config.bot.id,
        botName: this.config.bot.name,
        strategy: this.config.bot.strategy
      });

      // Load and validate configuration
      await this.loadConfiguration();

      // Initialize strategy
      await this.initializeStrategy();

      // Initialize strategy execution integration
      await this.initializeStrategyExecution();

      // Validate exchange connection
      await this.validateExchangeConnection();

      // Load persisted state if exists
      await this.loadPersistedState();

      this.state.status = 'stopped';
      
      console.log('‚úÖ Bot runtime initialized successfully', {
        botId: this.config.bot.id
      });

      this.emit('initialized');
    } catch (error) {
      console.error('‚ùå Failed to initialize bot runtime', {
        botId: this.config.bot.id,
        error: error instanceof Error ? error.message : String(error)
      });
      
      this.state.status = 'error';
      await this.handleError(
        error instanceof Error ? error : new Error(String(error)), 
        'bot-initialization'
      );
      throw error;
    }
  }

  /**
   * Start the bot execution
   */
  async start(): Promise<void> {
    if (this.state.status === 'running') {
      console.warn('‚ö†Ô∏è Bot is already running', {
        botId: this.config.bot.id
      });
      return;
    }

    if (this.isShuttingDown) {
      throw new Error('Cannot start bot during shutdown');
    }

    try {
      this.state.status = 'running';
      this.state.startedAt = new Date();
      this.state.errorCount = 0;

      console.log('‚ñ∂Ô∏è Starting bot execution', {
        botId: this.config.bot.id,
        checkInterval: this.config.checkInterval
      });

      // Start the main execution loop
      this.intervalId = setInterval(
        this.executionTick.bind(this),
        this.config.checkInterval
      );

      // Update database status
      await this.updateDatabaseStatus('running');

      this.emit('started');
    } catch (error) {
      this.state.status = 'error';
      this.state.lastError = error instanceof Error ? error.message : String(error);
      
      console.error('‚ùå Failed to start bot', {
        botId: this.config.bot.id,
        error: this.state.lastError
      });
      
      throw error;
    }
  }

  /**
   * Pause the bot execution
   */
  async pause(): Promise<void> {
    if (this.state.status !== 'running') {
      console.warn('‚ö†Ô∏è Bot is not running, cannot pause', {
        botId: this.config.bot.id,
        currentStatus: this.state.status
      });
      return;
    }

    this.state.status = 'paused';
    
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = undefined;
    }

    await this.updateDatabaseStatus('paused');

    console.log('‚è∏Ô∏è Bot execution paused', {
      botId: this.config.bot.id
    });

    this.emit('paused');
  }

  /**
   * Resume the bot execution
   */
  async resume(): Promise<void> {
    if (this.state.status !== 'paused') {
      console.warn('‚ö†Ô∏è Bot is not paused, cannot resume', {
        botId: this.config.bot.id,
        currentStatus: this.state.status
      });
      return;
    }

    this.state.status = 'running';
    
    this.intervalId = setInterval(
      this.executionTick.bind(this),
      this.config.checkInterval
    );

    await this.updateDatabaseStatus('running');

    console.log('‚ñ∂Ô∏è Bot execution resumed', {
      botId: this.config.bot.id
    });

    this.emit('resumed');
  }

  /**
   * Stop the bot execution
   */
  async stop(): Promise<void> {
    if (this.state.status === 'stopped') {
      console.warn('‚ö†Ô∏è Bot is already stopped', {
        botId: this.config.bot.id
      });
      return;
    }

    this.state.status = 'stopping';

    console.log('‚èπÔ∏è Stopping bot execution', {
      botId: this.config.bot.id
    });

    // Clear the execution interval
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = undefined;
    }

    // Cleanup strategy
    try {
      await this.config.strategy.cleanup(this.config.context);
    } catch (error) {
      console.warn('‚ö†Ô∏è Error during strategy cleanup', {
        botId: this.config.bot.id,
        error: error instanceof Error ? error.message : String(error)
      });
    }

    // Shutdown strategy execution integration
    try {
      if (this.strategyExecutionIntegration) {
        await this.strategyExecutionIntegration.shutdown();
        console.log('‚úÖ Strategy execution integration shutdown complete', {
          botId: this.config.bot.id
        });
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è Error during strategy execution integration shutdown', {
        botId: this.config.bot.id,
        error: error instanceof Error ? error.message : String(error)
      });
    }

    // Persist final state
    await this.persistState();

    // Cleanup enhanced state management components
    try {
      if (this.stateManager) {
        this.stateManager.shutdown();
      }
      if (this.shutdownManager) {
        this.shutdownManager.shutdown();
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è Error cleaning up state management components', {
        botId: this.config.bot.id,
        error: error instanceof Error ? error.message : String(error)
      });
    }

    this.state.status = 'stopped';
    await this.updateDatabaseStatus('stopped');

    console.log('‚úÖ Bot execution stopped', {
      botId: this.config.bot.id,
      performance: this.state.performance
    });

    this.emit('stopped');
  }

  /**
   * Get current bot state
   */
  getState(): BotState {
    return { ...this.state };
  }

  /**
   * Main execution tick - runs at configured intervals
   */
  private async executionTick(): Promise<void> {
    if (this.state.status !== 'running' || this.isShuttingDown) {
      return;
    }

    try {
      this.state.lastTickAt = new Date();
      this.state.performance.tickCount++;

      // Execute strategy using enhanced strategy execution integration
      let executionResult: StrategyExecutionResult;
      
      if (this.strategyExecutionIntegration) {
        // Use enhanced strategy execution integration
        executionResult = await this.strategyExecutionIntegration.executeStrategy();
        
        if (!executionResult.success) {
          throw new Error(executionResult.error || 'Strategy execution failed');
        }

        // Extract the strategy result
        const result = executionResult.result;
        if (!result) {
          console.warn('‚ö†Ô∏è Strategy execution succeeded but no result returned', {
            botId: this.config.bot.id
          });
          return;
        }

        // Process successful strategy execution
        if (result.action !== 'hold') {
          this.state.performance.signalCount++;
          
          console.log('üìä Enhanced strategy signal generated', {
            botId: this.config.bot.id,
            action: result.action,
            confidence: result.confidence,
            reason: result.reason,
            executionTime: executionResult.executionTime,
            metrics: executionResult.metrics
          });

          // Process the trading signal
          await this.processSignal(result);
        }

        // Emit enhanced tick event for monitoring
        this.emit('tick', {
          botId: this.config.bot.id,
          timestamp: this.state.lastTickAt,
          result,
          executionMetrics: executionResult.metrics,
          executionTime: executionResult.executionTime
        });

      } else {
        // Fallback to original strategy execution
        const result = await this.config.strategy.execute(this.config.context);

        if (result.action !== 'hold') {
          this.state.performance.signalCount++;
          
          console.log('üìä Strategy signal generated (fallback)', {
            botId: this.config.bot.id,
            action: result.action,
            confidence: result.confidence,
            reason: result.reason
          });

          // Process the trading signal
          await this.processSignal(result);
        }

        // Emit tick event for monitoring
        this.emit('tick', {
          botId: this.config.bot.id,
          timestamp: this.state.lastTickAt,
          result
        });
      }

    } catch (error) {
      this.state.performance.errorCount++;
      
      console.error('‚ùå Error in execution tick', {
        botId: this.config.bot.id,
        error: error instanceof Error ? error.message : String(error),
        errorCount: this.state.errorCount
      });

      // Use enhanced error handling
      await this.handleError(
        error instanceof Error ? error : new Error(String(error)), 
        'execution-tick'
      );

      // Check if we should stop due to too many errors
      if (this.state.errorCount >= this.config.maxRetries) {
        console.error('üö® Too many errors, stopping bot', {
          botId: this.config.bot.id,
          errorCount: this.state.errorCount,
          maxRetries: this.config.maxRetries
        });

        await this.stop();
        return;
      }

      // Exponential backoff for retries
      const delay = this.config.retryDelay * Math.pow(2, this.state.errorCount - 1);
      await new Promise(resolve => setTimeout(resolve, delay));
    }
  }

  /**
   * Load and validate bot configuration
   */
  private async loadConfiguration(): Promise<void> {
    // Validate required configuration fields
    if (!this.config.bot.configuration) {
      throw new Error('Bot configuration is missing');
    }

    if (!this.config.bot.exchangeApiKeyId) {
      throw new Error('Exchange API key ID is missing');
    }

    // Load exchange API credentials
    const apiKeyResult = await database.query(
      'SELECT * FROM exchange_api_keys WHERE id = $1 AND user_id = $2 AND is_active = true',
      [this.config.bot.exchangeApiKeyId, this.config.bot.userId]
    );

    if (apiKeyResult.length === 0) {
      throw new Error('Exchange API key not found or inactive');
    }

    console.log('‚úÖ Configuration loaded successfully', {
      botId: this.config.bot.id,
      exchange: this.config.bot.exchange,
      strategy: this.config.bot.strategy
    });
  }

  /**
   * Initialize the trading strategy
   */
  private async initializeStrategy(): Promise<void> {
    try {
      await this.config.strategy.initialize(this.config.context);
      
      console.log('‚úÖ Strategy initialized successfully', {
        botId: this.config.bot.id,
        strategy: this.config.bot.strategy
      });
    } catch (error) {
      console.error('‚ùå Failed to initialize strategy', {
        botId: this.config.bot.id,
        strategy: this.config.bot.strategy,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

  /**
   * Initialize strategy execution integration
   */
  private async initializeStrategyExecution(): Promise<void> {
    try {
      // Use provided strategy execution integration or create default one
      if (this.config.strategyExecutionIntegration) {
        this.strategyExecutionIntegration = this.config.strategyExecutionIntegration;
      } else {
        // Create default strategy execution integration
        const strategyConfig: StrategyExecutionConfig = {
          botId: this.config.bot.id,
          strategyType: this.config.bot.strategy as StrategyType,
          strategyConfig: {
            type: this.config.bot.strategy,
            parameters: this.config.bot.configuration || {}
          },
          executionInterval: this.config.checkInterval,
          enableDynamicLoading: true,
          enablePerformanceTracking: true,
          maxExecutionTime: 30000, // 30 seconds
          retryAttempts: this.config.maxRetries
        };

        this.strategyExecutionIntegration = new StrategyExecutionIntegration(
          strategyConfig,
          this.config.context
        );
      }

      // Initialize the strategy execution integration
      await this.strategyExecutionIntegration.initialize();

      // Set up event listeners for strategy execution
      this.strategyExecutionIntegration.on('strategy-executed', (data) => {
        this.emit('strategy-executed', data);
      });

      this.strategyExecutionIntegration.on('strategy-execution-error', (data) => {
        this.emit('strategy-execution-error', data);
      });

      this.strategyExecutionIntegration.on('strategy-swapped', (data) => {
        console.log('üîÑ Strategy swapped successfully', data);
        this.emit('strategy-swapped', data);
      });

      console.log('‚úÖ Strategy execution integration initialized successfully', {
        botId: this.config.bot.id,
        strategyType: this.config.bot.strategy
      });

    } catch (error) {
      console.error('‚ùå Failed to initialize strategy execution integration', {
        botId: this.config.bot.id,
        error: error instanceof Error ? error.message : String(error)
      });
      throw error;
    }
  }

