  GRACEFUL_SHUTDOWN_TIMEOUT: z.coerce.number().min(1000).default(30000),

  // Test Configuration
  AUTO_MIGRATE: z.coerce.boolean().default(false),
  AUTO_SEED: z.coerce.boolean().default(false),
