  // External Services
  EMAIL_SERVICE_API_KEY: z.string().optional(),
  SMS_SERVICE_API_KEY: z.string().optional(),
  NOTIFICATION_WEBHOOK_URL: z.string().optional(),
});

export type Config = z.infer<typeof ConfigSchema>;

