      if (!this.checkRateLimit('getOrder')) {
        throw new Error('Rate limit exceeded');
      }

      const formattedSymbol = this.formatSymbol(symbol, marketType);
      this.client.options.defaultType = marketType === MarketType.SPOT ? 'spot' : 'swap';

