impoimport type { SMASignalOutput } from '../../src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from '../src/JabbrLabs/signals/sma/sma-signal-processor'; * as fs from 'fs';
import * as path from 'path';

import type { SMASignalConfig} from './src/JabbrLabs/signals/sma/models';
import { SMASignalOutput } from './src/JabbrLabs/signals/sma/models';
import { SMASignalProcessor } from './src/JabbrLabs/signals/sma/sma-signal-processor';
import type { Candle } from './src/JabbrLabs/target-reacher/interfaces';

// Configuration
const config: SMASignalConfig = {
  fastPeriod: 9,
  slowPeriod: 21,
  minChangePercent: 0.5,
  confidenceThreshold: 0.6,
  priceSource: 'close',
  signalMode: 'crossover',
  useEMA: false
};

// Generate synthetic price data for testing
function generateSyntheticCandles(options: {
  startPrice: number,
  dataPoints: number,
  volatility: number,
  trendStrength: number,
  trendChangeProbability: number
}): Candle[] {
  const candles: Candle[] = [];
  let currentPrice = options.startPrice;
  let trend = 1; // 1 = up, -1 = down

  for (let i = 0; i < options.dataPoints; i++) {
    // Potentially change trend
    if (Math.random() < options.trendChangeProbability) {
      trend *= -1;
    }
    
    // Calculate price movement
    const trendMovement = trend * options.trendStrength * currentPrice;
    const randomMovement = (Math.random() * 2 - 1) * options.volatility * currentPrice;
    const movement = trendMovement + randomMovement;
    
    // Calculate candle prices
    const open = currentPrice;
    const close = currentPrice + movement;
    const high = Math.max(open, close) + Math.random() * options.volatility * currentPrice;
    const low = Math.min(open, close) - Math.random() * options.volatility * currentPrice;
    const volume = Math.random() * 100000 + 10000;
    
    // Create candle
    candles.push({
      timestamp: Date.now() + i * 900000, // 15-minute intervals
      open,
      high,
      low,
      close,
      volume
    });
    
    // Update current price for next iteration
    currentPrice = close;
  }
  
  return candles;
}

// Function to run backtest
async function runTest() {
  console.log('Starting SMA Crossover Strategy Test');
  console.log(`Settings: Fast Period: ${config.fastPeriod}, Slow Period: ${config.slowPeriod}, Threshold: ${config.confidenceThreshold}`);
  
  // Generate test data
  const candles = generateSyntheticCandles({
    startPrice: 100,
    dataPoints: 100,
    volatility: 0.01,
    trendStrength: 0.008,
    trendChangeProbability: 0.1
  });
  
  const processor = new SMASignalProcessor(config);
  
  const signals: any[] = [];
  const trades: any[] = [];
  let position: { side: 'buy' | 'sell' | null, entryPrice: number | null } = {
    side: null,
    entryPrice: null
  };
  
  let pnl = 0;
  
  // Process each candle
  for (let i = config.slowPeriod; i < candles.length; i++) {
    const windowCandles = candles.slice(0, i + 1);
    const output = processor.process(windowCandles);
    
    if (output && output.signal !== 0) {
      const candle = candles[i];
      signals.push({
        timestamp: candle.timestamp,
        price: candle.close,
        signal: output.signal > 0 ? 'buy' : 'sell',
        confidence: output.confidence,
        reason: output.reason
      });
      
      // Execute trades based on signals
      if (output.signal > 0 && position.side !== 'buy') {
        // Close existing position if any
        if (position.side === 'sell') {
          const profit = position.entryPrice! - candle.close;
          pnl += profit;
          trades.push({
            type: 'Close Short',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open long position
        position = { side: 'buy', entryPrice: candle.close };
        trades.push({
          type: 'Open Long',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      } else if (output.signal < 0 && position.side !== 'sell') {
        // Close existing position if any
        if (position.side === 'buy') {
          const profit = candle.close - position.entryPrice!;
          pnl += profit;
          trades.push({
            type: 'Close Long',
            timestamp: candle.timestamp,
            price: candle.close,
            profit
          });
        }
        
        // Open short position
        position = { side: 'sell', entryPrice: candle.close };
        trades.push({
          type: 'Open Short',
          timestamp: candle.timestamp,
          price: candle.close,
          reason: output.reason
        });
      }
    }
  }
  
  // Close final position at the end of the test
  if (position.side !== null) {
    const lastCandle = candles[candles.length - 1];
    let profit = 0;
    
    if (position.side === 'buy') {
      profit = lastCandle.close - position.entryPrice!;
      trades.push({
        type: 'Close Long (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    } else {
      profit = position.entryPrice! - lastCandle.close;
      trades.push({
        type: 'Close Short (End)',
        timestamp: lastCandle.timestamp,
        price: lastCandle.close,
        profit
      });
    }
    
    pnl += profit;
  }
  
  // Log results
  console.log(`Generated ${signals.length} signals and ${trades.length} trades`);
  console.log(`Final P&L: ${pnl.toFixed(2)}`);
  
  // Generate report
  const report = {
    config,
    summary: {
      signalCount: signals.length,
      tradeCount: trades.length,
      pnl
    },
    signals,
    trades
  };
  
  // Write report to file
  fs.writeFileSync(
    path.join(__dirname, 'sma-test-report.json'),
    JSON.stringify(report, null, 2)
  );
  
  console.log('Test complete. Report saved to sma-test-report.json');
}

runTest().catch(console.error);

/**
 * SMA Strategy Backtesting Script
 * 
 * This script runs a backtesting simulation for the SMA crossover strategy
 * using historical price data. It provides insights into how the strategy
 * would have performed over a historical time period.
 */

import fs from 'fs';
import path from 'path';

import { SMACrossoverStrategy } from './src/JabbrLabs/signals/sma/sma-crossover-strategy';
import type { Candle, StrategyContext } from './src/JabbrLabs/target-reacher/interfaces';

// Sample historical data (Normally, this would be loaded from a file or database)
// For a real implementation, add a CSV loader or API client to fetch historical data
const loadHistoricalData = (symbol: string, startDate: Date, endDate: Date): Candle[] => {
  // This is a placeholder - in a real implementation, load data from file or API
  console.log(`Loading historical data for ${symbol} from ${startDate.toISOString()} to ${endDate.toISOString()}`);
  
  // Generate synthetic data for demonstration purposes
  const candles: Candle[] = [];
  let price = 100; // Starting price
  const hoursInRange = Math.floor((endDate.getTime() - startDate.getTime()) / (60 * 60 * 1000));
  
  // Create a more realistic price pattern with clearer trends
  // Using a sine wave with noise for more predictable crossovers
  for (let i = 0; i < hoursInRange; i++) {
    // Create a sine wave pattern with 240 hour cycle (10 days)
    const cycle1 = Math.sin(i / 40) * 15;  // Primary trend
    const cycle2 = Math.sin(i / 20) * 5;   // Secondary trend
    const noise = (Math.random() - 0.5) * 3; // Random noise
    
    // Calculate price based on cycles and noise
    price = 100 + cycle1 + cycle2 + noise;
    
    // Add some volatility
    const timestamp = new Date(startDate.getTime() + i * 60 * 60 * 1000).getTime();
    const open = price * (1 + (Math.random() - 0.5) / 100);
    const close = price;
    const high = Math.max(open, close) * (1 + Math.random() / 100);
    const low = Math.min(open, close) * (1 - Math.random() / 100);
    const volume = 1000 + Math.random() * 9000;
    
    candles.push({
      timestamp,
      open,
      high,
      low,
      close,
      volume
    });
  }
  
  return candles;
};

// Create a mock context for backtesting
const createBacktestContext = (candles: Candle[]): StrategyContext => {
  const context: StrategyContext = {
