// Main backtesting function
const runBacktest = async (
  strategy: SMACrossoverStrategy,
  symbol: string,
  startDate: Date,
  endDate: Date,
  windowSize = 100
): Promise<{ trades: number; profitLoss: number; }> => {
  // Load historical data
  const allCandles = loadHistoricalData(symbol, startDate, endDate);
  
  // Get current config for display
  const testContext = createBacktestContext([]);
  const currentConfig = testContext.config;
  const fastPeriod = currentConfig.parameters.fastPeriod as number;
  const slowPeriod = currentConfig.parameters.slowPeriod as number;
  const signalMode = currentConfig.parameters.signalMode as string;
  
  console.log(`\n----- BACKTESTING SMA CROSSOVER STRATEGY -----`);
  console.log(`Symbol: ${symbol}`);
  console.log(`Period: ${startDate.toDateString()} to ${endDate.toDateString()}`);
  console.log(`Candles: ${allCandles.length}`);
  console.log(`Fast Period: ${fastPeriod}`);
  console.log(`Slow Period: ${slowPeriod}`);
  console.log(`Signal Mode: ${signalMode}`);
  console.log(`Minimum Confidence: ${currentConfig.execution?.minimumConfidence}`);
  console.log(`-----------------------------------------------\n`);
  
  // Stats
  let trades = 0;
  let profitLoss = 0;
  let position: 'long' | 'short' | null = null;
  let entryPrice = 0;
  const positionSize = 0.1; // Default position size
  let capital = 10000; // Starting capital (USDT)
  let equity = capital;
  
  // For tracking execution history
  const executionHistory: Array<{
    timestamp: number;
    action: string;
    price: number;
    reason: string;
    equity: number;
  }> = [];
  
  // Initialize strategy
  const context = createBacktestContext([]);
  await strategy.initialize(context);
  
  // Process historical data with a sliding window
  for (let i = windowSize; i < allCandles.length; i++) {
    // Get window of candles
    const windowCandles = allCandles.slice(i - windowSize, i);
    
    // Update context with current window
    context.marketData.getCandles = async () => [...windowCandles];
    
    // Get the last candle, with safety check
    const lastCandle = windowCandles[windowCandles.length - 1];
    if (!lastCandle) {
      console.error('Missing last candle in window');
      continue;
    }
    
    // Current price (close of the last candle)
    const currentPrice = lastCandle.close;
    const currentTimestamp = lastCandle.timestamp;
    
    // Update PnL if we have a position
    if (position === 'long') {
      const unrealizedPnl = (currentPrice - entryPrice) * positionSize;
      equity = capital + unrealizedPnl;
    } else if (position === 'short') {
      const unrealizedPnl = (entryPrice - currentPrice) * positionSize;
      equity = capital + unrealizedPnl;
    }
    
    // Mock position data
    if (position) {
      context.tradeExecutor.getPosition = async () => ({
        symbol,
        side: position as 'long' | 'short', // Type assertion to match Position interface
        size: positionSize,
        entryPrice,
        currentPrice,
        unrealizedPnl: equity - capital,
        timestamp: currentTimestamp
      });
    } else {
      context.tradeExecutor.getPosition = async () => null;
    }
    
    // Execute strategy with debugging
    try {
      // Debug log for candles
      if (i % 100 === 0) {
        console.log(`Processing window at index ${i}, price: ${currentPrice.toFixed(2)}`);
      }
      
      const result = await strategy.execute(context);
      
      // Log all strategy results for debugging
      if (i % 100 === 0 || result.action !== 'hold') {
        console.log(`Strategy result [${new Date(currentTimestamp).toISOString()}]:`, {
          success: result.success,
          action: result.action,
          reason: result.reason,
          confidence: result.confidence
        });
      }
      
      // Process trade
      if (result.success && (result.action === 'buy' || result.action === 'sell')) {
        // If we have a position and signal is in opposite direction, close it
        if (position && 
            ((position === 'long' && result.action === 'sell') ||
             (position === 'short' && result.action === 'buy'))) {
          // Calculate P&L
          const closingPnl = position === 'long' 
            ? (currentPrice - entryPrice) * positionSize
            : (entryPrice - currentPrice) * positionSize;
          
          profitLoss += closingPnl;
          capital += closingPnl;
          equity = capital;
          
          console.log(`[${new Date(currentTimestamp).toISOString()}] CLOSE ${position} position at $${currentPrice.toFixed(2)}, P&L: ${closingPnl.toFixed(2)}`);
          executionHistory.push({
            timestamp: currentTimestamp,
            action: `CLOSE ${position}`,
            price: currentPrice,
            reason: result.reason || '',
            equity
          });
          
          position = null;
          entryPrice = 0;
        }
        
        // Open new position
        if (!position) {
          position = result.action === 'buy' ? 'long' : 'short';
          entryPrice = currentPrice;
          trades++;
          
          console.log(`[${new Date(currentTimestamp).toISOString()}] OPEN ${position} position at $${currentPrice.toFixed(2)}`);
          executionHistory.push({
            timestamp: currentTimestamp,
            action: `OPEN ${position}`,
            price: currentPrice,
            reason: result.reason || '',
            equity
          });
        }
      }
    } catch (error) {
      console.error(`Error during backtesting:`, error);
    }
  }
  
  // Close any open position at the end of the backtest
  if (position) {
    // Get the last candle with safety check
    const lastCandle = allCandles[allCandles.length - 1];
    if (!lastCandle) {
      console.error('Missing last candle in dataset');
      return { trades, profitLoss };
    }
    
    const lastPrice = lastCandle.close;
    const finalPnl = position === 'long' 
      ? (lastPrice - entryPrice) * positionSize
      : (entryPrice - lastPrice) * positionSize;
    
    profitLoss += finalPnl;
    capital += finalPnl;
    equity = capital;
    
    console.log(`[End of backtest] CLOSE ${position} position at $${lastPrice.toFixed(2)}, P&L: ${finalPnl.toFixed(2)}`);
    executionHistory.push({
      timestamp: lastCandle.timestamp,
      action: `CLOSE ${position} (END)`,
      price: lastPrice,
      reason: 'End of backtest',
      equity
    });
  }
  
  // Print backtest results
  console.log('\n----- BACKTEST RESULTS -----');
  console.log(`Total trades: ${trades}`);
  console.log(`Total P&L: ${profitLoss.toFixed(2)} USDT`);
  console.log(`Final equity: ${equity.toFixed(2)} USDT`);
  console.log(`Return: ${(((equity - 10000) / 10000) * 100).toFixed(2)}%`);
  console.log('---------------------------\n');
  
  // Save execution history to file
  const outputDir = path.join(__dirname, 'backtest-results');
  try {
    if (!fs.existsSync(outputDir)) {
      fs.mkdirSync(outputDir, { recursive: true });
    }
    
    const filename = path.join(outputDir, `sma-backtest-${new Date().toISOString().replace(/:/g, '-')}.json`);
    fs.writeFileSync(filename, JSON.stringify({
      strategy: 'SMA Crossover',
      symbol,
      startDate: startDate.toISOString(),
      endDate: endDate.toISOString(),
      config: strategy.getDefaultConfig(),
      results: {
        trades,
        profitLoss,
        startingCapital: 10000,
        finalEquity: equity,
        returnPercent: ((equity - 10000) / 10000) * 100
      },
      executionHistory
    }, null, 2));
    
    console.log(`Backtest results saved to ${filename}`);
  } catch (error) {
    console.error('Failed to save backtest results:', error);
  }
  
  return { trades, profitLoss };
};

// Run the backtest
const main = async () => {
  const strategy = new SMACrossoverStrategy();
  
  // Three months of hourly data
  const endDate = new Date();
  const startDate = new Date();
  startDate.setMonth(endDate.getMonth() - 3);
  
  await runBacktest(strategy, 'BTC/USDT', startDate, endDate);
};

main().catch(error => {
  console.error('Backtest failed:', error);
});

import { EMAIndicator } from '../../../src/JabbrLabs/indicators/ema-indicator';

describe('EMAIndicator', () => {
  it('calculates EMA correctly for known data', () => {
    const ema = new EMAIndicator({ period: 3, smoothing: 2 });
    const data = [2, 4, 6, 8, 10];
    // For period=3, smoothing=2, multiplier=0.5
    // EMA[0]=2, EMA[1]=3, EMA[2]=4.5, EMA[3]=6.25, EMA[4]=8.125
    const expected = [2, 3, 4.5, 6.25, 8.125];
    const result = ema.calculate(data);
    expect(result.length).toBe(expected.length);
    result.forEach((val, i) => expect(val).toBeCloseTo(expected[i], 5));
  });

  it('throws if not enough data', () => {
    const ema = new EMAIndicator({ period: 5 });
    expect(() => ema.calculate([1, 2])).toThrow(/Insufficient data/);
  });

  it('validates parameters', () => {
    const ema = new EMAIndicator();
    expect(() => ema.setPeriod(0)).toThrow(/at least 1/);
    expect(() => ema.setSmoothing(0)).toThrow(/positive/);
    expect(() => new EMAIndicator({ period: -2 })).toThrow();
  });

  it('clones itself with same parameters', () => {
    const ema = new EMAIndicator({ period: 4, smoothing: 2 });
    const clone = ema.clone();
    expect(clone.getParameters()).toEqual(ema.getParameters());
    expect(clone.calculate([1,2,3,4,5,6,7,8,9])).toEqual(ema.calculate([1,2,3,4,5,6,7,8,9]));
  });

  it('handles empty input gracefully', () => {
    const ema = new EMAIndicator({ period: 2 });
    expect(() => ema.calculate([])).toThrow(/Insufficient data/);
  });
});

"use client";
import { zodResolver } from "@hookform/resolvers/zod";
import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import * as z from "zod";

// Example strategies and their parameters
const STRATEGIES = {
  SMA: {
    label: "Simple Moving Average",
    params: [
      { name: "period", label: "Period", type: "number", min: 1, max: 200, default: 14 },
      { name: "threshold", label: "Threshold", type: "number", min: 0, max: 100, default: 50 },
    ],
  },
  EMA: {
    label: "Exponential Moving Average",
    params: [
      { name: "period", label: "Period", type: "number", min: 1, max: 200, default: 14 },
      { name: "multiplier", label: "Multiplier", type: "number", min: 1, max: 10, default: 2 },
    ],
  },
  Custom: {
    label: "Custom Script",
    params: [
      { name: "script", label: "Strategy Script", type: "textarea" },
    ],
  },
};

const MARKET_TYPES = [
  { value: "spot", label: "Spot" },
  { value: "futures", label: "Futures" },
];

const schema = z.object({
  botName: z.string().min(3, "Bot name must be at least 3 characters"),
  marketType: z.enum(["spot", "futures"]),
  strategy: z.enum(["SMA", "EMA", "Custom"]),
  parameters: z.record(z.any()),
});

type FormData = z.infer<typeof schema>;

export default function BotCreatePage() {
  const [strategy, setStrategy] = useState<keyof typeof STRATEGIES>("SMA");
  const [marketType, setMarketType] = useState("spot");
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isSubmitting },
    reset,
  } = useForm<FormData>({
    resolver: zodResolver(schema),
    defaultValues: {
      botName: "",
      marketType: "spot",
      strategy: "SMA",
      parameters: {},
    },
  });

  // Watch for changes to update dynamic fields
  const selectedStrategy = watch("strategy");
  const selectedMarketType = watch("marketType");

  useEffect(() => {
    if (selectedStrategy) {setStrategy(selectedStrategy as keyof typeof STRATEGIES);}
  }, [selectedStrategy]);
  useEffect(() => {
    if (selectedMarketType) {setMarketType(selectedMarketType);}
  }, [selectedMarketType]);

  // Dynamic parameter fields
  const parameterFields = STRATEGIES[strategy].params;

  // Example: market-specific parameters
  const marketSpecificFields =
    marketType === "futures"
      ? [
          { name: "leverage", label: "Leverage", type: "number", min: 1, max: 100, default: 10 },
          { name: "marginType", label: "Margin Type", type: "select", options: ["isolated", "cross"], default: "isolated" },
        ]
      : [];

  // Combine all parameter fields
  const allFields = [...parameterFields, ...marketSpecificFields];

  const onSubmit = async (data: FormData) => {
    // Prepare payload
    const payload = {
      botName: data.botName,
      marketType: data.marketType,
      strategy: data.strategy,
      parameters: data.parameters,
    };
    try {
      const res = await fetch("/api/bots", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (!res.ok) {throw new Error("Failed to create bot");}
      reset();
      alert("Bot created successfully!");
    } catch (err: unknown) {
      if (err instanceof Error) {
        alert(err.message);
      } else {
        alert("Error creating bot");
      }
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded shadow">
      <h1 className="text-2xl font-bold mb-4">Create New Trading Bot</h1>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div>
          <label className="block font-medium">Bot Name</label>
          <input
            {...register("botName")}
            className="w-full border rounded px-3 py-2 mt-1"
            placeholder="Enter bot name"
            disabled={isSubmitting}
          />
          {errors.botName && <span className="text-red-500 text-sm">{errors.botName.message}</span>}
        </div>
        <div>
          <label className="block font-medium">Market Type</label>
          <select
            {...register("marketType")}
            className="w-full border rounded px-3 py-2 mt-1"
            disabled={isSubmitting}
          >
            {MARKET_TYPES.map((m) => (
              <option key={m.value} value={m.value}>
                {m.label}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label className="block font-medium">Strategy</label>
          <select
            {...register("strategy")}
            className="w-full border rounded px-3 py-2 mt-1"
            disabled={isSubmitting}
          >
            {Object.entries(STRATEGIES).map(([key, s]) => (
              <option key={key} value={key}>
                {s.label}
              </option>
            ))}
          </select>
        </div>
        {/* Dynamic parameter fields */}
        {allFields.map((field) => (
          <div key={field.name}>
            <label className="block font-medium">{field.label}</label>
            {field.type === "select" && "options" in field ? (
              <select
                {...register(`parameters.${field.name}`)}
                className="w-full border rounded px-3 py-2 mt-1"
                defaultValue={"default" in field ? String(field.default) : undefined}
                disabled={isSubmitting}
              >
                {Array.isArray(field.options) && field.options.map((opt: string) => (
                  <option key={opt} value={opt}>
                    {opt}
                  </option>
                ))}
              </select>
            ) : field.type === "textarea" ? (
              <textarea
                {...register(`parameters.${field.name}`)}
                className="w-full border rounded px-3 py-2 mt-1"
                rows={4}
                placeholder={field.label}
                disabled={isSubmitting}
              />
            ) : (
              <input
                type={field.type}
                {...register(`parameters.${field.name}`)}
                className="w-full border rounded px-3 py-2 mt-1"
                min={"min" in field ? String(field.min) : undefined}
                max={"max" in field ? String(field.max) : undefined}
                defaultValue={"default" in field ? String(field.default) : undefined}
                step={field.type === "number" ? 1 : undefined}
                disabled={isSubmitting}
              />
            )}
          </div>
        ))}
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50"
          disabled={isSubmitting}
        >
          {isSubmitting ? "Creating..." : "Create Bot"}
        </button>
      </form>
    </div>
  );
}

"use client";

import { CONSTANTS } from '@jabbr/shared';
import type { Bot, BotStatus } from '@jabbr/shared/src/types';
import Link from 'next/link';
import React, { useState, useEffect } from 'react';

import StrategyMonitor from '../../components/StrategyMonitor';
import useWebSocket from '../../hooks/useWebSocket';


// Enhanced filter options
interface BotFilters {
  status?: BotStatus | 'all';
  strategy?: string | 'all';
  exchange?: string | 'all';
  search?: string;
}

// Sort options
type SortField = 'name' | 'status' | 'strategy' | 'exchange' | 'createdAt' | 'performance';
type SortDirection = 'asc' | 'desc';

interface SortConfig {
  field: SortField;
  direction: SortDirection;
}

export default function BotsPage() {
  const [bots, setBots] = useState<Bot[]>([]);
  const [filteredBots, setFilteredBots] = useState<Bot[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedBot, setSelectedBot] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'bots' | 'strategies'>('bots');
  const [filters, setFilters] = useState<BotFilters>({ status: 'all', strategy: 'all', exchange: 'all', search: '' });
  const [sortConfig, setSortConfig] = useState<SortConfig>({ field: 'createdAt', direction: 'desc' });
  const [showFilters, setShowFilters] = useState(false);

  const {
    isConnected,
    subscribe
  } = useWebSocket({
    url: 'ws://localhost:3002/ws',
    onOpen: () => {
      // Subscribe to bot status updates
      subscribe(CONSTANTS.WS_CHANNELS.BOT_STATUS);
    },
    onMessage: (message) => {
      // Handle real-time bot status updates
      if (message.channel === CONSTANTS.WS_CHANNELS.BOT_STATUS && message.data) {
        setBots(prevBots => 
          prevBots.map(bot => 
            bot.id === message.data.botId 
              ? { ...bot, status: message.data.status }
              : bot
          )
        );
      }
    }
  });

  // Fetch bots on component mount
  useEffect(() => {
    fetchBots();
  }, []);

  // Filter and sort bots when filters or sort config changes
  useEffect(() => {
    let filtered = [...bots];

    // Apply filters
    if (filters.status && filters.status !== 'all') {
      filtered = filtered.filter(bot => bot.status === filters.status);
    }
    if (filters.strategy && filters.strategy !== 'all') {
      filtered = filtered.filter(bot => bot.strategy === filters.strategy);
    }
    if (filters.exchange && filters.exchange !== 'all') {
      filtered = filtered.filter(bot => bot.exchange === filters.exchange);
    }
    if (filters.search) {
      filtered = filtered.filter(bot => 
        bot.name.toLowerCase().includes(filters.search!.toLowerCase()) ||
        bot.description?.toLowerCase().includes(filters.search!.toLowerCase())
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      let aValue: any = a[sortConfig.field]; // eslint-disable-line @typescript-eslint/no-explicit-any
      let bValue: any = b[sortConfig.field]; // eslint-disable-line @typescript-eslint/no-explicit-any

      // Handle special cases
      if (sortConfig.field === 'performance') {
        aValue = a.performance?.totalPnL || 0;
        bValue = b.performance?.totalPnL || 0;
      }

      if (typeof aValue === 'string') {
        aValue = aValue.toLowerCase();
        bValue = bValue.toLowerCase();
      }

      if (sortConfig.direction === 'asc') {
        return aValue > bValue ? 1 : -1;
      } 
        return aValue < bValue ? 1 : -1;
      
    });

    setFilteredBots(filtered);
  }, [bots, filters, sortConfig]);

  const fetchBots = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await fetch('/api/bots', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` // JWT token
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch bots: ${response.statusText}`);
      }

      const data = await response.json();
      setBots(data.data || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch bots');
      console.error('Error fetching bots:', err);
    } finally {
      setLoading(false);
    }
  };

  // Bot lifecycle control functions
  const handleBotAction = async (botId: string, action: 'start' | 'stop' | 'pause' | 'resume') => {
    try {
      const response = await fetch(`/api/bots/${botId}/${action}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to ${action} bot`);
      }

      // Refresh bots list to get updated status
      await fetchBots();
    } catch (err) {
      alert(err instanceof Error ? err.message : `Failed to ${action} bot`);
    }
  };

  const getStatusColor = (status: BotStatus): string => {
    switch (status) {
      case 'running': return 'text-green-600 bg-green-100';
      case 'stopped': return 'text-gray-600 bg-gray-100';
      case 'paused': return 'text-yellow-600 bg-yellow-100';
      case 'starting': case 'stopping': case 'pausing': return 'text-blue-600 bg-blue-100';
      case 'error': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusIcon = (status: BotStatus): string => {
    switch (status) {
      case 'running': return 'üü¢';
      case 'stopped': return '‚ö´';
      case 'paused': return 'üü°';
      case 'starting': case 'stopping': case 'pausing': return 'üîÑ';
      case 'error': return 'üî¥';
      default: return '‚ö´';
    }
  };

  const canPerformAction = (status: BotStatus, action: string): boolean => {
    switch (action) {
      case 'start': return ['stopped', 'error'].includes(status);
      case 'stop': return ['running', 'paused'].includes(status);
      case 'pause': return status === 'running';
      case 'resume': return status === 'paused';
      default: return false;
    }
  };

  // Enhanced bot management functions
  const handleSort = (field: SortField) => {
    setSortConfig(prev => ({
      field,
      direction: prev.field === field && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const handleFilterChange = (filterType: keyof BotFilters, value: string) => {
    setFilters(prev => ({ ...prev, [filterType]: value }));
  };

  // Note: Bulk actions will be implemented in future iterations

  const deleteBot = async (botId: string) => {
    if (!confirm('Are you sure you want to delete this bot? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`/api/bots/${botId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete bot');
      }

      await fetchBots();
    } catch (err) {
      alert(err instanceof Error ? err.message : 'Failed to delete bot');
    }
  };

  // Get unique values for filter dropdowns
  const getUniqueStrategies = () => [...new Set(bots.map(bot => bot.strategy))];
  const getUniqueExchanges = () => [...new Set(bots.map(bot => bot.exchange))];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 p-8">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center justify-center h-64">
            <div className="text-lg text-gray-600">Loading bots...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex flex-col mb-8">
          <div className="flex justify-between items-center mb-6">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 mb-2">
                ü§ñ Trading Bots
              </h1>
              <p className="text-lg text-gray-600">
                Manage your automated trading bots ({filteredBots.length} of {bots.length} bots)
              </p>
            </div>
            
            <div className="flex space-x-4">
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors font-medium"
              >
                üîç {showFilters ? 'Hide' : 'Show'} Filters
              </button>
              
              <Link
                href="/bots/create"
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors font-medium"
              >
                ‚ûï Create New Bot
              </Link>
              
              <button
                onClick={fetchBots}
                className="bg-gray-600 text-white px-6 py-3 rounded-lg hover:bg-gray-700 transition-colors font-medium"
              >
                üîÑ Refresh
              </button>
            </div>
          </div>

          {/* Enhanced Filters */}
          {showFilters && (
            <div className="bg-white rounded-lg shadow-md p-6 mb-6">
              <h3 className="text-lg font-semibold mb-4">Filter & Search Bots</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {/* Search */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Search</label>
                  <input
                    type="text"
                    placeholder="Bot name or description..."
                    value={filters.search || ''}
                    onChange={(e) => handleFilterChange('search', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                {/* Status Filter */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
                  <select
                    value={filters.status || 'all'}
                    onChange={(e) => handleFilterChange('status', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
