];

console.log('üîß Fixing incorrect .at() usage in object property access...\n');

let totalFiles: number = 0;
let totalFixes: number = 0;

function isObjectPropertyAccess(line: string, matchStart: number): boolean {
  // Check context before the match to determine if it's object property access
  const beforeMatch: string = line.substring(0, matchStart);
  
  // Look for patterns that indicate object property access:
  // 1. obj.at(field) - where obj is not an array
  // 2. config.at(key) - configuration objects
  // 3. data.at(property) - data objects
  // 4. logger.at(level) - logger objects
  // 5. metadata.at(field) - metadata objects
  
  const objectPatterns: RegExp[] = [
    /\b(config|data|obj|object|metadata|options|params|settings|props)\s*\.\s*at\s*\(/,
    /\b[a-zA-Z_$][a-zA-Z0-9_$]*\s*\.\s*at\s*\(/
  ];
  
  return objectPatterns.some(pattern => pattern.test(beforeMatch + '.at('));
}

function fixObjectPropertyAccess(content: string): { content: string; fixes: number } {
  let fixes: number = 0;
  const lines: string[] = content.split('\n');
  
  for (let i = 0; i < lines.length; i++) {
    const line: string = lines[i];
    
    // Find .at( patterns
    const atMatches: RegExpMatchArray[] = [...line.matchAll(/\.at\s*\(/g)];
    
    for (const match of atMatches) {
      const matchStart: number = match.index!;
      
      if (isObjectPropertyAccess(line, matchStart)) {
        // Replace .at( with [
        const beforeAt: string = line.substring(0, matchStart);
        const afterAt: string = line.substring(matchStart + match[0].length);
        
        // Find the closing parenthesis
        let parenCount: number = 1;
        let endIndex: number = 0;
        
        for (let j = 0; j < afterAt.length; j++) {
          if (afterAt[j] === '(') parenCount++;
          else if (afterAt[j] === ')') {
            parenCount--;
            if (parenCount === 0) {
              endIndex = j;
              break;
            }
          }
        }
        
        if (endIndex > 0) {
          const propertyAccess: string = afterAt.substring(0, endIndex);
          const afterProperty: string = afterAt.substring(endIndex + 1);
          
          lines[i] = beforeAt + '[' + propertyAccess + ']' + afterProperty;
          fixes++;
        }
      }
    }
  }
  
  return { content: lines.join('\n'), fixes };
}

// Process each file
for (const filePath of targetFiles) {
  const fullPath: string = path.join(process.cwd(), filePath);
  
  if (!fs.existsSync(fullPath)) {
    console.log(`‚ö†Ô∏è  File not found: ${filePath}`);
    continue;
  }
  
  try {
    const content: string = fs.readFileSync(fullPath, 'utf8');
    const result = fixObjectPropertyAccess(content);
    
    if (result.fixes > 0) {
      fs.writeFileSync(fullPath, result.content, 'utf8');
      console.log(`‚úÖ ${filePath}: Fixed ${result.fixes} object property access patterns`);
      totalFiles++;
      totalFixes += result.fixes;
    } else {
      console.log(`‚úì  ${filePath}: No object property access fixes needed`);
    }
  } catch (error) {
    console.error(`‚ùå Error processing ${filePath}:`, (error as Error).message);
  }
}

console.log(`\nüéØ Summary:`);
console.log(`   Files modified: ${totalFiles}`);
console.log(`   Total fixes applied: ${totalFixes}`);
console.log(`\n‚ú® Object property access patterns fixed!`);

#!/usr/bin/env node

import * as fs from 'fs';
import * as path from 'path';
import { glob } from 'glob';

/**
 * Advanced script to automatically fix object injection security vulnerabilities
 * by replacing unsafe array access patterns with safe .at() method calls
 */

const BACKEND_DIR: string = path.join(__dirname, '..', 'packages', 'backend', 'src');

// Patterns to fix
interface Pattern {
  regex: RegExp;
  replacement: string;
  description: string;
}

const PATTERNS: Pattern[] = [
  // Array access patterns: arr[i] -> arr.at(i)
  {
    regex: /(\w+)\[(\w+)\]/g,
    replacement: '$1.at($2)',
    description: 'Replace array bracket notation with safe .at() method'
  },
  // Array length access: arr[arr.length - 1] -> arr.at(-1)
  {
    regex: /(\w+)\[(\w+)\.length\s*-\s*1\]/g,
    replacement: '$1.at(-1)',
    description: 'Replace arr[arr.length-1] with arr.at(-1)'
  },
  // Complex array access: arr[i - 1] -> arr.at(i - 1)
  {
    regex: /(\w+)\[(\w+\s*[-+]\s*\d+)\]/g,
    replacement: '$1.at($2)',
    description: 'Replace complex array index access with .at()'
  }
];

// Files to exclude (already fixed or special cases)
const EXCLUDE_PATTERNS: string[] = [
  '**/node_modules/**',
  '**/dist/**',
  '**/build/**',
  '**/*.test.ts',
  '**/*.spec.ts'
];

// Object property access patterns to validate
interface ObjectPattern {
  regex: RegExp;
  replacement: (match: string, obj: string, key: string) => string;
  description: string;
}

const OBJECT_ACCESS_PATTERNS: ObjectPattern[] = [
  {
    regex: /(\w+)\[(\w+)\]/g,
    replacement: (match: string, obj: string, key: string): string => {
      // Only fix if it's likely an array access, not object property access
      if (/^(req\.(params|body|query)|config|parameters)/.test(obj)) {
        return `Object.prototype.hasOwnProperty.call(${obj}, ${key}) ? ${obj}[${key} as keyof typeof ${obj}] : undefined`;
      }
      return `${obj}.at(${key})`;
    },
    description: 'Secure object property access'
  }
];

async function findTypeScriptFiles(): Promise<string[]> {
  try {
    const files: string[] = await glob('**/*.ts', {
      cwd: BACKEND_DIR,
      ignore: EXCLUDE_PATTERNS,
      absolute: true
    });
    return files;
  } catch (error) {
    throw error;
  }
}

function isArrayAccessContext(content: string, match: string, index: number): boolean {
  // Check if this is likely an array access vs object property access
  const beforeMatch: string = content.substring(Math.max(0, index - 50), index);
  const afterMatch: string = content.substring(index + match.length, index + match.length + 50);
  
  // Skip if it's clearly object property access
  if (/\.(params|body|query|headers|cookies)\[/.test(beforeMatch + match)) {
    return false;
  }
  
  // Skip if it's accessing a well-known object property
  if (/config\[|options\[|settings\[/.test(beforeMatch + match)) {
    return false;
  }
  
  return true;
}

function fixObjectInjectionInFile(filePath: string): number {
  console.log(`Processing: ${path.relative(BACKEND_DIR, filePath)}`);
  
  let content: string = fs.readFileSync(filePath, 'utf8');
  let changed: boolean = false;
  let changeCount: number = 0;
  
  // Apply each pattern
  PATTERNS.forEach(pattern => {
    const originalContent: string = content;
    let matches: number = 0;
    
    content = content.replace(pattern.regex, (match: string, ...args: any[]): string => {
      const [fullMatch, ...groups] = [match, ...args];
      
      // Skip if this doesn't look like array access
      const index: number = args[args.length - 2]; // Second to last arg is the index
      if (!isArrayAccessContext(originalContent, fullMatch, index)) {
        return fullMatch;
      }
      
      matches++;
      return pattern.replacement;
    });
    
    if (matches > 0) {
      console.log(`  Applied ${pattern.description}: ${matches} replacements`);
      changeCount += matches;
      changed = true;
    }
  });
  
  if (changed) {
    fs.writeFileSync(filePath, content, 'utf8');
    console.log(`  ‚úÖ Fixed ${changeCount} object injection issues in ${path.basename(filePath)}`);
    return changeCount;
  }
  
  return 0;
}

async function main(): Promise<void> {
  console.log('üîß Starting automated object injection vulnerability fixes...\n');
  
